INSERT INTO `course` VALUES ('25', 'Khoa học làm giàu cho người thích làm giàu', 'upload_1345c0438dfa41148f9048796438ae6a_large1545286129.jpg', '1', '1544415522', '1', '100000', '3', null, '50000', '1', '0', '<p>&quot;Đ&atilde; 10 năm tr&ocirc;i qua kể từ lần gần nhất v&agrave; duy nhất Việt Nam v&ocirc; địch AFF Cup. Bởi vậy, ng&agrave;y mai sẽ l&agrave; những gi&acirc;y ph&uacute;t đặc biệt với t&ocirc;i v&agrave; c&aacute;c cầu thủ. Đ&oacute; sẽ l&agrave; một trận đấu đặc biệt&quot;, HLV Park Hang-seo mở lời trong cuộc họp b&aacute;o ở s&acirc;n Bukit Jalil (Kuala Lumpur, Malaysia). &quot;Đ&acirc;y l&agrave; một trận đấu s&acirc;n kh&aacute;ch, dưới sức &eacute;p của 80.000 cổ động vi&ecirc;n chủ nh&agrave;. Mọi thứ chắc chắn sẽ rất kh&oacute; khăn. Ch&uacute;ng t&ocirc;i phải chuẩn bị thật kỹ lưỡng để c&oacute; thể gi&agrave;nh kết quả t&iacute;ch cực&quot;.</p>', null);
