INSERT INTO `question_flash_muti` VALUES ('8', null, null, '33', '44', '44', null, 'Ý thứ nhất', 'hệ thống các quan điểm, học thuyết khoa học, Mác xây dựng, Leenin phát triển', null, null, null, null, 'Là hệ thống các quan điểm và học thuyết khoa học do C.Mác và Ph.Ăngghen xây dựng, V.I.Leenin bảo vệ và phát triển, được hình thành và phát triển dựa trên cơ sở tổng kết thực tiễn và kế thừa những giá trị tư tưởng của nhân loại');
