INSERT INTO `question_flash_muti` VALUES ('31', '6', null, '41', '58', '29', null, 'Ý thứ hai', 'phương thức sản xuất tư bản, Tây Âu, phát triển, nền tảng, Cách mạng công nghiệp Anh, thế kỷ 18', null, null, null, null, 'Là thời kỳ phương thức sản xuất tư bản chủ nghĩa ở các nước Tây Âu đã phát triển mạnh mẽ trên nền tảng cuộc cách mạng công nghiệp được diễn ra trước tiên ở Anh vào cuối thế kỉ XVIII.');
INSERT INTO `question_flash_muti` VALUES ('32', '6', null, '41', '58', '29', null, 'Ý thứ ba', 'thủ công tư bản, tư bản chủ nghĩa, giai cấp vô sản', null, null, null, null, 'Cuộc cách mạng công nghiệp đánh dấu bước chuyển biến từ nền sản xuất thủ công tư bản sang nền đại công nghiệp tư bản chủ nghĩa, làm thay đổi sâu sắc cục diện xã hội, trước hết là sự hình thành và phát triển của giai cấp vô sản.');
