INSERT INTO `question_flash_muti` VALUES ('41', '6', null, '43', '61', '39', null, 'Ý thứ hai?', 'phương thức sản xuất tư bản, Tây Âu, phát triển, nền tảng, Cách mạng công nghiệp Anh, thế kỷ 18', null, null, null, null, 'Là thời kỳ phương thức sản xuất tư bản chủ nghĩa ở các nước Tây Âu đã phát triển mạnh mẽ trên nền tảng cuộc cách mạng công nghiệp được diễn ra trước tiên ở Anh vào cuối thế kỉ XVIII.');
INSERT INTO `question_flash_muti` VALUES ('42', '6', null, '43', '61', '39', null, 'Ý thứ ba', 'thủ công tư bản, tư bản chủ nghĩa, giai cấp vô sản', null, null, null, null, 'Cuộc cách mạng công nghiệp đánh dấu bước chuyển biến từ nền sản xuất thủ công tư bản sang nền đại công nghiệp tư bản chủ nghĩa, làm thay đổi sâu sắc cục diện xã hội, trước hết là sự hình thành và phát triển của giai cấp vô sản.');
INSERT INTO `question_flash_muti` VALUES ('43', '6', null, '43', '61', '39', null, 'Ý thứ tư', 'mâu thuẫn, lực lượng sản xuất, quan hệ sản xuất, 1825, Pháp, Anh, Đức', null, null, null, null, 'Thời kì này mâu thuẫn sâu sắc giữa Lực lượng sản xuất với Quan hệ sản xuất bộc lộ qua cuộc khủng hoảng kinh tế 1825 và hàng loạt các cuộc đấu tranh của giai cấp công nhân đã nổ ra ở Anh, Pháp, Đức.');
INSERT INTO `question_flash_muti` VALUES ('44', '6', null, '43', '61', '39', null, 'ý thứ năm', 'giai cấp vô sản, độc lập, tiên phong', null, null, null, null, 'Giai cấp vô sản đã trở thành lực lượng chính trị độc lập, tiên phong trong cuộc đấu tranh cho nền dân chủ, công bằng, tiến bộ xã hội. Thực tiễn cách mạng đã đặt ra yêu cầu khách quan là phải có lí luận soi đường và CNMÁC ra đời đã đáp ứng được yêu cầu khách quan đó .');
INSERT INTO `question_flash_muti` VALUES ('45', null, null, '43', '61', '61', null, 'Ý lớn thứ 3 là gì?', null, null, null, null, null, 'Tiền đề lý luận?');
INSERT INTO `question_flash_muti` VALUES ('46', '6', null, '43', '61', '45', null, 'Ý thứ nhất', 'nhu cầu khách quan, kế thừa tinh hoa', null, null, null, null, 'Chủ nghĩa Mác ra đời không chỉ xuất phát từ nhu cầu khách quan lịch sử, mà còn là kết quả của sự kế thừa tinh hoa di sản lý luận của nhân loại, trong đó, trực tiếp nhất là triết học cổ điển Đức, kinh tế học chính trị cổ điển Anh và chủ nghĩa xã hội không tưởng Pháp và Anh:');
INSERT INTO `question_flash_muti` VALUES ('47', '6', null, '43', '61', '45', null, 'ý thứ hai', 'triết học cổ điển Đức, kế thừa, biện chứng - Ph.Hêghen, chủ nghĩa duy vật-L.Phoiơbắc', null, null, null, null, 'Với triết học cổ điển Đức, đặc biệt là triết học của Ph.Hêghen và L.Phoiơbắc đã ảnh hưởng sâu sắc đến sự hình thành thế giới quan và phương pháp luận triết học của chủ nghĩa Mác. Các nhà sáng lập của chủ nghĩa Mác đã kế thừa những nội dung cơ bản trong phép biện chứng của Ph.Hêghen, chủ nghĩa duy vật của L.Phoiơbắc. Khắc phục những những hạn chế cơ bản của hai học thuyết ấy, đó là triết học duy tâm (Hêghen) và phương pháp siêu hình (Phoiơbắc). Sáng lập ra chủ nghĩa duy vật biện chứng và phép biện chứng duy vật và được vận dụng vào nghiên cứu một cách khoa học những quy luật chung nhất của sự phát triển xã hội, nghiên cứu quy luật ra đời, phát triển suy tàn của chủ nghĩa tư bản và sự ra đời tất yếu của Chủ nghĩa xã hội.');
INSERT INTO `question_flash_muti` VALUES ('48', '6', null, '43', '61', '45', null, 'ý thứ 3', 'triết học cổ điển Anh, kế thừa, quan điểm hợp lý khoa học, A.Xmít, Đ.Ricácđô, giá trị thặng dư,', null, null, null, null, 'Với kinh tế học chính trị cổ điển Anh, đặc biệt là những học thuyết của A.Xmít và Đ.Ricácđô, C.Mác và Ăngghen đã kế thừa những quan điểm hợp lý khoa học của những học thuyết này, đó là quan điểm duy vật trong trong nghiên cứu lĩnh vực khoa học kinh tế chính trị và học thuyết giá trị về lao động. Đồng thời phê phán và khắc phục tính chất chưa triệt để trong học thuyết giá trị về lao động và phương pháp siêu hình trong nghiên cứu. Trên cơ sở đó các ông đã xây dựng thành công học thuyết về giá trị thặng dư, luận chứng khoa học về bản chất , nguồn gốc kinh tế của chủ nghĩa tư bản, sự ra đời của chủ nghĩa xã hội.');
INSERT INTO `question_flash_muti` VALUES ('49', '6', null, '43', '61', '45', null, 'ý thứ 4', 'chủ nghĩa xã hội không tưởng, Anh, Pháp, H.Xanh Ximông, S.Phuriê, R.Ôoen, tư tưởng nhân đạo, chủ nghĩa xã hội.', null, null, null, null, 'Với chủ nghĩa xã hội không tưởng ở các nước Anh và Pháp, đặc biệt là nhà tư tưởng tiêu biểu H.Xanh Ximông, S.Phuriê và R.Ôoen. C.Mác và Ph.Ăngghen đã kế thừa những tư tưởng nhân đạo, sự phê phán hợp lý của các nhà tư tưởng này đối với những hạn chế của chủ nghĩa tư bản. Đồng thời, các ông cũng khắc phục và vượt qua hạn chế trong học thuyết của họ. Đó là tính chất không tưởng trong các học thuyết ấy. Từ đó xây dựng nên một lý luận mới – lý luận khoa học về chủ nghĩa xã hội.');
INSERT INTO `question_flash_muti` VALUES ('50', '6', null, '43', '61', '45', null, null, null, null, null, null, null, null);
