INSERT INTO `lesson` VALUES ('44', 'Bài tập nhóm câu hỏi 1', '', '1545047544', null, '42', '31', '1', '42', '42', '1', '', '10', '1', null, null, null, null, null);
INSERT INTO `lesson` VALUES ('45', 'Bài tập 1', '<p><span style=\"font-family:Comic Sans MS,cursive\"><span style=\"font-size:14px\">Nguyện vọng của HLV Park Hang Seo xuất ph&aacute;t từ việc hơn 1 năm qua &ocirc;ng đ&atilde; li&ecirc;n tục l&agrave;m việc ở c&aacute;c giải đấu89</span></span></p>\r\n\r\n<p style=\"margin-left:120px\"><img alt=\"\" src=\"/public/images/course/33/lesson/1548753197_2.png\" style=\"height:384px; width:576px\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-family:Comic Sans MS,cursive\"><span style=\"font-size:14px\"><img alt=\"\" src=\"/public/images/course/33/lesson/1548753078_1.png\" style=\"height:50%; width:576px\" />.</span></span></p>\r\n\r\n<p><span style=\"font-family:Comic Sans MS,cursive\"><span style=\"font-size:14px\">Quan trọng hơn, HLV người H&agrave;n Quốc muốn c&ugrave;ng đội tuyển quốc gia Việt Nam hướng tới mục ti&ecirc;u cao nhất l&agrave; dự World Cup. Giấc mơ n&agrave;y cũng được &ocirc;ng thầy 60 tuổi chia sẻ với b&aacute;o ch&iacute; H&agrave;n Quốc.</span></span></p>\r\n\r\n<p><span style=\"font-family:Comic Sans MS,cursive\"><span style=\"font-size:14px\">&ldquo;Truyền th&ocirc;ng Việt Nam đ&atilde; hỏi t&ocirc;i về khả năng dự World Cup của đội nh&agrave;, nhưng điều đ&oacute; cần thời gian.</span></span></p>\r\n\r\n<p><span style=\"font-family:Comic Sans MS,cursive\"><span style=\"font-size:14px\">T&ocirc;i đ&atilde; họp với VFF v&agrave; tr&igrave;nh b&agrave;y họ cần đầu tư nhiều hơn v&agrave;o cầu thủ trẻ, những người nhỏ hơn thế hệ b&acirc;y giờ 10 tuổi&rdquo;, HLV Park Hang Seo chia sẻ.</span></span></p>\r\n\r\n<p><span style=\"font-family:Comic Sans MS,cursive\"><span style=\"font-size:14px\">C&ograve;n khi n&oacute;i về th&agrave;nh t&iacute;ch của đội tuyển Việt Nam ở Asian Cup 2019, &ocirc;ng Park nhấn mạnh th&agrave;nh t&iacute;ch như vậy l&agrave; qu&aacute; tuyệt vời khi c&aacute;c cầu thủ đ&atilde; c&oacute; một năm thi đấu đến kiệt sức: &ldquo;T&ocirc;i nghĩ tuyển Việt Nam đ&atilde; rất may mắn.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><iframe allow=\"encrypted-media\" allowfullscreen=\"true\" allowtransparency=\"true\" aria-hidden=\"true\" frameborder=\"0\" id=\"fb_xdm_frame_https\" name=\"fb_xdm_frame_https\" scrolling=\"no\" src=\"https://staticxx.facebook.com/connect/xd_arbiter/r/d_vbiawPdxB.js?version=44#channel=f33290bddb87378&amp;origin=https%3A%2F%2Fwebtruyen.com\" tabindex=\"-1\" title=\"Facebook Cross Domain Communication Frame\"></iframe></p>\r\n\r\n<ul>\r\n	<li><a href=\"https://webtruyen.com/\" title=\"Đọc Truyện Online Hay Và Mới Nhất\"><img alt=\"doc truyen\" src=\"https://webtruyen.com/public/frontend/desk/img/logo.png\" /></a></li>\r\n	<li><a href=\"javascript:void(0);\" rel=\"nofollow\" title=\"Menu Thể loại\">&nbsp;Thể Loại</a></li>\r\n	<li><a href=\"javascript:void(0);\" rel=\"nofollow\" title=\"Menu sắp xếp\">&nbsp;Sắp Xếp</a></li>\r\n	<li>\r\n	<form action=\"https://webtruyen.com/home/action_search\" method=\"get\"><input name=\"key\" type=\"text\" />&nbsp;</form>\r\n	</li>\r\n	<li><a href=\"https://webtruyen.com/thien-long-bat-bo-ban-moi/mo-dau_491584.html#\" rel=\"nofollow\">&nbsp;Th&agrave;nh Vi&ecirc;n</a></li>\r\n</ul>\r\n\r\n<p><a href=\"https://webtruyen.com/\">Trang Chủ</a></p>\r\n\r\n<p><a href=\"https://webtruyen.com/kiem-hiep/\">Kiếm hiệp</a></p>\r\n\r\n<p><a href=\"https://webtruyen.com/thien-long-bat-bo-ban-moi/\">Thi&ecirc;n Long B&aacute;t Bộ (Bản Mới)</a></p>\r\n\r\n<p><a href=\"https://webtruyen.com/thien-long-bat-bo-ban-moi/mo-dau_491584.html\">Mở Đầu</a></p>\r\n\r\n<p><ins><ins><iframe data-google-container-id=\"1\" data-load-complete=\"true\" frameborder=\"0\" height=\"90\" id=\"google_ads_iframe_/94672926/bazan_728x90_1\" marginheight=\"0\" marginwidth=\"0\" name=\"google_ads_iframe_/94672926/bazan_728x90_1\" scrolling=\"no\" title=\"3rd party ad content\" width=\"728\"></iframe></ins></ins></p>\r\n\r\n<ul>\r\n	<li>\r\n	<h1><a href=\"https://webtruyen.com/thien-long-bat-bo-ban-moi/\">THI&Ecirc;N LONG B&Aacute;T BỘ (BẢN MỚI)</a></h1>\r\n	</li>\r\n	<li>&nbsp;\r\n	<h2><a href=\"https://webtruyen.com/tac-gia/kim-dung/\" title=\"Kim Dung\">Kim Dung</a></h2>\r\n	</li>\r\n	<li>\r\n	<h3>&nbsp;Chương 1: Mở Đầu</h3>\r\n	</li>\r\n	<li>&nbsp;02/08/2014</li>\r\n	<li>\r\n	<p><iframe allow=\"encrypted-media\" allowfullscreen=\"true\" allowtransparency=\"true\" frameborder=\"0\" height=\"1000px\" name=\"f214e5395734bc\" scrolling=\"no\" src=\"https://www.facebook.com/v2.9/plugins/like.php?action=like&amp;app_id=212174092286047&amp;channel=https%3A%2F%2Fstaticxx.facebook.com%2Fconnect%2Fxd_arbiter%2Fr%2Fd_vbiawPdxB.js%3Fversion%3D44%23cb%3Df3dbd7255a4ee%26domain%3Dwebtruyen.com%26origin%3Dhttps%253A%252F%252Fwebtruyen.com%252Ff33290bddb87378%26relation%3Dparent.parent&amp;container_width=954&amp;href=https%3A%2F%2Fwebtruyen.com%2Fthien-long-bat-bo-ban-moi%2F&amp;layout=button_count&amp;locale=en_US&amp;sdk=joey&amp;share=true&amp;show_faces=false&amp;size=small\" title=\"fb:like Facebook Social Plugin\" width=\"1000px\"></iframe></p>\r\n\r\n	<p><iframe data-gapiattached=\"true\" frameborder=\"0\" hspace=\"0\" id=\"I0_1552727655848\" marginheight=\"0\" marginwidth=\"0\" name=\"I0_1552727655848\" ng-non-bindable=\"\" scrolling=\"no\" src=\"https://apis.google.com/u/0/se/0/_/+1/fastbutton?usegapi=1&amp;origin=https%3A%2F%2Fwebtruyen.com&amp;url=https%3A%2F%2Fwebtruyen.com%2Fthien-long-bat-bo-ban-moi%2Fmo-dau_491584.html&amp;gsrc=3p&amp;jsh=m%3B%2F_%2Fscs%2Fapps-static%2F_%2Fjs%2Fk%3Doz.gapi.en.DhjKTdQUV8Y.O%2Fam%3DwQ%2Frt%3Dj%2Fd%3D1%2Frs%3DAGLTcCPa8IoRoHbi-V_e4L7VwARfvdHzwQ%2Fm%3D__features__#_methods=onPlusOne%2C_ready%2C_close%2C_open%2C_resizeMe%2C_renderstart%2Concircled%2Cdrefresh%2Cerefresh%2Conload&amp;id=I0_1552727655848&amp;_gfid=I0_1552727655848&amp;parent=https%3A%2F%2Fwebtruyen.com&amp;pfname=&amp;rpctoken=46588093\" tabindex=\"0\" vspace=\"0\" width=\"100%\"></iframe></p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;C&agrave;i Đặt<a href=\"https://webtruyen.com/thien-long-bat-bo-ban-moi/thanh-sam-loi-lac-hiem-phong-hanh_491588.html\" id=\"nextchap\" title=\"Thanh Sam Lỗi Lạc Hiểm Phong Hành\">Chương&nbsp;Sau&nbsp;</a></p>\r\n\r\n<p>Sau khi hỏi &yacute; kiến độc giả, một số đ&ocirc;ng đ&atilde; đề nghị ch&uacute;ng t&ocirc;i dịch bộ truyện Thi&ecirc;n Long B&aacute;t Bộ. Bộ truyện n&agrave;y c&ugrave;ng với Lộc Đỉnh K&yacute; l&agrave; hai bộ truyện d&agrave;i nhất của Kim Dung. Bản ch&uacute;ng t&ocirc;i dịch l&agrave; bản đ&atilde; được sửa chữa, t&aacute;i bản lần thứ hai do Viễn Ảnh c&ocirc;ng ti tại Đ&agrave;i Bắc ấn h&agrave;nh th&aacute;ng 8 năm 1981 bao gồm 5 quyển, 50 chương, 2124 trang.<br />\r\n<br />\r\nNgười ta vẫn bảo rằng Thi&ecirc;n Long B&aacute;t Bộ l&agrave; truyện nhiều t&igrave;nh tiết nhất, đ&ocirc;ng nh&acirc;n vật, mỗi vai c&oacute; một đặc t&iacute;nh ri&ecirc;ng kh&ocirc;ng người n&agrave;o giống người n&agrave;o v&agrave; c&oacute; đến năm s&aacute;u người c&oacute; thể coi như vai ch&iacute;nh. Tuy thế, trong khi c&ograve;n đăng tải tr&ecirc;n mặt b&aacute;o, Thi&ecirc;n Long B&aacute;t Bộ cũng đ&atilde; g&acirc;y ra nhiều m&acirc;u thuẫn, đặt ra nhiều c&acirc;u hỏi cho độc giả v&agrave; nhiều đoạn kh&ocirc;ng c&oacute; c&acirc;u trả lời. Do đ&oacute;, bộ truyện n&agrave;y đ&atilde; được Kim Dung sửa đổi nhiều chi tiết, mặc dầu nội dung kh&ocirc;ng kh&aacute;c nhưng đ&atilde; tạo n&ecirc;n những n&eacute;t chấm ph&aacute; mới khiến cho n&oacute; được xếp v&agrave;o một trong những kỳ thư của nh&agrave; văn Hương Cảng.<br />\r\n<br />\r\nTheo Trần Thế Tương, một học giả qu&aacute; cố Trung Hoa m&agrave; Kim Dung rất k&iacute;nh trọng, Thi&ecirc;n Long B&aacute;t Bộ l&agrave; một bộ truyện phải đọc đi đọc lại từ đầu ch&iacute; cuối nhiều lần mới thấy hết được c&aacute;i hay của n&oacute;. Những lời ph&ecirc; b&igrave;nh của học giả n&agrave;y đ&atilde; được Kim Dung coi như l&agrave; n&oacute;i l&ecirc;n được t&acirc;m sự của &ocirc;ng v&agrave; ch&iacute;nh v&igrave; thế, Thi&ecirc;n Long B&aacute;t Bộ c&oacute; thể n&oacute;i l&agrave; một bộ truyện m&agrave; Kim Dung rất t&acirc;m đắc. Trần ti&ecirc;n sinh l&agrave; người duy nhất được Kim Dung đề tặng trong bản mới lần in thứ hai n&agrave;y, một biệt lệ cho bất cứ tiểu thuyết n&agrave;o của Trung Hoa.<br />\r\n<br />\r\nCũng như Ỷ Thi&ecirc;n Đồ Long K&yacute;, Kim Dung đ&atilde; dụng c&ocirc;ng để đặt đề tựa cho mỗi chương th&agrave;nh một c&acirc;u trong một b&agrave;i thơ. Bộ Thi&ecirc;n Long B&aacute;t Bộ bao gồm năm b&agrave;i thơ, mỗi b&agrave;i mười c&acirc;u nhưng Ỷ Thi&ecirc;n Đồ Long K&yacute; theo lối ca tr&ugrave; (B&aacute; Lương Đ&agrave;i thể) c&ograve;n Thi&ecirc;n Long B&aacute;t Bộ th&igrave; l&agrave; năm b&agrave;i, mỗi quyển một b&agrave;i theo lối từ. Kim Dung ti&ecirc;n sinh tự nhận rằng m&igrave;nh rất k&eacute;m về văn chương thi ca, việc l&agrave;m của &ocirc;ng l&agrave; một nỗ lực hết sức gắng gượng - v&agrave; ch&uacute;ng t&ocirc;i cũng vậy, rất e ngại khi phải dịch những phần thi từ đ&oacute; ra tiếng Việt n&ecirc;n thường lần lữa kh&ocirc;ng ho&agrave;n tất. Nếu c&oacute; dịch th&igrave; chỉ để cho khỏi bị thiếu s&oacute;t nhưng chắc chắn c&oacute; rất nhiều lủng củng, xin độc giả cũng lượng t&igrave;nh cho.<br />\r\n<br />\r\nR&uacute;t kinh nghiệm của thời gian qua, ch&uacute;ng t&ocirc;i mong độc giả g&oacute;p &yacute; để tiếp tục sửa chữa thường xuy&ecirc;n v&agrave; th&ecirc;m bớt những g&igrave; cần phải bổ t&uacute;c. Rất đ&ocirc;ng th&acirc;n hữu đ&atilde; hiệu đ&iacute;nh gi&ugrave;m một số sai lầm trong bộ Ỷ Thi&ecirc;n Đồ Long K&yacute;, nh&acirc;n dịp n&agrave;y ch&uacute;ng t&ocirc;i cũng xin cảm tạ chung c&aacute;c vị đ&oacute;. Một số bạn b&egrave; gi&uacute;p dịch một v&agrave;i đoạn thơ văn, c&oacute; người đề nghị sửa lại một v&agrave;i chữ kh&oacute; hiểu, v&agrave; nhất l&agrave; bạn DPLT đ&atilde; chịu kh&oacute; t&igrave;m t&ograve;i, tra cứu s&aacute;ch vở chữ Hoa để gi&uacute;p người dịch những phần thi từ c&ograve;n bỏ dở.<br />\r\n<br />\r\nNhư đ&atilde; thưa từ trước - đ&acirc;y chỉ l&agrave; một việc l&agrave;m c&aacute; nh&acirc;n trong những th&igrave; giờ eo hẹp của đời sống hết sức bận rộn - sau tất cả những nỗ lực phải d&agrave;nh cho c&ocirc;ng việc v&agrave; gia đ&igrave;nh, việc dịch s&aacute;ch l&agrave; một c&aacute;i th&uacute; ti&ecirc;u khiển n&ecirc;n sẽ rất ... thư thả theo chương tr&igrave;nh hai tuần một chương như năm qua. Nếu đ&uacute;ng theo lịch tr&igrave;nh đ&oacute;, bộ truyện n&agrave;y sẽ c&oacute; thể ho&agrave;n tất v&agrave;o cuối năm 2003 hay đầu năm 2004. Một số bi&ecirc;n khảo cũng sẽ được tiến h&agrave;nh v&igrave; c&oacute; thể n&oacute;i, Thi&ecirc;n Long B&aacute;t Bộ l&agrave; một chuyến du h&agrave;nh khắp nước Trung Hoa trong thời Tống, một giai đoạn m&agrave; nhiều nước sống b&ecirc;n cạnh nhau, vẫn bang giao nhưng cũng đầy xung đột. Cứ theo những nh&agrave; chuy&ecirc;n khảo về Kim Dung đ&acirc;y l&agrave; bộ truyện m&agrave; Kim Dung dụng c&ocirc;ng hơn cả, chẳng kh&aacute;c g&igrave; một bức tranh trải d&agrave;i h&agrave;ng dặm với tất cả những chi tiết ly kỳ khiến cho người đọc phải say m&ecirc; kh&ocirc;ng sao dứt ra được.<br />\r\n<br />\r\nThời Bắc Tống l&agrave; một giai đoạn hết sức th&agrave;nh tựu về mặt văn chương, nghệ thuật của Trung Hoa mặc dầu c&oacute; nhiều suy sụp về ch&iacute;nh trị v&agrave; qu&acirc;n sự. Nước T&agrave;u l&uacute;c đ&oacute; được chia cắt th&agrave;nh nhiều quốc gia lu&ocirc;n lu&ocirc;n biến động l&uacute;c th&igrave; li&ecirc;n kết đồng minh, l&uacute;c lại nh&ograve;m ng&oacute;, th&ocirc;n t&iacute;nh lẫn nhau. Ph&iacute;a Bắc l&agrave; nước Li&ecirc;u của người Khiết Đan đ&oacute;ng đ&ocirc; ở L&acirc;m Ho&agrave;ng (Thượng Kinh). Ph&iacute;a T&acirc;y Bắc l&agrave; T&acirc;y Hạ đ&oacute;ng đ&ocirc; ở Linh Ch&acirc;u. Nh&agrave; Tống ở ph&iacute;a Đ&ocirc;ng Nam, kinh đ&ocirc; l&agrave; Khai Phong. Ch&iacute;nh T&acirc;y l&agrave; Thổ Phồn tức miền Ba Thục b&acirc;y giờ. T&acirc;y Nam l&agrave; Đại L&yacute; v&agrave; ch&iacute;nh Nam l&agrave; nước ta.<br />\r\n<br />\r\nQui m&ocirc; đ&oacute; khiến ch&uacute;ng ta được l&agrave;m một cuộc du ngoạn theo cả thời gian v&agrave; kh&ocirc;ng gian để đi ngang qua Đại L&yacute;, Tống, Li&ecirc;u, T&acirc;y Hạ, Thổ Phồn với tất cả những đặc trưng về văn h&oacute;a của thời đại v&agrave; sắc th&aacute;i địa phương. Nhiều nh&acirc;n vật ch&iacute;nh li&ecirc;n tiếp m&agrave; tuyệt nhi&ecirc;n kh&ocirc;ng c&oacute; một nh&acirc;n vật n&agrave;o ho&agrave;n to&agrave;n nổi bật v&agrave; chi phối to&agrave;n cục khiến ch&uacute;ng ta c&oacute; cảm tưởng đ&acirc;y l&agrave; nhiều truyện được nối lại với nhau. C&aacute;ch dựng truyện đ&oacute; đ&atilde; tạo n&ecirc;n một n&eacute;t đặc th&ugrave; v&agrave; đưa Kim Dung l&ecirc;n một tầm v&oacute;c ri&ecirc;ng m&agrave; kh&ocirc;ng phải ai cũng c&oacute; được.<br />\r\n<br />\r\nMặc d&ugrave; tr&ecirc;n tổng qu&aacute;t bộ truyện kh&ocirc;ng kh&aacute;c khi c&ograve;n đăng tr&ecirc;n mặt b&aacute;o bao nhi&ecirc;u nhưng nếu đi v&agrave;o chi tiết th&igrave; qu&iacute; độc giả sẽ thấy Kim Dung đ&atilde; thay đổi rất nhiều. Những chi tiết rườm r&agrave; v&agrave; kh&ocirc;ng li&ecirc;n tục đều bị cắt bỏ, trong đ&oacute; c&oacute; khoảng 40,000 chữ do Ngh&ecirc; Khu&ocirc;ng viết thay Kim Dung khi &ocirc;ng đi xa cũng bị loại đi v&agrave; nhiều đoạn mới được th&ecirc;m v&agrave;o. Những t&ecirc;n nh&acirc;n vật cũng nhiều thay đổi v&agrave; chắc chắn nhiều người sẽ đặt c&acirc;u hỏi về việc đ&oacute;. Tuy nhi&ecirc;n, một c&aacute;ch thường t&igrave;nh th&igrave; bản cuối c&ugrave;ng bao giờ cũng l&agrave; bản m&agrave; t&aacute;c giả vừa &yacute; nhất.<br />\r\n<br />\r\nBộ n&agrave;y được viết lần đầu năm 1963, đăng li&ecirc;n tục tr&ecirc;n Minh B&aacute;o ở Hongkong v&agrave; Nam Dương thương b&aacute;o ở Singapore, tổng cộng bốn năm mới ho&agrave;n th&agrave;nh. Bản tu đ&iacute;nh - tức bản đ&atilde; được Kim Dung sửa lại ho&agrave;n tất th&aacute;ng 10 năm 1978 v&agrave; ấn bản lần đầu v&agrave;o năm 1979. Cũng như mọi bộ truyện kh&aacute;c, mỗi chương c&oacute; một tấm h&igrave;nh nhưng trong Thi&ecirc;n Long B&aacute;t Bộ lại vẽ theo lối thủy mặc th&agrave;nh thử người dịch kh&ocirc;ng thể d&ugrave;ng dạng bitmap (bmp) để scan cho nhỏ m&agrave; phải d&ugrave;ng dạng jpg n&ecirc;n thường phải từ 500K trở l&ecirc;n. Việc đưa v&agrave;o mỗi chương sẽ biến file theo dạng Microsoft Word rất lớn khiến việc gửi đi trở n&ecirc;n kh&oacute; khăn, nhất l&agrave; những bằng hữu ở VN phải download rất bất tiện v&agrave; tốn k&eacute;m. Do đ&oacute; ch&uacute;ng t&ocirc;i sẽ chỉ gửi tấm h&igrave;nh đ&oacute; theo y&ecirc;u cầu v&agrave; sẽ thiết lập một danh s&aacute;ch ri&ecirc;ng cho những người n&agrave;o muốn sưu tầm cho đủ bộ. Hoặc giả ch&uacute;ng t&ocirc;i cũng c&oacute; thể gửi theo dạng pdf (phải d&ugrave;ng Acrobat Reader dể mở) nhưng dĩ nhi&ecirc;n format sẽ cố định v&agrave; người đọc muốn in ra sẽ kh&ocirc;ng thể sử dụng khổ giấy kh&aacute;c khổ 8.5 x 11 l&agrave; khổ m&agrave; người dịch d&ugrave;ng để setup tại Hoa Kỳ.<br />\r\n<br />\r\nMỗi c&acirc;u chuyện xoay quanh một chủ đề, Ỷ Thi&ecirc;n Đồ Long K&yacute; l&agrave; c&acirc;u chuyện của kiếm Ỷ Thi&ecirc;n v&agrave; đao Đồ Long c&ograve;n Thi&ecirc;n Long B&aacute;t Bộ th&igrave; n&oacute;i l&ecirc;n c&aacute;i phức tạp v&agrave; đa dạng của con người. Trước đ&acirc;y khi dịch ra tiếng Việt người ta đ&atilde; chia ra th&agrave;nh nhiều bộ nhỏ hơn (c&oacute; lẽ v&igrave; mục ti&ecirc;u thương mại) như Lục Mạch Thần Kiếm, Thi&ecirc;n Long B&aacute;t Bộ, Nhất Dương Chỉ ... nhưng nguy&ecirc;n t&aacute;c của Kim Dung chỉ c&oacute; một Thi&ecirc;n Long B&aacute;t Bộ m&agrave; th&ocirc;i. Kim Dung đ&atilde; giải th&iacute;ch c&aacute;i t&ecirc;n n&agrave;y như sau:<br />\r\n<br />\r\nThi&ecirc;n Long B&aacute;t Bộ l&agrave; chữ lấy từ kinh Phật. Trong kinh của Phật Gi&aacute;o đại thừa c&oacute; kể lại khi đức Phật thuyết ph&aacute;p cho c&aacute;c bồ t&aacute;t, t&igrave; khưu thường c&oacute; t&aacute;m loại động vật đến nghe. Kinh Ph&aacute;p Hoa, trong phẩm Đề B&agrave; Đạt Đa c&oacute; ch&eacute;p:<br />\r\n<br />\r\nThi&ecirc;n long b&aacute;t bộ, nh&acirc;n dữ phi nh&acirc;n, giai dao kiến bỉ Long Nữ th&agrave;nh Phật. (Trong t&aacute;m loại ch&uacute;ng sinh tr&ecirc;n trời, người cũng như kh&ocirc;ng phải người đều thấy được Long Nữ th&agrave;nh Phật)<br />\r\n<br />\r\nPhi nh&acirc;n l&agrave; n&oacute;i về những giống tr&ocirc;ng th&igrave; h&igrave;nh d&aacute;ng như người nhưng lại kh&ocirc;ng phải người. T&aacute;m hạng đ&oacute; đều l&agrave; &quot;phi nh&acirc;n&quot; l&agrave; những hạng thần vật m&agrave; Thi&ecirc;n v&agrave; Long đứng đầu n&ecirc;n được gọi l&agrave; Thi&ecirc;n Long B&aacute;t Bộ. T&aacute;m loại đ&oacute; gồm c&oacute;: Thi&ecirc;n, Long, Dạ Xoa, C&agrave;n Th&aacute;t B&agrave;, A Tu La, Ca L&acirc;u La, Khẩn Na La, Ma Hầu La Gia &oslash;.<br />\r\n<br />\r\n- Thi&ecirc;n l&agrave; thi&ecirc;n thần (Deva), trong Phật gi&aacute;o kh&ocirc;ng phải l&agrave; cao hơn hết nhưng chỉ được hưởng ph&uacute;c b&aacute;u l&acirc;u d&agrave;i, to lớn hơn người m&agrave; th&ocirc;i. Thi&ecirc;n thần vẫn c&ograve;n trong c&otilde;i sinh tử, c&oacute; sống c&oacute; chết. Trước khi chết thi&ecirc;n thần sẽ c&oacute; năm triệu chứng: quần &aacute;o mủn n&aacute;t, hoa tr&ecirc;n đầu h&eacute;o hon, th&acirc;n thể &ocirc; uế, n&aacute;ch chảy mồ h&ocirc;i, đứng ngồi kh&ocirc;ng y&ecirc;n l&agrave; thời kỳ đau buồn nhất của c&aacute;c thi&ecirc;n thần. C&oacute; mười hai thi&ecirc;n thần quan trọng nhất tượng trưng cho t&aacute;m hướng v&agrave; bốn tinh thể của vũ trụ mặt trời, mặt trăng, bầu trời v&agrave; mặt đất. Đế Th&iacute;ch l&agrave; l&atilde;nh tụ của mọi thi&ecirc;n thần.<br />\r\n<br />\r\n- Long l&agrave; long thần (Naga), nhưng rồng trong kinh Phật kh&ocirc;ng c&oacute; ch&acirc;n, tr&ocirc;ng giống như một con m&atilde;ng x&agrave; lớn. Quan niệm về rồng v&agrave; về Long Vương của Trung Hoa ch&iacute;nh l&agrave; vay mượn từ trong kinh Phật m&agrave; ra. Người Ấn Độ ng&agrave;y xưa rất s&ugrave;ng b&aacute;i Long Vương, cho rằng rồng l&agrave; ch&uacute;a tể c&aacute;c lo&agrave;i trong nước n&ecirc;n người Trung Hoa thường d&ugrave;ng chữ long để chỉ những g&igrave; cao qu&iacute; v&agrave; đức hạnh. Trong kinh c&oacute; ch&eacute;p l&agrave; một con rắn t&ecirc;n l&agrave; Mucilinda da cuộn th&agrave;nh một c&aacute;i t&agrave;n che cho đức Phật nhập định trong một cơn gi&ocirc;ng b&atilde;o v&agrave; nhiều con rắn thần kh&aacute;c thường giả dạng người đến để nghe Thế T&ocirc;n thuyết ph&aacute;p.<br />\r\n<br />\r\n- Dạ Xoa l&agrave; một loại quỉ thần (nguy&ecirc;n nghĩa dạ xoa l&agrave; thần ăn được quỉ), rất mẫn tiệp, nhẹ nh&agrave;ng, chia l&agrave;m ba giống ở tr&ecirc;n đất, tr&ecirc;n chốn kh&ocirc;ng hư v&agrave; tr&ecirc;n trời. Ng&agrave;y nay n&oacute;i đến dạ xoa chỉ h&agrave;m một nghĩa &aacute;c quỉ (chắc tại ảnh hưởng của truyện T&acirc;y Du K&yacute;) nhưng trong kinh Phật th&igrave; c&oacute; nhiều dạ xoa rất tốt. Dạ Xoa B&aacute;t Đại Tướng c&oacute; nhiệm vụ bảo hộ ch&uacute;ng sinh.<br />\r\n<br />\r\n- C&agrave;n Th&aacute;t B&agrave; (Apsaras) l&agrave; một giống kh&ocirc;ng ăn thịt, kh&ocirc;ng uống rượu chỉ sống bằng m&ugrave;i hương l&agrave; một trong những nhạc thần phục thị Đế Th&iacute;ch, th&acirc;n thể c&oacute; m&ugrave;i thơm. Theo nghĩa Phạn văn, C&agrave;n Th&aacute;t B&agrave; c&oacute; nghĩa l&agrave; biến ảo kh&ocirc;n lường v&igrave; m&ugrave;i hương v&agrave; &acirc;m nhạc đều m&ecirc;nh mang kh&ocirc;ng sao c&acirc;n đo được.<br />\r\n<br />\r\n- A Tu La (Ashura) l&agrave; một loại thần đặc biệt, đ&agrave;n &ocirc;ng th&igrave; thật xấu xa c&ograve;n đ&agrave;n b&agrave; th&igrave; thật xinh đẹp. A Tu La Vương thường đ&aacute;nh nhau với Đế Th&iacute;ch v&igrave; một b&ecirc;n c&oacute; đồ ăn, một b&ecirc;n c&oacute; mỹ nữ n&ecirc;n thường muốn chiếm đoạt của nhau. A Tu La t&iacute;nh t&igrave;nh đố kỵ th&iacute;ch tranh gi&agrave;nh l&agrave; một trong những ẩn dụ của nh&agrave; Phật để n&oacute;i về t&iacute;nh xấu xa của con người.<br />\r\n<br />\r\n- Ca L&acirc;u La (Garuda) l&agrave; một giống chim lớn, đầu c&oacute; một c&aacute;i bướu to gọi l&agrave; Như &Yacute; Ch&acirc;u. Lo&agrave;i chim n&agrave;y tiếng k&ecirc;u bi thảm, trong thần thoại Trung Hoa gọi l&agrave; Đại B&agrave;ng Kim S&iacute; Điểu (chim đại b&agrave;ng c&aacute;nh v&agrave;ng) m&agrave; danh tướng Nhạc Phi l&agrave; h&oacute;a th&acirc;n đầu thai. Giống chim n&agrave;y th&iacute;ch ăn rồng, mỗi ng&agrave;y phải bắt được một long vương hay 500 con rồng con để ăn thịt. V&igrave; n&oacute; ăn thịt rồng (độc x&agrave;) nhiều qu&aacute; n&ecirc;n khi chết chất độc x&ocirc;ng l&ecirc;n ch&aacute;y ti&ecirc;u th&agrave;nh tro, chỉ c&ograve;n một tr&aacute;i tim xanh biếc. H&igrave;nh của chim n&agrave;y thầy nhiều ở c&aacute;c đền thờ tại Cam Bốt.<br />\r\n<br />\r\n- Khẩn Na La giống người m&agrave; kh&ocirc;ng phải l&agrave; người, đầu c&oacute; sừng, giỏi m&uacute;a h&aacute;t cũng l&agrave; nhạc thần của Đế Th&iacute;ch.<br />\r\n<br />\r\n- Ma Hầu La Gia l&agrave; một vị thần rắn cực lớn, th&acirc;n h&igrave;nh l&agrave; người nhưng đầu l&agrave; đầu rắn.<br />\r\n<br />\r\nNhững con vật huyền thoại n&agrave;y c&oacute; lẽ l&agrave; do ảnh hưởng của những vị thần của Ấn Độ gi&aacute;o (Hindu) v&agrave; thường xen lẫn trong những truyện thần thoại của nhiều d&acirc;n tộc ở Nam v&agrave; T&acirc;y &Aacute; kh&ocirc;ng ho&agrave;n to&agrave;n l&agrave; của Phật Gi&aacute;o. Trong những tranh vẽ, đi&ecirc;u tượng của Đ&ocirc;ng &Aacute; ch&uacute;ng ta &iacute;t thấy những h&igrave;nh tướng của thi&ecirc;n long b&aacute;t bộ ngoại trừ một số b&iacute;ch họa từ đời Đường trở về trước ở T&acirc;n Cương. Người Việt ch&uacute;ng ta c&oacute; thể n&oacute;i ho&agrave;n to&agrave;n xa lạ với những h&igrave;nh ảnh n&agrave;y, ngay cả trong giới tu sĩ cũng &iacute;t người nghi&ecirc;n cứu đến.<br />\r\n<br />\r\nThi&ecirc;n Long B&aacute;t Bộ lấy địa b&agrave;n l&agrave; nước Đại L&yacute; đời Bắc Tống (v&agrave;o khoảng đời Tiền L&ecirc; v&agrave; đời L&yacute; b&ecirc;n m&igrave;nh). Nước Đại L&yacute; nằm ở ph&iacute;a T&acirc;y Trung Hoa, T&acirc;y Bắc nước ta địa b&agrave;n tỉnh V&acirc;n Nam b&acirc;y giờ, nổi tiếng với nhiều loại kỳ hoa dị thảo, đặc biệt l&agrave; tr&agrave; gồm cả tr&agrave; uống (tea) v&agrave; c&acirc;y hoa tr&agrave; (camelia). Theo sử s&aacute;ch, khoảng năm 937 Đo&agrave;n Tư B&igrave;nh sau khi chiếm được nước Nam Chiếu lập n&ecirc;n nước Đại L&yacute;. Đại L&yacute; l&agrave; một quốc gia theo Phật gi&aacute;o Mật t&ocirc;ng (Acarya), từ vua tới d&acirc;n đều s&ugrave;ng đạo, vua thường tại vị một thời gian rồi xuất gia l&agrave;m sư. Truyền thống đ&oacute; ảnh hưởng đến nước ta v&agrave; đời L&yacute; đời Trần nhiều người cũng nhường ng&ocirc;i rồi đi tu, l&agrave;m Th&aacute;i Thượng Ho&agrave;ng.<br />\r\n<br />\r\nNước Đại L&yacute; k&eacute;o d&agrave;i 316 năm, 22 đời vua trong đ&oacute; c&oacute; mười người bỏ ng&ocirc;i đi tu, hai người được đề cập trong Thi&ecirc;n Long B&aacute;t Bộ l&agrave; Bảo Định Đo&agrave;n Ch&iacute;nh Minh v&agrave; Trung T&ocirc;ng Đo&agrave;n Ch&iacute;nh Thuần. Một người kh&aacute;c cũng được Kim Dung n&oacute;i đến l&agrave; Cảnh T&ocirc;ng Đo&agrave;n Ch&iacute;nh Hưng (Kim Dung lại viết l&agrave; Đo&agrave;n Tr&iacute; Hưng) hay Đo&agrave;n Ho&agrave;ng Gia Nam Đế trong Anh H&ugrave;ng Xạ Đi&ecirc;u. &Ocirc;ng n&agrave;y cũng l&agrave; con ch&aacute;u họ Đo&agrave;n trong Thi&ecirc;n Long B&aacute;t Bộ.<br />\r\n<br />\r\nNgười Đại L&yacute; thuộc giống người Th&aacute;i, trải rộng từ V&acirc;n Nam qua bắc Th&aacute;i Lan, L&agrave;o v&agrave; thượng du Bắc Việt. Người Th&aacute;i gồm nhiều sắc d&acirc;n như Th&aacute;i Trắng, Th&aacute;i Đen, Th&aacute;i Đỏ, người Thổ, người N&ugrave;ng. Sau khi Đại L&yacute; bị người M&ocirc;ng Cổ th&ocirc;n t&iacute;nh, người Th&aacute;i c&ograve;n trốn l&aacute;nh trong rừng s&acirc;u v&agrave; di chuyển xuống ph&iacute;a Nam v&agrave; T&acirc;y Nam v&agrave; l&agrave; thủy tổ của người Th&aacute;i Lan ng&agrave;y nay.<br />\r\n<br />\r\nThi&ecirc;n Long B&aacute;t Bộ được đặt trong bối cảnh Bắc Tống, đời vua Triết T&ocirc;ng Nguy&ecirc;n T&aacute;, ni&ecirc;n hiệu Thiệu Th&aacute;nh khoảng cuối thế kỷ thứ 11. Kim Dung d&ugrave;ng c&aacute;i t&ecirc;n Thi&ecirc;n Long B&aacute;t Bộ l&agrave; để chỉ những biến h&oacute;a phức tạp của c&acirc;u chuyện, ly kỳ chẳng kh&aacute;c g&igrave; những chuyện cổ t&iacute;ch của Ấn Độ m&agrave; Phật Gi&aacute;o mượn l&agrave;m ẩn dụ. Mật t&ocirc;ng được truyền v&agrave;o Trung Hoa do ba nh&agrave; sư Thi&ecirc;n Tr&uacute;c l&agrave; Thiện V&ocirc; &Uacute;y (Subhnakarasimha), Kim Cương Tr&iacute; (Vajrabodhi) v&agrave; Bất Kh&ocirc;ng Kim Cương (Amoghavajra) v&agrave;o khoảng đầu thế kỷ thứ 8 ch&uacute; trọng đến niệm kinh, bắt ấn, sử dụng Mạn đ&agrave; la v&agrave; qu&aacute;n đỉnh. Gi&aacute;o ph&aacute;p được thầy truyền qua tr&ograve; bằng lời n&ecirc;n kh&ocirc;ng được phổ biến rộng r&atilde;i.<br />\r\n<br />\r\nPhật gi&aacute;o Mật T&ocirc;ng pha trộn đủ m&agrave;u sắc, nặng phần nghi thức, đ&ocirc;i khi m&ecirc; t&iacute;n mặc dầu những khu vực chịu ảnh hưởng của văn h&oacute;a Trung Hoa chuy&ecirc;n chở kh&aacute; nhiều t&iacute;nh nghệ thuật, điển h&igrave;nh l&agrave; những đi&ecirc;u tượng v&agrave; tranh vẽ m&agrave; người ta ph&aacute;t hiện ở V&acirc;n Nam, T&acirc;n Cương, nhất l&agrave; tại Đ&ocirc;n Ho&agrave;ng. Ch&iacute;nh v&igrave; m&agrave;u sắc nhiều thần b&iacute; của thời sơ khai, c&aacute;c t&ocirc;n gi&aacute;o bản địa nhất l&agrave; Nho gi&aacute;o đ&atilde; c&oacute; những xung khắc đ&aacute;ng kể với Phật gi&aacute;o. Đời Đường, H&agrave;n Dũ (768 - 824) đ&atilde; d&acirc;ng sớ xin ngăn cấm đạo Phật dẫn tới việc ph&aacute; hủy 5000 ch&ugrave;a lớn v&agrave; 40,000 am nhỏ. Mấy trăm ng&agrave;n tăng sĩ v&agrave; nữ ni bị bắt buộc phải ho&agrave;n tục. Điều đ&oacute; cũng kh&ocirc;ng phải l&agrave; kh&ocirc;ng hay v&igrave; ch&iacute;nh từ đ&oacute; Thiền t&ocirc;ng Trung Hoa c&oacute; cơ hội nảy nở, x&acirc;y dựng những cơ sở triết học thanh tho&aacute;t hơn v&agrave; tạo ra hẳn một trường ph&aacute;i mới, tổng hợp được nhiều ưu điểm, nhấn mạnh v&agrave;o việc &quot;kiến t&iacute;nh th&agrave;nh Phật&quot; v&agrave; xa rời việc thờ c&uacute;ng thần tượng v&agrave; nghi lễ.<br />\r\n<br />\r\nTuy nhi&ecirc;n, Kim Dung lại khai th&aacute;c được những h&igrave;nh th&aacute;i thần b&iacute; của t&ocirc;n gi&aacute;o l&agrave;m chất liệu khiến c&acirc;u chuyện trở n&ecirc;n hấp dẫn hơn. Quan &Acirc;m bồ t&aacute;t đ&atilde; được thế tục h&oacute;a qua một vương phi tự hiến th&acirc;n cho một người ăn m&agrave;y để sinh ra một đứa con hoang giải quyết hầu như to&agrave;n bộ những kh&uacute;c mắc triền mi&ecirc;n từ đầu đến cuối. Lẽ dĩ nhi&ecirc;n, chi tiết tưởng tượng kia khiến cho c&acirc;u chuyện th&ecirc;m li lỳ, tuyệt nhi&ecirc;n kh&ocirc;ng phải l&agrave; một c&aacute;ch giải th&iacute;ch những căn bản gi&aacute;o l&yacute;.<br />\r\n<br />\r\nNgo&agrave;i Phật gi&aacute;o Mật T&ocirc;ng đ&atilde; đ&oacute;ng một vai tr&ograve; kh&aacute; quan trọng l&agrave;m nền tảng cho nhiều t&igrave;nh tiết của c&acirc;u chuyện, hai đề t&agrave;i kh&aacute;c ch&uacute;ng ta cũng cần biết th&ecirc;m, mỗi đề t&agrave;i đều bao qu&aacute;t đ&aacute;ng trở th&agrave;nh một nghi&ecirc;n cứu ri&ecirc;ng. Trong phần giới thiệu n&agrave;y ch&uacute;ng t&ocirc;i chỉ đề cập đến một c&aacute;ch kh&aacute;i qu&aacute;t.<br />\r\n<br />\r\nThứ nhất l&agrave; ngo&agrave;i m&ocirc;n Nhất Dương Chỉ, họ Đo&agrave;n Đại L&yacute; c&ograve;n một tuyệt kỹ kh&aacute;c l&agrave; Lục Mạch Thần Kiếm, một loại kiếm kh&iacute; d&ugrave;ng s&aacute;u kinh mạch của hai b&agrave;n tay để sử dụng.<br />\r\n<br />\r\n- Thủ th&aacute;i &acirc;m phế kinh bắt đầu ở trung ti&ecirc;u, đi v&ograve;ng xuống ruột gi&agrave; rồi chạy l&ecirc;n ngực, yết hầu đi ra c&aacute;nh tay v&agrave; chấm dứt ở đầu ng&oacute;n tay c&aacute;i.<br />\r\n<br />\r\n- Thủ dương minh đại trường kinh bắt đầu từ đầu ng&oacute;n tay trỏ chạy l&ecirc;n vai rồi chia th&agrave;nh hai nh&aacute;nh, một nh&aacute;nh xuống ruột gi&agrave;, một nh&aacute;nh l&ecirc;n đầu chấm dứt ở cạnh mũi.<br />\r\n<br />\r\n- Thủ thiếu &acirc;m t&acirc;m kinh bắt đầu từ tim chia ra ba nh&aacute;nh, một nh&aacute;nh qua ho&agrave;nh c&aacute;ch mạc xuống ruột non, một nh&aacute;nh theo thực quản l&ecirc;n mắt, v&agrave; một nh&aacute;nh đi qua phổi, sang tay tới ng&oacute;n tay &uacute;t.<br />\r\n<br />\r\n- Thủ th&aacute;i dương tiểu trường kinh bắt đầu từ ngo&agrave;i ng&oacute;n tay &uacute;t chạy theo tay l&ecirc;n vai gặp đốc mạch ở huyệt đại truy chi th&agrave;nh hai nh&aacute;nh, một nh&aacute;nh đi xuống ruột non, một nh&aacute;nh chạy l&ecirc;n mặt đi v&agrave;o tai.<br />\r\n<br />\r\n- Thủ quyết &acirc;m bao t&acirc;m kinh bắt đầu từ ngực nối liền tam ti&ecirc;u rồi chạy ra c&aacute;nh tay tới ng&oacute;n tay giữa.<br />\r\n<br />\r\n- Thủ thiếu dương tam ti&ecirc;u kinh khởi đầu từ ng&oacute;n tay đeo nhẫn chạy theo tay l&ecirc;n vai chia th&agrave;nh hai nh&aacute;nh một nh&aacute;nh nối liền tam ti&ecirc;u, một nh&aacute;nh chạy l&ecirc;n cổ v&ograve;ng qua tai rồi ngừng lại tại mắt.<br />\r\n<br />\r\nThủ th&aacute;i &acirc;m PHẾ kinh, thủ quyết &acirc;m T&Acirc;M BAO kinh v&agrave; thủ thiếu &acirc;m T&Acirc;M kinh l&agrave; ba đường kinh &Acirc;M nằm ở b&ecirc;n trong c&aacute;nh tay, gọi l&agrave; THỦ TAM &Acirc;M thuộc l&yacute; (b&ecirc;n trong) dẫn ch&acirc;n kh&iacute; từ ngực chạy v&agrave;o tay.<br />\r\n<br />\r\nThủ dương minh ĐẠI TRƯỜNG kinh, thủ thiếu dương TAM TI&Ecirc;U kinh, v&agrave; thủ th&aacute;i dương TIỂU TRƯỜNG kinh nằm ở mặt ngo&agrave;i c&aacute;nh tay gọi l&agrave; THỦ TAM DƯƠNG, thuộc biểu (b&ecirc;n ngo&agrave;i) từ tay chạy l&ecirc;n đầu.<br />\r\n<br />\r\nThứ hai một đặc sản của nước Đại L&yacute; l&agrave; hoa tr&agrave; cũng được nhắc tới kh&aacute; nhiều. Khi đề cập tới v&ugrave;ng V&acirc;n Nam, người Trung Hoa vẫn thường tự h&agrave;o l&agrave; ở đ&acirc;y c&oacute; t&aacute;m loại danh chủng nổi tiếng tr&ecirc;n thế giới gồm c&oacute; sơn tr&agrave; (camelia), đỗ quy&ecirc;n (azalea), mộc lan (magnolia obovata), b&aacute;o xu&acirc;n, b&aacute;ch hợp (lily), phương lan (cymbidium), long đảm (gentian), lục nhung hao.<br />\r\n<br />\r\nSơn tr&agrave; của Đại L&yacute; đ&atilde; biến c&acirc;u chuyện trở n&ecirc;n thơ mộng v&agrave; th&uacute; vị khi ch&uacute;ng ta nghe Đo&agrave;n Dự luận về hoa tr&agrave; với Vương phu nh&acirc;n sau đ&acirc;y:<br />\r\n<br />\r\nVương phu nh&acirc;n vẻ mặt đắc &yacute;, n&oacute;i:<br />\r\n<br />\r\n- Đo&agrave;n c&ocirc;ng tử, nước Đại L&yacute; của cậu hoa tr&agrave; rất nhiều, nhưng nếu so s&aacute;nh với của ta chắc kh&ocirc;ng thể b&igrave; kịp.<br />\r\n<br />\r\nĐo&agrave;n Dự gật đầu:<br />\r\n<br />\r\n- Những hoa tr&agrave; loại n&agrave;y d&acirc;n Đại L&yacute; ch&uacute;ng t&ocirc;i chẳng ai trồng cả.<br />\r\n<br />\r\nVương phu nh&acirc;n cười khanh kh&aacute;ch:<br />\r\n<br />\r\n- Thật ư?<br />\r\n<br />\r\nĐo&agrave;n Dự n&oacute;i:<br />\r\n<br />\r\n- Những người d&acirc;n qu&ecirc; m&ugrave;a tầm thường ở Đại L&yacute; cũng biết c&aacute;c loại hoa n&agrave;y phẩm chất k&eacute;m cỏi, trồng chẳng cao sang g&igrave;.<br />\r\n<br />\r\nVương phu nh&acirc;n mặt biến sắc, giận dữ n&oacute;i:<br />\r\n<br />\r\n- Ngươi n&oacute;i sao? Ngươi bảo hoa tr&agrave; ta trồng l&agrave; loại tục phẩm ư? N&oacute;i như thế chẳng h&oacute;a, chẳng h&oacute;a .... khinh người qu&aacute; lắm.<br />\r\n<br />\r\nĐo&agrave;n Dự n&oacute;i:<br />\r\n<br />\r\n- Phu nh&acirc;n tin hay kh&ocirc;ng tin cũng t&ugrave;y ở người.<br />\r\n<br />\r\nCh&agrave;ng vừa n&oacute;i vừa chỉ một c&acirc;y hoa tr&agrave; ngũ sắc trước lầu:<br />\r\n<br />\r\n- Gi&aacute; thử như c&acirc;y n&agrave;y, b&agrave; tưởng n&oacute; qu&iacute; lắm ư. Hứ, đẹp thật, c&aacute;i lan can n&agrave;y thật l&agrave; đẹp, thật l&agrave; đẹp.<br />\r\n<br />\r\nCh&agrave;ng tấm tắc khen c&aacute;i lan can đẹp m&agrave; hoa th&igrave; chẳng nhắc đến một c&acirc;u kh&aacute;c n&agrave;o người xem chữ viết, kh&ocirc;ng nhắc đến chữ m&agrave; chỉ khen lấy khen để mực đen v&agrave; giấy tốt.<br />\r\n<br />\r\nC&acirc;y hoa tr&agrave; đ&oacute; vừa trắng vừa đỏ, vừa t&iacute;a vừa v&agrave;ng, sắc hoa thật l&agrave; rực rỡ, từ trước tới nay Vương phu nh&acirc;n vẫn coi l&agrave; tr&acirc;n phẩm. Nay thấy Đo&agrave;n Dự c&oacute; vẻ xem thường n&ecirc;n l&ocirc;ng m&agrave;y b&agrave; nhướng l&ecirc;n, mắt lộ s&aacute;t &yacute;. Đo&agrave;n Dự n&oacute;i:<br />\r\n<br />\r\n- Xin hỏi phu nh&acirc;n, giống hoa n&agrave;y ở Giang Nam gọi t&ecirc;n l&agrave; g&igrave;?<br />\r\n<br />\r\nVương phu nh&acirc;n hậm hực đ&aacute;p:<br />\r\n<br />\r\n- Ch&uacute;ng t&ocirc;i chẳng gọi t&ecirc;n g&igrave; đặc biệt, chỉ gọi l&agrave; hoa tr&agrave; Ngũ Sắc.<br />\r\n<br />\r\nĐo&agrave;n Dự mỉm cười:<br />\r\n<br />\r\n- Đại L&yacute; ch&uacute;ng t&ocirc;i c&oacute; đặt cho n&oacute; một c&aacute;i t&ecirc;n, gọi l&agrave; Lạc Đệ T&uacute; T&agrave;i (thư sinh thi hỏng).<br />\r\n<br />\r\nVương phu nh&acirc;n hừ một tiếng:<br />\r\n<br />\r\n- C&aacute;i t&ecirc;n sao kh&oacute; nghe l&agrave;m vậy, chắc ngươi tiện mồm đặt ra chứ g&igrave;. C&acirc;y hoa đẹp đẽ như thế, sao lại giống g&atilde; thư sinh thi hỏng?<br />\r\n<br />\r\n- Phu nh&acirc;n thử nh&igrave;n lại xem, c&acirc;y hoa n&agrave;y cả thảy tất cả mấy m&agrave;u.<br />\r\n<br />\r\nVương phu nh&acirc;n n&oacute;i:<br />\r\n<br />\r\n- Ta đ&atilde; đếm qua, cũng phải c&oacute; đến 15, 16 m&agrave;u kh&aacute;c nhau.<br />\r\n<br />\r\nĐo&agrave;n Dự n&oacute;i:<br />\r\n<br />\r\n- Cả thảy đ&uacute;ng ra l&agrave; 17 m&agrave;u. Đại L&yacute; c&oacute; một loại hoa qu&iacute; gi&aacute; nổi tiếng t&ecirc;n gọi Thập B&aacute;t Học Sĩ. Đ&oacute; l&agrave; thi&ecirc;n hạ cực phẩm, tr&ecirc;n c&acirc;y c&oacute; đủ 18 đ&oacute;a hoa, mỗi đ&oacute;a một m&agrave;u kh&aacute;c nhau, hồng th&igrave; to&agrave;n hồng, t&iacute;a th&igrave; to&agrave;n t&iacute;a, kh&ocirc;ng một mảy lẫn lộn. Nhưng cả 18 đ&oacute;a hoa mỗi đ&oacute;a h&igrave;nh trạng kh&aacute;c nhau, mỗi đ&oacute;a một vẻ, nở th&igrave; c&ugrave;ng nở, t&agrave;n th&igrave; c&ugrave;ng t&agrave;n, phu nh&acirc;n đ&atilde; thấy bao giờ chưa?<br />\r\n<br />\r\nVương phu nh&acirc;n nghe qua thẫn thờ, lắc đầu:<br />\r\n<br />\r\n- Tr&ecirc;n đời n&agrave;y lại c&oacute; loại hoa như thế sao? Đến nghe n&oacute;i tới ta cũng chưa nghe bao giờ.<br />\r\n<br />\r\nĐo&agrave;n Dự n&oacute;i:<br />\r\n<br />\r\n- So với Thập B&aacute;t Học Sĩ k&eacute;m một mức l&agrave; Thập Tam Th&aacute;i Bảo l&agrave; 13 đ&oacute;a hoa kh&aacute;c nhau tr&ecirc;n c&ugrave;ng một c&acirc;y. Rồi đến B&aacute;t Ti&ecirc;n Qu&aacute; Hải l&agrave; 8 đ&oacute;a kh&aacute;c nhau một c&acirc;y. Thất Ti&ecirc;n Nữ l&agrave; 7 đ&oacute;a. Phong Trần Tam Hiệp l&agrave; ba đ&oacute;a. Nhị Kiều l&agrave; một trắng một đỏ hai đ&oacute;a. N&oacute;i đến hoa tr&agrave; l&agrave; phải thuần sắc, nếu như trong đỏ c&oacute; lẫn trắng, trong trắng lẫn t&iacute;a th&igrave; l&agrave; hạ phẩm rồi.<br />\r\n<br />\r\nVương phu nh&acirc;n bỗng nhi&ecirc;n đờ đẫn, nh&igrave;n về xa xăm, n&oacute;i một m&igrave;nh:<br />\r\n<br />\r\n- Sao y kh&ocirc;ng n&oacute;i cho ta biết nhỉ?&nbsp;<br />\r\n<br />\r\nĐo&agrave;n Dự n&oacute;i tiếp:<br />\r\n<br />\r\n- Trong B&aacute;t Ti&ecirc;n Qu&aacute; Hải c&oacute; cả hoa m&agrave;u t&iacute;a sậm v&agrave; m&agrave;u hồng nhạt, đ&oacute; l&agrave; L&yacute; Thiết Qu&agrave;i v&agrave; H&agrave; Ti&ecirc;n C&ocirc;, nếu thiếu hai m&agrave;u đ&oacute;, d&ugrave; l&agrave; t&aacute;m m&agrave;u kh&aacute;c nhau cũng kh&ocirc;ng được gọi l&agrave; B&aacute;t Ti&ecirc;n Qu&aacute; Hải m&agrave; gọi l&agrave; B&aacute;t Bảo Trang, tuy cũng l&agrave; danh chủng nhưng so với B&aacute;t Ti&ecirc;n Qu&aacute; Hải c&ograve;n thua một bậc.<br />\r\n<br />\r\nVương phu nh&acirc;n n&oacute;i:<br />\r\n<br />\r\n- Nguy&ecirc;n lai như thế.<br />\r\n<br />\r\nĐo&agrave;n Dự lại tiếp:<br />\r\n<br />\r\n- Trở lại Phong Trần Tam Hiệp cũng c&oacute; ch&iacute;nh phẩm v&agrave; ph&oacute; phẩm. Nếu l&agrave; ch&iacute;nh phẩm, trong ba đ&oacute;a th&igrave; đ&oacute;a m&agrave;u t&iacute;a phải to nhất, đ&oacute; l&agrave; Cầu Nhi&ecirc;m Kh&aacute;ch, đ&oacute;a m&agrave;u trắng nhỏ hơn, đ&oacute; l&agrave; L&yacute; Tịnh, đ&oacute;a m&agrave;u hồng nhỏ nhất nhưng cũng kiều diễm nhất đ&oacute; l&agrave; Hồng Phất Nữ. Nếu như hoa m&agrave;u hồng lại to hơn hoa m&agrave;u t&iacute;a, hoa m&agrave;u trắng th&igrave; l&agrave; ph&oacute; phẩm, hai đằng một trời một vực.<br />\r\n<br />\r\nNgười ta đ&atilde; bảo quen thuộc như đồ trong nh&agrave; m&igrave;nh. Trong nh&agrave; Đo&agrave;n Dự c&oacute; tất cả những loại n&agrave;y n&ecirc;n ch&agrave;ng n&oacute;i ra vanh v&aacute;ch đ&acirc;u ra đấy. Vương phu nh&acirc;n nghe thật th&iacute;ch th&uacute; thở d&agrave;i:<br />\r\n<br />\r\n- Đến ph&oacute; phẩm ta cũng chưa được nh&igrave;n qua n&oacute;i g&igrave; ch&iacute;nh phẩm !<br />\r\n<br />\r\nĐo&agrave;n Dự chỉ v&agrave;o c&acirc;y hoa tr&agrave; ngũ sắc n&oacute;i:<br />\r\n<br />\r\n- C&ograve;n loại hoa tr&agrave; n&agrave;y, nếu luận về sắc so với Thập B&aacute;t Học Sĩ c&ograve;n k&eacute;m một, lại kh&ocirc;ng thuần, nở th&igrave; khi sớm khi muộn, hoa th&igrave; đ&oacute;a nhỏ đ&oacute;a to. Chẳng kh&aacute;c g&igrave; Đ&ocirc;ng Thi giả nhăn mặt, học đ&ograve;i l&agrave;m Thập B&aacute;t Học Sĩ th&igrave; kh&ocirc;ng xong. V&igrave; thế ch&uacute;ng t&ocirc;i đặt cho c&aacute;i t&ecirc;n Lạc Đệ T&uacute; T&agrave;i.<br />\r\n<br />\r\nQuả thật khi đọc xong đoạn n&agrave;y, ch&uacute;ng t&ocirc;i cũng t&ograve; m&ograve; đi t&igrave;m t&agrave;i liệu để xem những g&igrave; Kim Dung viết ra l&agrave; c&oacute; thực hay chỉ do &oacute;c tưởng tượng phong ph&uacute; của &ocirc;ng nhưng đến nay vẫn chưa c&oacute; kết quả như &yacute;. Theo những s&aacute;ch vở ch&uacute;ng t&ocirc;i c&oacute; trong tay, đặc biệt l&agrave; cuốn V&acirc;n Nam Phong Vật Ch&iacute; (Dư Gia Hoa, V&acirc;n Nam Nh&acirc;n D&acirc;n xb 1984) trong đ&oacute; c&oacute; những chương nghi&ecirc;n cứu kh&aacute; c&ocirc;ng phu về hoa tr&agrave; nhưng kh&ocirc;ng thấy n&oacute;i đến những chủng loại m&agrave; Kim Dung đ&atilde; đề cập.<br />\r\n<br />\r\nTr&ecirc;n thế giới c&oacute; chừng hơn t&aacute;m mươi loại hoa tr&agrave; (kh&ocirc;ng kể những giống được lai tạo) th&igrave; V&acirc;n Nam đ&atilde; c&oacute; khoảng ba mươi lăm loại. Trong những loại hoa tr&agrave; danh tiếng ở V&acirc;n Nam người ta thấy c&oacute; đồng tử diện, tuyết sư, t&ugrave;ng tử l&acirc;n, tử b&agrave;o, kim đ&aacute;i, ng&acirc;n đ&aacute;i, mẫu đơn tr&agrave;, hận thi&ecirc;n cao, điệp s&iacute;, liễu diệp ng&acirc;n hồng, tảo đ&agrave;o hồng, kim bi&ecirc;n mẫu đơn, th&ocirc;ng thảo phiến ... mỗi loại c&oacute; một đặc điểm.<br />\r\n<br />\r\nHiện nay tại nhiều danh thắng, cổ t&iacute;ch tại hai tỉnh V&acirc;n - Qu&iacute; c&ograve;n nhiều c&acirc;y tr&agrave; cổ thụ, mỗi năm ra h&agrave;ng vạn đ&oacute;a hoa, chẳng hạn như tại ch&ugrave;a Ngọc Phong b&ecirc;n bờ s&ocirc;ng Lệ ch&acirc;n n&uacute;i Ngọc Long c&ograve;n một c&acirc;y tr&agrave; k&eacute;p d&iacute;nh liền hai loại với nhau n&ecirc;n m&agrave;u sắc rất phong ph&uacute;. C&acirc;y tr&agrave; n&agrave;y trồng từ đời Minh Th&agrave;nh H&oacute;a (thế kỷ thứ 15) t&iacute;nh ra đ&atilde; hơn 500 năm được mệnh danh l&agrave; &quot;đệ nhất ho&agrave;n cầu&quot;. Tần Nh&acirc;n Xương, một thực vật gia Trung Hoa đ&atilde; n&oacute;i:<br />\r\n<br />\r\n- Vua c&aacute;c lo&agrave;i tr&agrave; tr&ecirc;n thế giới l&agrave; ở Trung Hoa, vua c&aacute;c lo&agrave;i tr&agrave; của Trung Hoa l&agrave; ở V&acirc;n Nam, vua c&aacute;c lo&agrave;i tr&agrave; của V&acirc;n Nam l&agrave; hoa tr&agrave; ở Lệ Giang.<br />\r\n<br />\r\nQu&aacute;ch Mạt Nhược, một thi sĩ nổi tiếng của Trung Hoa đầu thế kỷ 20 khi đến V&acirc;n Nam đ&atilde; cảm t&aacute;c một b&agrave;i thơ như sau:<br />\r\n<br />\r\nNhững tưởng tr&agrave; kia ưa qu&iacute; tộc,<br />\r\n<br />\r\nAi ngờ d&acirc;n d&atilde; cũng đầy s&acirc;n.<br />\r\n<br />\r\nMẫu đơn ai nấy đều ca tụng,<br />\r\n<br />\r\nTa nghĩ hoa tr&agrave; trội mẫu đơn.<br />\r\n<br />\r\n(Diễm thuyết tr&agrave; hoa thị tỉnh hoa,<br />\r\n<br />\r\nKim lai thủy kiến m&atilde;n th&agrave;nh h&agrave;.<br />\r\n<br />\r\nNh&acirc;n nh&acirc;n đ&ocirc; đạo mẫu đơn hảo,<br />\r\n<br />\r\nNg&atilde; đạo mẫu đơn bất cập tr&agrave;)<br />\r\n<br />\r\nKhi l&agrave;m một số nghi&ecirc;n cứu nho nhỏ về đất V&acirc;n Nam - Qu&iacute; Ch&acirc;u, người dịch đ&ocirc;i khi cũng thấy bồi hồi khi đọc tới những chi tiết địa phương rất gần gũi với người Việt ch&uacute;ng ta, nhất l&agrave; về một số sắc tộc thiểu số m&agrave; nhiều học giả cho rằng c&ugrave;ng giống với người m&igrave;nh. Chắc chắn nếu c&oacute; những khảo s&aacute;t qui m&ocirc;, ch&uacute;ng ta c&oacute; thể t&igrave;m được kh&aacute; nhiều di t&iacute;ch văn h&oacute;a c&ograve;n lưu lại ở miền nam Trung Hoa để c&oacute; c&aacute;i nh&igrave;n ch&iacute;nh x&aacute;c hơn về lịch sử, truyền thống v&agrave; nguồn gốc của d&acirc;n tộc Việt Nam. Một người bạn t&ocirc;i sau những chuyến du lịch ở Quảng Ch&acirc;u, đi v&agrave;o những v&ugrave;ng xa x&ocirc;i hẻo l&aacute;nh để nghi&ecirc;n cứu về &acirc;m nhạc, đối chiếu với những điệu d&acirc;n ca của m&igrave;nh đ&atilde; phải ngạc nhi&ecirc;n về sự tương đồng kh&oacute; hiểu giữa sinh hoạt của người T&agrave;u ở Nam Trung Hoa v&agrave; người miền Bắc nước ta. Biết đ&acirc;u trong khi dịch Thi&ecirc;n Long B&aacute;t Bộ ch&uacute;ng ta sẽ c&ugrave;ng nhau t&igrave;m ra được một điểm n&agrave;o mới mẻ chăng?<br />\r\n<br />\r\nCũng như Ỷ Thi&ecirc;n Đồ Long K&yacute;, ch&uacute;ng t&ocirc;i mong mỏi được sự tiếp tay của qu&iacute; bằng hữu trong mọi l&atilde;nh vực để c&ugrave;ng nhau chia xẻ niềm vui khi đọc Thi&ecirc;n Long B&aacute;t Bộ.</p>\r\n\r\n<p><iframe align=\"top\" frameborder=\"0\" height=\"250\" hspace=\"0\" id=\"adnzone_30176_0_132787\" marginheight=\"0\" name=\"adnzone_30176_0_132787\" scrolling=\"No\" src=\"javascript:if(typeof(adnzone30176)!=\'undefined\'){adnzone30176.renderIframe();}else{parent.adnzone30176.renderIframe();}\" vspace=\"0\" width=\"300\"></iframe></p>\r\n\r\n<p><iframe align=\"top\" frameborder=\"0\" height=\"250\" hspace=\"0\" id=\"adnzone_29388_0_134489\" marginheight=\"0\" name=\"adnzone_29388_0_134489\" scrolling=\"No\" src=\"javascript:if(typeof(adnzone29388)!=\'undefined\'){adnzone29388.renderIframe();}else{parent.adnzone29388.renderIframe();}\" vspace=\"0\" width=\"300\"></iframe></p>\r\n\r\n<p><a id=\"loadListChap\" name=\"loadListChap\">&nbsp;DS Chương</a><a href=\"javascript:void(0);\" id=\"chaperror\" onclick=\"report_chapter()\" rel=\"nofollow\" title=\"report chapter\">&nbsp;B&aacute;o Lỗi</a><a href=\"https://webtruyen.com/thien-long-bat-bo-ban-moi/thanh-sam-loi-lac-hiem-phong-hanh_491588.html\" id=\"nextchap\" title=\"Thanh Sam Lỗi Lạc Hiểm Phong Hành\">Chương&nbsp;Sau&nbsp;</a></p>\r\n\r\n<p><a href=\"https://webtruyen.com/truyen-full/\" target=\"_blank\" title=\"truyen full\"><img alt=\"truyện full\" src=\"https://webtruyen.com/quangcao/truyen-full.jpg\" /></a></p>\r\n\r\n<p>TRUYỆN KIẾM HIỆP HOT</p>\r\n\r\n<ul>\r\n	<li>\r\n	<h3><a href=\"https://webtruyen.com/kiem-dao-doc-ton/\" title=\"Kiếm Đạo Độc Tôn\">Kiếm Đạo Độc T&ocirc;n</a></h3>\r\n	</li>\r\n	<li>\r\n	<h3><a href=\"https://webtruyen.com/ma-ngan/\" title=\"Ma Ngân\">Ma Ng&acirc;n</a></h3>\r\n	</li>\r\n	<li>\r\n	<h3><a href=\"https://webtruyen.com/ma-thien-ky/\" title=\"Ma Thiên Ký\">Ma Thi&ecirc;n K&yacute;</a></h3>\r\n	</li>\r\n	<li>\r\n	<h3><a href=\"https://webtruyen.com/chua-te-chi-vuong/\" title=\"Chúa Tể Chi Vương\">Ch&uacute;a Tể Chi Vương</a></h3>\r\n	</li>\r\n	<li>\r\n	<h3><a href=\"https://webtruyen.com/ngao-thien-cuong-ton