INSERT INTO `lesson` VALUES ('97', 'bài tập trắc nghiệm', '', '1548771837', null, '94', '39', '1', '94', '94', '1', '', '10', '1', null, null, null, null, null);
INSERT INTO `lesson` VALUES ('99', 'CHƯƠNG I : BIẾN CỐ NGẪU NHIÊN', '<p><span style=\"font-size:14pt\"><strong>89+Kh&aacute;i niệm x&aacute;c suất:</strong></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">X&aacute;c suất của m&ocirc;t biến cố l&agrave; một gi&aacute; trị bằng số đặc trưng cho khả năng kh&aacute;ch quan được xuất hiện của biến cố đ&oacute; trong kết quả của ph&eacute;p thử.</span></p>\r\n\r\n<p><span style=\"font-size:14pt\"><strong>+ Định nghĩa cổ điển về x&aacute;c suất:</strong></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">Giả sử trong một ph&eacute;p thử c&oacute; tất cả n kết cục đồng khả năng, trong đ&oacute; c&oacute; m kết cục thuận lợi cho biến cố A, v&agrave; n l&agrave; số kết cục đồng khả năng c&oacute; thể xảy ra trong ph&eacute;p thử. Khi đ&oacute;:</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">X&aacute;c suất của biến cố A, k&iacute; hiệu P(A) =mn</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">VD:</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">Một l&ocirc; h&agrave;ng c&oacute; 10 ch&iacute;nh phẩm, 4 phế phẩm.</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">Lấy ngẫu nhi&ecirc;n 3 sản phẩm.</span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:14pt\">T&iacute;nh x&aacute;c suất để 3 sản phẩm đ&oacute; đều l&agrave; ch&iacute;nh phẩm.</span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:14pt\">LG:</span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:14pt\">Gọi A: &ldquo;ba sản phẩm lấy ra đều l&agrave; ch&iacute;nh phẩm&rdquo;</span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:14pt\">Số kết cục thuận lợi cho A l&agrave; m = C103</span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:14pt\">Số kết cục đồng khả năng xảy ra l&agrave; n =&nbsp;C143</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:14pt\">P(A) = C103C143</span><span style=\"font-size:14pt\">=&nbsp;3091</span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14pt\">Giải th&iacute;ch: v&igrave; trong 14 sản phẩm c&oacute;&nbsp; 10 ch&iacute;nh phẩm</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">- lấy ngẫu nhi&ecirc;n 3 sản phẩm đều l&agrave; ch&iacute;nh phẩm n&ecirc;n 3 sản phẩm đ&oacute; l&agrave; từ 10 cp. Ta c&oacute; C103</span><span style=\"font-size:14pt\">&nbsp;c&aacute;ch chọn ngẫu nhi&ecirc;n 3 trong 10 cp.</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">-số kết cục đồng khả năng xảy ra nghĩa l&agrave; ngo&agrave;i trường hợp 3 sp đ&oacute; đều l&agrave; cp th&igrave; 3 sp ấy c&oacute; thể c&oacute; cả cp v&agrave; pp. n&ecirc;n ta chọn ngẫu nhi&ecirc;n&nbsp; 3 sp trong tổng 14 sp l&agrave;&nbsp;C143</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14pt\"><strong>+Biến cố xung khắc:</strong></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">Hai biến cố A v&agrave; B gọi l&agrave; xung khắc với nhau nếu ch&uacute;ng kh&ocirc;ng thể đồng thời xảy ra trong một ph&eacute;p thử, tức l&agrave; A.B =V</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">VD: Tung một đồng xu </span></p>\r\n\r\n<p><span style=\"font-size:14pt\">A l&agrave; biến cố &ldquo;Xuất hiện mặt ngửa&rdquo;</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">B l&agrave; biến cố &ldquo;Xuất hiện mặt sấp&rdquo;</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:14pt\">A,B l&agrave; hai biến cố xung khắc.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14pt\">Giải th&iacute;ch: V l&agrave; biến cố kh&ocirc;ng thể c&oacute;, l&agrave; biến cố chắc chắn kh&ocirc;ng xảy ra khi thực hiện ph&eacute;p thử.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14pt\"><strong>+ Biến cố đối lập:</strong></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">Hai biến cố A v&agrave; B được gọi l&agrave; đối lập với nhau nếu ch&uacute;ng lập th&agrave;nh một hệ đầy đủ.</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">K&iacute; hiệu biến cố đối lập của biến cố A l&agrave;&nbsp;A</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">VD:</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">C&oacute; 2 ch&iacute;nh phẩm, 3 phế phẩm.</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">Lấy ngẫu nhi&ecirc;n 3 sản phẩm.</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">A l&agrave; biến cố &ldquo;c&oacute; nhiều nhất 1 phế phẩm trong 3 sản phẩm lấy ra&rdquo;</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:14pt\">A&nbsp;l&agrave; biến cố c&oacute; &iacute;t nhất 2 phế phẩm trong 3 sản phẩm lấy ra&rdquo;</span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14pt\">Giải th&iacute;ch: lấy 3 sản phẩm ra th&igrave; số phế phẩm&nbsp;c&oacute; trong đ&oacute; c&oacute; thể l&agrave; 0;1;2;3</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:14pt\">Đ&oacute; l&agrave; tất cả khả năng c&oacute; thể xảy ra</span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:14pt\">Biến cố A l&agrave; c&aacute;c trường hợp 0;1</span></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:14pt\">A&macr;&nbsp;l&agrave; c&aacute;c trường hợp 2;3</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:14pt\">Hai biến cố n&agrave;y đ&atilde; chỉ ra hết tất cả trường hợp</span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:14pt\">( Hiểu n&ocirc;m na l&agrave; lấy all trường hợp trừ A th&igrave; được A ngang ^^)</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14pt\"><strong>+Biến cố độc lập:</strong></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">Hai biến cố A v&agrave; B được gọi l&agrave; độc lập với nhau nếu việc xuất hiện hay kh&ocirc;ng xuất hi&ecirc;n của biến cố n&agrave;y kh&ocirc;ng ảnh hưởng đến x&aacute;c suất của biến cố c&ograve;n lại.</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">VD:</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">Một cầu thủ s&uacute;t 5 lần độc lập v&agrave;o g&ocirc;n.</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">Ai l&agrave; biến cố &ldquo;lần thứ i s&uacute;t tr&uacute;ng&rdquo;</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">=&gt;A1; A2; A3;A4;A5 l&agrave; c&aacute;c biến cố độc lập.</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">Giải th&iacute;ch: lần&nbsp; s&uacute;t tr&uacute;ng hay kh&ocirc;ng, th&igrave; lần tiếp vẫn chỉ xảy ra 2 TH l&agrave; v&agrave;o hoặc kh&ocirc;ng, n&ecirc;n n&oacute; chả ảnh hưởng g&igrave; đến nhau.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14pt\"><strong>+ Hệ đầy đủ c&aacute;c biến cố:</strong></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">H1;H2;&hellip;.;Hn được gọi l&agrave; một hệ đầy đủ c&aacute;c biến cố nếu ch&uacute;ng xung khắc từng đ&ocirc;i v&agrave; khi ph&eacute;p thử thực hiện một trong những biến cố tr&ecirc;n nhất định xảy ra.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14pt\">VD: Bắn sung v&agrave;o bia 5 lần</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">Hi l&agrave; biến cố &ldquo; c&oacute; I lần bắn tr&uacute;ng bia&rdquo; (i=0;5&macr;</span><span style=\"font-size:14pt\">)</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:14pt\">H0;H1;&hellip;;H5 tạo th&agrave;nh một hệ đầy đủ.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14pt\"><strong>+ Định l&yacute; nh&acirc;n x&aacute;c suất:</strong></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">P(A.B) = P(A).P(B/A)</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">-Hệ quả:</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">1.Nếu P(B) &gt; 0 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; th&igrave; P(A/B) =&nbsp;P(A.B)P(B)</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">2.Nếu A, B <strong>độc lập</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p>\r\n\r\n<p><span style=\"font-size:14pt\">&nbsp;P(A.B) = P(A).P(B)</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">3. Nếu&nbsp;A1;&nbsp;A2;.....;An&nbsp;<strong>kh&ocirc;ng độc lập:</strong></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">P(A1.A2.....An) = P(A1).P(A2/A1)&hellip;..P(An).P(An/A1.A2....An)</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">4. Nếu A1;A2;&hellip;.;An <strong>độc lập:</strong></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">P(A1.A2&hellip;&hellip;An) = P(A1).P(A2)&hellip;..P(An).</span></p>\r\n\r\n<p><span style=\"font-size:14pt\"><strong>+ Cộng x&aacute;c suất:</strong></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">P(A+B) = P(A) + P(B) &ndash; P(A.B)</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">-Hệ quả:</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">1. Nếu A,B xung khắc:</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">P(A+B) = P(A) + P(B)</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">2. Nếu&nbsp;A1;A2;A3.....;An &nbsp;xung khắc từng đ&ocirc;i một th&igrave;:</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">P(A1+A2+A3.....+An) = P(A1) + P(A2)+&hellip;..+P(An).</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">3. P(A) = 1 &ndash; P(B)</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">4. Nếu&nbsp;A1;A2;A3.....;An tạo th&agrave;nh hệ đầy đủ </span></p>\r\n\r\n<p><span style=\"font-size:14pt\">P(A1) + P(A2)+&hellip;..+P(An) = 1</span></p>\r\n\r\n<p><span style=\"font-size:14pt\"><strong>+C&ocirc;ng thức đầy đủ:</strong></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">Giả sử&nbsp;H1;H2;H3;.....;Hn l&agrave; một hệ đầy đủ c&aacute;c biến cố v&agrave; biến cố A c&oacute; thể xảy ra đồng thời với một trong c&aacute;c biến cố n&agrave;y, khi đ&oacute;:</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">P(A) =&nbsp;&sum;i=1n&nbsp;P(Hi).&nbsp;P(AHi)</span></p>\r\n\r\n<p><span style=\"font-size:14pt\"><strong>+C&ocirc;ng thức BAYES:</strong></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">Giả sử&nbsp;H1;H2;H3;.....;Hn l&agrave; một hệ đầy đủ c&aacute;c biến cố v&agrave; biến cố A c&oacute; thể xảy ra đồng thời với 1 trong c&aacute;c biến cố n&agrave;y v&agrave; P(A) &gt;0. Khi đ&oacute;:</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">P(Hi/A) =&nbsp;&nbsp;P(Hi).&nbsp;P(AHi)P(A)&nbsp;&nbsp;&nbsp;&nbsp;với&nbsp;(i=0;n)</span></p>\r\n\r\n<p>&nbsp;</p>', '1550110793', null, '0', '40', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('101', 'CHƯƠNG II: ĐẠI LƯỢNG NGÃU NHIÊN VÀ QUY LUẬT PHÂN PHỐI XÁC SUẤT CỦA NÓ', '<p><span style=\"font-size:14pt\"><strong>+ Đại lượng ngẫu nhi&ecirc;n </strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:14pt\">Đ/N: &nbsp;Đại lượng ngẫu nhi&ecirc;n l&agrave; đại lượng m&agrave; n&oacute; c&oacute; thể nhận một gi&aacute; trị bất kỳ trong tập c&aacute;c gi&aacute; trị c&oacute; thể c&oacute;.</span></li>\r\n	<li><span style=\"font-size:14pt\">Ph&acirc;n loại ĐLNN:&nbsp; 2 loại</span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:14pt\">*ĐLNN rời rạc:</span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:14pt\">&nbsp;ĐLNN X được gọi l&agrave; ĐLNN rời rạc nếu n&oacute; nh&acirc;n hữu hạn hoặc v&ocirc; hạn đếm được c&aacute;c gi&aacute; trị.</span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:14pt\"><u>V&iacute; dụ:</u> Một xạ thủ bắn li&ecirc;n tiếp v&agrave;o một bia đạn đến khi tr&uacute;ng bia thi dưng lại, c&aacute;c l&acirc;n bắn độc l&acirc;p nhau. Gọi Y la số vi&ecirc;n đạn xạ thủ đ&oacute; đ&atilde; bắn ra.</span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:14pt\">Th&igrave; Y l&agrave;&nbsp;một ĐLNN rời rạc, c&oacute; thể nhận một trong c&aacute;c gi&aacute; trị 1;2;3;&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; *ĐLNN li&ecirc;n tục: </span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:14pt\">ĐLNN X được gọi l&agrave; ĐLNN li&ecirc;n tục nếu c&aacute;c gi&aacute; trị của n&oacute; c&oacute; thể lấp đ&acirc;y một khoảng tr&ecirc;n trục số.</span></p>\r\n\r\n<p><span style=\"font-size:14pt\"><u>V&iacute; dụ:</u>&nbsp;Một xạ thủ bắn li&ecirc;n tiếp v&agrave;o một bia đạn. Gọi Z la khoảng c&aacute;ch tư điểm chạm của vi&ecirc;n đạn đến t&acirc;m bia thi Z&nbsp; l&agrave; ĐLNN li&ecirc;n tục.</span></p>\r\n\r\n<p style=\"margin-left:6px\"><span style=\"font-size:14pt\"><strong>+H&agrave;m mật độ x&aacute;c suất:</strong></span></p>\r\n\r\n<p style=\"margin-left:6px\"><span style=\"font-size:14pt\">Đ/N: Nếu X l&agrave; ĐLNN li&ecirc;n tục c&oacute; h&agrave;m mật độ x&aacute;c suất F(x) khả vi tai mọi x thi khi đ&oacute; X c&oacute; ham mật độ x&aacute;c suất f(x) được x&aacute;c định như sau:</span></p>\r\n\r\n<p style=\"margin-left:6px\"><span style=\"font-size:14pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; f(x)<sub> </sub>=<sub>&nbsp; </sub>F&rsquo;(x)</span></p>\r\n\r\n<p style=\"margin-left:6px\"><span style=\"font-size:14pt\">T&iacute;nh chất:</span></p>\r\n\r\n<p>1.&nbsp;f(x)&nbsp;&ge;&nbsp;0&nbsp;&nbsp;&nbsp;&forall;x2.&nbsp;F(x)&nbsp;=&nbsp;&int;-&infin;xf(t)dt3.&nbsp;&int;-&infin;+&infin;f(x)dx&nbsp;=&nbsp;14.&nbsp;&nbsp;P(&nbsp;a&lt;X&lt;b)&nbsp;=&nbsp;&int;abf(x)dx</p>\r\n\r\n<p style=\"margin-left:6px\"><span style=\"font-size:14pt\"><strong>+H&agrave;m ph&acirc;n phối x&aacute;c suất:</strong></span></p>\r\n\r\n<p style=\"margin-left:6px\"><span style=\"font-size:14pt\">Đ/N: H&agrave;m ph&acirc;n phối x&aacute;c suất của ĐLNN X, k&iacute; hiệu F(x) x&aacute;c định bởi</span></p>\r\n\r\n<p style=\"margin-left:6px\"><span style=\"font-size:14pt\">F(x)=P(X &lt; x)</span></p>\r\n\r\n<p style=\"margin-left:6px\"><span style=\"font-size:14pt\">T&iacute;nh chất:</span></p>\r\n\r\n<p>1.&nbsp;0&nbsp;&le;&nbsp;F(x)&le;1&nbsp;&nbsp;&nbsp;&forall;x2.&nbsp;Nếu&nbsp;x1&nbsp;&lt;&nbsp;x2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;th&igrave;&nbsp;F(x1)&nbsp;&le;&nbsp;F(x2)3.&nbsp;P(x1&nbsp;&le;X&le;x2)&nbsp;=&nbsp;F(x2)&nbsp;-&nbsp;F(x1)4.&nbsp;limx&rarr;-&infin;F(x)&nbsp;=&nbsp;0limx&rarr;+&infin;F(x)&nbsp;=15.&nbsp;Nếu&nbsp;x&nbsp;li&ecirc;n&nbsp;tục&nbsp;th&igrave;P(&nbsp;x1&nbsp;&lt;&nbsp;X&nbsp;&lt;&nbsp;x2)&nbsp;=&nbsp;P(&nbsp;x1&nbsp;&le;&nbsp;X&lt;&nbsp;x2)=&nbsp;P(&nbsp;x1&nbsp;&lt;&nbsp;X&le;&nbsp;x2)&nbsp;=P(&nbsp;x1&nbsp;&le;&nbsp;X&le;&nbsp;x2)</p>\r\n\r\n<p><span style=\"font-size:14pt\"><strong>+K&igrave; vọng to&aacute;n:&nbsp;&mu;&nbsp;=&nbsp;E(X)</strong></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">C&ocirc;ng thức:</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">Nếu X c&oacute; bảng ph&acirc;n phối x&aacute;c suất (X l&agrave; ĐLNN rời rạc)</span></p>\r\n\r\n<p>&mu;&nbsp;=&nbsp;E(X)&nbsp;=&nbsp;&sum;xi.pi</p>\r\n\r\n<p><span style=\"font-size:14pt\">Nếu X c&oacute; h&agrave;m mật độ f(x) (X l&agrave; ĐLNN li&ecirc;n tục)</span></p>\r\n\r\n<p>&mu;&nbsp;=&nbsp;E(X)=&int;-&infin;+&infin;x.f(x)dx</p>\r\n\r\n<p><span style=\"font-size:14pt\">T&iacute;nh chất:</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:14pt\">E(C) =C với C l&agrave; hằng số</span></li>\r\n	<li><span style=\"font-size:14pt\">E(C.X)= C.E(X)</span></li>\r\n	<li><span style=\"font-size:14pt\">E(X+Y)=E(X)+E(Y)</span></li>\r\n	<li><span style=\"font-size:14pt\">X,Y độc lập : E(XY)=E(X).E(Y)</span></li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14pt\"><strong>+Phương sai: Var(X)</strong></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">C&ocirc;ng thức: Var(X)=&nbsp;E.[X-E(X)]2=E(X)2-&mu;2</span></p>\r\n\r\n<p><span style=\"font-size:14pt\">T&iacute;nh chất:</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:14pt\">Var(C) =0 với C l&agrave; hăng số</span></li>\r\n	<li><span style=\"font-size:14pt\">&nbsp;Var(C.X)= C2</span><span style=\"font-size:14pt\">. Var(X)</span></li>\r\n	<li><span style=\"font-size:14pt\">Nếu X, Y đ&ocirc;c l&acirc;p : Var(X+Y) = Var(X)+ Var(Y)</span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:14pt\">VD:</span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:14pt\"><strong>+Độ lệch chuẩn:&nbsp;&sigma;</strong></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:14pt\">C&ocirc;ng thức:</span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:14pt\">Se(X) =&nbsp;&sigma;&nbsp;=Var(X)</span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:14pt\"><strong>+Mod:</strong></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:14pt\">Nếu X l&agrave; ĐLNN rơi rạc, mod (X) l&agrave; gi&aacute; trị của X m&agrave; tại đ&oacute; x&aacute;c suất lớn nhất</span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:14pt\">Nếu X l&agrave; ĐLNN li&ecirc;n tục, mod (X) l&agrave; gi&aacute; trị của X m&agrave; tại đ&oacute; ham mật độ x&aacute;c suất đạt cực đại.</span></p>', '1550110935', null, '0', '40', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('102', 'Bài tập 1', '', '1550113158', null, '99', '40', '1', '99', '99', '1', '', '10', '1', null, null, null, null, null);
