INSERT INTO `lesson` VALUES ('106', 'PHẦN  II. ĐỀ CƯƠNG', null, '1550117210', null, '0', '41', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('107', 'NHÓM CÂU HỎI II', '', '1550117258', null, '106', '41', '1', '106', '106', '1', '', '1', '1', null, null, null, null, null);
INSERT INTO `lesson` VALUES ('108', 'PHẦN I. LÝ THUYẾT TRỌNG TÂM CHƯƠNG 4', '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><img alt=\"\" src=\"/public/images/course/42/lesson/1553069309_hoc-thuyet-gia-tri-min.png\" style=\"height:565px; width:800px\" />Chương IV: Học thuyết gi&aacute; trị</span></strong></span></span></p>\r\n\r\n<ol style=\"list-style-type:upper-roman\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:14.0pt\">Điều kiện ra đời, đặc trưng v&agrave; ưu đ&atilde;i của sản xuất h&agrave;ng h&oacute;a</span></em></strong></span></span></li>\r\n</ol>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Điều kiện ra đời v&agrave; tồn tại của sản xuất h&agrave;ng h&oacute;a</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Ph&acirc;n c&ocirc;ng lao động x&atilde; hội </span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Sự t&aacute;ch biệt tương đối về mặt kinh tế của những người sản xuất</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li style=\"text-align:justify\"><span style=\"color:#c0392b\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Đặc trưng v&agrave; ưu thế của sản xuất h&agrave;ng h&oacute;a</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Đặc trưng (3)</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Sản xuất để trao đổi mua b&aacute;n</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Vừa mang t&iacute;nh tư nh&acirc;n, vừa mang t&iacute;nh x&atilde; hội</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Mục đ&iacute;ch l&agrave; gi&aacute; trị, lợi nhuận, chứ kh&ocirc;ng phải l&agrave; gi&aacute; trị sử dụng.</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Ưu thế (4)</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Ph&aacute;t triển sản xuất h&agrave;ng h&oacute;a l&agrave;m cho ph&acirc;n c&ocirc;ng lao động x&atilde; hội ng&agrave;y c&agrave;ng s&acirc;u sắc , chuy&ecirc;n m&ocirc;n h&oacute;a, hợp t&aacute;c h&oacute;a ng&agrave;y c&agrave;ng tăng &rarr; x&oacute;a bỏ t&iacute;nh tự cung tự cấp </span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ T&iacute;nh t&aacute;ch biệt về kinh tế đ&ograve;i hỏi người sản xuất h&agrave;ng h&oacute;a phải năng động trong sản xuất</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Sản xuất h&agrave;ng h&oacute;a quy m&ocirc; lớn c&oacute; ưu thế hơn về quy m&ocirc;, tr&igrave;nh độ kỹ thuật, c&ocirc;ng nghệ, về khả năng thỉa m&atilde;n nhu cầu,&hellip;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ L&agrave; m&ocirc; h&igrave;nh kinh tế mở, th&uacute;c đấy giao lưu kinh tế, giao lưu văn h&oacute;a, n&acirc;ng cao đời sống vật chất v&agrave; tinh thần.</span></span></span></p>\r\n\r\n<ol start=\"2\" style=\"list-style-type:upper-roman\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:14.0pt\">H&agrave;ng h&oacute;a</span></em></strong></span></span></li>\r\n</ol>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"color:#c0392b\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">H&agrave;ng h&oacute;a v&agrave; hai thuộc t&iacute;nh của h&agrave;ng h&oacute;a</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">&nbsp;Kh&aacute;i niệm: L&agrave; sản phẩm của lao động, c&oacute; thể thỏa m&atilde;n nhu cầu n&agrave;o đ&oacute; của con người th&ocirc;ng qua trao đổi, mua b&aacute;n</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Hai thuộc t&iacute;nh: + Gi&aacute; trị sử dụng</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + Gi&aacute; trị</span></span></span></p>\r\n\r\n<ol start=\"2\">\r\n	<li style=\"text-align:justify\"><span style=\"color:#c0392b\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">T&iacute;nh hai mặt của lao động trong sản xuất h&agrave;ng h&oacute;a</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Lao động cụ thể</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Lao động trừu tượng</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"3\">\r\n	<li style=\"text-align:justify\"><span style=\"color:#c0392b\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Lượng gi&aacute; trị h&agrave;ng h&oacute;a v&agrave; c&aacute;c nh&acirc;n tố ảnh hưởng đến lượng gi&aacute; trị h&agrave;ng h&oacute;a</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Thước đo lượng gi&aacute; trị của h&agrave;ng h&oacute;a</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">C&aacute;c nh&acirc;n tố ảnh&nbsp; hưởng đến lượng gi&aacute; trị h&agrave;ng h&oacute;a</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:24px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Năng suất lao động</span></span></span></p>\r\n\r\n<p style=\"margin-left:24px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Mức độ phức tạp của lao động</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Cấu th&agrave;nh lượng gi&aacute; trị h&agrave;ng h&oacute;a</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:24px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Gi&aacute; trị cũ ( c )</span></span></span></p>\r\n\r\n<p style=\"margin-left:24px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Gi&aacute; trị mới ( v+m )</span></span></span></p>\r\n\r\n<ol start=\"3\" style=\"list-style-type:upper-roman\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:14.0pt\">Tiền tệ</span></em></strong></span></span></li>\r\n</ol>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"color:#c0392b\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Lịch sử ra đời v&agrave; bản chất của tiền tệ</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Sự ph&aacute;t triển c&aacute;c h&igrave;nh th&aacute;i gi&aacute; trị</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ H&igrave;nh th&aacute;i gi&aacute; trị giản đơn hay ngẫu nhi&ecirc;n</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ H&igrave;nh th&aacute;i gi&aacute; trị đầy đủ hay mở rộng</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ H&igrave;nh th&aacute;i chung của gi&aacute; trị</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ H&igrave;nh th&aacute;i tiền tệ</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Bản chất của tiền tệ: l&agrave; h&agrave;ng h&oacute;a đặc biệt được t&aacute;ch ra l&agrave;m vật ngang gi&aacute; chung, thể hiện lao động x&atilde; hội v&agrave; quan hệ giữa những người sản xuất h&agrave;ng h&oacute;a</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li style=\"text-align:justify\"><span style=\"color:#c0392b\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">C&aacute;c chức năng của tiền tệ v&agrave; quy luật lưu th&ocirc;ng tiền tệ</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">C&aacute;c chức năng</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Thước đo gi&aacute; trị</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Phương tiện lưu th&ocirc;ng</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Phương tiện cất trữ</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Phương tiện thanh to&aacute;n</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Tiền tệ thế giới</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Quy luật lưu th&ocirc;ng tiền tệ v&agrave; vấn đề lạm ph&aacute;t</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Quy luật lưu th&ocirc;ng tiền tệ l&agrave; quy luật quy định số lượng tiền cần cho lưu th&ocirc;ng h&agrave;ng h&oacute;a ở mỗi thời kỳ nhất định</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Lạm ph&aacute;t l&agrave; t&igrave;nh trạng mức gi&aacute; chung của to&agrave;n bộ nền kinh tế tăng l&ecirc;n một thời gian nhất định</span></span></span></p>\r\n\r\n<ol start=\"4\" style=\"list-style-type:upper-roman\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:14.0pt\">Quy luật gi&aacute; trị</span></em></strong></span></span></li>\r\n</ol>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"color:#c0392b\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Nội dung v&agrave; y&ecirc;u cầu của quy luật gi&aacute; trị</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Quy luật gi&aacute; trị l&agrave; quy luật kinh tế căn bản&nbsp; của sản xuất v&agrave; trao đổi h&agrave;ng h&oacute;a, ở đ&acirc;u c&oacute; sản xuất v&agrave; trao đổi h&agrave;ng h&oacute;a th&igrave; ở đ&oacute; c&oacute; sự tồn tại v&agrave; ph&aacute;t huy t&aacute;c dụng của quy luật gi&aacute; trị</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Y&ecirc;u cầu: việc sản xuất v&agrave; trao đổi h&agrave;ng h&oacute;a phải dựa tr&ecirc;n cơ sở hao ph&iacute; lao động x&atilde; hội cần thiết.</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li style=\"text-align:justify\"><span style=\"color:#c0392b\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">T&aacute;c động của quy luật gi&aacute; trị</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Điều tiết sản xuất v&agrave; lưu th&ocirc;ng h&agrave;ng h&oacute;a</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">K&iacute;ch th&iacute;ch cải tiến lỹ thuật, hợp l&yacute; h&oacute;a sản xuất, tăng năng suất lao động, th&uacute;c đẩy lực lượng x&atilde; hội sản xuất ph&aacute;t triển</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Thực hiện sự lựa chọn tự nhi&ecirc;n v&agrave; ph&acirc;n h&oacute;a người sản xuất h&agrave;ng h&oacute;a th&agrave;nh người gi&agrave;u, người ngh&egrave;o</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>', '1550135305', null, '0', '42', '1', '0', null, '0', null, null, null, null, null, null, '/images/course/42/lesson/1553069240_hoc-thuyet-gia-tri-min.png', null);
