INSERT INTO `lesson` VALUES ('111', 'Chương 01.', '<p><img src=\"/public/images/course/43/lesson/1550568333_chuong-7-ham-2-bien-2.jpeg\" style=\"height:447px; width:600px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Định nghĩa của h&agrave;m 2 biến:</p>\r\n\r\n<p>Giới hạn v&agrave; li&ecirc;n tục</p>\r\n\r\n<p>V&iacute; dụ b&agrave;i tập:</p>\r\n\r\n<p>-Đưa v&iacute; dụ ra: giải.</p>\r\n\r\n<p>&nbsp;</p>', '1550568304', null, '0', '43', '1', '0', null, '0', null, null, null, null, null, null, null, null);
