INSERT INTO `lesson` VALUES ('126', 'Chương 1', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Chương 1 TỔNG LUẬN VỀ</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">PHƯƠNG PH&Aacute;P NGHI&Ecirc;N CỨU KHOA HỌC</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">1.1. Nghi&ecirc;n cứu khoa học, ph&acirc;n loại nghi&ecirc;n cứu khoa học:</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<ol>\r\n			<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Kh&aacute;i niệm về nghi&ecirc;n cứu khoa học:</span></span></span></li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Nghi&ecirc;n cứu l&agrave; g&igrave;?</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Nguồn gốc thuật ngữ &ldquo;nghi&ecirc;n cứu&rdquo; từ trong tiếng Ph&aacute;p &ldquo;recherche&rdquo; với &yacute; nghĩa ban đầu l&agrave; &ldquo;sự t&igrave;m kiếm&rdquo;.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Theo Shuttleworth Martyn (2008): Nghi&ecirc;n cứu bao h&agrave;m bất cứ sự thu thập dữ liệu, th&ocirc;ng tin v&agrave; dữ kiện n&agrave;o nhằm th&uacute;c đẩy tri thức.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Theo Cresswell (2008): Nghi&ecirc;n cứu l&agrave; một qu&aacute; tr&igrave;nh gồm c&aacute;c bước thu thập v&agrave; ph&acirc;n t&iacute;ch th&ocirc;ng tin nhằm gia tăng sự hiểu biết của ch&uacute;ng ta về một chủ đề hay một vấn đề.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Khoa học l&agrave; g&igrave;?</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Khoa học bao gồm một hệ thống tri thức về quy luật của vật chất v&agrave; sự vận động của vật chất, những quy luật của tự nhi&ecirc;n, x&atilde; hội v&agrave; tư duy.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Kh&aacute;i niệm Nghi&ecirc;n cứu khoa học:</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Phương ph&aacute;p nghi&ecirc;n cứu khoa học l&agrave; qu&aacute; tr&igrave;nh được sử dụng để thu thập th&ocirc;ng tin v&agrave; dữ liệu phục vụ cho c&aacute;c quyết định nghi&ecirc;n cứu. C&aacute;c phương ph&aacute;p nghi&ecirc;n cứu c&oacute; thể bao gồm nghi&ecirc;n cứu l&yacute; thuyết, phỏng vấn, khảo s&aacute;t v&agrave; c&aacute;c nghi&ecirc;n cứu kỹ thuật kh&aacute;c; v&agrave; c&oacute; thể bao gồm cả th&ocirc;ng tin hiện tại v&agrave; qu&aacute; khứ.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Chương 1 TỔNG LUẬN VỀ</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">PHƯƠNG PH&Aacute;P NGHI&Ecirc;N CỨU KHOA HỌC</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">1.1. Nghi&ecirc;n cứu khoa học, ph&acirc;n loại nghi&ecirc;n cứu khoa học:</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<ol>\r\n			<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Kh&aacute;i niệm về nghi&ecirc;n cứu khoa học:</span></span></span></li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Nghi&ecirc;n cứu l&agrave; g&igrave;?</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Nguồn gốc thuật ngữ &ldquo;nghi&ecirc;n cứu&rdquo; từ trong tiếng Ph&aacute;p &ldquo;recherche&rdquo; với &yacute; nghĩa ban đầu l&agrave; &ldquo;sự t&igrave;m kiếm&rdquo;.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Theo Shuttleworth Martyn (2008): Nghi&ecirc;n cứu bao h&agrave;m bất cứ sự thu thập dữ liệu, th&ocirc;ng tin v&agrave; dữ kiện n&agrave;o nhằm th&uacute;c đẩy tri thức.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Theo Cresswell (2008): Nghi&ecirc;n cứu l&agrave; một qu&aacute; tr&igrave;nh gồm c&aacute;c bước thu thập v&agrave; ph&acirc;n t&iacute;ch th&ocirc;ng tin nhằm gia tăng sự hiểu biết của ch&uacute;ng ta về một chủ đề hay một vấn đề.</span></span></span></p>\r\n\r\n<p><img alt=\"\" src=\"/public/images/course/46/lesson/1550843063_52676554-1133501430143802-7731763636217053184-n.jpg\" style=\"height:960px; width:720px\" /></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Khoa học l&agrave; g&igrave;?</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Khoa học bao gồm một hệ thống tri thức về quy luật của vật chất v&agrave; sự vận động của vật chất, những quy luật của tự nhi&ecirc;n, x&atilde; hội v&agrave; tư duy.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Kh&aacute;i niệm Nghi&ecirc;n cứu khoa học:</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Phương ph&aacute;p nghi&ecirc;n cứu khoa học l&agrave; qu&aacute; tr&igrave;nh được sử dụng để thu thập th&ocirc;ng tin v&agrave; dữ liệu phục vụ cho c&aacute;c quyết định nghi&ecirc;n cứu. C&aacute;c phương ph&aacute;p nghi&ecirc;n cứu c&oacute; thể bao gồm nghi&ecirc;n cứu l&yacute; thuyết, phỏng vấn, khảo s&aacute;t v&agrave; c&aacute;c nghi&ecirc;n cứu kỹ thuật kh&aacute;c; v&agrave; c&oacute; thể bao gồm cả th&ocirc;ng tin hiện tại v&agrave; qu&aacute; khứ.</span></span></span></p>', '1550807118', null, '0', '46', '1', '0', null, '0', null, null, null, null, null, null, '/images/course/46/lesson/1550842878_52676554-1133501430143802-7731763636217053184-n.jpg', null);
