INSERT INTO `lesson` VALUES ('128', 'mindmap', '<p><img alt=\"KhÃ´ng cÃ³ mÃ´ táº£ áº£nh.\" src=\"https://scontent.fhan2-4.fna.fbcdn.net/v/t1.0-9/52676554_1133501430143802_7731763636217053184_n.jpg?_nc_cat=104&amp;_nc_oc=AQlwvPhpu2flhB8tGY9Nk9Z4HaWQeggi0qLuJrgRn6voHeVm3oP4mx7Jd7CuZSj1zNk&amp;_nc_ht=scontent.fhan2-4.fna&amp;oh=c9b904a2b8aee3b339ac2b3bd502e3ee&amp;oe=5CE64242\" /></p>', '1550843001', null, '126', '46', '1', '126', '126', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('129', 'Chương 1', '<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">CHƯƠNG 1: TỔNG LUẬN VỀ PHƯƠNG PH&Aacute;P NGHI&Ecirc;N CỨU KHOA HỌC</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><u><strong><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">1.1. Nghi&ecirc;n cứu khoa học, ph&acirc;n loại nghi&ecirc;n cứu khoa học:</span></span></strong></u></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><em>1.1.1.&nbsp;<span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Kh&aacute;i niệm về nghi&ecirc;n cứu khoa học:</span></span></em></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Nghi&ecirc;n cứu l&agrave; g&igrave;?</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Nguồn gốc thuật ngữ &ldquo;nghi&ecirc;n cứu&rdquo; từ trong tiếng Ph&aacute;p &ldquo;recherche&rdquo; với &yacute; nghĩa ban đầu l&agrave; &ldquo;sự t&igrave;m kiếm&rdquo;.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Theo Shuttleworth Martyn (2008): Nghi&ecirc;n cứu bao h&agrave;m bất cứ sự thu thập dữ liệu, th&ocirc;ng tin v&agrave; dữ kiện n&agrave;o nhằm th&uacute;c đẩy tri thức.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Theo Cresswell (2008): Nghi&ecirc;n cứu l&agrave; một qu&aacute; tr&igrave;nh gồm c&aacute;c bước thu thập v&agrave; ph&acirc;n t&iacute;ch th&ocirc;ng tin nhằm gia tăng sự hiểu biết của ch&uacute;ng ta về một chủ đề hay một vấn đề.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Khoa học l&agrave; g&igrave;?</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Khoa học bao gồm một hệ thống tri thức về quy luật của vật chất v&agrave; sự vận động của vật chất, những quy luật của tự nhi&ecirc;n, x&atilde; hội v&agrave; tư duy.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Kh&aacute;i niệm Nghi&ecirc;n cứu khoa học:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Phương ph&aacute;p nghi&ecirc;n cứu khoa học l&agrave; qu&aacute; tr&igrave;nh được sử dụng để thu thập th&ocirc;ng tin v&agrave; dữ liệu phục vụ cho c&aacute;c quyết định nghi&ecirc;n cứu. C&aacute;c phương ph&aacute;p nghi&ecirc;n cứu c&oacute; thể bao gồm nghi&ecirc;n cứu l&yacute; thuyết, phỏng vấn, khảo s&aacute;t v&agrave; c&aacute;c nghi&ecirc;n cứu kỹ thuật kh&aacute;c; v&agrave; c&oacute; thể bao gồm cả th&ocirc;ng tin hiện tại v&agrave; qu&aacute; khứ.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><em>1.1.2.&nbsp;<span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Ph&acirc;n loại nghi&ecirc;n cứu khoa học:</span></span></em></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Nghi&ecirc;n cứu cơ bản v&agrave; nghi&ecirc;n cứu ứng dụng:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Nghi&ecirc;n cứu cơ bản l&agrave; một nghi&ecirc;n cứu c&oacute; hệ thống hướng tới sự ph&aacute;t triển tri thức hay sự hiểu biết về c&aacute;c kh&iacute;a cạnh cơ bản của hiện tượng.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Nghi&ecirc;n cứu ứng dụng Nghi&ecirc;n cứu ứng dụng l&agrave; một h&igrave;nh thức điều tra c&oacute; hệ thống li&ecirc;n quan đến ứng dụng thực tế của khoa học.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Nghi&ecirc;n cứu quy nạp v&agrave; nghi&ecirc;n cứu diễn dịch:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Nghi&ecirc;n cứu diễn dịch l&agrave; suy luận dựa tr&ecirc;n c&aacute;ch tiếp cận giả thuyết &ndash; suy luận dựa tr&ecirc;n việc x&acirc;y dựng một hay nhiều giả thuyết v&agrave; sau đ&oacute; đặt c&aacute;c giả thuyết đ&oacute; trước một thực tế. Mục đ&iacute;ch l&agrave; để đ&aacute;nh gi&aacute; về sự th&iacute;ch đ&aacute;ng của giả thuyết được đưa ra ban đầu.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Nghi&ecirc;n cứu quy nạp: đưa ra một kết luận phỏng đo&aacute;n dựa tr&ecirc;n suy luận từ quy luật lặp đi lặp lại v&agrave; kh&ocirc;ng đổi quan s&aacute;t được đối với một số sự việc v&agrave; r&uacute;t ra sự tồn tại của một sự việc kh&aacute;c kh&ocirc;ng được chứng minh nhưng lại c&oacute; li&ecirc;n quan thường xuy&ecirc;n đến c&aacute;c sự viện đ&atilde; được quan s&aacute;t trước đ&oacute;.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Nghi&ecirc;n cứu định t&iacute;nh v&agrave; nghi&ecirc;n cứu định lượng:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Ph&acirc;n biệt dựa v&agrave;o bản chất dữ liệu:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Theo Miles v&agrave; Huberman (1984), dữ liệu định t&iacute;nh mang h&igrave;nh thức của từ chứ kh&ocirc;ng l&agrave; con số.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Theo Yin (2013) &ldquo;dữ liệu số&rdquo; cung cấp bằng chứng về mặt số lượng trong khi &ldquo;dữ liệu kh&ocirc;ng phải số&rdquo; cung cấp bằng chứng c&oacute; t&iacute;nh chất định t&iacute;nh.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Ph&acirc;n biệt dựa v&agrave;o t&iacute;nh chất kh&aacute;ch quan hay chủ quan của kết quả nghi&ecirc;n cứu:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Nghi&ecirc;n cứu định lượng thường đảm bảo t&iacute;nh kh&aacute;ch quan nhiều hơn do đặc trưng của ph&acirc;n t&iacute;ch số liệu thống k&ecirc;.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Ph&acirc;n biệt dựa v&agrave;o t&iacute;nh linh hoạt của nghi&ecirc;n cứu:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Trong nghi&ecirc;n cứu định t&iacute;nh: Nh&agrave; nghi&ecirc;n cứu c&oacute; thể linh hoạt trong việc thu thập dư liệu.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>1.2.&nbsp;<span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Những thuật ngữ cơ bản trong nghi&ecirc;n cứu khoa học:</span></span></strong></u></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Thuật ngữ &ldquo;Kh&aacute;i niệm&rdquo; l&agrave; g&igrave;?</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Kh&aacute;i niệm l&agrave; qu&aacute; tr&igrave;nh nhận thức hay tư duy của con người bắt đầu từ những tri gi&aacute;c hay bằng những quan s&aacute;t sự vật hiện thực t&aacute;c động đến gi&aacute;c quan.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Kh&aacute;i niệm gồm: nội h&agrave;m (tập hợp c&aacute;c dấu hiệu cơ bản của đối tượng được phản &aacute;nh trong kh&aacute;i niệm) v&agrave; ngoại di&ecirc;n (l&agrave; tập hợp tất cả c&aacute;c đối tượng c&oacute; c&aacute;c dấu hiệu trong nội h&agrave;m của kh&aacute;i niệm)</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Thuật ngữ &ldquo;m&ocirc; h&igrave;nh&rdquo;:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ M&ocirc; h&igrave;nh thể hiện mối quan hệ c&oacute; t&iacute;nh hệ thống giữa c&aacute;c nh&acirc;n tố. M&ocirc; h&igrave;nh thể hiện quy luật của hiện tượng sự vật dưới dạng đơn giản h&oacute;a. M&ocirc; h&igrave;nh nghi&ecirc;n cứu thể hiện mối quan hệ của c&aacute;c nh&acirc;n tố (c&aacute;c biến) trong phạm vi nghi&ecirc;n cứu. Mối quan hệ n&agrave;y cần được ph&aacute;t hiện v&agrave; kiểm chứng trong qu&aacute; tr&igrave;nh nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Thuật ngữ &ldquo;biến số&rdquo;:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Biến số l&agrave; từ được d&ugrave;ng để m&ocirc; tả sự vật, hiện tượng c&oacute; sự biến đổi kh&aacute;c nhau m&agrave; nh&agrave; nghi&ecirc;n cứu muốn nghi&ecirc;n cứu, quan s&aacute;t.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Hai dạng biến số:</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Biến số phạm tr&ugrave; (định t&iacute;nh) được h&igrave;nhh th&agrave;nh bởi một tập hợp c&aacute;c đặc t&iacute;nh của một loại phạm tr&ugrave; kh&ocirc;ng theo số đo hoặc thang đo.</span></span></span></li>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Biến số số (biến định lượng) được thể hiện bằng những đơn vị trong đ&oacute; c&aacute;c con số được g&aacute;n cho mỗi đơn vị của biến mang &yacute; nghĩa to&aacute;n học</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Trong nghi&ecirc;n cứu thực nghiệm, c&oacute; thể ph&acirc;n loại biến số th&agrave;nh biến độc lập v&agrave; biến phụ thuộc.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Biến độc lập l&agrave; c&aacute;c yếu tố, điều kiện khi bị thay đổi tr&ecirc;n đối tượng nghi&ecirc;n cứu sẽ ảnh hưởng đến kết quả nghi&ecirc;n cứu.</span></span></span></li>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Biến phụ thuộc l&agrave; những chỉ ti&ecirc;u đo đạc v&agrave; bị ảnh hưởng trong suốt qu&aacute; tr&igrave;nh th&iacute; nghiệm hay c&oacute; thể n&oacute;i kết quả đo dạc phụ thuộc v&agrave;o sự thay đổi của biến độc lập.</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Nh&agrave; nghi&ecirc;n cứu phải trả lời c&acirc;u hỏi: m&igrave;nh muốn nghi&ecirc;n cứu c&aacute;i g&igrave;.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- C&aacute;c thuật ngữ kh&aacute;c:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Kh&aacute;ch thể nghi&ecirc;n cứu: L&agrave; hệ thống sự vật, hiện tượng tồn tại kh&aacute;ch quan trong c&aacute;c mối li&ecirc;n hệ m&agrave; nh&agrave; nghi&ecirc;n cứu cần kh&aacute;m ph&aacute;. Kh&aacute;ch thể nghi&ecirc;n cứu l&agrave; vật mang đối tượng nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Phương ph&aacute;p nghi&ecirc;n cứu: L&agrave; c&aacute;ch thức thực hiện nghi&ecirc;n cứu, bao gồm: nghi&ecirc;n cứu l&yacute; thuyết, nghi&ecirc;n cứu thực nghiệm v&agrave; nghi&ecirc;n cứu phi thực nghiệm.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Dữ liệu: L&agrave; tiền đề của mọi l&yacute; thuyết. Nh&agrave; nghi&ecirc;n cứu t&igrave;m kiếm v&agrave; thu thập dữ liệu v&agrave; sau đ&oacute; tiến h&agrave;nh xử l&yacute; dữ liệu nhằm đưa ra kết quả v&agrave; ho&agrave;n thiện hay ph&aacute;t triển l&yacute; thuyết đ&atilde; được chứng minh trước đấy.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Dữ liệu gồm 2 loại: dữ liệu thứ cấp v&agrave; dữ liệu sơ cấp.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>1.3.&nbsp;<span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Tiến tr&igrave;nh tư duy trong nghi&ecirc;n cứu khoa học:</span></span></strong></u></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Tr&igrave;nh tự nghi&ecirc;n cứu khoa học gồm 4 bước:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Bước 1: X&aacute;c định v&agrave; lựa chọn vấn đề nghi&ecirc;n cứu:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Việc x&aacute;c định v&agrave; lựa chọn vấn đề nghi&ecirc;n cứu l&agrave; việc đặt c&acirc;u hỏi &ldquo;cần chứng minh điều g&igrave;&quot;?</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">C&oacute; hai trường hợp lựa chọn đề t&agrave;i:</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Nh&agrave; nghi&ecirc;n cứu được giao đề t&agrave;i.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Nh&agrave; nghi&ecirc;n cứu tự ph&aacute;t hiện vấn đề nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Bước 2: X&acirc;y dựng luận điểm khoa học:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Nh&agrave; nghi&ecirc;n cứu tiến h&agrave;nh xem x&eacute;t lịch sử nghi&ecirc;n cứu vấn đề, chỉ ra xem từ trước đến nay, người ta đ&atilde; nghi&ecirc;n cứu vấn đề n&agrave;y như thế n&agrave;o.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Sau đ&oacute;, chỉ ra những vấn đề được giải quyết, giải quyết chưa thấu đ&aacute;o hoặc chưa được giải quyết.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Bước 3: Chứng minh luận điểm khoa học:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Nh&agrave; nghi&ecirc;n cứu chứng minh c&aacute;c luận điểm đ&atilde; đưa ra ở bước 2 bằng c&aacute;c luận cứ khoa học.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Ph&eacute;p chứng minh gồm 3 bộ phận: Luận điểm, luận cứ v&agrave; phương ph&aacute;p.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Bước 4: Tr&igrave;nh b&agrave;y luận điểm khoa học:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">L&agrave; qu&aacute; tr&igrave;nh nh&agrave; nghi&ecirc;n cứu viết b&aacute;o c&aacute;o tr&igrave;nh b&agrave;y lại qu&aacute; tr&igrave;nh nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Đ&acirc;y l&agrave; c&ocirc;ng việc cuối c&ugrave;ng v&agrave; quan trọng nhất nhằm tr&igrave;nh b&agrave;y kết quả nghi&ecirc;n cứu sao cho người đọc dễ hiểu.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Bao gồm: t&oacute;m tắt, tr&igrave;nh b&agrave;y số liệu, kết quả nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Tiến tr&igrave;nh tư duy được cụ thể ở 7 bước thực hiện cơ bản trong NCKH:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Bước 1 : Quan s&aacute;t sự vật, hiện tượng</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Bước 2 : Ph&aacute;t hiện v&agrave; đặt vấn đề nghi&ecirc;n cứu</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Bước 3 : X&acirc;y dựng giả thuyết</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Bước 4 : Thu thập th&ocirc;ng tin</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Bước 5 : X&acirc;y dựng luận cứ l&yacute; thuyết v&agrave; luận cứ thực tiễn</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Bước 6 : Ph&acirc;n t&iacute;ch v&agrave; thảo luận</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Bước 7 : Kết luận v&agrave; đề nghị</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>1.4.&nbsp;<span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">C&aacute;c sản phẩm nghi&ecirc;n cứu khoa học:</span></span></strong></u></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Kh&oacute;a luận tốt nghiệp:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Kh&oacute;a luận tốt nghiệp l&agrave; một c&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu khoa học của sinh vi&ecirc;n c&aacute;c trường đại học thực hiện v&agrave;o học kỳ cuối để tốt nghiệp ra trường.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">H&igrave;nh thức v&agrave; nội dung kh&oacute;a luận bao gồm:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Phần mở đầu</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Tổng quan nghi&ecirc;n cứu</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Khung l&yacute; thuyết v&agrave; phương ph&aacute;p nghi&ecirc;n cứu </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Kết quả nghi&ecirc;n cứu v&agrave; thảo luận</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Kết luận v&agrave; kiến nghị </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">T&agrave;i liệu tham khảo </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Phụ lục</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Luận văn thạc sĩ:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Bố cục phụ thuộc chuy&ecirc;n ng&agrave;nh v&agrave; đề t&agrave;i cụ thể. Th&ocirc;ng thường bao gồm: </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Phần mở đầu</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Tổng quan nghi&ecirc;n cứu</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Những nghi&ecirc;n cứu thực nghiệm hoặc l&yacute; thuyết </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Tr&igrave;nh b&agrave;y, đ&aacute;nh gi&aacute;, b&agrave;n luận kết quả</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Kết luận v&agrave; kiến nghị những nghi&ecirc;n cứu tiếp theo </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Danh mục c&ocirc;ng tr&igrave;nh c&ocirc;ng bố của t&aacute;c giả (nếu c&oacute;) </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Phụ lục</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">T&oacute;m tắt luận &aacute;n </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Mở đầu</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Tổng quan t&igrave;nh h&igrave;nh nghi&ecirc;n cứu, mục ti&ecirc;u v&agrave; nhiệm vụ nghi&ecirc;n cứu </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Cơ sở l&yacute; luận v&agrave; giả thuyết khoa học</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Kết quả nghi&ecirc;n cứu, ph&acirc;n t&iacute;ch v&agrave; b&agrave;n luận kết quả nghi&ecirc;n cứu </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Kết luận v&agrave; kiến nghị về những nghi&ecirc;n cứu tiếp theo</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Danh mục c&ocirc;ng tr&igrave;nh c&ocirc;ng bố của t&aacute;c giả </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Danh mục t&agrave;i liệu tham khảo</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Phụ lục</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- B&aacute;o c&aacute;o đề t&agrave;i nghi&ecirc;n cứu khoa học: </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Đề t&agrave;i NCKH l&agrave; một h&igrave;nh thức tổ chức NCKH được đặc trưng bởi một nhiệm vụ nghi&ecirc;n cứu v&agrave; do một người hoặc nh&oacute;m người thực hiện.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">C&aacute;c loại b&aacute;o c&aacute;o đề t&agrave;i NCKH: Dự &aacute;n, chương tr&igrave;nh.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Bố cục:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Mở đầu.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Chương 1: Tổng quan t&igrave;nh h&igrave;nh nghi&ecirc;n cứu </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Chương 2: Phương ph&aacute;p nghi&ecirc;n cứu </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Chương 3: Kết quả v&agrave; thảo luận</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Kết luận v&agrave; đề nghị</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Danh mục t&agrave;i liệu tham khảo </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Phụ lục</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- B&agrave;i b&aacute;o khoa học:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">B&agrave;i b&aacute;o khoa học viết để c&ocirc;ng bố tr&ecirc;n tạp ch&iacute;, hội nghị khoa học, tham gia tranh luận v&agrave; cần tr&igrave;nh b&agrave;y ch&iacute;nh x&aacute;c về kết quả nghi&ecirc;n cứu. </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Bố cục:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Ti&ecirc;u đề: T&ecirc;n b&agrave;i b&aacute;o </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">T&aacute;c giả</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Địa chỉ thư t&iacute;n </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">T&oacute;m lược</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Giới thiệu</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Phương ph&aacute;p</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Kết quả Thảo luận</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Kết luận v&agrave; đề nghị </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Cảm tạ</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">T&agrave;i liệu tham khảo</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Chuy&ecirc;n đề khoa học:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Chuy&ecirc;n đề khoa học l&agrave; một phần của đề t&agrave;i/dự &aacute;n. </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Bố cục bao gồm: Mở đầu.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Kết quả nghi&ecirc;n cứu </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Kết luận v&agrave; kiến nghị </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">T&agrave;i liệu tham khảo</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Phụ lục</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', '1550900333', null, '0', '48', '1', '0', null, '0', null, null, null, null, null, null, '/images/course/48/lesson/1550927433_chuong-1-tong-quan-ve-phuong-phap-nghien-cuu-khoa-hoc.png', null);
INSERT INTO `lesson` VALUES ('130', 'Chương 2', '<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">CHƯƠNG 2: THIẾT KẾ NGHI&Ecirc;N CỨU</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><u><strong><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">2.1. Một số định nghĩa:</span></span></strong></u></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Mối quan hệ giữa một số kh&aacute;i niệm quan trọng như &yacute; tưởng nghi&ecirc;n cứu, vấn đề nghi&ecirc;n cứu, mục đ&iacute;ch v&agrave; mục ti&ecirc;u nghi&ecirc;n cứu, c&acirc;u hỏi nghi&ecirc;n cứu, giả thuyết nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><em><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">2.1.1. &Yacute; tưởng nghi&ecirc;n cứu:</span></span></em></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- L&agrave; những &yacute; tưởng ban đầu về vấn đề nghi&ecirc;n cứu, từ những &yacute; tưởng ban đầu n&agrave;y, nh&agrave; nghi&ecirc;n cứu sẽ tiếp tục t&igrave;m hiểu để nhận dạng được vấn đề nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Trong thực tiễn nghi&ecirc;n cứu đ&atilde; tổng kết một số cơ chế ch&iacute;nh như sau:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Cơ chế trực gi&aacute;c: &Yacute; tưởng mới xuất hiện như tia chớp, đ&oacute; l&agrave; một h&igrave;nh thức nhảy vọt của tư duy được gọi l&agrave; trực gi&aacute;c.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Cơ chế ph&acirc;n t&iacute;ch nguv&ecirc;n nh&acirc;n v&agrave; hậu quả của một vấn đề, ph&aacute;t hiện m&acirc;u thuẫn, thiếu s&oacute;t.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Cơ chế tiếp cận thực tiễn.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><em><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">2.1.2.&nbsp; Vấn đề nghi&ecirc;n cứu:</span></span></em></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Vấn đề nghi&ecirc;n cứu l&agrave; một vấn đề c&oacute; thực ph&aacute;t sinh trong cuộc sống được nghi&ecirc;n cứu để t&igrave;m ra c&aacute;ch thức tốt nhất nhằm giải quyết vấn đề đ&oacute;.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Đặc điểm cơ bản của vấn đề nghi&ecirc;n cứu:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Vấn đề nghi&ecirc;n cứu phải l&agrave; một vấn đề c&oacute; thực.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Giải quyết vấn đề nghi&ecirc;n cứu phải mang lại lợi &iacute;ch thiết thực cho con người</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Một vấn đề nghi&ecirc;n cứu được x&aacute;c định r&otilde; r&agrave;ng v&agrave; đ&uacute;ng đắn l&agrave; điều kiện ti&ecirc;n quyết cho th&agrave;nh c&ocirc;ng của nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Th&ocirc;ng thường c&oacute; thể nhận dạng vấn đề nghi&ecirc;n cứu từ 2 nguồn:</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Từ l&yacute; thuyết.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Từ thực tế.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">X&aacute;c định vấn đề nghi&ecirc;n cứu được tiến h&agrave;nh sau khi đ&atilde; nhận dạng được vấn đề nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Vấn đề nghi&ecirc;n cứu thường được chia th&agrave;nh 2 dạng ch&iacute;nh: </span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+Dạng nguy&ecirc;n thủy (Original research).</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+Dạng nghi&ecirc;n cứu lặp (Replication research).</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Khi x&aacute;c định vấn đề nghi&ecirc;n cứu hay đề t&agrave;i nghi&ecirc;n cứu nh&agrave; khoa học cần phải c&acirc;n nhắc một số yếu tố nguồn lực sau:</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Sự hiểu biết, vốn tri thức, năng lực tr&iacute; tuệ, l&ograve;ng say m&ecirc;,. . . của người nghi&ecirc;n cứu đối với vấn đề đặt ra.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Nguồn th&ocirc;ng tin, tư liệu, địa b&agrave;n nghi&ecirc;n cứu, điều kiện thực nghiệm, c&aacute;c hướng khai th&aacute;c mới về th&ocirc;ng tin, nh&acirc;n lực, t&agrave;i lực, vật lực c&oacute; triển vọng.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ C&aacute;c điều kiện về tổ chức, kinh ph&iacute; v&agrave; sự quản l&yacute; l&atilde;nh đạo của c&aacute;c cơ quan quản l&yacute; nghi&ecirc;n cứu khoa học.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><em><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">2.1.3. Mục đ&iacute;ch v&agrave; c&acirc;u hỏi nghi&ecirc;n cứu:</span></span></em></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Mục đ&iacute;ch nghi&ecirc;n cứu:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Mục đ&iacute;ch trả lời c&acirc;u hỏi &quot;nghi&ecirc;n cứu để l&agrave;m g&igrave;?&quot;, hoặc &quot;để phục vụ cho điều g&igrave;?&quot; v&agrave; mang &yacute; nghĩa thực tiển của nghi&ecirc;n cứu, nhắm đến đối tượng phục vụ sản xuất, nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">C&acirc;u hỏi nghi&ecirc;n cứu (research question): l&agrave; một ph&aacute;t biểu mang t&iacute;nh bất định về một vấn đề.</span></span></span></li>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Đối tượng nghi&ecirc;n cứu: l&agrave; bản chất của sự vật hay hiện tượng cần xem x&eacute;t v&agrave; l&agrave;m r&otilde; trong vấn đề nghi&ecirc;n cứu.</span></span></span></li>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Phạm vi nghi&ecirc;n cứu: đối tượng nghi&ecirc;n cứu được khảo s&aacute;t trong trong phạm vi nhất định về mặt thời gian, kh&ocirc;ng gian v&agrave; lĩnh vực nghi&ecirc;n cứu.</span></span></span></li>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Giả thuyết nghi&ecirc;n cứu xuất ph&aacute;t từ c&acirc;u hỏi nghi&ecirc;n cứu, lời sơ bộ, cần chứng minh về c&acirc;u hỏi nghi&ecirc;n cứu của đề t&agrave;i.</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Giả thuyết c&agrave;ng đơn giản c&agrave;ng tốt v&agrave; c&oacute; thể được kiểm nghiệm v&agrave; mang t&iacute;nh khả thi.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Một giả thuyết nghi&ecirc;n cứu c&oacute; thể được ph&aacute;t triển theo 2 dạng thức:</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Dạng thức quan hệ nh&acirc;n - quả: Một giả thuyết tốt phải chứa đựng mối quan hệ nh&acirc;n quả, v&agrave; thường sử dụng từ ướm thử c&oacute; thể.</span></span></span></li>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Dạng thức nếu - vậy th&igrave;: Đ&oacute; l&agrave; Nếu (Hệ quả hoặc nguy&ecirc;n nh&acirc;n). . . c&oacute; li&ecirc;n quan tới (Nguy&ecirc;n nh&acirc;n hoặc hệ quả). . . , vậy th&igrave; nguy&ecirc;n nh&acirc;n đ&oacute; c&oacute; thể hay ảnh hưởng đến hiệu quả.</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Trong việc x&acirc;y dựng giả thuyết nghi&ecirc;n cứu cần trả lời c&aacute;c c&acirc;u hỏi sau: Giả thuyết n&agrave;y c&oacute; thể tiến h&agrave;nh thực nghiệm được kh&ocirc;ng?</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ C&aacute;c biến số hay c&aacute;c yếu tố n&agrave;o cần được nghi&ecirc;n cứu?</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Phương ph&aacute;p thử nghiệm n&agrave;o được sử dụng trong nghi&ecirc;n cứu?</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ C&aacute;c chỉ ti&ecirc;u n&agrave;o cần được đo lường trong suốt qu&aacute; tr&igrave;nh thử nghiệm?</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Phương ph&aacute;p xử l&yacute; số liệu n&agrave;o được d&ugrave;ng để b&aacute;c bỏ hay chấp nhận giả thuyết?</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><u><strong><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">2.2. Tổng quan nghi&ecirc;n cứu:</span></span></strong></u></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><em><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">2.2.1. Kh&aacute;i niệm v&agrave; vai tr&ograve; của tổng quan nghi&ecirc;n cứu:</span></span></em></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Kh&aacute;i niệm tổng quan l&yacute; thuyết:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">L&agrave; việc chọn lọc c&aacute;c t&agrave;i liệu về chủ đề nghi&ecirc;n cứu, trong đ&oacute; bao gồm c&aacute;c th&ocirc;ng tin, &yacute; tưởng, dữ liệu v&agrave; bằng chứng được tr&igrave;nh b&agrave;y tr&ecirc;n một quan điểm n&agrave;o đ&oacute; để ho&agrave;n th&agrave;nh c&aacute;c mục ti&ecirc;u đ&atilde; x&aacute;c định, đồng thời đ&aacute;nh gi&aacute; một c&aacute;ch hiệu quả c&aacute;c t&agrave;i liệu n&agrave;y tr&ecirc;n cơ sở li&ecirc;n hệ với nghi&ecirc;n cứu đang được thực hiện.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Việc tổng quan l&yacute; thuyết c&oacute; thể chia th&agrave;nh 2 nh&oacute;m:</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Nh&oacute;m thứ nhất: Tập trung v&agrave;o tổng quan c&aacute;c nghi&ecirc;n cứu thực tiễn đ&atilde; thực hiện trong qu&aacute; khứ để đưa ra kết luận chung về kết quả của c&aacute;c nghi&ecirc;n cứu n&agrave;y, nhằm mục đ&iacute;ch đ&uacute;c r&uacute;t những g&igrave; đ&atilde; l&agrave;m được (đ&atilde; tổng qu&aacute;t được) v&agrave; những g&igrave; cần được tiếp tục nghi&ecirc;n cứu (khe hổng nghi&ecirc;n cứu).</span></span></span></li>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Nh&oacute;m thứ hai : Tập trung v&agrave;o tổng quan l&yacute; thuyết trong đ&oacute; tr&igrave;nh b&agrave;y c&aacute;c l&yacute; thuyết đ&atilde; c&oacute; c&ugrave;ng giải th&iacute;ch một hiện tượng khoa học n&agrave;o đ&oacute; v&agrave; so s&aacute;nh ch&uacute;ng về mật độ s&acirc;u, t&iacute;nh nhất qu&aacute;n cũng như khả năng dự b&aacute;o của ch&uacute;ng. Như vậy, tổng quan l&yacute; thuyết cũng thường chứa đựng phần tổng quan nghi&ecirc;n cứu. Do đ&oacute;, c&oacute; thể sử dụng thuật ngữ tổng quan l&yacute; thuyết cho cả tổng quan nghi&ecirc;n cứu v&agrave; tổng quan l&yacute; thuyết (thuần t&uacute;y).</span></span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><em><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">2.2.2. Quy tr&igrave;nh tổng quan nghi&ecirc;n cứu:</span></span></em></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Vai tr&ograve; của tổng quan l&yacute; thuyết c&ograve;n được thể hiện qua việc phục vụ cho c&aacute;c c&ocirc;ng đoạn của qu&aacute; tr&igrave;nh nghi&ecirc;n cứu:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ C&aacute;c c&acirc;u hỏi đặt ra đối với c&ocirc;ng việc n&agrave;y, cụ thể như sau: </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">a. Nguồn t&agrave;i liệu n&agrave;o cần tham khảo về chủ đề nghi&ecirc;n cứu?</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">b. Những vấn đề, c&acirc;u hỏi nghi&ecirc;n cứu về chủ đề nghi&ecirc;n cứu l&agrave; g&igrave;? </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">c. Những vấn đề, những tranh luận ch&iacute;nh về chủ đề nghi&ecirc;n cứu? </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">d. Những &yacute; tưởng, kh&aacute;i niệm, l&yacute; thuyết về chủ đề nghi&ecirc;n cứu?</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">e. Những phương ph&aacute;p luận, phương ph&aacute;p v&agrave; c&ocirc;ng cụ nghi&ecirc;n cứu đ&atilde; sử dụng v&agrave; những tranh luận về việc sử dụng ch&uacute;ng?</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">f. C&aacute;ch thức sắp xếp những tri thức đ&atilde; c&oacute; về chủ đề nghi&ecirc;n cứu?</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Quy tr&igrave;nh tổng quan l&yacute; thuyết gồm c&aacute;c bước như sau:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Bước 1. X&aacute;c định những từ kh&oacute;a về chủ đề nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Bước 2. Tiến h&agrave;nh t&igrave;m kiếm t&agrave;i liệu li&ecirc;n quan.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Bước 3. Liệt k&ecirc; c&aacute;c t&agrave;i liệu c&oacute; li&ecirc;n quan mật thiết đến đề t&agrave;i nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Bước 4. Tiến h&agrave;nh nghi&ecirc;n cứu t&agrave;i liệu đ&atilde; lựa chọn.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Bước 5. Thiết kế sơ đồ tổng kết t&agrave;i liệu.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Bước 6. T&oacute;m tắt c&aacute;c b&agrave;i b&aacute;o quan trọng về chủ đề nghi&ecirc;n cứu, tr&iacute;ch dẫn v&agrave; liệt k&ecirc; c&aacute;c t&agrave;i liệu tham khảo.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Bước 7. Tổng kết lại c&aacute;c t&agrave;i liệu đ&atilde; nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><u><strong><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">2.3. Nội dung thiết kế nghi&ecirc;n cứu:</span></span></strong></u></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><em><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">2.3.1. Kh&aacute;i niệm:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></em></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- C&oacute; nhiều kh&aacute;i niệm về thiết kế nghi&ecirc;n cứu, nhưng đều thống nhất với nhau ở c&aacute;c nội dung, đ&oacute; l&agrave;:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">1)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thiết kế nghi&ecirc;n cứu l&agrave; bản kế hoạch về lựa chọn nguồn v&agrave; loại th&ocirc;ng tin sẽ sử dụng để c&oacute; thể trả lời những c&acirc;u hỏi nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">2)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thiết kế nghi&ecirc;n cứu l&agrave; kết cấu cơ bản thể hiện mối quan hệ giữa c&aacute;c biến của nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">3)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thiết kế nghi&ecirc;n cứu l&agrave; bản t&oacute;m tắt qu&aacute; tr&igrave;nh nghi&ecirc;n cứu từ c&ocirc;ng việc x&aacute;c định giả thiết đến ph&acirc;n t&iacute;ch dữ liệu.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Bản thiết kế nghi&ecirc;n cứu sẽ gồm c&oacute; 5 nội dung chủ yếu:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ X&aacute;c định &yacute; tưởng nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ X&aacute;c định l&yacute; thuyết nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ X&aacute;c định c&acirc;u hỏi nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ X&aacute;c định phương ph&aacute;p thu thập v&agrave; xử l&yacute; dữ liệu. </span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ X&aacute;c định phương ph&aacute;p chọn mẫu nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><em><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">2.3.2. Nội dung thiết kế nghi&ecirc;n cứu:</span></span></em></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- X&aacute;c định &yacute; tưởng v&agrave; vấn đề nghi&ecirc;n cứu; mục ti&ecirc;u v&agrave; c&aacute;c c&acirc;u hỏi nghi&ecirc;n cứu; h&igrave;nh th&agrave;nh giả thuyết nghi&ecirc;n cứu. Tất cả c&aacute;c nghi&ecirc;n cứu đều được định hướng bởi &yacute; tưởng nghi&ecirc;n cứu v&agrave; chuyển th&agrave;nh c&aacute;c c&acirc;u hỏi nghi&ecirc;n cứu, đến lượt m&igrave;nh c&aacute;c c&acirc;u hỏi nghi&ecirc;n cứu được x&aacute;c định từ mục ti&ecirc;u nghi&ecirc;n cứu của đề t&agrave;i. Mục ti&ecirc;u nghi&ecirc;n cứu ch&iacute;nh l&agrave; c&acirc;u hỏi kh&aacute;i qu&aacute;t, c&ograve;n c&acirc;u hỏi nghi&ecirc;n cứu (research question) l&agrave; c&aacute;c c&acirc;u hỏi cụ thể trong nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- X&aacute;c định phương ph&aacute;p tiếp cận:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Phương ph&aacute;p tiếp cận định lượng (Quantitative Approach hay Fixed Design)</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">L&agrave; c&aacute;ch tiếp cận li&ecirc;n quan đến việc nghi&ecirc;n cứu thực nghiệm mang t&iacute;nh hệ thống c&aacute;c thuộc t&iacute;nh định lượng, hiện tượng v&agrave; quan hệ giữa ch&uacute;ng. C&aacute;ch tiếp cận n&agrave;y nhấn mạnh đến phương ph&aacute;p nghi&ecirc;n cứu c&oacute; cấu tr&uacute;c chặt chẽ nhằm th&uacute;c đẩy qu&aacute; tr&igrave;nh lặp lại nghi&ecirc;n cứu (trong c&aacute;c t&igrave;nh huống, bối cảnh kh&aacute;c nhau) v&agrave; những quan s&aacute;t c&oacute; thể định lượng được sử dụng cho ph&acirc;n t&iacute;ch thống k&ecirc;.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">L&agrave; c&aacute;ch tiếp cận trong đ&oacute; nghi&ecirc;n cứu vi&ecirc;n t&igrave;m hiểu h&agrave;nh vi, động cơ v&agrave; &yacute; đồ đối tượng nghi&ecirc;n cứu (con người) v&agrave; những l&yacute; do điều khiển những h&agrave;nh vi đ&oacute;. Sự kh&aacute;c biệt cơ bản giữa nghi&ecirc;n cứu định t&iacute;nh v&agrave; nghi&ecirc;n cứu định lượng l&agrave; phương ph&aacute;p nghi&ecirc;n cứu định t&iacute;nh tập trung v&agrave;o qu&aacute; tr&igrave;nh thay v&igrave; kết quả, c&aacute;i tổng thể thay v&igrave; c&aacute;c biến độc lập v&agrave; tập trung v&agrave;o &yacute; nghĩa hơn l&agrave; thống k&ecirc; h&agrave;nh vi.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">C&oacute; s&aacute;u phương ph&aacute;p nghi&ecirc;n cứu c&oacute; thể &aacute;p dụng trong lĩnh vực nghi&ecirc;n cứu quản l&yacute; v&agrave; kinh doanh: nghi&ecirc;n cứu t&igrave;nh huống (case study), nghi&ecirc;n cứu l&yacute; thuyết (grounded theory), nghi&ecirc;n cứu nh&acirc;n học (ethnography), v&agrave; nghi&ecirc;n cứu h&agrave;nh động (action research) nghi&ecirc;n cứu thực nghiệm (experiment) v&agrave; nghi&ecirc;n cứu m&ocirc; tả (survey). Trong số đ&oacute;:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Phương ph&aacute;p tiếp cận định lượng: sử dụng c&aacute;c chiến lược như nghi&ecirc;n cứu thực nghiệm v&agrave; m&ocirc; tả (survey).</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Phương ph&aacute;p tiếp cận định t&iacute;nh: sử dụng bốn phương ph&aacute;p c&ograve;n lại gồm nghi&ecirc;n cứu t&igrave;nh huống, nghi&ecirc;n cứu l&yacute; thuyết, nghi&ecirc;n cứu nh&acirc;n học v&agrave; nghi&ecirc;n cứu h&agrave;nh động.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- X&aacute;c định c&aacute;c phương ph&aacute;p nghi&ecirc;n cứu cụ thể:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Phương ph&aacute;p khảo s&aacute;t (survey) l&agrave; phương ph&aacute;p thu thập dữ liệu nghi&ecirc;n cứu phổ biến nhất dựa tr&ecirc;n c&aacute;c bảng hỏi (questionnaire). Đặc điểm ch&iacute;nh của phương ph&aacute;p khảo s&aacute;t l&agrave; được sử dụng trong phương ph&aacute;p tiếp cận định lượng, thu thập một lượng nhỏ dữ liệu dưới định dạng được ti&ecirc;u chuẩn h&oacute;a từ một mẫu tương đối lớn v&agrave; qu&aacute; tr&igrave;nh chọn mẫu mang t&iacute;nh đại diện từ một tổng thể đ&atilde; biết.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Phương ph&aacute;p quan s&aacute;t.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Quan s&aacute;t l&agrave; phương ph&aacute;p thu thập dữ liệu truyền thống bằng việc quan s&aacute;t, ghi ch&eacute;p, m&ocirc; tả, ph&acirc;n t&iacute;ch v&agrave; diễn giải một c&aacute;ch hệ thống c&aacute;c hiện tượng x&atilde; hội được nghi&ecirc;n cứu. C&oacute; hai phương ph&aacute;p quan s&aacute;t kh&aacute;c nhau: quan s&aacute;t theo phương ph&aacute;p định lượng v&agrave; quan s&aacute;t định t&iacute;nh.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ Phỏng vấn.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Phỏng vấn l&agrave; một trong những phương ph&aacute;p thu thập dữ liệu chủ yếu trong nghi&ecirc;n cứu định t&iacute;nh. Phỏng vấn l&agrave; một phương ph&aacute;p rất hiệu quả trong đ&aacute;nh gi&aacute; nhận thức, c&aacute;c &yacute; nghĩa, x&aacute;c định c&aacute;c t&igrave;nh huống, cấu tr&uacute;c của hiện tượng nghi&ecirc;n cứu của một người hoặc nh&oacute;m người. Đ&acirc;y cũng l&agrave; một trong những phương ph&aacute;p mạnh nhất nhất để c&oacute; được sự thấu hiểu người kh&aacute;c (Punch, 2005).</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Dữ liệu thứ cấp bao gồm c&aacute;c văn bản viết như th&ocirc;ng b&aacute;o, bi&ecirc;n bản cuộc họp, thư từ, nhật k&yacute;, tiểu sử, th&ocirc;ng b&aacute;o của ch&iacute;nh phủ, c&aacute;c bản ghi h&agrave;nh ch&iacute;nh v&agrave; b&aacute;o c&aacute;o gửi c&aacute;c cổ đ&ocirc;ng hoặc đối tượng hữu quan cũng như c&aacute;c t&agrave;i liệu kh&ocirc;ng phải văn bản như băng ghi &acirc;m, phim ảnh, phim v&agrave; c&aacute;c chương tr&igrave;nh truyền h&igrave;nh.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Một số nghi&ecirc;n cứu c&oacute; thể dựa ho&agrave;n to&agrave;n v&agrave;o c&aacute;c dữ liệu thứ cấp trong khi một số nghi&ecirc;n cứu kh&aacute;c như nghi&ecirc;n cứu t&igrave;nh huống, nghi&ecirc;n cứu l&yacute; thuyết c&oacute; thể sử dụng kết hợp với phương ph&aacute;p phỏng vấn v&agrave; quan s&aacute;t. Khi sử dụng kết hợp với c&aacute;c dữ liệu kh&aacute;c, t&agrave;i liệu thu thập được c&oacute; thể rất quan trọng trong ph&eacute;p kiểm tra ch&eacute;o (triangulation), trong đ&oacute; một sự kết hợp ch&eacute;o c&aacute;c phương ph&aacute;p kh&aacute;c nhau v&agrave; c&aacute;c loại dữ liệu kh&aacute;c nhau được sử dụng trong một dự &aacute;n duy nhất.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">-&nbsp; Lập kế hoạch thời gian v&agrave; sử dụng c&aacute;c nguồn lực:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Sử dụng nguồn lực:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">C&aacute;c nguồn lực về t&agrave;i ch&iacute;nh, nh&acirc;n lực cho nghi&ecirc;n cứu như thế n&agrave;o, c&oacute; ph&ugrave; hợp với thời gian nghi&ecirc;n cứu hay kh&ocirc;ng?</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><em><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">2.3.4 C&aacute;c ti&ecirc;u ch&iacute; lựa chọn:</span></span></em></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- Sự ph&ugrave; hợp giữa vấn đề v&agrave; c&aacute;ch tiếp cận thiết kế nghi&ecirc;n cứu:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Nếu vấn đề l&agrave; nhận diện những yếu tố ảnh hưởng đến một kết quả, sử dụng một biện ph&aacute;p can thiệp, t&igrave;m hiểu c&aacute;c yếu tố dự b&aacute;o tốt nhất cho kết quả</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">. . . hay giải th&iacute;ch th&igrave; c&aacute;ch tiếp cận định lượng l&agrave; tốt nhất.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Mặt kh&aacute;c, nếu cần t&igrave;m hiểu một kh&aacute;i niệm hay một hiện tượng v&igrave; gần như kh&ocirc;ng c&oacute; nghi&ecirc;n cứu n&agrave;o từng được thực hiện về kh&aacute;i niệm hay hiện tượng đ&oacute;, th&igrave; c&aacute;ch tiếp cận định t&iacute;nh sẽ ph&ugrave; hợp.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Thiết kế theo c&aacute;c phương ph&aacute;p kết hợp sẽ gi&uacute;p ta thu t&oacute;m tốt nhất cả hai c&aacute;ch tiếp cận định t&iacute;nh v&agrave; định lượng.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Kinh nghiệm c&aacute; nh&acirc;n:</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Một nh&agrave; nghi&ecirc;n cứu được huấn luyện trong c&aacute;c chương tr&igrave;nh kỹ thuật, viết khoa học, thống k&ecirc; v&agrave; thống k&ecirc; điện to&aacute;n, vốn quen thuộc với c&aacute;c tạp ch&iacute; định lượng trong thư viện, rất c&oacute; thể sẽ chọn thiết kế định lượng. C&aacute;ch tiếp cận định t&iacute;nh li&ecirc;n quan nhiều hơn đến h&igrave;nh thức viết văn chương hơn, c&aacute;c chương tr&igrave;nh ph&acirc;n t&iacute;ch văn bản điện to&aacute;n, v&agrave; kinh nghiệm trong việc thực hiện c&aacute;c cuộc phỏng vấn c&oacute; kết th&uacute;c mở v&agrave; quan s&aacute;t. Nh&agrave; nghi&ecirc;n cứu theo c&aacute;c phương ph&aacute;p kết hợp cần quen thuộc với cả nghi&ecirc;n cứu định lượng v&agrave; định t&iacute;nh.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Độc giả:</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Cuối c&ugrave;ng, c&aacute;c nh&agrave; nghi&ecirc;n cứu cần nhạy cảm trước độc giả, người m&agrave; họ sẽ b&aacute;o c&aacute;o nghi&ecirc;n cứu của họ. C&aacute;c độc giả n&agrave;y c&oacute; thể l&agrave; c&aacute;c nh&agrave; bi&ecirc;n tập tạp ch&iacute;, độc giả tạp ch&iacute;, hội đồng tốt nghiệp, những người tham dự hội nghị, hay đồng nghiệp trong ng&agrave;nh. C&aacute;c sinh vi&ecirc;n n&ecirc;n xem x&eacute;t những c&aacute;ch tiếp cận thường được gi&aacute;o vi&ecirc;n huớng dẫn ủng hộ v&agrave; sử dụng.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><strong><u><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">2.4. M&ocirc; h&igrave;nh nghi&ecirc;n cứu:</span></span></u></strong></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><em><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">2.4.1. Kh&aacute;i niệm v&agrave; vai tr&ograve; của m&ocirc; h&igrave;nh nghi&ecirc;n cứu:</span></span></em></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- M&ocirc; h&igrave;nh: L&agrave; một hệ thống c&aacute;c yếu tố vật chất hoặc &yacute; niệm (tư duy) để biểu diễn, phản &aacute;nh hoặc t&aacute;i tạo đối tượng cần nghi&ecirc;n cứu, n&oacute; đ&oacute;ng vai tr&ograve; đại diện, thay thế đối tượng thực sao cho việc nghi&ecirc;n cứu m&ocirc; h&igrave;nh cho ta những th&ocirc;ng tin mới tương tự đối tượng thực.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">- T&iacute;nh chất của m&ocirc; h&igrave;nh nghi&ecirc;n cứu:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ T&iacute;nh tương tự : c&oacute; sự tương tự giữa m&ocirc; h&igrave;nh v&agrave; vật gốc, ch&uacute;ng c&oacute; những đặc điểm cơ bản c&oacute; thể so s&aacute;nh với nhau được như: cấu tr&uacute;c (đẳng cấu), chức năng, thuộc t&iacute;nh, cơ chế vận h&agrave;nh. . .</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ T&iacute;nh đơn giản: m&ocirc; h&igrave;nh chỉ ph&aacute;n &aacute;nh một hoặc một số mặt n&agrave;o đ&oacute; của đối tượng gốc.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ T&iacute;nh trực quan: m&ocirc; h&igrave;nh l&agrave; sự t&aacute;i hiện đối tượng nghi&ecirc;n cứu dưới dạng trực quan.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ T&iacute;nh l&yacute; tưởng: khi m&ocirc; h&igrave;nh h&oacute;a đối tượng gốc, ta đ&atilde; kh&aacute;i qu&aacute;t h&oacute;a, trừu tượng h&oacute;a, phản &aacute;nh đặc t&iacute;nh của đối tượng gốc ở mức độ ho&agrave;n thiện hơn (l&yacute; tưởng).</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">+ T&iacute;nh quy luật ri&ecirc;ng : m&ocirc; h&igrave;nh c&oacute; những t&iacute;nh chất ri&ecirc;ng được quy định bởi c&aacute;c phần tử tạo n&ecirc;n n&oacute;.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Vai tr&ograve; của m&ocirc; h&igrave;nh nghi&ecirc;n cứu:</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Gi&uacute;p hệ thống ho&aacute; c&aacute;c yếu tố t&aacute;c động l&ecirc;n vấn đề nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Hệ thống mối quan hệ nh&acirc;n quả giữa c&aacute;c yếu tố t&aacute;c động l&ecirc;n vấn đề nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Hệ thống v&agrave; dự đo&aacute;n chiều của c&aacute;c mối quan hệ t&aacute;c động nh&acirc;n quả trong vấn đề nghi&ecirc;n cứu</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">M&ocirc; h&igrave;nh nghi&ecirc;n cứu c&oacute; thể hiểu l&agrave; hệ thống c&aacute;c mối quan hệ giữa c&aacute;c đối tượng, c&aacute;c yếu tố trong vấn đề nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">M&ocirc; h&igrave;nh nghi&ecirc;n cứu được x&acirc;y dựng dựa tr&ecirc;n nền tảng l&yacute; thuyết thu thập được trong tổng quan nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">V&iacute; dụ:</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Nghi&ecirc;n cứu cầu về h&agrave;ng ho&aacute; v&agrave; dịch vụ, c&oacute; rất nhiều yếu tố t&aacute;c động đến cầu như: gi&aacute; cả của h&agrave;ng ho&aacute; (P), gi&aacute; cả của h&agrave;ng ho&aacute; li&ecirc;n quan (PR), thu nhập người ti&ecirc;u d&ugrave;ng (I), kỳ vọng (E ), thị hiếu (T ), thuế (t), trợ cấp (s) . . . Khi thể hiện mối quan hệ, lượng cầu (số lượng h&agrave;ng ho&aacute; m&agrave; người mua muốn mua v&agrave; c&oacute; khả năng mua) chịu t&aacute;c động của nhiều biến số như m&ocirc; h&igrave;nh h&agrave;m số sau:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">QD = f (P, PR , I, E , T , t, s . . . )</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">Mối quan hệ giữa c&aacute;c biến số trong m&ocirc; h&igrave;nh n&agrave;y c&oacute; thể được m&ocirc; tả cụ thể hơn nữa theo phương tr&igrave;nh tuyến t&iacute;nh:</span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><span style=\"font-size:14.0pt\">QD = c + &alpha;P + &beta;PR + &gamma;I + sT + &theta;t + &thetasym;s</span></span></span></p>\r\n\r\n<p>&nbsp;</p>', '1550901142', null, '0', '48', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('131', 'Chương 3', '<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">CHƯƠNG 3: NGHI&Ecirc;N CỨU ĐỊNH T&Iacute;NH</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>3.1 Tổng quan về nghi&ecirc;n cứu định t&iacute;nh:</strong></u></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>3.1.1. Kh&aacute;i niệm v&agrave; đặc điểm của nghi&ecirc;n cứu định t&iacute;nh:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">- Kh&aacute;i niệm:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Nghi&ecirc;n cứu định t&iacute;nh (NCĐT) l&agrave; nghi&ecirc;n cứu được đặc trưng bởi mục đ&iacute;ch của nghi&ecirc;n cứu v&agrave; phương ph&aacute;p được tiến h&agrave;nh để nghi&ecirc;n cứu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Mục đ&iacute;ch NCĐT l&agrave; nghi&ecirc;n cứu những mặt, những vấn đề của cuộc sống, x&atilde; hội, quan t&acirc;m đến &yacute; nghĩa của c&aacute;c hiện tượng, t&igrave;nh huống, sự việc.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phương ph&aacute;p tiến h&agrave;nh nghi&ecirc;n cứu định t&iacute;nh l&agrave; những phương ph&aacute;p gắn liền c&acirc;u chữ hơn l&agrave; c&aacute;c con số.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">NCĐT l&agrave; nghi&ecirc;n cứu thu thập, ph&acirc;n t&iacute;ch những dữ liệu mang t&iacute;nh m&ocirc; tả như những c&acirc;u viết, những h&agrave;nh vi xử sự của con người được quan s&aacute;t.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">- Đặc điểm của nghi&ecirc;n cứu định t&iacute;nh:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Về mục đ&iacute;ch nghi&ecirc;n cứu: nhằm mi&ecirc;u tả to&agrave;n diện, chi tiết vấn đề nghi&ecirc;n cứu. Bản chất NCĐT l&agrave; thăm d&ograve;, kh&aacute;m ph&aacute; để m&ocirc; tả v&agrave; hiểu r&otilde; được vấn đề.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Về chức năng: NCĐT cho ph&eacute;p kh&aacute;m ph&aacute; được cảm x&uacute;c, trạng th&aacute;i t&acirc;m l&yacute;, xử sự của c&aacute;c chủ thể cũng như kinh nghiệm của c&aacute;c chủ thể tham gia nghi&ecirc;n cứu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Về c&ocirc;ng cụ thu thập dữ liệu: Nh&agrave; nghi&ecirc;n cứu phải sử dụng nhiều chiến thuật, nhiều phương thức để thu thập dữ liệu t&ugrave;y theo hướng nghi&ecirc;n cứu, đối tượng nghi&ecirc;n cứu:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Về dạng dữ liệu được thu thập: v&igrave; NCĐT cung cấp, ph&acirc;n t&iacute;ch những dữ liệu về mặt &yacute; nghĩa, nội dung, kh&ocirc;ng phải l&agrave; c&aacute;c con số, c&aacute;c biến số n&ecirc;n dữ liệu</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Về mẫu khảo s&aacute;t: NCĐT thường sử dụng c&aacute;c mẫu khảo s&aacute;t nhỏ.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Về c&aacute;ch tiếp cận: NCĐT chủ yếu mang t&iacute;nh chủ quan bởi nghi&ecirc;n cứu n&agrave;y nhằm t&igrave;m hiểu về h&agrave;nh vi, xử sự của con người cũng như t&igrave;m ra c&aacute;c nguy&ecirc;n nh&acirc;n, l&yacute; do của những h&agrave;nh vi, xử sự n&agrave;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Về kết quả nghi&ecirc;n cứu: Kết quả của NCĐT được thể hiện bằng b&aacute;o c&aacute;o mang t&iacute;nh tường thuật m&ocirc; tả lại hiện tượng, t&igrave;nh huống v&agrave; chứa đựng những tr&iacute;ch dẫn trực tiếp từ người tham gia nghi&ecirc;n cứu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Về mức độ linh hoạt của nghi&ecirc;n cứu: NCĐT cho ph&eacute;p sự h&ograve;a hợp trong tương t&aacute;c giữa nh&agrave; nghi&ecirc;n cứu v&agrave; những người tham gia.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>3.1.2 C&aacute;c phương ph&aacute;p v&agrave; c&ocirc;ng cụ nghi&ecirc;n cứu định t&iacute;nh:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">C&oacute; nhiều phương ph&aacute;p v&agrave; c&ocirc;ng cụ nghi&ecirc;n cứu định t&iacute;nh kh&aacute;c nhau v&agrave; kh&oacute; c&oacute; thể ph&acirc;n loại một c&aacute;ch ho&agrave;n chỉnh.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phương ph&aacute;p phổ biến trong kinh doanh: phương ph&aacute;p l&yacute; thuyết nền (ground theory) v&agrave; phương ph&aacute;p t&igrave;nh huống.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">C&ocirc;ng cụ ch&iacute;nh: l&agrave; thảo luận nh&oacute;m, phỏng vấn c&aacute; nh&acirc;n v&agrave; quan s&aacute;t.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phương ph&aacute;p l&yacute; thuyết nền:</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phương ph&aacute;p l&yacute; thuyết nền hiểu đơn giản l&agrave; phương ph&aacute;p dựa tr&ecirc;n những dữ liệu cơ bản để tạo ra l&yacute; thuyết. C&aacute;c l&yacute; thuyết được h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển dựa tr&ecirc;n sự thu thập v&agrave; ph&acirc;n t&iacute;ch th&ocirc;ng tin của nh&agrave; nghi&ecirc;n cứu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Hai bước để &ldquo;tạo sinh giả thuyết&rdquo;: (1) ph&aacute;t vấn v&agrave; (2) tạo sinh giả thuyết bằng c&aacute;ch m&atilde; h&oacute;a l&yacute; thuyết.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Trọng t&acirc;m của phương ph&aacute;p n&agrave;y l&agrave; &quot;mẫu l&yacute; thuyết&quot;.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Đặc trưng của phương ph&aacute;p l&yacute; thuyết nền l&agrave; ba cấp độ m&atilde; h&oacute;a dữ liệu:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">M&atilde; h&oacute;a mở (open coding).</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">M&atilde; h&oacute;a định hướng (axial coding). </span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">M&atilde; h&oacute;a lựa chọn (selective coding).</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">3.2. C&aacute;c phương ph&aacute;p sử dụng trong nghi&ecirc;n cứu định t&iacute;nh:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>3.2.1.&nbsp;Phương ph&aacute;p nghi&ecirc;n cứu t&igrave;nh huống:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">- L&agrave; phương ph&aacute;p nghi&ecirc;n cứu một hoặc nhiều t&igrave;nh huống nhằm l&agrave;m s&aacute;ng tỏ một hiện tượng cần nghi&ecirc;n cứu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Nh&agrave; nghi&ecirc;n cứu n&ecirc;n lựa chọn nhiều t&igrave;nh huống nếu nghi&ecirc;n cứu c&oacute; mục ti&ecirc;u l&agrave; nhằm kh&aacute;i qu&aacute;t h&oacute;a, tạo ra l&yacute; thuyết mới.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Nghi&ecirc;n cứu một t&igrave;nh huống sẽ ph&ugrave; hợp đối với những nghi&ecirc;n cứu đi s&acirc;u m&ocirc; tả, xem x&eacute;t to&agrave;n diện một vấn đề n&agrave;o đ&oacute;.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Quy tr&igrave;nh x&acirc;y dựng l&yacute; thuyết bằng phương ph&aacute;p t&igrave;nh huống:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Chọn t&igrave;nh huống nghi&ecirc;n cứu:</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Theo Baxter v&agrave; Jack (2008), để x&aacute;c định được t&igrave;nh huống nghi&ecirc;n cứu, nh&agrave; nghi&ecirc;n cứu h&atilde;y tự đặt ra c&aacute;c c&acirc;u hỏi sau để trả lời:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ M&igrave;nh muốn nghi&ecirc;n cứu c&aacute; nh&acirc;n con người, hay nghi&ecirc;n cứu về một chương tr&igrave;nh, hay về một qu&aacute; tr&igrave;nh?</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Liệu m&igrave;nh c&oacute; muốn nghi&ecirc;n cứu về sự kh&aacute;c biệt giữa c&aacute;c tổ chức hay kh&ocirc;ng</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">- C&oacute; nhiều PP tiến h&agrave;nh nghi&ecirc;n cứu t&igrave;nh huống: NC diễn giải, nghi&ecirc;n cứu m&ocirc; tả, nghi&ecirc;n cứu một t&igrave;nh huống hoặc đa t&igrave;nh huống.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Quy tr&igrave;nh x&acirc;y dựng l&yacute; thuyết bằng PP t&igrave;nh huống theo Eisenhardt (1989):</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Bước 1 : x&aacute;c định c&acirc;u hỏi nghi&ecirc;n cứu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Bước 2 : Lựa chọn t&igrave;nh huống</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Bước 3 : Chọn c&ocirc;ng cụ v&agrave; phương ph&aacute;p thu thập dữ liệu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Bước 4 : Tiến h&agrave;nh thu thập dữ liệu tại hiện trường.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Bước 5 : Ph&acirc;n t&iacute;ch dữ liệu</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Bước 6 : Thiết lập c&aacute;c giả thuyết </span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Bước 7 : So s&aacute;nh với l&yacute; thuyết </span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Bước 8 : Kết luận</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>3.2.2.&nbsp;Phương ph&aacute;p nghi&ecirc;n cứu t&agrave;i liệu:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">L&agrave; phương ph&aacute;p được đặc trưng bởi việc ph&acirc;n t&iacute;ch, nghi&ecirc;n cứu c&aacute;c dữ liệu đ&atilde; c&oacute; sẵn được thể hiện dưới dạng văn bản, h&igrave;nh ảnh đ&atilde; được lưu, đ&atilde; được định dạng m&agrave; kh&ocirc;ng c&oacute; sự can thiệp của nh&agrave; nghi&ecirc;n cứu.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>3.2.3.&nbsp;Phương ph&aacute;p kh&aacute;c:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">- Phương ph&aacute;p hiện tượng học:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">L&agrave; phương ph&aacute;p nghi&ecirc;n cứu định t&iacute;nh m&agrave; nh&agrave; nghi&ecirc;n cứu sử dụng để hiểu r&otilde; việc một hoặc nhiều c&aacute; nh&acirc;n trải nghiệm một sự việc n&agrave;o đ&oacute;</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Trường hợp sử dụng: những nghi&ecirc;n cứu t&igrave;m hiểu về động cơ, h&agrave;nh vi của con người đ&atilde; trải qua một hiện tượng cụ thể n&agrave;o đ&oacute;.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">- Phương ph&aacute;p d&acirc;n tộc học:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Mục ti&ecirc;u chủ yếu của phương ph&aacute;p n&agrave;y l&agrave; m&ocirc; tả về văn h&oacute;a của một nh&oacute;m người.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Lưu &yacute; l&agrave; thuật ngữ văn h&oacute;a được sử dụng ở đ&acirc;y l&agrave; nhằm chia sẻ về th&aacute;i độ, c&aacute;c gi&aacute; trị, th&oacute;i quen, ng&ocirc;n ngữ v&agrave; những gi&aacute; trị vật chất v&agrave; tinh thần của nh&oacute;m người đ&oacute;.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">- Phương ph&aacute;p nghi&ecirc;n cứu lịch sử học:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">L&agrave; phương ph&aacute;p nghi&ecirc;n cứu những sự kiện đ&atilde; xảy ra trong qu&aacute; khứ bằng c&aacute;ch thu thập một c&aacute;ch c&oacute; hệ thống v&agrave; đ&aacute;nh gi&aacute; kh&aacute;ch quan c&aacute;c dữ liệu li&ecirc;n quan đến sự kiện đ&oacute;.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Mục đ&iacute;ch: kiểm tra giả thuyết về nguy&ecirc;n nh&acirc;n, hiệu ứng hoặc xu hướng của những sự kiện n&agrave;y v&agrave; giải th&iacute;ch c&aacute;c sự kiện hiện tại cũng như dự đo&aacute;n c&aacute;c sự kiện trong tương lai.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>3.3. Quy tr&igrave;nh nghi&ecirc;n cứu định t&iacute;nh:</strong></u></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">- X&aacute;c định c&acirc;u hỏi nghi&ecirc;n cứu ban đầu:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Nh&agrave; nghi&ecirc;n cứu x&aacute;c định được c&acirc;u hỏi nghi&ecirc;n cứu r&otilde; r&agrave;ng th&igrave; sẽ x&aacute;c định đ&uacute;ng đối tượng nghi&ecirc;n cứu, mức độ nghi&ecirc;n cứu v&agrave; phương ph&aacute;p nghi&ecirc;n cứu ph&ugrave; hợp.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">C&acirc;u hỏi nghi&ecirc;n cứu cần đạt c&aacute;c ti&ecirc;u ch&iacute;:</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ C&acirc;u hỏi r&otilde; r&agrave;ng, kh&ocirc;ng qu&aacute; d&agrave;i, phức tạp, dễ g&acirc;y nhầm lẫn.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ C&acirc;u hỏi c&oacute; khả năng thực hiện được, c&oacute; t&iacute;nh đến nguồn dữ liệu, tiềm lực kỹ thuật v&agrave; con người.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ C&acirc;u hỏi phải ph&ugrave; hợp, hướng tới c&acirc;u trả lời mang t&iacute;nh khoa học, chứ kh&ocirc;ng phải mang t&iacute;nh đạo đức, kh&ocirc;ng phải l&agrave; c&acirc;u trả lời chứa đựng những nhận định mang t&iacute;nh chủ quan, c&aacute; nh&acirc;n.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Kh&aacute;m ph&aacute; vấn đề nghi&ecirc;n cứu:</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Kh&aacute;m ph&aacute; vấn đề nhằm đạt được những th&ocirc;ng tin c&oacute; chất lượng v&agrave; t&igrave;m ra được c&aacute;ch thức tốt nhất để c&oacute; được những th&ocirc;ng tin đ&oacute;.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Nội dung thực hiện để kh&aacute;m ph&aacute; ra được vấn đề cần nghi&ecirc;n cứu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Tham khảo t&agrave;i liệu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Thực hiện c&aacute;c cuộc phỏng vấn, quan s&aacute;t.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Đối tượng phỏng vấn thường hướng tới ở bước n&agrave;y: c&aacute;c chuy&ecirc;n gia trong lĩnh vực nghi&ecirc;n cứu, c&aacute;c nh&acirc;n chứng v&agrave; những người c&oacute; li&ecirc;n quan trực tiếp.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">C&oacute; thể thực hiện phỏng vấn thăm d&ograve; c&ugrave;ng với c&aacute;c quan s&aacute;t v&agrave; ph&acirc;n t&iacute;ch t&agrave;i liệu.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">X&aacute;c định vấn đề nghi&ecirc;n cứu:</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Việc x&aacute;c định được vấn đề nghi&ecirc;n cứu thường được thực hiện theo hai bước sau: trước ti&ecirc;n, l&agrave;m s&aacute;ng tỏ c&aacute;c vấn đề c&oacute; thể đặt ra trong nghi&ecirc;n cứu, sau đ&oacute; lựa chọn v&agrave; x&acirc;y dựng một vấn đề của nghi&ecirc;n cứu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Vấn đề nghi&ecirc;n cứu cần được lựa chọn tr&ecirc;n hai ti&ecirc;u ch&iacute; sau:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ C&oacute; hay kh&ocirc;ng khung l&yacute; thuyết ph&ugrave; hợp với vấn đề đặt ra.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Khả năng thực hiện vấn đề nghi&ecirc;n cứu.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">X&acirc;y dựng m&ocirc; h&igrave;nh nghi&ecirc;n cứu:</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phương ph&aacute;p quy nạp&nbsp;&nbsp;&nbsp;&nbsp; </span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phương ph&aacute;p diễn dịch</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Kiểm nghiệm:</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Quy tr&igrave;nh kiểm nghiệm:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Bước 1 : X&aacute;c định c&ocirc;ng cụ kiểm nghiệm: C&ocirc;ng cụ đ&oacute; phải c&oacute; khả năng tạo ra c&aacute;c th&ocirc;ng tin ph&ugrave; hợp v&agrave; cần thiết để kiểm chứng giả thuyết.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Bước 2 : Đ&aacute;nh gi&aacute; c&ocirc;ng cụ kiểm nghiệm. C&acirc;u hỏi trong c&ocirc;ng cụ quan s&aacute;t phải r&otilde; rang, người đọc đều hiểu chung về c&ugrave;ng một vấn đề.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Bước 3 : Thu thập dữ liệu. Thu thập c&aacute;c th&ocirc;ng tin một c&aacute;ch r&otilde; r&agrave;ng từ những đối tượng được x&aacute;c định.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Ph&acirc;n t&iacute;ch dữ liệu:</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Mục ti&ecirc;u của ph&acirc;n t&iacute;ch dữ liệu l&agrave;:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Kiểm chứng giả thuyết.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Giải th&iacute;ch những vấn đề thực tiễn nằm ngo&agrave;i dự đo&aacute;n. </span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Xem x&eacute;t lại hoặc chọn lọc c&aacute;c giả thuyết.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Cải tiến m&ocirc; h&igrave;nh nghi&ecirc;n cứu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ T&igrave;m ra c&aacute;c hướng nghi&ecirc;n cứu mới trong tương lai.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>3.4. Thu thập dữ liệu trong nghi&ecirc;n cứu định t&iacute;nh:</strong></u></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>3.4.1. Dữ liệu trong nghi&ecirc;n cứu định t&iacute;nh Dữ liệu tồn tại ở nhiều dạng kh&aacute;c nhau:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Dạnh định t&iacute;nh (bằng chữ): năng lực cạnh tranh sản phẩm . . .</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Dạng định lượng (bằng số): doanh thu, chi ph&iacute; lao động . . .</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>3.4.2. Chọn mẫu trong nghi&ecirc;n cứu định t&iacute;nh:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">- Nguy&ecirc;n tắc chọn mẫu trong nghi&ecirc;n cứu định t&iacute;nh:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Th&ocirc;ng tin được thu thập cho tới khi kh&ocirc;ng c&oacute; dấu hiệu mới th&igrave; lượng mẫu được coi l&agrave; đủ.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Chất lượng mẫu quyết định to&agrave;n bộ chất lượng của qu&aacute; tr&igrave;nh nghi&ecirc;n cứu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ V&igrave; số mẫu trong nghi&ecirc;n cứu định t&iacute;nh thường nhỏ, t&aacute;c động của những sai lệch khi chọn mẫu với kết quả nghi&ecirc;n cứu thường rất nghi&ecirc;m trọng.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">- C&aacute;c phương ph&aacute;p chọn mẫu trong nghi&ecirc;n cứu định t&iacute;nh:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Chọn mẫu theo mục đ&iacute;ch.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Chọn mẫu theo chỉ ti&ecirc;u</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Chọn mẫu theo &quot;quả b&oacute;ng tuyết&quot;.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>3.4.3. C&ocirc;ng cụ thu thập dữ liệu trong nhi&ecirc;n cứu định t&iacute;nh:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">- Phỏng vấn s&acirc;u:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phỏng vấn s&acirc;u l&agrave; phương ph&aacute;p đưa ra những c&acirc;u hỏi đối với người đối thoại để thu thập th&ocirc;ng tin xem đối tượng được phỏng vấn l&agrave;m g&igrave;, suy nghĩ g&igrave; hoặc cảm thấy g&igrave;.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">C&acirc;u hỏi phỏng vấn s&acirc;u thường sử dụng c&aacute;c c&acirc;u hỏi phi cấu tr&uacute;c, b&aacute;n cấu tr&uacute;c hoặc c&oacute; cấu tr&uacute;c v&agrave; n&oacute;i chung c&oacute; kết th&uacute;c mở; &iacute;t về số lượng v&agrave; nhằm khơi gợi quan điểm v&agrave; &yacute; kiến của người tham gia.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Phỏng vấn phi cấu tr&uacute;c:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Nh&agrave; nghi&ecirc;n cứu ghi nhớ một số chủ đề cần phỏng vấn v&agrave; c&oacute; thể sử dụng một danh mục chủ đề để khỏi bỏ s&oacute;t trong khi phỏng vấn.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Nh&agrave; nghi&ecirc;n cứu c&oacute; thể chủ động thay đổi thứ tự c&aacute;c chủ đề theo ho&agrave;n cảnh phỏng vấn.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phỏng vấn phi cấu tr&uacute;c giống như n&oacute;i chuyện, thoải m&aacute;i v&agrave; cởi mở.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phỏng vấn phi cấu tr&uacute;c rất kh&oacute; hệ thống h&oacute;a c&aacute;c th&ocirc;ng tin v&agrave; ph&acirc;n t&iacute;ch số liệu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phỏng vấn b&aacute;n cấu tr&uacute;c</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phỏng vấn b&aacute;n cấu tr&uacute;c v&agrave; phỏng vấn dựa theo danh mục c&aacute;c c&acirc;u hỏi hoặc chủ đề cần đề cập đến.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Thứ tự v&agrave; c&aacute;ch đặt c&acirc;u hỏi c&oacute; thể t&ugrave;y thuộc v&agrave;o ngữ cảnh v&agrave; đặc điểm của đối tượng phỏng vấn.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Nh&agrave; nghi&ecirc;n cứu c&oacute; thể sử dụng bản hướng dẫn b&aacute;n cấu tr&uacute;c tr&ecirc;n cơ sở những phỏng vấn thăm d&ograve; trước đ&oacute; để x&aacute;c định c&acirc;u hỏi ph&ugrave; hợp.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">- Thảo luận nh&oacute;m:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">L&agrave; c&aacute;ch thức thi thập dữ liệu qua đ&oacute; những th&agrave;nh vi&ecirc;n được lựa chọn thảo luận về phản ứng hoặc cảm gi&aacute;c của họ về một sản phẩm, dịch vụ, một t&igrave;nh huống hoặc một kh&aacute;i niệm dưới sự hướng dẫn của một người trưởng nh&oacute;m (người hướng dẫn thảo luận).</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Ph&acirc;n t&iacute;ch dữ liệu l&agrave; qu&aacute; tr&igrave;nh r&uacute;t gọn c&aacute;c dữ liệu được thu thập lại l&agrave;m cho ch&uacute;ng c&oacute; &yacute; nghĩa.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Qu&aacute; tr&igrave;nh ph&acirc;n t&iacute;ch dữ liệu c&oacute; thể ph&acirc;n th&agrave;nh ba bước sau:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">(1)&nbsp;&nbsp;&nbsp;&nbsp; M&atilde; h&oacute;a dữ liệu</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">(2)&nbsp;&nbsp;&nbsp;&nbsp; Tạo nh&oacute;m th&ocirc;ng tin</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">(3)&nbsp;&nbsp;&nbsp;&nbsp; Kết nối dữ liệu</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>3.5. Ph&acirc;n t&iacute;ch dữ liệu trong nghi&ecirc;n cứu định t&iacute;nh:</strong></u></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>3.5.1&nbsp; M&atilde; h&oacute;a dữ liệu:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Mục đ&iacute;ch: nhận dạng c&aacute;c dữ liệu, m&ocirc; tả dữ liệu v&agrave; tập hợp c&aacute;c dữ liệu nhằm phục vụ x&aacute;c định mối quan hệ giữa c&aacute;c dữ liệu sau n&agrave;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Vai tr&ograve;: gi&uacute;p nh&agrave; nghi&ecirc;n cứu c&oacute; được c&aacute;i nh&igrave;n tổng qu&aacute;t về dữ liệu, gi&uacute;p nh&agrave; nghi&ecirc;n cứu kh&aacute;m ph&aacute; được c&aacute;c kh&aacute;i niệm nghi&ecirc;n cứu, l&agrave;m cơ sở để x&acirc;y dựng kh&aacute;i niệm v&agrave; l&yacute; thuyết.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phương thức thể hiện: bằng ng&ocirc;n từ, h&igrave;nh ảnh; ph&acirc;n chia c&aacute;c c&acirc;u, c&aacute;c đoạn dữ liệu . . .</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Nh&agrave; nghi&ecirc;n cứu c&oacute; thể lập danh mục c&aacute;c nội dung nghi&ecirc;n cứu v&agrave; gắn với từng chủ đề cụ thể.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>3.5.2&nbsp; Tạo nh&oacute;m th&ocirc;ng tin:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Mục đ&iacute;ch: nhằm ph&acirc;n t&iacute;ch mối quan hệ giữa c&aacute;c nh&oacute;m th&ocirc;ng tin.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">C&aacute;c nh&oacute;m th&ocirc;ng tin n&agrave;y c&oacute; thể l&agrave; c&aacute;c kh&aacute;i niệm được n&ecirc;u trong giả thuyết, hoặc c&oacute; thể l&agrave; c&aacute;c chiều nghi&ecirc;n cứu của kh&aacute;i niệm, hoặc c&aacute;c chỉ dẫn, thuộc t&iacute;nh nhằm x&aacute;c lập kh&aacute;i niệm.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>3.5.3&nbsp; Kết nối dữ liệu:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Mục đ&iacute;ch: nhằm so s&aacute;nh được kết quả quan s&aacute;t với kết quả được mong đợi cũng như giải th&iacute;ch được khoảng c&aacute;ch nếu c&oacute; giữa hai loại kết quả n&agrave;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Nh&agrave; nghi&ecirc;n cứu tiến h&agrave;nh so s&aacute;nh để c&oacute; thể ph&aacute;t triển c&aacute;c chủ đề v&agrave; c&aacute;c chủng loại th&ocirc;ng tin.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>3.5.3 Kết nối dữ liệu:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Nh&agrave; nghi&ecirc;n cứu sẽ thực hiện việc suy luận, ph&aacute;t triển m&ocirc; h&igrave;nh, hoặc kh&aacute;i qu&aacute;t th&agrave;nh l&yacute; thuyết.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Đồng thời, nh&agrave; nghi&ecirc;n cứu cần kiểm tra lại sự thống nhất giữa c&aacute;c kh&aacute;i niệm v&agrave; l&yacute; thuyết.</span></span></p>', '1550901155', null, '0', '48', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('132', 'Chương 4', '<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">CHƯƠNG 4: NGHI&Ecirc;N CỨU ĐỊNH LƯỢNG</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>4.1. Tổng quan về nghi&ecirc;n cứu định lượng:</strong></u></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Dựa v&agrave;o sự kh&aacute;c biệt trong c&aacute;ch thức thu thập v&agrave; ph&acirc;n t&iacute;ch dữ liệu, c&oacute; thể ph&acirc;n chia th&agrave;nh 3 phương ph&aacute;p nghi&ecirc;n cứu:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Nghi&ecirc;n cứu định t&iacute;nh (qualitative research) </span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Nghi&ecirc;n cứu định lượng (quantitative research) </span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Nghi&ecirc;n cứu phối hợp (mixed research)</span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>4.1.1. Nghi&ecirc;n cứu định lượng:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Theo Burns &amp; Grove (1987) phương ph&aacute;p định lượng l&agrave; &quot;một qui tr&igrave;nh nghi&ecirc;n cứu ch&iacute;nh thức, kh&aacute;ch quan v&agrave; c&oacute; hệ thống trong đ&oacute; c&aacute;c dữ liệu số được sử dụng để thu thập th&ocirc;ng tin về thế giới&quot; v&agrave; &quot;đ&oacute; l&agrave; một phương ph&aacute;p được sử dụng để m&ocirc; tả v&agrave; kiểm định c&aacute;c mối quan hệ, li&ecirc;n hệ nh&acirc;n quả&quot;.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Đặc trưng của nghi&ecirc;n cứu định lượng l&agrave; gắn với thu thập v&agrave; xử l&yacute; số liệu dưới dạng số, dựa tr&ecirc;n cơ sở l&yacute; thuyết để x&acirc;y dựng m&ocirc; h&igrave;nh nghi&ecirc;n cứu v&agrave; c&aacute;c giả thuyết đi k&egrave;m. C&aacute;c m&ocirc; h&igrave;nh to&aacute;n v&agrave; c&ocirc;ng cụ thống k&ecirc; sẽ được d&ugrave;ng trong việc m&ocirc; tả, giải th&iacute;ch v&agrave; dự đo&aacute;n c&aacute;c hiện tượng.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Tiến tr&igrave;nh nghi&ecirc;n cứu định lượng gồm c&aacute;c c&ocirc;ng việc: </span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">X&aacute;c định tổng thể nghi&ecirc;n cứu</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">X&aacute;c định mẫu điều tra </span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Thiết kế bảng c&acirc;u hỏi</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Điều tra v&agrave; thu thập bảng hỏi </span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Ph&acirc;n t&iacute;ch dữ liệu</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Tr&igrave;nh b&agrave;y kết quả nghi&ecirc;n cứu</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Đưa ra c&aacute;c diễn giải v&agrave; b&agrave;n luận kết quả nghi&ecirc;n cứu.</span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>4.1.2. Quy tr&igrave;nh nghi&ecirc;n cứu định lượng:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">(1) C&acirc;u hỏi nghi&ecirc;n cứu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Ta bắt đầu với việc x&aacute;c định vấn đề v&agrave; mục đ&iacute;ch nghi&ecirc;n cứu được cụ thể h&oacute;a th&agrave;nh một hoặc một v&agrave;i c&acirc;u hỏi nghi&ecirc;n cứu (research question). Vấn đề v&agrave; c&acirc;u hỏi nghi&ecirc;n cứu đ&aacute;ng để nghi&ecirc;n cứu khi n&oacute; chưa được c&aacute;c nh&agrave; khoa học trước đ&oacute; giải quyết v&agrave; c&oacute; &yacute; nghĩa thực tiễn.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">(2) Tổng quan nghi&ecirc;n cứu (Đ&atilde; tr&igrave;nh b&agrave;y tại chương 2 mục 2.2).</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">(3) Phương ph&aacute;p nghi&ecirc;n cứu.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Thu thập v&agrave; ph&acirc;n t&iacute;ch dữ liệu ph&ugrave; hợp với ph&ugrave; hợp với c&acirc;u hỏi v&agrave; c&aacute;c giả thuyết nghi&ecirc;n cứu đ&atilde; đề ra.</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Lựa chọn thang đo v&agrave; thiết kế bảng c&acirc;u hỏi.</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">X&aacute;c lập c&aacute;ch thức chọn mẫu v&agrave; điều tra.</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Lựa chọn c&ocirc;ng cụ thống k&ecirc; ph&ugrave; hợp để ph&acirc;n t&iacute;ch số liệu.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">(4) Kết quả nghi&ecirc;n cứu.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Tr&igrave;nh b&agrave;y kết quả ph&acirc;n t&iacute;ch dữ liệu.</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Diễn giải kết quả theo ng&ocirc;n ngữ thống k&ecirc; v&agrave; ng&ocirc;n ngữ nghi&ecirc;n cứu.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">(5) Trao đổi, b&agrave;n luận.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Kết quả n&agrave;o l&agrave; khẳng định lại từ trong l&yacute; thuyết.</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Kết quả n&agrave;o l&agrave; đ&oacute;ng g&oacute;p mới.</span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>4.2. Dữ liệu nghi&ecirc;n cứu định lượng:</strong></u></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Theo Mark Saunders, dựa v&agrave;o c&aacute;ch thức thu thập dữ liệu, c&oacute; thể chia th&agrave;nh 2 nguồn dữ liệu ch&iacute;nh: dữ liệu sơ cấp (primary data) v&agrave; dữ liệu thứ cấp (secondary data).</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>4.2.1. Dữ liệu thứ cấp:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Thường c&aacute;c nh&agrave; khoa học sẽ t&igrave;m trong c&aacute;c cuốn s&aacute;ch, t&agrave;i liệu chuy&ecirc;n khảo, b&agrave;i b&aacute;o khoa học li&ecirc;n quan đến chủ đề nghi&ecirc;n cứu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Ngo&agrave;i ra, do c&oacute; nhiều dữ liệu thứ cấp kh&ocirc;ng dễ d&agrave;ng truy cập, sinh vi&ecirc;n c&oacute; thể t&igrave;m tới c&aacute;c ấn phẩm của b&ecirc;n thứ ba về c&aacute;c doanh nghiệp.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Một k&ecirc;nh th&ocirc;ng tin được sử dụng nhiều nhất hiện nay, ch&iacute;nh l&agrave; Internet. Tuy nhi&ecirc;n, lượng th&ocirc;ng tin tr&ecirc;n k&ecirc;nh n&agrave;y lại rất nhiều v&agrave; kh&ocirc;ng được sắp xếp một c&aacute;ch khoa học, v&igrave; thế sinh vi&ecirc;n cần lựa chọn những k&ecirc;nh th&ocirc;ng tin ch&iacute;nh thống (như cơ quan nh&agrave; nước, viện nghi&ecirc;n cứu, tổ chức quốc tế&hellip;) v&agrave; thực hiện lựa chọn từ kh&oacute;a ph&ugrave; hợp để ph&acirc;n loại th&ocirc;ng tin ngay từ đầu.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Ưu điểm:</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Gi&uacute;p tiết kiệm thời gian v&agrave; chi ph&iacute; thu thập dữ liệu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Qu&aacute; tr&igrave;nh thu thập dữ liệu thứ cấp đảm bảo sự k&iacute;n đ&aacute;o trong nghi&ecirc;n cứu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Sử dụng dữ liệu thứ cấp gi&uacute;p việc thực hiện c&aacute;c nghi&ecirc;n cứu d&agrave;i hạn c&oacute; so s&aacute;nh đối chiếu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Sử dụng dữ liệu thứ cấp c&oacute; thể dẫn tới những kh&aacute;m ph&aacute; bất ngờ.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Nhược điểm:</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Dữ liệu thứ cấp c&oacute; thể được thu thập cho mục đ&iacute;ch n&agrave;o đ&oacute; kh&ocirc;ng ph&ugrave; hợp với nhu cầu của nh&agrave; khoa học.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Truy cập dữ liệu thứ cấp c&oacute; thể kh&oacute; khăn hoặc tốn k&eacute;m.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>4.2.2. Dữ liệu sơ cấp:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">&nbsp;Khi dữ liệu thứ cấp kh&ocirc;ng c&oacute; sẵn hoặc kh&ocirc;ng gi&uacute;p trả lời c&aacute;c c&acirc;u hỏi nghi&ecirc;n cứu hiện tại, ch&uacute;ng ta cần tự m&igrave;nh thu thập dữ liệu cho ph&ugrave; hợp với y&ecirc;u cầu của vấn đề nghi&ecirc;n cứu. C&aacute;c dữ liệu tự thu thập n&agrave;y ch&iacute;nh l&agrave; dữ liệu sơ cấp.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Dữ liệu chưa c&oacute; sẵn: Đ&atilde; c&oacute; trong thực tế nhưng chưa ai thu thập.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Dữ liệu chưa c&oacute; trong thực tế: Chưa tồn tại trong thực tế t&iacute;nh đến thời điểm nghi&ecirc;n cứu. Nh&agrave; khoa học cần thiết kế c&aacute;c thử nghiệm ph&ugrave; hợp đề tạo ra v&agrave; thu thập dữ liệu.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>4.3. Chọn mẫu trong nghi&ecirc;n cứu định lượng:</strong></u></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>4.3.1. Sự cần thiết phải chọn mẫu:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">V&igrave; nhiều l&yacute; do m&agrave; nh&agrave; nghi&ecirc;n cứu kh&ocirc;ng tiến h&agrave;nh thu thập dữ liệu của tổng thể m&agrave; chỉ chọn một nh&oacute;m nhỏ hơn (chọn mẫu) để nghi&ecirc;n cứu.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">T&iacute;nh khả thi của nghi&ecirc;n cứu:</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Nh&agrave; nghi&ecirc;n cứu c&oacute; thể sử dụng c&aacute;c c&ocirc;ng cụ suy diễn như ước lượng, kiểm định, m&ocirc; h&igrave;nh h&oacute;a. . . để từ kết quả tr&ecirc;n mẫu suy luận ra c&aacute;c tham số của tổng thể (đ&aacute;m đ&ocirc;ng).</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Ng&acirc;n s&aacute;ch v&agrave; thời gian nghi&ecirc;n cứu kh&ocirc;ng cho ph&eacute;p nghi&ecirc;n cứu to&agrave;n bộ. Chọn mẫu c&oacute; thể cho kết quả ch&iacute;nh x&aacute;c hơn.</span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">4.3.2. Sai số v&agrave; chọn mẫu:</span></span></em></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">- Sai số do chọn mẫu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">- Sai số kh&ocirc;ng do chọn mẫu.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>4.3.3. C&aacute;c kh&aacute;i niệm cơ bản trong chọn mẫu:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Tổng thể nghi&ecirc;n cứu (population) hay đ&aacute;m đ&ocirc;ng: L&agrave; tập hợp to&agrave;n bộ c&aacute;c đối tượng cần được nghi&ecirc;n cứu. Số lượng c&aacute;c phần tử của đ&aacute;m đ&ocirc;ng được gọi l&agrave; k&iacute;ch thước đ&aacute;m đ&ocirc;ng N.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phần tử (element): L&agrave; đối tượng cần thu thập dự liệu hay c&ograve;n gọi l&agrave; đối tượng nghi&ecirc;n cứu (đối tượng thu thập dữ liệu - subject)</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Mẫu nghi&ecirc;n cứu (sample): Một tập hợp nhỏ hơn được r&uacute;t ra từ đ&aacute;m đ&ocirc;ng được gọi l&agrave; mẫu. C&aacute;ch chọn c&aacute;c phần tử từ đ&aacute;m đ&ocirc;ng v&agrave;o mẫu được gọi l&agrave; phương ph&aacute;p chọn mẫu. C&oacute; nhiều phương ph&aacute;p chọn mẫu nhưng c&oacute; thể chia th&agrave;nh 2 phương ph&aacute;p cơ bản: chọn mẫu theo x&aacute;c suất v&agrave; kh&ocirc;ng theo x&aacute;c suất. Số phần tử của mẫu gọi l&agrave; k&iacute;ch thước mẫu, k&yacute; hiệu n.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Đơn vị (unit): Trong nhiều phương ph&aacute;p chọn mẫu người ta chia đ&aacute;m đ&ocirc;ng th&agrave;nh nhiều nh&oacute;m, mỗi nh&oacute;m c&oacute; những đặc t&iacute;nh ri&ecirc;ng phục vụ cho qu&aacute; tr&igrave;nh chọn mẫu. Những nh&oacute;m được h&igrave;nh th&agrave;nh sau qu&aacute; tr&igrave;nh chia nhỏ đ&aacute;m đ&ocirc;ng được gọi l&agrave; đơn vị mẫu (sampling unit).</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Khung chọn mẫu (sampling frame): L&agrave; danh s&aacute;ch liệt k&ecirc; tất cả c&aacute;c đơn vị v&agrave; phẩn tử của đ&aacute;m đ&ocirc;ng.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>4.3.4. Quy tr&igrave;nh chọn mẫu:</em></span></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">X&aacute;c định đ&aacute;m đ&ocirc;ng nghi&ecirc;n cứu. </span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Việc x&aacute;c định tổng thể nghi&ecirc;n cứu được tiến h&agrave;nh khi thiết kế nghi&ecirc;n cứu v&agrave; x&aacute;c định đối tượng cần thu thập dữ liệu để đ&aacute;p ứng mục ti&ecirc;u v&agrave; phạm vi nghi&ecirc;n cứu.</span></span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">X&aacute;c định khung mẫu.</span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Nh&agrave; nghi&ecirc;n cứu cần liệt k&ecirc; danh mục c&aacute;c dữ liệu v&agrave; th&ocirc;ng số cần thiết cho việc chọn mẫu.</span></span></p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">X&aacute;c định k&iacute;ch thước mẫu.</span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">L&agrave; c&ocirc;ng việc quan trọng của qu&aacute; tr&igrave;nh chọn mẫu v&igrave; n&oacute; ảnh hưởng tới qu&aacute; tr&igrave;nh ph&acirc;n t&iacute;ch dữ liệu cũng như độ tin cậy của kết quả nghi&ecirc;n cứu. K&iacute;ch thước mẫu phụ thuộc v&agrave;o mục ti&ecirc;u, đối tượng, phương ph&aacute;p ph&acirc;n t&iacute;ch sử dụng trong mỗi nghi&ecirc;n cứu cụ thể.</span></span></p>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">X&aacute;c định phương ph&aacute;p chọn mẫu. </span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">C&aacute;c phương ph&aacute;p chọn mẫu được chia th&agrave;nh hai nh&oacute;m ch&iacute;nh l&agrave; chọn mẫu x&aacute;c suất v&agrave; chọn mẫu phi x&aacute;c suất.</span></span></p>\r\n\r\n<ol start=\"5\">\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Tiến h&agrave;nh chọn mẫu.</span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">L&agrave; c&ocirc;ng việc cuối c&ugrave;ng trong quy tr&igrave;nh chọn mẫu.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>4.3.5. C&aacute;c phương ph&aacute;p chọn mẫu:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u>4.3.5.1. Phương ph&aacute;p chọn mẫu ngẫu nhi&ecirc;n:</u></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phương ph&aacute;p ngẫu nhi&ecirc;n hệ thống:</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Trong phương ph&aacute;p n&agrave;y ta sắp xếp c&aacute;c phần tử của đ&aacute;m đ&ocirc;ng từ 1 đến N, sau đ&oacute; x&aacute;c định bước nhảy N/n (SI = sampling interval). Gi&aacute; trị n/N được gọi l&agrave; tỷ lệ chọn mẫu (sampling fraction). Khi đ&oacute; ta chia đ&aacute;m đ&ocirc;ng th&agrave;nh n nh&oacute;m, mỗi nh&oacute;m gồm N/n phần tử. Phần tử đầu ti&ecirc;n của mẫu được chọn theo phương ph&aacute;p ngẫu nhi&ecirc;n đơn giản từ một nh&oacute;m n&agrave;o đ&oacute;. Giả sử phần tử đầu ti&ecirc;n c&oacute; thứ tự a trong nh&oacute;m th&igrave; c&aacute;c phần tử tiếp theo được chọn dựa tr&ecirc;n thứ tự a + N/n, a + 2.N/n . . .</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phương ph&aacute;p ph&acirc;n tầng:</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Để thực hiện chọn mẫu theo phương ph&aacute;p n&agrave;y ta thực hiện chia đ&aacute;m đ&ocirc;ng th&agrave;nh c&aacute;c nh&oacute;m nhỏ (stratum), c&aacute;c nh&oacute;m n&agrave;y ch&iacute;nh l&agrave; c&aacute;c đơn vị chọn mẫu. C&aacute;c nh&oacute;m n&agrave;y thỏa m&atilde;n điều kiện c&aacute;c phần tử của nh&oacute;m c&oacute; t&iacute;nh đồng nhất cao v&agrave; c&aacute;c phần tử giữa c&aacute;c nh&oacute;m c&oacute; t&iacute;nh dị biệt cao. Để chọn phần tử cho mẫu trong từng nh&oacute;m ta c&oacute; thể sử dụng phương ph&aacute;p hệ thống hoặc ngẫu nhi&ecirc;n đơn giản.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phương ph&aacute;p chọn mẫu ph&acirc;n tầng c&oacute; thể thực hiện theo tỉ lệ (số ph&acirc;n tử chọn cho mẫu trong mỗi nh&oacute;m tỉ lệ với số phần tử của nh&oacute;m) hoặc kh&ocirc;ng theo tỉ lệ (số ph&acirc;n tử chọn cho mẫu trong mỗi nh&oacute;m kh&ocirc;ng tỉ lệ với số phần tử của nh&oacute;m).</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phương ph&aacute;p chọn mẫu theo cụm:</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Hay c&ograve;n gọi l&agrave; chọn mẫu nhiều giai đoạn, thường &aacute;p dụng khi tổng thể c&oacute; quy m&ocirc; qu&aacute; lớn hoặc địa b&agrave;n nghi&ecirc;n cứu qu&aacute; rộng. Trước ti&ecirc;n ta ph&acirc;n chia tổng thể th&agrave;nh c&aacute;c đơn vị cấp I, rồi chọn c&aacute;c đơn vị mẫu cấp I. Sau đ&oacute; chia đơn vị mẫu cấp I đ&atilde; chọn th&agrave;nh c&aacute;c đơn vị mẫu cấp II . . . Việc chọn mẫu ở mỗi cấp c&oacute; thể &aacute;p dụng c&aacute;ch chọn mẫu ngẫu nhi&ecirc;n đơn giản, chọn hệ thống hoặc chọn mẫu ph&acirc;n tầng.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u>4.3.5.2. Phương ph&aacute;p chọn mẫu phi ngẫu nhi&ecirc;n:</u></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">L&agrave; phương ph&aacute;p chọn mẫu m&agrave; c&aacute;c phần tử c&oacute; thể kh&ocirc;ng c&oacute; khả năng ngang nhau để được chọn v&agrave;o mẫu.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phương ph&aacute;p chọn mẫu thuận tiện:</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Chọn mẫu thuận tiện l&agrave; c&aacute;ch m&agrave; ta c&oacute; thể chọn những phần tử m&agrave; ta c&oacute; thể tiếp cận được cho đến khi đủ k&iacute;ch thước mẫu m&agrave; ta y&ecirc;u cầu.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phương ph&aacute;p chọn mẫu ph&aacute;n đo&aacute;n:</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Đ&acirc;y l&agrave; phương ph&aacute;p kh&ocirc;ng ngẫu nhi&ecirc;n trong đ&oacute; ta tự ph&aacute;n đo&aacute;n sự th&iacute;ch hợp của c&aacute;c phần tử được chọn v&agrave;o mẫu.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phương ph&aacute;p chọn mẫu định mức:</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">L&agrave; phương ph&aacute;p chọn mẫu dựa v&agrave;o c&aacute;c đặc t&iacute;nh kiểm so&aacute;t (control characteristic) x&aacute;c định trong đ&aacute;m đ&ocirc;ng để chọn số phần tử cho mẫu sao cho ch&uacute;ng c&oacute; c&ugrave;ng tỉ lệ của đ&aacute;m đ&ocirc;ng theo c&aacute;c thuộc t&iacute;nh kiểm so&aacute;t.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phương ph&aacute;p quả cầu tuyết:</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Ban đầu ta thực hiện việc chọn ngẫu nhi&ecirc;n một số phần tử của đ&aacute;m đ&ocirc;ng, sau khi nghi&ecirc;n cứu c&aacute;c phần tử n&agrave;y ta th&ocirc;ng qua c&aacute;c phần tử đ&oacute; để giới thiệu c&aacute;c phần tử tiếp theo cho mẫu đến khi được k&iacute;ch thước mẫu như ta mong muốn.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>4.3.6. X&aacute;c định k&iacute;ch thước mẫu:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u>4.3.6.1. X&aacute;c định cỡ mẫu theo chuẩn mực cơ bản:</u></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">K&iacute;ch thước mẫu tối thiểu 30 (n &ge; 30) đảm bảo cho c&aacute;c ph&acirc;n t&iacute;ch thống k&ecirc; th&ocirc;ng thường c&oacute; &yacute; nghĩa.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u>4.3.6.2. X&aacute;c định cỡ mẫu để ước lượng trung b&igrave;nh tổng thể.</u></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>4.4. Đo lường v&agrave; thu thập dữ liệu định lượng:</strong></u></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Mục n&agrave;y sẽ bắt đầu bằng việc tr&igrave;nh b&agrave;y về vấn đề đo lường v&agrave; c&aacute;c cấp độ thang đo, sau đ&oacute; đề cập tới việc thiết kế bảng c&acirc;u hỏi v&agrave; tiến h&agrave;nh điều tra, thu thập dữ liệu.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>4.4.1. Đo lường v&agrave; cấp độ thang đo trong nghi&ecirc;n cứu:</em></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">C&oacute; 2 vấn đề đo lường trong nghi&ecirc;n cứu định lượng l&agrave; đo lường kh&aacute;i niệm nghi&ecirc;n cứu (gọi l&agrave; biến nghi&ecirc;n cứu) v&agrave; x&aacute;c định thang đo (hay thang điểm) cho mỗi c&acirc;u hỏi.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Giả sử, ch&uacute;ng ta sẽ kiểm định mối li&ecirc;n hệ giữa nhận thức về khả năng khởi nghiệp với &yacute; định khởi nghiệp (với mẫu sinh vi&ecirc;n năm cuối, chuy&ecirc;n ng&agrave;nh Quản trị kinh doanh).</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Vấn đề đầu ti&ecirc;n l&agrave; c&aacute;c kh&aacute;i niệm &quot;&yacute; định&quot; v&agrave; &quot;nhận thức về khả năng khởi nghiệp&quot; sẽ được đo lường như thế n&agrave;o?</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u>4.4.4.1. Đo lường c&aacute;c kh&aacute;i niệm nghi&ecirc;n cứu:</u></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">- Sau khi thu thập dữ liệu, nh&agrave; khoa học cần kiểm tra lại xem 3 mục hỏi.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">(X1, X2, X3) c&oacute; thực sự tin cậy để đo kh&aacute;i niệm &quot;&yacute; định khởi nghiệp&quot; kh&ocirc;ng? C&aacute;c ph&acirc;n t&iacute;ch thống k&ecirc; được sử dụng l&agrave; Ph&acirc;n t&iacute;ch độ tin cậy v&agrave; kiểm định gi&aacute; trị.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Tr&ecirc;n bảng c&acirc;u hỏi, mỗi mục hỏi tr&ecirc;n sẽ được thể hiện l&agrave; một c&acirc;u hỏi. Nh&agrave; khoa học cần đưa ra một thang đo hợp l&yacute; để người trả lời điền v&agrave;o.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">C&oacute; nhiều loại thang đo kh&aacute;c nhau m&agrave; nh&agrave; khoa học c&oacute; thể sử dụng, t&ugrave;y thuộc v&agrave;o loại c&acirc;u hỏi v&agrave; c&aacute;c ph&acirc;n t&iacute;ch thống k&ecirc; sẽ sử dụng.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Thang đo cấp định dạng v&agrave; thứ tự được gọi l&agrave; thang đo định t&iacute;nh (qualitative/non-metric scale); thang đo cấp qu&atilde;ng v&agrave; tỉ lệ được gọi l&agrave; thang đo định lượng (quantitative/ metric scale). C&aacute;c phương ph&aacute;p ph&acirc;n t&iacute;ch dữ liệu đ&ograve;i hỏi thang đo ở cấp th&iacute;ch hợp cho từng biến. V&igrave; vậy khi d&ugrave;ng cấp thang đo n&agrave;o ch&uacute;ng ta cần ch&uacute; &yacute; đến phương ph&aacute;p ph&acirc;n t&iacute;ch theo sau.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u>4.4.1.2. Cấp độ thang đo:</u></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Theo Stevens (1951) c&oacute; 4 cấp độ thang đo l&agrave; (i) thang đo cấp định danh (nominal scale), thang đ&ocirc; cấp thứ tự (ordinal scale), thang đo cấp qu&atilde;ng (interval scale) v&agrave; thang đo cấp tỉ lệ (ratio scale).</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u>4.4.2.1 Định khung bảng c&acirc;u hỏi:</u></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Nh&agrave; khoa học cần phải bắt đầu bằng việc x&aacute;c định phạm vi hay c&aacute;c chủ điểm ch&iacute;nh cần thu thập th&ocirc;ng tin v&agrave; x&acirc;y dựng khung bảng hỏi như một sơ đồ c&acirc;y. Vấn đề tiếp theo l&agrave; xem x&eacute;t c&aacute;c bước nhảy trong diễn tiến của bảng c&acirc;u hỏi. Cuối c&ugrave;ng l&agrave; cần x&aacute;c định thang điểm hay thang trả lời cho mỗi c&acirc;u hỏi đặt ra.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">V&iacute; dụ:</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Ch&uacute;ng ta cần nghi&ecirc;n cứu về Vfresh nước cam để tung ra sản phẩm mới dưới dạng nước cam nhiều hương vị. Với c&aacute;c nội dung cần thu thập th&ocirc;ng tin như bảng tr&ecirc;n, trong diễn tiến bảng c&acirc;u hỏi sẽ c&oacute; &iacute;t nhất 1 bước nhảy, những người đ&atilde; d&ugrave;ng sản phẩm sẽ trả lời c&aacute;c c&acirc;u hỏi ri&ecirc;ng, người chưa d&ugrave;ng sản phẩm sẽ được dẫn dắt tới c&acirc;u hỏi th&iacute;ch hợp. Cuối c&ugrave;ng, cần xet x&eacute;t c&aacute;c thang đo hay lựa chọn trả lời ph&ugrave; hợp cho mỗi c&acirc;u hỏi.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u>4.4.2.2. X&aacute;c định thứ tự c&aacute;c c&acirc;u hỏi.</u></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u>4.4.2.3. Vấn đề soạn thảo c&acirc;u hỏi:</u></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Vấn đề ch&iacute;nh đặt ra trong việc soạn thảo bảng c&acirc;u hỏi l&agrave; việc sử dụng từ ngữ v&agrave; văn phong như thế n&agrave;o để người trả lời c&oacute; thể hiểu đ&uacute;ng &yacute; c&acirc;u hỏi. C&aacute;c nguy&ecirc;n tắc đặt ra l&agrave;: Ngắn gọn, dễ hiểu, đơn nhất, trung lập v&agrave; phong ph&uacute;.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Người soạn thảo bảng c&acirc;u hỏi c&oacute; thể gặp nhiều lỗi kh&aacute;c nhau dẫn đến kh&ocirc;ng thu thập được đ&uacute;ng th&ocirc;ng tin phục vụ cho nghi&ecirc;n cứu. C&oacute; 12 lỗi cơ bản được thống k&ecirc; m&agrave; người soạn thảo bảng hỏi n&ecirc;n tr&aacute;nh:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">1) Đ&ocirc;i khi người ta đặt c&aacute;c c&acirc;u hỏi &ldquo;th&ocirc;ng tin c&aacute; nh&acirc;n&rdquo; ở đầu bảng c&acirc;u hỏi, trong khi vị tr&iacute; đ&uacute;ng l&agrave; ở cuối.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">2) C&acirc;u hỏi kh&ocirc;ng thử nghiệm trước </span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">3) C&acirc;u hỏi 2 &yacute;</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">4) C&acirc;u hỏi đ&atilde; bao gồm c&acirc;u trả lời </span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">5) Hiệu ứng Halo: Mất đi sự trung lập </span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">6) Lỗi thuật ngữ</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">7) C&acirc;u hỏi kh&ocirc;ng r&otilde; r&agrave;ng</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">8) C&acirc;u hỏi thiếu một số phương &aacute;n trả lời hoặc phương &aacute;n trả lời kh&ocirc;ng ph&ugrave; hợp </span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">9) Đưa ra mốc thời gian kh&ocirc;ng cụ thể hoặc tr&ugrave;ng nhau</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">10) Thứ tự c&aacute;c c&acirc;u hỏi: C&acirc;u trước gợi &yacute; trả lời cho c&acirc;u sau </span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">11) C&aacute;c c&acirc;u trả lời &aacute;p đặt</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">12) D&ugrave;ng qu&aacute; nhiều c&acirc;u hỏi mở</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>4.4.3. Nhập v&agrave; chuẩn bị dữ liệu:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u>4.4.3.1. Xử l&yacute; sơ bộ bảng c&acirc;u hỏi:</u></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">C&aacute;c sai s&oacute;t trong qu&aacute; tr&igrave;nh thu thập dữ liệu:</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Thiết kế bảng c&acirc;u hỏi chưa đạt y&ecirc;u cầu: C&acirc;u hỏi phức tạp, kh&ocirc;ng r&otilde; r&agrave;ng; sử dụng thuật ngữ chuy&ecirc;n m&ocirc;n kh&oacute; hiểu; tr&igrave;nh b&agrave;y kh&ocirc;ng r&otilde; r&agrave;ng. . .</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Sai s&oacute;t từ nh&acirc;n vi&ecirc;n phỏng vấn: Do chủ quan, thiếu kinh nghiệm v&agrave; kỹ năng phỏng vấn, do chưa hiểu r&otilde; c&aacute;c c&acirc;u hỏi để giải th&iacute;ch cho người trả lời. . .</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Sai s&oacute;t từ người trả lời: Do họ kh&ocirc;ng nghi&ecirc;m t&uacute;c trong việc trả lời c&aacute;c c&acirc;u hỏi, hoặc do thiếu thời gian dẫn đến điền bảng c&acirc;u hỏi vội v&agrave;ng, qua loa.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u>4.4.3.2. M&atilde; h&oacute;a dữ liệu:</u></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">C&aacute;c dữ liệu thu thập trong bảng hỏi cần được m&atilde; h&oacute;a v&agrave; nhập v&agrave;o c&aacute;c phần mềm ph&acirc;n t&iacute;ch dữ liệu ho&agrave;n to&agrave;n dưới dạng con số.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Đối với c&aacute;c c&acirc;u hỏi c&oacute; thang đo định lượng, c&aacute;c c&acirc;u trả lời đ&atilde; được m&atilde; h&oacute;a dưới dạng con số, việc nhập dữ liệu được thực hiện theo đ&uacute;ng c&aacute;c con số tương ứng. Đối với c&acirc;u hỏi c&oacute; thang định t&iacute;nh, nh&agrave; khoa học sẽ g&aacute;n số cho c&aacute;c c&acirc;u trả lời theo quy ước của m&igrave;nh. Đối với c&acirc;u hỏi được chọn nhiều c&acirc;u trả lời, th&igrave; mỗi &yacute; trả lời sẽ được m&atilde; h&oacute;a th&agrave;nh một biến nhỏ trong bảng dữ liệu.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u>4.4.3.3. Nhập dữ liệu:</u></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Sau khi thu thập dữ liệu sơ cấp bằng bảng hỏi ở tr&ecirc;n thi c&ocirc;ng việc tiếp theo m&agrave; nh&agrave; nghi&ecirc;n cứu cần phải l&agrave;m đ&oacute; l&agrave; nhập dữ liệu. Trước khi nhập dữ liệu, cần đ&aacute;nh số thứ tự c&aacute;c bảng c&acirc;u hỏi thu được để tiện việc kiểm tra sau n&agrave;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Ng&agrave;y n&agrave;y với sự ph&aacute;t triển mạnh mẽ của c&ocirc;ng nghệ th&ocirc;ng tin th&igrave; việc nhập dữ liệu trở l&ecirc;n đơn giản hơn. Trong khi ch&uacute;ng t&ocirc;i kh&ocirc;ng c&oacute; khuyến nghị cụ thể ở đ&acirc;y, tuy nhi&ecirc;n một số ứng dụng như: ExcelTM, SPSSTM , EpidataTM l&agrave; hữu &iacute;ch.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Bảng dữ liệu ho&agrave;n chỉnh sẽ l&agrave; một ma trận m&agrave; trong đ&oacute;: Mỗi cột biểu thị một biến v&agrave; mỗi d&ograve;ng biểu thị th&ocirc;ng tin của một người trả lời.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u>4.3.3.4. L&agrave;m sạch dữ liệu:</u></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">L&agrave;m sạch dữ liệu l&agrave; bước cuối c&ugrave;ng nhằm tiếp tục ph&aacute;t hiện c&aacute;c sai s&oacute;t trong qu&aacute; tr&igrave;nh thu thập dữ liệu v&agrave; c&aacute;c sai s&oacute;t c&oacute; thể xảy ra trong qu&aacute; tr&igrave;nh nhập dữ liệu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Đ&oacute; l&agrave; trường hợp c&oacute; c&aacute;c &ocirc; trống trong bảng dữ liệu, c&aacute;c gi&aacute; trị bị nhập sai hoặc c&aacute;c c&acirc;u trả lời kh&ocirc;ng hợp l&yacute;.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><u>4.5. Xử l&iacute; dữ liệu:</u></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Sau khi đ&atilde; l&agrave;m sạch dữ liệu (data cleaning) th&igrave; bạn đ&atilde; sẵn s&agrave;ng bắt đầu ph&acirc;n t&iacute;ch, bước tiếp theo nh&agrave; nghi&ecirc;n cứu thực hiện xử l&yacute; dữ liệu.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>4.5.1 Ph&acirc;n t&iacute;ch thống k&ecirc; m&ocirc; tả:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Ph&acirc;n t&iacute;ch thống k&ecirc; m&ocirc; tả l&agrave; kĩ thuật ph&acirc;n t&iacute;ch đơn giản nhất của một nghi&ecirc;n cứu định lượng. Bất k&igrave; một nghi&ecirc;n cứu định lượng n&agrave;o cũng tiến h&agrave;nh c&aacute;c ph&acirc;n t&iacute;ch n&agrave;y, &iacute;t nhất l&agrave; để thống k&ecirc; về đối tượng điều tra.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>4.5.2. Ph&acirc;n t&iacute;ch chuy&ecirc;n s&acirc;u kh&aacute;c:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u>4.5.2.1. Ph&acirc;n t&iacute;ch nh&acirc;n tố (factor analysis):</u></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Ph&acirc;n t&iacute;ch nh&acirc;n tố l&agrave; một trong những phương ph&aacute;p ph&acirc;n t&iacute;ch thống k&ecirc; d&ugrave;ng để r&uacute;t gọn một tập hợp nhiều biến quan s&aacute;t phụ thuộc lẫn nhau th&agrave;nh một tập biến (gọi l&agrave; c&aacute;c nh&acirc;n tố) &iacute;t hơn để ch&uacute;ng c&oacute; &yacute; nghĩa hơn nhưng vẫn chứa đựng hầu hết th&ocirc;ng tin của tập biến ban đầu.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Đ&acirc;y l&agrave; một ph&acirc;n t&iacute;ch thuộc nh&oacute;m kỹ thuật ph&acirc;n t&iacute;ch đa biến phụ thuộc, nghĩa l&agrave; kh&ocirc;ng c&oacute; biến phụ thuộc v&agrave; biến độc lập, m&agrave; n&oacute; dựa v&agrave;o mối tương quan giữa c&aacute;c biến với nhau. Một tập k biến quan s&aacute;t được r&uacute;t gọn th&agrave;nh 1 tập F , (F &lt; k) c&aacute;c nh&acirc;n tố c&oacute; &yacute; nghĩa hơn.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Th&ecirc;m nữa, ph&acirc;n t&iacute;ch nh&acirc;n tố cũng được sử dụng để kiểm định thang đo.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phương ph&aacute;p Cronbach Alpha: D&ugrave;ng để đ&aacute;nh gi&aacute; độ tin cậy của thang đo</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Phương ph&aacute;p ph&acirc;n t&iacute;ch nh&acirc;n tố kh&aacute;m ph&aacute; EFA (Exploratory Factor Analysis): Gi&uacute;p đ&aacute;nh gi&aacute; gi&aacute; trị thang đo.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\"><u>4.5.2.2. Ph&acirc;n t&iacute;ch độ tin cậy:</u></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Trước khi kiểm định c&aacute;c giả thuyết khoa học, cần kiểm định thang đo c&aacute;c biến số trong m&ocirc; h&igrave;nh. Một trong c&aacute;c thủ tục kiểm định thang đo l&agrave; ph&acirc;n t&iacute;ch độ tin cậy của thang đo. Phương ph&aacute;p n&agrave;y sự dụng hệ số Cronbach Alpha kiểm định mức độ tin cậy v&agrave; tương quan giữa c&aacute;c biến quan s&aacute;t trong thang đo. Hệ số Cronbach&rsquo;s alpha của một thang đo cần 2 y&ecirc;u cầu cơ bản:</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Hệ số Cronbach&rsquo;s alpha tổng (chung)&gt; 0.6 Hệ số tương quan biến &ndash; tổng &gt; 0.3</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Độ tin cậy tốt nhất được x&aacute;c định trong khoảng từ 0.7 đến 0.8.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Ph&acirc;n t&iacute;ch hồi quy l&agrave; ph&acirc;n t&iacute;ch để x&aacute;c định quan hệ phụ thuộc của một biến (biến phụ thuộc) v&agrave;o một hoặc nhiều biến kh&aacute;c (gọi l&agrave; biến phụ thuộc).</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Trong nghi&ecirc;n cứu kinh doanh, ph&acirc;n t&iacute;ch hồi quy đa biến thường được sử dụng để kiểm định mối quan hệ giữa một hoặc nhiều nh&acirc;n tố ảnh hưởng tới một biến phụ thuộc.</span></span></p>', '1550901165', null, '0', '48', '1', '0', null, '0', null, null, null, null, null, null, '/images/course/48/lesson/1550926755_chuong-4-nghien-cuu-dinh-luong.png', null);
INSERT INTO `lesson` VALUES ('133', 'Chương 5', '<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">CHƯƠNG 5: VIẾT V&Agrave; THUYẾT TR&Igrave;NH B&Aacute;O C&Aacute;O NGHI&Ecirc;N CỨU KHOA HỌC</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><u><span style=\"font-size:14.0pt\">Cấu tr&uacute;c v&agrave; thuyết tr&igrave;nh nghi&ecirc;n cứu khoa học:</span></u></strong></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Với c&aacute;c b&aacute;o c&aacute;o nghi&ecirc;n cứu khoa học trong lĩnh vực nghi&ecirc;n cứu khoa học kinh tế, cấu tr&uacute;c phổ biến thường được d&ugrave;ng gồm c&oacute; bốn phần, trong tiếng Anh, cấu tr&uacute;c n&agrave;y c&ograve;n được goi l&agrave; cấu tr&uacute;c IMRAD (Introduction, Methods, Results, v&agrave; Discussions).</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần 1 Giới thiệu</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần 2 Phương ph&aacute;p nghi&ecirc;n cứu </span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần 3 Kết quả nghi&ecirc;n cứu </span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần 4 Thảo luận</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Tr&igrave;nh tự logic của viết b&aacute;o c&aacute;o nghi&ecirc;n cứu khoa học kinh tế theo cấu tr&uacute;c IMRAD c&oacute; thể được diễn giải như sau:</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần 1: Giới thiệu (Introduction) vấn đề g&igrave; cần được nghi&ecirc;n cứu?</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần 2: Phương ph&aacute;p nghi&ecirc;n cứu (Methods) vấn đề đ&oacute; như thế n&agrave;o, hay vấn đề n&ecirc;n được nghi&ecirc;n cứu như thế n&agrave;o?</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần 3: Kết quả (Results) hay c&aacute;c ph&aacute;t hiện (Findings) của nghi&ecirc;n cứu l&agrave; g&igrave;? </span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần 4: Thảo luận (Discussions) c&aacute;c ph&aacute;t hiện, c&aacute;c h&agrave;m &yacute;, c&aacute;c đề xuất.</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:28px\">&nbsp;</p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<ol>\r\n			<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:14.0pt\">Cấu tr&uacute;c b&aacute;o c&aacute;o nghi&ecirc;n cứu khoa học:</span></em></span></span></li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Cấu tr&uacute;c b&aacute;o c&aacute;o nghi&ecirc;n cứu khoa học thường gồm ba phần: phần mở đầu, phần nội dung v&agrave; phần kết th&uacute;c. T&ugrave;y loại b&aacute;o c&aacute;o nghi&ecirc;n cứu khoa học m&agrave; cấu tr&uacute;c c&oacute; thể c&oacute; c&aacute;c nội dung, phần viết ri&ecirc;ng hoặc viết kết hợp kh&aacute;c nhau. Tuy nhi&ecirc;n, cấu tr&uacute;c c&aacute;c b&aacute;o c&aacute;o nghi&ecirc;n cứu khoa học n&ecirc;n bao gồm:</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Ti&ecirc;u đề, t&ecirc;n b&agrave;i b&aacute;o, t&ecirc;n đề t&agrave;i (Title) T&oacute;m lược (Summary, Abstract)</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần một hoặc chương 1: Đặt vấn đề, dẫn nhập, giới thiệu (Introduction)</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần hoặc chương 2: Tổng quan l&iacute; thuyết hoặc tổng quan t&agrave;i liệu (Literature Review)</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần hoặc chương 3: Phương ph&aacute;p nghi&ecirc;n cứu (Methods)</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần hoặc chương 4: Kết quả v&agrave; thảo luận (Results and Discussions) Phần hoặc chương 5. Kết luận (Conclusions)</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần hoặc chương 6: Khuyến nghị, h&agrave;m &yacute; ch&iacute;nh s&aacute;ch (Recommendation/ Policy Implication). Phần 5 v&agrave; phần 6 c&oacute; thể viết kết hợp chung.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">T&agrave;i liệu tham khảo TLTK (References),</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Ngo&agrave;i ra c&ograve;n c&oacute; thể c&oacute; phần:</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Lời cảm tạ (Acknowledgements) </span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phụ lục (Appendices)</span></span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<ol start=\"2\">\r\n			<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:14.0pt\">C&aacute;ch viết b&aacute;o c&aacute;o khoa học:</span></em></span></span></li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần t&oacute;m lược:</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Đối với b&agrave;i b&aacute;o khoa học:</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần t&oacute;m lược của b&agrave;i b&aacute;o khoa học thường l&agrave; một đoạn văn ngắn v&agrave; s&uacute;c t&iacute;ch, c&oacute; nội dung d&agrave;i khoảng 150 - 200 chữ. Đoạn văn n&agrave;y n&ecirc;n chứa bốn th&agrave;nh phần: 1) Mục ti&ecirc;u nghi&ecirc;n cứu, c&acirc;u hỏi hoặc giả thuyết nghi&ecirc;n cứu; 2) Phương ph&aacute;p nghi&ecirc;n cứu; 3) C&aacute;c ph&aacute;t hiện chủ yếu; 4) Kết luận.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần n&agrave;y n&ecirc;n viết ngắn gọn, kh&ocirc;ng d&ocirc;ng d&agrave;i, v&agrave; d&ugrave;ng c&acirc;u ngắn, cụ thể, r&otilde; nghĩa. Hơn nữa, phần n&agrave;y n&ecirc;n tập trung t&oacute;m lược c&aacute;c kết quả, c&aacute;c ph&aacute;t hiện của nghi&ecirc;n cứu, v&agrave; hạn chế tr&igrave;nh b&agrave;y về bối cảnh nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Trang t&oacute;m lược thường được viết đầu ti&ecirc;n để người đọc c&oacute; thể hiểu ngay nội dung cơ bản của sản phẩm. Cấu tr&uacute;c t&oacute;m lược thường gồm từ hai đến bốn đoạn văn. Cũng c&oacute; khi t&oacute;m lược được viết liền mạch, kh&ocirc;ng ph&acirc;n định th&agrave;nh c&aacute;c đoạn ri&ecirc;ng. Nội dung t&oacute;m lược cần bao gồm:</span></span></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">X&aacute;c định vấn đề nghi&ecirc;n cứu v&agrave; mục ti&ecirc;u nghi&ecirc;n cứu </span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phương ph&aacute;p nghi&ecirc;n cứu</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">C&aacute;c ph&aacute;t hiện của nghi&ecirc;n cứu </span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Kết luận hoặc/v&agrave; khuyến nghị.</span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần/Chương 1:</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Mục đ&iacute;ch của viết phần đặt vấn đề l&agrave; nhằm x&aacute;c định t&iacute;nh cấp b&aacute;ch v&agrave; cần thiết của vấn đề nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Đối với những đề t&agrave;i nghi&ecirc;n cứu mang t&iacute;nh h&agrave;n l&acirc;m, học thuật th&igrave; phần đặt vấn đề cần tập trung l&agrave;m r&otilde; về khoảng trống kiến thức l&iacute; thuyết, v&agrave; cần được giải quyết. Nếu đề t&agrave;i nghi&ecirc;n cứu mang t&iacute;nh ứng dụng, phần đặt vấn đề n&ecirc;n tập trung l&agrave;m r&otilde; những tồn tại, kh&oacute; khăn trong thực tiễn v&agrave; đề ra c&aacute;c giải ph&aacute;p giải quyết.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Trong phần đặt vấn đề, t&aacute;c giả cũng cần tr&igrave;nh b&agrave;y mục ti&ecirc;u, c&acirc;u hỏi v&agrave; giả thuyết nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Đối với b&agrave;i b&aacute;o khoa học, phần giới thiệu kh&ocirc;ng n&ecirc;n viết qu&aacute; hai trang.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Đối với luận văn hoặc kh&oacute;a luận tốt nghiệp đại học, hoặc b&aacute;o c&aacute;o khoa học, phần đặt vấn đề thường được cấu tr&uacute;c th&agrave;nh một chương với c&aacute;c nội dung sau:</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Tr&igrave;nh b&agrave;y bối cảnh nghi&ecirc;n cứu: việc tr&igrave;nh b&agrave;y n&agrave;y nhằm tr&igrave;nh b&agrave;y c&aacute;c ph&aacute;t hiện chủ yếu li&ecirc;n quan đến khoảng trống kiến thức, với mục ti&ecirc;u l&agrave; chứng minh rằng đề t&agrave;i chưa được nghi&ecirc;n cứu, v&agrave; do đ&oacute; chỉ ra sự cần thiết phải nghi&ecirc;n cứu. Bối cảnh nghi&ecirc;n cứu c&oacute; độ d&agrave;i khoảng 2 - 4 trang A4 chuẩn.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Tuy&ecirc;n bố đề t&agrave;i nghi&ecirc;n cứu: th&ocirc;ng qua tr&igrave;nh b&agrave;y bối cảnh nghi&ecirc;n cứu, t&aacute;c giả cần x&aacute;c lập vấn đề nghi&ecirc;n cứu ch&iacute;nh v&agrave; đ&oacute; ch&iacute;nh l&agrave; t&ecirc;n đề t&agrave;i nghi&ecirc;n cứu. T&ecirc;n đề t&agrave;i nghi&ecirc;n cứu được v&iacute; như &quot;bộ mặt&quot; hoặc l&agrave; &quot;linh hồn&quot; của to&agrave;n bộ c&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Mục ti&ecirc;u nghi&ecirc;n cứu: l&agrave; x&aacute;c định vấn đề cần giải quyết, kết quả của nghi&ecirc;n cứu đạt được. Đối với nghi&ecirc;n cứu h&agrave;n l&acirc;m, mục ti&ecirc;u nghi&ecirc;n cứu l&agrave; giải quyết khoảng trống kiến thức đ&atilde; được ph&aacute;t hiện.Đối với nghi&ecirc;n cứu ứng dụng, mục ti&ecirc;u nghi&ecirc;n cứu l&agrave; c&aacute;c đề xuất, c&aacute;c gợi &yacute;, c&aacute;c giải ph&aacute;p để giải quyết vấn đề (xung đột, tồn tại bất hợp l&iacute;. . . ) được đưa ra trong phần bối cảnh nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">C&acirc;u hỏi nghi&ecirc;n cứu: tr&igrave;nh b&agrave;y c&aacute;c c&acirc;u hỏi tổng qu&aacute;t nhất m&agrave; người nghi&ecirc;n cứu phải trả lời để giải quyết được vấn đề nghi&ecirc;n cứu, khoảng trống kiến thức. Đối với nghi&ecirc;n cứu ứng dụng, việc trả lời c&acirc;u hỏi l&agrave; đ&oacute;ng g&oacute;p về thực tiễn.Đối với nghi&ecirc;n cứu h&agrave;n l&acirc;m, việc trả lời được c&acirc;u hỏi l&agrave; đ&oacute;ng g&oacute;p về l&iacute; luận.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Giả thuyết nghi&ecirc;n cứu: mỗi c&acirc;u hỏi nghi&ecirc;n cứu n&ecirc;n c&oacute; một giả thuyết nghi&ecirc;n cứu k&egrave;m theo. Lưu &yacute; đối với nghi&ecirc;n cứu định lượng, giả thuyết nghi&ecirc;n cứu l&agrave; c&aacute;c ph&aacute;t biểu về mối quan hệ nh&acirc;n quả c&oacute; thể xảy ra v&agrave; cần phải kiểm chứng.Giả thuyết nghi&ecirc;n cứu cũng c&oacute; thể được đưa ra trong nghi&ecirc;n cứu định t&iacute;nh.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">&Yacute; nghĩa của nghi&ecirc;n cứu: c&oacute; thể l&agrave; &yacute; nghĩa l&iacute; luận v&agrave; thực tiễn, l&agrave; sự đ&oacute;ng g&oacute;p nếu nghi&ecirc;n cứu được thực hiện. Th&ocirc;ng thường, c&aacute;c nghi&ecirc;n cứu l&iacute; thuyết c&oacute; &yacute; nghĩa bổ xung v&agrave;o kho kiến thức l&iacute; thuyết, lấp khoảng trống kiến thức l&iacute; thuyết.Đối với nghi&ecirc;n cứu ứng dụng, &yacute; nghĩa thực tiễn nhấn mạnh đến kết quả nghi&ecirc;n cứu giải quyết những vấn đề đ&atilde; đặt ra như thế n&agrave;o?</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Thiết kế nghi&ecirc;n cứu</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Cần tr&igrave;nh b&agrave;y phạm vi nghi&ecirc;n cứu về thời gian v&agrave; kh&ocirc;ng gian, đơn vị nghi&ecirc;n cứu, c&aacute;c c&ocirc;ng cụ d&ugrave;ng để thu thập dữ liệu v&agrave; quy tr&igrave;nh nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Thiết kế nghi&ecirc;n cứu cũng cần tr&igrave;nh b&agrave;y c&aacute;c hạn chế m&agrave; đề t&agrave;i gặp phải, c&aacute;c giới hạn của nghi&ecirc;n cứu về thời gian, kinh ph&iacute;, mức độ kiểm so&aacute;t v&agrave; c&aacute;ch thức để vượt qua khắc phục, hạn chế.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần/Chương 2: Tổng quan l&yacute; thuyết/t&agrave;i liệu</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Sau phần giới thiệu l&agrave; phần tổng quan l&iacute; thuyết hay tổng quan t&agrave;i liệu. Phần n&agrave;y tr&igrave;nh b&agrave;y ngắn gọn c&aacute;c kết quả của c&aacute;c nghi&ecirc;n cứu trước, (nếu c&oacute; c&aacute;c kết quả được cập nhật th&igrave; c&agrave;ng tốt).</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Nếu tr&igrave;nh b&agrave;y theo từng c&ocirc;ng tr&igrave;nh, cần chỉ r&otilde; t&aacute;c giả của nghi&ecirc;n cứu; phạm vi nghi&ecirc;n cứu, thời gian nghi&ecirc;n cứu, phương ph&aacute;p nghi&ecirc;n cứu, kết quả nghi&ecirc;n cứu đạt được, v&agrave; hạn chế/tồn tại đặt ra (c&oacute; thể l&agrave; vấn đề m&agrave; nghi&ecirc;n cứu n&agrave;y được thực hiện tiếp).</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Nếu tr&igrave;nh b&agrave;y theo từng nh&oacute;m nội dung hoặc nh&oacute;m vấn đề cần giải quyết, cần chỉ r&otilde; c&aacute;c nghi&ecirc;n cứu trước đ&atilde; giải quyết vấn đề g&igrave;, chưa giải quyết được vấn đề g&igrave;, đ&acirc;u l&agrave; khoảng trống kiến thức v&agrave; đ&acirc;u l&agrave; tồn tại/bất cập chưa được giải quyết. C&aacute;ch tr&igrave;nh b&agrave;y n&agrave;y được khuyến kh&iacute;ch sử dụng tr&ecirc;n thế giới hơn l&agrave; tr&igrave;nh b&agrave;y theo từng c&ocirc;ng tr&igrave;nh.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Khi viết tổng quan l&iacute; thuyết, cần đặc biệt lưu &yacute; đến c&aacute;ch viết tr&iacute;ch dẫn v&agrave; ghi t&agrave;i liệu tham khảo. Đa số c&aacute;c c&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu đ&atilde; c&ocirc;ng bố c&oacute; trong danh mục t&agrave;i liệu tham khảo. V&igrave; thế việc đưa c&ocirc;ng tr&igrave;nh n&agrave;o v&agrave;o phần tổng quan t&agrave;i liệu cũng cần được đưa v&agrave;o phần danh mục t&agrave;i liệu tham khảo để bảo đảm nguồn gốc tr&iacute;ch dẫn r&otilde; r&agrave;ng.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Tr&iacute;ch dẫn</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">C&oacute; nhiều kiểu tr&iacute;ch dẫn, v&iacute; dụ khi viết kh&oacute;a luận tốt nghiệp, sinh vi&ecirc;n c&oacute; thể sử dụng Hướng dẫn viết b&aacute;o c&aacute;o khoa học (2012) v&agrave; Quy định của bản hướng dẫn k&egrave;m theo Quyết định số 754/QĐ-ĐHTM ng&agrave;y 26 th&aacute;ng 11 năm 2012 của Hiệu trưởng Trường Đại học Thương mại.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần 3/chương 3: Phương ph&aacute;p nghi&ecirc;n cứu</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Đối với b&agrave;i b&aacute;o khoa học</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần n&agrave;y thể hiện nội dung phương ph&aacute;p nghi&ecirc;n cứu v&agrave; dữ liệu d&ugrave;ng để t&iacute;nh to&aacute;n. Người nghi&ecirc;n cứu phải x&acirc;y dựng được m&ocirc; h&igrave;nh nghi&ecirc;n cứu ở đ&acirc;y. Kế tiếp, người nghi&ecirc;n cứu phải tr&igrave;nh b&agrave;y m&ocirc; h&igrave;nh thực nghiệm, cấu tr&uacute;c của n&oacute;, l&iacute; do lựa chọn c&aacute;c yếu tố/th&agrave;nh phần của m&ocirc; h&igrave;nh; diễn giải c&aacute;c mối quan hệ giữa c&aacute;c biến (độc lập v&agrave; phụ thuộc). M&ocirc; h&igrave;nh cần được diễn tả dưới c&aacute;c sơ đồ mối quan hệ. Người nghi&ecirc;n cứu cần tiến h&agrave;nh c&aacute;c kiểm định thống k&ecirc; được sử dụng để kiểm định độ tin cậy của m&ocirc; h&igrave;nh nghi&ecirc;n cứu đề xuất.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Người nghi&ecirc;n cứu cần tr&igrave;nh b&agrave;y c&aacute;ch thu thập dữ liệu, nguồn dữ liệu, c&aacute;ch xử l&iacute; dữ liệu v&agrave; ph&acirc;n t&iacute;ch dữ liệu.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Đối với luận văn tốt nghiệp:</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">&nbsp;Phương ph&aacute;p nghi&ecirc;n cứu thường được tr&igrave;nh b&agrave;y tại chương 3. Khi viết, cần l&agrave;m r&otilde; c&aacute;c phần sau:</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Tiếp cận nghi&ecirc;n cứu chỉ ra c&aacute;ch tiếp cận để giải quyết vấn đề nghi&ecirc;n cứu, &aacute;p dụng tiếp cận quy nạp hay diễn dịch, lịch sử so s&aacute;nh, hệ thống, phương ph&aacute;p nghi&ecirc;n cứu định t&iacute;nh hay định lượng, hay phối hợp v&agrave; c&agrave;ng cụ thể c&agrave;ng tốt.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Giả thuyết nghi&ecirc;n cứu hay luận điểm cần nghi&ecirc;n cứu, thường được viết ngắn gọn, từ 3 đến 5 d&ograve;ng.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Khung l&yacute; thuyết v&agrave; khung ph&acirc;n t&iacute;ch (c&oacute; thể được tr&igrave;nh b&agrave;y trong phần tổng quan l&iacute; thuyết)</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Thiết kế nghi&ecirc;n cứu tr&igrave;nh b&agrave;y phương ph&aacute;p chọn mẫu, kế hoạch lấy mẫu; quy m&ocirc; mẫu v&agrave; chiến lược chọn mẫu, thu thập dữ liệu, xử l&iacute; v&agrave; ph&acirc;n t&iacute;ch dữ liệu trong nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Đơn vị nghi&ecirc;n cứu</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">C&ocirc;ng cụ thu thập th&ocirc;ng tin nếu th&ocirc;ng tin được thu thập bằng phiếu điều tra/bảng hỏi, th&igrave; c&oacute; thể t&oacute;m lược nội dung c&ocirc;ng cụ n&agrave;y. Cần chỉ r&otilde; xem phiếu điều tra/bảng c&acirc;u hỏi theo phụ lục số mấy.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Quy tr&igrave;nh thu thập th&ocirc;ng tin : M&ocirc; tả đầy đủ, chi tiết phương ph&aacute;p chọn mẫu nghi&ecirc;n cứu, cỡ mẫu nghi&ecirc;n cứu, quy tr&igrave;nh tiến h&agrave;nh tr&ecirc;n thực tế.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Xử l&yacute; v&agrave; ph&acirc;n t&iacute;ch dữ liệu m&ocirc; tả c&aacute;ch thức xử l&yacute; dữ liệu, phần mềm /c&ocirc;ng cụ sử dụng để ph&acirc;n t&iacute;ch dữ liệu</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">C&aacute;c vấn đề đạo đức trong nghi&ecirc;n cứu thực tế.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần/Chương 4: Kết quả/thảo luận</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Đối với b&agrave;i b&aacute;o khoa học</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Đ&acirc;y ch&iacute;nh l&agrave; phần tr&igrave;nh b&agrave;y c&aacute;c ph&aacute;t hiện chủ yếu của đề t&agrave;i nghi&ecirc;n cứu, v&agrave; thảo luận về c&aacute;c ph&aacute;t hiện n&agrave;y.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">T&ugrave;y theo c&aacute;ch viết m&agrave; hai nội dung n&agrave;y c&oacute; thể được tr&igrave;nh b&agrave;y chung trong một phần hoặc t&aacute;ch ri&ecirc;ng th&agrave;nh hai phần ri&ecirc;ng biệt nhau.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Kết quả nghi&ecirc;n cứu được tr&igrave;nh b&agrave;y dưới dạng văn bản, t&oacute;m lược kết quả bằng bảng số liệu, biểu đồ, h&igrave;nh ảnh . . . trước, sau đ&oacute; diễn giải, thảo luận, b&igrave;nh luận c&aacute;c kết quả.C&aacute;c kết quả cần phải được x&aacute;c nhận sự tin cậy thống k&ecirc; th&ocirc;ng qua việc &aacute;p dụng c&aacute;c kiểm định thống k&ecirc; cần thiết đối với đề t&agrave;i sử dụng phương ph&aacute;p nghi&ecirc;n cứu định lượng.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Đối với luận văn tốt nghiệp</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Kết quả/thảo luận thường được tr&igrave;nh b&agrave;y trong chương 4. Tr&igrave;nh tự viết c&oacute; thể c&oacute; một v&agrave;i c&aacute;ch như sau:</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Người viết c&oacute; thể đi từ th&ocirc;ng tin tổng qu&aacute;t, m&ocirc; tả vấn đề nghi&ecirc;n cứu cho đến kết quả ph&acirc;n t&iacute;ch dựa tr&ecirc;n th&ocirc;ng tin, dữ liệu thu thập được.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">C&oacute; thể viết theo từng kh&iacute;a cạnh/ nội dung; với mỗi nội dung tr&igrave;nh b&agrave;y c&oacute; c&aacute;c số liệu, biểu đồ, h&igrave;nh vẽ hoặc văn bản kết hợp với thảo luận hoặc viết thảo luận ri&ecirc;ng.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Khi viết, cần lựa chọn c&aacute;c th&ocirc;ng tin quan trọng, nổi bật, ch&iacute;nh x&aacute;c v&agrave; c&ocirc; đọng, qua đ&oacute; c&oacute; thể l&agrave;m r&otilde; c&aacute;c kết quả, ph&aacute;t hiện của nghi&ecirc;n cứu. Những dữ liệu &iacute;t quan trọng cần được chuyển sang phần phụ lục, tr&aacute;nh t&igrave;nh trạng nội dung chương n&agrave;y qu&aacute; ph&acirc;n t&aacute;n, kh&ocirc;ng l&agrave;m r&otilde; kết quả nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">C&aacute;c kết quả nghi&ecirc;n cứu n&ecirc;n được kiểm định bằng c&aacute;c thống k&ecirc; cần thiết để đảm bảo mức độ tin cậy.Nếu viết thảo luận ri&ecirc;ng, cần gắn kết c&aacute;c b&igrave;nh luận, giải th&iacute;ch với c&aacute;c giả thuyết, mục ti&ecirc;u v&agrave; c&acirc;u hỏi nghi&ecirc;n cứu.Tương tự như viết b&igrave;nh luận của b&agrave;i b&aacute;o, cần c&oacute; so s&aacute;nh, đ&aacute;nh gi&aacute; với c&aacute;c kết quả nghi&ecirc;n cứu trước, đưa ra c&aacute;c nhận x&eacute;t c&aacute; nh&acirc;n người nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần/chương 5: Kết luận v&agrave; khuyến nghị</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Đối với b&agrave;i b&aacute;o khoa học</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Viết kết luận cần nhấn mạnh c&aacute;c ph&aacute;t hiện chủ yếu của nghi&ecirc;n cứu, &yacute; nghĩa của c&aacute;c ph&aacute;t hiện v&agrave; c&aacute;c h&agrave;m &yacute; ch&iacute;nh s&aacute;ch của ch&uacute;ng</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Đối với luận văn</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Ngo&agrave;i y&ecirc;u cầu tr&ecirc;n cần chỉ r&otilde; c&aacute;c c&acirc;u hỏi nghi&ecirc;n cứu đ&atilde; được giải quyết như thế n&agrave;o, mục ti&ecirc;u nghi&ecirc;n cứu đặt ra đ&aacute;p ứng như thế n&agrave;o. Nếu c&acirc;u hỏi nghi&ecirc;n cứu chưa được giải quyết, cũng phải chỉ r&otilde;.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Kết luận thể hiện sự đ&oacute;ng g&oacute;p v&agrave;o kiến thức chung, giải quyết vấn đề nghi&ecirc;n cứu, khoảng trống kiến thức đ&atilde; được lấp đầy.Kết luận cần ngắn gọn, x&uacute;c t&iacute;ch, c&oacute; t&iacute;nh kh&aacute;i qu&aacute;t h&oacute;a cao to&agrave;n bộ nội dung của đề t&agrave;i nghi&ecirc;n cứu.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Thường c&oacute; hai loại khuyến nghị: d&agrave;nh cho nghi&ecirc;n cứu kế tiếp v&agrave; khuyến nghị ch&iacute;nh s&aacute;ch.</span></span></span></p>\r\n\r\n<p style=\"margin-left:38px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Khuyến nghị nghi&ecirc;n cứu tiếp theo chỉ ra những nội dung cần nghi&ecirc;n cứu trong tương lai, do hạn chế của nghi&ecirc;n cứu chưa giải quyết được, chưa l&agrave;m r&otilde;, hoặc trong khi nghi&ecirc;n cứu, người nghi&ecirc;n cứu ph&aacute;t hiện c&aacute;c khoảng trống kiến thức nghi&ecirc;n cứu mới hoặc c&aacute;c vấn đề cấp b&aacute;ch cần c&oacute; giải ph&aacute;p tiếp tục giải quyết.</span></span></span></p>\r\n\r\n<p style=\"margin-left:38px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Khuyến nghị ch&iacute;nh s&aacute;ch nhằm đề xuất c&aacute;c thay đổi, bổ sung cần thiết để khắc phục vấn đề. Khuyến nghị cần đ&uacute;c r&uacute;t từ kết quả nghi&ecirc;n cứu tr&igrave;nh b&agrave;y trong c&aacute;c phần trước.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Phần t&agrave;i liệu tham khảo</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Viết t&agrave;i liệu tham khảo theo đ&uacute;ng quy định về tr&iacute;ch dẫn v&agrave; thống nhất như đ&atilde; tr&igrave;nh b&agrave;y trong c&aacute;c chương trước</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Phần phụ lục</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phần phụ lục l&agrave; phần cuối c&ugrave;ng khi viết b&aacute;o c&aacute;o nghi&ecirc;n cứu khoa học. Phần phụ lục cần được đưa c&aacute;c t&agrave;i liệu, bảng số liệu &iacute;t quan trọng kh&ocirc;ng cần tr&igrave;nh b&agrave;y trong phần/chương kết quả/thảo luận, nhưng c&oacute; &yacute; nghĩa l&iacute; giải c&aacute;c minh chứng đ&atilde; đưa ra trong phần trước. Một số nội dung trong phần phụ lục cần c&oacute; l&agrave;:</span></span></span></p>\r\n\r\n<p style=\"margin-left:19px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phiếu điều tra (nếu c&oacute;)</span></span></span></p>\r\n\r\n<p style=\"margin-left:19px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">C&aacute;c bảng số liệu th&ocirc; quan trọng </span></span></span></p>\r\n\r\n<p style=\"margin-left:19px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">C&aacute;c kết quả thống k&ecirc; chi tiết</span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"2\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><u><span style=\"font-size:14.0pt\">H&igrave;nh thức viết b&aacute;o c&aacute;o nghi&ecirc;n cứu khoa học</span></u></strong></span></span>\r\n\r\n		<ol>\r\n			<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:14.0pt\">Đặc trưng của một số b&aacute;o c&aacute;o nghi&ecirc;n cứu khoa học</span></em></span></span></li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol style=\"list-style-type:lower-alpha\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">B&aacute;o c&aacute;o đề t&agrave;i nghi&ecirc;n cứu khoa học.</span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">B&aacute;o c&aacute;o đề t&agrave;i nghi&ecirc;n cứu khoa học l&agrave; một sản phẩm tr&igrave;nh b&agrave;y kết quả nghi&ecirc;n cứu của t&aacute;c gi&aacute; trong đ&oacute; bao gồm: t&iacute;nh hệ thống về cơ sở l&yacute; luận của đề t&agrave;i; những giả thuyết hoặc những kết luận khoa học; t&iacute;nh mới hoặc c&aacute;c kinh nghiệm đ&atilde; r&uacute;t ra từ thực tiễn dưới dạng giải ph&aacute;p. Dung lượng thường được viết khoảng 40 - 50 trang A4.</span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"2\" style=\"list-style-type:lower-alpha\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">T&oacute;m tắt b&aacute;o c&aacute;o đề t&agrave;i nghi&ecirc;n cứu khoa học.</span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Đối với bản b&aacute;o c&aacute;o t&oacute;m tắt đề t&agrave;i nghi&ecirc;n cứu khoa học t&aacute;c giả vẫn phải tu&acirc;n thủ cấu tr&uacute;c của một b&aacute;o c&aacute;o nghi&ecirc;n cứu khoa học. Tuy nhi&ecirc;n nội dung của c&aacute;c phần, chương cũng như c&aacute;c mục sẽ chỉ bao h&agrave;m những nội dung ch&iacute;nh, căn bản nhất. Dung lượng thường được viết khoảng 2 - 4 trang A4.</span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"3\" style=\"list-style-type:lower-alpha\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">B&aacute;o c&aacute;o tr&igrave;nh b&agrave;y tại hội nghị khoa học sinh vi&ecirc;n.</span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Đ&acirc;y l&agrave; bản được soạn dưới dạng c&aacute;c file tr&igrave;nh diễn. Nội dung ch&iacute;nh tập trung v&agrave;o việc tr&igrave;nh b&agrave;y t&iacute;nh cấp thiết của đề t&agrave;i, thực trạng, ph&acirc;n t&iacute;ch, đ&aacute;nh gi&aacute; v&agrave; đưa ra c&aacute;c kết quả ch&iacute;nh đ&atilde; nghi&ecirc;n cứu cũng như c&aacute;c giải ph&aacute;p hay kiến nghị. Th&ocirc;ng thường một bản tr&igrave;nh diễn được tr&igrave;nh b&agrave;y trong khoảng 5 - 7 ph&uacute;t.</span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"4\" style=\"list-style-type:lower-alpha\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">B&aacute;o c&aacute;o thực tập tốt nghiệp.</span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Đ&acirc;y l&agrave; t&agrave;i liệu khoa học thể hiện kết quả qu&aacute; tr&igrave;nh quan s&aacute;t hoặc tham gia một phần v&agrave;o qu&aacute; tr&igrave;nh t&aacute;c nghiệp tại doanh nghiệp. Nội dung của của b&aacute;o c&aacute;o thực tập tốt nghiệp tu&acirc;n thủ theo cấu tr&uacute;c quy định của c&aacute;c khoa chuy&ecirc;n ng&agrave;nh hoặc nh&agrave; trường. Th&ocirc;ng thường dung lượng l&agrave; khoảng 15 - 20 trang A4.</span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"5\" style=\"list-style-type:lower-alpha\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">C&aacute;c t&agrave;i liệu khoa học kh&aacute;c.</span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">C&aacute;c nghi&ecirc;n cứu của sinh vi&ecirc;n c&oacute; thể xuất bản dưới dạng c&aacute;c b&aacute;o c&aacute;o khoa học. C&aacute;c b&agrave;i b&aacute;o khoa học được xuất bản trong c&aacute;c kỷ yếu khoa học sinh vi&ecirc;n trở l&ecirc;n. T&ugrave;y thuộc v&agrave;o nơi b&agrave;i b&aacute;o c&aacute;o khoa học được c&ocirc;ng b&agrave;i b&aacute;o cần tu&acirc;n thủ c&aacute;c y&ecirc;u cầu về cấu tr&uacute;c v&agrave; c&aacute;ch tr&igrave;nh b&agrave;y được đặt ra.</span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<ol start=\"2\">\r\n			<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:14.0pt\">Văn phong sd trong b&aacute;o c&aacute;o nghi&ecirc;n cứu khoa học</span></em></span></span></li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Một số điểm cần ch&uacute; &yacute; khi tr&igrave;nh b&agrave;y b&aacute;o c&aacute;o khoa học:</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Tr&igrave;nh b&agrave;y theo một bố cục chặt chẽ, logic v&agrave; gắn kết giữa c&aacute;c chương, mục. B&aacute;o c&aacute;o cần được tr&igrave;nh b&agrave;y theo cấu tr&uacute;c r&otilde; r&agrave;ng, chặt chẽ.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Đảm bảo t&iacute;nh kh&aacute;ch quan v&agrave; thận trọng khi tr&igrave;nh b&agrave;y c&aacute;c nhận x&eacute;t, &yacute; kiến hoặc kết luận. Khi ph&acirc;n t&iacute;ch, b&igrave;nh luận một vấn đề cần tr&aacute;nh đưa ra c&aacute;c nhận x&eacute;t mang t&iacute;nh tuyệt đối như &quot;chắc chắn l&agrave;&quot;, &quot;tất cả&quot;, &quot;cần phải&quot;. . . T&aacute;c giả n&ecirc;n sử dụng c&aacute;c từ mang t&iacute;nh thận trọng, kh&aacute;ch quan như &quot;cho thấy&quot;, &quot;c&oacute; biểu hiện&quot;, &quot;phần lớn&quot; . . .</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Tr&aacute;nh d&ugrave;ng qu&aacute; nhiều đại từ ng&ocirc;i thứ nhất v&agrave; thứ hai (t&ocirc;i, ch&uacute;ng ta, anh/chị. . . ). Việc sử dụng ng&ocirc;i thứ ba trong lập luận, ph&acirc;n t&iacute;ch sẽ l&agrave;m tăng t&iacute;nh kh&aacute;ch quan.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Kh&ocirc;ng n&ecirc;n sử dụng ng&ocirc;n ngữ văn kể chuyện hoặc diễn đạt cảm t&iacute;nh. V&iacute; dụ n&ecirc;n tr&aacute;nh sử dụng &quot;kh&ocirc;ng thể tin/ chấp nhận&quot;, &quot;tuyệt vời&quot;. . .</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Lời văn trong b&aacute;o c&aacute;o n&ecirc;n được sử dụng ở thể bị động. Tuy nhi&ecirc;n khi cần nhấn mạnh chủ thể thực hiện th&igrave; lời văn sẽ chuyển sang thể chủ động.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Kh&ocirc;ng n&ecirc;n thể hiện sự nhận định hoặc nhận x&eacute;t mạnh mẽ trong c&aacute;c t&agrave;i liệu khoa học nếu như điều nhận định, nhận x&eacute;t ấy kh&ocirc;ng được r&uacute;t ra từ số liệu được chứng minh trong c&aacute;c t&agrave;i liệu khoa học.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Ng&ocirc;n ngữ to&aacute;n học được sử dụng khi t&aacute;c giả muốn tr&igrave;nh b&agrave;y những quan hệ định lượng trong t&agrave;i liệu nghi&ecirc;n cứu của m&igrave;nh. C&oacute; nhiều h&igrave;nh thức để tr&igrave;nh b&agrave;y c&aacute;c quan hệ định lượng như bảng số liệu, đồ thị, biểu đồ. . . Khi tr&igrave;nh b&agrave;y c&aacute;c quan hệ định lượng t&aacute;c giả n&ecirc;n ph&acirc;n t&iacute;ch chi tiết hoặc r&uacute;t ra c&aacute;c nhận định cụ thể.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Ng&ocirc;n ngữ sơ đồ sẽ l&agrave; h&igrave;nh ảnh trực quan khi muốn diễn tả mối li&ecirc;n hệ giữa c&aacute;c th&agrave;nh phần hay c&ocirc;ng đoạn của một qu&aacute; tr&igrave;nh t&aacute;c nghiệp hay hoạt động của hệ thống. Khi tr&igrave;nh b&agrave;y sơ đồ, t&aacute;c giả n&ecirc;n c&oacute; sự giải th&iacute;ch chi tiết quy tr&igrave;nh hoạt động của hệ thống ở dưới, tr&aacute;nh việc chỉ n&ecirc;u chung chung.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Trong một số b&aacute;o c&aacute;o khoa học c&oacute; thể sử dụng h&igrave;nh ảnh minh họa. &nbsp; </span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Đối với khu&ocirc;n khổ học phần th&igrave; h&igrave;nh thức v&agrave; c&aacute;ch thuyết tr&igrave;nh sẽ tu&acirc;n thủ theo bản hướng dẫn k&egrave;m theo Quy định số 754/QĐ-ĐHTM ng&agrave;y 26 th&aacute;ng 11 năm 2012 của Hiệu trưởng Trường đại học Thương Mại (mẫu số 5, 6 v&agrave; 7) .</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Đối với c&aacute;c t&agrave;i liệu khoa học kh&aacute;c sẽ được tr&igrave;nh b&agrave;y theo quy định chung của t&agrave;i liệu khoa học hoặc theo y&ecirc;u cầu của đơn vị xuất bản.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Nội dung của b&aacute;o c&aacute;o khoa học sẽ tr&igrave;nh b&agrave;y kết quả nghi&ecirc;n cứu v&agrave; thường bao gồm c&aacute;c nội dung: t&iacute;nh cấp thiết của đề t&agrave;i, bối cảnh vấn đề nghi&ecirc;n cứu, phương ph&aacute;p nghi&ecirc;n cứu, những kết quả ph&acirc;n t&iacute;ch v&agrave; kết luận về vấn đề nghi&ecirc;n cứu, c&aacute;c đề xuất v&agrave; kiến nghị nếu c&oacute;.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ C&aacute;c quy định chung v&ecirc; h&igrave;nh thức tr&igrave;nh b&agrave;y</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Khổ giấy : Khổ giấy cho to&agrave;n bộ đề t&agrave;i l&agrave; khổ giấy A4(210mm x 297mm). Với văn bản được soạn thảo tr&ecirc;n m&aacute;y t&iacute;nh sử dụng tr&igrave;nh soạn thảo Microsoft Office Word sử dụng font chữ Việt Unicode c&oacute; quy định cụ thể về việc căn lề (tr&ecirc;n, dưới, tr&aacute;i, phải) l&agrave; 2cm mỗi ph&iacute;a.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Trang b&igrave;a của đề t&agrave;i : Ngo&agrave;i phần logo v&agrave; ti&ecirc;u đề đầu (thường l&agrave; t&ecirc;n trường), trang n&agrave;y bao gồm: t&ecirc;n đề t&agrave;i, t&ecirc;n t&aacute;c giả v&agrave; lớp h&agrave;nh ch&iacute;nh; t&ecirc;n gi&aacute;o vi&ecirc;n hướng dẫn v&agrave; d&ograve;ng ti&ecirc;u đề cuối về thời gian.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">T&ecirc;n đề t&agrave;i : T&ecirc;n đề t&agrave;i phải n&ecirc;u r&otilde; &yacute; nghĩa, mục đ&iacute;ch của đề t&agrave;i một c&aacute;ch ngắn ngọn s&uacute;c t&iacute;ch. T&ecirc;n phải viết với kiểu chữ in hoa, cỡ 14 font Times new roman, in đậm v&agrave; căn giữa.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">T&ecirc;n t&aacute;c giả, lớp: T&ecirc;n t&aacute;c giả (đồng t&aacute;c giả nếu c&oacute;) bao gồm đầy đủ họ v&agrave; t&ecirc;n, t&ecirc;n lớp h&agrave;nh ch&iacute;nh. Nếu c&oacute; hơn một t&aacute;c giả th&igrave; t&ecirc;n mỗi người sẽ được tr&igrave;nh b&agrave;y một d&ograve;ng. Định dạng sử dụng l&agrave; font Times new roman, đậm cỡ 12 v&agrave; căn phải.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">T&ecirc;n v&agrave; học h&agrave;m, học vị của gi&aacute;o vi&ecirc;n hướng dẫn: Định dạng phần n&agrave;y giống như phần t&ecirc;n t&aacute;c giả.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Ti&ecirc;u đề cuối : Bao gồm t&ecirc;n địa danh, năm xuất bản. Định dạng phần n&agrave;y sử dụng font Times new roman in hoa, cỡ 14, in đậm v&agrave; căn giữa.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Trang b&igrave;a phụ của đề t&agrave;i : Trang phụ n&agrave;y l&agrave; bản copy của trang b&igrave;a.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">C&aacute;c trang nội dung của đề t&agrave;i : Bao gồm trang mục lục, danh mục bảng biểu, danh mục từ viết tắt (xếp theo bảng chữ c&aacute;i), nội dung mở đầu, nội dung c&aacute;c chương v&agrave; phần kết luận. Trừ phần mục lục, t&agrave;i liệu tham khảo v&agrave; mục lục, c&aacute;c trang n&agrave;y được tr&igrave;nh b&agrave;y v&agrave; đ&aacute;nh số thứ tự trong khoảng 40 đến 60 trang (với đề t&agrave;i nghi&ecirc;n cứu khoa học sinh vi&ecirc;n hoặc kh&oacute;a luận tốt nghiệp). Định dạng phần n&agrave;y sử dụng font Times new roman in hoa, cỡ 13, c&aacute;ch d&ograve;ng</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">1.3 đến 1.5 lines, mật độ thường v&agrave; kh&ocirc;ng n&eacute;n hoặc k&eacute;o gi&atilde;n giữa c&aacute;c chữ. Đối với d&ograve;ng ti&ecirc;u đề th&igrave; khoảng c&aacute;ch d&ograve;ng n&ecirc;n l&agrave; 6 pt (đối với c&aacute;ch trước v&agrave; sau). Định dạng trang của phần n&agrave;y l&agrave; 3 cm ở lề phải v&agrave; 2cm ở c&aacute;c lề c&ograve;n lại.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">C&aacute;ch đ&aacute;nh ti&ecirc;u đề: Ti&ecirc;u đề cho nội dung t&agrave;i liệu thường bao gồm ti&ecirc;u đề chương v&agrave; ti&ecirc;u đề mục cho từng chương.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Ti&ecirc;u đề chương : Ti&ecirc;u đề chương được tr&igrave;nh b&agrave;y ở đầu một trang mới, viết chữ in hoa, cỡ 13 với font chữ Times new roman d&atilde;n d&ograve;ng l&agrave; 1.3. Định dạng cho ti&ecirc;u đề n&agrave;y l&agrave; căn giữa v&agrave; khoảng c&aacute;ch đoạn (trước, sau) l&agrave; 12pt.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Ti&ecirc;u đề mục v&agrave; tiểu mục: C&aacute;ch đ&aacute;nh đề mục cho ti&ecirc;u đề trong c&aacute;c chương được quy định theo dạng 1.1; 1.2; 1.1.1, 1.2.2. . . t&ugrave;y thuộc v&agrave;o số thứ tự v&agrave; số cấp của ti&ecirc;u đề. Định dạng cho ti&ecirc;u đề l&agrave; chữ in đậm, căn s&aacute;t lề tr&aacute;i v&agrave; c&oacute; chung cỡ chữ với nội dung của c&aacute;c đoạn văn bản trong chương.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Ch&uacute; &yacute;: &nbsp;Việc đ&aacute;nh nh&oacute;m số th&agrave;nh nh&oacute;m chữ số tối đa gồm bốn chữ số. đối với mỗi mục sẽ bao gồm &iacute;t nhất hai ti&ecirc;u mục</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Bảng biểu, sơ đồ, phương tr&igrave;nh v&agrave; h&igrave;nh vẽ : Bảng biểu, sơ đồ, phương tr&igrave;nh v&agrave; h&igrave;nh vẽ phải được đ&aacute;nh số gắn với số chương. V&iacute; dụ: &quot;Bảng 4.3&quot; c&oacute; nghĩa l&agrave; h&igrave;nh số 3 trong chương 4. Đối với bảng, sơ đồ, phương tr&igrave;nh lấy từ nguồn kh&aacute;c phải được tr&iacute;ch dẫn tu&acirc;n theo quy tắc tr&iacute;ch dẫn.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>', '1550901176', null, '0', '48', '1', '0', null, '0', null, null, null, null, null, null, '/images/course/48/lesson/1550925774_chuong-5-viet-va-thuyet-trinh-bao-cao-nckh.png', null);
INSERT INTO `lesson` VALUES ('150', '01. chuong 1', '<p><img alt=\"\" src=\"/public/images/course/52/lesson/1551008656_2.png\" style=\"height:384px; width:576px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Cuối năm 2013, tr&ecirc;n đường l&aacute;i xe về qu&ecirc; ăn Tết dọc duy&ecirc;n hải miền Trung, ch&uacute;ng t&ocirc;i dừng uống nước tại một quầy b&aacute;n dưa hấu ven lộ. Gần đ&oacute;, những mảng tường th&eacute;p c&ugrave;ng ống kh&oacute;i sơn trắng - đỏ chọc thẳng l&ecirc;n nền trời xanh đầy nắng gi&oacute;. Đ&oacute; l&agrave; nh&agrave; m&aacute;y nhiệt điện Vĩnh T&acirc;n 2, thuộc tổ hợp điện lực Vĩnh T&acirc;n, đang được x&acirc;y dựng.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Trong c&acirc;u chuyện, người b&aacute;n h&agrave;ng vui mừng v&igrave; huyện c&oacute; nh&agrave; m&aacute;y mới, sẽ c&oacute; nhiều c&ocirc;ng nh&acirc;n mua h&agrave;ng của họ. B&agrave; c&ograve;n hy vọng tổ hợp điện lực sẽ tạo ra nhiều c&ocirc;ng ăn việc l&agrave;m cho d&acirc;n địa phương, &quot;như lời c&aacute;c anh ở trển&quot;.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Bẵng đi một thời gian, giữa th&aacute;ng 4-2015, cuộc &quot;tụ tập đ&ocirc;ng người&quot; với quy m&ocirc; chưa từng c&oacute; đ&atilde; diễn ra ở B&igrave;nh Thuận. Nguy&ecirc;n nh&acirc;n ch&iacute;nh bởi những bức x&uacute;c chồng chất do &ocirc; nhiễm tạo ra bởi nh&agrave; m&aacute;y nhiệt điện Vĩnh T&acirc;n 2 khiến m&ocirc;i trường sống của d&acirc;n cư bị bức tử.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Người d&acirc;n B&igrave;nh Thuận đ&atilde; bức x&uacute;c kh&ocirc;ng chỉ bởi những ống kh&oacute;i ph&aacute; hỏng bầu trời, m&agrave; c&ograve;n bởi một hậu quả kh&aacute;c của điện lực Vĩnh T&acirc;n: ph&aacute;t t&aacute;n bụi v&agrave; c&aacute;c chất độc hại từ b&atilde;i tro xỉ đang g&acirc;y &ocirc; nhiễm nước ngầm, bức hại biển v&agrave; nguồn lợi thủy sản - sinh kế của họ. Chỉ chưa đầy năm năm từ ng&agrave;y tổ m&aacute;y nhiệt điện than đầu ti&ecirc;n vận h&agrave;nh tại đ&acirc;y, số liệu của Bộ C&ocirc;ng thương cho thấy b&atilde;i tro xỉ Vĩnh T&acirc;n đ&atilde; chứa khoảng 4,5 triệu m<sup>3</sup>.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Lượng tro xỉ thải ra từ nhiệt điện Vĩnh T&acirc;n nếu chứa trong c&aacute;c container 40 feet với tải trọng 30 tấn, đặt tr&ecirc;n c&aacute;c toa t&agrave;u th&igrave; phải cần đến gần 127.000 toa t&agrave;u. Đo&agrave;n t&agrave;u tro xỉ đ&oacute; c&oacute; chiều d&agrave;i l&agrave; 1.900 km, với đầu m&aacute;y tại ga S&agrave;i G&ograve;n v&agrave; toa cuối ở ga Đồng Đăng. Đều đặn mỗi năm, điện lực Vĩnh T&acirc;n tạo ra một đo&agrave;n t&agrave;u tro xỉ d&agrave;i như thế.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Nhưng Việt Nam kh&ocirc;ng chỉ c&oacute; mỗi điện lực Vĩnh T&acirc;n, m&agrave; c&ograve;n điện lực Duy&ecirc;n Hải (Tr&agrave; Vinh), Long Ph&uacute; (S&oacute;c Trăng), S&ocirc;ng Hậu (Hậu Giang), M&ocirc;ng Dương (Quảng Ninh), Vũng &Aacute;ng (H&agrave; Tĩnh), Quỳnh Lập (Nghệ An), Quảng Trạch (Quảng B&igrave;nh), Hải Dương, Hải Ph&ograve;ng, Nam Định, Quảng Trị, Long An... v&agrave; rất nhiều nữa. Ch&uacute;ng cũng đang v&agrave; sẽ tạo ra những &quot;đo&agrave;n t&agrave;u&quot; tro xỉ kh&aacute;c.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">C&oacute; một sự li&ecirc;n hệ đ&aacute;ng suy ngẫm về vụ &quot;tụ tập đ&ocirc;ng người&quot; ở Vĩnh T&acirc;n. Năm 2015 cũng l&agrave; năm m&agrave; Việt Nam ch&iacute;nh thức chuyển từ quốc gia xuất khẩu năng lượng sang nhập khẩu năng lượng, trong đ&oacute; chiếm tỷ trọng lớn nhất l&agrave; than đ&aacute; để đ&aacute;p ứng cho nhu cầu ng&agrave;y c&agrave;ng tăng cao của c&aacute;c nh&agrave; m&aacute;y nhiệt điện.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Lịch sử hơn 120 năm xuất khẩu than đ&aacute; của Việt Nam nay đ&atilde; kết th&uacute;c để chuyển sang một trang mới. Trang mới n&agrave;y ng&agrave;y c&agrave;ng &quot;đen&quot; hơn. Chỉ ri&ecirc;ng năm 2018, số liệu thống k&ecirc; cho thấy Việt Nam đ&atilde; chi một số tiền kỷ lục l&agrave; hơn 2 tỷ USD để nhập khẩu than đ&aacute;. Thực tế th&igrave; ti&ecirc;u thụ than đ&aacute; của Việt Nam đ&atilde; tăng phi m&atilde; đến 350% chỉ trong v&ograve;ng 10 năm qua, so với tỷ lệ trung b&igrave;nh c&ugrave;ng giai đoạn to&agrave;n thế giới l&agrave; 26%. Nhưng &quot;cơn nghiện&quot; than đ&aacute; của Việt Nam vẫn chưa thấy điểm dừng, nếu kh&ocirc;ng muốn n&oacute;i l&agrave; c&agrave;ng ng&agrave;y nghiện c&agrave;ng nặng.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">C&aacute;c chất &ocirc; nhiễm ph&aacute;t thải từ nhiệt điện than tr&ecirc;n thế giới từ l&acirc;u đ&atilde; được x&aacute;c định l&agrave; thủ phạm g&acirc;y ra &ocirc; nhiễm kh&ocirc;ng kh&iacute;, nguồn nước, đất trồng, ph&aacute; hủy c&aacute;c hệ sinh th&aacute;i, v&agrave; g&acirc;y ra bệnh tật cho c&aacute;c cộng đồng d&acirc;n cư. Kh&ocirc;ng chỉ thế, nhiệt điện than c&ograve;n bị chỉ đ&iacute;ch danh l&agrave; thủ phạm ch&iacute;nh ph&aacute;t thải kh&iacute; nh&agrave; k&iacute;nh, g&acirc;y ra hiện tượng ấm l&ecirc;n to&agrave;n cầu v&agrave; biến đổi kh&iacute; hậu. Năm 2016, nguy&ecirc;n Chủ tịch Ng&acirc;n h&agrave;ng thế giới Jim Yong Kim đ&atilde; c&oacute; một ph&aacute;t biểu chấn động: &quot;Nếu Việt Nam tiến h&agrave;nh lắp đặt 40 GW nhiệt điện than v&agrave; to&agrave;n bộ khu vực thực thi c&aacute;c kế hoạch nhiệt điện than, t&ocirc;i nghĩ cuộc sống sẽ kết th&uacute;c. Đ&acirc;y sẽ l&agrave; một thảm họa cho h&agrave;nh tinh của ch&uacute;ng ta&quot;.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Cuối năm 2013, tr&ecirc;n đường l&aacute;i xe về qu&ecirc; ăn Tết dọc duy&ecirc;n hải miền Trung, ch&uacute;ng t&ocirc;i dừng uống nước tại một quầy b&aacute;n dưa hấu ven lộ. Gần đ&oacute;, những mảng tường th&eacute;p c&ugrave;ng ống kh&oacute;i sơn trắng - đỏ chọc thẳng l&ecirc;n nền trời xanh đầy nắng gi&oacute;. Đ&oacute; l&agrave; nh&agrave; m&aacute;y nhiệt điện Vĩnh T&acirc;n 2, thuộc tổ hợp điện lực Vĩnh T&acirc;n, đang được x&acirc;y dựng.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Trong c&acirc;u chuyện, người b&aacute;n h&agrave;ng vui mừng v&igrave; huyện c&oacute; nh&agrave; m&aacute;y mới, sẽ c&oacute; nhiều c&ocirc;ng nh&acirc;n mua h&agrave;ng của họ. B&agrave; c&ograve;n hy vọng tổ hợp điện lực sẽ tạo ra nhiều c&ocirc;ng ăn việc l&agrave;m cho d&acirc;n địa phương, &quot;như lời c&aacute;c anh ở trển&quot;.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Bẵng đi một thời gian, giữa th&aacute;ng 4-2015, cuộc &quot;tụ tập đ&ocirc;ng người&quot; với quy m&ocirc; chưa từng c&oacute; đ&atilde; diễn ra ở B&igrave;nh Thuận. Nguy&ecirc;n nh&acirc;n ch&iacute;nh bởi những bức x&uacute;c chồng chất do &ocirc; nhiễm tạo ra bởi nh&agrave; m&aacute;y nhiệt điện Vĩnh T&acirc;n 2 khiến m&ocirc;i trường sống của d&acirc;n cư bị bức tử.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Người d&acirc;n B&igrave;nh Thuận đ&atilde; bức x&uacute;c kh&ocirc;ng chỉ bởi những ống kh&oacute;i ph&aacute; hỏng bầu trời, m&agrave; c&ograve;n bởi một hậu quả kh&aacute;c của điện lực Vĩnh T&acirc;n: ph&aacute;t t&aacute;n bụi v&agrave; c&aacute;c chất độc hại từ b&atilde;i tro xỉ đang g&acirc;y &ocirc; nhiễm nước ngầm, bức hại biển v&agrave; nguồn lợi thủy sản - sinh kế của họ. Chỉ chưa đầy năm năm từ ng&agrave;y tổ m&aacute;y nhiệt điện than đầu ti&ecirc;n vận h&agrave;nh tại đ&acirc;y, số liệu của Bộ C&ocirc;ng thương cho thấy b&atilde;i tro xỉ Vĩnh T&acirc;n đ&atilde; chứa khoảng 4,5 triệu m<sup>3</sup>.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Lượng tro xỉ thải ra từ nhiệt điện Vĩnh T&acirc;n nếu chứa trong c&aacute;c container 40 feet với tải trọng 30 tấn, đặt tr&ecirc;n c&aacute;c toa t&agrave;u th&igrave; phải cần đến gần 127.000 toa t&agrave;u. Đo&agrave;n t&agrave;u tro xỉ đ&oacute; c&oacute; chiều d&agrave;i l&agrave; 1.900 km, với đầu m&aacute;y tại ga S&agrave;i G&ograve;n v&agrave; toa cuối ở ga Đồng Đăng. Đều đặn mỗi năm, điện lực Vĩnh T&acirc;n tạo ra một đo&agrave;n t&agrave;u tro xỉ d&agrave;i như thế.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Nhưng Việt Nam kh&ocirc;ng chỉ c&oacute; mỗi điện lực Vĩnh T&acirc;n, m&agrave; c&ograve;n điện lực Duy&ecirc;n Hải (Tr&agrave; Vinh), Long Ph&uacute; (S&oacute;c Trăng), S&ocirc;ng Hậu (Hậu Giang), M&ocirc;ng Dương (Quảng Ninh), Vũng &Aacute;ng (H&agrave; Tĩnh), Quỳnh Lập (Nghệ An), Quảng Trạch (Quảng B&igrave;nh), Hải Dương, Hải Ph&ograve;ng, Nam Định, Quảng Trị, Long An... v&agrave; rất nhiều nữa. Ch&uacute;ng cũng đang v&agrave; sẽ tạo ra những &quot;đo&agrave;n t&agrave;u&quot; tro xỉ kh&aacute;c.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">C&oacute; một sự li&ecirc;n hệ đ&aacute;ng suy ngẫm về vụ &quot;tụ tập đ&ocirc;ng người&quot; ở Vĩnh T&acirc;n. Năm 2015 cũng l&agrave; năm m&agrave; Việt Nam ch&iacute;nh thức chuyển từ quốc gia xuất khẩu năng lượng sang nhập khẩu năng lượng, trong đ&oacute; chiếm tỷ trọng lớn nhất l&agrave; than đ&aacute; để đ&aacute;p ứng cho nhu cầu ng&agrave;y c&agrave;ng tăng cao của c&aacute;c nh&agrave; m&aacute;y nhiệt điện.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Lịch sử hơn 120 năm xuất khẩu than đ&aacute; của Việt Nam nay đ&atilde; kết th&uacute;c để chuyển sang một trang mới. Trang mới n&agrave;y ng&agrave;y c&agrave;ng &quot;đen&quot; hơn. Chỉ ri&ecirc;ng năm 2018, số liệu thống k&ecirc; cho thấy Việt Nam đ&atilde; chi một số tiền kỷ lục l&agrave; hơn 2 tỷ USD để nhập khẩu than đ&aacute;. Thực tế th&igrave; ti&ecirc;u thụ than đ&aacute; của Việt Nam đ&atilde; tăng phi m&atilde; đến 350% chỉ trong v&ograve;ng 10 năm qua, so với tỷ lệ trung b&igrave;nh c&ugrave;ng giai đoạn to&agrave;n thế giới l&agrave; 26%. Nhưng &quot;cơn nghiện&quot; than đ&aacute; của Việt Nam vẫn chưa thấy điểm dừng, nếu kh&ocirc;ng muốn n&oacute;i l&agrave; c&agrave;ng ng&agrave;y nghiện c&agrave;ng nặng.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">C&aacute;c chất &ocirc; nhiễm ph&aacute;t thải từ nhiệt điện than tr&ecirc;n thế giới từ l&acirc;u đ&atilde; được x&aacute;c định l&agrave; thủ phạm g&acirc;y ra &ocirc; nhiễm kh&ocirc;ng kh&iacute;, nguồn nước, đất trồng, ph&aacute; hủy c&aacute;c hệ sinh th&aacute;i, v&agrave; g&acirc;y ra bệnh tật cho c&aacute;c cộng đồng d&acirc;n cư. Kh&ocirc;ng chỉ thế, nhiệt điện than c&ograve;n bị chỉ đ&iacute;ch danh l&agrave; thủ phạm ch&iacute;nh ph&aacute;t thải kh&iacute; nh&agrave; k&iacute;nh, g&acirc;y ra hiện tượng ấm l&ecirc;n to&agrave;n cầu v&agrave; biến đổi kh&iacute; hậu. Năm 2016, nguy&ecirc;n Chủ tịch Ng&acirc;n h&agrave;ng thế giới Jim Yong Kim đ&atilde; c&oacute; một ph&aacute;t biểu chấn động: &quot;Nếu Việt Nam tiến h&agrave;nh lắp đặt 40 GW nhiệt điện than v&agrave; to&agrave;n bộ khu vực thực thi c&aacute;c kế hoạch nhiệt điện than, t&ocirc;i nghĩ cuộc sống sẽ kết th&uacute;c. Đ&acirc;y sẽ l&agrave; một thảm họa cho h&agrave;nh tinh của ch&uacute;ng ta&quot;.</span></span></span></p>\r\n\r\n<p><math xmlns=\"http://www.w3.org/1998/Math/MathML\"><mfrac><mn>8</mn><mn>9</mn></mfrac></math></p>', '1551008627', null, '0', '52', '1', '0', null, '0', null, null, null, null, null, null, null, null);
