INSERT INTO `lesson` VALUES ('162', 'câu 1', '', '1551020724', null, '154', '54', '1', '154', '154', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('166', 'Chương 7 :HÀM HAI BIẾN', '<ol start=\"14\" style=\"list-style-type:lower-alpha\">\r\n	<li value=\"345\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:20.0pt\"><img alt=\"\" src=\"/public/images/course/51/lesson/1551022122_chuong7.png\" style=\"height:964px; width:1000px\" /></span></strong></span></span></li>\r\n	<li value=\"345\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:20.0pt\">Chương 7&nbsp;</span></strong><strong><span style=\"font-size:16.0pt\">H&agrave;m hai biến</span></strong></span></span></li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li><span style=\"font-size:16pt\"><em><u>Mở đầu về h&agrave;m hai biến</u></em></span>\r\n\r\n		<ol>\r\n			<li><span style=\"font-size:16pt\">Định nghĩa h&agrave;m hai biến</span></li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Hiểu theo c&aacute;ch đơn giản một quan hệ f n&agrave;o đ&oacute;, với mỗi cặp gi&aacute; trị của 2 biến (x, y) th&igrave; ta lu&ocirc;n nhận được một v&agrave; chỉ một gi&aacute; trị của biến z thỏa m&atilde;n z=f(x, y). đẳng thức n&agrave;y được gọi l&agrave; h&agrave;m hai biến </span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<ol>\r\n			<li><span style=\"font-size:16pt\">Giới hạn v&agrave; t&iacute;nh li&ecirc;n tục của h&agrave;m hai biến</span></li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Giới hạn bội </span></span></span><img src=\"/public/images/course/51/lesson/1551022004_image-20190224222941-1.png\" style=\"height:54px; width:258px\" /></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Giới hạn lặp </span></span></span><img src=\"/public/images/course/51/lesson/1551022004_image-20190224222941-2.png\" style=\"height:50px; width:253px\" /><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;</span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img src=\"/public/images/course/51/lesson/1551022004_image-20190224222941-3.png\" style=\"height:50px; width:253px\" /><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;</span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Li&ecirc;n tục: giống định nghĩa với h&agrave;m một biến </span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<ol>\r\n			<li style=\"list-style-type:none\">\r\n			<ol start=\"2\">\r\n				<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Th&igrave; z=f (x, y) li&ecirc;n tục &harr;</span></span></span><img src=\"/public/images/course/51/lesson/1551022004_image-20190224222941-4.png\" style=\"height:54px; width:343px\" /></li>\r\n				<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Đương nhi&ecirc;n nếu h&agrave;m hai biến l&agrave; h&agrave;m sơ cấp x&aacute;c định tr&ecirc;n một l&acirc;n cận điểm </span><em><span style=\"font-size:16.0pt\">x</span></em></span></span><img src=\"/public/images/course/51/lesson/1551022004_image-20190224222941-5.png\" style=\"height:36px; width:84px\" /><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&nbsp;th&igrave; n&oacute; li&ecirc;n tục tại điểm</span></span></span><img src=\"/public/images/course/51/lesson/1551022004_image-20190224222941-6.png\" style=\"height:36px; width:84px\" /></li>\r\n			</ol>\r\n			</li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:72px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:96px\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<ol>\r\n			<li><span style=\"font-size:16pt\">Đạo h&agrave;m v&agrave; vi ph&acirc;n của h&agrave;m hai biến</span></li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Đạo h&agrave;m ri&ecirc;ng cấp 1</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Với h&agrave;m z=z (x, y) li&ecirc;n tục v&agrave; x&aacute;c định, khi ∆x</span><span style=\"font-size:16.0pt\">&rarr;0 v&agrave; giới hạn sau <span style=\"background-color:yellow\">tồn tại</span> v&agrave; <span style=\"background-color:yellow\">hữu hạn</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><img src=\"/public/images/course/51/lesson/1551022004_image-20190224222941-7.png\" style=\"height:67px; width:294px\" /></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Th&igrave; ta n&oacute;i h&agrave;m số tr&ecirc;n c&oacute; đạo h&agrave;m ri&ecirc;ng cấp 1 theo biến x.</span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">K&iacute; hiệu&nbsp; </span></span></span><img src=\"/public/images/course/51/lesson/1551022004_image-20190224222941-8.png\" style=\"height:36px; width:88px\" /></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:aqua\">&rarr; b&agrave;i tập về t&iacute;nh đạo h&agrave;m ri&ecirc;ng cấp 1</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">C&aacute;ch giải quyết b&agrave;i tập n&agrave;y:</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">T&iacute;nh đạo h&agrave;m ri&ecirc;ng cấp 1 theo biến x th&igrave; coi chỉ c&oacute; biến l&agrave; x, v&agrave; y coi l&agrave; hằng số (v&agrave; tương tự với biến l&agrave; y th&igrave; coi x l&agrave; hằng số)</span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">VD: t&iacute;nh đạo h&agrave;m ri&ecirc;ng cấp 1 của h&agrave;m số sau:</span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<ol>\r\n			<li style=\"list-style-type:none\">\r\n			<ol>\r\n				<li style=\"list-style-type:none\">\r\n				<ol>\r\n					<li style=\"list-style-type:none\">\r\n					<ol>\r\n						<li style=\"list-style-type:none\">\r\n						<ol>\r\n							<li style=\"list-style-type:none\">\r\n							<ol style=\"list-style-type:lower-alpha\">\r\n								<li><img src=\"/public/images/course/51/lesson/1551022004_image-20190224222941-9.png\" style=\"height:37px; width:123px\" /><img src=\"/public/images/course/51/lesson/1551022004_image-20190224222941-10.png\" style=\"height:36px; width:139px\" /></li>\r\n								<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">+đạo h&agrave;m ri&ecirc;ng cấp 1 theo x n&ecirc;n coi y l&agrave; hằng số do đ&oacute;</span></span></span></li>\r\n								<li><img src=\"/public/images/course/51/lesson/1551022004_image-20190224222941-11.png\" style=\"height:74px; width:780px\" /></li>\r\n								<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">+ tương tự ta c&oacute;</span></span></span></li>\r\n								<li><img src=\"/public/images/course/51/lesson/1551022004_image-20190224222941-12.png\" style=\"height:94px; width:780px\" /></li>\r\n							</ol>\r\n							</li>\r\n						</ol>\r\n						</li>\r\n					</ol>\r\n					</li>\r\n				</ol>\r\n				</li>\r\n			</ol>\r\n			</li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Đạo h&agrave;m ri&ecirc;ng cấp cao</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">ở đ&acirc;y ch&uacute;ng ta quan t&acirc;m đến đạo h&agrave;m ri&ecirc;ng cấp 2 nhiều hơn</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:96px\"><img src=\"/public/images/course/51/lesson/1551022005_image-20190224222941-13.png\" style=\"height:49px; width:266px\" /><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&rarr; đạo h&agrave;m ri&ecirc;ng cấp 2 theo xx l&agrave; đạo h&agrave;m của </span></span></span><img src=\"/public/images/course/51/lesson/1551022005_image-20190224222941-14.png\" style=\"height:36px; width:88px\" /><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&nbsp;theo x</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:96px\"><img src=\"/public/images/course/51/lesson/1551022005_image-20190224222941-15.png\" style=\"height:64px; width:271px\" /><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&nbsp;&rarr; đạo h&agrave;m ri&ecirc;ng cấp 2 theo yy l&agrave; đạo h&agrave;m của </span></span></span><img src=\"/public/images/course/51/lesson/1551022005_image-20190224222941-16.png\" style=\"height:39px; width:88px\" /><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&nbsp;theo y</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:96px\"><img src=\"/public/images/course/51/lesson/1551022005_image-20190224222941-17.png\" style=\"height:61px; width:572px\" /><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&rarr; </span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">đạo h&agrave;m ri&ecirc;ng cấp 2 theo xy l&agrave; đạo h&agrave;m của </span></span></span><img src=\"/public/images/course/51/lesson/1551022005_image-20190224222941-18.png\" style=\"height:36px; width:88px\" /><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&nbsp;theo y </span></span></span></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&rarr; <span style=\"background-color:aqua\">b&agrave;i tập về t&iacute;nh đạo h&agrave;m ri&ecirc;ng cấp 2</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">C&aacute;ch giải quyết b&agrave;i tập n&agrave;y</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:120px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 1</span></span><span style=\"font-size:16.0pt\">: T&iacute;nh đạo h&agrave;m ri&ecirc;ng cấp 1 như tr&ecirc;n</span></span></span></p>\r\n\r\n<p style=\"margin-left:120px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 2</span></span><span style=\"font-size:16.0pt\">:</span></span></span></p>\r\n\r\n<p style=\"margin-left:168px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">+Đạo h&agrave;m ri&ecirc;ng cấp 2 theo xx l&agrave; đạo h&agrave;m của </span></span></span><img src=\"/public/images/course/51/lesson/1551022005_image-20190224222941-19.png\" style=\"height:36px; width:88px\" /><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&nbsp;theo x</span></span></span></p>\r\n\r\n<p style=\"margin-left:168px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">+Đạo h&agrave;m ri&ecirc;ng cấp 2 theo yy l&agrave; đạo h&agrave;m của </span></span></span><img src=\"/public/images/course/51/lesson/1551022005_image-20190224222941-20.png\" style=\"height:39px; width:88px\" /><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&nbsp;theo y</span></span></span></p>\r\n\r\n<p style=\"margin-left:192px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">+Đạo h&agrave;m ri&ecirc;ng cấp 2 theo xy l&agrave; đạo h&agrave;m của </span></span></span><img src=\"/public/images/course/51/lesson/1551022005_image-20190224222941-21.png\" style=\"height:36px; width:88px\" /><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&nbsp;theo y</span></span></span></p>\r\n\r\n<p style=\"margin-left:120px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">VD </span><span style=\"font-size:16.0pt\">t&iacute;nh đạo h&agrave;m ri&ecirc;ng cấp 2 của h&agrave;m số sau:</span></span></span></p>\r\n\r\n<p style=\"margin-left:168px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:16.0pt\">z</span></em></span></span><img src=\"/public/images/course/51/lesson/1551022005_image-20190224222941-22.png\" style=\"height:37px; width:123px\" /><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&nbsp;với </span><em><span style=\"font-size:16.0pt\">0</span></em></span></span><img src=\"/public/images/course/51/lesson/1551022005_image-20190224222941-23.png\" style=\"height:36px; width:139px\" /></p>\r\n\r\n<p style=\"margin-left:168px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Theo kết quả b&ecirc;n tr&ecirc;n ta c&oacute; </span></span></span></p>\r\n\r\n<p style=\"margin-left:168px\"><img src=\"/public/images/course/51/lesson/1551022005_image-20190224222941-24.png\" style=\"height:37px; width:286px\" /></p>\r\n\r\n<p style=\"margin-left:168px\"><img src=\"/public/images/course/51/lesson/1551022005_image-20190224222941-25.png\" style=\"height:70px; width:251px\" /></p>\r\n\r\n<p style=\"margin-left:192px\"><img src=\"/public/images/course/51/lesson/1551022005_image-20190224222941-26.png\" style=\"height:86px; width:780px\" /></p>\r\n\r\n<p style=\"margin-left:192px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:16.0pt\">&rarr;</span></em></span></span><img src=\"/public/images/course/51/lesson/1551022005_image-20190224222941-27.png\" style=\"height:211px; width:780px\" /></p>\r\n\r\n<p style=\"margin-left:168px\"><img src=\"/public/images/course/51/lesson/1551022005_image-20190224222941-28.png\" style=\"height:272px; width:780px\" /></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Vi ph&acirc;n to&agrave;n phần </span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Khi c&aacute;c số gia ∆x</span><span style=\"font-size:16.0pt\">&rarr;0,</span><span style=\"font-size:16.0pt\"> ∆y</span><span style=\"font-size:16.0pt\">&rarr;0 th&igrave; </span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><img src=\"/public/images/course/51/lesson/1551022005_image-20190224222941-29.png\" style=\"height:39px; width:598px\" /><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; </span></span></p>\r\n\r\n<p style=\"margin-left:42px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&rarr; <strong><span style=\"background-color:aqua\">b&agrave;i tập về t&iacute;nh gần đ&uacute;ng một gi&aacute; trị</span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:36px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">C&aacute;ch giải quyết b&agrave;i tập n&agrave;y</span></span></span></p>\r\n\r\n<p style=\"margin-left:36px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 1</span></span><span style=\"font-size:16.0pt\">: Từ biểu thức đ&atilde; cho x&aacute;c định h&agrave;m z=z (x, y)</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước2</span></span><span style=\"font-size:16.0pt\">: Chọn gi&aacute; trị </span><span style=\"font-size:16.0pt\">x<sub>0</sub>, y<sub>0</sub>, ∆x, ∆y</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">ch&uacute; &yacute; chọn sao cho biểu thức z=z (x<sub>0</sub>, y<sub>0</sub>) t&iacute;nh được dễ d&agrave;ng, v&agrave; ∆x</span><span style=\"font-size:16.0pt\">&rarr;0,</span><span style=\"font-size:16.0pt\"> ∆y</span><span style=\"font-size:16.0pt\">&rarr;0</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 3</span></span><span style=\"font-size:16.0pt\"> T&iacute;nh đạo h&agrave;m ri&ecirc;ng cấp 1 tại </span><span style=\"font-size:16.0pt\">(x<sub>0</sub>, y<sub>0</sub>)</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 4</span></span><span style=\"font-size:16.0pt\"> Thay v&agrave;o c&ocirc;ng thức v&agrave; t&iacute;nh ra kết quả</span></span></span></p>\r\n\r\n<p><img src=\"/public/images/course/51/lesson/1551022005_image-20190224222941-29.png\" style=\"height:39px; width:598px\" /></p>\r\n\r\n<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">T&igrave;m cực trị tự do của h&agrave;m hai biến </span></span></span></strong></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Hướng dẫn giải</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 1</span> X&aacute;c định điểm dừng M</span><span style=\"font-size:16.0pt\">(x<sub>0</sub>, y<sub>0</sub>) thỏa m&atilde;n </span></span></span><span class=\"math-tex\">\\(\\left\\{ \\begin{array}{l} z{\'_x}\\left( {{x_0};{y_0}} \\right) = 0\\\\ z{\'_y}\\left( {{x_0};{y_0}} \\right) = 0 \\end{array} \\right.\\)</span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"> </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 2</span></span><span style=\"font-size:16.0pt\"> T&iacute;nh </span></span></span><span class=\"math-tex\">\\(\\begin{array}{l} A = z\'{\'_{x{\\rm{x}}}}\\left( {{x_0};{y_0}} \\right)\\\\ B = z\'{\'_{xy}}\\left( {{x_0};{y_0}} \\right)\\\\ C = z\'{\'_{yy}}\\left( {{x_0};{y_0}} \\right) \\end{array}\\)</span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"> </span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">D=B<sup>2</sup>-AC</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 3</span></span><span style=\"font-size:16.0pt\"> Kết luận </span></span></span></p>\r\n\r\n<p style=\"margin-left:36px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&nbsp; D&gt;0 tại </span><span style=\"font-size:16.0pt\">M (x<sub>0</sub>, y<sub>0</sub>) h&agrave;m số kh&ocirc;ng c&oacute; cực trị</span></span></span></p>\r\n\r\n<p style=\"margin-left:36px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&nbsp; D&lt; 0 th&igrave; tại </span><span style=\"font-size:16.0pt\">M (x<sub>0</sub>, y<sub>0</sub>) h&agrave;m số c&oacute; cực trị </span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Cụ thể </span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">+với A&gt;0 h&agrave;m số đạt cực tiểu tại </span><span style=\"font-size:16.0pt\">M (x<sub>0</sub>, y<sub>0</sub>), z<sub>ct</sub>=f (x<sub>0</sub>, y<sub>0</sub>)</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">+ Với A&lt;0 h&agrave;m số đạt cực đại tại </span><span style=\"font-size:16.0pt\">M (x<sub>0</sub>, y<sub>0</sub>), z<sub>cđ</sub>=f (x<sub>0</sub>, y<sub>0</sub>)</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:fuchsia\">Ch&uacute; &yacute;</span></span><span style=\"font-size:16.0pt\"> D=0 th&igrave; ch&uacute;ng ta phải l&agrave;m theo phương ph&aacute;p kh&aacute;c, thường l&agrave; </span><span style=\"font-size:16.0pt\">sử dụng tổng b&igrave;nh phương b&eacute; nhất, tức l&agrave; gh&eacute;p th&agrave;nh tổng c&aacute;c b&igrave;nh phương </span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">VD7.4 T&igrave;m cực trị của h&agrave;m hai biến sau<span class=\"math-tex\">\\(f\\left( {x;y} \\right) = {x^2} + 2{\\rm{x}}y + 2{y^2} + x - y + 1\\)</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 1</span> X&aacute;c định điểm dừng M</span><span style=\"font-size:16.0pt\">(x<sub>0</sub>, y<sub>0</sub>) thỏa m&atilde;n</span></span></span><span class=\"math-tex\">\\(\\left\\{ \\begin{array}{l} f{\'_x}\\left( {{x_0};{y_0}} \\right) = 0\\\\ f{\'_y}\\left( {{x_0};{y_0}} \\right) = 0 \\end{array} \\right. \\Leftrightarrow \\left\\{ \\begin{array}{l} 2{{\\rm{x}}_o} + 2{y_0} + 1 = 0\\\\ 2{{\\rm{x}}_0} + 4{y_0} - 1 = 0 \\end{array} \\right. \\Leftrightarrow \\left\\{ \\begin{array}{l} {x_0} =  - \\frac{3}{2}\\\\ {y_0} = 1 \\end{array} \\right.\\)</span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 2</span></span><span style=\"font-size:16.0pt\"> T&iacute;nh </span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"margin-left:48px\"><span class=\"math-tex\">\\(\\begin{array}{l} A = f\'{\'_{x{\\rm{x}}}}\\left( {{x_0};{y_0}} \\right) = 2 &gt; 0\\\\ B = f\'{\'_{xy}}\\left( {{x_0};{y_0}} \\right) = 2\\\\ C = f\'{\'_{yy}}\\left( {{x_0};{y_0}} \\right) = 4 \\end{array}\\)</span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">D=B<sup>2</sup>-AC= -4&lt;0&nbsp; suy ra <span class=\"math-tex\">\\(M\\left( { - \\frac{3}{2};1} \\right)\\)</span> l&agrave; điểm cực trị m&agrave;<span class=\"math-tex\">\\( A = f\'{\'_{x{\\rm{x}}}}\\left( {{x_0};{y_0}} \\right) = 2 &gt; 0\\)</span>n&ecirc;n&nbsp; n&oacute; l&agrave; điểm cực tiểu</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Vậy h&agrave;m số đ&atilde; cho đạt cực tiểu l&agrave; </span>&nbsp;<span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\({f_{ct}} = f\\left( { - \\frac{3}{2};1} \\right) = \\frac{{ - 1}}{4}\\)</span></span><span style=\"font-size:16.0pt\"> tại </span><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(M\\left( { - \\frac{3}{2};1} \\right)\\)</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong><span style=\"font-family:Calibri,sans-serif\">T&igrave;m cực trị c&oacute; điều kiện của h&agrave;m hai biến </span></strong></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Hướng dẫn giải </span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Sử dụng phương ph&aacute;p thế </span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Nếu h&agrave;m g(x)=0 đơn giản, việc r&uacute;t một biến theo biến c&ograve;n lại đơn giản v&agrave; h&agrave;m một biến nhận được sau c&ugrave;ng kh&ocirc;ng phức tạp th&igrave; ta sử dụng phương ph&aacute;p thế</span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Bước 1: từ điều kiện cho trước ta <span style=\"background-color:yellow\">r&uacute;t 1 ẩn theo ẩn c&ograve;n lại</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Bước 2: Thế v&agrave;o h&agrave;m hai biến đ&atilde; cho để n&oacute; <span style=\"background-color:yellow\">quay về h&agrave;m một biến</span>,</span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Bước 3: t&igrave;m cực trị như đ&atilde; học ở THPT</span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Sử dụng phương ph&aacute;p nh&acirc;n tử Largrange</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 1</span></span><span style=\"font-size:16.0pt\"> Lập h&agrave;m Lagrange c&oacute; dạng </span><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(L\\left( {x;y;\\lambda } \\right) = z\\left( {x;y} \\right) + \\lambda g\\left( {x;y} \\right)\\)</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">trong đ&oacute; </span><span style=\"font-size:16.0pt\">&nbsp;?</span><span style=\"font-size:16.0pt\"> được gọi l&agrave; nh&acirc;n tử </span><span style=\"font-size:16.0pt\">Lagrange</span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 2</span></span><span style=\"font-size:16.0pt\">: T&igrave;m điểm dừng: x&aacute;c định bộ số </span><span style=\"font-size:16.0pt\">(x<sub>0</sub>, y<sub>0</sub>, </span><span style=\"font-size:16.0pt\">?</span><sub><span style=\"font-size:16.0pt\">0</span></sub><span style=\"font-size:16.0pt\">) thỏa m&atilde;n đồng thời </span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:yellow\">L<sub>x</sub>&rsquo; </span></span><span style=\"font-size:16.0pt\"><span style=\"background-color:yellow\">(x<sub>0</sub>, y<sub>0</sub>, </span></span><span style=\"font-size:16.0pt\"><span style=\"background-color:yellow\">?</span></span><sub><span style=\"font-size:16.0pt\"><span style=\"background-color:yellow\">0</span></span></sub><span style=\"font-size:16.0pt\"><span style=\"background-color:yellow\">) =0 ;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-size:16.0pt\"><span style=\"background-color:yellow\">L<sub>y</sub>&rsquo;</span></span><span style=\"font-size:16.0pt\"><span style=\"background-color:yellow\">(x<sub>0</sub>,y<sub>0</sub>, </span></span><span style=\"font-size:16.0pt\"><span style=\"background-color:yellow\">?</span></span><sub><span style=\"font-size:16.0pt\"><span style=\"background-color:yellow\">0</span></span></sub><span style=\"font-size:16.0pt\"><span style=\"background-color:yellow\">)=0;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-size:16.0pt\"><span style=\"background-color:yellow\">&nbsp;L</span></span><sub><span style=\"font-size:16.0pt\"><span style=\"background-color:yellow\">?</span></span></sub><span style=\"font-size:16.0pt\"><span style=\"background-color:yellow\">&rsquo;</span></span><span style=\"font-size:16.0pt\"><span style=\"background-color:yellow\">(x<sub>0</sub>,y<sub>0</sub>, </span></span><span style=\"font-size:16.0pt\"><span style=\"background-color:yellow\">?</span></span><sub><span style=\"font-size:16.0pt\"><span style=\"background-color:yellow\">0</span></span></sub><span style=\"font-size:16.0pt\"><span style=\"background-color:yellow\">)=0</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 3</span></span><span style=\"font-size:16.0pt\">: T&iacute;nh định thức Hessian tại điểm dừng</span></span></span><span class=\"math-tex\">\\(\\left| H \\right| = \\left| {\\begin{array}{*{20}{c}} 0&amp;{g{\'_x}}&amp;{g{\'_y}}\\\\ {g{\'_x}}&amp;{L\'{\'_{x{\\rm{x}}}}}&amp;{L\'{\'_{xy}}}\\\\ {g{\'_y}}&amp;{{L_{xy}}}&amp;{L\'{\'_{yy}}} \\end{array}} \\right|\\)</span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"> </span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">|H|&gt;0 th&igrave; h&agrave;m số đạt <span style=\"background-color:yellow\">cực đại</span> c&oacute; điều kiện tại M (x<sub>0</sub>, y<sub>0</sub>), z<sub>cđ</sub>=f (x<sub>0</sub>, y<sub>0</sub>)</span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">|H|&lt;0 th&igrave; h&agrave;m số đạt <span style=\"background-color:yellow\">cực tiểu</span> c&oacute; điều kiện tại M (x<sub>0</sub>, y<sub>0</sub>), z<sub>ct</sub>=f (x<sub>0</sub>, y<sub>0</sub>)</span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:16.0pt\">VD7.5 T&igrave;m cực trị c&oacute; điều kiện của h&agrave;m hai biến sau</span></em><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(z = f\\left( {x;y} \\right) = 6 - 4{\\rm{x}} - 3y\\)</span></span><em><span style=\"font-size:16.0pt\"> với </span></em><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\({x^2} + {y^2} = 1\\)</span></span><em> </em></span></span></p>\r\n\r\n<p style=\"margin-left:120px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Ta c&oacute; <span class=\"math-tex\">\\(g\\left( {x;y} \\right) = {x^2} + {y^2} - 1 = 0\\)</span> </span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 1</span></span><span style=\"font-size:16.0pt\"> Lập h&agrave;m Lagrange </span><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(L = 6 - 4{\\rm{x}} - 3y + \\lambda \\left( {{x^2} + {y^2} - 1} \\right)\\)</span></span> </span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 2</span></span><span style=\"font-size:16.0pt\">: T&igrave;m điểm dừng</span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Điểm dừng của h&agrave;m Lagrange </span></span></span><span class=\"math-tex\">\\(\\left\\{ \\begin{array}{l} L{\'_x} = 2\\lambda x - 4 = 0\\\\ L{\'_y} = 2\\lambda x - 3 = 0\\\\ L{\'_\\lambda } = {x^2} + {y^2} - 1 = 0 \\end{array} \\right. \\Leftrightarrow \\left\\{ \\begin{array}{l} x = \\frac{2}{\\lambda }\\\\ y =  = \\frac{3}{{2\\lambda }}\\\\ {x^2} + {y^2} - 1 = 0 \\end{array} \\right. \\Leftrightarrow \\left\\{ \\begin{array}{l} \\lambda  = \\frac{5}{2}\\\\ x = \\frac{4}{5}\\\\ y = \\frac{3}{5} \\end{array} \\right.;\\left\\{ \\begin{array}{l} \\lambda  =  - \\frac{5}{2}\\\\ x =  - \\frac{4}{5}\\\\ y =  - \\frac{3}{5} \\end{array} \\right.\\)</span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"> </span></span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Vậy c&oacute; 2 điểm dừng l&agrave; </span></span></span><span class=\"math-tex\">\\(\\begin{array}{l} M\\left( {\\frac{4}{5};\\frac{3}{5}} \\right);{\\lambda _1} = \\frac{5}{2}\\\\ N\\left( { - \\frac{4}{5}; - \\frac{3}{5}} \\right);{\\lambda _2} =  - \\frac{5}{2} \\end{array}\\)</span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"> </span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 3</span></span><span style=\"font-size:16.0pt\">: T&iacute;nh định thức Hessian tại điểm dừng</span></span></span><span class=\"math-tex\">\\(\\begin{array}{l} L\'{\'_{x{\\rm{x}}}} = 2\\lambda ;L\'{\'_{yy}} = 2\\lambda ;L\'{\'_{xy}} = 0\\\\ g{\'_x} = 2{\\rm{x}};g{\'_y} = 2y \\end{array}\\)</span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"> </span></span><span class=\"math-tex\">\\(|H| = \\left| {\\begin{array}{*{20}{c}} 0&amp;{2{\\rm{x}}}&amp;{2y}\\\\ {2{\\rm{x}}}&amp;{2\\lambda }&amp;0\\\\ {2y}&amp;0&amp;{2\\lambda } \\end{array}} \\right| =  - 8\\lambda \\left( {{x^2} + {y^2}} \\right) =  - 8\\lambda \\)</span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"> </span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(M\\left( {\\frac{4}{5};\\frac{3}{5}} \\right);{\\lambda _1} = \\frac{5}{2} \\Rightarrow |H| &lt; 0\\)</span></span><span style=\"font-size:16.0pt\">n&ecirc;n<span class=\"math-tex\">\\( M\\left( {\\frac{4}{5};\\frac{3}{5}} \\right)\\)</span>l&agrave; điểm cực tiểu v&agrave; <span class=\"math-tex\">\\({f_{ct}} = f\\left( {\\frac{4}{5};\\frac{3}{5}} \\right) = 1\\)</span> </span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(N\\left( { - \\frac{4}{5}; - \\frac{3}{5}} \\right);{\\lambda _2} =  - \\frac{5}{2} \\Rightarrow |H| &gt; 0\\)</span></span><span style=\"font-size:16.0pt\">n&ecirc;n<span class=\"math-tex\">\\( N\\left( { - \\frac{4}{5}; - \\frac{3}{5}} \\right)\\)</span>l&agrave; điểm cực đại v&agrave; <span class=\"math-tex\">\\({f_{cd}} = f\\left( { - \\frac{4}{5}; - \\frac{3}{5}} \\right) = 11\\)</span> </span></span></span></p>\r\n\r\n<p>&nbsp;</p>', '1551021930', null, '0', '51', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('167', 'Chương 8:PHÉP TÍNH TÍCH PHÂN', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:16.0pt\"><img alt=\"\" src=\"/public/images/course/51/lesson/1551100914_c8.png\" style=\"height:1319px; width:1000px\" /></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:16.0pt\">Chương 8 Ph&eacute;p t&iacute;nh t&iacute;ch ph&acirc;n </span></strong></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">T&iacute;ch ph&acirc;n bất định </span></span></span>\r\n\r\n		<ol>\r\n			<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Nguy&ecirc;n h&agrave;m v&agrave; ph&eacute;p t&iacute;nh t&iacute;ch ph&acirc;n </span></span></span></li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Định nghĩa 8.1 (gi&aacute;o tr&igrave;nh Tr 193)</span></span></span></p>\r\n\r\n<p style=\"margin-left:144px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Cho h&agrave;m y=f(x), x&aacute;c định trr&ecirc;n đoạn [a; b]. H&agrave;m F(x) được gọi l&agrave; nguy&ecirc;n h&agrave;m của f(x) tr&ecirc;n [a; b] nếu:</span></span></span></p>\r\n\r\n<p style=\"margin-left:144px\"><img src=\"/public/images/course/51/lesson/1551022636_image-20190224224013-1.png\" style=\"height:36px; width:264px\" /></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Mệnh đề 8.1 (gi&aacute;o tr&igrave;nh tr 193)</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Tr&ecirc;n [a; b] nếu F(x) l&agrave; một nguy&ecirc;n h&agrave;m của f(x) th&igrave; với hằng số C t&ugrave;y &yacute; h&agrave;m F(x)+C cũng l&agrave; một nguy&ecirc;n h&agrave;m của f(x). Hơn nữa, mọi nguy&ecirc;n h&agrave;m của f(x) đều c&oacute; dạng F(x)+C với C l&agrave; một hằng số n&agrave;o đ&oacute;.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Định nghĩa 8.2 (gi&aacute;o tr&igrave;nh tr 193)</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Nếu F(x) l&agrave; một nguy&ecirc;n h&agrave;m của f(x) th&igrave; biều thức F(x)+C vớ C l&agrave; một hằng số t&ugrave;y &yacute; , được gọi l&agrave; t&iacute;ch ph&acirc;n bất định của h&agrave;m số f(x) v&agrave; được k&iacute; hiệu l&agrave;</span></span></span><img src=\"/public/images/course/51/lesson/1551022636_image-20190224224013-2.png\" style=\"height:37px; width:103px\" /></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">N&oacute;i c&aacute;ch kh&aacute;c </span></span></span><img src=\"/public/images/course/51/lesson/1551022636_image-20190224224013-3.png\" style=\"height:37px; width:247px\" /><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&nbsp;nếu </span></span></span><br />\r\n<img src=\"/public/images/course/51/lesson/1551022636_image-20190224224013-4.png\" style=\"height:36px; width:264px\" /></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">f(x) l&agrave; h&agrave;m dưới dấu t&iacute;ch ph&acirc;n, f(x)dx l&agrave; biểu thức dưới dấu t&iacute;ch ph&acirc;n, x l&agrave; biến lấy t&iacute;ch ph&acirc;n </span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<ol start=\"2\">\r\n			<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">C&aacute;c t&iacute;nh chất của t&iacute;ch ph&acirc;n bất định (gi&aacute;o tr&igrave;nh tr 194)</span></span></span></li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n	<li value=\"1\"><img src=\"/public/images/course/51/lesson/1551022636_image-20190224224013-5.png\" style=\"height:37px; width:516px\" /></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Nếu F(x) l&agrave; h&agrave;m khả vi th&igrave; </span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:168px\"><img src=\"/public/images/course/51/lesson/1551022636_image-20190224224013-6.png\" style=\"height:66px; width:259px\" /></p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Nếu </span><span style=\"font-size:16.0pt\">?</span><span style=\"font-size:16.0pt\"> l&agrave; một hằng số th&igrave; </span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:168px\"><img src=\"/public/images/course/51/lesson/1551022636_image-20190224224013-7.png\" style=\"height:66px; width:285px\" /></p>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Nếu f(x), g(x) đều c&oacute; nguy&ecirc;n h&agrave;m th&igrave; </span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:168px\"><img src=\"/public/images/course/51/lesson/1551022636_image-20190224224013-8.png\" style=\"height:66px; width:480px\" /></p>\r\n\r\n<ol start=\"5\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Nếu c&oacute; </span><em><span style=\"font-size:16.0pt\">&nbsp;</span></em></span></span><img src=\"/public/images/course/51/lesson/1551022636_image-20190224224013-9.png\" style=\"height:37px; width:247px\" /><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&nbsp;&nbsp;v&agrave;</span></span></span><img src=\"/public/images/course/51/lesson/1551022636_image-20190224224013-10.png\" style=\"height:36px; width:102px\" /><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&nbsp;th&igrave; </span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:168px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:16.0pt\">&nbsp;</span></em></span></span><img src=\"/public/images/course/51/lesson/1551022636_image-20190224224013-11.png\" style=\"height:37px; width:236px\" /><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;</span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<ol start=\"3\">\r\n			<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">Bảng c&ocirc;ng thức t&iacute;ch ph&acirc;n (gi&aacute;o tr&igrave;nh tr 194,195)</span></span></span></li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n	<li value=\"1\"><img src=\"/public/images/course/51/lesson/1551022636_image-20190224224013-12.png\" style=\"height:54px; width:286px\" /></li>\r\n	<li><img src=\"/public/images/course/51/lesson/1551022636_image-20190224224013-13.png\" style=\"height:51px; width:184px\" /></li>\r\n	<li><img src=\"/public/images/course/51/lesson/1551022636_image-20190224224013-14.png\" sty