INSERT INTO `lesson` VALUES ('185', 'câu 3', '', '1551184938', null, '154', '54', '1', '154', '154', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('193', 'Chương Mở đầu', '<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><u>CHƯƠNG MỞ ĐẦU</u></strong>: ĐỐI TƯỢNG, PHƯƠNG PH&Aacute;P NGHI&Ecirc;N CỨU V&Agrave;&Yacute; NGHĨA HỌC TẬP M&Ocirc;N TƯ TƯỞNG HỒ CH&Iacute; MINH</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"/public/images/course/55/lesson/1551236349_pic-md.jpg\" style=\"height:288px; width:700px\" /></span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify;\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><u><strong><span style=\"font-family:Times New Roman,Times,serif\">1. Đối tượng nghi&ecirc;n cứu</span></strong></u></span><br />\r\n	<strong><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">1.1. Kh&aacute;i niệm</span></span></strong><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Tư tưởng l&agrave; <strong>sự phản &aacute;nh hiện thực trong &yacute; thức</strong>, l&agrave; biểu hiện quan hệ của con người với thế giới chung quanh.<br />\r\n	- TTHCM l&agrave; một hệ <strong>thống quan điểm to&agrave;n diện v&agrave; s&acirc;u sắc</strong> về những vấn đề cơ bản của CMVN, <strong>từ CM DT d&acirc;n chủ ND đến CM XHCN</strong>; l&agrave; kết quả của <strong>sự vận dụng sang tạo v&agrave; ph&aacute;t triển CNMLN</strong> v&agrave;o điều kiện cụ thể nước ta; đồng thời l&agrave; <strong>sự kết tinh tinh hoa VH DT v&agrave; tr&iacute; tuệ thời đại</strong> nhằm GPDT, GPGC v&agrave; GPCN.</span></span><br />\r\n	<strong><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">1.2.&nbsp;Đối tượng nghi&ecirc;n cứu</span></span></strong><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Hệ thống c&aacute;c quan điểm, l&iacute; luận thể hiện trong di sản của HCM.<br />\r\n	- Qu&aacute; tr&igrave;nh vận động, hiện thực h&oacute;a c&aacute;c quan điểm, l&iacute; luận đ&oacute; v&agrave;o thực tiễn CMVN.</span></span><br />\r\n	<strong><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">1.3.&nbsp;Nhiệm vụ m&ocirc;n học</span></span></strong><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>- Cơ sở h&igrave;nh th&agrave;nh</strong> TTHCM.<br />\r\n	<strong>- C&aacute;c thời k&igrave; h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển</strong> của TTHCM.<br />\r\n	<strong>- Nội dung, bản chất CM, khoa học, đặc điểm</strong> của c&aacute;c quan điểm trong hệ thống TTHCM.<br />\r\n	<strong>- Vai tr&ograve; nền tảng tư tưởng</strong>, <strong>kim chỉ nam h&agrave;nh động</strong> của TTHCM&nbsp; đối với CMVN.<br />\r\n	<strong>- Qu&aacute; tr&igrave;nh nhận thức, vận dụng, ph&aacute;t triển</strong> TTHCM qua c&aacute;c giai đoạn CM của Đảng, Nh&agrave; nước ta.<br />\r\n	<strong>- C&aacute;c gi&aacute; trị tư tưởng, l&iacute; luận</strong> của HCM đối với kho tang tư tưởng, l&iacute; luận CM TG của thời đại</span></span></span></li>\r\n	<li style=\"text-align:justify;\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><u><strong><span style=\"font-family:Times New Roman,Times,serif\">2.Phương ph&aacute;p nghi&ecirc;n cứu</span></strong></u></span><br />\r\n	<strong><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">2.1.&nbsp;Cơ sở phương ph&aacute;p luận</span></span></strong><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Bảo đảm <strong>sự thống nhất</strong> nguy&ecirc;n tắc <strong>t&iacute;nh đảng v&agrave; t&iacute;nh khoa học</strong>.<br />\r\n	&nbsp; +&nbsp;Phải đứng tr&ecirc;n <strong>lập trường, quan điểm, phương ph&aacute;p luận</strong> <strong>CNMLN</strong> v&agrave; <strong>quan điểm, đường lối của ĐCSVN.&nbsp;</strong><br />\r\n	<strong>&nbsp; +&nbsp;</strong>Bảo đảm <strong>t&iacute;nh kh&aacute;ch quan</strong> khi ph&acirc;n t&iacute;ch, l&yacute; giải v&agrave; đ&aacute;nh gi&aacute; TTHCM.<br />\r\n	=&gt;&nbsp;T&iacute;nh đảng v&agrave; t&iacute;nh khoa học thống nhất với nhau trong <strong>phản &aacute;nh trung thực, kh&aacute;ch quan TTHCM</strong> tr&ecirc;n <strong>cơ sở lập trường, phương ph&aacute;p luận v&agrave; định hướng ch&iacute;nh trị</strong>.<br />\r\n	-&nbsp;Quan điểm thực tiễn v&agrave; nguy&ecirc;n tắc <strong>l&iacute; luận gắn liền với thực tiễn.<br />\r\n	&nbsp; +&nbsp;</strong>HCM lu&ocirc;n b&aacute;m s&aacute;t thực tiễn, coi trọng tổng kết thực tiễn để <strong>n&acirc;ng cao năng lực hoạt động thực tiễn v&agrave; tr&igrave;nh độ l&yacute; luận,</strong> <strong>kết hợp l&yacute; luận vs thực tiễn</strong>, lu&ocirc;n xuất ph&aacute;t từ thực tiễn, vận dụng s&aacute;ng tạo v&agrave; ph&aacute;t triển CNMLN để đề ra đường lối CM đ&uacute;ng đắn.<br />\r\n	&nbsp; +&nbsp;Nghi&ecirc;n cứu, học tập TTHCM cần qu&aacute;n triệt quan điểm <strong>l&yacute; luận gắn liền với thực tiễn</strong>, học đi đ&ocirc;i vs h&agrave;nh, biết <strong>vận dụng kiến thức</strong> đ&atilde; học <strong>v&agrave;o thực tiễn</strong>, phục vụ sự nghiệp CM của đất nước.<br />\r\n	-&nbsp;Quan điểm <strong>lịch sử - cụ thể</strong>.<br />\r\n	&nbsp; +&nbsp;Nghi&ecirc;n cứu TTHCM phải <strong>xem x&eacute;t</strong> c&aacute;c <strong>quan điểm của Người</strong> đ&atilde; xuất hiện trong lịch sử như n&agrave;o, đ&atilde; trải qua những giai đoạn ph&aacute;t triển chủ yếu n&agrave;o v&agrave; hiện nay trở th&agrave;nh như thế n&agrave;o.<br />\r\n	&nbsp; +&nbsp;Khi học tập nghi&ecirc;n cứu cần <strong>nắm vững quan điểm lịch sử - cụ thể</strong> mới gi&uacute;p ch&uacute;ng ta nhận thức được bản chất của TTHCM.<br />\r\n	-&nbsp;Quan điểm <strong>to&agrave;n diện v&agrave; hệ thống</strong>.<br />\r\n	&nbsp; +&nbsp;<strong>Nắm vững v&agrave; đầy đủ</strong> hệ thống c&aacute;c quan điểm trong TTHCM.<br />\r\n	&nbsp; +&nbsp;Qu&aacute;n triệt <strong>mối li&ecirc;n hệ qua lại giữa c&aacute;c yếu tố, c&aacute;c nội dung trong hệ thống.</strong><br />\r\n	&nbsp; +&nbsp;Hạt nh&acirc;n cốt l&otilde;i l&agrave; <strong>tư tưởng độc lập, tự do, d&acirc;n chủ v&agrave; CNXH.</strong><br />\r\n	-&nbsp;Quan điểm <strong>kế thừa v&agrave; ph&aacute;t triển.<br />\r\n	&nbsp; +&nbsp;</strong>HCM l&agrave; <strong>một mẫu mực</strong> về sự vận dụng, ph&aacute;t triển CNMLN v&agrave;o điều kiện cụ thể của VN .<br />\r\n	&nbsp; +&nbsp;Người đ&atilde; <strong>bổ sung, ph&aacute;t triển s&aacute;ng tạo CNMLN</strong> tr&ecirc;n nhiều lĩnh vực, h&igrave;nh th&agrave;nh hệ thống c&aacute;c luận điểm l&yacute; luận mới mẻ.<br />\r\n	&nbsp; +&nbsp;Khi nghi&ecirc;n cứu, học tập ko chỉ kế thừa, vận dụng m&agrave; c&ograve;n phải <strong>ph&aacute;t triển s&aacute;ng tạo</strong> TTHCM trong điều kiện lịch sử mới.<br />\r\n	-&nbsp;Kết hợp <strong>nghi&ecirc;n cứu t&aacute;c phẩm với thực tiễn chỉ đạo CM</strong> của HCM.<br />\r\n	&nbsp; +&nbsp;HCM <strong>l&agrave; 1 nh&agrave; l&yacute; luận - thực tiễn</strong>, người x&acirc;y dựng l&yacute; luận, vạch cương lĩnh, - đường lối v&agrave; trực tiếp tổ chức, l&atilde;nh đạo thực hiện trong thực tiễn.<br />\r\n	&nbsp; +&nbsp;Cần <strong>nghi&ecirc;n cứu c&aacute;c t&aacute;c phẩm v&agrave; hoạt động thực tiễn của HCM</strong>; nghi&ecirc;n cứu thực tiễn CM dưới sự tổ chức, l&atilde;nh đạo của Đảng do Người đứng đầu.<br />\r\n	-&nbsp;Kết quả hoạt động thực tiễn ch&iacute;nh l&agrave; lời giải thich r&otilde; r&agrave;ng về gi&aacute; trị KH của TTHCM.</span></span><br />\r\n	<strong><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">2.2.&nbsp;Phương ph&aacute;p cụ thể</span></span></strong><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Phương ph&aacute;p logic v&agrave; phương ph&aacute;p lịch sử.<br />\r\n	-&nbsp;Phương ph&aacute;p li&ecirc;n ng&agrave;nh.<br />\r\n	-&nbsp;VD: ph&acirc;n t&iacute;ch, tổng hợp, so s&aacute;nh, điều tra điền d&atilde;, phỏng vấn nh&acirc;n chứng lịch sử,&hellip;</span></span></span></li>\r\n	<li style=\"text-align:justify;\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><u><strong><span style=\"font-family:Times New Roman,Times,serif\">3.&Yacute; nghĩa học tập</span></strong></u></span><br />\r\n	<strong><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">3.1.&nbsp;N&acirc;ng cao năng lực tư duy l&iacute; luận v&agrave; phương ph&aacute;p c&ocirc;ng t&aacute;c</span></span></strong><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;N&acirc;ng cao nhận thức về <strong>vai tr&ograve;, vị tr&iacute; của TTHCM</strong>, l&agrave;m cho TTHCM &nbsp;<strong>giữ vai tr&ograve; chủ đạo trong đời sống tinh thần</strong> của thế hệ trẻ.<br />\r\n	-&nbsp;<strong>Bồi dưỡng, củng cố </strong>cho SV <strong>lập <span style=\"display:none\">&nbsp;</span>trường, quan điểm CM</strong>; ki&ecirc;n định mục ti&ecirc;u <strong>độc lập DT gắn với CNXH.</strong><br />\r\n	-&nbsp;Gi&uacute;p SV t&iacute;ch cực, chủ động trong <strong>đấu tranh ph&ecirc; ph&aacute;n</strong> những quan điểm sai tr&aacute;i, <strong>bảo vệ CNMLN, TTHCM v&agrave; đường lối, ch&iacute;nh s&aacute;ch của Đảng v&agrave; Nh&agrave; nước.</strong></span></span><br />\r\n	<strong><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">3.2.&nbsp;Bồi dưỡng phẩm chất đạo đức CM v&agrave; r&egrave;n luyện bản lĩnh ch&iacute;nh trị</span></span></strong><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;N&acirc;ng cao l&ograve;ng tự h&agrave;o về <strong>&ldquo;Anh h&ugrave;ng d&acirc;n tộc&rdquo;, &ldquo;Nh&agrave; văn h&oacute;a kiệt xuất&rdquo;</strong>, từ đ&oacute; &ldquo;Sống, chiến đấu v&agrave; lao động theo gương B&aacute;c Hồ vĩ đại&rdquo;.<br />\r\n	-&nbsp;Ph&aacute;t triền <strong>niềm tự h&agrave;o DT</strong>, c&oacute; <strong>niềm tin v&agrave;o Đảng</strong> trong tương lai.<br />\r\n	-&nbsp;Tu dưỡng, r&egrave;n luyện bản th&acirc;n để <strong>ho&agrave;n th&agrave;nh tốt nhiệm vụ của m&igrave;nh</strong>, c&oacute; <strong>sự đ&oacute;ng g&oacute;p thiết thực, hiệu quả</strong> v&agrave;o sự nghiệp CM theo con đường HCM v&agrave; Đảng đ&atilde; chọn</span></span></span></li>\r\n</ol>', '1551234838', null, '0', '55', '1', '0', null, '0', null, null, null, null, null, null, '/images/course/55/lesson/1551235567_pic-md.jpg', null);
INSERT INTO `lesson` VALUES ('194', 'Chương 1', '<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em><u>CHƯƠNG 1</u></em></strong>: CƠ SỞ, QU&Aacute; TR&Igrave;NH H&Igrave;NH TH&Agrave;NH V&Agrave; PH&Aacute;T TRIỂN TTHCM.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"/public/images/course/55/lesson/1551236313_pic-1.jpg\" style=\"height:362px; width:700px\" /></span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:left;\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>1. Cơ sở h&igrave;nh th&agrave;nh TTHCM</strong></u></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1.1.&nbsp;Cơ sở kh&aacute;ch quan</strong></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">1.1.1.&nbsp;Bối cảnh lịch sử</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Bối cảnh lịch sử VN cuối thế kỉ XIX dầu thế kỉ XX.<br />\r\n	&nbsp; +&nbsp;<strong>C&aacute;c m&acirc;u thuẫn trong XH</strong> ng&agrave;y c&agrave;ng gay gắt.<br />\r\n	&nbsp; +&nbsp;VN từ một nước PK độc lập trở th&agrave;nh nước <strong>thuộc địa nửa PK.</strong><br />\r\n	&nbsp; +&nbsp;<strong>Phong tr&agrave;o đấu tranh GPDT</strong> dấy l&ecirc;n mạnh mẽ nhưng đều <strong>thất bại</strong>.<br />\r\n	-&nbsp;Bối cảnh thời đại<br />\r\n	&nbsp; +&nbsp;<strong>CNTB</strong> chuyển sang giai đoạn <strong>đế quốc chủ nghĩa</strong>, trở th&agrave;nh <strong>kẻ th&ugrave; chung</strong> của c&aacute;c DT thuộc địa v&agrave; GCCN TG.<br />\r\n	&nbsp;&nbsp;+&nbsp;<strong>Thắng lợi CMT10 Nga</strong> mở ra thời đại CM chống đế quốc, GPDT.<br />\r\n	&nbsp; +&nbsp;<strong>Sự ra đời của Quốc tế Cộng sản</strong> gắn kết PTCN với PT GPDT trong cuộc đấu tranh chống kẻ th&ugrave; chung.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">1.1.2.&nbsp;Những tiền đề tư tưởng &ndash; l&iacute; luận</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Gi&aacute; trị truyền thống của DT VN.<br />\r\n	-&nbsp;Tinh hoa văn h&oacute;a nh&acirc;n loại.<br />\r\n	-&nbsp;Chủ nghĩa M&aacute;c &ndash; L&ecirc;nin<br />\r\n	=&gt;&nbsp;Chủ nghĩa M&aacute;c &ndash; L&ecirc;nin l&agrave; <strong>nh&acirc;n tố quyết định trực tiếp</strong> đến bản chất CM v&agrave; khoa học của TTCM.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1.2.&nbsp;Nh&acirc;n tố chủ quan.</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Khả năng tư duy v&agrave; tr&iacute; tuệ của HCM.<br />\r\n	-&nbsp;Phẩm chất đạo đức, năng lực hoạt động thực tiễn.</span></span></span></li>\r\n	<li style=\"text-align:left\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>2. Qu&aacute; tr&igrave;nh h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển TTHCM</strong></u></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Trước năm 1911: <strong>H&igrave;nh th&agrave;nh</strong> tư tưởng y&ecirc;u nước v&agrave; ch&iacute; hướng cứu nước.<br />\r\n	-&nbsp;Từ 1911-1920: <strong>T&igrave;m thấy con đường cứu nước</strong>, GPDT.<br />\r\n	-&nbsp;Từ 1921-1930: H&igrave;nh th&agrave;nh cơ bản <strong>tư tưởng về CMVN.</strong><br />\r\n	-&nbsp;Từ 1930-1945: Vượt qua thử th&aacute;ch, ki&ecirc;n tr&igrave; <strong>giữ vững lập trường CM.</strong><br />\r\n	-&nbsp;Từ 1945-1969: TTHCM tiếp tục <strong>ph&aacute;t triển v&agrave; ho&agrave;n thiện</strong>.</span></span></span></li>\r\n	<li style=\"text-align:left;\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>3. Gi&aacute; trị TTHCM</strong></u></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>3.1.&nbsp;TTHCM soi sang con đường giải ph&oacute;ng v&agrave; ph&aacute;t triển DT.</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;L&agrave; <strong>t&agrave;i sản tinh thần v&ocirc; gi&aacute;</strong> của DTVN.<br />\r\n	&nbsp; +&nbsp;L&agrave; <strong>sản phẩm của DT v&agrave; thời đại</strong>, trường tồn bất diệt c&ugrave;ng DT.<br />\r\n	&nbsp; +&nbsp;<strong>Được kiểm nghiệm trong thực tiễn</strong>, tiếp tục soi s&aacute;ng v&agrave; đảm bảo cho tương lai, tiền đ&ograve; của DT.<br />\r\n	&nbsp; +&nbsp;N&eacute;t đặc sắc nhất trong TTHCM l&agrave; <strong>quan điểm về GPDT </strong>v&agrave; <strong>con đường ph&aacute;t triển của DT.</strong><br />\r\n	-&nbsp;<strong>Nền tảng tư tưởng</strong>, <strong>kim chỉ nam h&agrave;nh động</strong> của TTHCM&nbsp; đối với CMVN.<br />\r\n	&nbsp; +Soi sang con đường thực hiện mục ti&ecirc;u: <strong>d&acirc;n gi&agrave;u, nước mạnh, XH c&ocirc;ng bằng, d&acirc;n chủ, văn minh.</strong><br />\r\n	&nbsp; +&nbsp;L&agrave; <strong>ngọn cờ dẫn dắt CMVN</strong> đi từ thắng lợi n&agrave;y đến thắng lợi kh&aacute;c.<br />\r\n	&nbsp; +&nbsp;L&agrave; <strong>nền tảng</strong> để Đảng vạch ra <strong>đường lối CM</strong> đ&uacute;ng đắn, l&agrave; <strong>sợi chỉ đỏ</strong> dẫn dắt <strong>thắng lợi của DT.</strong></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>3.2.&nbsp;TTHCM đối với sự ph&aacute;t triển thế giới</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Phản &aacute;nh <strong>kh&aacute;t vọng độc lập tư do</strong> của c&aacute;c DT bị &aacute;p bức.<br />\r\n	-&nbsp;T&igrave;m ra c&aacute;c <strong>giải ph&aacute;p đấu tranh</strong> GP lo&agrave;i người.<br />\r\n	&nbsp; +&nbsp;X&aacute;c định đ&uacute;ng <strong>con đường cứu nước</strong> v&agrave; c&oacute; <strong>phương ph&aacute;p thức tỉnh</strong> c&aacute;c DT thuộc địa đứng l&ecirc;n tự GP.<br />\r\n	&nbsp; +&nbsp;Nhận thức đ&uacute;ng <strong>sự chuyển biến của thời đại</strong>, đề ra đường lối, chiến lược, s&aacute;ch lược đ&uacute;ng đắn.<br />\r\n	-&nbsp;<strong>Cổ vũ c&aacute;c DT đấu tranh</strong> v&igrave; mục đ&iacute;ch cao cả.</span></span></span></li>\r\n</ol>', '1551234838', null, '0', '55', '1', '0', null, '0', null, null, null, null, null, null, '/images/course/55/lesson/1551236326_pic-1.jpg', null);
INSERT INTO `lesson` VALUES ('195', 'Chương 2', '<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em><u>CHƯƠNG 2</u></em></strong>: TƯ TƯỞNG HỒ CH&Iacute; MINH VỀ VẤN ĐỀ D&Acirc;N TỘC V&Agrave;</span></span><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">C&Aacute;CH MẠNG GIẢI PH&Oacute;NG D&Acirc;N TỘC.</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"/public/images/course/55/lesson/1551238543_pic-2.jpg\" style=\"height:377px; width:700px\" /></span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:left;\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>1. TTHCM về vấn đề DT</strong></u></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1.1.&nbsp;Vấn đề DT thuộc địa</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Thực chất của vấn đề DT thuộc địa<br />\r\n	&nbsp; +&nbsp;Đấu tranh <strong>chống CNTD, GPDT, gi&agrave;nh độc lập DT</strong>, thực hiện <strong>quyền DT tự quyết</strong>, th&agrave;nh lập <strong>Nh&agrave; nước độc lập.</strong><br />\r\n	&nbsp; +&nbsp;Lựa chọn <strong>con đường ph&aacute;t triển DT</strong>: trong bối cảnh thời đại mới l&agrave; <strong>CNXH.</strong><br />\r\n	-&nbsp;C&aacute;ch tiếp cận của HCM: từ <strong>quyền con người</strong> được n&ecirc;u trong:<br />\r\n	&nbsp; +&nbsp;<strong>Tuy&ecirc;n ng&ocirc;n</strong> <strong>độc lập Mỹ 1976</strong>: tất cả mọi người sinh ra đều c&oacute; <strong>quyền b&igrave;nh đẳng.</strong><br />\r\n	&nbsp; +&nbsp;<strong>Tuy&ecirc;n ng&ocirc;n nh&acirc;n quyền v&agrave; d&acirc;n quyền Ph&aacute;p 1791</strong>: người ta sinh ra được tự do v&agrave; b&igrave;nh đẳng về quyền lợi, v&agrave; phải <strong>lu&ocirc;n lu&ocirc;n được tự do v&agrave; b&igrave;nh đẳng về quyền lợi.</strong><br />\r\n	=&gt;&nbsp;HCM đ&atilde; kh&aacute;i qu&aacute;t l&ecirc;n th&agrave;nh <strong>vấn đề DT</strong>: tất cả c&aacute;c DT sinh ra tr&ecirc;n TG đều c&oacute; quyền b&igrave;nh đẳng, <strong>DT n&agrave;o cũng c&oacute; quyền sống, quyền sung sướng, quyền tự do.</strong><br />\r\n	-&nbsp;Nội dung của Độc lập DT:<br />\r\n	&nbsp; +&nbsp;Độc lập, tự do l&agrave; <strong>kh&aacute;t vọng lớn nhất</strong> của c&aacute;c DT thuộc địa.<br />\r\n	&nbsp; +&nbsp;Độc lập DT phải l&agrave; <strong>một nền độc lập thực sự</strong>, ho&agrave;n to&agrave;n gắn với h&ograve;a b&igrave;nh, thống nhất, to&agrave;n vẹn l&atilde;nh thổ.<br />\r\n	&nbsp; +&nbsp;Độc lập DT phải mang lại <strong>cơm no &aacute;o ấm</strong> cho người d&acirc;n.<br />\r\n	&nbsp; +&nbsp;Độc lập, tự do l&agrave; mục ti&ecirc;u chiến đấu, l&agrave; nguồn sức mạnh l&agrave;m n&ecirc;n chiến thắng của DT VN trong thế kỉ XX, <strong>l&agrave; kh&aacute;t vọng trong suốt cuộc đời hoạt động CM của HCM</strong>.<br />\r\n	-&nbsp;Chủ nghĩa y&ecirc;u nước ch&acirc;n ch&iacute;nh &ndash; một động lực lớn của đất nước<br />\r\n	&nbsp; +&nbsp;HCM thấy r&otilde; <strong>sức mạnh của CNYN</strong> ở c&aacute;c DT thuộc địa.<br />\r\n	&nbsp; +&nbsp;CNYN ch&acirc;n ch&iacute;nh l&agrave; <strong>một bộ phận của tinh thần quốc tế</strong>.<br />\r\n	&nbsp; +&nbsp;HCM đề nghị những người Cộng sản phải <strong>nắm bắt v&agrave; ph&aacute;t huy</strong> sức mạnh của CNYN.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1.2.&nbsp;Mối quan hệ giữa vấn đề DT v&agrave; vấn đề GC.</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Vấn đề DT v&agrave; vấn đề GC c&oacute; <strong>quan hệ chặt chẽ</strong> với nhau.<br />\r\n	&nbsp; +&nbsp;HCM <strong>lu&ocirc;n đứng tr&ecirc;n vấn đề GC</strong> để nhận thức v&agrave; giải quyết vấn đề DT.<br />\r\n	&nbsp; +&nbsp;HCM <strong>khẳng định vai tr&ograve; lịch sử của GCCN</strong> v&agrave; quyền l&atilde;nh đạo duy nhất của ĐCS trong qu&aacute; tr&igrave;nh CMVN, sử dụng bạo lực CM quần ch&uacute;ng để chống lại bạo lực phản CM của kẻ th&ugrave;; thiết lập ch&iacute;nh quyền Nh&agrave; nước của d&acirc;n, do d&acirc;n, v&igrave; d&acirc;n; gắn kết mục ti&ecirc;u độc lập DT với CNXH.<br />\r\n	-&nbsp;<strong>GPDT l&agrave; vấn đề tr&ecirc;n hết</strong>, trước hết, <strong>ĐLDT gắn liền với CNXH</strong>.<br />\r\n	&nbsp; +&nbsp;HCM lu&ocirc;n gắn <strong>DT với GC, DT với quốc tế, độc lập DT với CNXH.</strong><br />\r\n	&nbsp; +&nbsp;TTHCM phản &aacute;nh q<strong>uy luật kh&aacute;ch quan</strong> của sự nghiệp GPDT trong thời đại CNĐQ,&nbsp;<strong>mối quan hệ khăng kh&iacute;t</strong> giữa GPDT với GPGC v&agrave; GP con người.<br />\r\n	-&nbsp;GPDT tạo <strong>tiền đề</strong> để GPGC.<br />\r\n	&nbsp; +&nbsp;HCM giải quyết vấn đề DT theo qđiểm GC, đồng thời đặt vấn đề GC trong vấn đề DT.<br />\r\n	&nbsp; +&nbsp;<strong>GPDT l&agrave; điều kiện để GPGC</strong>, lợi &iacute;ch của GC phải phục t&ugrave;ng lợi &iacute;ch DT.<br />\r\n	-&nbsp;<strong>Đấu tranh</strong> cho <strong>độc lập của DT m&igrave;nh</strong>, đồng thời <strong>t&ocirc;n trọng độc lập của DT kh&aacute;c</strong>.<br />\r\n	&nbsp; +&nbsp;HCM kh&ocirc;ng chỉ đấu tranh cho DTVN m&agrave; c&ograve;n đấu tranh cho c&aacute;c DT bị &aacute;p bức.<br />\r\n	&nbsp; +&nbsp;HCM gi&uacute;p đỡ c&aacute;c DT kh&aacute;c tr&ecirc;n TG với phương ch&acirc;m <strong>&ldquo;gi&uacute;p bạn l&agrave; tự gi&uacute;p m&igrave;nh&rdquo;</strong>.<br />\r\n	&nbsp; +&nbsp;HCM chủ trương phải bằng <strong>CM thắng lợi của mỗi nước</strong> m&agrave; đ&oacute;ng g&oacute;p v&agrave;o <strong>thắng lợi chung của CMTG</strong>.</span></span></span></li>\r\n	<li style=\"text-align:left;\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>2. TTHCM về CM GPDT</strong></u></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>2.1.&nbsp;T&iacute;nh chất, nhiệm vụ v&agrave; mục ti&ecirc;u CMGPDT</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;<strong>M&acirc;u thuẫn</strong> trong XH phương đ&ocirc;ng: <strong>DT thuộc địa v&agrave; CNĐQ.</strong><br />\r\n	-&nbsp;T&iacute;nh chất: đấu tranh <strong>GPDT</strong>.<br />\r\n	-&nbsp;Đối tượng: <strong>CNTD v&agrave; tay sai</strong>.<br />\r\n	-&nbsp;Nhiệm vụ h&agrave;ng đầu: <strong>chống đế quốc, gi&agrave;nh ĐLDT</strong>.<br />\r\n	-&nbsp;Mục ti&ecirc;u cấp thiết: l&agrave; <strong>quyền lợi chung</strong> của DT.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>2.2.&nbsp;CMGPDT muốn thắng lợi phải đi theo con đường CMVS</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;B&agrave;i học từ <strong>sự thất bại </strong>của con đường cứ nước <strong>trước HCM</strong>.<br />\r\n	-&nbsp;CM d&acirc;n chủ tư sản <strong>kh&ocirc;ng triệt để</strong>.<br />\r\n	-&nbsp;<strong>Thắng lợi của CMT10 Nga</strong> v&agrave; <strong>sự ra đời của QTCS</strong>.<br />\r\n	=&gt;&nbsp;Con đường CMVS.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>2.3.&nbsp;CMGPDT trong thời đại mới phải do ĐCS l&atilde;nh đạo</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;CM cần phải c&oacute; Đảng l&atilde;nh đạo để <strong>đề ra đường lối, tập hợp lực lượng, tổ chức đấu tranh</strong> gi&agrave;nh ch&iacute;nh quyền.<br />\r\n	- Đảng lấy <strong>CNMLN l&agrave;m cốt</strong> v&agrave; phải được <strong>tổ chức chặt chẽ</strong>, <strong>kỉ luật nghi&ecirc;m minh</strong> v&agrave; <strong>mật thiết li&ecirc;n lạc với quần ch&uacute;ng</strong>.<br />\r\n	-&nbsp;ĐCSVN l&agrave; <strong>Đảng của GCCN, NDLĐ v&agrave; to&agrave;n DT</strong>, l&agrave; nh&acirc;n tố h&agrave;ng đầu đảm bảo cho thắng lợi của CM.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>2.4.&nbsp;Lực lượng của CMGPDT bao gồm to&agrave;n DT</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;HCM đ&aacute;nh gi&aacute; cao <strong>vai tr&ograve; của quần ch&uacute;ng</strong> trong đấu tranh CM.<br />\r\n	-&nbsp;Người đ&aacute;nh gi&aacute; cao vai tr&ograve; của <strong>khởi nghĩa vũ trang</strong>: quần ch&uacute;ng lao động l&agrave; <strong>người bị &aacute;p bức</strong> n&ecirc;n c&oacute; khả năng tham gia CM. Sức mạnh của họ l&agrave; <strong>nh&acirc;n tố then chốt</strong> cho thắng lợi CM.<br />\r\n	-&nbsp;<strong>Ph&ecirc; ph&aacute;n</strong> việc lấy <strong>&aacute;m s&aacute;t c&aacute; nh&acirc;n, bạo động non</strong> l&agrave; phướng thức h&agrave;nh động CM.<br />\r\n	-&nbsp;Lực lượng của CMGPDT bao gồm <strong>to&agrave;n DT</strong>:<br />\r\n	&nbsp; +&nbsp;Đại bộ phận GCCN, GCND, tiểu TS, TS,&hellip; đi v&agrave;o phe <strong>v&ocirc; sản.</strong><br />\r\n	&nbsp; +&nbsp;Ph&uacute; n&ocirc;ng, trung, tiểu địa chủ, tư bản An Nam <strong>chưa r&otilde; mặt phản CN th&igrave; phải lợi dụng</strong>, &iacute;t l&acirc;u cho họ <strong>đứng trung lập</strong>.<br />\r\n	&nbsp; +&nbsp;Bộ phận n&agrave;o <strong>phản CM </strong>th&igrave; phải <strong>đ&aacute;nh đổ</strong>.<br />\r\n	=&gt;&nbsp;Vai tr&ograve; động lực CM của GCCN v&agrave; GCND.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>2.5.&nbsp;CMGPDT cần được tiến h&agrave;nh chủ động s&aacute;ng tạo v&agrave; c&oacute; khả năng gi&agrave;nh thắng lợi trước CMVS ở ch&iacute;nh quốc.</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Quan điểm của <strong>quốc tế CS</strong>:<br />\r\n	&nbsp; + Chưa quan t&acirc;m đ&uacute;ng mức đến CM thuộc địa.<br />\r\n	&nbsp; +&nbsp;Xem thắng lợi của CM thuộc địa <strong>phụ thuộc</strong> v&agrave;o CMVS ở ch&iacute;nh quốc.<br />\r\n	-&nbsp;Quan điểm của HCM:<br />\r\n	&nbsp; +&nbsp;Nội dung:<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;CMGPDT cần được tiến h&agrave;nh <strong>chủ động, s&aacute;ng tạo</strong>.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;<strong>CMGPDT</strong> ở thuộc địa v&agrave; <strong>CMVS</strong> ở ch&iacute;nh quốc <strong>c&oacute; mối quan hệ mật thiết</strong> với nhau, c&ugrave;ng chung 1 mục ti&ecirc;u l&agrave; CN đế quốc.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;CMGPDT c&oacute; <strong>khả năng gi&agrave;nh thắng lợi trước CMVS</strong> ở ch&iacute;nh quốc.&nbsp;<br />\r\n	+&nbsp;Cơ sở:<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;Khi <strong>TBCN chuyển sang</strong> giai đoạn <strong>đế quốc CN</strong> th&igrave; một trong những <strong>nguồn sống</strong> của n&oacute; l&agrave; c&aacute;c nước <strong>thuộc địa</strong>. Đ&oacute; l&agrave; nơi CNTB lấy nguy&ecirc;n liệu cho c&aacute;c nh&agrave; m&aacute;y của n&oacute;, nơi n&oacute; đầu tư, ti&ecirc;u thụ. Nọc độc v&agrave; sức sống của con rắn độc TBCN đang tập trung ở c&aacute;c thuộc địa.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;CM thuộc địa c&oacute; tầm quan trọng đặc biệt, <strong>ND c&aacute;c DT thuộc địa</strong> <strong>c&oacute; khả năng CM to lớn</strong>. Theo HCM, phải &ldquo; L&agrave;m cho c&aacute;c d&acirc;n tộc thuộc địa, từ trc đến nay vẫn c&aacute;ch biệt nhau, hiểu biết nhau hơn v&agrave; đo&agrave;n kết lại để đặt cơ sở cho 1 Li&ecirc;n minh phương Đ&ocirc;ng tương lai, khối li&ecirc;n minh n&agrave;y sẽ l&agrave; 1 trong những c&aacute;i c&aacute;nh của CMVS&rdquo;.<br />\r\n	=&gt;&nbsp;Đ&acirc;y l&agrave; một <strong>LĐ s&aacute;ng tạo, c&oacute; gi&aacute; trị v&agrave; thực tiễn to lớn</strong>. Quan điểm n&agrave;y kh&aacute;c ho&agrave;n to&agrave;n với quan điểm của quốc tế CS đ&atilde; đ&oacute;ng g&oacute;p v&agrave;o kho t&agrave;ng l&iacute; luận của CMTG. Luận điểm n&agrave;y đ&atilde; được thẳng lợi của phong tr&agrave;o CMGPDT tr&ecirc;n to&agrave;n thế giới trong gần một thế kỷ qua chứng minh l&agrave; ho&agrave;n to&agrave;n đ&uacute;ng đắn.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>2.6.&nbsp;CMGPDT phải được tiến h&agrave;nh bằng con đường CM bạo lực</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;T&iacute;nh tất yếu của bạo lực CM:<br />\r\n	&nbsp; +&nbsp;<strong>Kẻ th&ugrave; SD bạo lực để x&acirc;m lược</strong> đất nước ta n&ecirc;n con đường để gi&agrave;nh v&agrave; giữ được độc lập DT chỉ c&oacute; thể l&agrave; CM bạo lực.<br />\r\n	&nbsp; +&nbsp;<strong>Bạo lực CM l&agrave; bạo lực của quần ch&uacute;ng</strong> bao gồm: lực lượng ch&iacute;nh trị quần ch&uacute;ng v&agrave; lực lượng vũ trang ND, t&ugrave;y từng điều kiện m&agrave; SD h&iacute;nh thức đấu tranh ph&ugrave; hợp.<br />\r\n	-&nbsp;Bạo lực CM gắn b&oacute; hữu cơ với nh&acirc;n đạo h&ograve;a b&igrave;nh:<br />\r\n	&nbsp; +&nbsp;HCM lu&ocirc;n <strong>tranh thủ</strong> khả năng <strong>giải quyết xung đột</strong> bằng <strong>đ&agrave;m ph&aacute;n</strong> h&ograve;a b&igrave;nh.<br />\r\n	&nbsp; +&nbsp;<strong>Chiến tranh</strong> chỉ l&agrave; <strong>giải ph&aacute;p</strong> bắt buộc <strong>cuối c&ugrave;ng.</strong><br />\r\n	&nbsp; +&nbsp;<strong>Kết hợp</strong> gi&agrave;nh thắng lợi <strong>qu&acirc;n sự </strong>với giải ph&aacute;p <strong>ngoại giao</strong> để kết th&uacute;c chiến tranh.<br />\r\n	-&nbsp;H&igrave;nh th&aacute;i của bao lực CM:<br />\r\n	&nbsp; +&nbsp;<strong>Khởi nghĩa to&agrave;n d&acirc;n v&agrave; chiến tranh ND</strong> l&agrave; n&eacute;t đặc sắc trong TTHCM.<br />\r\n	&nbsp; +&nbsp;Kết hợp c&aacute;c h&igrave;nh th&aacute;i của bạo lực CM, qu&acirc;n sự, ngoại giao, KT, VH, tư tưởng.<br />\r\n	&nbsp; +&nbsp;<strong>Tự lực c&aacute;nh sinh,</strong> độc lập tự chủ, tranh thủ sự gi&uacute;p đỡ của bạn b&egrave; quốc tế.</span></span></span></li>\r\n</ol>', '1551234838', null, '0', '55', '1', '0', null, '0', null, null, null, null, null, null, '/images/course/55/lesson/1551238578_pic-2.jpg', null);
INSERT INTO `lesson` VALUES ('196', 'Chương 3', '<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><u>CHƯƠNG 3</u></strong><u>:</u> TƯ TƯỞNG HỒ CH&Iacute; MINH VỀ CHỦ NGHĨA X&Atilde; HỘI V&Agrave;&nbsp;</span></span><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">CON ĐƯỜNG QU&Aacute; ĐỘ L&Ecirc;N CHỦ NGHĨA&nbsp;X&Atilde; HỘI Ở VIỆT NAM</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"display:none\">&nbsp;</span><img alt=\"\" src=\"/public/images/course/55/lesson/1551239648_pic-3.jpg\" style=\"height:290px; width:700px\" /></span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:left;\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>1. Tư tưởng Hồ Ch&iacute; Minh về chủ nghĩa x&atilde; hội ở Việt Nam</strong></u></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1.1.&nbsp;Quan điểm của HCM về t&iacute;nh tất yếu của CNXH ở VN</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;<strong>Ph&ugrave; hợp với qu&aacute; tr&igrave;nh tiến h&oacute;a v&agrave; ph&aacute;t triển của lo&agrave;i người</strong><br />\r\n	HCM đ&atilde; tiếp thu vận dụng s&aacute;ng tạo <strong>CNMLN về sự ph&aacute;t triển tất yếu của x&atilde; hội lo&agrave;i người </strong>theo c&aacute;c h&igrave;nh th&aacute;i KT-XH: Tiến l&ecirc;n CNXH l&agrave; <strong>bước ph&aacute;t triển tất yếu ở VN</strong> sau khi nước nh&agrave; đ&atilde; gi&agrave;nh được độc lập theo con đường CM v&ocirc; sản.<br />\r\n	-&nbsp;<strong>Ph&ugrave; hợp với mục ti&ecirc;u CM v&agrave; ph&aacute;t triển của to&agrave;n d&acirc;n</strong><br />\r\n	Mục ti&ecirc;u GPDT theo con đường CM v&ocirc; sản m&agrave; HCM đ&atilde; lựa chọn cho DTVN l&agrave; nước nh&agrave; được độc lập, ND được hưởng cuộc sống ấm no, tự do, hạnh ph&uacute;c.Khi gi&agrave;nh được độc lập DT, ND ta sẽ XD một XH mới - XHCN.<br />\r\n	-&nbsp;<strong>Chỉ c&oacute; CNXH mới đảm bảo vững chắc độc lập DT</strong><br />\r\n	<strong>Độc lập DT v&agrave; CNXH gắn liền với nhau.</strong> Độc lập DT l&agrave; tiền đề l&agrave; điều kiện ti&ecirc;n quyết để XD CNXH: XD th&agrave;nh c&ocirc;ng CNXH l&agrave; một điều kiện bảo đảm vững chắc, đồng thời l&agrave; mục ti&ecirc;u cho độc lập DT hướng tới. Thực tế chứng minh con đường đ&oacute; l&agrave; tất yếu, duy nhất đ&uacute;ng, ph&ugrave; hợp với điều kiện tất yếu ở VN v&agrave; xu thế thời đại. </span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1.2.&nbsp;Quan điểm của HCM về đặc trưng bản chất của CNXH ở Việt Nam</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;C&aacute;ch tiếp cận của HCM về CNXH<br />\r\n	&nbsp; +&nbsp;Tiếp thu l&iacute; luận MLN từ y&ecirc;u cầu tất yếu của c&ocirc;ng cuộc GPDT VN. Người t&igrave;m thấy trong l&iacute; luận MLN <strong>sự thống nhất biện chứng</strong>&nbsp;của GPDT,&nbsp;GPXH, GP con người. Đ&oacute; cũng l&agrave; <strong>mục&nbsp;ti&ecirc;u cuối&nbsp;c&ugrave;ng</strong> của CNCS theo&nbsp;đ&uacute;ng bản chất của CNMLN.<br />\r\n	&nbsp; +&nbsp;Tiếp cận CNXH ở đạo đức: hướng tới <strong>gi&aacute; trị nh&acirc;n đạo, nh&acirc;n văn m&aacute;c-x&iacute;t</strong>. <span style=\"background-color:white\">Sự ph&aacute;t triển tự do của mỗi người l&agrave; điều kiện cho sự ph&aacute;t triển tự do của tất cả mọi người.</span><br />\r\n	&nbsp; +&nbsp;Tiếp cận CNXH về VH: <span style=\"background-color:white\">Qu&aacute; tr&igrave;nh XD XHCN ở VN cũng l&agrave; qu&aacute; tr&igrave;nh XD một nền VH m&agrave; trong đ&oacute; kết tinh, kế thừa, ph&aacute;t triển những gi&aacute; trị truyền thống VH tốt đẹp h&agrave;ng ng&agrave;n năm của DT VN, tiếp thu tinh hoa VH TG.</span><br />\r\n	=&gt;&nbsp;Tu&acirc;n theo quy luật ph&aacute;t triển: <strong>CNXH gắn liền với độc lập DT</strong><br />\r\n	-&nbsp;Đặc trưng bản chất tổng qu&aacute;t của CNXH ở VN<br />\r\n	&nbsp; +&nbsp;Quan niệm của HCM về CNXH ở VN:<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;CNXH l&agrave; <strong>chế độ XH ho&agrave;n chỉnh</strong>, bao gồm nhiều mặt kh&aacute;c nhau của đời sống XH.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;Nhấn mạnh một số mặt kh&aacute;c nhau của đời sống XH: KT, VH, XH, ch&iacute;nh trị.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;Nhấn mạnh mục ti&ecirc;u <strong>v&igrave; lợi &iacute;ch Tổ quốc, của ND.</strong><br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;X&aacute;c định <strong>động lực XD XHCN</strong>, sức mạnh của to&agrave;n DT kết hợp với sức mạnh thời đại<br />\r\n	&nbsp; +&nbsp;Đặc trưng bản chất tổng qu&aacute;t của CNXH ở VN:<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;Chế độ ch&iacute;nh trị do<strong> NDLĐ l&agrave;m chủ</strong>.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;Nền KT ph&aacute;t triển gắn liền với sự <strong>ph&aacute;t triển KH-KT.&nbsp;<br />\r\n	&nbsp; &nbsp; &nbsp;</strong>&bull;​​​​​​​&nbsp;XH <strong>ph&aacute;t triển về VH, đạo đức</strong></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1.3.&nbsp;Quan điểm của HCM về mục ti&ecirc;u, động lực của CNXH ở Việt Nam</strong></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">1.3.1.&nbsp;Những mục ti&ecirc;u cơ bản</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Mục ti&ecirc;u chung của CNXH: <strong>độc lập, tự do cho d&acirc;n tộc, hạnh ph&uacute;c cho nh&acirc;n d&acirc;n.</strong><br />\r\n	-&nbsp;Mục ti&ecirc;u cao nhất của CNXH: <strong>n&acirc;ng cao đời sống nh&acirc;n d&acirc;n.</strong><br />\r\n	-&nbsp;C&aacute;c mục ti&ecirc;u cụ thể:<br />\r\n	&nbsp; +&nbsp;Mục ti&ecirc;u ch&iacute;nh trị:<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;Chế độ ch&iacute;nh trị do <strong>NDLĐ l&agrave;m chủ</strong>.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;Nh&agrave; nước của d&acirc;n, do d&acirc;n, v&igrave; d&acirc;n, c&oacute; <strong>2 chức năng</strong> l&agrave; d&acirc;n chủ với nh&acirc;n d&acirc;n v&agrave; chuy&ecirc;n ch&iacute;nh với kẻ th&ugrave;<strong>.</strong><br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;Ph&aacute;t huy <strong>quyền l&agrave;m chủ của nh&acirc;n d&acirc;n</strong>: d&acirc;n chủ trực tiếp v&agrave; d&acirc;n chủ đại diện.<br />\r\n	&nbsp; +&nbsp;Mục ti&ecirc;u kinh tế:<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;C&ocirc;ng - n&ocirc;ng nghiệp hiện đại, KH - KT ti&ecirc;n tiến.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;Nền KT ph&aacute;t triển to&agrave;n diện, trong đ&oacute; <strong>c&ocirc;ng - n&ocirc;ng nghiệp l&agrave; ch&iacute;nh</strong>.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;Kết hợp c&aacute;c loại lợi &iacute;ch kinh tế đ&uacute;ng đắn, thực hiện <strong>chế độ kho&aacute;n.</strong><br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;Đời sống vật chất của ND ng&agrave;y c&agrave;ng đc cải thiện<br />\r\n	&nbsp; +&nbsp;Mục ti&ecirc;u văn h&oacute;a - x&atilde; hội:<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;X&oacute;a nạn m&ugrave; chữ, ph&aacute;t triển GD, VH nghệ thuật, thực hiện nếp sống mới,...<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;X&acirc;y dựng <strong>nền VH mới</strong> d&acirc;n tộc, khoa học, đại ch&uacute;ng.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;Đ&agrave;o tạo <strong>con người mới XHCN</strong>: phải c&oacute; tư tưởng XHCN, trau dồi, r&egrave;n luyện đạo đức v&agrave; t&agrave;i năng.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">1.3.2.&nbsp;Động lực của CNXH</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Nội lực của CNXH:<br />\r\n	&nbsp; +&nbsp;Động lực con người: Bao gồm <strong>cộng đồng v&agrave; c&aacute; nh&acirc;n</strong>. Để ph&aacute;t huy động lực con người cần phải:<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;Ph&aacute;t huy <strong>sức mạnh đo&agrave;n kết cả cộng đồng DT</strong>.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;Ph&aacute;t huy <strong>sức mạnh con người.</strong><br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;Ph&aacute;t huy <strong>quyền l&agrave;m chủ v&agrave; &yacute; thức l&agrave;m chủ của người lao động </strong>trong sở hữu, trong SX v&agrave; ph&acirc;n phối.<br />\r\n	&nbsp; +&nbsp;Động lực kinh tế: ph&aacute;t triển kinh tế, sản xuất, giải ph&oacute;ng mọi năng lực sản xuất, l&agrave;m cho mọi người, mọi nh&agrave; trở n&ecirc;n c&oacute; &iacute;ch quốc lợi d&acirc;n, gắn liền KT với kĩ thuật, KT với XH.<br />\r\n	&nbsp; +&nbsp;Động lực VH, KH, GD: coi đ&oacute; l&agrave; <strong>động lực tinh thần</strong> kh&ocirc;ng thể thiếu của CNXH<br />\r\n	-&nbsp;Ngoại lực:<br />\r\n	&nbsp; +&nbsp;Tranh thủ sự gi&uacute;p đỡ, hợp t&aacute;c quốc tế, <strong>kết hợp sức mạnh DT với sức mạnh quốc tế.</strong><br />\r\n	&nbsp; +&nbsp;Phải sử dụng tốt những <strong>th&agrave;nh tựu KH &ndash; KT.</strong><br />\r\n	-&nbsp;<strong>Những yếu tố k&igrave;m h&atilde;m sự nghiệp x&acirc;y dựng CNXH ở VN:</strong> <strong>CN c&aacute; nh&acirc;n</strong> l&agrave; &ldquo;bệnh mẹ&rdquo; đẻ ra h&agrave;ng loạt c&aacute;c bệnh kh&aacute;c như: tham &ocirc;, l&atilde;ng ph&iacute;, quan li&ecirc;u...</span></span></span></li>\r\n	<li style=\"text-align:left;\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>2. Tư tưởng Hồ Ch&iacute; Minh về con đường qu&aacute; độ l&ecirc;n CNXH ở Việt Nam</strong></u></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>2.1.&nbsp;Đặc điểm, nhiệm vụ v&agrave; nội dung của thời k&igrave; qu&aacute; độ l&ecirc;n CNXH ở VN</strong></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">2.1.1.&nbsp;Loại h&igrave;nh, đặc điểm, m&acirc;u thuẫn thời k&igrave; qu&aacute; độ l&ecirc;n CNXH ở VN</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Quan điểm của HCM về loại h&igrave;nh qu&aacute; độ l&ecirc;n CNXH ở VN:<br />\r\n	&nbsp; +&nbsp;CNMLN chỉ ra c&oacute; 2 con đường qu&aacute; độ l&ecirc;n CNXH: <strong>qu&aacute; độ trực tiếp v&agrave; qu&aacute; độ gi&aacute;n tiếp.</strong><br />\r\n	&nbsp; +&nbsp;Theo HCM ở <strong>VN theo con đường qu&aacute; độ gi&aacute;n tiếp</strong>, cụ thể từ nước thuộc địa nửa PK, NN lạc hậu sau khi gi&agrave;nh đc ĐLDT qu&aacute; độ đi l&ecirc;n CNXH.<br />\r\n	-&nbsp;Đặc điểm lớn nhất trong thời k&igrave; qu&aacute; độ l&ecirc;n CNXH ở nc ta: l&agrave; từ nước <strong>n&ocirc;ng nghiệp lạc hậu tiến l&ecirc;n CNXH</strong>, kh&ocirc;ng kinh qua giai đoạn ph&aacute;t triển TBCN.<br />\r\n	-&nbsp;M&acirc;u thuẫn cơ bản nhất trong thời k&igrave; qu&aacute; độ l&ecirc;n CNXH ở nước ta: <strong>m&acirc;u thuẫn giữa nhu cầu ph&aacute;t triển cao của đất nước theo xu hướng tiến bộ v&agrave; thực trạng KT-XH qu&aacute; thấp k&eacute;m</strong>.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">2.1.2.&nbsp;Thực chất v&agrave; nhiệm vụ lịch sử của thời k&igrave; qu&aacute; độ l&ecirc;n CNXH ở VN</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Thực chất:<br />\r\n	&nbsp; +&nbsp;Qu&aacute; tr&igrave;nh <strong>cải biến nền sản xuất lạc hậu th&agrave;nh nền sản xuất ti&ecirc;n tiến, hiện đại.</strong><br />\r\n	&nbsp; +&nbsp;L&agrave; cuộc đấu tranh giai cấp gay go, phức tạp trong điều kiện mới, khi ND ta ho&agrave;n th&agrave;nh cơ bản CMDTDC, so s&aacute;nh lực lượng trong nước v&agrave; QT c&oacute; nhiều thay đổi.<br />\r\n	-&nbsp;Nhiệm vụ:<br />\r\n	&nbsp; +&nbsp;XD nền tảng vật chất v&agrave; kỹ thuật cho CNXH XD c&aacute;c tiền đề KT, ch&iacute;nh trị, VH, tư tưởng cho CNXH.<br />\r\n	&nbsp; +&nbsp;Cải tạo XH cũ, XD XH mới, trong đ&oacute; <strong>XD l&agrave; quan trọng nhất</strong>.<br />\r\n	-&nbsp;T&iacute;nh chất phức tạp, kh&oacute; khăn của thời kỳ qu&aacute; độ.<br />\r\n	&nbsp; +&nbsp;Đ&acirc;y l&agrave; cuộc CM l&agrave;m <strong>đảo lộn mọi mặt của đời sống XH</strong>, cả LLSX v&agrave; QHSX, cả cơ sở hạ tầng v&agrave; kiến tr&uacute;c thượng tầng.<br />\r\n	&nbsp; +&nbsp;Đảng, Nh&agrave; nước v&agrave; ND ta <strong>chưa c&oacute; kinh nghiệm</strong>, nhất l&agrave; tr&ecirc;n lĩnh vực KT.<br />\r\n	&nbsp; +&nbsp;Sự nghiệp XD CNXH ở nước ta lu&ocirc;n lu&ocirc;n bị <strong>c&aacute;c thế lực phản động</strong> trong v&agrave; ngo&agrave;i nước t&igrave;m c&aacute;ch chống ph&aacute;</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">2.1.3.&nbsp;Nội dung XD CNXH ở nước ta thời k&igrave; qu&aacute; độ</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Lĩnh vực ch&iacute;nh trị<br />\r\n	&nbsp; +&nbsp;Giữ vững v&agrave; ph&aacute;t huy <strong>vai tr&ograve; l&atilde;nh đạo của Đảng.</strong><br />\r\n	&nbsp; +&nbsp;Củng cố, tăng cường v<strong>ai tr&ograve; quản l&yacute; của Nh&agrave; nước.</strong><br />\r\n	&nbsp; +&nbsp;Củng cố v&agrave; mở rộng <strong>Mặt trận DT thống nhất.</strong><br />\r\n	&nbsp; +&nbsp;Củng cố v&agrave; tăng cường sức mạnh của <strong>to&agrave;n bộ hệ thống ch&iacute;nh trị</strong> cũng như <strong>từng th&agrave;nh tố.</strong><br />\r\n	-&nbsp;Lĩnh vực kinh tế<br />\r\n	&nbsp; +&nbsp;Đề cập tr&ecirc;n 3 mặt: LLSX, QHSX v&agrave; cơ chế quản l&iacute; KT.<br />\r\n	&nbsp; +&nbsp;Tăng NSLĐ tr&ecirc;n cơ sở tiến h&agrave;nh <strong>CNH XHCN.</strong><br />\r\n	&nbsp; +&nbsp;Cơ cấu KT: cơ cấu ng&agrave;nh; v&ugrave;ng, l&atilde;nh thổ v&agrave; th&agrave;nh phần KT.<br />\r\n	&nbsp; +&nbsp;Quản l&iacute; KT phải dựa tr&ecirc;n <strong>cơ sở hạch to&aacute;n</strong>, thực hiện ph&acirc;n phối theo LĐ, kho&aacute;n trong sản xuất.<br />\r\n	-&nbsp;Lĩnh vực VH &ndash; XH<br />\r\n	&nbsp; +&nbsp;Nhấn mạnh đến việc<strong> x&acirc;y dựng con người mới.</strong><br />\r\n	&nbsp; +&nbsp;Đề cao vai tr&ograve; của VH, GD v&agrave; KH-KT.<br />\r\n	&nbsp; +&nbsp;Coi trọng <strong>n&acirc;ng cao d&acirc;n tr&iacute;</strong>; đ&agrave;o tạo v&agrave; sử dụng nh&acirc;n t&agrave;i.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>2.2.&nbsp;Nguy&ecirc;n tắc, bước đi, biện ph&aacute;p thực hiện trong qu&aacute; tr&igrave;nh x&acirc;y dựng CNXH</strong></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">2.2.1.&nbsp;Nguy&ecirc;n tắc</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Qu&aacute;n triệt c&aacute;c nguy&ecirc;n l&iacute; CNMLN về <strong>XD chế độ mới</strong>, c&oacute; thể tham khảo, học tập c&aacute;c nước anh em.<br />\r\n	-&nbsp;X&aacute;c định bước đi v&agrave; nguy&ecirc;n tắc cụ thể, phải <strong>xuất ph&aacute;t từ điều kiện thực tế, đặc điểm DT, nhu cầu v&agrave; bản năng thực tế</strong> của ND.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">2.2.2.&nbsp;Bước đi</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Phương ch&acirc;m: <strong>dần dần, thận trọng</strong> từng bước một, từ thấp đến cao, l&agrave;m vững chắc từng bước, <strong>ph&ugrave; hợp với điều kiện thực tế</strong>.<br />\r\n	-&nbsp;<strong>Ch&uacute; trọng vai tr&ograve; CNH</strong> CNXH, đ&oacute; l&agrave; nhiệm vụ trọng t&acirc;m trong thời kỳ qu&aacute; độ l&ecirc;n CNXH.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">2.2.3.&nbsp;C&aacute;c biện ph&aacute;p cụ thể</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Cải tạo XH cũ, XD XH mới, trong đ&oacute; <strong>x&acirc;y dựng l&agrave; quan trọng nhất</strong>.<br />\r\n	-&nbsp;Kết hợp XD với bảo vệ, đồng thời thực hiện <strong>hai nhiệm vụ chiến lược</strong> ở hai v&ugrave;ng nam bắc.<br />\r\n	-&nbsp;XD CNXH phải <strong>c&oacute; kế hoạch, biện ph&aacute;p, quyết t&acirc;m</strong> thực hiện thắng lợi kế hoạch.<br />\r\n	-&nbsp;Đem <strong>của d&acirc;n, sức d&acirc;n, t&agrave;i d&acirc;n </strong>l&agrave;m lợi cho d&acirc;n</span></span></span></li>\r\n</ol>', '1551234838', null, '0', '55', '1', '0', null, '0', null, null, null, null, null, null, '/images/course/55/lesson/1551239673_pic-3.jpg', null);
INSERT INTO `lesson` VALUES ('197', 'Chương 4', '<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><u>CHƯƠNG 4</u></strong>: TƯ TƯỞNG HỒ CH&Iacute; MINH VỀ ĐẢNG CỘNG SẢN VIỆT NAM</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"/public/images/course/55/lesson/1551243384_pic-4.jpg\" style=\"height:255px; width:700px\" /></span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:left;\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>1. Quan niệm của HCM về vai tr&ograve; v&agrave; bản chất của ĐCSVN</strong></u></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1.1.&nbsp;Sự ra đời của ĐCSVN</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Quan điểm HCM về sự ra đời của ĐCSVN<br />\r\n	&nbsp; +&nbsp;L&agrave; sự kết hợp của <strong>CNMLN, PTCN v&agrave; PTYN.</strong><br />\r\n	&nbsp; +&nbsp;Đ&aacute;nh gi&aacute; cao <strong>vai tr&ograve; của GCCN </strong>l&agrave; l&atilde;nh đạo trong CMVN, x&acirc;y dựng Đảng theo CNMLN.<br />\r\n	-&nbsp;HCM đưa th&ecirc;m <strong>PTYN</strong> v&agrave; coi n&oacute; l&agrave; một trong ba yếu tố dẫn tới sự ra đời ĐCSVN v&igrave;:<br />\r\n	&nbsp; +&nbsp;PTYN c&oacute; <strong>vai tr&ograve;, vị tr&iacute; to lớn</strong> trong qu&aacute; tr&igrave;nh ph&aacute;t triển DT VN, l&agrave; <strong>nh&acirc;n tố chủ đạo</strong> quyết định sự nghiệp chống giặc ngoại x&acirc;m của DT ta.<br />\r\n	&nbsp; +&nbsp;<strong>PTCN kết hợp được PTYN</strong> bởi v&igrave; hai phong tr&agrave;o đ&oacute; đều c&oacute; <strong>mục ti&ecirc;u chung</strong> l&agrave;: GPDT, l&agrave;m cho DT VN được ho&agrave;n to&agrave;n độc lập, XD đất nước vững mạnh hơn.<br />\r\n	&nbsp; +<strong>PTND kết hợp PTCN</strong> th&agrave;nh <strong>qu&acirc;n chủ lực</strong> của CM.<br />\r\n	&nbsp; +&nbsp;PTYN của tr&iacute; thức VN l&agrave; nh&acirc;n tố quan trọng th&uacute;c đẩy sự kết hợp c&aacute;c yếu tố cho <strong>sự ra đời của ĐCSVN.</strong></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1.2.&nbsp;Vai tr&ograve; của ĐCSVN</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\">-&nbsp;<strong>ĐCSVN giữ vai tr&ograve; l&atilde;nh đạo CMVN</strong>. Sự l&atilde;nh đạo đ&uacute;ng đắn của Đảng l&agrave; nh&acirc;n tố h&agrave;ng đầu để đưa CMVN đi đến thắng lợi: đ</span>ề ra đường lối, phương ch&acirc;m , phương ph&aacute;p CM đ&uacute;ng đắn.<br />\r\n	-&nbsp;Gi&aacute;c ngộ, gi&aacute;o dục, tuy&ecirc;n truyền d&acirc;n ch&uacute;ng để <strong>thức tỉnh</strong> họ.<br />\r\n	-&nbsp;Tập hợp, tổ chức đo&agrave;n kết ND th&agrave;nh <strong>đội qu&acirc;n thật mạnh</strong>.<br />\r\n	-&nbsp;Sự ra đời, tồn tại v&agrave; ph&aacute;t triển của ĐCSVN <strong>ph&ugrave; hợp với quy luật tất yếu của XH</strong>.<br />\r\n	-&nbsp;Vai tr&ograve; của Đảng đ&atilde; được <strong>lịch sử chứng minh</strong>: kh&aacute;ng chiến chống Ph&aacute;p, chống Mỹ đều d&agrave;nh được thắng lợi.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1.3.&nbsp;Bản chất của ĐCSVN</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;<strong>ĐCSVN l&agrave; Đảng của GCCN</strong>, đội ti&ecirc;n phong của GCCN, mang bản chất GCCN:<br />\r\n	&nbsp; +&nbsp;Từ <strong>sứ mệnh lịch sử của GCCN</strong>.<br />\r\n	&nbsp; +&nbsp;Nền tảng l&yacute; luận v&agrave; tư tưởng của Đảng l&agrave; CNMLN, mục ti&ecirc;u của Đảng cần đạt tới l&agrave; <strong>CN cộng sản</strong>.<br />\r\n	&nbsp; +&nbsp;Đảng tu&acirc;n thủ 1 c&aacute;ch nghi&ecirc;m t&uacute;c, chặt chẽ những <strong>nguy&ecirc;n tắc x&acirc;y dựng Đảng kiểu mới</strong> của GCVS.<br />\r\n	-&nbsp;Đảng kh&ocirc;ng những l&agrave; Đảng của GCCN m&agrave; c&ograve;n l&agrave; <strong>Đảng của NDLĐ v&agrave; của to&agrave;n DT</strong>:<br />\r\n	&nbsp; +&nbsp;<strong>Lợi &iacute;ch cơ bản</strong> của GCCN, của NDLĐ v&agrave; của to&agrave;n DT l&agrave; một.<br />\r\n	&nbsp; +&nbsp;Trong <strong>th&agrave;nh phần của Đảng</strong>, ngo&agrave;i c&ocirc;ng nh&acirc;n c&ograve;n c&oacute; những người ưu t&uacute; thuộc GC n&ocirc;ng d&acirc;n, tầng lớp tr&iacute; thức v&agrave; c&aacute;c th&agrave;nh phần kh&aacute;c.<br />\r\n	&nbsp; +&nbsp;<strong>Sức mạnh của Đảng</strong> kh&ocirc;ng chỉ bắt nguồn từ GCCN m&agrave; c&ograve;n bắt nguồn từ c&aacute;c tầng lớp NDLĐ kh&aacute;c.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1.4.&nbsp;Quan niệm về ĐCSVN cầm quyền</strong></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">1.4.1.&nbsp;Đảng <strong>l&atilde;nh đạo nh&acirc;n d&acirc;n gi&agrave;nh ch&iacute;nh quyền</strong>, trở th&agrave;nh Đảng cầm quyền.</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;ĐCSVN &ldquo;tận t&acirc;m&rdquo;, &ldquo;tận lực&rdquo;, &ldquo;phụng sự&rdquo; v&agrave; &ldquo;trung th&agrave;nh&rdquo; với <strong>lợi &iacute;ch của d&acirc;n tộc</strong>. Đảng d&igrave;u dắt, l&atilde;nh đạo CMVN, xứng đ&aacute;ng trở th&agrave;nh Đảng cầm quyền.<br />\r\n	-&nbsp;Đả</span></span><span style=\"font-family:Times New Roman,Times,serif\">ng</span><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"> đ&atilde; l&atilde;nh đạo to&agrave;n thể d&acirc;n tộc gi&agrave;nh ch&iacute;nh quyền, <strong>th&agrave;nh lập nước Việt Nam d&acirc;n chủ cộng h&ograve;a (2/9/1945)</strong>. Đ&oacute; cũng l&agrave; thời điểm ĐCSVN trở th&agrave;nh Đảng cầm quyền.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">1.4.2.&nbsp;Quan niệm của HCM về Đảng cầm quyền</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Kh&aacute;i niệm: Đảng cầm quyền chỉ <strong>một đảng ch&iacute;nh trị đại diện cho một giai cấp </strong>đang nắm giữ v&agrave; l&atilde;nh đạo ch&iacute;nh quyền để điều h&agrave;nh, quản l&yacute; đất nước nhằm <strong>thực hiện lợi &iacute;ch của giai cấp m&igrave;nh</strong>. Đảng cầm quyền l&agrave; Đảng l&atilde;nh đạo nhưng Đảng l&atilde;nh đạo chưa chắc đ&atilde; cầm quyền.<br />\r\n	-&nbsp;Mục đ&iacute;ch, l&yacute; tưởng của Đảng cầm quyền: phấn đấu cho <strong>Tổ quốc ho&agrave;n to&agrave;n độc lập</strong>, cho CNXH ho&agrave;n to&agrave;n thắng lợi tr&ecirc;n đất nước ta v&agrave; tr&ecirc;n to&agrave;n TG.<br />\r\n	=&gt;&nbsp;Đảng cầm quyền vừa l&agrave; <strong>người l&atilde;nh đạo</strong>, vừa l&agrave; <strong>người đầy tớ trung th&agrave;nh của ND</strong>.<br />\r\n	-&nbsp;Đảng cầm quyền l&agrave; <strong>người l&atilde;nh đạo</strong>:<br />\r\n	&nbsp; +&nbsp;X&aacute;c định <strong>quyền l&atilde;nh đạo duy nhất</strong> của Đảng đối với to&agrave;n thể DT sau khi Đảng l&atilde;nh đạo ch&iacute;nh quyền Nh&agrave; nước.<br />\r\n	&nbsp; +&nbsp;X&aacute;c định đối tượng l&atilde;nh đạo: <strong>to&agrave;n thể DT</strong>.<br />\r\n	&nbsp; +&nbsp;X&aacute;c định điều kiện l&atilde;nh đạo: vừa c&oacute; <strong>đạo đức</strong> vừa c&oacute; <strong>tr&iacute; tuệ</strong>.<br />\r\n	&nbsp; +&nbsp;Phương thức l&atilde;nh đạo của Đảng:<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;Đề ra <strong>đường lối, nghị quyết, chủ trương</strong>.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;Vận dụng <strong>thuyết phục, tuy&ecirc;n truyền.<br />\r\n	&nbsp; &nbsp; &nbsp;</strong>&bull;​​​​​​​&nbsp;Th&ocirc;ng qua <strong>c&ocirc;ng t&aacute;c c&aacute;n bộ</strong> v&agrave; <strong>sự n&ecirc;u gương của Đảng vi&ecirc;n</strong>.<br />\r\n	-&nbsp;Đảng cầm quyền l&agrave; <strong>người đầy tớ trung th&agrave;nh</strong> của ND:<br />\r\n	&nbsp; +<strong>Th&aacute;i độ tận t&acirc;m tận tụy</strong> để phục vụ ND.<br />\r\n	&nbsp;&nbsp;+&nbsp;Vừa c&oacute; <strong>đạo đức</strong> vừa c&oacute; <strong>năng lực</strong>.<br />\r\n	Hai kh&aacute;i niệm &ldquo;người l&atilde;nh đạo&rdquo;, &ldquo;người đầy tớ&rdquo; tuy kh&aacute;c nhau nhưng <strong>gắn b&oacute; thống nhất</strong> trong mối quan hệ biện chứng với nhau. D&ugrave; l&agrave; &ldquo;người l&atilde;nh đạo&rdquo; hay &ldquo;người đầy tớ&rdquo; đều c&ugrave;ng <strong>chung mục đ&iacute;ch l&agrave; v&igrave; d&acirc;n</strong>. L&agrave;m tốt chắc năng &ldquo;l&atilde;nh đạo&rdquo; v&agrave; l&agrave;m tr&ograve;n nhiệm vụ &ldquo;đầy tớ&rdquo; cho ND l&agrave; cơ sở vững chắc nhất <strong>đảm bảo uy t&iacute;n v&agrave; năng lực l&atilde;nh đạo của Đảng</strong>.</span></span></span></li>\r\n	<li style=\"text-align:left;\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>2. Tư tưởng HCM về x&acirc;y dựng ĐCSVN trong sạch, vững mạnh</strong></u></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>2.1.&nbsp;X&acirc;y dựng Đảng -&nbsp;quy luật tồn tại v&agrave; ph&aacute;t triển của Đảng</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;X&acirc;y dựng, chỉnh đốn Đảng l&agrave; <strong>nhiệm vụ tất yếu</strong>, thường xuy&ecirc;n, vừa c&oacute; t&iacute;nh cấp b&aacute;ch, vừa c&oacute; t&iacute;nh l&acirc;u d&agrave;i.<br />\r\n	-&nbsp;T&iacute;nh tất yếu kh&aacute;ch quan c&ocirc;ng t&aacute;c x&acirc;y dựng, chỉnh đốn Đảng:<br />\r\n	&nbsp; +&nbsp;XD, chỉnh đốn Đảng <strong>bị chế định bởi qu&aacute; tr&igrave;nh ph&aacute;t triển li&ecirc;n tục của sự nghiệp CM </strong>do Đảng l&atilde;nh đạo.<br />\r\n	&nbsp; +&nbsp;Do ảnh hưởng, <strong>t&aacute;c động của m&ocirc;i trường XH</strong>, c&aacute;c quan hệ XH đến c&aacute;n bộ, Đảng vi&ecirc;n v&agrave; tổ chức Đảng.<br />\r\n	&nbsp; +&nbsp;X&acirc;y dựng Đảng l&agrave; cơ hội để mỗi c&aacute;n bộ, Đảng vi&ecirc;n <strong>tự r&egrave;n luyện </strong>tốt hơn.<br />\r\n	&nbsp; +&nbsp;Khi trở th&agrave;nh Đảng cầm quyền, việc <strong>XD Đảng phải tiến h&agrave;nh li&ecirc;n tục, thường xuy&ecirc;n hơn v&igrave; bản th&acirc;n quyền lực c&oacute; t&iacute;nh hai mặt.</strong></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>2.2.&nbsp;Nội dung c&ocirc;ng t&aacute;c x&acirc;y dựng ĐCSVN</strong></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">2.2.1.&nbsp;X&acirc;y dựng Đảng về tư tưởng, l&iacute; luận</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;<strong>CNMLN</strong> trở th&agrave;nh c&aacute;i cốt, trở th&agrave;nh nền tảng tư tưởng v&agrave; kim chỉ nam cho mọi h&agrave;nh động của ĐCSVN.<br />\r\n	-&nbsp;Những điểm lưu &yacute; trong việc <strong>tiếp nhận v&agrave; vận dụng CNMLN</strong>.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">2.2.2.&nbsp;X&acirc;y dựng Đảng về ch&iacute;nh trị</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Bao <strong>gồm nhiều nội dung</strong>: x&acirc;y dựng đường lối ch&iacute;nh trị, bảo vệ ch&iacute;nh trị, x&acirc;y dựng v&agrave; thực hiện nghị quyết, x&acirc;y dựng v&agrave; ph&aacute;t triển hệ tư tưởng, ch&iacute;nh trị,...<br />\r\n	-&nbsp;Đường lối ch&iacute;nh trị l&agrave; <strong>vấn đề cốt</strong> từ trong sự tồn tại v&agrave; ph&aacute;t triển của Đảng.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">2.2.3.&nbsp;X&acirc;y dựng Đảng về tổ chức, bộ m&aacute;y, c&ocirc;ng t&aacute;c c&aacute;n bộ</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;XD Đảng về tổ chức:<br />\r\n	&nbsp; +&nbsp;Sức mạnh của Đảng bắt nguồn từ tổ chức. ĐCSVN đc <strong>tổ chức theo hệ thống từ TW đến cơ sở</strong> rất chặt chẽ v&agrave; c&oacute; t&iacute;nh kỉ luật cao.<br />\r\n	&nbsp; +&nbsp;Trong hệ thống tổ chức của Đảng, HCM rất <strong>coi trọng vai tr&ograve; của chi bộ.</strong><br />\r\n	-&nbsp;C&aacute;c nguy&ecirc;n tắc tổ chức sinh hoạt Đảng:<br />\r\n	&nbsp; +&nbsp;Tập trung d&acirc;n chủ:<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;L&agrave; <strong>nguy&ecirc;n tắc cơ bản</strong> của Đảng.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;<strong>Tập trung</strong> l&agrave; <strong>sự thống nhất về tư tưởng, tổ chức v&agrave; hoạt động</strong>, thiểu số phải phục t&ugrave;ng đa số, cấp dưới phục t&ugrave;ng cấp tr&ecirc;n v&agrave; c&aacute;c Đảng vi&ecirc;n phải chấp h&agrave;nh nghị quyết của Đảng.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;<strong>D&acirc;n chủ</strong> l&agrave; mọi vấn đề, mọi người được quyền <strong>tự do b&agrave;y tỏ &yacute; kiến của m&igrave;nh</strong>, g&oacute;p phần t&igrave;m ra ch&acirc;n l&iacute;.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;Mối quan hệ <strong>khăng kh&iacute;t</strong>, l&agrave; <strong>hai vế của một nguy&ecirc;n tắc</strong>, tập trung tr&ecirc;n nền tảng d&acirc;n chủ, d&acirc;n chủ sưới sự chỉ đạo của tập trung<br />\r\n	&nbsp; +&nbsp;Tập thể l&atilde;nh đạo, c&aacute; nhận phụ tr&aacute;ch:<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;L&agrave; <strong>nguy&ecirc;n tắc l&atilde;nh đạo</strong> của Đảng<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;<strong>Tập thể l&atilde;nh đạo</strong> v&igrave; một người d&ugrave; kh&ocirc;n ngoan, t&agrave;i giỏi đến mấy cũng kh&ocirc;ng thể tr&ocirc;ng thấy v&agrave; xem x&eacute;t được mọi mặt của vấn đề. Nhiều người th&igrave; nhiều kinh nghiệm, người thấy mặt n&agrave;y, người thấy mặt kh&aacute;c của vấn đề.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;<strong>C&aacute; nh&acirc;n phụ tr&aacute;ch</strong> v&igrave; như thế mới c&oacute; chuy&ecirc;n tr&aacute;ch, c&ocirc;ng việc mới chạy.<br />\r\n	&nbsp; +&nbsp;Tự ph&ecirc; b&igrave;nh v&agrave; ph&ecirc; b&igrave;nh:<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;L&agrave; <strong>nguy&ecirc;n tắc sinh hoạt</strong> của Đảng, l&agrave; luật ph&aacute;t triển của Đảng.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;Mục đ&iacute;ch: l&agrave;m cho c&aacute;n bộ, Đảng vi&ecirc;n <strong>ph&aacute;t huy ưu điểm, khắc phục hạn chế</strong>, vươn tới ch&acirc;n, thiện, mĩ.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;Th&aacute;i độ, phương ph&aacute;p phải tiến h&agrave;nh thường ng&agrave;y, phải <strong>thẳng thắn, ch&acirc;n th&agrave;nh, trung thực</strong>; kh&ocirc;ng thể nang, kh&ocirc;ng giấu giếm, phải c&oacute; <strong>t&igrave;nh y&ecirc;u thương lẫn nhau</strong>.<br />\r\n	&nbsp; +&nbsp;Kỉ luật nghi&ecirc;m minh,tự gi&aacute;c:<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;Sức mạnh của Đảng v&agrave; của mỗi Đảng vi&ecirc;n <strong>bắt nguồn từ tổ chức &yacute; thức kỉ luật nghi&ecirc;m minh, tự gi&aacute;c</strong>.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;<strong>T&iacute;nh nghi&ecirc;m minh</strong>: đ&ograve;i hỏi tất cả c&aacute;c tổ chức Đảng, mọi Đảng vi&ecirc;n đều <strong>b&igrave;nh đẳng</strong> trước điều lệ Đảng, trước ph&aacute;p luật VN, trước quyết định của Đảng.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;<strong>T&iacute;nh tự gi&aacute;c</strong>: mỗi c&aacute;n bộ, Đảng vi&ecirc;n v&agrave; tổ chức Đảng <strong>tự nguyện phấn đấu</strong> cho l&iacute; tưởng cộng sản CN.<br />\r\n	&nbsp; +&nbsp;Đo&agrave;n kết thống nhất trong Đảng<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;Sự <strong>đo&agrave;n kết thống nhất</strong> của Đảng phải <strong>dựa tr&ecirc;n cơ sở l&yacute; luận của Đảng l&agrave; CNMLN</strong>; cương lĩnh, điều lệ Đảng; đường lối, quan điểm của Đảng; nghị quyết của tổ chức đảng c&aacute;c cấp.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;Muốn đo&agrave;n kết thống nhất trong Đảng phải thực h&agrave;nh d&acirc;n chủ rộng r&atilde;i ở trong Đảng.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;​​​​​​​&nbsp;C&oacute; đo&agrave;n kết tốt th&igrave; mới tạo ra cơ sở vững chắc để thống nhất &yacute; ch&iacute; v&agrave; h&agrave;nh động.<br />\r\n	-&nbsp;C&aacute;n bộ, c&ocirc;ng t&aacute;c c&aacute;n bộ của Đảng<br />\r\n	&nbsp; +&nbsp;Vị tr&iacute;, vai tr&ograve; của c&aacute;n bộ l&agrave; c&aacute;i d&acirc;y chuyền của bộ m&aacute;y, muốn <strong>việc th&agrave;nh c&ocirc;ng hay thất bại l&agrave; do c&aacute;n bộ tốt hay k&eacute;m.</strong><br />\r\n	&nbsp; +&nbsp;<strong>C&ocirc;ng t&aacute;c c&aacute;n bộ l&agrave; c&ocirc;ng t&aacute;c gốc của Đảng</strong>, bao h&agrave;m c&aacute;c mắt kh&acirc;u li&ecirc;n ho&agrave;n: tuyển chọn c&aacute;n bộ; đ&agrave;o tạo, huấn luyện, bồi dưỡng c&aacute;n bộ, đ&aacute;nh gi&aacute; đ&uacute;ng c&aacute;n bộ...</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">2.2.4.&nbsp;X&acirc;y dựng Đảng về đạo đức</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Đạo đức tạo n&ecirc;n <strong>uy t&iacute;n, sức mạnh</strong> của Đảng, gi&uacute;p Đảng đủ <strong>tư c&aacute;ch l&atilde;nh đạo, hướng dẫn quần ch&uacute;ng ND</strong>.<br />\r\n	-&nbsp;Đạo đức của Đảng ta l&agrave; <strong>đạo đức mới</strong>, đạo đức CM, đạo đức CSCN m&agrave; nội dung cốt l&otilde;i l&agrave; CN nh&acirc;n đạo chiến đấu.<br />\r\n	-&nbsp;GD đạo đức CM l&agrave; <strong>nội dung quan trọng</strong> trong việc <strong>tu dưỡng, r&egrave;n luyệ</strong>n c&aacute;n bộ, đảng vi&ecirc;n nhằm l&agrave;m cho Đảng lu&ocirc;n thật sự trong sạch.</span></span></span></li>\r\n</ol>', '1551234838', null, '0', '55', '1', '0', null, '0', null, null, null, null, null, null, '/images/course/55/lesson/1551243396_pic-4.jpg', null);
INSERT INTO `lesson` VALUES ('198', 'Chương 5', '<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><u>CHƯƠNG 5</u></strong>: TƯ TƯỞNG HỒ CH&Iacute; M&Igrave;NH VỀ ĐẠI ĐO&Agrave;N KẾT D&Acirc;N TỘC V&Agrave; ĐO&Agrave;N KẾT QUỐC TẾ</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"/public/images/course/55/lesson/1551244401_pic-5.jpg\" style=\"height:248px; width:700px\" /></span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:left;\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>1. Tư tưởng HCM về đại đo&agrave;n kết</strong></u></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1.1.&nbsp;Vai tr&ograve; của đại đo&agrave;n kết DT trong sự nghiệp CM</strong></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">1.1.1.&nbsp;Đại đo&agrave;n kết DT l&agrave; vấn đề chiến lược đảm bảo th&agrave;nh c&ocirc;ng của CM.</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;<strong>Đại đo&agrave;n kết DT l&agrave; một chiến lược cơ bản, nhất qu&aacute;n v&agrave; l&acirc;u d&agrave;i</strong>, xuy&ecirc;n suốt qtr&igrave;nh CM v&agrave; l&agrave; vấn đề sống c&ograve;n của CMVN.<br />\r\n	-&nbsp;Đại đo&agrave;n kết DT l&agrave; <strong>.cội nguồn tạo n&ecirc;n sức </strong>mạnh.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">1.1.2.&nbsp;Đại đo&agrave;n kết DT l&agrave; mục ti&ecirc;u, nhiệm vụ h&agrave;ng đầu của Đảng, của DT.</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;ĐCSVN - lực lượng duy nhất l&atilde;nh đạo CMVN, v&igrave; vậy đại <strong>đo&agrave;n kết DT phải được qu&aacute;n triệt từ chủ trương, đường lối</strong> tới hoạt động thực tiễn của Đảng.<br />\r\n	-&nbsp;Đại đo&agrave;n kết DT l&agrave; <strong>nhiệm vụ h&agrave;ng đầu mọi giai đoạn CM</strong>.<br />\r\n	-&nbsp;Đại đo&agrave;n kết DT c&ograve;n l&agrave; <strong>nhiệm vụ h&agrave;ng đầu của cả DT</strong> v&igrave; CM l&agrave; sự nghiệp của quần ch&uacute;ng phải do quần ch&uacute;ng, v&igrave; quần ch&uacute;ng.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1.2.&nbsp;Lực lượng đại đo&agrave;n kết d&acirc;n tộc</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Kh&aacute;i niệm d&acirc;n v&agrave; nh&acirc;n d&acirc;n trong TTHCM<br />\r\n	&nbsp; +&nbsp;Vừa l&agrave; mỗi <strong>con người VN cụ thể.</strong><br />\r\n	&nbsp; +&nbsp;Vừa l&agrave; <strong>tập hợp đ&ocirc;ng đảo quần ch&uacute;ng nh&acirc;n d&acirc;n</strong>.<br />\r\n	&nbsp; +&nbsp;Kh&ocirc;ng ph&acirc;n biệt t&ocirc;n gi&aacute;o, giai cấp, t&iacute;n ngưỡng, gi&agrave; trẻ, g&aacute;i trai, gi&agrave;u ngh&egrave;o.<br />\r\n	-&nbsp;Đại đo&agrave;n kết DT l&agrave; đại đo&agrave;n kết to&agrave;n d&acirc;n:<br />\r\n	&nbsp; +&nbsp;Đại đo&agrave;n kết DT bao gồm <strong>nhiều tầng nấc, nhiều cấp độ c&aacute;c quan hệ</strong> li&ecirc;n kết qua lại giữa c&aacute;c th&agrave;nh vi&ecirc;n c&aacute;c bộ phận, c&aacute;c lực lượng của DT.<br />\r\n	&nbsp; +&nbsp;N&oacute;i đại đo&agrave;n kết DT cũng c&oacute; nghĩa l&agrave; phải <strong>tập hợp được tất cả mọi người d&acirc;n trong cuộc đấu tranh chung.&nbsp;<br />\r\n	&nbsp; =&gt;&nbsp;</strong>Nội h&agrave;m kh&aacute;i niệm đại đo&agrave;n kết trong TTHCM phong ph&uacute;.<br />\r\n	&nbsp; +&nbsp;Trong qu&aacute; tr&igrave;nh XD khối đại đo&agrave;n kết to&agrave;n d&acirc;n, phải <strong>đứng vững tr&ecirc;n lập trường những giai cấp c&ocirc;ng nh&acirc;n</strong> giải quyết h&agrave;i h&ograve;a mỗi quan hệ GC - DT.<br />\r\n	-&nbsp;Điều kiện thực hiện đại đo&agrave;n kết d&acirc;n tộc<br />\r\n	&nbsp; +&nbsp;Cần phải <strong>thừa kế truyền thống y&ecirc;u nc, nh&acirc;n nghĩa, đo&agrave;n kết của dt</strong>. Đ&oacute; l&agrave; c<strong>ội nguồn sức mạnh v&ocirc; địch</strong> đề cả DT chiến đấu v&agrave; chiến thắng mọi thi&ecirc;n tai, địch họa, l&agrave;m cho đất nước được trường tồn, bản sắc DT được giữ vững.<br />\r\n	&nbsp; +&nbsp;Phải c&oacute; <strong>l&ograve;ng khoan dung độ lượng</strong> với con người. Đ&oacute; l&agrave; sự tiếp nối v&agrave; ph&aacute;t triển truyền thống nh&acirc;n &aacute;i bao dung của DT.<br />\r\n	&nbsp; +&nbsp;Cần <strong>c&oacute; niềm tin v&agrave;o nh&acirc;n d&acirc;n</strong>. Theo Người, d&acirc;n l&agrave; <strong>chỗ dựa vững chắc</strong> của Đảng, l&agrave; <strong>nguồn sức mạnh v&ocirc; tận v&agrave; v&ocirc; địch</strong> của khối đại đo&agrave;n kết, quyết định thắng lợi của CM, l&agrave; gốc v&agrave; chủ thể của Mặt trận.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1.3.&nbsp;H&igrave;nh thức tổ chức của khối đại đo&agrave;n kết d&acirc;n tộc</strong></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">1.3.1.&nbsp;H&igrave;nh thức tổ chức của khối đại đo&agrave;n kết d&acirc;n tộc l&agrave; Mặt trận d&acirc;n tộc thống nhất.</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Đại đo&agrave;n kết DT biến th&agrave;nh <strong>sức mạnh vật chất</strong>, trở th&agrave;nh <strong>lực lượng vật chất</strong> c&oacute; tổ chức l&agrave; Mặt trận DT thống nhất. Mặt trận dt thống nhất c&oacute; nhiều t&ecirc;n gọi kh&aacute;c nhau qua c&aacute;c thời k&igrave;:<br />\r\n	&nbsp; +&nbsp;Hội phản đế đồng minh Đ&ocirc;ng Dương (1930)<br />\r\n	&nbsp; +&nbsp;Măt trận d&acirc;n chủ (1936)<br />\r\n	&nbsp; +&nbsp;Mặt trận nh&acirc;n d&acirc;n phản đế (1939)<br />\r\n	&nbsp; +&nbsp;Mặt trận Việt Minh (1941)<br />\r\n	&nbsp; +&nbsp;Mặt trận Li&ecirc;n Việt (1946)<br />\r\n	&nbsp; +&nbsp;Mặt trận DT giải ph&oacute;ng miền Nam VN (1960)<br />\r\n	&nbsp; +&nbsp;Mặt trận tổ quốc VN (1955,1976)</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">1.3.2.&nbsp;Một số nguy&ecirc;n tắc cơ bản về x&acirc;y dựng v&agrave; hoạt động của Mặt trận d&acirc;n tộc thống nhất</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;MTDTTN phải được <strong>XD tr&ecirc;n nền tảng c&ocirc;ng - n&ocirc;ng - tr&iacute;,</strong> đặt <strong>dưới sự l&atilde;nh đạo của Đảng</strong>.<br />\r\n	&nbsp; +&nbsp;MTDTTN l&agrave; một <strong>khối đại đo&agrave;n kết chặt chẽ</strong>, dựa tr&ecirc;n n&egrave;n tảng khối li&ecirc;n minh c&ocirc;ng &ndash; n&ocirc;ng &ndash; tri thức.<br />\r\n	&nbsp; +&nbsp;Đại đo&agrave;n kết l&agrave; <strong>c&ocirc;ng việc của to&agrave;n DT</strong>, song n&oacute; chỉ c&oacute; thể được củng cố b&agrave; ph&aacute;t triển vững chắc khi đặt dưới dự l&atilde;nh đạo của ĐCS.<br />\r\n	&nbsp; +&nbsp;Đ&acirc;y l&agrave; <strong>nguy&ecirc;n tắc cốt l&otilde;i</strong> trong chiến lược Đại đo&agrave;n kết của HCM.<br />\r\n	&nbsp; +&nbsp;Để l&atilde;nh đạo mặt trận, Đảng phải c&oacute; <strong>cơ sở Mặt trận</strong> đ&uacute;ng đắn, ph&ugrave; hợp với từng giai đoạn, từng thời kỳ CM, ph&ugrave; hợp với quyền lợi v&agrave; nguyện vọng của đại đa số ND.<br />\r\n	-&nbsp;MTDTTN phải hoạt động tr&ecirc;n cơ sở <strong>bảo đảm lợi &iacute;ch cao của DT, quyền lợi cơ bản của c&aacute;c tầng lớp ND</strong>.<br />\r\n	&nbsp; +&nbsp;Chỉ c&oacute; thể thực hiện đo&agrave;n kết khi <strong>c&oacute; chung mục đ&iacute;nh, số phận</strong>, nếu kh&ocirc;ng suy nghĩa như nhau, kh&ocirc;ng chung c&ugrave;ng mục đ&iacute;ch, chung một số phận th&igrave; d&ugrave; c&oacute; k&ecirc;u gọi đo&agrave;n kết thế n&agrave;o cũng kh&ocirc;ng đo&agrave;n kết được.<br />\r\n	&nbsp; +&nbsp;Những quyền lợi cơ bản của c&aacute;c tầng lớp ND được HCM kết tinh v&agrave;o ti&ecirc;u chỉ của VNDCCH l&agrave; <strong>độc lập, tự do, hạnh ph&uacute;c</strong>.<br />\r\n	-&nbsp;MTDTTN phải hoạt động theo <strong>nguy&ecirc;n tắc hiệp thương d&acirc;n chủ</strong>, đảm bảo đo&agrave;n kết ng&agrave;y c&agrave;ng rộng r&atilde;i, bền vững.<br />\r\n	&nbsp; +&nbsp;MTDTTN l&agrave; <strong>tổ chức ch&iacute;nh trị - x&atilde; hội rộng lớn</strong> của cả DT, bao gồm nhiều GC, tầng lớp , đảng ph&aacute;i, DT, t&ocirc;n gi&aacute;o kh&aacute;c nhau, với nhiều lợi &iacute;ch kh&aacute;c nhau.<br />\r\n	&nbsp; +&nbsp;Nguy&ecirc;n tắc hiệp thương DC đ&ograve;i hỏi tất cả mọi vấn đề của Mặt trận phải được đem ra để tất cả <strong>c&aacute;c th&agrave;nh vi&ecirc;n c&ugrave;ng nhau b&agrave;n bạc c&ocirc;ng khai</strong>, để đi đến nhất tr&iacute;, loại trừ mọi sự &aacute;p đặt hoặc d&acirc;n chủ h&igrave;nh thức.<br />\r\n	&nbsp; +&nbsp;Để thực hiện tốt nguy&ecirc;n tắc hiệp thương d&acirc;n chủ phải <strong>đứng vững tr&ecirc;n lập trường GCCN</strong>, giải quyết h&agrave;i h&ograve;a mối quan hệ giữa lợi &iacute;ch DT v&agrave; lợi &iacute;ch DC, lợi &iacute;ch chung v&agrave; lợi &iacute;ch ri&ecirc;ng, lợi &iacute;ch l&acirc;u d&agrave;i v&agrave; lợi &iacute;ch trước mắt.<br />\r\n	-&nbsp;MTDTTN l&agrave; <strong>khối đo&agrave;n kết chặt chẽ, l&acirc;u d&agrave;i, đo&agrave;n kết thật sự, ch&acirc;n th&agrave;nh th&acirc;n &aacute;i gi&uacute;p đỡ nhau c&ugrave;ng tiến bộ</strong>.<br />\r\n	&nbsp; +&nbsp;L&agrave; một <strong>tập hợp nhiều tầng lớp, DT, t&ocirc;n gi&aacute;o,&nbsp; đảng ph&aacute;i</strong>, b&ecirc;n cạnh những điểm tương đồng vẫn c&ograve;n c&oacute; những điểm kh&aacute;c nhau cần phải b&agrave;n bạc, hiệp thương DC để thu hẹp nh&acirc;n tố kh&aacute;c biệt, nh&acirc;n l&ecirc;n nh&acirc;n tố t&iacute;ch cực, nh&acirc;n tố chung đi đến thống nhất, đo&agrave;n kết.<br />\r\n	&nbsp; +&nbsp;Khắc phục t&igrave;nh trạng đo&agrave;n kết xu&ocirc;i chiều, đồng thời phải c&oacute; <strong>tấm l&ograve;ng nh&acirc;n &aacute;i, khoan dung độ lượng</strong>, khắc phục thi&ecirc;n kiến, hẹp h&ograve;i, thiển cận; n&ecirc;u cao tinh thần tự ph&ecirc; b&igrave;nh v&agrave; ph&ecirc; b&igrave;nh, củng cố v&agrave; mở rộng khối đại đo&agrave;n kết.<br />\r\n	&nbsp; +&nbsp;Đo&agrave;n kết thực sự nghĩa l&agrave; <strong>vừa đo&agrave;n kết vừa đấu tranh</strong>, học những c&aacute;i tốt của nhau ph&ecirc; b&igrave;nh những c&aacute;i sai của nhau v&agrave; ph&ecirc; b&igrave;nh tr&ecirc;n lập trường th&acirc;n &aacute;i, v&igrave; nước, v&igrave; d&acirc;n.</span></span></span></li>\r\n	<li style=\"text-align:left;\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>2. Tư tưởng HCM về đo&agrave;n kết Quốc tế</strong></u></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>2.1.&nbsp;Vai tr&ograve; của đo&agrave;n kết quốc tế</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Thực hiện đo&agrave;n kết quốc tế nhằm kết hợp sức mạnh DT với sức mạnh thời đại, tạo sức mạnh tổng hợp cho CMVN<br />\r\n	&nbsp; +&nbsp;Sức mạnh DT:<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;Sức mạnh của <strong>chủ nghĩa y&ecirc;u nước v&agrave; &yacute; thức tự lực</strong>, tự cường DT.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;Sức mạnh của <strong>tinh thần đo&agrave;n kết.</strong><br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;<strong>&Yacute; ch&iacute; đấu tranh</strong> anh dũng, bất khuất cho độc lập tự do.<br />\r\n	&nbsp; +&nbsp;Sức mạnh thời đại:<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;Sức mạnh vĩ đại tiềm ẩn trong c&aacute;c <strong>tr&agrave;o lưu CMTG.</strong><br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;<strong>CMVN l&agrave; một bộ phận của CMTG</strong>, CMVN chỉ c&oacute; thể th&agrave;nh c&ocirc;ng khi thực hiện đo&agrave;n kết phong tr&agrave;o CMTG<br />\r\n	-&nbsp;Thực hiện đo&agrave;n kết quốc tế, nhằm g&oacute;p phần c&ugrave;ng ND thế giới thực hiện thắng lợi mục ti&ecirc;u CM.<br />\r\n	&nbsp; +&nbsp;Thực hiện đo&agrave;n kết quốc tế tức l&agrave; <strong>kết hợp chủ nghĩa y&ecirc;u nước với chủ nghĩa quốc tế v&ocirc; sản</strong> nhằm g&oacute;p phần c&ugrave;ng ND TG đấu tranh cho mục ti&ecirc;u chung, h&ograve;a b&igrave;nh, độc lập DT, d&acirc;n chủ v&agrave; tiến bộ XH.<br />\r\n	&nbsp; +&nbsp;Muốn tăng cường đo&agrave;n kết quốc tế th&igrave; ĐCS phải <strong>đấu tranh chống chủ nghĩa s&ocirc;-vanh</strong>, cơ hội, vị kỳ, ph&acirc;n biệt chủng tộc; phải <strong>GD chủ nghĩa y&ecirc;u nước ch&acirc;n ch&iacute;nh kết hợp chủ nghĩa quốc tế</strong> <strong>v&ocirc; sản</strong> cho ND.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>2.2.&nbsp;Lực lượng đo&agrave;n kết v&agrave; h&igrave;nh thức tổ chức</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;C&aacute;c lực lượng cần đo&agrave;n kết<br />\r\n	&nbsp; +&nbsp;<strong>Phong tr&agrave;o cộng sản v&agrave; c&ocirc;ng nh&acirc;n TG</strong> - lực lượng n&ograve;ng cốt của đo&agrave;n kết quốc tế, HCM cho rằng, sự đo&agrave;n kết giữa GCVS QT l&agrave; 1 bảo đảm vững chắc cho thắng lợi của CNCS.<br />\r\n	&nbsp; +&nbsp;<strong>Phong tr&agrave;o đấu tranh GPDT</strong>: HCM đ&atilde; ph&aacute;t hiện ra &acirc;m mưu chia rẽ DT của c&aacute;c nước đế quốc, tạo sự biệt lập, đối kh&aacute;ng v&agrave; th&ugrave; gh&eacute;t DT, chủng tộc nhằm l&agrave;m suy yếu phong tr&agrave;o đấu tranh GPDT ở c&aacute;c nước thuộc địa.<br />\r\n	&nbsp; +&nbsp;<strong>C&aacute;c lực lượng tiến bộ, những người y&ecirc;u chuộng h&ograve;a b&igrave;nh</strong>, d&acirc;n chủ, tự do v&agrave; c&ocirc;ng l&yacute;: Gắn cuộc đấu tranh v&igrave; độc lập của DT với mục ti&ecirc;u h&ograve;a b&igrave;nh, tự do v&agrave; c&ocirc;ng l&yacute;, HCM đ&atilde; khơi gợi lương tri của lo&agrave;i người tiến b&ocirc;, tạo n&ecirc;n những tiếng n&oacute;i ủng hộ mạnh mẽ từ c&aacute;c tổ chức quần ch&uacute;ng, c&aacute;c nh&acirc;n sĩ tr&iacute; thức v&agrave; từng con người tr&ecirc;n h&agrave;nh tinh.<br />\r\n	-&nbsp;H&igrave;nh thức tổ chức<br />\r\n	&nbsp; Tư tưởng đại đo&agrave;n kết v&igrave; thắng lợi CM của HCM đ&atilde; định hướng cho việc h&igrave;nh th&agrave;nh 4 tầng mặt trận:<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;Mặt trận đại đo&agrave;n kết DT.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;Mặt trận đo&agrave;n kết Việt - Mi&ecirc;n&nbsp; - L&agrave;o.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;Mặt trận nh&acirc;n d&acirc;n&nbsp;&Aacute; Phi đo&agrave;n kết vs VN.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;Mặt trận ND TG đo&agrave;n kết vs VN chống ĐQ x&acirc;m lược.<br />\r\n	=&gt;&nbsp;Đ&acirc;y l&agrave; sự ph&aacute;t triển rực rỡ nhất v&agrave; thắng lơi to lớn nhất của tư tưởng đại đo&agrave;n kết của HCM</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>2.3.&nbsp;Nguy&ecirc;n tắc đo&agrave;n kết Quốc tế</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Đo&agrave;n kết tr&ecirc;n cơ sở thống nhất mục ti&ecirc;u v&agrave; lợi &iacute;ch c&oacute; l&iacute;, c&oacute; t&igrave;nh.<br />\r\n	&nbsp; +&nbsp;Để <strong>đo&agrave;n kết với phong tr&agrave;o CS v&agrave; c&ocirc;ng nh&acirc;n quốc tế,</strong> HCM giương cao ngộn cơ độc lập DT gắn liền với CNXH, thực hiện đo&agrave;n kết thống nhất tr&ecirc;n nền tảng của CNMLN v&agrave; chủ nghĩa quốc tế v&ocirc; sản, c&oacute; l&yacute;, c&oacute; t&igrave;nh.<br />\r\n	&nbsp; +&nbsp;Để <strong>đo&agrave;n kết với c&aacute;c DT tr&ecirc;n TG</strong>, HCM giương cao ngọn cờ độc lập, tự do v&agrave; quyền b&igrave;nh đẳng giữa c&aacute;c DT.<br />\r\n	&nbsp; +&nbsp;Để <strong>đo&agrave;n kết với c&aacute;c lực lượng tiến bộ</strong> tr&ecirc;n TG, HCM giương cao ngọn cờ h&ograve;a b&igrave;nh v&agrave; c&ocirc;ng l&iacute;<br />\r\n	-&nbsp;Đo&agrave;n kết tr&ecirc;n cơ sở độc lập tự chủ, tự lực, tự cường.<br />\r\n	&nbsp; +&nbsp;Để đo&agrave;n kết quốc tế tốt nhất l&agrave; phải c&oacute; nội lực tốt. Nội lực l&agrave; nhận tố quyết định, c&ograve;n nguồn lực ngoại sinh chỉ c&oacute; thể ph&aacute;t huy t&aacute;c dụng th&ocirc;ng qua nguồn lực nội sinh.<br />\r\n	&nbsp; +&nbsp;Muốn tranh thủ được sự ủng hộ QT, Đảng phải c&oacute; đường lối độc lập, tự chủ, đ&uacute;ng đắn.</span></span></span></li>\r\n</ol>', '1551234838', null, '0', '55', '1', '0', null, '0', null, null, null, null, null, null, '/images/course/55/lesson/1551244415_pic-5.jpg', null);
INSERT INTO `lesson` VALUES ('199', 'Chương 6', '<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><u>CHƯƠNG 6</u></strong>: TƯ TƯỞNG HỒ CH&Iacute; MINH VỀ X&Acirc;Y DỰNG NH&Agrave; NƯỚC </span></span><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">CỦA D&Acirc;N, DO D&Acirc;N, V&Igrave; D&Acirc;N</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"/public/images/course/55/lesson/1551244862_pic-6.jpg\" style=\"height:414px; width:700px\" /></span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:left;\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>1. X&acirc;y dựng Nh&agrave; nước thể hiện quyền l&agrave; chủ v&agrave; l&agrave;m chủ của ND</strong></u></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1.1.&nbsp;Nh&agrave; nước của d&acirc;n</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;X&aacute;c lập tất cả mọi <strong>quyền lực</strong> trong NN v&agrave; trong XH đều <strong>thuộc về ND.</strong><br />\r\n	&nbsp; +&nbsp;Thể hiện trong <strong>Hiến ph&aacute;p 1946</strong>: tất cả quyền b&igrave;nh đẳng trong nước đều l&agrave; của to&agrave;n thể ND VN, kh&ocirc;ng ph&acirc;n biệt n&ograve;i giống, g&aacute;i trai, gi&agrave;u ngh&egrave;o, GC, t&ocirc;n gi&aacute;o; những việc quan hệ đến vận mệnh quốc gia sẽ đưa ra to&agrave;n d&acirc;n phục quyết.<br />\r\n	&nbsp; +&nbsp;<strong>ND c&oacute; quyền kiểm so&aacute;t NN</strong>, ND c&oacute; quyền b&atilde;i miễn những đại biểu Quốc hội v&agrave; đại biểu Hội đồng ND nếu những đại biểu đ&oacute; ko xứng đ&aacute;ng với sự t&iacute;n nhiệm của ND.<br />\r\n	-&nbsp;HCM n&ecirc;u l&ecirc;n quan điểm <strong>d&acirc;n chủ v&agrave; d&acirc;n l&agrave;m chủ.</strong><br />\r\n	&nbsp; +&nbsp;D&acirc;n chủ l&agrave; <strong>kh&aacute;t vọng mu&ocirc;n đời của con người</strong>.<br />\r\n	&nbsp; +&nbsp;D&acirc;n l&agrave; chủ c&oacute; nghĩa l&agrave; <strong>x&aacute;c định vị thế của d&acirc;n</strong>, d&acirc;n l&agrave;m chủ c&oacute; nghĩa l&agrave; x&aacute;c định quyền v&agrave; nghĩa vụ của d&acirc;n.<br />\r\n	&nbsp; +&nbsp;Quyền lực của ND được đặt ở <strong>vị tr&iacute; tối thượng</strong>. Một NN như thế l&agrave; một NN tiến bộ trong bước đường ph&aacute;t triển của nh&acirc;n loại.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1.2.&nbsp;Nh&agrave; nước do d&acirc;n</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;<strong>Nh&agrave; nước do d&acirc;n lập n&ecirc;n, d&acirc;n l&agrave;m chủ, d&acirc;n ủng hộ.</strong><br />\r\n	&nbsp; +&nbsp;NN lấy <strong>d&acirc;n l&agrave;m gốc.</strong><br />\r\n	&nbsp; +&nbsp;Hoạt động của NN v&igrave; <strong>quyền lợi của ND</strong> chứ kh&ocirc;ng phải v&igrave; quyền lợi của 1 nh&oacute;m người hay 1 tập đo&agrave;n XH n&agrave;o đ&oacute;.<br />\r\n	-&nbsp;Quyền tham gia XD v&agrave; quản l&iacute; NN của d&acirc;n được thể hiện:<br />\r\n	&nbsp; +&nbsp;To&agrave;n bộ <strong>c&ocirc;ng d&acirc;n c&oacute; quyền bầu ra Quốc hội</strong>.<br />\r\n	&nbsp; +&nbsp;Quốc hội bầu ra chủ tịch nước, UB thường vụ Quốc hội v&agrave; Hội đồng CP.<br />\r\n	&nbsp; +&nbsp;Hội đồng CP l&agrave; <strong>cơ quan h&agrave;nh ch&iacute;nh cao nhất</strong> của NN, thực hiện c&aacute;c nghị quyết của Quốc hội v&agrave; chấp h&agrave;nh ph&aacute;p luật.<br />\r\n	&nbsp; +&nbsp;Mọi c&ocirc;ng việc của bộ m&aacute;y NN trong việc quản l&iacute; XH đều <strong>thực hiện &yacute; của d&acirc;n</strong>.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1.3.&nbsp;Nh&agrave; nước v&igrave; d&acirc;n</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;L&agrave; NN lấy <strong>lợi &iacute;ch ch&iacute;nh đ&aacute;ng của ND l&agrave;m mục ti&ecirc;u</strong>, tất cả đều v&igrave; lợi &iacute;ch của ND, kh&ocirc;ng c&oacute; bất k&igrave; lợi &iacute;ch n&agrave;o kh&aacute;c.<br />\r\n	-&nbsp;L&agrave; <strong>NN trong sạch</strong>, kh&ocirc;ng c&oacute; bất k&igrave; một đặc quyền đặc lợi n&agrave;o.<br />\r\n	-&nbsp;Trong NN đ&oacute;, đội ngũ c&aacute;n bộ kh&ocirc;ng phải l&agrave; &ldquo;quan CM&rdquo; m&agrave; l&agrave; <strong>&ldquo;đầy tớ&rdquo; cho ND</strong>.</span></span></span></li>\r\n	<li style=\"text-align:left;\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>2. Quan điểm của HCM về sự thống nhất giữa bản nhất GCCN với t&iacute;nh ND v&agrave; t&iacute;nh DT của Nh&agrave; nước</strong></u></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>2.1.&nbsp;Về bản chất GCCN của Nh&agrave; nước</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Nh&agrave; nước do <strong>ĐCS l&atilde;nh đạo.</strong><br />\r\n	&nbsp; +&nbsp;ĐCSVN l&atilde;nh đạo nh&agrave; nước giữ vững v&agrave; tăng cường bản chất GCCN.<br />\r\n	&nbsp; +&nbsp;Đảng l&atilde;nh đạo nh&agrave; nước bằng <strong>phương thức th&iacute;ch hợp</strong>.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;Đảng l&atilde;nh đạo bằng <strong>đường lối, quan điểm, chủ trương</strong> để nh&agrave; nước <strong>thể chế h&oacute;a </strong>th&agrave;nh ph&aacute;p luật, ch&iacute;nh s&aacute;ch, kế hoạch.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;Đảng l&atilde;nh đạo Nh&agrave; nước <strong>bằng hoạt động của c&aacute;c tổ chức đảng v&agrave; đảng vi&ecirc;n</strong> của m&igrave;nh trong bộ m&aacute;y, cơ quan nh&agrave; nước.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;Đảng l&atilde;nh đạo nh&agrave; nước bằng <strong>c&ocirc;ng t&aacute;c kiểm tra</strong>.<br />\r\n	-&nbsp;Bản chất giai cấp của Nh&agrave; nước ta thể hiện ở <strong>t&iacute;nh định hướng XHCN</strong> của sự ph&aacute;t triển đất nước.<br />\r\n	-&nbsp;Bản chất GCCN của Nh&agrave; nước ta thể hiện ở <strong>nguy&ecirc;n tắc tổ chức v&agrave; hoạt động cơ bản</strong> của n&oacute; l&agrave; nguy&ecirc;n tắc tập trung d&acirc;n chủ.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>2.2.&nbsp;Bản chất của GCCN thống nhất với t&iacute;nh nh&acirc;n d&acirc;n, t&iacute;nh d&acirc;n tộc của nh&agrave; nước</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;NN ta ra đời l&agrave; <strong>kết quả của cuộc đấu tranh l&acirc;u d&agrave;i, gian khổ</strong> của rất nhiều thế hệ VN.<br />\r\n	-&nbsp;T&iacute;nh thống nhất c&ograve;n biểu hiện ở chỗ NN ta <strong>bảo vệ lợi &iacute;ch của ND,</strong> lấy lợi &iacute;ch của DT l&agrave;m cơ bản.<br />\r\n	-&nbsp;Trong thực tế, NN ta đ&atilde; <strong>đứng ra l&agrave;m nhiệm vụ</strong> của cả DT giao ph&oacute;, đ&atilde; l&atilde;nh đạo ND tiến h&agrave;nh c&aacute;c cuộc kh&aacute;ng chiến để bảo vệ nền độc lập, tự do của Tổ quốc, XD một NN VN h&ograve;a b&igrave;nh, thống nhất, độc lập, d&acirc;n chủ v&agrave; gi&agrave;u mạnh, g&oacute;p phần t&iacute;ch cực v&agrave;o sự ph&aacute;t triển tiến bộ của TG.</span></span></span></li>\r\n	<li style=\"text-align:left;\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>3. X&acirc;y dựng Nh&agrave; nước c&oacute; hiệu lực ph&aacute;p l&iacute; mạnh mẽ</strong></u></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>3.1.&nbsp;X&acirc;y dựng một Nh&agrave; nước hợp ph&aacute;p, hợp hiến</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Chỉ một ng&agrave;y sau khi đọc Tuy&ecirc;n ng&ocirc;n độc lập, Chủ tịch HCM đ&atilde; đề nghị <strong>tổ chức Tổng tuyển cử</strong> c&agrave;ng sớm c&agrave;ng tốt đề lập Quốc hội rồi từ đ&oacute; lập Ch&iacute;nh phủ v&agrave; c&aacute;c cơ quan, bộ m&aacute;y ch&iacute;nh thức kh&aacute;c của Nh&agrave; nước mới.<br />\r\n	-&nbsp;C&oacute; được một Nh&agrave; nước hợp hiến, thể hiện quyền lực tối cao của nh&acirc;n d&acirc;n như vậy th&igrave; nước ta mới c&oacute; <strong>cơ sở ph&aacute;p l&yacute; vững chắc</strong> để l&agrave;m việc với qu&acirc;n Đồng minh, mới c&oacute; <strong>quan hệ quốc tế b&igrave;nh đẳng</strong>, mới <strong>thiết lập được một cơ chế quyền lực hợp ph&aacute;p </strong>theo đ&uacute;ng th&ocirc;ng lệ của một Nh&agrave; nước ph&aacute;p quyền hiện đại.<br />\r\n	-&nbsp;<strong>Ng&agrave;y 6/1/1946 cuộc Tổng tuyển cử được tiến h&agrave;nh</strong> thắng lợi với chế độ phổ th&ocirc;ng đầu phiếu, trực tiếp v&agrave; bỏ phiếu k&iacute;n.<br />\r\n	-&nbsp;<strong>Ng&agrave;y 2/3/1946, Quốc hội kh&oacute;a I nước Việt Nam D&acirc;n chủ Cộng h&ograve;a đ&atilde; họp phi&ecirc;n đầu ti&ecirc;n</strong>, lập ra c&aacute;c tổ chức, bộ m&aacute;y v&agrave; c&aacute;c chức vụ ch&iacute;nh thức của Nh&agrave; nước. HCM được bầu l&agrave;m Chủ tịch Ch&iacute;nh phủ li&ecirc;n hiệp đầu ti&ecirc;n của nước ta.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>3.2.&nbsp;Hoạt động quản l&iacute; Nh&agrave; nước bằng Hiến ph&aacute;p, ph&aacute;p luật v&agrave; ch&uacute; trọng đưa ph&aacute;p luật v&agrave;o cuộc sống.</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Quản l&yacute; nh&agrave; nước quan trọng nhất l&agrave; quản l&yacute; bằng hệ thống luật, trong đ&oacute; quan trọng bậc nhất l&agrave; hiến ph&aacute;p<br />\r\n	-&nbsp;Ch&uacute; trọng đưa hiến ph&aacute;p v&agrave; ph&aacute;p luật v&agrave; cuộc sống</span></span></span></li>\r\n	<li style=\"text-align:left;\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>4. X&acirc;y dựng Nh&agrave; nước trong sạch, hoạt động c&oacute; hiệu quả</strong></u></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>4.1.&nbsp;X&acirc;y dựng đội ngũ c&aacute;n bộ c&ocirc;ng chức đủ đức v&agrave; t&agrave;i</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;HCM đề cao vai tr&ograve; của c&aacute;n bộ, c&ocirc;ng chức, c&ocirc;ng chức l&agrave; &ldquo;c&aacute;i gốc của mọi việc&rdquo;.<br />\r\n	-&nbsp;HCM đưa ra những y&ecirc;u cầu đề xd đội ngũ c&aacute;n bộ c&ocirc;ng chức.<br />\r\n	-&nbsp;Bộ m&aacute;y nh&agrave; nc cần gọn nhẹ, c&oacute; hiệu lực, ph&ugrave; hợp vs từng giai đoạn.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>4.2.&nbsp;Đề ph&ograve;ng v&agrave; khắc phục những ti&ecirc;u cực trong hoạt động của Nh&agrave; nước</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;X&acirc;y dựng một <strong>NN của d&acirc;n, do d&acirc;n, v&igrave; d&acirc;n</strong> kh&ocirc;ng bao giờ t&aacute;ch rời với việc l&agrave;m cho <strong>NN lu&ocirc;n trong sạch, vững mạnh</strong>.<br />\r\n	-&nbsp;Khi NN gi&agrave;nh được độc lập, ch&iacute;nh quyền CM c&ograve;n non trẻ cũng như l&uacute;c CM chuyển giai đoạn, HCM c&agrave;ng ch&uacute; &yacute; hơn bao giờ hết đến việc <strong>bảo đảm sự trong sạch, vững mạnh của c&aacute;c cấp ch&iacute;nh quyền</strong> bởi những ti&ecirc;u cực rất dễ trở th&agrave;nh nguy cơ l&agrave;m biến chất NN.<br />\r\n	-&nbsp;Sau khi th&agrave;nh lập NN VNDCCH, HCM đ&atilde; gửi thư cho UBND c&aacute;c kỳ, c&aacute;c tỉnh, huyện, l&agrave;ng n&ecirc;u r&otilde; chống đặc quyền đặc lợi; bộ m&aacute;y <strong>NN kh&ocirc;ng phải bộ m&aacute;y &aacute;p bức, b&oacute;c lột ND, c&aacute;n bộ, c&ocirc;ng chức kh&ocirc;ng phải những &ldquo;&ocirc;ng quan CM&rdquo;.</strong><br />\r\n	-&nbsp;HCM chỉ ra 6 bệnh cần đề ph&ograve;ng<strong>: tr&aacute;i ph&eacute;p, cậy thế, hủ h&oacute;a, t&ugrave; t&uacute;ng, chia rẽ, ki&ecirc;u ngạo.</strong><br />\r\n	-&nbsp;Trong qu&aacute; tr&igrave;nh XD NN VNDCCH, HCM chỉ r&otilde; những ti&ecirc;u cực sau v&agrave; nhắc nhở mọi người đề ph&ograve;ng:<br />\r\n	&nbsp; +&nbsp;<strong>Đặc quyền, đặc lợi:</strong> XD NN trong sạch, vững mạnh đ&ograve;i hỏi phải tẩy trừ những th&oacute;i cậy m&igrave;nh l&agrave; người trong cơ quan ch&iacute;nh quyền để cửa quyền, h&aacute;ch dịch với d&acirc;n, lạm quyền, đồng thời vơ v&eacute;t tiền của, lợi dụng chức quyền l&agrave;m lợi cho m&igrave;nh.<br />\r\n	&nbsp; +&nbsp;<strong>Tham &ocirc;, l&atilde;ng ph&iacute;, quan li&ecirc;u</strong>: l&agrave; &ldquo;giặc nội x&acirc;m&rdquo;, &ldquo;giặc ở trong long&rdquo;, thứ giặc nguy hiểm hơn cả giặc ngoại x&acirc;m.<br />\r\n	&nbsp; +&nbsp;<strong>T&ugrave; t&uacute;ng, chia rẽ, ki&ecirc;u ngạo</strong>: g&acirc;y mất đo&agrave;n kết, g&acirc;y rối cho c&ocirc;ng t&aacute;c.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>4.3.&nbsp;Tăng cường t&iacute;nh nghi&ecirc;m minh của ph&aacute;p luật đi đ&ocirc;i đẩy mạnh gi&aacute;o dục đạo đức CM.</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;HCM <strong>kết hợp nhuần nhuyễn đức trị v&agrave; ph&aacute;p trị</strong> trong quản l&yacute; XH.<br />\r\n	-&nbsp;Trong việc thực thi quyền hạn v&agrave; tr&aacute;ch nhiệm của m&igrave;nh HCM lu&ocirc;n <strong>thống nhất h&agrave;i h&ograve;a giữa l&yacute; tr&iacute; v&agrave; t&igrave;nh cảm</strong>.</span></span></span></li>\r\n</ol>', '1551234838', null, '0', '55', '1', '0', null, '0', null, null, null, null, null, null, '/images/course/55/lesson/1551244879_pic-6.jpg', null);
INSERT INTO `lesson` VALUES ('200', 'Chương 7', '<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><u>CHƯƠNG 7</u></strong>: TƯ TƯỞNG HỒ CH&Iacute; MINH VỀ VĂN H&Oacute;A, ĐẠO ĐỨC&nbsp;V&Agrave; X&Acirc;Y DỰNG CON NGƯỜI MỚI</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"display:none\">&nbsp;</span><img alt=\"\" src=\"/public/images/course/55/lesson/1551245787_pic-7.jpg\" style=\"height:358px; width:700px\" /></span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:left;\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>1. Những quan điểm cơ bản của HCM về văn h&oacute;a</strong></u></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1.1.&nbsp;Định nghĩa về văn h&oacute;a v&agrave; quan điểm về x&acirc;y dựng nền văn h&oacute;a mới.</strong></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">1.1.1.&nbsp;Định nghĩa văn h&oacute;a.</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">&ldquo;V&igrave; lẽ sinh tồn cũng như mục đ&iacute;ch của cuộc sống, lo&agrave;i người mới s&aacute;ng tạo v&agrave; ph&aacute;t minh ra <strong>ng&ocirc;n ngữ, chữ viết, đạo đức, PL, KH, t&ocirc;n gi&aacute;o, văn học, nghệ thuật, những c&ocirc;ng cụ cho sinh hoạt hằng ng&agrave;y về mặc, ăn, ở v&agrave; c&aacute;c phương thức sử dụng</strong>. To&agrave;n bộ những s&aacute;ng tạo v&agrave; ph&aacute;t minh đ&oacute; tức l&agrave; văn h&oacute;a.&rdquo;</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">1.1.2.&nbsp;Quan điểm về x&acirc;y dựng một nền văn h&oacute;a mới</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;X&acirc;y dựng t&acirc;m l&yacute;.<br />\r\n	-&nbsp;X&acirc;y dựng lu&acirc;n l&yacute;.<br />\r\n	-&nbsp;X&acirc;y dựng x&atilde; hội.<br />\r\n	-&nbsp;X&acirc;y dựng ch&iacute;nh trị.<br />\r\n	-&nbsp;X&acirc;y dựng kinh tế.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1.2.&nbsp;Quan điểm của HCM về c&aacute;c vấn đề chung của văn h&oacute;a.</strong></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">1.2.1.&nbsp;Quan điểm về vị tr&iacute; v&agrave; vai tr&ograve; của văn h&oacute;a trong đời sống x&atilde; hội.</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Văn h&oacute;a l&agrave; đời sống tinh thần của xh, thuộc kiến thức thượng tầng.<br />\r\n	&nbsp; +&nbsp;Trong quan hệ với Ch&iacute;nh trị - XH<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;Ch&iacute;nh trị, XH được giải ph&oacute;ng th&igrave; VH mới được giải ph&oacute;ng.<br />\r\n	&nbsp; &nbsp; &nbsp;&bull;&nbsp;Ch&iacute;nh trị giải ph&oacute;ng <strong>mở đường</strong> cho VH ph&aacute;t triển.<br />\r\n	&nbsp; +&nbsp;Trong quan hệ với kinh tế: x&acirc;y dựng kinh tế để tạo điều kiện cho việc x&acirc;y dựng v&agrave; ph&aacute;t triển VH. Phải đẩy mạnh KT trước.<br />\r\n	-&nbsp;Văn h&oacute;a&nbsp;kh&ocirc;ng thể đứng ngo&agrave;i m&agrave; <strong>phải ở trong kinh tế v&agrave; ch&iacute;nh trị</strong>, phải <strong>phục vụ nhiệm vụ ch&iacute;nh trị</strong> v&agrave; <strong>th&uacute;c đẩy sự ph&aacute;t triển kinh tế</strong>.<br />\r\n	&nbsp; +&nbsp;Văn h&oacute;a&nbsp;phải t&iacute;ch cực, chủ động tham gia thực hiện những nhiệm vụ ch&iacute;nh trị, th&uacute;c đẩy x&acirc;y dựng v&agrave; ph&aacute;t triểm kinh tế.<br />\r\n	&nbsp; +&nbsp;L&agrave;m kinh tế v&agrave; ch&iacute;nh trị cũng phải <strong>c&oacute; t&iacute;nh VH</strong>.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">1.2.2.&nbsp;Quan điểm về t&iacute;nh chất của nền văn h&oacute;a</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;T&iacute;nh d&acirc;n tộc<br />\r\n	&nbsp; +&nbsp;Mang <strong>đặc trưng</strong> của DT.<br />\r\n	&nbsp; +&nbsp;Giữ g&igrave;n, kế thừa, ph&aacute;t huy những <strong>truyền thống DT</strong>.<br />\r\n	&nbsp; +&nbsp;Ph&aacute;t triển những truyền thống DT cho <strong>ph&ugrave; hợp với điều kiện lịch sử mới của đất nước.</strong><br />\r\n	-&nbsp;T&iacute;nh KHT<br />\r\n	&nbsp; + T&iacute;nh hiện đại, ti&ecirc;n tiến, thận với <strong>tr&agrave;o lưu tiến h&oacute;a </strong>của thời đại.<br />\r\n	&nbsp; +&nbsp;<strong>Đấu tranh</strong> với những g&igrave; tr&aacute;i KH, phản tiến bộ.<br />\r\n	&nbsp; +&nbsp;<strong>Gạn đục khơi trong</strong>, kế thừa truyền thống tốt đẹp của DT v&agrave; tiếp thu tinh hoa VH nh&acirc;n loại.<br />\r\n	-&nbsp;T&iacute;nh đại ch&uacute;ng:<br />\r\n	&nbsp; +&nbsp;Phải <strong>phục vụ nh&acirc;n d&acirc;n</strong>.<br />\r\n	&nbsp; +&nbsp;Ph&ugrave; hợp với <strong>nguyện vọng của nh&acirc;n d&acirc;n</strong>.<br />\r\n	&nbsp; +&nbsp;Đậm đ&agrave; <strong>t&iacute;nh nh&acirc;n văn</strong>.<br />\r\n	&nbsp; +&nbsp;Do <strong>nh&acirc;n d&acirc;n x&acirc;y dựng</strong> n&ecirc;n.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">1.2.3.&nbsp;Quan điểm về chức năng của văn h&oacute;a</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;<strong>Bồi dưỡng tư tưởng</strong> đ&uacute;ng đắn v&agrave; những t&igrave;nh cảm cao đẹp<br />\r\n	&nbsp; +&nbsp;Mục đ&iacute;ch: Bồi dưỡng, <strong>n&ecirc;u cao tư tưởng đ&uacute;ng đắn</strong> v&agrave; t&igrave;nh cảm cao đẹp cho nh&acirc;n d&acirc;n, <strong>loại bỏ những tư tưởng sai lầm</strong>, t&igrave;nh cảm thấp h&egrave;n c&oacute; thể c&oacute; trong tư tưởng, t&igrave;nh cảm mỗi người.<br />\r\n	&nbsp; +&nbsp;VH phải đặc biệt quan t&acirc;m đến những <strong>tư tưởng v&agrave; t&igrave;nh cảm lớn</strong>, <strong>chi phối đời sống tinh thần </strong>của mỗi người v&agrave; cả DT.<br />\r\n	&nbsp; +&nbsp;Chức năng n&agrave;y phải được <strong>tiến h&agrave;nh thường xuy&ecirc;n li&ecirc;n tục</strong>.<br />\r\n	-&nbsp;<strong>Mở rộng hiểu biết, n&acirc;ng cao d&acirc;n tr&iacute;</strong><br />\r\n	&nbsp; +&nbsp;D&acirc;n tr&iacute; l&agrave; <strong>tr&igrave;nh độ hiểu biết</strong>, kiến thức của người d&acirc;n.<br />\r\n	&nbsp; +&nbsp;Nội dung của n&acirc;ng cao d&acirc;n tr&iacute; l&agrave; <strong>bắt đầu từ chỗ biết đọc, biết viết</strong>, tiếp đ&oacute; l&agrave; <strong>sự hiểu biết c&aacute;c lĩnh vực kh&aacute;c nhau của đời sống XH</strong> như KT, ch&iacute;nh trị, KH, kĩ thuật, thực tiễn VN v&agrave; TG.<br />\r\n	&nbsp; +&nbsp;N&acirc;ng cao d&acirc;n tr&iacute; nhằm hướng v&agrave;o mục ti&ecirc;u chung l&agrave; <strong>ĐLDT v&agrave; CNXH</strong>.<br />\r\n	-&nbsp;Bồi dưỡng những phẩm chất, <strong>phong c&aacute;ch v&agrave; lối sống tốt đẹp, l&agrave;nh mạnh</strong>, hưỡng con người đến ch&acirc;n, thiện, mỹ để <strong>ho&agrave;n thiện bản th&acirc;n.</strong><br />\r\n	&nbsp; +&nbsp;HCM đề ra <strong>phẩm chất, phong c&aacute;ch cần thiết</strong> để mỗi người tư tu dưỡng, đặc biệt quan t&acirc;m đến phẩm chất đạo đức - ch&iacute;nh trị của c&aacute;n bộ, đảng vi&ecirc;n.<br />\r\n	&nbsp; +&nbsp;VH gi&uacute;p con người l&agrave;m <strong>nảy nở c&aacute;i tốt đẹp</strong>, giảm bớt c&aacute;i lạc hậu, xấu xa, bảo thủ, <strong>vươn tới c&aacute;i ch&acirc;n, thiện, mỹ</strong> để ho&agrave;n thiện bản th&acirc;n.<br />\r\n	&nbsp; +&nbsp;Phải l&agrave;m cho VH <strong>thấm s&acirc;u v&agrave;o t&acirc;m l&yacute; quốc d&acirc;n</strong>. Văn h&oacute;a phải soi đường cho quốc d&acirc;n đi.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1.3.&nbsp;Quan điểm của HCM về một số lĩnh vực ch&iacute;nh của văn h&oacute;a</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Văn h&oacute;a gi&aacute;o dục.<br />\r\n	-&nbsp;Văn h&oacute;a văn nghệ.<br />\r\n	-&nbsp;Văn h&oacute;a đời sống</span></span></span></li>\r\n	<li style=\"text-align:left;\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>2. Tư tưởng HCM về đạo đức</strong></u></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>2.1.&nbsp;Nội dung cơ bản của TTHCM về đạo đức</strong></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">2.1.1.&nbsp;Quan điểm về vai tr&ograve; v&agrave; sức mạnh của đạo đức</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Đạo đức l&agrave; c&aacute;i gốc của người CM<br />\r\n	-&nbsp;Đạo đức l&agrave; nh&acirc;n tố tạo n&ecirc;n sức hấp dẫn của CNXH</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">2.1.2.&nbsp;Quan điểm về những chuẩn mực đạo đức CM</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;<strong>Trung với nước, hiếu với d&acirc;n</strong><br />\r\n	&nbsp; +&nbsp;HCM cho rằng trung với nước phải gắn liền hiếu với d&acirc;n. V&igrave; nước l&agrave; của d&acirc;n, c&ograve;n d&acirc;n lại l&agrave; chủ nh&acirc;n của nước, c&aacute;n bộ l&agrave; đầy tớ của d&acirc;n chứ kh&ocirc;ng phải &ldquo;quan CM&rdquo;.<br />\r\n	&nbsp; +&nbsp;Trung với nước l&agrave; <strong>tuyệt đối trung th&agrave;nh</strong> với sự nghiệp dựng nước; l&agrave; suốt đời phấn đấu cho Đảng, cho CM.<br />\r\n	&nbsp; +&nbsp;Hiếu với d&acirc;n thể hiện ở chỗ <strong>thương d&acirc;n, tin d&acirc;n, phục vụ ND hết l&ograve;ng</strong>.<br />\r\n	&nbsp; +&nbsp;Với c&aacute;n bộ l&atilde;nh đạo, HCM y&ecirc;u cầu phải nắm vững d&acirc;n t&igrave;nh, hiểu r&otilde; d&acirc;n t&acirc;m, thường xuy&ecirc;n quan t&acirc;m cải thiện d&acirc;n sinh, n&acirc;ng cao d&acirc;n tr&iacute;.<br />\r\n	-&nbsp;<strong>Cần, kiệm, li&ecirc;m, ch&iacute;nh, ch&iacute; c&ocirc;ng v&ocirc; tư</strong><br />\r\n	&nbsp; +&nbsp;Cần l&agrave; si&ecirc;ng năng, chăm chỉ; lao động c&oacute; kế hoạch, c&oacute; hiệu quả, c&oacute; năng suất cao với tinh thần tự lực c&aacute;nh sinh. Kiệm l&agrave; tiết kiệm, ko ph&ocirc; trường h&igrave;nh thức, kh&ocirc;ng li&ecirc;n hoan, ch&egrave; ch&eacute;n lu b&ugrave;. Li&ecirc;m l&agrave; lu&ocirc;n t&ocirc;n trọng của c&ocirc;ng v&agrave; của d&acirc;n, phải &ldquo;trong sạch, kh&ocirc;ng tham lam&rdquo; tiền của, đại vị, danh tiếng. Ch&iacute;nh l&agrave; thẳng thắn, đứng đắn.<br />\r\n	&nbsp; +&nbsp;C&aacute;c đức t&iacute;nh n&agrave;y c&oacute; <strong>quan hệ chặt chẽ với nhau</strong>, ai cũng phải thực hiện, song c&aacute;n bộ, đảng vi&ecirc;n phải thực h&agrave;nh trước l&agrave;m kiểu mẫu cho d&acirc;n.<br />\r\n	&nbsp; +&nbsp;Ch&iacute; c&ocirc;ng v&ocirc; tư l&agrave; c&ocirc;ng bằng, c&ocirc;ng t&acirc;m, kh&ocirc;ng thi&ecirc;n vị, l&agrave;m việc g&igrave; cũng kh&ocirc;ng nghĩ đến m&igrave;nh trước, chỉ v&igrave; Đảng, v&igrave; DT; n&ecirc;u cao chủ nghĩa tập thể, <strong>trừ bỏ chủ nghĩa c&aacute; nh&acirc;n</strong>.<br />\r\n	-&nbsp;<strong>Thương y&ecirc;u con người, sống c&oacute; t&igrave;nh nghĩa.<br />\r\n	&nbsp; +&nbsp;</strong>Người CM l&agrave; người gi&agrave;u t&igrave;nh cảm, c&oacute; t&igrave;nh cảm CM mới đi l&agrave;m CM.<br />\r\n	&nbsp; +&nbsp;T&igrave;nh y&ecirc;u thương đ&oacute; l&agrave; một t&igrave;nh cảm rộng lớn, trước hết d&agrave;nh cho những người ngh&egrave;o khổ, nếu kh&ocirc;ng c&oacute; t&igrave;nh y&ecirc;u thương như vậy th&igrave; kh&ocirc;ng thể n&oacute;i đến CM, CNXH v&agrave; CNCS.<br />\r\n	&nbsp; +&nbsp;T&igrave;nh y&ecirc;u thương phải được <strong>XD tr&ecirc;n lập trường GCCN</strong>, thể hiện trong mối quan hệ h&agrave;ng ng&agrave;y với bạn b&egrave;, anh em,&hellip;<br />\r\n	-&nbsp;<strong>C&oacute; tinh thần quốc tế trong s&aacute;ng</strong><br />\r\n	&nbsp; +&nbsp;Chủ nghĩa quốc tế l&agrave; một trong những phẩm chất quan trọng nhất của đạo đức cộng sản chủ nghĩa, <strong>bắt nguồn từ bản chất GCCN</strong>, nhằm v&agrave;o mối quan hệ rộng lớn, vượt ra khỏi quốc gia.<br />\r\n	&nbsp; +&nbsp;HCM chủ trương <strong>gi&uacute;p bạn l&agrave; tự gi&uacute;p m&igrave;nh</strong>.<br />\r\n	&nbsp; +&nbsp;Đo&agrave;n kết quốc tế l&agrave; nhằm thực hiện những mục ti&ecirc;u lớn của thời đại l&agrave; h&ograve;a b&igrave;nh, độc lập DT, d&acirc;n chủ v&agrave; tiến bộ XH, l&agrave; hợp t&aacute;c v&agrave; hữu nghị theo tinh th&acirc;n: <strong>bốn phương v&ocirc; sản, bốn bể đều l&agrave; anh em.</strong><br />\r\n	&nbsp; +&nbsp;Tạo ra một <strong>kiểu quan hệ quốc tế mới</strong>: đối thoại thay cho đối đầu, tạo nền văn h&oacute;a ho&agrave;n b&igrave;nh cho nh&acirc;n loại</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">2.1.3.&nbsp;Những nguy&ecirc;n tắc x&acirc;y dựng đạo đức mới</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;<strong>N&oacute;i đi đ&ocirc;i với l&agrave;m, phải n&ecirc;u gương về đạo đức.</strong><br />\r\n	&nbsp; +&nbsp;N&oacute;i đi đ&ocirc;i với l&agrave;m l&agrave; <strong>đặc trưng bản chất của TTHCM </strong>- đạo đức CM; đối lập ho&agrave;n to&agrave;n với th&oacute;i đạo đức giả của GC b&oacute;c lột, n&oacute;i một đằng l&agrave;m một nẻo, thậm ch&iacute; n&oacute;i m&agrave; kh&ocirc;ng l&agrave;m.<br />\r\n	&nbsp; +&nbsp;N&ecirc;u gương về đạo đức l&agrave; một <strong>n&eacute;t đẹp của truyền thống VH phương Đ&ocirc;ng</strong>. N&oacute;i đi đ&ocirc;i với l&agrave;m gắn liền với n&ecirc;u gương về đạo đức.<br />\r\n	&nbsp; +&nbsp;Một nền đạo đức mới chỉ c&oacute; thể XD tr&ecirc;n một c&aacute;i nền rộng lớn, vững chắc, khi những chuẩn mực đạo đức trở th&agrave;nh h&agrave;nh vi đạo đức h&agrave;ng ng&agrave;y của to&agrave;n XH.<br />\r\n	-&nbsp;<strong>X&acirc;y đi đ&ocirc;i với chống</strong><br />\r\n	&nbsp; +&nbsp;Trong đời sống h&agrave;ng ng&agrave;y, những hiện tượng tốt - xấu, đ&uacute;ng - sai, c&aacute;i đạo đức v&agrave; c&aacute;i v&ocirc; đạo đức thường đan xen nhau, đối chọi nhau th&ocirc;ng qua h&agrave;nh vi của những con người kh&aacute;c nhau, thậm ch&iacute; trong c&ugrave;ng một con người. V&igrave; vậy, <strong>x&acirc;y phải đi đ&ocirc;i với chống, muốn x&acirc;y phải chống, chống nhằm mục đ&iacute;ch x&acirc;y</strong>.<br />\r\n	&nbsp; +&nbsp;Việc gi&aacute;o dục đạo đức phải tiến h&agrave;nh ph&ugrave; hợp với từng giai đoạn CM; ph&ugrave; hợp với lứa tuổi, ng&agrave;nh nghề, GC, tầng lớp v&agrave; từng MT; phải <strong>khơi dậy được &yacute; thức đạo đức</strong> ở mỗi con người.<br />\r\n	&nbsp; +&nbsp;X&acirc;y phải đi đ&ocirc;i với chống, với việc loại bỏ c&aacute;i sai, c&aacute;i xấu, c&aacute;i v&ocirc; đạo đức trong đời sống h&agrave;ng ng&agrave;y.<br />\r\n	-&nbsp;<strong>Phải tu dưỡng đạo đức suốt đời</strong><br />\r\n	&nbsp; +&nbsp;Một nền đạo đức mới chỉ c&oacute; thể được XD tr&ecirc;n <strong>cơ sở tự gi&aacute;c</strong> tu dưỡng đạo đức của mỗi người.<br />\r\n	&nbsp; +&nbsp;Đạo đức CM l&agrave; đ<strong>ạo đức dấn th&acirc;n, đạo đức trong h&agrave;nh động</strong> v&igrave; độc lập, tự do của DT, hạnh ph&uacute;c của ND; chỉ c&oacute; trong h&agrave;nh động, đạo đức CM mới bộc lộ r&otilde; những gi&aacute; trị của m&igrave;nh.<br />\r\n	&nbsp; +&nbsp;Đạo đức CM đ&ograve;i hỏi mỗi người phải tự gi&aacute;c r&egrave;n luyện th&ocirc;ng qua <strong>hoạt động thực tiễn, trong c&ocirc;ng việc, trong c&aacute;c mỗi quan hệ</strong>; phải <strong>tự nh&igrave;n thẳng, kh&ocirc;ng tự lừa dối</strong>; phải nh&igrave;n r&otilde; c&aacute;i hay, c&aacute;i tốt để ph&aacute;t huy v&agrave; thấy r&otilde; c&aacute;i xấu, c&aacute;i &aacute;c để khắc phục; tu dưỡng suốt đời như c&ocirc;ng việc rửa mặt h&agrave;ng ng&agrave;y.</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>2.2.&nbsp;Sinh vi&ecirc;n học tập v&agrave; l&agrave;m theo tư tưởng, tấm gương đạo đức HCM</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;<strong>Học trung với nước, hiếu với d&acirc;n</strong>, suốt đời đấu tranh cho sự nghiệp GPDT, GPGC, GP con người.<br />\r\n	-&nbsp;<strong>Học cần, kiệm, li&ecirc;m, ch&iacute;nh ch&iacute; c&ocirc;ng v&ocirc; tư</strong>, đời ri&ecirc;ng trong sang, nếp sống giản dị v&agrave; đức khi&ecirc;m tốn phi thường.<br />\r\n	-&nbsp;<strong>Học tấm gương về &yacute; ch&iacute; v&agrave; nghị lực tinh thần to lớn, quyết t&acirc;m vượt qua mọi thử th&aacute;ch</strong>, gian nguy để đạt được mục đ&iacute;ch cuộc sống.</span></span></span></li>\r\n	<li style=\"text-align:left;\"><span style=\"font-size:16px\"><span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>3. Tư tưởng HCM về x&acirc;y dựng con người mới</strong></u></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>3.1.&nbsp;Quan niệm của HCM về con người</strong></span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Con người được nh&igrave;n nhận như một ch&iacute;nh thể.<br />\r\n	-&nbsp;Con người cụ thể, lịch sử.<br />\r\n	-&nbsp;Bản chất con người mang t&iacute;nh x&atilde; hội</span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>3.2.&nbsp;Quan điểm của HCM về vai tr&ograve; của con người v&agrave; chiến lược &ldquo;trồng người&rdquo;.</strong></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">3.2.1.&nbsp;Quan điểm của HCM về vai tr&ograve; của con người.</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;Con người l&agrave; vốn qu&yacute; nhất, <strong>nh&acirc;n tố quyết định th&agrave;nh c&ocirc;ng của sự nghiệp CM.</strong><br />\r\n	&nbsp; +&nbsp;<strong>ND s&aacute;ng tạo ra mọi gi&aacute; trị vật chất v&agrave; tinh thần</strong>. HCM tổng kết ngắn gọn: D&acirc;n ta tốt lắm. D&acirc;n ta l&agrave; t&agrave;i năng, tr&iacute; tuệ v&agrave; s&aacute;ng tạo, họ biết giải quyết nhiều vấn đề một c&aacute;ch đơn giản, mau ch&oacute;ng, đầy đủ, đặc biệt l&agrave; l&ograve;ng sốt sắng, hang h&aacute;i của d&acirc;n để thực hiện con đường CM.<br />\r\n	&nbsp; +&nbsp;ND l&agrave; <strong>yếu tố quyết định th&agrave;nh c&ocirc;ng</strong> của CM.<br />\r\n	-&nbsp;Con người <strong>vừa l&agrave; mục ti&ecirc;u, vừa l&agrave; động lực CM</strong>; phải coi trọng, chăm s&oacute;c, ph&aacute;t huy nh&acirc;n tố con người.<br />\r\n	&nbsp; +&nbsp;ND vừa l&agrave; mục ti&ecirc;u, vừa l&agrave; động lực của CM.<br />\r\n	&nbsp; +&nbsp;Con người l&agrave; mục ti&ecirc;u của CM n&ecirc;n <strong>mọi chủ trương, đương lối, ch&iacute;nh s&aacute;ch của Đảng, CP đều v&igrave; lợi &iacute;ch ch&iacute;nh đ&aacute;ng của con người</strong>.Đảng l&agrave; l&atilde;nh đạo nhưng ND l&agrave; chủ. D&acirc;n như nước, bộ đội như c&aacute;, lực lượng bao nhi&ecirc;u l&agrave; nhờ ở d&acirc;n hết.<br />\r\n	&nbsp; +&nbsp;Chống c&aacute;c bệnh: xa ND, khinh ND, sợ ND; kh&ocirc;ng tin cậy ND; kh&ocirc;ng hiểu biết ND; kh&ocirc;ng y&ecirc;u thương ND.<br />\r\n	&nbsp; +&nbsp;Con người l&agrave; động lực CM được nh&igrave;n nhận tr&ecirc;n phạm vi cả nước, to&agrave;n thể đồng b&agrave;o, nhưng trước hết ở <strong>GCCN v&agrave; n&ocirc;ng d&acirc;n</strong>. Kh&ocirc;ng phải mọi con người đều trở th&agrave;nh động lực m&agrave; phải l&agrave; những con người được gi&aacute;c ngộ v&agrave; tổ chức.<br />\r\n	&nbsp; +&nbsp;Con người l&agrave; động lực chỉ c&oacute; thể thực hiện được khi <strong>hoạt động c&oacute; tổ chức, c&oacute; l&atilde;nh đạo</strong>. V&igrave; vậy, cần c&oacute; sự l&atilde;nh đạo của ĐCSVN.<br />\r\n	&nbsp; +&nbsp;<strong>Ki&ecirc;n quyết khắc phục</strong> kịp thời c&aacute;c phản động lực trong con người v&agrave; tổ chức. Đ&oacute; l&agrave; <strong>chủ nghĩa c&aacute; nh&acirc;n.</strong></span></span><br />\r\n	<span style=\"color:#e74c3c\"><span style=\"font-family:Times New Roman,Times,serif\">3.2.2.&nbsp;Quan điểm của HCM về chiến lược &ldquo;trồng người&rdquo;</span></span><br />\r\n	<span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\">-&nbsp;&ldquo;<strong>Trồng người</strong>&rdquo; l&agrave; y&ecirc;u cầu kh&aacute;ch quan, <strong>vừa cấp b&aacute;ch, vừa l&acirc;u d&agrave;i của CM.</strong><br />\r\n	&nbsp; +&nbsp;Tr&ecirc;n cơ sở khẳng định con người vừa l&agrave; mục ti&ecirc;u, vừa l&agrave; động lực CM, HCM rất quan t&acirc;m đến sự nghiệp GD, đ&agrave;o tạo, r&egrave;n luyện con người.<br />\r\n	&nbsp; +&nbsp;Con người phải được đặt v&agrave;o <strong>vị tr&iacute; trung t&acirc;m </strong>của sự ph&aacute;t triển. N&oacute; vừa nằm trong <strong>chiến lược ph&aacute;t triển </strong>KT - XH của đất nước với nghĩa rộng, vừa nằm trong <strong>chiến lược GD v&agrave; đ&agrave;o tạo</strong> theo nghĩa hẹp.<br />\r\n	-&nbsp;&ldquo;Muốn XD CNXH, trước hết cần c&oacute; những con người XHCN&rdquo;.<br />\r\n	&nbsp; +&nbsp;<strong>Con người XHCN phải do CNXH tạo ra</strong>. Phải đặt nhiệm vụ x&acirc;y dựng con người mới x&atilde; hội chủ nghĩa, l&agrave;m gương, l&ocirc;i cuốn x&atilde; hội.<br />\r\n	&nbsp; +&nbsp;Mỗi bước x&acirc;y dựng những con người như vậy l&agrave; một nấc thang <strong>x&acirc;y dựng chủ nghĩa x&atilde; hội.</strong><br />\r\n	&nbsp; +&nbsp;Quan niệm của HCM về con người mới x&atilde; hội chủ nghĩa c&oacute; <strong>2 mặt gắn b&oacute; chăt chẽ </strong>với nhau. Một l&agrave;, <strong>kế thừa </strong>những gi&aacute; trị tốt đẹp của con người truyền thống. Hai l&agrave;, <strong>h&igrave;nh th&agrave;nh</strong> những phẩm chất mới như: c&oacute; tư tưởng x&atilde; hội chủ nghĩa; c&oacute; đạo đức x&atilde; hội chủ nghĩa; c&oacute; tr&iacute; tuệ bản lĩnh để l&agrave;m chủ;...&nbsp;&nbsp;<br />\r\n	-&nbsp;Chiến lược &ldquo;trồng người&rdquo; l&agrave; một trọng t&acirc;m, một <strong>bộ phận hợp th&agrave;nh của chiến lược ph&aacute;t triển KT &ndash; XH. </strong><br />\r\n	&nbsp; +&nbsp;Để thực hiện trồng người cần nhiều biện ph&aacute;p, nhưng <strong>gi&aacute;o dục v&agrave; đ&agrave;o tạo l&agrave; biện ph&aacute;p quan trọng bậc nhất</strong> v&igrave; gi&aacute;o dục tốt tạo ra t&iacute;nh thiện v&agrave; ngươc lại.<br />\r\n	&nbsp; +&nbsp;Nội dung v&agrave; phương ph&aacute;p gi&aacute;o dục phải to&agrave;n diện cả về đức, tr&iacute;, thể, mỹ, phải đặt đạo đức, l&yacute; tưởng v&agrave; t&igrave;nh cảm c&aacute;ch mạng, lối sống x&atilde; hội chủ nghĩa l&ecirc;n đầu.<br />\r\n	&nbsp; +&nbsp;<strong>&ldquo;Trồng người&rdquo; l&agrave; c&ocirc;ng việc &ldquo;trăm năm&rdquo; kh&ocirc;ng thể n&oacute;ng vội , </strong>kh&ocirc;ng phải l&agrave;m một l&uacute;c l&agrave; xong cũng kh&ocirc;ng phải t&ugrave;y tiện, đến đ&acirc;u hay đến đ&oacute;.</span></span></span></li>\r\n</ol>', '1551234838', null, '0', '55', '1', '0', null, '0', null, null, null, null, null, null, '/images/course/55/lesson/1551245797_pic-7.jpg', null);
