INSERT INTO `lesson` VALUES ('248', 'Câu 16: Quan điểm của HCM về động lực xây dựng Chủ nghĩa xã hội ở VN ? Những yếu tố nào kìm hãm sự nghiệp xây dựng Chủ nghĩa xã hội ở VN?', '', '1551500669', null, '202', '55', '1', '202', '202', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('249', 'Câu 17: Quan điểm của HCM về loại hình quá độ lên Chủ nghĩa xã hội ở VN. Đặc điểm lớn nhất và mâu thuẫn cơ bản nhất trong thời kì quá độ lên Chủ nghĩa xã hội ở nước ta theo TTHCM là gì?', '', '1551501439', null, '202', '55', '1', '202', '202', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('250', 'Câu 18: Quan điểm của HCM về nội dung xây dựng Chủ nghĩa xã hội ở VN trong thời kì quá độ?', '', '1551502106', null, '202', '55', '1', '202', '202', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('251', 'Câu 19: Quan điểm của HCM về những mục tiêu cơ bản của Chủ nghĩa xã hội ở VN?', '', '1551504699', null, '202', '55', '1', '202', '202', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('253', 'Câu 20: Quan điểm của HCM về bản chất giai cấp của Đảng Cộng sản Việt Nam. Bàn về vấn đề “Đảng của ai”, HCM đã có quan điểm như thế nào?', '', '1551508465', null, '202', '55', '1', '202', '202', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('257', 'PHẦN I.1 LÝ THUYẾT TRỌNG TÂM CHƯƠNG 5', '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:16.0pt\"><img alt=\"\" src=\"/public/images/course/42/lesson/1553069440_hoc-thuyet-gia-tri-thang-du-min.png\" style=\"height:815px; width:800px\" />Chương V: Học thuyết gi&aacute; trị thặng dư</span></strong></span></span></p>\r\n\r\n<ol style=\"list-style-type:upper-roman\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:13.0pt\">Sự chuyển h&oacute;a của tiền th&agrave;nh tư bản</span></em></strong></span></span></li>\r\n</ol>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">C&ocirc;ng thức chung của tư bản: T &ndash; H &ndash; T&rsquo;</span></span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:240px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;T&rsquo; = T + ∆T</span></span></span></p>\r\n\r\n<ol start=\"2\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:#e74c3c\">M&acirc;u thuẫn của c&ocirc;ng thức chung củ tư bản: </span>&ldquo;tư bản kh&ocirc;ng thể xuất hiện từ lưu th&ocirc;ng v&agrave; cũng kh&ocirc;ng thể xuất hiện ở b&ecirc;n ngo&agrave;i lưu th&ocirc;ng. N&oacute; phải xuất hiện trong lưu th&ocirc;ng v&agrave; đồng thời kh&ocirc;ng phải trong lưu th&ocirc;ng.&rdquo;</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">H&agrave;ng h&oacute;a sức lao động </span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Sức lao động v&agrave; điều kiện để sức lao động trở th&agrave;nh h&agrave;ng h&oacute;a</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Sức lao động </span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Điều kiện:</span></span></span></p>\r\n\r\n<ul style=\"list-style-type:circle\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Tự do về th&acirc;n thể, l&agrave;m chủ được sức lao động của m&igrave;nh v&agrave; c&oacute; quyền b&aacute;n sức lao động của m&igrave;nh như một h&agrave;ng h&oacute;a</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Ngườic&oacute; sức lao động bị tước đoạt hết mọi tư liệu sản xuất, để tồn tại buộc phải b&aacute;n sức lao động</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Hai thuộc t&iacute;nh: </span><span style=\"font-size:13.0pt\">+ </span><span style=\"font-size:13.0pt\">Gi&aacute; trị: </span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-size:13.0pt\">+ </span><span style=\"font-size:13.0pt\">Gi&aacute; trị sử dụng</span></span></span></p>\r\n\r\n<ol start=\"2\" style=\"list-style-type:upper-roman\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:13.0pt\">Qu&aacute; tr&igrave;nh sản xuất ra gi&aacute; trị thặng dư</span></em></strong></span></span></li>\r\n</ol>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Sự thống nhất giữa qu&aacute; tr&igrave;nh sản xuất ra gi&aacute; trị sử dụng v&agrave; sản xuất ra gi&aacute; trị thặng dư</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Đặc điểm: c&ocirc;ng nh&acirc;n l&agrave;m việc dưới sự kiểm so&aacute;t của nh&agrave; tư bản, được nh&agrave; tư bản to&agrave;n quyền sử dụng</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Sản phẩm của người c&ocirc;ng nh&acirc;n tại ra thuộc về nh&agrave; tư bản</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&rarr; Vậy gi&aacute; trị thặng dư l&agrave; một bộ phận của gi&aacute; trị mới d&ocirc;i ra ngo&agrave;i gi&aacute; trị sức lao động do c&ocirc;ng nh&acirc;n l&agrave;m thu&ecirc; tạo ra v&agrave; bị nh&agrave; tư bản chiếm đ&oacute;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp; Ng&agrave;y lao động của c&ocirc;ng nh&acirc;n chia l&agrave;m 2 phần: phần ng&agrave;y lao động m&agrave; người c&ocirc;ng nh&acirc;n tạo ra một lượng gi&aacute; trị ngang với gi&aacute; trị sức lao động &ndash; thời gian lao động tất yếu, phần c&ograve;n lại gọi l&agrave; thời gian lao động thặng dư.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&rarr; Bản chất b&oacute;c lột của tư bản.</span></span></span></p>\r\n\r\n<ol start=\"2\">\r\n	<li style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Bản chất của tư bản. Tư bản bất biến v&agrave; tư bản khả biến</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Bản chất: phản &aacute;nh quan hệ sản xuất x&atilde; hội m&agrave; trong đ&oacute; giai cấp tư sản chiếm đoạt gi&aacute; trị thặng dư do giai cấp c&ocirc;ng nh&acirc;n s&aacute;ng tạo ra</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Tư bản bất biến v&agrave; tư bản khả biến</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Bộ phận tư bản biến th&agrave;nh tư liệu sản xuất m&agrave; gi&aacute; trị dduwwojc bảo to&agrave;n v&agrave; chuyển v&agrave;o sản phẩm, tức l&agrave; kh&ocirc;ng thay đổi về lượng gi&aacute; trị của n&oacute; được gọi l&agrave; tưu bản bất biến ( c ).</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Bộ phận tư bản biến th&agrave;nh sức lao động kh&ocirc;ng t&aacute;i hiện ra, nhưng th&ocirc;ng qua lao động trừu tượng của c&ocirc;ng nh&acirc;n l&agrave;m thu&ecirc; m&agrave; tăng l&ecirc;n, tức l&agrave; biến đổi về lượng được gọi l&agrave; tư bản khả biến ( v).</span></span></span></p>\r\n\r\n<ol start=\"3\">\r\n	<li style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Tỷ suất gi&aacute; trị thặng dư v&agrave; khối lượng gi&aacute; trị thặng dư.</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Tỷ suất gi&aacute; trị thặng dư</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; m&rsquo; = </span><em><span style=\"font-size:16.0pt\">m</span></em><em><span style=\"font-size:16.0pt\">v</span></em><em><span style=\"font-size:16.0pt\"> &times;100%</span></em><span style=\"font-size:11.0pt\"> </span>&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-size:16.0pt\">hoặc m&rsquo;= </span><em><span style=\"font-size:16.0pt\">thời gian lao động thặng dư</span></em><em><span style=\"font-size:16.0pt\">thời gian lao động tất yếu</span></em><em><span style=\"font-size:16.0pt\">&times;100%</span></em><span style=\"font-size:11.0pt\"> </span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Khối lượng gi&aacute; trị thặng dư</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">M = m&rsquo; . V&nbsp;&nbsp;&nbsp; hay&nbsp;&nbsp;&nbsp; M = </span><em><span style=\"font-size:13.0pt\">m</span></em><em><span style=\"font-size:13.0pt\">v</span></em><em><span style=\"font-size:13.0pt\"> . V</span></em><span style=\"font-size:11.0pt\"> </span></span></span></p>\r\n\r\n<ol start=\"4\">\r\n	<li style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Hai phương ph&aacute;p sản xuất gi&aacute; trị thặng dư</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Sản xuất gi&aacute; trị thặng dư tương đối: k&eacute;o d&agrave;i tuyệt đối ng&agrave;y lao động của c&ocirc;ng nh&acirc;n trong điều kiện thời gian lao động tất yếu kh&ocirc;ng đổi. </span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Sản xuất gi&aacute; trị thặng dư tương đối: r&uacute;t ngắn thời gian lao động tất yếu lại để k&eacute;o d&agrave;i một c&aacute;ch tương ứng thời gian lao động thặng dư tr&ecirc;n cơ sở tăng năng suất lao động x&atilde; hội trong điều kiện độ f=d&agrave;i ng&agrave;y lao động kh&ocirc;ng đổi.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Gi&aacute; trị thặng dư si&ecirc;u ngạch: l&agrave; phần gi&aacute; trị thặng dư thu được do tăng năng suất lao động c&aacute; biệt, l&agrave;m cho gi&aacute; trị c&aacute; biệt của h&agrave;ng h&oacute;a thấp hơn gi&aacute; trị h&agrave;ng h&oacute;a của n&oacute;.</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&rarr; Sản xuất gi&aacute; trị thặng dư &ndash; quy luật kinh tế tuyệt đối của chủ nghĩa tư bản.</span></span></span></p>\r\n\r\n<ol start=\"3\" style=\"list-style-type:upper-roman\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:13.0pt\">Tiền c&ocirc;ng trong chủ nghĩa tư bản</span></em></strong></span></span></li>\r\n</ol>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:#e74c3c\">Bản chất kinh tế:</span> l&agrave; h&igrave;nh thức biểu hiện bằng tiền của gi&aacute; trị sức lao động, hay gi&aacute; cả của sức lao động, nhưng lại biểu hiện ra b&ecirc;n ngo&agrave;i th&agrave;nh gi&aacute; cả của lao động.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Hai h&igrave;nh thức cơ bản của tiền c&ocirc;ng:</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Tiền c&ocirc;ng t&iacute;nh teo thời gian: tuy thuộc v&agrave;o thời gian lao động của c&ocirc;ng nh&acirc;n</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Tiền c&ocirc;ng t&iacute;nh theo sản phẩm: phụ thuộc v&agrave;o số lượng sản phẩm hay số lượng những bộ phận của sản phẩm m&agrave; c&ocirc;ng nh&acirc;n đ&atilde; sản xuất ra hoặc l&agrave; số lượng c&ocirc;ng việc đ&atilde; ho&agrave;n th&agrave;nh.</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"3\">\r\n	<li style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Tiền c&ocirc;ng danh nghĩa v&agrave; tiền c&ocirc;ng thực tế.</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Tiền c&ocirc;ng danh nghĩa l&agrave; số tiền m&agrave; người c&ocirc;ng nh&acirc;n nhận dduwwojc do b&aacute;n sức lao động của m&igrave;nh cho nh&agrave; tư bản.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Tiền c&ocirc;ng thực tế l&agrave; tiền c&ocirc;ng được biểu hiện bằng luwowjg=ng h&agrave;ng h&oacute;a ti&ecirc;u d&ugrave;ng v&agrave; dịch vụ m&agrave; c&ocirc;ng nh&acirc;n mua được bằng tiền c&ocirc;ng danh nghĩa/</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"4\" style=\"list-style-type:upper-roman\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:13.0pt\">Sự chuyển h&oacute;a của tư bản th&agrave;nh t&iacute;ch lũy tư bản</span></em></strong></span></span></li>\r\n</ol>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Thực chất v&agrave; động cơ của t&iacute;ch lũy tư bản</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Thực chất: l&agrave; sự chuyển h&oacute;a một phần gi&aacute; trị thặng dư th&agrave;nh tư bản, hay l&agrave; qu&aacute; tr&igrave;nh tư bản h&oacute;a gi&aacute; trị thặng dư</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Động cơ: quy luật gi&aacute; trị thặng dư</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">T&iacute;ch tụ tư bản v&agrave; tập trung tư bản. </span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">T&iacute;ch tụ tư bản: L&agrave; sự tăng th&ecirc;m quy m&ocirc; tư bản c&aacute; biệt bằng c&aacute;ch tư bản h&oacute;a gi&aacute; trị thặng dư.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Tập trung tư bản: L&agrave; sự hợp nhất một số tư bản nhỏ th&agrave;nh một tư bản c&aacute; biệt lớn.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">So s&aacute;nh:</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Điểm giống: Cả hai đều l&agrave;m tăng quy m&ocirc; tư bản c&aacute; biệt</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Điểm kh&aacute;c:</span></span></span></li>\r\n</ul>\r\n\r\n<table border=\"1\" cellspacing=\"0\" class=\"MsoTableGrid\" style=\"border-collapse:collapse; border:solid windowtext 1.0pt; margin-left:0px; width:481.3pt\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:26.5pt; width:240.65pt\">\r\n			<p style=\"margin-left:48px; text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">T&iacute;ch tụ tư bản</span></span></span></p>\r\n			</td>\r\n			<td style=\"height:26.5pt; width:240.65pt\">\r\n			<p style=\"margin-left:48px; text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Tập trung tư bản</span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:78.8pt; vertical-align:top; width:240.65pt\">\r\n			<ul>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Nguồn: l&agrave; thời gian lao động thặng dư (m) tư bản h&oacute;a.</span></span></span></li>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">L&agrave;m tư bản c&aacute; biệt v&agrave; tư bản x&atilde; hội tăng.</span></span></span></li>\r\n			</ul>\r\n\r\n			<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n			<ul>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Biểu hiện mối quan hệ giữa tư bản v&agrave; lao động</span></span></span></li>\r\n			</ul>\r\n			</td>\r\n			<td style=\"height:78.8pt; vertical-align:top; width:240.65pt\">\r\n			<ul>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Nguồn: l&agrave; c&aacute;c nh&agrave; tư bản đ&atilde; h&igrave;nh th&agrave;nh trong x&atilde; hội.</span></span></span></li>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">L&agrave; sự bố tr&iacute; lại bộ phận tư bản x&atilde; hội đ&atilde; c&oacute;, quy m&ocirc; tư bản x&atilde; hội kh&ocirc;ng đổi.</span></span></span></li>\r\n				<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Biểu hiện mối quan hệ giữa c&aacute;c nh&agrave; tư bản với nhau.</span></span></span></li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"margin-left:60px\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Mối quan hệ giữa t&iacute;ch lũy với t&iacute;ch tụ v&agrave; t&acirc;p trung tư bản:</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">T&iacute;ch tụ tư bản l&agrave;m tăng th&ecirc;m quy m&ocirc; v&agrave; sức mạnh của tư bản c&aacute; biệt. Ngược lại, tập trung tư bản tạo điều kiện thuận lợi để tăng cường b&oacute;c lột gi&aacute; trị thặng dư, n&ecirc;n đẩy nhanh t&iacute;ch tụ tư bản. Ảnh hưởng qua lại của t&iacute;ch tụ v&agrave; tập trung tư bản l&agrave;m cho t&iacute;ch lũy tư bản ng&agrave;y c&agrave;ng mạnh. Như vậy, qu&aacute; tr&igrave;nh t&iacute;ch lũy tư bản gắn liền với qu&aacute; tr&igrave;nh t&iacute;ch tụ v&agrave; tập trung tư bản ng&agrave;y c&agrave;ng tăng, l&agrave;m cho m&acirc;u thuẫn kinh tế cơ bản của chủ nghĩa tư bản ng&agrave;y c&agrave;ng s&acirc;u sắc.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ol start=\"3\">\r\n	<li style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Cấu tạo hữu cơ của tư bản</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Kh&aacute;i niệm: l&agrave; cấu tạo gi&aacute; trị của TB, do cấu tạo kỹ thuật của TB quyết định, phản &aacute;nh sự thay đổi của cấu tạo kỹ thuật của TB</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Phản &aacute;nh mqh của cấu tạo kỹ thuật v&agrave; cấu tạo gi&aacute; trị của TB</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">C&ocirc;ng thức: Cấu tạo hữu cơ của TB =&nbsp; </span><em><span style=\"font-size:13.0pt\">C</span></em><em><span style=\"font-size:13.0pt\">V</span></em><span style=\"font-size:11.0pt\"> </span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"5\" style=\"list-style-type:upper-roman\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:13.0pt\">Qu&aacute; tr&igrave;nh lưu th&ocirc;ng của tư bản v&agrave; gi&aacute; trị thặng dư</span></em></strong></span></span></li>\r\n</ol>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Tuần ho&agrave;n v&agrave; chu chuyển của tư bản</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Tuần ho&agrave;n của tư bản</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\">Giai đoạn 1: giai đoạn lưu th&ocirc;ng:</span></strong></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:96px; text-align:justify\"><img alt=\"\" src=\"/public/images/course/42/lesson/1553068729_giai-doan-1-ly-thuyet.PNG\" style=\"height:130px; width:248px\" /></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\">Giai đoạn 2: giai đoạn sản xuất:</span></strong></span></span></li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"/public/images/course/42/lesson/1553068658_giai-doan-2-ly-thuyet.PNG\" style=\"height:100px; width:274px\" /></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\">Giai đoạn 3: giai đoạn lưu th&ocirc;ng: </span></strong><span style=\"font-size:13.0pt\">H&rsquo; &ndash; T&rsquo;</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Chu chuyển của tư bản</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Tốc độ chu chuyển:&nbsp;&nbsp;&nbsp;<span class=\"math-tex\">\\(n = {CH \\over ch}\\)</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Trong đ&oacute;, (n) l&agrave; số v&ograve;ng (hay lần) chu chuyển của tư bản; (CH) l&agrave; thời gian trong năm; (ch) l&agrave; thời gian cho 1 v&ograve;ng của chu chuyển của tư bản.</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Tư bản cố định v&agrave; tư bản lưu động</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">T&aacute;i sản xuất v&agrave; lưu th&ocirc;ng của tư bản x&atilde; hội</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Tổng sản phẩm x&atilde; hội: C + V + M</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:144px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">C _&nbsp; tư bản bất biến</span></span></span></p>\r\n\r\n<p style=\"margin-left:144px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">V _ tư bản khả biến</span></span></span></p>\r\n\r\n<p style=\"margin-left:144px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">M _ gi&aacute; trị thặng dư</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Hai khu vực của nền sản xuất x&atilde; hội:<br />\r\n	+ Khu vực I: Sản xuất tư liệu sản xuất</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Khu vực II: Sản xuất tư liệu ti&ecirc;u d&ugrave;ng</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Điều kiện thực hiện sản phẩm x&atilde; hội trong t&aacute;i sản xuất giản đơn </span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:144px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">I (v + m) = II c</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Điều kiện thực hiện sản phẩm x&atilde; hội trong t&aacute;i sản xuất mở rộng</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:144px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">I (v + m) &gt; IIc</span></span></span></p>\r\n\r\n<ol start=\"3\">\r\n	<li style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Khủng hoảng kinh tế trong tư bản chủ nghĩa</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Bản chất: khủng hoảng ( kh&ocirc;ng phải thừa so với nhu cầu x&atilde; hội m&agrave; l&agrave; thừa so với sức mua c&oacute; hạn của quần ch&uacute;ng lao động.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Nguy&ecirc;n nh&acirc;n:</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ M&acirc;u thuẫn giữa t&iacute;nh tổ chức, t&iacute;nh kế hoạch trong từng x&iacute; nghiệp rất chặt chẽ v&agrave; khoa học với khuynh huwosng tự ph&aacute;t v&ocirc; ch&iacute;nh phủ trong to&agrave;n x&atilde; hội.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ M&acirc;u thuẫn giữa khuynh hướng t&iacute;ch lũy, mở rộng kh&ocirc;ng c&oacute; giưới hạn của tư bản so vơi sức mua ng&agrave;y c&agrave;ng eo hẹp của quần ch&uacute;ng do bị bần c&ugrave;ng h&oacute;a.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ M&acirc;u thuẫn đối kh&aacute;ng giữa giai cấp tư bản v&agrave; giai cấp lao động l&agrave;m thu&ecirc;.</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">T&iacute;nh chu kỳ của khủng hoảng kinh tế</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Khủng hoảng =&gt; Ti&ecirc;u điều =&gt; Phục hồi =&gt; Hưng thịnh</span></span></span></p>\r\n\r\n<ol start=\"6\" style=\"list-style-type:upper-roman\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:13.0pt\">C&aacute;c h&igrave;nh th&aacute;i tư bản v&agrave; c&aacute;c h&igrave;nh thức biểu hiện của gi&aacute; trị thặng dư</span></em></strong></span></span></li>\r\n</ol>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Chi ph&iacute; sản xuất tư bản chủ nghĩa, lợi nhuận v&agrave; tỷ suất lợi nhuận</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Chi ph&iacute; sản xuất tư bản chủ nghĩa: k = c + v</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Lợi nhuận: W = p + m </span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">p l&agrave; lợi nhuận</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Tỷ suất lợi nhuận</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; p&rsquo; = </span><em><span style=\"font-size:16.0pt\">m</span></em><em><span style=\"font-size:16.0pt\">c+v</span></em><em><span style=\"font-size:16.0pt\"> &times;100%</span></em><span style=\"font-size:11.0pt\"> </span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Những nh&acirc;n tố ảnh hưởng đến tỷ suất lợi nhuận</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Tỷ suất gi&aacute; trị thặng dư</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Cấu tạo hữu cơ của tư bản</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Tốc độ chu chuyển của tư bản</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Tiết kiệm tư bản bất biến</span></span></span></p>\r\n\r\n<ol start=\"2\">\r\n	<li style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Lợi nhuận b&igrave;nh qu&acirc;n v&agrave; gi&aacute; cả bất biến</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Cạnh tranh trong nội bộ ng&agrave;nh h&igrave;nh th&agrave;nh gi&aacute; cả thị trường </span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Cạnh tranh giữa c&aacute;c ng&agrave;nh h&igrave;nh th&agrave;nh lợi nhuận b&igrave;nh qu&acirc;n</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Tỷ suất lợi nhuận b&igrave;nh qu&acirc;n</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:13.0pt\">p&#39;</span></em><em><span style=\"font-size:13.0pt\">=</span></em><span style=\"font-size:13.0pt\">&Sigma;<em>M</em><em> </em><em>c</em><em>&aacute;</em><em>c</em><em> </em><em>ng</em><em>&agrave;</em><em>n</em><em>h</em></span><span style=\"font-size:13.0pt\">&Sigma;</span><em><span style=\"font-size:13.0pt\">c+v </span></em><em><span style=\"font-size:13.0pt\">c&aacute;c ng&agrave;nh</span></em><em><span style=\"font-size:13.0pt\">*100%</span></em><span style=\"font-size:11.0pt\"> </span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Lợi nhuận b&igrave;nh qu&acirc;n l&agrave; lợi nhuận m&agrave; c&aacute;c ng&agrave;nh nhận được như nhau bất kể cấu tạo hữu cơ như n&agrave;o. K&yacute; hiệu l&agrave; </span><em><span style=\"font-size:13.0pt\">p</span></em><span style=\"font-size:11.0pt\"> </span><span style=\"font-size:13.0pt\">.</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:192px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:13.0pt\">p</span></em><em><span style=\"font-size:13.0pt\">= </span></em><em><span style=\"font-size:13.0pt\">p</span></em><em><span style=\"font-size:13.0pt\">&#39;</span></em><span style=\"font-size:11.0pt\"> </span>&nbsp;<em><span style=\"font-size:13.0pt\">&times;k</span></em><span style=\"font-size:11.0pt\"> </span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Gi&aacute; cả sản xuất = k + </span><em><span style=\"font-size:13.0pt\">p</span></em><span style=\"font-size:11.0pt\"> </span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"3\">\r\n	<li style=\"text-align:justify\"><span style=\"color:#e74c3c\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Sự ph&acirc;n chia gi&aacute; trị thặng dư giữa c&aacute;c giai cấp b&oacute;c lột trong chủ nghĩa tư bản</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Tư bản thương nghiệp v&agrave; lợi nhuận thương nghiệp:</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Tư bản thương nghiệp l&agrave; một bộ phận của tư bản c&ocirc;ng nghiệp được t&aacute;ch ra v&agrave; phục vụ qu&aacute; tr&igrave;nh lưu th&ocirc;ng h&agrave;ng h&oacute;a của tư bản c&ocirc;ng nghiệp.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Lợi nhuận thương nghiệp l&agrave; một phần của gi&aacute; trị thặng dư được s&aacute;ng tạo ra trong lĩnh vực sản xuất v&agrave; do tư bản c&ocirc;ng nghiệp nhượng lại cho tư bản thuwong nghiệp, để tư bản thương nghiệp thực hiện chức năng lưu th&ocirc;ng.</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Tư bản cho vay v&agrave; lợi tức cho vay</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Tư bản cho vay l&agrave; tư bản tiền tệ tạm thời nh&agrave;n rỗi m&agrave; người chủ sở hữu n&oacute; cho nguwoif kh&aacute;c sử dụng trong một thời gian nhằm nhận được số tiền lời nhất định. Số tiền lời đ&oacute; l&agrave; lợi tức ( z ).</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp; Vận động theo c&ocirc;ng thức T &ndash; T&rsquo;, trong đ&oacute; T&rsquo; = T + z</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp; Điều kiện: 0 &lt; z &lt; </span><em><span style=\"font-size:13.0pt\">p</span></em><span style=\"font-size:11.0pt\"> </span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Tỷ suất lợi tức</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-size:16.0pt\">z&rsquo; = </span><em><span style=\"font-size:16.0pt\">z</span></em><em><span style=\"font-size:16.0pt\">Tổng tư bản cho vay</span></em><em><span style=\"font-size:16.0pt\"> &times;100%</span></em><span style=\"font-size:11.0pt\"> </span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">C&aacute;c yếu tố ảnh hưởng đến tỷ suất lợi tức</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Tỷ suất lợi nhuận b&igrave;nh qu&acirc;n</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Tỷ lệ ph&acirc;n chia lợi nhuận b&igrave;nh qu&acirc;n th&agrave;nh lợi tức v&agrave; lợi nhuận của nh&agrave; tư bản hoạt động</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Quan hệ cung cầu về tư bản cho vay</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Quan hệ t&iacute;n dụng tư bản chủ nghĩa . Ng&acirc;n h&agrave;ng v&agrave; lợi nhuận ng&acirc;n h&agrave;ng.</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ T&iacute;n dụng thương nghiệp: l&agrave; h&igrave;nh thức t&iacute;n dụng giữa c&aacute;c nh&agrave; tư bản trực tiếp kinh doanh, mua b&aacute;n chịu h&agrave;ng h&oacute;a với nhau.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ T&iacute;n dụng ng&acirc;n h&agrave;ng: l&agrave; quan hệ vay mượn th&ocirc;ng qua ng&acirc;n h&agrave;ng l&agrave;m m&ocirc; giới.</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Ng&acirc;n h&agrave;ng v&agrave; lợi nhuận ng&acirc;n h&agrave;ng</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Ng&acirc;n h&agrave;ng: chỉ l&agrave; trung gian thực hiện hai nghiệp vụ nhận gửi v&agrave; cho vay.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Trong cạnh tranh, lợi nhuận ng&acirc;n h&agrave;ng ngang bằng với lợi nhuận b&igrave;nh qu&acirc;n.</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Ph&acirc;n biệt tư bản ng&acirc;n h&agrave;ng với tư bản cho vay</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">C&ocirc;ng ty cổ phần, tư bản giả v&agrave; thị trường chứng kho&aacute;n</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ C&ocirc;ng ty cổ phần được h&igrave;nh th&agrave;nh bằng con đường tập trung vốn th&ocirc;ng qua việc ph&aacute;t h&agrave;nh cổ phiếu v&agrave; tr&aacute;i phiếu.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Tư bản giả tồn tại dưới h&igrave;nh thức c&aacute;c chứng kho&aacute;n c&oacute; gi&aacute; v&agrave; mang lại thu nhập cho người sở hữu c&aacute;c chứng kho&aacute;n đ&oacute;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Thị trường chứng kho&aacute;n l&agrave; thị trường mua b&aacute;n tr&aacute;i phiếu, cổ phiếu, chứng kho&aacute;n.</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Quan hệ sản xuất tư bản chủ nghĩa trong n&ocirc;ng nghiệp v&agrave; địa t&ocirc; tư bản chủ nghĩa</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Trong n&ocirc;ng nghiệp, h&igrave;nh thức canh t&aacute;c phong kiến dần chuyển sang h&igrave;nh thức tư bản chủ nghĩa, thu&ecirc; nh&acirc;n c&ocirc;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Địa t&ocirc; tư bản chủ nghĩa l&agrave; phần gi&aacute; trị thặng dư c&ograve;n lại sau khi đ&atilde; khấu trừ đi phần lợi nhuận b&igrave;nh qu&acirc;n m&agrave; tư bản kinh doanh n&ocirc;ng nghiệp phải nộp cho địa chủ. </span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Địa t&ocirc; ch&ecirc;nh lệch l&agrave; phần địa t&ocirc; thu được ở tr&ecirc;n những ruộng đất c&oacute; lợi thế về điều kiện sản xuất.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp; Địa t&ocirc; ch&ecirc;nh lệch = Gi&aacute; cả sản xuất chung &ndash; gi&aacute; cả sản xuất c&aacute; biệt</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Địa t&ocirc; tuyệt đối l&agrave; loại địa t&ocirc; m&agrave; tất cả c&aacute;c nh&agrave; tư bản kinh doanh n&ocirc;ng nghiệp đều phải nộp cho địa chủ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Gi&aacute; cả ruộng đất: l&agrave; gi&aacute; mua quyền thu địa t&ocirc; do ruộng đất mang lại theo tỷ suất lợi tức hiện h&agrave;nh.</span></span></span></p>', '1551582308', null, '0', '42', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('258', 'PHẦN I.2 LÝ THUYẾT TRỌNG TÂM CHƯƠNG 6', '<p><strong><img alt=\"\" src=\"/public/images/course/42/lesson/1553069573_hoc-thuyet-ve-chu-nghia-tu-ban-doc-quyen-va-chu-nghia-tu-ban-doc-quyen-trong-nuoc-min.png\" style=\"height:618px; width:800px\" />Chương VI: Học thuyết về chủ nghĩa tư bản độc quyền v&agrave; chủ nghĩa tư bản độc quyền nh&agrave; nước</strong></p>\r\n\r\n<p><strong><em>I. Chủ nghĩa tư bản độc quyền</em></strong></p>\r\n\r\n<ol>\r\n	<li><span style=\"color:#e74c3c\">Những nguy&ecirc;n nh&acirc;n chuyển biến của chủ nghĩa tư bản tự do cạnh tranh th&agrave;nh chủ nghĩa tư bản độc quyền</span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Thứ nhất, sự ph&aacute;t triển của lực lượng sản xuất</li>\r\n	<li>Thứ hai, tăng năng suất lao động, tăng khả năng t&iacute;ch lũy tư bản, th&uacute;c đấy ph&aacute;t triển sản xuất lớn.</li>\r\n	<li>Thứ ba, cơ cấu kinh tế biến đổi theo hướng tập trung sản xuất quy m&ocirc; lớn</li>\r\n	<li>Thứ tư, c&aacute;c nh&agrave; tư bản lớn l&agrave;m gi&agrave;u với số tư bản tập trung, quy m&ocirc; x&iacute; nghiệp ng&agrave;y c&agrave;ng lớn.</li>\r\n	<li>Thứ năm, cuộc khủng hoảng kinh tế 1873 l&agrave;m thuc đẩy nhanh qu&aacute; tr&igrave;nh t&iacute;ch tụ v&agrave; tập trung tư bản.</li>\r\n	<li>Thứ s&aacute;u, sự ph&aacute;t triển của hệ thống t&iacute;n dụng tư bản</li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"color:#e74c3c\">Những đặc điểm kinh tế cơ bản của chủ nghĩa tư bản độc quyền</span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Những h&igrave;nh thức độc quyền cơ bản: C&aacute;cten, Xanhđica, Tơrớt, C&ocirc;ngx&oacute;ocxiom,&hellip;</li>\r\n	<li>Tư bản t&agrave;i ch&iacute;nh v&agrave; bọn đầu xỏ t&agrave;i ch&iacute;nh</li>\r\n</ul>\r\n\r\n<p>+ Tư bản t&agrave;i ch&iacute;nh l&agrave; kết quả của sự hợp nhất giữa tư bản ng&acirc;n h&agrave;ng của một số &iacute;t ng&acirc;n h&agrave;ng độc quyền lớn nhất, với tư bản của những li&ecirc;n minh độc quyền c&aacute;c nh&agrave; c&ocirc;ng nghiệp.</p>\r\n\r\n<p>+ Sự ph&aacute;t triển của tư bản t&agrave;i ch&iacute;nh dẫn đến sự h&igrave;nh th&agrave;nh một nh&oacute;m nhỏ độc quyền, chi phối to&agrave;n bộ đời song kinh tế v&agrave; ch&iacute;nh trị của to&agrave;n x&atilde; hội tư bản gọi l&agrave; c&aacute;c đầu sỏ t&agrave;i ch&iacute;nh.</p>\r\n\r\n<ul>\r\n	<li>Xuất khẩu tư bản: c&oacute; hai h&igrave;nh thức</li>\r\n</ul>\r\n\r\n<p>+ Xuất khẩu tư bản hoạt động ( đầu tư trực tiếp) l&agrave; đưa tư bản ra nước ngo&agrave;i để trực tiếp kinh doanh thu lợi nhuận cao.</p>\r\n\r\n<p>+ Xuất khẩu tư bản cho vay ( đầu tư gi&aacute;n tieespP l&agrave; cho vay để thu lợi tức.</p>\r\n\r\n<ul>\r\n	<li>Sự ph&acirc;n chia thế giới về kinh tế giữa c&aacute;c tổ chức độc quyền: h&igrave;nh th&agrave;nh c&aacute;c li&ecirc;n minh độc quyền quốc tế dưới dạng c&aacute;cten, xanhđica, tơrớt quốc tế,&hellip;</li>\r\n	<li>Sự ph&acirc;n chia thế giới về l&atilde;nh thổ giữa c&aacute;c cường quốc đế quốc: c&aacute;c cuwofng quốc đế quốc ra sức x&acirc;m chiếm thuộc địa. Sự ph&acirc;n chia l&atilde;nh thổ v&agrave; ph&aacute;t triển kh&ocirc;ng đều của chủ nghĩa tư bản &rarr; đấu tranh đ&ograve;i chia lại thế giới.</li>\r\n</ul>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"color:#e74c3c\">Sự hoạt động của quy luật gi&aacute; trị v&agrave; quy luật gi&aacute; trị thặng dư trong giai đoạn chủ nghĩa tư bản độc quyền.</span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Quan hệ giữa độc quyền v&agrave; cạnh tranh trong giai đoạn chủ nghĩa tư bản độc quyền</li>\r\n</ul>\r\n\r\n<p>+ Độc quyền sinh ra từ cạnh tranh tự do, đối lập với cạnh tranh tự do</p>\r\n\r\n<p>+ Sự xuất hiện của độc quyền kh&ocirc;ng thủ ti&ecirc;u được cạnh tranh tự do m&agrave; l&agrave;m cho cạnh tranh trở n&ecirc;n đa dạng, gay gắt v&agrave; c&oacute; sức ph&aacute; hoại to lớn.</p>\r\n\r\n<ul>\r\n	<li>Biểu hiện hoạt động của quy luậy gi&aacute; trị v&agrave; quy luật gi&aacute; trị thặng dư trong giai đoạn chủ nghĩa tư bản độc quyền.</li>\r\n</ul>\r\n\r\n<p>+ Quy luật gi&aacute; trị biểu hiện th&agrave;nh quy luật gi&aacute; cả độc quyền</p>\r\n\r\n<p>+ Quy luật gi&aacute; trị thặng dư biếu hiện th&agrave;nh biểu hiện th&agrave;nh quy luật lợi nhuận độc quyền cao.</p>\r\n\r\n<p><strong><em>II. Chủ nghĩa tư bản độc quyền nh&agrave; nước</em></strong></p>\r\n\r\n<ol>\r\n	<li><span style=\"color:#e74c3c\">Nguy&ecirc;n&nbsp; nh&acirc;n h&igrave;nh th&agrave;nh v&agrave; bản chất</span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Nguy&ecirc;n nh&acirc;n</li>\r\n</ul>\r\n\r\n<p><strong>+ </strong>T&iacute;ch tụ tập trung tư bản ph&aacute;t triển đẻ ra những cơ cấu kinh tế quy m&ocirc; lớn đ&ograve;i hỏi 1 sự điều tiết x&atilde; hội đối với sản xuất v&agrave; ph&acirc;n phối từ một trung t&acirc;m.</p>\r\n\r\n<p>+ Sự ph&aacute;t triển cao của lực lượng sản xuất xuất hiện một số ng&agrave;nh m&agrave; c&aacute;c tổ chức độc quyền tư nh&acirc;n kh&ocirc;ng thể hoặc kh&ocirc;ng muốn kinh doanh.</p>\r\n\r\n<p>+ Sự thống trị của độc quyền đ&atilde; l&agrave;m s&acirc;u sắc th&ecirc;m sự đối kh&aacute;ng giữa giai cấp tư sản với giai cấp v&ocirc; sản v&agrave; nh&acirc;n d&acirc;n lao động &rarr; c&oacute; ch&iacute;nh s&aacute;ch để xoa dịu m&acirc;u thuẫn</p>\r\n\r\n<p>+ Xu hướng quốc tế h&oacute;a đời sống kinh tế, sự b&agrave;nh trướng của c&aacute;c li&ecirc;n minh độc quyền quốc tế vấp phải những h&agrave;ng r&agrave;o quốc gia d&acirc;n tộc v&agrave; xung đột lợi &iacute;ch với c&aacute;c đối thủ</p>\r\n\r\n<ul>\r\n	<li>Bản chất</li>\r\n</ul>\r\n\r\n<p>+ L&agrave; sự kết hợp sức mạnh của c&aacute;c tổ chức độc quyền tư nh&acirc;n với sức mạnh của c&aacute;c nh&agrave; nước tư sản th&agrave;nh một thiết chế v&agrave; thể chế thống nhất.</p>\r\n\r\n<p>+ Tronh đ&oacute;, nh&agrave; nước tư sản bị phụ thuộc v&agrave;o c&aacute;c tổ chức độc quyền v&agrave; can thiệp v&agrave;o c&aacute;c qu&aacute; tr&igrave;nh kinh tế nhằm bảo vệ lợi &iacute;ch của c&aacute;c tổ chức độc quyền v&agrave; cứu nguy cho chủ nghĩa tư bản.</p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"color:#e74c3c\">Những biểu hiện chủ yếu của chủ nghĩa tư bản độc quyền nh&agrave; nước.</span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Sự kết hợp về nh&acirc;n sự giữa tổ chức độc quyền v&agrave; nh&agrave; nước</li>\r\n</ul>\r\n\r\n<p>+ H&igrave;nh th&agrave;nh c&aacute;c hội chủ x&iacute; nghiệp: Li&ecirc;n đo&agrave;n c&ocirc;ng nghiệp Italia, Tổ chức li&ecirc;n hiệp c&ocirc;ng nghiệp Đức,&hellip;</p>\r\n\r\n<p>+ Trở th&agrave;nh lực lượng ch&iacute;nh trị, kinh tế to lớn, hoạt động như l&agrave; c&aacute;c cơ quant ham mưu cho nh&agrave; nước</p>\r\n\r\n<p>+ Th&ocirc;ng qua c&aacute;c hội chủ, đại biểu của c&aacute;c tổ chức độc quyền tham gia v&agrave;o bộ m&aacute;y nh&agrave; nước. Đồng thời c&aacute;c quan chức vfa nh&acirc;n vi&ecirc;n ch&iacute;nh phủ được c&agrave;i v&agrave;o c&aacute;c ban quản trị của c&aacute;c tổ chức.</p>\r\n\r\n<ul>\r\n	<li>Sự h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển sở hữu tư bản độc quyền nh&agrave; nước.</li>\r\n</ul>\r\n\r\n<p>+ Được h&igrave;nh th&agrave;nh dưới nhiều h&igrave;nh thức kh&aacute;c nhau: x&acirc;y dựng x&iacute; nghiệp nh&agrave; nước bằng vốn ng&acirc;n s&aacute;ch, quốc hữu h&oacute;a c&aacute;c x&iacute; nghiệp tư nh&acirc;n, nh&agrave; nước mua cổ phần của c&aacute;c x&iacute; nghiệp tư nh&acirc;n&hellip;</p>\r\n\r\n<p>+ Sở hữu nh&agrave; nước thực hiện c&aacute;c chức năng quan trọng:</p>\r\n\r\n<ul>\r\n	<li>Mở rộng sản xuất tư bản chủ nghĩa, bảo đảm địa b&agrave;n rộng lớn cho chủ nghĩa tư bản</li>\r\n	<li>Giải ph&oacute;ng tư bản của tổ chức độc quyền từ những ng&agrave;nh &iacute;t l&atilde;i sang những ng&agrave;nh kinh doanh c&oacute; hiệu quả hơn.</li>\r\n	<li>L&agrave;m chỗ dựa kinh tế cho nh&agrave; nước.</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Sự điều tiết kinh tế của nh&agrave; nước tư sản</li>\r\n</ul>\r\n\r\n<p>+ Hệ thống điều tiết kinh tế l&agrave; một tổng thể những thiết chế v&agrave; thể chế kinh tế của nh&agrave; nước</p>\r\n\r\n<p>+ Bao gồm nhiều ch&iacute;nh s&aacute;ch: ch&iacute;nh s&aacute;ch chống khủng hoảng chu k&igrave;, chống lạm ph&aacute;t, ch&iacute;nh s&aacute;ch tăng trưởng kinh tế,&hellip;</p>\r\n\r\n<p>+ Th&ocirc;ng qua c&aacute;c c&ocirc;ng cụ như: ng&acirc;n s&aacute;ch, thuế, hệ thống tiền tệ - t&iacute;n dụng,...</p>\r\n\r\n<p><strong><em>III. Chủ nghĩa tư bản ng&agrave;y nay v&agrave; những biểu hiện mới của n&oacute;.</em></strong></p>\r\n\r\n<ol>\r\n	<li><span style=\"color:#e74c3c\">Những biểu hiện mới trong năm đặc điểm của chủ nghĩa tư bản độc quyền</span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Tập trung sản xuất v&agrave; h&igrave;nh thức độc quyền mới: sự xuất hiện những c&ocirc;ng ty độc quyền xuy&ecirc;n quốc gia b&ecirc;n cạnh sự ph&aacute;t triển của c&aacute;c x&iacute; nghiệp vừa v&agrave; nhỏ</li>\r\n	<li>Sự thay đổi trong c&aacute;c h&igrave;nh thức tổ chức v&agrave; cơ chế thống trị của tư bản t&agrave;i ch&iacute;nh: phạm vi li&ecirc;n kết được mở rộng, họ c&oacute; nhiều người đại diện hơn trong ch&iacute;nh phủ,&nbsp; th&agrave;nh lập c&aacute;c ng&acirc;n h&agrave;ng đa quốc gia v&agrave; xuy&ecirc;n quốc gia,...</li>\r\n	<li>Xuất khẩu tư bản vẫn l&agrave; cơ sở của độc quyền quốc tế sau chiến tranh, nhung quy m&ocirc;, chiều hướng v&agrave; kết cấu của việc xuất khẩu tư bản đ&atilde; c&oacute; bước ph&aacute;t triển mới.</li>\r\n	<li>Sự ph&acirc;n chia thế giới giữa c&aacute;c li&ecirc;n minh của chủ nghĩa tư bản: xu huwosng quốc tế h&oacute;a, to&agrave;n cầu h&oacute;a ng&agrave;y c&agrave;ng tăng b&ecirc;n cạnh xu hướng khu vực h&oacute;a nền kinh tế.</li>\r\n	<li>Sự ph&acirc;n chia thế giới giữa c&aacute;c cường quốc vẫn tiếp tục dưới những h&igrave;nh th&uacute;c cạnh tranh vầ thống trị mới: ciến tranh thương mại, chiến tranh sắc tộc, t&ocirc;n gi&aacute;o,...</li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"color:#e74c3c\">Những biểu hiện mới trong cơ chế điều tiết kinh tế của chủ nghĩa tư bản độc quyền nh&agrave; nước.</span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Tỷ trọng kinh tế nh&agrave; nước tăng</li>\r\n	<li>Kinh tế thuộc nh&agrave; nước v&agrave; tư nh&acirc;n kết hợp tăng l&ecirc;n mạnh mẽ.</li>\r\n	<li>Chi ti&ecirc;u t&agrave;i ch&iacute;nh để điều tiết qu&aacute; tr&igrave;nh t&aacute;i sản xuất x&atilde; hội tăng l&ecirc;n</li>\r\n	<li>Phương thức điều tiết của nh&agrave; nước linh hoạt, mềm dẻo hơn với phạm vi rộng hơn.</li>\r\n</ul>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"color:#e74c3c\">Những n&eacute;t mới trong sự ph&aacute;t triển của chủ nghĩa tư bản hiện đại</span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Sự ph&aacute;t triển nhảy vọt về lực lượng sản xuất:</li>\r\n</ul>\r\n\r\n<p>+ C&aacute;ch mạng c&ocirc;ng nghệ th&ocirc;ng tin v&agrave; c&ocirc;ng nghệ cao ph&aacute;t triển mạnh mẽ</p>\r\n\r\n<p>+ Gi&aacute;o dục &ndash; đ&agrave;o tạo được tăng cường &rarr; n&acirc;ng cao chất lượng nguồn nh&acirc;n lực</p>\r\n\r\n<ul>\r\n	<li>C&oacute; xu hướng chuyển từ kinh tế c&ocirc;ng nghiệp sang kinh tế tri thức</li>\r\n	<li>Sự điều chỉnh về quan hệ sản xuất v&agrave; quan hệ giai cấp</li>\r\n</ul>\r\n\r\n<p>+ Sự ph&acirc;n t&aacute;n quyền nắm cổ phiếu tăng</p>\r\n\r\n<p>+ Kết cấu giai cấp c&oacute; nhiều biến đổi, c&aacute;c giai cấp, tầng lớp, đo&agrave;n thể x&atilde; hội v&agrave; tập đo&agrave;n c&ugrave;ng tồn tại v&agrave; t&aacute;c động lẫn nhau.</p>\r\n\r\n<p>+ Thu nhập bằng tiền lương của người lao động tăng.</p>\r\n\r\n<ul>\r\n	<li>Thể chế quản l&yacute; kinh doanh trong nội bộ doanh nghiệp c&oacute; những biến đổi</li>\r\n</ul>\r\n\r\n<p>+ Thiết lập cơ cấu tổ chức h&agrave;ng ngang v&agrave; mạng lưới</p>\r\n\r\n<p>+ D&ugrave;ng c&ocirc;ng nghệ cao cải c&aacute;ch cơ chế quản l&yacute; nh&agrave; nước</p>\r\n\r\n<p>+ Cải c&aacute;ch quản l&yacute;, lấy con người l&agrave;m gốc, n&acirc;ng cao kỹ năng, tay nghề</p>\r\n\r\n<p>+ C&aacute;c doanh nghiệp lớn v&agrave; nhỏ h&oacute;a c&ugrave;ng hỗ trợ nhau tồn tại.</p>\r\n\r\n<ul>\r\n	<li>Điều tiết vĩ m&ocirc; của nh&agrave; nước được tăng cường</li>\r\n	<li>C&aacute;c c&ocirc;ng ty xuy&ecirc;n quốc gia c&oacute; vai tr&ograve; ng&agrave;y c&agrave;ng quan trọng trong hệ thống kinh tế tư bản chủ nghĩa, l&agrave; lực lượng chủ yếu th&uacute;c đấy to&agrave;n cầu h&oacute;a kinh tế.</li>\r\n	<li>Điều tiết v&agrave; phối hợp quốc tế được tăng cường.</li>\r\n</ul>\r\n\r\n<p><strong>IV. Vai tr&ograve;, hạn chế v&agrave; xu hướng vận động của chủ nghĩa tư bản</strong></p>\r\n\r\n<p><strong>1.<span style=\"color:#e74c3c\">&nbsp;</span></strong><span style=\"color:#e74c3c\">Vai tr&ograve; của chủ nghĩa tư bản đối với sự ph&aacute;t triển của nền sản xuất x&atilde; hội</span></p>\r\n\r\n<ul>\r\n	<li>Giải ph&oacute;ng lo&agrave;i người khỏi x&atilde; hội phong kiến; chuyển từ nền kinh tế tự cung tự cấp sang kinh tế h&agrave;ng h&oacute;a tư bản chủ nghĩa, từ sản xuất nhỏ th&agrave;nh sản xuất lớn.</li>\r\n	<li>Ph&aacute;t triển lực lượng sản xuất</li>\r\n	<li>Thực hiện x&atilde; hội h&oacute;a sản xuất</li>\r\n	<li>Tổ chức sản xuất c&ocirc;ng xưởng, x&acirc;y dựng được t&aacute;c phong c&ocirc;ng nghiệp</li>\r\n	<li>Thiết lập n&ecirc;n nền d&acirc;n chủ tư sản</li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"color:#e74c3c\">Hạn chế của chủ nghĩa tư bản</span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Sự ra đời của chủ nghĩa tư bản gắn liền với sự &aacute;p bức, b&oacute;c lột</li>\r\n	<li>Cơ sở cho sự tồn tại v&agrave; ph&aacute;t triển của chủ nghĩa tư bản l&agrave; quan hệ b&oacute;c lột của c&aacute;c nh&agrave; tư bản với c&ocirc;ng nh&acirc;n l&agrave;m thu&ecirc;.</li>\r\n	<li>C&aacute;c cuộc chiến tranh thế giới đ&atilde; để lại nhiều hậu quả nặng nề</li>\r\n	<li>Tạo khoảng c&aacute;ch giữa c&aacute;c nước gi&agrave;u v&agrave; c&aacute;c nước ngh&egrave;o</li>\r\n</ul>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"color:#e74c3c\">Xu hướng vận động</span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Phương thức sản xuất tư bản chủ nghĩa sẽ bị thủ ti&ecirc;u v&agrave; một phương thức sản xuất mới &ndash; phương thức sản xuất cộng sản chủ nghĩa sẽ ra đời v&agrave; phủ định phưong thức sản xuất tư bản chủ nghĩa</li>\r\n	<li>Chủ nghĩa tư bản vẫn tiếp tục điều chỉnh để th&iacute;ch ứng trước những biến động, m&acirc;u thuẫn b&ecirc;n trong v&agrave; ngo&agrave;i nước.</li>\r\n	<li>Những m&acirc;u thuẫn vẫn tồn tại: m&acirc;u thuẫn giai cấp, m&acirc;u thuẫn giữa năng lực sản xuất v&ocirc; hạn v&agrave; khả năng thanh t&oacute;n hạn chế,&hellip; đe dọa sự tồn vong của chủ nghĩa tư bản.</li>\r\n</ul>', '1551582465', null, '0', '42', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('259', 'PHẦN I.3 LÝ THUYẾT TRỌNG TÂM CHƯƠNG 7', '<p><strong>CHƯƠNG VII. SỨ M&Ecirc;̣NH LỊCH SỬ CỦA GIAI C&Acirc;́P C&Ocirc;NG NH&Acirc;N VÀ CÁCH MẠNG XÃ H&Ocirc;̣I CHỦ NGHĨA</strong></p>\r\n\r\n<p><strong>I. SỨ M&Ecirc;̣NH LỊCH SỬ CỦA GIAI C&Acirc;́P C&Ocirc;NG NH&Acirc;N</strong></p>\r\n\r\n<ol>\r\n	<li><strong>Khái ni&ecirc;̣m giai c&acirc;́p c&ocirc;ng nh&acirc;n và sứ m&ecirc;̣nh lịch sử của giai c&acirc;́p.</strong></li>\r\n</ol>\r\n\r\n<p><span style=\"color:#e74c3c\">a, Khái ni&ecirc;̣m: Giai c&acirc;́p c&ocirc;ng nh&acirc;n là</span></p>\r\n\r\n<p>&nbsp;- m&ocirc;̣t t&acirc;̣p đoàn xã h&ocirc;̣i &ocirc;̉n định</p>\r\n\r\n<p>&nbsp;- hình thành và phát tri&ecirc;̉n cùng với sự phát tri&ecirc;̉n của c&ocirc;ng nghi&ecirc;̣p hi&ecirc;̣n đại, với nhịp đ&ocirc;̣ phát tri&ecirc;̉n của lực lượng sản xu&acirc;́t có tính ch&acirc;́t xã h&ocirc;̣i hóa ngày càng cao</p>\r\n\r\n<p>&nbsp;- là lực lượng cơ bản ti&ecirc;n ti&ecirc;́n trong quy trình c&ocirc;ng ngh&ecirc;̣, dịch vụ c&ocirc;ng nghi&ecirc;̣p trực ti&ecirc;́p hoặc gián ti&ecirc;́p tham gia vào quá trình sản xu&acirc;́t, tái sản xu&acirc;́t ra của cải v&acirc;̣t ch&acirc;́t và cải tạo các quan h&ecirc;̣ xã h&ocirc;̣i</p>\r\n\r\n<p>- đại bi&ecirc;̉u cho lực lượng sản xu&acirc;́t và phương thức sản xu&acirc;́t ti&ecirc;n ti&ecirc;́n trong thời đại hi&ecirc;̣n nay.</p>\r\n\r\n<p><span style=\"color:#e74c3c\">b, N&ocirc;̣i dung và đặc đi&ecirc;̉m sứ m&ecirc;̣nh lịch sử của giai c&acirc;́p c&ocirc;ng nh&acirc;n</span></p>\r\n\r\n<ul>\r\n	<li>Sứ m&ecirc;̣nh lịch sử của giai c&acirc;́p c&ocirc;ng nh&acirc;n:</li>\r\n</ul>\r\n\r\n<p>+ xóa bỏ chủ nghĩa tư bản, xóa bỏ ch&ecirc;́ đ&ocirc;̣ người bóc l&ocirc;̣t người</p>\r\n\r\n<p>+ x&acirc;y dựng xã h&ocirc;̣i mới- xã h&ocirc;̣i XHCN và c&ocirc;̣ng sản chủ nghĩa</p>\r\n\r\n<p>+ giải phóng giai c&acirc;́p c&ocirc;ng nh&acirc;n, nh&acirc;n d&acirc;n lao đ&ocirc;̣ng và giải phóng toàn th&ecirc;̉ nh&acirc;n loại khỏi mọi sự áp bức, bóc l&ocirc;̣t, nghèo nàn lạc h&acirc;̣u.</p>\r\n\r\n<ul>\r\n	<li>N&ocirc;̣i dung của sứ m&ecirc;̣nh lịch sử của giai c&acirc;́p c&ocirc;ng nh&acirc;n:</li>\r\n</ul>\r\n\r\n<p>+ Trong m&ocirc;̃i thời kì chuy&ecirc;̉n bi&ecirc;́n cách mạng l&ecirc;n m&ocirc;̣t hình thái kinh t&ecirc;́- xã h&ocirc;̣i cao hơn lu&ocirc;n có m&ocirc;̣t giai c&acirc;́p đứng ở vị trí trung t&acirc;m của lịch sử, đóng vai trò là đ&ocirc;̣ng lực chủ y&ecirc;́u và là lực lượng lãnh đạo quá trình cách mạng đó. Giai c&acirc;́p c&ocirc;ng nh&acirc;n là giai c&acirc;́p có sứ m&ecirc;̣nh lịch sử đó.</p>\r\n\r\n<p>Giai c&acirc;́p c&ocirc;ng nh&acirc;n chỉ có th&ecirc;̉ thoát khỏi ách áp bức bằng con đường đ&acirc;́u tranh giai c&acirc;́p ch&ocirc;́ng giai c&acirc;́p tư sản, bằng con đường thủ ti&ecirc;u ch&ecirc;́ đ&ocirc;̣ tư hữu v&ecirc;̀ tư li&ecirc;̣u sản xu&acirc;́t, và thi&ecirc;́t l&acirc;̣p ch&ecirc;́ đ&ocirc;̣ c&ocirc;ng hữu v&ecirc;̀ tư li&ecirc;̣u sản xu&acirc;́t.</p>\r\n\r\n<p>+&nbsp; Thực hi&ecirc;̣n sứ m&ecirc;̣nh phải trải qua hai bước ( theo C.Mac &amp; Ph.Ăngghen)</p>\r\n\r\n<p>B1: &ldquo;&hellip; giai c&acirc;́p v&ocirc; sản bi&ecirc;́n thành giai c&acirc;́p th&ocirc;́ng trị&rdquo;. Và &ldquo;giai c&acirc;́p v&ocirc; sản chi&ecirc;́m l&acirc;́y chính quy&ecirc;̀n nhà nước&rdquo;</p>\r\n\r\n<p>B2: &hellip;giai c&acirc;́p v&ocirc; sản dùng sự th&ocirc;́ng trị của mình đ&ecirc;̉ từng bước đoạt l&acirc;́y toàn b&ocirc;̣ tư bản trong tay giai c&acirc;́p tư sản đ&ecirc;̉ t&acirc;̣p trung t&acirc;́t cả những c&ocirc;ng cụ sản xu&acirc;́t vào trong tay nhà nước ti&ecirc;́n hành t&ocirc;̉ chức x&acirc;y dựng xã h&ocirc;̣i mới- xã h&ocirc;̣i chủ nghĩa.</p>\r\n\r\n<p>Hai bước này có quan h&ecirc;̣ chặt chẽ với nhau: kh&ocirc;ng thực hi&ecirc;̣n được B1 thì kh&ocirc;ng thực hi&ecirc;̣n được B2, nhưng B2 là bước quan trọng nh&acirc;́t đ&ecirc;̉ giai c&acirc;́p c&ocirc;ng nh&acirc;n hoàn thành sứ m&ecirc;̣nh của mình.</p>\r\n\r\n<ul>\r\n	<li>Đ&ecirc;̉ thực hi&ecirc;̣n được sứ m&ecirc;̣nh lịch sử thì giai c&acirc;́p c&ocirc;ng nh&acirc;n phải:</li>\r\n</ul>\r\n\r\n<p>+ t&acirc;̣p hợp được các t&acirc;̀ng lớp nh&acirc;n d&acirc;n lao đ&ocirc;̣ng xung quanh nó</p>\r\n\r\n<p>+ đ&acirc;́u tranh cách mạng xóa bỏ ch&ecirc;́ đ&ocirc;̣ xã h&ocirc;̣i cũ và x&acirc;y dựng xã h&ocirc;̣i mới v&ecirc;̀ mọi mặt từ kinh t&ecirc;́, chính trị và văn hóa, tư tưởng.</p>\r\n\r\n<ol start=\"2\">\r\n	<li><strong>Những đi&ecirc;̀u ki&ecirc;̣n khách quan quy định sứ m&ecirc;̣nh lịch sử của giai c&acirc;́p c&ocirc;ng nh&acirc;n</strong>.</li>\r\n</ol>\r\n\r\n<p><span style=\"color:#e74c3c\">a, Địa vị kinh t&ecirc;́- xã h&ocirc;̣i của giai c&acirc;́p c&ocirc;ng nh&acirc;n trong xã h&ocirc;̣i tư bản chủ nghĩa.</span></p>\r\n\r\n<p><span style=\"color:#e74c3c\">b,Những đặc đi&ecirc;̉m chính trị- xã h&ocirc;̣i của giai c&acirc;́p c&ocirc;ng nh&acirc;n.(4 đđ lớn)</span></p>\r\n\r\n<ul>\r\n	<li>Là giai c&acirc;́p ti&ecirc;n phong cách mạng có tinh th&acirc;̀n cách mạng tri&ecirc;̣t đ&ecirc;̉ nh&acirc;́t</li>\r\n	<li>Là giai c&acirc;́p có ý thức t&ocirc;̉ chức kỷ lu&acirc;̣t cao</li>\r\n	<li>Giai c&acirc;́p c&ocirc;ng nh&acirc;n có bản ch&acirc;́t qu&ocirc;́c t&ecirc;́</li>\r\n</ul>\r\n\r\n<ol start=\"3\">\r\n	<li><strong>Vai trò của Đảng c&ocirc;̣ng sản trong quá trình thực hi&ecirc;̣n sứ m&ecirc;̣nh lịch sử của giai c&acirc;́p c&ocirc;ng nh&acirc;n.</strong></li>\r\n</ol>\r\n\r\n<p><span style=\"color:#e74c3c\">a, Tính t&acirc;́t y&ecirc;́u của sự hình thành, phát tri&ecirc;̉n chính đảng của giai c&acirc;́p c&ocirc;ng nh&acirc;n.</span></p>\r\n\r\n<p>Khi đảng c&ocirc;̣ng sản ra đời, th&ocirc;ng qua sự lãnh đạo của đảng, giai c&acirc;́p c&ocirc;ng nh&acirc;n nh&acirc;̣n thức được vai trò, vị trí của mình trong xã h&ocirc;̣i, hi&ecirc;̉u được con đường bi&ecirc;̣n pháp đ&acirc;́u tranh các mạn, từ đó t&acirc;̣p hợp được đ&ocirc;ng đảo qu&acirc;̀n cúng nh&acirc;n d&acirc;n lao đ&ocirc;̣ng, thưc hi&ecirc;̣n vi&ecirc;̣c l&acirc;̣t đ&ocirc;̉ chủ nghĩa tư bản, giải phóng giai c&acirc;́p mình, giải phóng toàn xã h&ocirc;̣i và t&ocirc;̉ chức x&acirc;y dựng mới v&ecirc;̀ mọi mặt.</p>\r\n\r\n<p><span style=\"color:#e74c3c\">b, M&ocirc;́i quan h&ecirc;̣ giữa đảng c&ocirc;̣ng sản và giai c&acirc;́p c&ocirc;ng nh&acirc;n.</span></p>\r\n\r\n<ul>\r\n	<li>Đảng c&ocirc;̣ng sản là t&ocirc;̉ chức chính trị cao nh&acirc;́t của giai c&acirc;́p c&ocirc;ng nh&acirc;n, đại bi&ecirc;̉u cho lợi ích và trí tu&ecirc;̣ của giai c&acirc;́p c&ocirc;ng nh&acirc;n và toàn th&ecirc;̉ nh&acirc;n d&acirc;n lao đ&ocirc;̣ng</li>\r\n	<li>Giai c&acirc;́p c&ocirc;ng nh&acirc;n là cơ sở của đảng c&ocirc;̣ng sản, là ngu&ocirc;̀n b&ocirc;̉ sung lực lượng phong phú cho đảng c&ocirc;̣ng sản.</li>\r\n	<li>Đảng c&ocirc;̣ng sản là lãnh tụ chính trị của giai c&acirc;́p c&ocirc;ng nh&acirc;n và nh&acirc;n d&acirc;n lao đ&ocirc;̣ng.</li>\r\n	<li>Đảng c&ocirc;̣ng sản là b&ocirc;̣ tham mưu chi&ecirc;́n đ&acirc;́u của giai c&acirc;́p c&ocirc;ng nh&acirc;n và cả d&acirc;n t&ocirc;̣c. ( vai trò quy&ecirc;́t định)</li>\r\n</ul>\r\n\r\n<p><strong>II. CÁCH MẠNG XÃ H&Ocirc;̣I CHỦ NGHĨA</strong></p>\r\n\r\n<ol>\r\n	<li><strong>Cách mạng xã h&ocirc;̣i chủ nghĩa và nguy&ecirc;n nh&acirc;n của nó</strong></li>\r\n</ol>\r\n\r\n<p><span style=\"color:#e74c3c\">a, Khái ni&ecirc;̣m cách mạng xã h&ocirc;̣i chủ nghĩa.</span></p>\r\n\r\n<ul>\r\n	<li>Theo nghĩa hẹp: cách mạng xã h&ocirc;̣i chủ nghĩa là m&ocirc;̣t cu&ocirc;̣c cách mạng chính trị, được k&ecirc;́t thúc bằng vi&ecirc;̣c giai c&acirc;́p c&ocirc;ng nh&acirc;n cùng với nh&acirc;n d&acirc;n lao đ&ocirc;̣ng được chính quy&ecirc;̀n, thi&ecirc;́t l&acirc;̣p được nhà nước chuy&ecirc;n chính v&ocirc; sản- nhà nước của giai c&acirc;́p c&ocirc;ng nh&acirc;n và qu&acirc;̀n chúng nh&acirc;n d&acirc;n lao đ&ocirc;̣ng</li>\r\n	<li>Theo nghĩa r&ocirc;̣ng: cách mạng xã h&ocirc;̣i chủ nghĩa g&ocirc;̀m 2 thời kì: cách mạng v&ecirc;̀ chính trị nhằm thi&ecirc;́t l&acirc;̣p nhà nước chuy&ecirc;n chính v&ocirc; sản và ti&ecirc;́p theo là thời kỳ giai c&acirc;́p c&ocirc;ng nh&acirc;n và nh&acirc;n d&acirc;n lao đ&ocirc;̣ng sử dụng nhà nước của mình đ&ecirc;̉ cải tạo xã h&ocirc;̣i cũ v&ecirc;̀ mọi mặt kinh t&ecirc;́ văn hoá, chính trị, tư tưởng&hellip; x&acirc;y dựng xã h&ocirc;̣i mới v&ecirc;̀ mọi mặt nhằm thực hi&ecirc;̣n thắng lợi chủ nghĩa xã h&ocirc;̣i và chủ nghĩa c&ocirc;̣ng sản.</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#e74c3c\">b, Nguy&ecirc;n nh&acirc;n của cách mạng xã h&ocirc;̣i chủ nghĩa.</span></p>\r\n\r\n<p>Nguy&ecirc;n nh&acirc;n s&acirc;u xa: m&acirc;u thu&acirc;̃n gay gắt giữa nhu c&acirc;̀u phát tri&ecirc;̉n của lực lượng sản xu&acirc;́t với sự kìm hãm của quan h&ecirc;̣ sản xu&acirc;́t đã trở n&ecirc;n l&ocirc;̃i thời.</p>\r\n\r\n<p>Trong xã h&ocirc;̣i TBCN, lực lượng sản xu&acirc;́t ngày càng phát tri&ecirc;̉n, ngày càng có tính xã h&ocirc;̣i hóa cao, m&acirc;u thu&acirc;̃n gay gắt với quan h&ecirc;̣ sản xu&acirc;́t mang tính ch&acirc;́t tư nh&acirc;n tư bản chủ nghĩa v&ecirc;̀ tư li&ecirc;̣u sản xu&acirc;́t.</p>\r\n\r\n<p>Quy luật cạnh tranh, t&iacute;nh chất v&ocirc; ch&iacute;nh phủ trong sản xuất dưới chế độ TBCN dẫn tới khủng hoảng thừa, buộc một số nh&agrave; doanh nghiệp phải ngừng sản xuất &nbsp;=&gt; giai cấp c&ocirc;ng nh&acirc;n kh&ocirc;ng c&oacute; việc l&agrave;m, họ đ&atilde; đứng l&ecirc;n đấu tranh chống lại giai cấp tư sản.</p>\r\n\r\n<p>Cu&ocirc;̣c cách mạng xã h&ocirc;̣i chủ nghĩa kh&ocirc;ng tự di&ecirc;̃n ra mà phải là k&ecirc;́t quả giác ng&ocirc;̣ của giai c&acirc;́p c&ocirc;ng nh&acirc;n và qu&acirc;̀n chúng nh&acirc;n d&acirc;n lao đ&ocirc;̣ng.</p>\r\n\r\n<ol start=\"2\">\r\n	<li><strong>Mục ti&ecirc;u, đ&ocirc;̣ng lực và n&ocirc;̣i dung của cách mạng xã h&ocirc;̣i chủ nghĩa.</strong></li>\r\n</ol>\r\n\r\n<p><span style=\"color:#e74c3c\">a, Mục ti&ecirc;u</span></p>\r\n\r\n<ul>\r\n	<li>Giải phóng xã h&ocirc;̣i, giải phóng con người</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Tính nh&acirc;n văn s&acirc;u sắc</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Mục ti&ecirc;u phát tri&ecirc;̉n d&acirc;̀n d&acirc;̀n qua các giai đoạn: từ sự đoàn k&ecirc;́t của giai c&acirc;́p c&ocirc;ng nh&acirc;n với những người lao đ&ocirc;̣ng khác l&acirc;̣t đ&ocirc;̉ giai c&acirc;́p th&ocirc;́ng trị, áp bức bóc l&ocirc;̣t đ&ecirc;̉ giành l&acirc;́y chính quy&ecirc;̀n; đ&ecirc;́n t&acirc;̣p hợp các t&acirc;̀ng lớp nh&acirc;n d&acirc;n lao đ&ocirc;̣ng vào c&ocirc;ng cu&ocirc;̣c t&ocirc;̉ chức xã h&ocirc;̣i mới; cao nh&acirc;́t là giai đoạn kh&ocirc;ng còn giai c&acirc;́p, kh&ocirc;ng còn nhà nước, giai c&acirc;́p v&ocirc; sản tự xóa bỏ mình là giai c&acirc;́p th&ocirc;́ng trị.</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#e74c3c\">b, Đ&ocirc;̣ng lực</span></p>\r\n\r\n<ul>\r\n	<li>Với mục đích nh&acirc;n văn s&acirc;u sắc đã thu hút được sự tham gia của qu&acirc;̀n chúng nh&acirc;n d&acirc;n lao đ&ocirc;̣ng trong su&ocirc;́t quá trình cách mạng.</li>\r\n	<li>Giai c&acirc;́p c&ocirc;ng nh&acirc;n vừa là giai c&acirc;́p lãnh đạo, vừa là đ&ocirc;̣ng lực chủ y&ecirc;́u trong cách mạng xã h&ocirc;̣i chủ nghĩa.</li>\r\n	<li>Giai c&acirc;́p n&ocirc;ng d&acirc;n có nhi&ecirc;̀u lợi ích cơ bản th&ocirc;́ng nh&acirc;́t với lợi ích của giai c&acirc;́p c&ocirc;ng nh&acirc;n, giai c&acirc;́p này trở thành m&ocirc;̣t đ&ocirc;̣ng lực to lớn trong cách mạng xã h&ocirc;̣i chủ nghĩa.</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#e74c3c\">c, N&ocirc;̣i dung( CMXHCN thực hi&ecirc;̣n tr&ecirc;n mọi lĩnh vực của đời s&ocirc;́ng xã h&ocirc;̣i)</span></p>\r\n\r\n<ul>\r\n	<li>Lĩnh vực chính trị</li>\r\n	<li>Lĩnh vực kinh t&ecirc;́</li>\r\n	<li>Lĩnh vực tư tưởng- văn hóa</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#e74c3c\">d, Lý lu&acirc;̣n cách mạng kh&ocirc;ng ngừng của chủ nghĩa Mac- Lenin</span></p>\r\n\r\n<p>Ở những nước lạc h&acirc;̣u v&ecirc;̀ kinh t&ecirc;́, chủ nghĩa tư bản còn ở trình đ&ocirc;̣ kém ( trung bình) giai c&acirc;́p c&ocirc;ng nh&acirc;n phải thực hi&ecirc;̣n cu&ocirc;̣c cách mạng d&acirc;n chủ tư sản ki&ecirc;̉u mới sau đó đi l&ecirc;n chủ nghĩa xã h&ocirc;̣i.</p>\r\n\r\n<p>Những nước thu&ocirc;̣c địa mới được giải phóng cũng có th&ecirc;̉ quá đ&ocirc;̣ đi l&ecirc;n CNXH.</p>\r\n\r\n<ol start=\"3\">\r\n	<li><strong>Li&ecirc;n minh giữa giai c&acirc;́p c&ocirc;ng nh&acirc;n với giai c&acirc;́p n&ocirc;ng d&acirc;n và các t&acirc;̀ng lớp lao đ&ocirc;̣ng khác trong cách mạng xã h&ocirc;̣i chủ nghĩa.</strong></li>\r\n</ol>\r\n\r\n<p><span style=\"color:#e74c3c\">a, Tính t&acirc;́t y&ecirc;́u &amp; Cơ sở khách quan</span></p>\r\n\r\n<ul>\r\n	<li>Tính t&acirc;́t y&ecirc;́u</li>\r\n</ul>\r\n\r\n<p>Lenin cho rằng n&ecirc;́u kh&ocirc;ng thực hi&ecirc;̣n li&ecirc;n minh chặt chẽ v&ocirc;́i giai c&acirc;́p n&ocirc;ng d&acirc;n và các t&acirc;̀ng lớp lao đ&ocirc;̣ng khác thì giai c&acirc;́p c&ocirc;ng nh&acirc;n kh&ocirc;ng th&ecirc;̉ giữ vững được chính quy&ecirc;̀n nhà nước.</p>\r\n\r\n<p>Mục ti&ecirc;u cu&ocirc;́i cùng của cu&ocirc;̣c cách mạng xã h&ocirc;̣i chủ nghĩa là ti&ecirc;́n l&ecirc;n x&acirc;y dựng m&ocirc;̣t xã h&ocirc;̣i kh&ocirc;ng còn giai c&acirc;́p, kh&ocirc;ng còn nhà nước. Đi&ecirc;̀u đó chỉ có th&ecirc;̉ thực hi&ecirc;̣n được tr&ecirc;n cơ sở x&acirc;y dựng kh&ocirc;́i li&ecirc;n minh vững chắc giữa giai c&acirc;́p c&ocirc;ng nh&acirc;n, giai c&acirc;́p n&ocirc;ng d&acirc;n và các t&acirc;̀ng lớp lao đ&ocirc;̣ng khác.</p>\r\n\r\n<ul>\r\n	<li>Cơ sở khách quan</li>\r\n</ul>\r\n\r\n<p>- Thứ nh&acirc;́t, trong xã h&ocirc;̣i tư bản chủ nghĩa, giai c&acirc;́p c&ocirc;ng nh&acirc;n, giai c&acirc;́p n&ocirc;ng d&acirc;n cũng như nhi&ecirc;̀u t&acirc;̀ng lớp lao đ&ocirc;̣ng khác đ&ecirc;̀u là những người lao đ&ocirc;̣ng, đ&ecirc;̀u bị áp bức bóc l&ocirc;̣t.</p>\r\n\r\n<p>- Thứ hai, n&ecirc;̀n kinh t&ecirc;́ qu&ocirc;́c d&acirc;n là m&ocirc;̣t th&ecirc;̉ th&ocirc;́ng nh&acirc;́t của nhi&ecirc;̀u ngành ngh&ecirc;̀, nhưng trong đó c&ocirc;ng nghi&ecirc;̣p và n&ocirc;ng nghi&ecirc;̣p là 2 ngành sản xu&acirc;́t chính trong xã h&ocirc;̣i, vì v&acirc;̣y c&acirc;̀n có sự li&ecirc;n minh chặt chẽ giữa giai c&acirc;́p c&ocirc;ng nh&acirc;n và n&ocirc;ng d&acirc;n đ&ecirc;̉ 2 ngành kinh t&ecirc;́ này phát tri&ecirc;̉n.</p>\r\n\r\n<p>- Thứ ba, xét v&ecirc;̀ mặt chính trị- xã h&ocirc;̣i GCCN, GCND và các t&acirc;̀ng lớp lao đ&ocirc;̣ng khác là lực lượng chính trị to lớn trong x&acirc;y dựng, bảo v&ecirc;̣ chính quy&ecirc;̀n nhà nước, trong x&acirc;y dựng kh&ocirc;́i đoàn k&ecirc;́t d&acirc;n t&ocirc;̣c.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><span style=\"color:#e74c3c\">b, N&ocirc;̣i dung và nguy&ecirc;n tắc cơ bản của vi&ecirc;̣c li&ecirc;n minh</span></p>\r\n\r\n<ul>\r\n	<li>N&ocirc;̣i dung</li>\r\n</ul>\r\n\r\n<p>- Li&ecirc;n minh v&ecirc;̀ chính trị</p>\r\n\r\n<p>- Li&ecirc;n minh v&ecirc;̀ kinh t&ecirc;́</p>\r\n\r\n<p>- N&ocirc;̣i dung tư tưởng- văn hóa( có 3 lí do khi&ecirc;́n đ&acirc;y là m&ocirc;̣t n&ocirc;̣i dung quan trọng)</p>\r\n\r\n<p>+ N&ocirc;ng d&acirc;n, c&ocirc;ng nh&acirc;n và các t&acirc;̀ng lớp lao đ&ocirc;̣ng khác c&acirc;̀n thường xuy&ecirc;n n&acirc;ng cao trình đ&ocirc;̣ tư tưởng- văn hóa đ&ecirc;̉ x&acirc;y dựng n&ecirc;n m&ocirc;̣t xã h&ocirc;̣i với n&ecirc;̀n c&ocirc;ng nghi&ecirc;̣p sản xu&acirc;́t hi&ecirc;̣n đại.</p>\r\n\r\n<p>+ CNXH mong mu&ocirc;́n x&acirc;y dựng m&ocirc;̣t xã h&ocirc;̣i nh&acirc;n văn, nh&acirc;n đạo: có tinh th&acirc;̀n hữu nghị, tương trợ, giúp đỡ l&acirc;̃n nhau.</p>\r\n\r\n<p>+ nh&acirc;n d&acirc;n c&acirc;̀n có trình đ&ocirc;̣ văn hóa- tư tưởng, hi&ecirc;̉u bi&ecirc;́t chính sách pháp&nbsp; lu&acirc;̣t đ&ecirc;̉ tham gia quản lí kinh t&ecirc;́, quản lí xã h&ocirc;̣i.</p>\r\n\r\n<ul>\r\n	<li>Nguy&ecirc;n tắc cơ bản</li>\r\n</ul>\r\n\r\n<p>- Đảm bảo vai trò lãnh đạo của giai c&acirc;́p c&ocirc;ng nh&acirc;n</p>\r\n\r\n<p>- Đảm bảo nguy&ecirc;n tắc tự nguy&ecirc;̣n</p>\r\n\r\n<p>- K&ecirc;́t hợp đúng đắn các lợi ích</p>\r\n\r\n<p><strong>III. HÌNH THÁI KINH T&Ecirc;́- XÃ H&Ocirc;̣I C&Ocirc;̣NG SẢN CHỦ NGHĨA</strong></p>\r\n\r\n<ol>\r\n	<li><strong>Xu hướng t&acirc;́t y&ecirc;́u của sự xu&acirc;́t hi&ecirc;̣n hình thái kinh t&ecirc;́- xã h&ocirc;̣i c&ocirc;̣ng sản chủ nghĩa. </strong></li>\r\n	<li><strong>Các giai đoạn phát tri&ecirc;̉n của hình thái kinh t&ecirc;́- xã h&ocirc;̣i c&ocirc;̣ng sản chủ nghĩa.</strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Phát tri&ecirc;̉n từ th&acirc;́p l&ecirc;n cao: từ xã h&ocirc;̣i XHCN đ&ecirc;́n xã h&ocirc;̣i CSCN</li>\r\n</ul>\r\n\r\n<p>+ Thời kỳ quá đ&ocirc;̣</p>\r\n\r\n<p>+ Giai đoạn đ&acirc;̀u của xã h&ocirc;̣i c&ocirc;̣ng sản chủ nghĩa( xã h&ocirc;̣i xã h&ocirc;̣i chủ nghĩa)</p>\r\n\r\n<p>+ Giai đoạn cao của xã h&ocirc;̣i c&ocirc;̣ng sản chủ nghĩa</p>\r\n\r\n<p><span style=\"color:#e74c3c\">a, Thời kỳ quá đ&ocirc;̣ từ chủ nghĩa tư bản l&ecirc;n chủ nghĩa xã h&ocirc;̣i</span></p>\r\n\r\n<p>+ tính t&acirc;́t y&ecirc;́u của thời kỳ quá đ&ocirc;̣ ( có 3 căn cứ)</p>\r\n\r\n<p>+ đặc đi&ecirc;̉m và thực ch&acirc;́t của thời kỳ quá đ&ocirc;̣: sự t&ocirc;̀n tại của những y&ecirc;́u t&ocirc;́ xã h&ocirc;̣i cũ b&ecirc;n cạnh những nh&acirc;n t&ocirc;́ mới của chủ nghĩa xã h&ocirc;̣i trong m&ocirc;́i quan h&ecirc;̣ vừa th&ocirc;́ng nh&acirc;́t vừa đ&acirc;́u tranh với nhau tr&ecirc;n t&acirc;́t cả các lĩnh vực của đời s&ocirc;́ng xã h&ocirc;̣i ( kinh t&ecirc;́; chính trị; tư tưởng- văn hóa)</p>\r\n\r\n<p>+ n&ocirc;̣i dung của thời kỳ quá đ&ocirc;̣: trong mọi lĩnh vực</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Kinh t&ecirc;́</strong>: thực hi&ecirc;̣n sắp x&ecirc;́p, b&ocirc;́ trí lại lực lượng sản xu&acirc;́t hi&ecirc;̣n có; cải tạo cũ, x&acirc;y dựng mới</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Tư tưởng- văn hóa</strong>: tuy&ecirc;n truy&ecirc;̀n, ph&ocirc;̉ bi&ecirc;́n tư tưởng KH-CM; khắc phục ti&ecirc;u cực</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Xã h&ocirc;̣i</strong>: khắc phục t&ecirc;̣ nạn xã h&ocirc;̣i, khắc phục những ch&ecirc;nh l&ecirc;̣ch phát tri&ecirc;̉n, x&acirc;y dựng m&ocirc;́i quan h&ecirc;̣ t&ocirc;́t đẹp</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Chính trị</strong>: ch&ocirc;́ng lại th&ecirc;́ lực thù địch, củng c&ocirc;́ nhà nước và n&ecirc;̀n d&acirc;n chủ xhcn</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"color:#e74c3c\">b, Xã h&ocirc;̣i xã h&ocirc;̣i chủ nghĩa ( 6 đặc trưng cơ bản)</span></p>\r\n\r\n<p>+ n&ecirc;̀n đại c&ocirc;ng nghi&ecirc;̣p xã h&ocirc;̣i chủ nghĩa được phát tri&ecirc;̉n l&ecirc;n từ n&ecirc;̀n đại c&ocirc;ng nghi&ecirc;̣p của tư bản chủ nghĩa.</p>\r\n\r\n<p>+ chủ nghĩa xã h&ocirc;̣i xóa bỏ ch&ecirc;́ đ&ocirc;̣ tư hữu tư nh&acirc;n tư bản chủ nghĩa, thi&ecirc;́t l&acirc;̣p ch&ecirc;́ đ&ocirc;̣ sở hữu XHCN v&ecirc;̀ tư li&ecirc;̣u sản xu&acirc;́t.</p>\r\n\r\n<p>+ xã h&ocirc;̣i XHCN là m&ocirc;̣t ch&ecirc;́ đ&ocirc;̣ xã h&ocirc;̣i tạo ra được cách t&ocirc;̉ chức lao đ&ocirc;̣ng và kỷ lu&acirc;̣t lao đ&ocirc;̣ng mới.</p>\r\n\r\n<p>+ xã h&ocirc;̣i XHCN&nbsp; là m&ocirc;̣t ch&ecirc;́ đ&ocirc;̣ xã h&ocirc;̣i thực hi&ecirc;̣n nguy&ecirc;n tắc ph&acirc;n ph&ocirc;́i theo lao đ&ocirc;̣ng, coi đó là nguy&ecirc;n tắc cơ bản nh&acirc;́t.</p>\r\n\r\n<p>+ xã h&ocirc;̣i XHCN là m&ocirc;̣t xã h&ocirc;̣i mà ở đó nhà nước mang bản ch&acirc;́t giai c&acirc;́p c&ocirc;ng nh&acirc;n, có tính nh&acirc;n d&acirc;n r&ocirc;̣ng rãi, tính d&acirc;n t&ocirc;̣c s&acirc;u sắc.</p>\r\n\r\n<p>+ xã h&ocirc;̣i XHCN là m&ocirc;̣t xã h&ocirc;̣i đã thực hi&ecirc;̣n được sự giải phóng con người khỏi ách áp bức bóc l&ocirc;̣t; thực hi&ecirc;̣n sự bình đẳng xã h&ocirc;̣i, tạo đi&ecirc;̀u ki&ecirc;̣n cho con người phát tri&ecirc;̉n toàn di&ecirc;̣n.</p>\r\n\r\n<p><span style=\"color:#e74c3c\">c, Giai đoạn cao của hình thái kinh t&ecirc;́- xã h&ocirc;̣i c&ocirc;̣ng sản chủ nghĩa</span></p>\r\n\r\n<ul>\r\n	<li>V&ecirc;̀ mặt kinh t&ecirc;́: LLSX phát tri&ecirc;̉n v&ocirc; cùng mạnh mẽ, của cải xã h&ocirc;̣i d&ocirc;̀i dào, ý thức con người được n&acirc;ng l&ecirc;n, khoa học phat tri&ecirc;̉n, lao đ&ocirc;̣ng của con người được giảm nhẹ, lúc đó nh&acirc;n loại mới được thực hi&ecirc;̣n theo nguy&ecirc;n tắc &ldquo; làm theo năng lực, hưởng theo nhu c&acirc;̀u&rdquo;</li>\r\n	<li>V&ecirc;̀ mặt xã h&ocirc;̣i: trình đ&ocirc;̣ xã h&ocirc;̣i ngày càng phát tri&ecirc;̉n, con người có đi&ecirc;̀u ki&ecirc;̣n phát tri&ecirc;̉n năng lực của mình, tri thức con người được n&acirc;ng cao, kh&ocirc;ng còn có sự khác bi&ecirc;̣t giữa thành thị và n&ocirc;ng th&ocirc;n.</li>\r\n</ul>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + nhà nước trở thành kh&ocirc;ng c&acirc;̀n thi&ecirc;́t, nó tự ti&ecirc;u vong.</p>\r\n\r\n<ul>\r\n	<li>Đ&ecirc;̉ có giai đoạn cao của chủ nghĩa c&ocirc;̣ng sản đòi hỏi giai c&acirc;́p c&ocirc;ng nh&acirc;n và nh&acirc;n d&acirc;n lao đ&ocirc;̣ng phải n&ocirc;̃ lực ph&acirc;́n đ&acirc;́u, phải kh&ocirc;ng ngừng n&acirc;ng cao năng su&acirc;́t lao đ&ocirc;̣ng, phát tri&ecirc;̉n llsx, cải tạo quan h&ecirc;̣ sản xu&acirc;́t theo chủ nghĩa xã h&ocirc;̣i, n&acirc;ng cao ý thức con người.</li>\r\n</ul>', '1551582843', null, '0', '42', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('260', 'PHẦN I.4 LÝ THUYẾT TRỌNG TÂM CHƯƠNG 8', '<p><strong>CHƯƠNG VIII. NHỮNG V&Acirc;́N Đ&Ecirc;̀ CHÍNH TRỊ- XÃ H&Ocirc;̣I CÓ TÍNH QUY LU&Acirc;̣T TRONG TI&Ecirc;́N TRÌNH CÁCH MẠNG XÃ H&Ocirc;̣I CHỦ NGHĨA.</strong></p>\r\n\r\n<p><strong>I. X&Acirc;Y DỰNG N&Ecirc;̀N D&Acirc;N CHỦ XÃ H&Ocirc;̣I CHỦ NGHĨA VÀ NHÀ NƯỚC XÃ H&Ocirc;̣I CHỦ NGHĨA</strong></p>\r\n\r\n<ol>\r\n	<li><strong>X&acirc;y dựng n&ecirc;̀n d&acirc;n chủ xã h&ocirc;̣i chủ nghĩa</strong></li>\r\n</ol>\r\n\r\n<p><span style=\"color:#e74c3c\">a, Quan ni&ecirc;̣m v&ecirc;̀ d&acirc;n chủ và n&ecirc;̀n d&acirc;n chủ</span></p>\r\n\r\n<ul>\r\n	<li>D&acirc;n chủ</li>\r\n</ul>\r\n\r\n<p>- Với tư cách là sản ph&acirc;̉m ti&ecirc;́n hóa của lịch sử, là nhu c&acirc;̀u khách quan của con người: với tư cách là quy&ecirc;̀n lực của nh&acirc;n d&acirc;n, d&acirc;n chủ là sự phản ánh những giá trị nh&acirc;n văn, là k&ecirc;́t quả của cu&ocirc;̣c đ&acirc;́u tranh l&acirc;u dài của nh&acirc;n d&acirc;n ch&ocirc;́ng lại áp bức, bóc l&ocirc;̣t, b&acirc;́t c&ocirc;ng.</p>\r\n\r\n<p>- D&acirc;n chủ với tư cách là m&ocirc;̣t phạm trù chính trị gắn với m&ocirc;̣t ki&ecirc;̉u nhà nước và m&ocirc;̣t giai c&acirc;́p c&acirc;̀m quy&ecirc;̀n thì sẽ kh&ocirc;ng có &ldquo; d&acirc;n chủ phi giai c&acirc;́p&rdquo;, &ldquo; d&acirc;n chủ chung chung&rdquo;</p>\r\n\r\n<p>- Với tư cách là m&ocirc;̣t h&ecirc;̣ giá trị phản ánh trình đ&ocirc;̣ phát tri&ecirc;̉n cá nh&acirc;n và c&ocirc;̣ng đ&ocirc;̀ng xã h&ocirc;̣i trong quá trình giải phóng xã h&ocirc;̣i, ch&ocirc;́ng áp bức, bóc l&ocirc;̣t và n&ocirc; dịch đ&ecirc;̉ ti&ecirc;́n tới tự do, bình đẳng.</p>\r\n\r\n<ul>\r\n	<li>N&ecirc;̀n d&acirc;n chủ</li>\r\n</ul>\r\n\r\n<p>- N&ecirc;̀n d&acirc;n chủ hay ch&ecirc;́ đ&ocirc;̣ d&acirc;n chủ là hình thái d&acirc;n chủ gắn với bản ch&acirc;́t, tính ch&acirc;́t của nhà nước; là trạng thái được xác định trong những đi&ecirc;̀u ki&ecirc;̣n lích sử cụ th&ecirc;̉ của xã h&ocirc;̣i có giai c&acirc;́p. N&ecirc;̀n d&acirc;n chủ do giai c&acirc;́p th&ocirc;́ng trị đặt ra được th&ecirc;̉ ch&ecirc;́ hóa bằng pháp lu&acirc;̣t.</p>\r\n\r\n<p><span style=\"color:#e74c3c\">b, Những đặc trưng cơ bản của n&ecirc;̀n d&acirc;n chủ xã h&ocirc;̣i chủ nghĩa</span></p>\r\n\r\n<ul>\r\n	<li>Với tư cách là ch&ecirc;́ đ&ocirc;̣ nhà nước được sáng tạo bởi qu&acirc;̀n chúng nh&acirc;n d&acirc;n lao đ&ocirc;̣ng dưới sự lãnh đạo của đảng c&ocirc;̣ng sản, d&acirc;n chủ xã h&ocirc;̣i chủ nghĩa bảo đảm mọi quy&ecirc;̀n lực đ&ecirc;̀u thu&ocirc;̣c v&ecirc;̀ nh&acirc;n d&acirc;n.</li>\r\n	<li>N&ecirc;̀n d&acirc;n chủ xã h&ocirc;̣i chủ nghĩa có cơ sở kinh t&ecirc;́ là ch&ecirc;́ đ&ocirc;̣ c&ocirc;ng hữu v&ecirc;̀ những tư li&ecirc;̣u sản xu&acirc;́t chủ y&ecirc;́u của toàn xã h&ocirc;̣i.</li>\r\n	<li>Tr&ecirc;n cơ sở của sự k&ecirc;́t hợp hài hòa lợi ích cá nh&acirc;n, lợi ích t&acirc;̣p th&ecirc;̉ và lợi ích của toàn xã h&ocirc;̣i, n&ecirc;̀n d&acirc;n chủ xã h&ocirc;̣i chủ nghĩa có sức đ&ocirc;̣ng vi&ecirc;n, thu hút mọi ti&ecirc;̀m năng sáng tạo, tính tích cực xã h&ocirc;̣i của nh&acirc;n d&acirc;n trong sự nghi&ecirc;̣p x&acirc;y dựng xã h&ocirc;̣i mới.</li>\r\n	<li>N&ecirc;̀n d&acirc;n chủ xã h&ocirc;̣i chủ nghĩa c&acirc;̀n có và phải có những đi&ecirc;̀u ki&ecirc;̣n t&ocirc;̀n tại với tư cách là m&ocirc;̣t n&ecirc;̀n d&acirc;n chủ r&ocirc;̣ng rãi nh&acirc;́t trong lịch sử nhưng v&acirc;̃n là n&ecirc;̀n d&acirc;n chủ mang tính giai c&acirc;́p.</li>\r\n	<li>N&ecirc;̀n d&acirc;n chủ xã h&ocirc;̣i chủ nghĩa kh&ocirc;ng ngừng được mở r&ocirc;̣ng cùng với sự phát tri&ecirc;̉n kinh t&ecirc;́, xã h&ocirc;̣i; hoàn thi&ecirc;̣n h&ecirc;̣ th&ocirc;́ng lu&acirc;̣t pháp, cơ ch&ecirc;́ hoạt đ&ocirc;̣ng và trình đ&ocirc;̣ d&acirc;n trí.</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#e74c3c\">c, Tính t&acirc;́t y&ecirc;́u của vi&ecirc;̣c x&acirc;y dựng n&ecirc;̀n d&acirc;n chủ xã h&ocirc;̣i chủ nghĩa</span></p>\r\n\r\n<ul>\r\n	<li>D&acirc;n chủ phải được mở r&ocirc;̣ng đ&ecirc;̉ phát huy cao đ&ocirc;̣ tính tích cực, sáng tạo của nh&acirc;n d&acirc;n, đ&ecirc;̉ nh&acirc;n d&acirc;n tham gia vào c&ocirc;ng vi&ecirc;̣c quản lý nhà nước, quản lý và phát tri&ecirc;̉n xã h&ocirc;̣i.</li>\r\n	<li>N&ecirc;̀n d&acirc;n chủ XHCN bắt ngu&ocirc;̀n từ bản ch&acirc;́t của ch&ecirc;́ đ&ocirc;̣ xã h&ocirc;̣i chủ nghĩa. X&acirc;y dựng n&ecirc;̀n d&acirc;n chủ XHCN là quy lu&acirc;̣t của sự hình thành và tự hoàn thi&ecirc;̣n của h&ecirc;̣ th&ocirc;́ng chuy&ecirc;n chính v&ocirc; sản, h&ecirc;̣ th&ocirc;́ng chính trị xã h&ocirc;̣i chủ nghĩa.</li>\r\n	<li>X&acirc;y dựng n&ecirc;̀n d&acirc;n chủ XHCN là quá trình v&acirc;̣n đ&ocirc;̣ng và thực hành d&acirc;n chủ; là quá trình v&acirc;̣n đ&ocirc;̣ng bi&ecirc;́n d&acirc;n chủ từ khả năng thành hi&ecirc;̣n thực trong mọi lĩnh vực của đời s&ocirc;́ng xã h&ocirc;̣i; là quá trình đưa các giá trị, chu&acirc;̉n mực, nguy&ecirc;n tắc của d&acirc;n chủ vào thực ti&ecirc;̃n x&acirc;y dựng cu&ocirc;̣c s&ocirc;́ng mới.</li>\r\n	<li>X&acirc;y dựng n&ecirc;̀n d&acirc;n chủ XHCN trở thành đi&ecirc;̀u ki&ecirc;̣n, ti&ecirc;̀n đ&ecirc;̀ thực hi&ecirc;̣n quy&ecirc;̀n lực, quy&ecirc;̀n làm chủ của nh&acirc;n d&acirc;n; là đi&ecirc;̀u ki&ecirc;̣n c&acirc;̀n thi&ecirc;́t, t&acirc;́t y&ecirc;́u đ&ecirc;̉ m&ocirc;̃i c&ocirc;ng d&acirc;n được s&ocirc;́ng trong b&acirc;̀u kh&ocirc;ng khí d&acirc;n chủ thực sự.</li>\r\n	<li>X&acirc;y dựng n&ecirc;̀n d&acirc;n chủ XHCN là quá trình thực hi&ecirc;̣n d&acirc;n chủ hóa đời s&ocirc;́ng xã h&ocirc;̣i dưới sự lãnh đạo của giai c&acirc;́p c&ocirc;ng nh&acirc;n th&ocirc;ng qua chính đảng của nó( đảng c&ocirc;̣ng sản).</li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><strong>X&acirc;y dựng nhà nước xã h&ocirc;̣i chủ nghĩa</strong></li>\r\n</ol>\r\n\r\n<p><span style=\"color:#e74c3c\">a, Khái ni&ecirc;̣m &ldquo;nhà nước xã h&ocirc;̣i chủ nghĩa&rdquo;</span></p>\r\n\r\n<p>Nhà nước XHCN là t&ocirc;̉ chức mà th&ocirc;ng qua đó, đảng của giai c&acirc;́p c&ocirc;ng nh&acirc;n thực hi&ecirc;̣n vai trò lãnh đạo của mình đ&ocirc;́i với toàn xã h&ocirc;̣i; là m&ocirc;̣t t&acirc;̀ng lớp chính trị thu&ocirc;̣c ki&ecirc;́n trúc thượng t&acirc;̀ng dựa tr&ecirc;n cơ sở kinh t&ecirc;́ của chủ nghĩa xã h&ocirc;̣i; đó là m&ocirc;̣t nhà nước ki&ecirc;̉u mới, thay th&ecirc;́ nhà nước tư sản nhờ k&ecirc;́t quả của cu&ocirc;̣c cách mạng XHCN; là hình thức chuy&ecirc;n chính v&ocirc; sản được thực hi&ecirc;̣n trong thời kì quá đ&ocirc;̣ l&ecirc;n chủ nghĩa xã h&ocirc;̣i.</p>\r\n\r\n<p><span style=\"color:#e74c3c\">b, Đặc trưng, chức năng và nhi&ecirc;̣m vụ của nhà nước xã h&ocirc;̣i chủ nghĩa</span></p>\r\n\r\n<ul>\r\n	<li>Đặc trưng</li>\r\n</ul>\r\n\r\n<p>- Nhà nước XHCN là c&ocirc;ng cụ cơ bản đ&ecirc;̉ thực hi&ecirc;̣n quy&ecirc;̀n lực của nh&acirc;n d&acirc;n lao đ&ocirc;̣ng, đặt dưới sự lãnh đạo của đảng c&ocirc;̣ng sản.</p>\r\n\r\n<p>- Nhà nước XHCN là c&ocirc;ng cụ chuy&ecirc;n chính giai c&acirc;́p, nhưng vì lợi ích của t&acirc;́t cả những người lao đ&ocirc;̣ng tức là tuy&ecirc;̣t đại đa s&ocirc;́ nh&acirc;n d&acirc;n; thực hi&ecirc;̣n sự tr&acirc;́n áp đ&ocirc;́i với lực lượng ch&ocirc;́ng đ&ocirc;́i, phá hoại sự nghi&ecirc;̣p cách mạng xã h&ocirc;̣i chủ nghĩa.</p>\r\n\r\n<p>- T&ocirc;̉ chức, x&acirc;y dựng toàn di&ecirc;̣n xã h&ocirc;̣i mới- xã h&ocirc;̣i XHCN và c&ocirc;̣ng sản chủ nghĩa</p>\r\n\r\n<p>- Nhà nước xã h&ocirc;̣i chủ nghĩa là y&ecirc;́u t&ocirc;́ cơ bản của n&ecirc;̀n d&acirc;n chủ XHCN</p>\r\n\r\n<p>- Nhà nước XHCN là m&ocirc;̣t ki&ecirc;̉u nhà nước đặc bi&ecirc;̣t , &ldquo; nhà nước kh&ocirc;ng còn nguy&ecirc;n nghĩa&rdquo;, là &ldquo; nửa nhà nước&rdquo;. Sau khi những cơ sở kinh t&ecirc;́- xã h&ocirc;̣i cho sự t&ocirc;̀n tại của nhà nước m&acirc;́t đi thì nhà nước cũng kh&ocirc;ng còn, nhà nước &ldquo; tự ti&ecirc;u vong&rdquo;</p>\r\n\r\n<ul>\r\n	<li>Chức năng</li>\r\n</ul>\r\n\r\n<p>- Đ&acirc;̣p tan sự phản kháng của kẻ thù ch&ocirc;́ng lại sự nghi&ecirc;̣p x&acirc;y dựng chủ nghĩa xã h&ocirc;̣i, bảo v&ecirc;̀ đ&ocirc;̣c l&acirc;̣p chủ quy&ecirc;̀n của đ&acirc;́t nước, giữ vững an ninh xã h&ocirc;̣i.</p>\r\n\r\n<p>- Chức năng chủ y&ecirc;́u: tích cực x&acirc;y dựng chủ nghĩa c&ocirc;̣ng sản, sáng tạo ra m&ocirc;̣t xã h&ocirc;̣i mới.</p>\r\n\r\n<ul>\r\n	<li>Nhi&ecirc;̣m vụ</li>\r\n</ul>\r\n\r\n<p>- Quản lý kinh t&ecirc;́, x&acirc;y dựng và phát tri&ecirc;̉n kinh t&ecirc;́</p>\r\n\r\n<p>- Cải thi&ecirc;̣n kh&ocirc;ng ngừng đời s&ocirc;́ng v&acirc;̣t ch&acirc;́t và tinh th&acirc;̀n cho nh&acirc;n d&acirc;n</p>\r\n\r\n<p>- Quản lý văn hóa- xã h&ocirc;̣i, x&acirc;y dựng n&ecirc;̀n văn hóa xã h&ocirc;̣i chủ nghĩa, thực hi&ecirc;̣n giáo dục đào tạo con người phát tri&ecirc;̉n toàn di&ecirc;̣n, chăm sóc sức khỏe nh&acirc;n d&acirc;n</p>\r\n\r\n<p>- Đ&ocirc;́i ngoại nhằm mở r&ocirc;̣ng quan h&ecirc;̣ hợp tác hữu nghị, bình đẳng, t&ocirc;n trọng l&acirc;̃n nhau vì sự phát tri&ecirc;̉n và ti&ecirc;́n b&ocirc;̣ xã h&ocirc;̣i đ&ocirc;́i với nh&acirc;n d&acirc;n các nước tr&ecirc;n th&ecirc;́ giới.</p>\r\n\r\n<p><span style=\"color:#e74c3c\">c, Tính t&acirc;́t y&ecirc;́u</span></p>\r\n\r\n<ul>\r\n	<li>X&acirc;y dựng chủ nghĩa xã h&ocirc;̣i là quá trình cải tạo xã h&ocirc;̣i cũ, x&acirc;y dựng xã h&ocirc;̣i mới tr&ecirc;n t&acirc;́t cả các lĩnh vực kinh t&ecirc;́, chính trị, văn hóa, tư tưởng. Với ý nghĩa đó, nhà nước XHCN là phương thức, phương ti&ecirc;̣n, là m&ocirc;̣t c&ocirc;ng cụ chủ y&ecirc;́u của nh&acirc;n d&acirc;n trong trong sự nghi&ecirc;̣p x&acirc;y dựng và bảo v&ecirc;̣ T&ocirc;̉ qu&ocirc;́c. Bởi v&acirc;̣y đ&ecirc;̉ đảm bảo cho sự nghi&ecirc;̣p x&acirc;y dựng CNXH thành c&ocirc;ng thì vi&ecirc;̣c x&acirc;y dựng và kh&ocirc;ng ngừng hoàn thi&ecirc;̣n nhà nước XHCN- m&ocirc;̣t trong những c&ocirc;ng cụ chủ y&ecirc;́u của quá tình cải tạo xã h&ocirc;̣i cũ, x&acirc;y dựng xã h&ocirc;̣i mới, là m&ocirc;̣t y&ecirc;u c&acirc;̀u t&acirc;́t y&ecirc;́u khách quan trong ti&ecirc;́n trình cách mạng xã h&ocirc;̣i chủ nghĩa.</li>\r\n</ul>\r\n\r\n<p><strong>II. X&Acirc;Y DỰNG N&Ecirc;̀N VĂN HÓA XÃ H&Ocirc;̣I CHỦ NGHĨA </strong></p>\r\n\r\n<ol>\r\n	<li><strong>Khái ni&ecirc;̣m văn hóa, n&ecirc;̀n văn hóa và n&ecirc;̀n văn hóa xã h&ocirc;̣i chủ nghĩa</strong></li>\r\n</ol>\r\n\r\n<p>a, Khái ni&ecirc;̣m văn hóa, n&ecirc;̀n văn hóa</p>\r\n\r\n<ul>\r\n	<li>Văn hóa là toàn b&ocirc;̣ những giá trị v&acirc;̣t ch&acirc;́t và tinh th&acirc;̀n do con người tạo ra bằng lao đ&ocirc;̣ng và hoạt đ&ocirc;̣ng thực ti&ecirc;̃n trong quá trình lịch sử của mình; bi&ecirc;̉u hi&ecirc;̣n trình đ&ocirc;̣ phát tri&ecirc;̉n xã h&ocirc;̣i trong từng thời kỳ lịch sử nh&acirc;́t định.</li>\r\n</ul>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;+ văn hóa v&acirc;̣t ch&acirc;́t</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;+ văn hóa tinh th&acirc;̀n</p>\r\n\r\n<ul>\r\n	<li>N&ecirc;̀n văn hóa là bi&ecirc;̉u hi&ecirc;̣n cho toàn b&ocirc;̣ n&ocirc;̣i dung, tính ch&acirc;́t của văn hóa được hình thành và phát tri&ecirc;̉n tr&ecirc;n cơ sở kinh t&ecirc;́- chính trị của m&ocirc;̃i thời kỳ kịch sử, trong đó ý thức h&ecirc;̣ của giai c&acirc;́p th&ocirc;́ng trị chi ph&ocirc;́i phương hướng phát tri&ecirc;̉n và quy&ecirc;́t định h&ecirc;̣ th&ocirc;́ng các chính sách, pháp lu&acirc;̣t quản lý các hoạt đ&ocirc;̣ng văn hóa.</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#e74c3c\">b, Khái ni&ecirc;̣m n&ecirc;̀n văn hóa XHCN</span></p>\r\n\r\n<p>N&ecirc;̀n văn hóa XHCN là n&ecirc;̀n văn hóa được x&acirc;y dựng và phát tri&ecirc;̉n tr&ecirc;n n&ecirc;̀n tẳng h&ecirc;̣ tư tưởng của giai c&acirc;́p c&ocirc;ng nh&acirc;n, do đảng c&ocirc;̣ng sản lãnh đạo nhằm thoả mãn nhu c&acirc;̀u kh&ocirc;ng ngừng tăng l&ecirc;n v&ecirc;̀ đời s&ocirc;́ng văn hóa tinh th&acirc;̀n của nh&acirc;n d&acirc;n, đưa nh&acirc;n d&acirc;n lao đ&ocirc;̣ng thực sự trở thành chủ th&ecirc;̉ sáng tạo và hưởng thụ văn hóa.</p>\r\n\r\n<ul>\r\n	<li>Đặc trưng cơ bản của n&ecirc;̀n văn hóa xã h&ocirc;̣i chủ nghĩa</li>\r\n</ul>\r\n\r\n<p>+ H&ecirc;̣ tư tưởng của giai c&acirc;́p c&ocirc;ng nh&acirc;n là n&ocirc;̣i dung c&ocirc;́t lõi, giữ vai trò chủ đạo, quy&ecirc;́t định phương hướng phát tri&ecirc;̉n n&ecirc;̀n văn hóa xã h&ocirc;̣i chủ nghĩa</p>\r\n\r\n<p>+ N&ecirc;̀n văn hóa xã h&ocirc;̣i chủ nghĩa là n&ecirc;̀n văn hóa có tính nh&acirc;n d&acirc;n r&ocirc;̣ng rãi và tính d&acirc;n t&ocirc;̣c s&acirc;u sắc.</p>\r\n\r\n<p>+ N&ecirc;̀n văn hóa XHCN là n&ecirc;̀n văn hóa được hình thành, phát tri&ecirc;̉n m&ocirc;̣t cách tự giác, đặt dưới sự lãnh đạo của giai c&acirc;́p c&ocirc;ng nh&acirc;n th&ocirc;ng qua t&ocirc;̉ chức đảng c&ocirc;̣ng sản, có sự quản lý của nhà nước XHCN.</p>\r\n\r\n<ol start=\"2\">\r\n	<li><strong>Tính t&acirc;́t y&ecirc;́u của vi&ecirc;̣c x&acirc;y dựng n&ecirc;̀n văn hóa xã h&ocirc;̣i chủ nghĩa</strong></li>\r\n</ol>\r\n\r\n<p>Tính t&acirc;́t y&ecirc;́u của vi&ecirc;̣c x&acirc;y dựng n&ecirc;̀n văn hóa xã h&ocirc;̣i chủ nghĩa xu&acirc;́t phát từ những căn cứ sau:</p>\r\n\r\n<ul>\r\n	<li>Tính tri&ecirc;̣t đ&ecirc;̉, toàn di&ecirc;̣n của cách mạng XHCN đòi hỏi phải thay đ&ocirc;̉i phương thức sản xu&acirc;́t tinh th&acirc;̀n, làm cho phương thức sản xu&acirc;́t tinh th&acirc;̀n phù hợp với phương thức sản xu&acirc;́t mới của xã h&ocirc;̣i XHCN.</li>\r\n	<li>X&acirc;y dựng n&ecirc;̀n văn hóa XHCN là t&acirc;́t y&ecirc;́u trong quá trình cải tạo t&acirc;m lý, ý thức và đời s&ocirc;́ng tinh th&acirc;̀n của ch&ecirc;́ đ&ocirc;̣ cũ đ&ecirc;̉ lại nhằm giải phóng nh&acirc;n d&acirc;n lao đ&ocirc;̣ng thoát khỏi ảnh hưởng tư tưởng, ý thức của xã h&ocirc;̣i cũ lạc h&acirc;̣u.</li>\r\n	<li>X&acirc;y dựng n&ecirc;̀n văn hóa XHCN là t&acirc;́t y&ecirc;́u trong quá trình n&acirc;ng cao trình đ&ocirc;̣ văn hóa cho qu&acirc;̀n chúng nh&acirc;n d&acirc;n lao đ&ocirc;̣ng. Đ&acirc;y là đi&ecirc;̀u ki&ecirc;̣n c&acirc;̀n thi&ecirc;́t đ&ecirc;̉ đ&ocirc;ng đảo qu&acirc;̀n chúng nh&acirc;n d&acirc;n lao đọng chi&ecirc;́n thắng nghèo nàn, lạc h&acirc;̣u, n&acirc;ng cao trình đ&ocirc;̣ và nhu c&acirc;̀u văn hóa của qu&acirc;̀n chúng.</li>\r\n	<li>X&acirc;y dựng n&ecirc;̀n văn hóa XHCN là m&ocirc;̣t t&acirc;́t y&ecirc;́u khách quan, bởi vì văn hóa vừa là mục ti&ecirc;u, vừa là đ&ocirc;̣ng lực của quá trình x&acirc;y dựng chủ nghĩa xã h&ocirc;̣i.</li>\r\n</ul>\r\n\r\n<ol start=\"3\">\r\n	<li><strong>N&ocirc;̣i dung và phương thức x&acirc;y dựng n&ecirc;̀n văn hóa XHCN</strong></li>\r\n</ol>\r\n\r\n<p><span style=\"color:#e74c3c\">a, Những n&ocirc;̣i dung cơ bản của quá trình x&acirc;y dựng n&ecirc;̀n văn hóa XHCN</span></p>\r\n\r\n<ul>\r\n	<li>C&acirc;̀n phải n&acirc;ng cao trình đ&ocirc;̣ d&acirc;n trí, hình thành đ&ocirc;̣i ngũ tri thức của xã h&ocirc;̣i mới.</li>\r\n	<li>X&acirc;y dựng con người phát tri&ecirc;̉n toàn di&ecirc;̣n</li>\r\n	<li>X&acirc;y dựng l&ocirc;́i s&ocirc;́ng xã h&ocirc;̣i chủ nghĩa</li>\r\n	<li>X&acirc;y dựng gia đình văn hóa</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#e74c3c\">b, Phương thức x&acirc;y dựng n&ecirc;̀n văn hóa XHCN</span></p>\r\n\r\n<ul>\r\n	<li>Giữ vững và tăng cường vai trò chủ đạo của h&ecirc;̣ tư tưởng giai c&acirc;́p c&ocirc;ng nh&acirc;n trong đời s&ocirc;́ng tinh th&acirc;̀n của xã h&ocirc;̣i.</li>\r\n	<li>Kh&ocirc;ng ngừng tăng cường sự lãnh đạo của đảng c&ocirc;̣ng sản và vai trò quản lý của nhà nước XHCN đ&ocirc;́i với hoạt đ&ocirc;̣ng văn hóa.</li>\r\n	<li>X&acirc;y dựng n&ecirc;̀n văn hóa XHCN phải theo phương thức k&ecirc;́t hợp giữa vi&ecirc;̣c thừa k&ecirc;́ những giá trị trong di sản văn hóa d&acirc;n t&ocirc;̣c với vi&ecirc;̣c ti&ecirc;́p thu có chọn lọc những tinh hoa của văn hóa nh&acirc;n loại.</li>\r\n	<li>T&ocirc;̉ chức và l&ocirc;i cu&ocirc;́n qu&acirc;̀n chúng nh&acirc;n d&acirc;n vào các hoạt đ&ocirc;̣ng và sáng tạo văn hóa</li>\r\n</ul>\r\n\r\n<p><strong>III. GIẢI QUY&Ecirc;́T V&Acirc;̀N Đ&Ecirc;̀ D&Acirc;N T&Ocirc;̣C VÀ T&Ocirc;N GIÁO</strong></p>\r\n\r\n<ol>\r\n	<li><strong>V&acirc;́n đ&ecirc;̀ d&acirc;n t&ocirc;̣c và những quan đi&ecirc;̉m cơ bản của chủ nghĩa Mac-Lenin trong vi&ecirc;̣c giải quy&ecirc;́t v&acirc;́n đ&ecirc;̀ d&acirc;n t&ocirc;̣c</strong></li>\r\n</ol>\r\n\r\n<p><span style=\"color:#e74c3c\">a, Khái ni&ecirc;̣m d&acirc;n t&ocirc;̣c</span></p>\r\n\r\n<p>Khái ni&ecirc;̣m d&acirc;n t&ocirc;̣c thường được dùng với 2 nghĩa:</p>\r\n\r\n<ul>\r\n	<li>Là m&ocirc;̣t b&ocirc;̣ ph&acirc;̣n của c&ocirc;̣ng đ&ocirc;̀ng qu&ocirc;́c gia, là c&ocirc;̣ng đ&ocirc;̀ng xã h&ocirc;̣i theo nghĩa là các t&ocirc;̣c người.</li>\r\n	<li>Là m&ocirc;̣t c&ocirc;̣ng đ&ocirc;̀ng qu&ocirc;́c gia, là toàn b&ocirc;̣ nh&acirc;n d&acirc;n m&ocirc;̣t nước, là qu&ocirc;́c gia- d&acirc;n t&ocirc;̣c.</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Khái ni&ecirc;̣m qu&ocirc;́c gia và khái ni&ecirc;̣m d&acirc;n t&ocirc;̣c có sự gắn bó chặt chẽ với nhau, b&ocirc;̉ sung và thúc đ&acirc;̉y l&acirc;̃n nhau trong quá trình phát tri&ecirc;̉n.</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#e74c3c\">b, Hai xu hướng phát tri&ecirc;̉n của d&acirc;n t&ocirc;̣c và v&acirc;́n đ&ecirc;̀ d&acirc;n t&ocirc;̣c trong x&acirc;y dựng chủ nghĩa xã h&ocirc;̣i</span></p>\r\n\r\n<ul>\r\n	<li>Xu hướng thứ nh&acirc;́t: do sự chín mu&ocirc;̀i của ý thức d&acirc;n t&ocirc;̣c, sự thức tỉnh v&ecirc;̀ quy&ecirc;̀n s&ocirc;́ng của mình, các c&ocirc;̣ng đ&ocirc;̀ng d&acirc;n cư mu&ocirc;́n tách ra đ&ecirc;̉ thành l&acirc;̣p các qu&ocirc;́c gia d&acirc;n t&ocirc;̣c đ&ocirc;̣c l&acirc;̣p</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:40px\">&nbsp;+ bi&ecirc;̉u hi&ecirc;̣n của xu hướng: phong trào đ&acirc;́u tranh ch&ocirc;́ng áp bức d&acirc;n t&ocirc;̣c; có tác đ&ocirc;̣ng n&ocirc;̉i b&acirc;̣t trong giai đoạn đ&acirc;̀u của chủ nghĩa tư bản.</p>\r\n\r\n<ul>\r\n	<li>Xu hướng thứ hai: các d&acirc;n t&ocirc;̣c ở từng qu&ocirc;́c gia, k&ecirc;̉ cả các d&acirc;n t&ocirc;̣c ở nhi&ecirc;̀u qu&ocirc;́c gia mu&ocirc;́n li&ecirc;n hi&ecirc;̣p lại với nhau.</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:40px\">+ bi&ecirc;̉u hi&ecirc;̣n của xu hướng: sự giao lưu kinh t&ecirc;́, văn hóa tạo n&ecirc;n m&ocirc;́i li&ecirc;n h&ecirc;̣ qu&ocirc;́c gia và qu&ocirc;́c t&ecirc;́ mở r&ocirc;̣ng giữa các d&acirc;n t&ocirc;̣c, xóa bỏ sự bi&ecirc;̣t l&acirc;̣p, thúc đ&acirc;̉y các d&acirc;n t&ocirc;̣c lại g&acirc;̀n nhau.</p>\r\n\r\n<ul>\r\n	<li>D&acirc;n t&ocirc;̣c trong ti&ecirc;́n trình x&acirc;y dựng chủ nghĩa xã h&ocirc;̣i có sự v&acirc;̣n đ&ocirc;̣ng mới theo hướng ngày càng ti&ecirc;́n b&ocirc;̣, văn minh. Trong đó 2 xu hướng khách quan của sự phát tri&ecirc;̉n d&acirc;n t&ocirc;̣c sẽ phát huy tác dụng cùng chi&ecirc;̀u, b&ocirc;̉ sung, h&ocirc;̃ trợ cho nhau và di&ecirc;̃n ra trong từng d&acirc;n t&ocirc;̣c, trong cả c&ocirc;̣ng đ&ocirc;̀ng qu&ocirc;́c gia. Quan h&ecirc;̣ d&acirc;n t&ocirc;̣c sẽ là bi&ecirc;̉u hi&ecirc;̣n sinh đ&ocirc;̣ng của hai xu hướng đó trong đi&ecirc;̀u ki&ecirc;̣n của c&ocirc;ng cu&ocirc;̣c x&acirc;y dựng xã h&ocirc;̣i mới- xã h&ocirc;̣i XHCN.</li>\r\n	<li>Ti&ecirc;́n trình x&acirc;y dựng chủ nghĩa xã h&ocirc;̣i đã tạo ra những đi&ecirc;̀u ki&ecirc;̣n thu&acirc;̣n lợi đ&ecirc;̉ x&acirc;y dựng quan h&ecirc;̣ d&acirc;n t&ocirc;̣c bình đẳng, hợp tác giúp đỡ nhau cùng ti&ecirc;́n b&ocirc;̣ giữa các d&acirc;n t&ocirc;̣c.</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#e74c3c\">c, Những quan đi&ecirc;̉m cơ bản của chủ nghĩa Mac-Lenin trong vi&ecirc;̣c giải quy&ecirc;́t v&acirc;́n đ&ecirc;̀ d&acirc;n t&ocirc;̣c.</span></p>\r\n\r\n<ul>\r\n	<li>Theo quan đi&ecirc;̉m của Mac-Lenin giải quy&ecirc;́t v&acirc;́n đ&ecirc;̀ d&acirc;n t&ocirc;̣c phải gắn với cách mạng v&ocirc; sản và tr&ecirc;n cơ sở của cách mạng xã h&ocirc;̣i chủ nghĩa. Khi xem xét và giải quy&ecirc;́t v&acirc;́n đ&ecirc;̀ d&acirc;n t&ocirc;̣c phải đứng vững tr&ecirc;n l&acirc;̣p trường giai c&acirc;́p c&ocirc;ng nh&acirc;n, có nghĩa là phải dựa tr&ecirc;n cơ sở và vì lợi ích cơ bản, l&acirc;u dài của d&acirc;n t&ocirc;̣c.</li>\r\n	<li>Dựa tr&ecirc;n tư tưởng của C.Mac &amp; Ph.Ăngghen, Lenin n&ecirc;u ra &ldquo; Cương lĩnh d&acirc;n t&ocirc;̣c&rdquo;&nbsp; ( được coi là cương lĩnh d&acirc;n t&ocirc;̣c của chủ nghĩa Mac-Lenin) với 3 n&ocirc;̣i dung cơ bản:</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:40px\">+ Các d&acirc;n t&ocirc;̣c hoàn toàn bình đẳng</p>\r\n\r\n<p style=\"margin-left:40px\">+ Các d&acirc;n t&ocirc;̣c được quy&ecirc;̀n tự quy&ecirc;́t</p>\r\n\r\n<p style=\"margin-left:40px\">+ Li&ecirc;n hi&ecirc;̣p c&ocirc;ng nh&acirc;n t&acirc;́t cả các d&acirc;n t&ocirc;̣c</p>\r\n\r\n<ol start=\"2\">\r\n	<li><strong>T&ocirc;n giáo và những quan đi&ecirc;̉m cơ bản của chủ nghĩa Mac-Lenin trong vi&ecirc;̣c giải quy&ecirc;́t v&acirc;́n đ&ecirc;̀ t&ocirc;n giáo.</strong></li>\r\n</ol>\r\n\r\n<p><span style=\"color:#e74c3c\">a, Khái ni&ecirc;̣m t&ocirc;n giáo</span></p>\r\n\r\n<ul>\r\n	<li>Với hình thái phát tri&ecirc;̉n đ&acirc;̀y đủ của t&ocirc;n giáo, nó bao g&ocirc;̀m: ý thức t&ocirc;n giáo, h&ecirc;̣ th&ocirc;̀ng t&ocirc;̉ chức t&ocirc;n giáo cùng với những hoạt đ&ocirc;̣ng mang tính ch&acirc;́t nghi thức tín ngưỡng của nó.</li>\r\n	<li>T&ocirc;n giáo là sản ph&acirc;̉m của con người, gắn với những đi&ecirc;̀u ki&ecirc;̣n tự nhi&ecirc;n và lịch sử cụ th&ecirc;̉, xác định. V&ecirc;̀ bản ch&acirc;́t, t&ocirc;n giáo là m&ocirc;̣t hi&ecirc;̣n tượng xã h&ocirc;̣i phản ánh sự b&ecirc;́ tắc, b&acirc;́t lực của con người trước tự nhi&ecirc;n và xã h&ocirc;̣i. Tuy nhi&ecirc;n trong ý thức t&ocirc;n giáo cũng chứa đựng nhi&ecirc;̀u giá trị phù hợp với đạo đức, đạo lý của con người.</li>\r\n	<li>T&ocirc;n giáo góp ph&acirc;̀n bù đắp những hụt h&acirc;̃ng trong cu&ocirc;̣c s&ocirc;́ng, n&ocirc;̃i tr&ocirc;́ng vắng trong t&acirc;m h&ocirc;̀n, xoa dịu n&ocirc;̃i đau của con người.</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#e74c3c\">b, V&acirc;́n đ&ecirc;̀ t&ocirc;n giáo trong ti&ecirc;́n trình x&acirc;y dựng chủ nghĩa xã h&ocirc;̣i</span></p>\r\n\r\n<p>Trong x&acirc;y dựng chủ nghĩa xã h&ocirc;̣i, t&ocirc;n giáo v&acirc;̃n còn t&ocirc;̀n tại: do những nguy&ecirc;n nh&acirc;n sau: ( 5 nn)</p>\r\n\r\n<ul>\r\n	<li>Nguy&ecirc;n nh&acirc;n nh&acirc;̣n thức: v&acirc;̃n còn nhi&ecirc;̀u hi&ecirc;̣n tượng tự nhi&ecirc;n, xã h&ocirc;̣i và của con người mà khoa học chưa lý giải được, trong khi đó trình đ&ocirc;̣ d&acirc;n trí lại v&acirc;̃n chưa thực sự được n&acirc;ng cao. Do đó khi&ecirc;́n cho m&ocirc;̣t b&ocirc;̣ ph&acirc;̣n nh&acirc;n d&acirc;n đi tìm sự an ủi che chở và lý giải chúng từ sức mạnh của th&acirc;̀n linh.</li>\r\n	<li>Nguy&ecirc;n nh&acirc;n kinh t&ecirc;́: trong đời s&ocirc;́ng hi&ecirc;̣n thực, sự b&acirc;́t bình đẳng v&ecirc;̀ kinh t&ecirc;́, chính trị, văn hóa, xã h&ocirc;̣i v&acirc;̃n còn di&ecirc;̃n ra, sự cách bi&ecirc;̣t khá lớn v&ecirc;̀ đời s&ocirc;́ng v&acirc;̣t ch&acirc;́t tinh th&acirc;̀n giữa các nhóm d&acirc;n cư còn t&ocirc;̀n tại ph&ocirc;̉ bi&ecirc;́n. Do đó những y&ecirc;́u t&ocirc;́ may rủi, ng&acirc;̃u nhi&ecirc;n v&acirc;̃n tác đ&ocirc;̣ng mạnh mẽ đ&ecirc;́n con người, làm cho con người d&ecirc;̃ trở n&ecirc;n thụ đ&ocirc;̣ng với tư tưởng nhờ c&acirc;̣y, c&acirc;̀u mong vào những lực lượng si&ecirc;u nhi&ecirc;n.</li>\r\n	<li>Nguy&ecirc;n nh&acirc;n t&acirc;m lý: trong m&ocirc;́i quan h&ecirc;̣ giữa t&ocirc;̀n tại xã h&ocirc;̣i và ý thức xã h&ocirc;̣i, ý thức xã h&ocirc;̣i thường có tính bảo thủ hơn so với sự bi&ecirc;́n đ&ocirc;̉i của t&ocirc;̀n tại xã h&ocirc;̣i, trong đó ý thức t&ocirc;n giáo thường lại là y&ecirc;́u t&ocirc;́ mang tính ch&acirc;́t b&ecirc;̀n vững nh&acirc;́t trong tinh th&acirc;̀n của m&ocirc;̃i con người, của xã h&ocirc;̣i.</li>\r\n	<li>Nguy&ecirc;n nh&acirc;n chính trị- xã h&ocirc;̣i: xét v&ecirc;̀ mặt giá trị, có những nguy&ecirc;n tắc của t&ocirc;n giáo phù hợp với chủ nghĩa xã h&ocirc;̣i, với chủ trương, đường l&ocirc;́i, chính sách của nhà nước XHCN. Đó là những giá trị đạo đức, văn hóa với tinh th&acirc;̀n nh&acirc;n đạo, hướng thi&ecirc;̣n&hellip; đáp ứng được nhu c&acirc;̀u của m&ocirc;̣t b&ocirc;̣ ph&acirc;̣n qu&acirc;̀n chúng nh&acirc;n d&acirc;n. Chính vì th&ecirc;́, trong m&ocirc;̣t chừng mực nh&acirc;́t định, t&ocirc;n giáo có sức thu hút mạnh mẽ đ&ocirc;́i với m&ocirc;̣t b&ocirc;̣ ph&acirc;̣n qu&acirc;̀n chúng nh&acirc;n d&acirc;n.</li>\r\n	<li>Nguy&ecirc;n nh&acirc;n văn hóa: trong sinh hoạt văn hóa xã h&ocirc;̣i, sinh hoạt tín ngưỡng t&ocirc;n giáo đã đáp ứng được nhu c&acirc;̀u văn hóa tinh th&acirc;̀n của c&ocirc;̣ng đ&ocirc;̀ng xã h&ocirc;̣i và trong m&ocirc;̣t mức đ&ocirc;̣ nh&acirc;́t định, có ý nghĩa giáo dục ý thức c&ocirc;̣ng đ&ocirc;̀ng, phong cách l&ocirc;́i s&ocirc;́ng của m&ocirc;̃i cá nh&acirc;n trong c&ocirc;̣ng đ&ocirc;̀ng.</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#e74c3c\">c, Các quan đi&ecirc;̉m cơ bản của chủ nghĩa Mac-Lenin trong vi&ecirc;̣c giải quy&ecirc;́t v&acirc;́n đ&ecirc;̀ t&ocirc;n giáo (5 qđ)</span></p>\r\n\r\n<ul>\r\n	<li>Giải quy&ecirc;́t những v&acirc;́n đ&ecirc;̀ phát sinh từ t&ocirc;n giáo trong đời s&ocirc;́ng xã h&ocirc;̣i phải gắn li&ecirc;̀n với quá trình cải tạo xã h&ocirc;̣i cũ, x&acirc;y dựng xã h&ocirc;̣i mới.</li>\r\n	<li>T&ocirc;n trọng tự do tín ngưỡng, tự do t&ocirc;n giáo của nh&acirc;n d&acirc;n.</li>\r\n	<li>Thực hi&ecirc;̣n đoàn k&ecirc;́t những người có t&ocirc;n giáo với những người kh&ocirc;ng có t&ocirc;n giáo, đoàn k&ecirc;́t các t&ocirc;n giáo, đoàn k&ecirc;́t những người theo t&ocirc;n giáo với những người kh&ocirc;ng theo t&ocirc;n giáo, đoàn k&ecirc;́t toàn d&acirc;n t&ocirc;̣c. Nghi&ecirc;m c&acirc;́m chia rẽ c&ocirc;̣ng đ&ocirc;̀ng vì lý do tín ngưỡng t&ocirc;n giáo.</li>\r\n	<li>Ph&acirc;n bi&ecirc;̣t rõ 2 mặt chính trị và tư tưởng tr&ocirc;ng v&acirc;́n đ&ecirc;̀ t&ocirc;n giáo. Mặt tư tưởng th&ecirc;̉ hi&ecirc;̣n sự tín ngưỡng trong t&ocirc;n giáo. Mặt chính trị là sự lợi dụng t&ocirc;n giáo của những ph&acirc;̀n tử phản đ&ocirc;̣ng nhằm ch&ocirc;́ng lại sự nghi&ecirc;̣p cách mạng, sự nghi&ecirc;̣p x&acirc;y dựng chủ nghĩa xã h&ocirc;̣i.</li>\r\n	<li>Phải có quan đi&ecirc;̉m lịch sử- cụ th&ecirc;̉ khi giải quy&ecirc;́t v&acirc;́n đ&ecirc;̀ t&ocirc;n giáo.</li>\r\n</ul>', '1551582908', null, '0', '42', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('261', 'PHẦN I.5 LÝ THUYẾT TRỌNG TÂM CHƯƠNG 9', '<p style=\"text-align:center\"><strong>CHƯƠNG IX. CHỦ NGHĨA XÃ H&Ocirc;̣I HI&Ecirc;̣N THỰC VÀ TRI&Ecirc;̉N VỌNG</strong></p>\r\n\r\n<p><strong>I. CHỦ NGHĨA XÃ H&Ocirc;̣I HI&Ecirc;̣N THỰC </strong></p>\r\n\r\n<ol>\r\n	<li><strong>Cách mạng Tháng Mười Nga và m&ocirc; hình chủ nghĩa xã h&ocirc;̣i hi&ecirc;̣n thực đ&acirc;̀u ti&ecirc;n tr&ecirc;n th&ecirc;́ giới.</strong></li>\r\n</ol>\r\n\r\n<p><span style=\"color:#e74c3c\">a, Cách mạng Tháng Mười Nga</span></p>\r\n\r\n<p>Cách mạng Tháng Mười Nga là thắng lợi vĩ đại nh&acirc;́t của giai c&acirc;́p c&ocirc;ng nh&acirc;n, nh&acirc;n d&acirc;n lao đ&ocirc;̣ng và các d&acirc;n t&ocirc;̣c bị áp bức do giai c&acirc;́p c&ocirc;ng nh&acirc;n và đ&ocirc;̣i ti&ecirc;n phong của họ là Đảng B&ocirc;ns&ecirc;vich lãnh đạo.</p>\r\n\r\n<p>Với sự thắng lợi của cách mạng Tháng Mười Nga, lịch sử đã mở ra m&ocirc;̣t con đường mới cho sự giải phóng các d&acirc;n t&ocirc;̣c bị chủ nghĩa thực d&acirc;n áp bức. Nó đã mở đ&acirc;̀u m&ocirc;̣t thời đại mới trong lịch sử- thời đại quá đ&ocirc;̣ từ chủ nghĩa tư bản l&ecirc;n chủ nghĩa xã h&ocirc;̣i tr&ecirc;n phạm vi toàn th&ecirc;́ giới.</p>\r\n\r\n<p><span style=\"color:#e74c3c\">b, M&ocirc; hình chủ nghĩa xã h&ocirc;̣i đ&acirc;̀u ti&ecirc;n tr&ecirc;n th&ecirc;́ giới</span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><strong>Sự ra đời, phát tri&ecirc;̉n của h&ecirc;̣ th&ocirc;́ng xã h&ocirc;̣i chủ nghĩa và những thành tựu của chủ nghĩa xã h&ocirc;̣i hi&ecirc;̣n thực.</strong></li>\r\n</ol>\r\n\r\n<p><span style=\"color:#e74c3c\">a, Sự ra đời và phát tri&ecirc;̉n của h&ecirc;̣ th&ocirc;́ng các nước xã h&ocirc;̣i chủ nghĩa</span></p>\r\n\r\n<p>Sau chi&ecirc;́n tranh th&ecirc;́ giới thứ hai, h&ecirc;̣ th&ocirc;́ng xã h&ocirc;̣i chủ nghĩa th&ecirc;́ giới ra đời bao g&ocirc;̀m 13 nước. Năm 1960, tại Maxcova H&ocirc;̣i nghị 81 Đảng C&ocirc;̣ng sản và c&ocirc;ng nh&acirc;n của các nước tr&ecirc;n th&ecirc;́ giới đã ra tuy&ecirc;n b&ocirc;́ và khẳng định: &ldquo; đặc đi&ecirc;̉m chủ y&ecirc;́u của thời đại chúng ta là h&ecirc;̣ th&ocirc;́ng xã h&ocirc;̣i chủ nghĩa th&ecirc;́ giới đang trở thành nh&acirc;n t&ocirc;́ quy&ecirc;́t định sự phát tri&ecirc;̉n của xã h&ocirc;̣i loài người&rdquo;</p>\r\n\r\n<p><span style=\"color:#e74c3c\">b, Những thành tựu của chủ nghĩa xã h&ocirc;̣i hi&ecirc;̣n thực ( 5 tt)</span></p>\r\n\r\n<ul>\r\n	<li>Ch&ecirc;́ đ&ocirc;̣ XHCN đã từng bước đưa nh&acirc;n d&acirc;n lao đ&ocirc;̣ng l&ecirc;n làm chủ xã h&ocirc;̣i, thúc đ&acirc;̉y trào lưu đ&acirc;́u tranh cho quy&ecirc;̀n tự do d&acirc;n chủ tr&ecirc;n toàn th&ecirc;́ giới.</li>\r\n	<li>Trong hơn 70 năm x&acirc;y dựng CNXH, Li&ecirc;n X&ocirc; và các nước XHCN khác đã đạt được sự phát tri&ecirc;̉n mạnh mẽ v&ecirc;̀ ti&ecirc;̀m lực kinh t&ecirc;́, x&acirc;y dựng cơ sở v&acirc;̣t ch&acirc;́t của chủ nghĩa xã h&ocirc;̣i tr&ecirc;n quy m&ocirc; lớn với trình đ&ocirc;̣ hi&ecirc;̣n đại, bảo đảm ngày càng t&ocirc;́t hơn đời s&ocirc;́ng v&acirc;̣t ch&acirc;́t và tinh th&acirc;̀n của nh&acirc;n d&acirc;n.</li>\r\n	<li>Với sự lớn mạnh toàn di&ecirc;̣n, chủ nghĩa xã h&ocirc;̣i có ảnh hưởng s&acirc;u sắc trong đời s&ocirc;́ng chính trị th&ecirc;́ giới, đóng vai trò quy&ecirc;́t định đ&ocirc;́i với sự sụp đ&ocirc;̉ h&ecirc;̣ th&ocirc;́ng thu&ocirc;̣c địa của chủ nghĩa đ&ecirc;́ qu&ocirc;́c, mở ra kỷ nguy&ecirc;n mới- kỷ nguy&ecirc;n đ&ocirc;̣c l&acirc;̣p d&acirc;n t&ocirc;̣c và thời đại quá đ&ocirc;̣ l&ecirc;n CNXH tr&ecirc;n phạm vi toàn th&ecirc;́ giới.</li>\r\n	<li>Sức mạnh của chủ nghĩa xã h&ocirc;̣i hi&ecirc;̣n thực đóng vai trò quy&ecirc;́t định đ&acirc;̉y lùi nguy cơ chi&ecirc;́n tranh hủy di&ecirc;̣t, bảo v&ecirc;̣ hòa bình th&ecirc;́ giới.</li>\r\n	<li>Tại các nước phương T&acirc;y, nh&acirc;n d&acirc;n lao đ&ocirc;̣ng được sức h&acirc;́p d&acirc;̃n thực t&ecirc;́ của chủ nghĩa xã h&ocirc;̣i đã đ&acirc;́u tranh đòi các quy&ecirc;̀n d&acirc;n sinh, d&acirc;n chủ, các phúc lợi xã h&ocirc;̣i&hellip; với sức ép của các nước XHCN,các nước phương t&acirc;y đã phải nhượng b&ocirc;̣ và ch&acirc;́p nh&acirc;̣n thực t&ecirc;́ r&acirc;́t nhi&ecirc;̀u y&ecirc;u sách đó.</li>\r\n</ul>\r\n\r\n<p><strong>II. SỰ KHỦNG HOẢNG, SỤP Đ&Ocirc;̉ CỦA M&Ocirc; HÌNH CHỦ NGHĨA XÃ H&Ocirc;̣I X&Ocirc; VI&Ecirc;́T VÀ NGUY&Ecirc;N NH&Acirc;N CỦA NÓ.</strong></p>\r\n\r\n<ol>\r\n	<li><strong>Sự khủng hoảng và sụp đ&ocirc;̉ của m&ocirc; hình chủ nghĩa xã h&ocirc;̣i X&ocirc; vi&ecirc;́t</strong></li>\r\n	<li><strong>Nguy&ecirc;n nh&acirc;n d&acirc;̃n đ&ecirc;́n sự khủng hoảng và sụp đ&ocirc;̉ của &ocirc; hình chủ nghĩa xã h&ocirc;̣i X&ocirc; vi&ecirc;́t</strong></li>\r\n</ol>\r\n\r\n<p><span style=\"color:#e74c3c\">a, Nguy&ecirc;n nh&acirc;n s&acirc;u xa</span></p>\r\n\r\n<ul>\r\n	<li>Thời gian đ&acirc;̀u, k&ecirc;́ hoạch hóa t&acirc;̣p trung đã phát huy tác dụng mạnh mẽ, song đã bi&ecirc;́n dạng thành k&ecirc;́ hoạch hóa t&acirc;̣p trung quan li&ecirc;u, bao c&acirc;́p. Sau chi&ecirc;́n tranh th&ecirc;́ giới thứ 2 Li&ecirc;n X&ocirc; v&acirc;̃n ti&ecirc;́p tục duy trì m&ocirc; hình này =&gt; ch&ecirc;́ đ&ocirc;̣ bao c&acirc;́p tràn lan, tri&ecirc;̣t ti&ecirc;u tính chủ đ&ocirc;̣ng, sáng tạo của người lao đ&ocirc;̣ng.</li>\r\n	<li>Ch&acirc;̣m đ&ocirc;̉i mới cơ ch&ecirc;́ kinh t&ecirc;́, h&ecirc;̣ th&ocirc;́ng quản lý, nói chung là ch&acirc;̣m đ&ocirc;̉i mới m&ocirc; hình chủ nghĩa xã h&ocirc;̣i. H&acirc;̣u quả là Li&ecirc;n X&ocirc; đã thua kém rõ r&ecirc;̣t với các nước tư bản trong lĩnh vực c&ocirc;ng ngh&ecirc;̣ và năng su&acirc;́t lao đ&ocirc;̣ng.</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#e74c3c\">b, Nguy&ecirc;n nh&acirc;n chủ y&ecirc;́u, trực ti&ecirc;́p</span></p>\r\n\r\n<ul>\r\n	<li>Trong cải t&ocirc;̉, Đảng c&ocirc;̣ng sản Li&ecirc;n X&ocirc; đã mắc sai l&acirc;̀m nghi&ecirc;m trọng v&ecirc;̀ đường l&ocirc;́i chính trị, tư tưởng và t&ocirc;̉ chức. Đó là đường l&ocirc;́i hữu khuynh, cơ h&ocirc;̣i và xét lại, xa rời những nguy&ecirc;n tắc của chủ nghĩa Mac- Lenin; nguy hi&ecirc;̉m hơn lại ở m&ocirc;̣t b&ocirc;̣ ph&acirc;̣n lớn những người lãnh đạo cao nh&acirc;́t trong b&ocirc;̣ máy lãnh đạo Đảng và Nhà nước.</li>\r\n	<li>Chủ nghĩa đ&ecirc;́ qu&ocirc;́c thực d&acirc;n đã can thi&ecirc;̣p toàn di&ecirc;̣n, vừa tinh vi, vừa trắng trợn, thực hi&ecirc;̣n được &ldquo;di&ecirc;̃n bi&ecirc;́n hòa bình&rdquo; trong n&ocirc;̣i b&ocirc;̣ Li&ecirc;n X&ocirc; và các nước Đ&ocirc;ng &Acirc;u.</li>\r\n</ul>\r\n\r\n<p>Tóm lại, sự phá hoại của chủ nghĩa đ&ecirc;́ qu&ocirc;́c cùng với sự phản b&ocirc;̣i từ b&ecirc;n trong và từ tr&ecirc;n chóp bu của cơ quan lãnh đạo cao nát là nguy&ecirc;n nh&acirc;n trực ti&ecirc;́p làm cho Li&ecirc;n X&ocirc; sụp đ&ocirc;̉.</p>\r\n\r\n<p><strong>III. TRI&Ecirc;̉N VỌNG CỦA CHỦ NGHĨA XÃ H&Ocirc;̣I</strong></p>\r\n\r\n<ol>\r\n	<li><strong>Chủ nghĩa tư bản kh&ocirc;ng phải là tương lai của xã h&ocirc;̣i loài người.</strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Bản ch&acirc;́t của chủ nghĩa tư bản kh&ocirc;ng thay đ&ocirc;̉i.</li>\r\n	<li>C&acirc;́c y&ecirc;́u t&ocirc;́ của XHCN đã xu&acirc;́t hi&ecirc;̣n trong lòng xã h&ocirc;̣i tư bản.</li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><strong>Chủ nghĩa xã h&ocirc;̣i- tương lai của xã h&ocirc;̣i loài người.</strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Li&ecirc;n X&ocirc; và các nước XHCN Đ&ocirc;ng &Acirc;u sụp đ&ocirc;̉ kh&ocirc;ng có nghĩa là sự cáo chung của chủ nghĩa xã h&ocirc;̣i.</li>\r\n	<li>Các nước XHCN còn lại ti&ecirc;́n hành cải cách, đ&ocirc;̉i mới và ngày càng đạt được những thành tựu to lớn.</li>\r\n	<li>Đã xu&acirc;́t hi&ecirc;̣n những nh&acirc;n t&ocirc;́ mới của xu hướng đi l&ecirc;n chủ nghĩa xã h&ocirc;̣i ở m&ocirc;̣t s&ocirc;́ qu&ocirc;́c gia trong th&ecirc;́ giới đương đại.</li>\r\n</ul>', '1551582967', null, '0', '42', '1', '0', null, '0', null, null, null, null, null, null, null, null);
