INSERT INTO `lesson` VALUES ('270', 'Chương 1: 1  Giới thiệu khái quát về Marketing', '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">CHƯƠNG 1: GIỚI THIỆU VỀ MARETKING</span></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\">&nbsp;Bản chất của marketing</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Lịch sử ph&aacute;t triển của marketing</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+Sản xuất sản phẩm quy&ecirc;t định</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+Ph&acirc;n phối- b&aacute;n h&agrave;ng quyết định</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+Nhu cầu thị trường quyết định</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+Kh&aacute;ch h&agrave;ng c&oacute; sức mua v&agrave; nhu cầu chưa được thỏa m&atilde;n quyết định</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Ph&acirc;n định marketing cổ điển v&agrave; marketing hiện đại</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Điểm xuất ph&aacute;t</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Mục ti&ecirc;u</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Hoạt động</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">C&ocirc;ng cụ</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\">Một số kh&aacute;i niệm</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><u><em>Marketing</em></u>: Marketing l&agrave; một dạng hoạt động của con người nhằm thỏa m&atilde;n nhu cầu v&agrave; mong muốn th&ocirc;ng qua trao đổi.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><u><em>Nhu cầu (need</em></u>): cảm gi&aacute;c thiếu hụt m&agrave; c&ograve;n người c&oacute; thể cảm nhận được. </span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><u><em>Mong muốn (want)</em></u>: biểu hiện cụ thể của nhu cầu, tương ứng với tr&igrave;nh độ v&agrave; nh&acirc;n c&aacute;ch con người.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><u><em>Y&ecirc;u cầu (demand)</em></u>: l&agrave; những mong muốn c&oacute; khả năng thanh to&aacute;n.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><u><em>Sản phẩm:</em></u> tất cả những g&igrave; c&oacute; thể thỏa m&atilde;n nhu cầu, được cung ứng cho thị trường nhằm tạo sự ch&uacute; &yacute;, mua, hay ti&ecirc;u d&ugrave;ng.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><u><em>Trao đổi:</em></u> h&agrave;nh vi nhận một thứ n&agrave;o đ&oacute; v&agrave; đưa lại một thứ g&igrave; đ&oacute;.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><u><em>Thị trường:</em></u> tập hợp những người mua h&agrave;ng hiện c&oacute; v&agrave; sẽ c&oacute;.</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\">Qu&aacute; tr&igrave;nh tiến động marketing</span></strong></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">B1: Thị trường (nhu cầu)</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">B2: Ấn định sản phẩm</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">B3: Chọn thị trường đ&iacute;ch v&agrave; mục ti&ecirc;u</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">B4: Định sản phẩm hỗn hợp</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">B5: Định gi&aacute; b&aacute;n</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">B6: Định k&ecirc;nh ph&acirc;n phối</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">B7: Định sức b&aacute;n</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">B8: X&uacute;c tiến thương mại</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">B9:&nbsp; Theo d&otilde;i v&agrave; khuếch trương b&aacute;n</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">B10: Thị trường (nhu cầu sản phẩm)</span></span></span></p>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\">Vai tr&ograve; v&agrave; chức năng của marketing</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><u><span style=\"font-size:13.0pt\">Vai tr&ograve; của marketing</span></u></em></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; &nbsp; &nbsp;K&ecirc;́t n&ocirc;́i doanh nghi&ecirc;̣p với thị trường.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; &nbsp; Là m&ocirc;̣t b&ocirc;̣ ph&acirc;̣n chức năng quan trọng giúp DN đạt được mục đích m&ocirc;̣t cách hi&ecirc;̣u quả nh&acirc;́t.</span></span></span></li>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Chức năng của marketing</span></span></span></em></u></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; &nbsp; Th&iacute;ch ứng</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; &nbsp; Ph&acirc;n phối</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; &nbsp; Ti&ecirc;u thụ</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; &nbsp; Hỗ trợ</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; &nbsp; Mạo hiểm</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; &nbsp; Điều h&ograve;a, phối hợp</span></span></span></li>\r\n</ul>', '1551696780', null, '0', '58', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('271', 'Câu 1 (nhóm 1):', '<p>): N&ecirc;u kh&aacute;i niệm marketing. Ph&acirc;n biệt giữa marketing cổ điểm v&agrave; marketing hiện đại. N&ecirc;u c&aacute;c giai đoạn của marketing trong kinh doanh. Tr&igrave;nh b&agrave;y đặc điểm v&agrave; lấy v&iacute; dụ minh họa một trong c&aacute;c giai đoạn ph&aacute;t triển của marketing (nhu cầu thị trường quyết định; Kh&aacute;ch h&agrave;ng c&oacute; sức mua v&agrave; nhu cầu chưa được thỏa m&atilde;n quyết định). N&ecirc;u kh&aacute;i niệm gi&aacute; trị (theo quan điểmp marketing)? N&ecirc;u c&aacute;c chức năng của marketing kinh doanh? Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh họa một trong c&aacute;c chức năng marketing kinh doanh (Th&iacute;ch ứng, ti&ecirc;u thụ)</p>', '1551696977', null, '270', '58', '1', '270', '270', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('272', 'CHỮA ĐỀ THI', '<h1 style=\"text-align:center\"><span style=\"font-family:Times New Roman,serif\"><span style=\"font-size:18.6667px\"><strong>ĐỀ 1</strong></span></span></h1>\r\n\r\n<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">C&acirc;u 1 </span></span></span></strong></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">T&iacute;nh gần đ&uacute;ng gi&aacute; trị sau<span class=\"math-tex\">\\(A = \\ln \\left( {e + \\sqrt {4,01}  - \\sqrt[3]{{7,98}}} \\right),e \\approx 2,7273\\)</span> </span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">X&eacute;t h&agrave;m số <span class=\"math-tex\">\\(z = f\\left( {x;y} \\right) = \\ln \\left( {e + \\sqrt x  - \\sqrt[3]{y}} \\right)\\)</span> </span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Chọn <span class=\"math-tex\">\\({x_0} = 4;{y_0} = 8;\\Delta x = 0,01;\\Delta y =  - 0,02\\)</span> </span></span></span></p>\r\n\r\n<p style=\"margin-left:120px\"><span class=\"math-tex\">\\(\\begin{array}{l} f\\left( {4;8} \\right) = 1\\\\ f{\'_x}\\left( {x;y} \\right) = \\frac{1}{{2\\sqrt x \\left( {e + \\sqrt x  - \\sqrt[3]{y}} \\right)}} \\Rightarrow f{\'_x}\\left( {4;8} \\right) = \\frac{1}{{4{\\rm{e}}}}\\\\ f{\'_y}\\left( {x;y} \\right) = \\frac{{ - 1}}{{3\\sqrt[3]{{{y^2}}}\\left( {e + \\sqrt x  - \\sqrt[3]{y}} \\right)}} \\Rightarrow f{\'_x}\\left( {4;8} \\right) = \\frac{{ - 1}}{{{\\rm{12e}}}} \\end{array}\\)</span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"> </span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Vậy <span class=\"math-tex\">\\(A \\approx f\\left( {4;8} \\right) + f{\'_x}\\left( {4;8} \\right).\\Delta x + f{\'_y}\\left( {4;8} \\right).\\Delta y \\approx 1,00153\\)</span> </span></span></span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">X&eacute;t t&iacute;nh hội tụ&nbsp; ph&acirc;n k&igrave; của t&iacute;ch ph&acirc;n sau<span class=\"math-tex\">\\(I = \\int\\limits_0^1 {\\frac{{{x^3}}}{{\\sqrt {1 - {x^2}} }}} \\)</span> </span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span class=\"math-tex\">\\(I = \\int\\limits_0^1 {\\frac{{{x^3}}}{{\\sqrt {1 - {x^2}} }}}  = \\mathop {\\lim }\\limits_{a \\to {1^ - }} \\int\\limits_0^a {\\frac{{{x^3}}}{{\\sqrt {1 - {x^2}} }}} \\)</span></span> </span></span>&nbsp; &nbsp; &nbsp;&nbsp;</p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span class=\"math-tex\">\\(I\\left( a \\right) = \\int\\limits_0^a {\\frac{{{x^3}}}{{\\sqrt {1 - {x^2}} }}} \\)</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Đặt <span class=\"math-tex\">\\(t = \\sqrt {1 - {x^2}}  \\Rightarrow {x^2} = 1 - {t^2};x{\\rm{dx}} =  - t{\\rm{d}}t\\)</span>, Khi đ&oacute; ta c&oacute;</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span class=\"math-tex\">\\(\\begin{array}{l} I\\left( a \\right) = \\int\\limits_0^a {\\frac{{{x^3}}}{{\\sqrt {1 - {x^2}} }}}  = \\int\\limits_1^{\\sqrt {1 - {a^2}} } {\\frac{{(1 - {t^2}). - t{\\rm{d}}t}}{t}}  = \\int\\limits_1^{\\sqrt {1 - {a^2}} } {({t^2} - 1} )dt = \\frac{{{t^3}}}{3} - t\\left| \\begin{array}{l} \\sqrt {1 - {a^2}} \\\\ 1 \\end{array} \\right.\\\\  = \\frac{{\\sqrt {{{\\left( {1 - {a^2}} \\right)}^3}} }}{3} - \\sqrt {1 - {a^2}}  + \\frac{2}{3}\\\\  \\Rightarrow I = \\int\\limits_0^1 {\\frac{{{x^3}}}{{\\sqrt {1 - {x^2}} }}}  = \\mathop {\\lim }\\limits_{a \\to {1^ - }} \\int\\limits_0^a {\\frac{{{x^3}}}{{\\sqrt {1 - {x^2}} }}}  = \\mathop {\\lim }\\limits_{a \\to {1^ - }} \\left( {\\frac{{\\sqrt {{{\\left( {1 - {a^2}} \\right)}^3}} }}{3} - \\sqrt {1 - {a^2}}  + \\frac{2}{3}} \\right) = \\frac{2}{3} \\end{array}\\)</span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"> </span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Vậy t&iacute;ch ph&acirc;n suy rộng đ&atilde; cho hội tụ v&agrave; <span class=\"math-tex\">\\(I = \\int\\limits_0^1 {\\frac{{{x^3}}}{{\\sqrt {1 - {x^2}} }}}  = \\frac{2}{3}\\)</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">C&acirc;u 2</span></span></span></strong></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Giải phương tr&igrave;nh vi ph&acirc;n<span class=\"math-tex\">\\( \\left( {{y^2} - 2} \\right)d{\\rm{x}} = \\left( {y + {x^2}y} \\right)dy\\)</span> </span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+Nhận thấy <span class=\"math-tex\">\\(y =  \\pm \\sqrt 2 \\)</span> l&agrave; một nghiệm của phương tr&igrave;nh vi ph&acirc;n đ&atilde; cho</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+<span class=\"math-tex\">\\(y \\ne  \\pm \\sqrt 2 \\)</span> th&igrave; ta thấy đ&acirc;y l&agrave; dạng phương tr&igrave;nh vi ph&acirc;n biến số ph&acirc;n li n&ecirc;n ta c&oacute;</span></span></span><span style=\"font-size:18px\"><span class=\"math-tex\">\\(\\begin{array}{l} \\frac{{d{\\rm{x}}}}{{1 + {x^2}}} = \\frac{{y{\\rm{d}}y}}{{{y^2} - 2}} \\Rightarrow \\int {\\frac{{d{\\rm{x}}}}{{1 + {x^2}}} = \\int {\\frac{{y{\\rm{d}}y}}{{{y^2} - 2}}} }  \\Leftrightarrow {\\mathop{\\rm arctanx}\\nolimits}  = \\frac{1}{2}\\ln \\left| {{y^2} - 2} \\right| + C\\\\ \\end{array}\\)</span></span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"> </span></span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Giải phương tr&igrave;nh sai ph&acirc;n <span class=\"math-tex\">\\(y\\left( {n + 2} \\right) + 2y\\left( {n + 1} \\right) + 4y\\left( n \\right) = 3n - 4\\)</span> </span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Giải phương tr&igrave;nh thuần nhất </span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phương tr&igrave;nh đặc trưng <span class=\"math-tex\">\\({\\lambda ^2} + 2\\lambda  + 4 = 0 \\Leftrightarrow \\lambda  =  - 1 \\pm \\sqrt 3 i = 2\\left( {\\cos \\frac{{2\\pi }}{3} \\pm i\\sin \\frac{{2\\pi }}{3}} \\right)\\)</span> </span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Suy ra <span class=\"math-tex\">\\(\\overline y \\left( n \\right) = {2^n}\\left( {{C_1}\\cos \\frac{{2n\\pi }}{3} + {C_2}i\\sin \\frac{{2n\\pi }}{3}} \\right)\\)</span> </span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Giải phương tr&igrave;nh kh&ocirc;ng thuần nhất </span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"math-tex\">\\(f\\left( n \\right) = 3n + 4 = {1^n}\\left( {3n - 4} \\right)\\)</span><span style=\"font-size:14.0pt\">tức l&agrave; &alpha;=1 kh&ocirc;ng l&agrave; nghiệm của phương tr&igrave;nh đặc trưng. </span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Do đ&oacute;<span class=\"math-tex\">\\( \\widehat y\\left( n \\right) = an + b\\)</span> Khi đ&oacute; </span></span></span><span style=\"font-size:18px\"><span class=\"math-tex\">\\(\\begin{array}{l} \\widehat y\\left( {n + 1} \\right) = an + \\left( {a + b} \\right)\\\\ \\widehat y\\left( {n + 2} \\right) = an + \\left( {2a + b} \\right) \\end{array}\\)</span></span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"> </span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Thay v&agrave;o phương tr&igrave;nh kh&ocirc;ng thuần nhất ta c&oacute;</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span class=\"math-tex\">\\(\\begin{array}{l} an + \\left( {2{\\rm{a}} + b} \\right) + 2{\\rm{a}}n + 2\\left( {a + b} \\right) + 4{\\rm{a}}n + 4b = 3n - 4\\\\  \\Leftrightarrow 7{\\rm{a}}n + \\left( {4{\\rm{a}} + 7b} \\right) = 3n - 4\\\\  \\Rightarrow \\left\\{ \\begin{array}{l} 7{\\rm{a}} = 3\\\\ 4{\\rm{a}} + 7b =  - 4 \\end{array} \\right. \\Leftrightarrow \\left\\{ \\begin{array}{l} a = \\frac{3}{7}\\\\ b = \\frac{{ - 40}}{{49}} \\end{array} \\right. \\end{array}\\)</span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"> </span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Vậy <span class=\"math-tex\">\\(y\\left( n \\right) = \\overline y \\left( n \\right) = {2^n}\\left( {{C_1}\\cos \\frac{{2n\\pi }}{3} + {C_2}i\\sin \\frac{{2n\\pi }}{3}} \\right) + \\frac{{3n}}{7} - \\frac{{40}}{{49}}\\)</span> </span></span></span></p>\r\n\r\n<h1 style=\"margin-left:72px; text-align:center\"><strong><span style=\"font-family:Times New Roman,serif\"><span style=\"font-size:18.6667px\">ĐỀ 2</span></span></strong></h1>\r\n\r\n<p style=\"margin-left:72px\"><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">C&acirc;u 1</span></span></span></strong></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">T&igrave;m cực trị c&oacute; điều kiện của h&agrave;m hai biến sau</span></span></span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"math-tex\">\\(\\begin{array}{l} {\\rm{z}} = f\\left( {x;y} \\right) = {x^3} + {y^3} - 3{\\rm{x}}y\\\\ g\\left( {x;y} \\right) = x + y + 2 = 0 \\end{array}\\)</span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"> </span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">X&eacute;t h&agrave;m Lagrange </span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span class=\"math-tex\">\\(L = {x^3} + {y^3} - 3{\\rm{x}}y + \\lambda \\left( {x + y + 2} \\right)\\)</span></span> </span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Điểm dừng của h&agrave;m Lagrange:</span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span class=\"math-tex\">\\(\\begin{array}{l} \\left\\{ \\begin{array}{l} L{\'_x} = 3{{\\rm{x}}^2} - 3y + \\lambda  = 0\\\\ L{\'_y} = 3{y^2} - 3x + \\lambda  = 0\\\\ L{\'_\\lambda } = x + y + 2 = 0 \\end{array} \\right. \\Leftrightarrow \\left\\{ \\begin{array}{l} 3{{\\rm{x}}^2} - 3y + \\lambda  = 0\\\\ \\left( {x - y} \\right)\\left( {x + y + 1} \\right) = 0\\\\ x + y + 2 = 0 \\end{array} \\right. \\Leftrightarrow \\left\\{ \\begin{array}{l} \\lambda  = 3y - 3{{\\rm{x}}^2}\\\\ x = y\\\\ x + y + 2 = 0 \\end{array} \\right.\\\\  \\Leftrightarrow \\left\\{ \\begin{array}{l} x = y =  - 1\\\\ \\lambda  =  - 6 \\end{array} \\right. \\end{array}\\)</span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"> </span></span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Ta c&oacute; </span></span></span><span class=\"math-tex\">\\(\\begin{array}{l} L\'{\'_{x{\\rm{x}}}} = 6{\\rm{x}} =  - 6;L\'{\'_{xy}} =  - 3;L\'{\'_{yy}} = 6y =  - 6\\\\ g{\'_x} = 1;g{\'_y} = 1 \\end{array}\\)</span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">V&agrave; </span></span></span><span class=\"math-tex\">\\(\\left| H \\right| = \\left| {\\begin{array}{*{20}{c}} 0&amp;1&amp;1\\\\ 1&amp;{ - 6}&amp;{ - 3}\\\\ 1&amp;{ - 3}&amp;{ - 6} \\end{array}} \\right| = 6 &gt; 0\\)</span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;</span></span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Vậy h&agrave;m số đ&atilde; cho c&oacute; cực đại tại điểm M(-1;-1) v&agrave; <span class=\"math-tex\">\\({{\\rm{z}}_{c{\\rm{d}}}} = f\\left( { - 1; - 1} \\right) =  - 5\\)</span> </span></span></span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">X&eacute;t t&iacute;nh hội tụ ph&acirc;n k&igrave; của t&iacute;ch ph&acirc;n sau</span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span class=\"math-tex\">\\(I = \\int\\limits_0^1 {\\frac{{1 + 3{{\\ln }^2}x}}{x}d{\\rm{x}}} \\)</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span class=\"math-tex\">\\(I = \\int\\limits_0^1 {\\frac{{1 + 3{{\\ln }^2}x}}{x}d{\\rm{x}} = \\mathop {\\lim }\\limits_{a \\to {0^ + }} \\int\\limits_a^1 {\\frac{{1 + 3{{\\ln }^2}x}}{x}dx} } \\)</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span class=\"math-tex\">\\(I\\left( a \\right) = \\int\\limits_a^1 {\\frac{{1 + 3{{\\ln }^2}x}}{x}dx} \\)</span></span> <span style=\"font-size:14.0pt\">Đặt <span class=\"math-tex\">\\(t = \\ln {\\rm{x}} \\Rightarrow dt = \\frac{{d{\\rm{x}}}}{x}\\)</span> n&ecirc;n ta c&oacute; </span></span></span><span class=\"math-tex\">\\(I\\left( a \\right) = \\int\\limits_{\\ln a}^0 {\\left( {1 + 3{t^2}} \\right)} dt = t + {t^3}\\left| \\begin{array}{l} 0\\\\ \\ln a \\end{array} \\right. =  - \\ln a - {\\ln ^3}a \\)</span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"> </span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span class=\"math-tex\">\\(I = \\int\\limits_0^1 {\\frac{{1 + 3{{\\ln }^2}x}}{x}d{\\rm{x}} = \\mathop {\\lim }\\limits_{a \\to {0^ + }} \\int\\limits_a^1 {\\frac{{1 + 3{{\\ln }^2}x}}{x}dx} } \\)</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:18px\"><span class=\"math-tex\">\\(I = \\mathop {\\lim }\\limits_{a \\to 0 + } \\left( { - \\ln a - {{\\ln }^3}a} \\right) =  - \\infty\\)</span></span></span></span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">&nbsp;Vậy t&iacute;ch ph&acirc;n suy rộng đ&atilde; cho ph&acirc;n k&igrave;</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">C&acirc;u 2</span></span></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Giải phương tr&igrave;nh vi ph&acirc;n <span class=\"math-tex\">\\(y\' - 2{\\rm{x}}y = 2{{\\rm{x}}^3}{\\mathop{\\rm sinx}\\nolimits} \\)</span> </span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Đ&acirc;y l&agrave; phương tr&igrave;nh vi ph&acirc;n tuyến t&iacute;nh cấp 1&nbsp;&nbsp;</span></span></span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Suy ra nghiệm tổng qu&aacute;t <span class=\"math-tex\">\\(y = C.{e^{\\int {2{\\rm{xdx}}} }} = C.{e^{{x^2}}}\\)</span> </span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Coi C=C(x), ta c&oacute;</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:18px\"><span class=\"math-tex\">\\( \\begin{array}{l} C\'.{e^{{x^2}}} = {x^3}\\sin x \\Leftrightarrow C\' = \\frac{{{x^3}\\sin x}}{{{e^{{x^2}}}}} \\Rightarrow C = \\int {\\frac{{{x^3}\\sin x}}{{{e^{{x^2}}}}}d{\\rm{x}}} \\\\  \\Rightarrow y = {e^{{x^2}}}.\\int {\\frac{{{x^3}\\sin x}}{{{e^{{x^2}}}}}d{\\rm{x}}} \\end{array}\\)</span></span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"> </span></span></span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Giải phương tr&igrave;nh sai ph&acirc;n <span class=\"math-tex\">\\(y\\left( {n + 2} \\right) - 4y\\left( {n + 1} \\right) + 3y\\left( n \\right) = {2^n}\\left( {n + 3} \\right)\\)</span> </span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Giải phương tr&igrave;nh thuần nhất </span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Phương tr&igrave;nh đặc trưng </span></span></span><span class=\"math-tex\">\\({\\lambda ^2} - 4\\lambda  + 3 = 0 \\Leftrightarrow \\left[ \\begin{array}{l} {\\lambda _1} = 1\\\\ {\\lambda _2} = 3 \\end{array} \\right.\\)</span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"> </span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Suy ra nghiệm tổng qu&aacute;t phương tr&igrave;nh thuần nhất <span class=\"math-tex\">\\(\\overline y  = {C_1}{e^x} + {C_2}{e^{3{\\rm{x}}}}\\)</span> </span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">+ Giải phương tr&igrave;nh kh&ocirc;ng thuần nhất </span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span class=\"math-tex\">\\(f\\left( n \\right) = {2^n}\\left( {n + 3} \\right)\\)</span></span><span style=\"font-size:14.0pt\"> tức l&agrave; &alpha;=2 kh&ocirc;ng l&agrave; nghiệm của phương tr&igrave;nh đặc trưng </span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Do đ&oacute;&nbsp;<span class=\"math-tex\">\\(\\widehat y\\left( n \\right) = {2^n}\\left( {an + b} \\right)\\)</span>&nbsp;&nbsp;</span></span></span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Khi đ&oacute; </span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span class=\"math-tex\">\\(\\widehat y\\left( {n + 2} \\right) = {2^{n + 2}}\\left( {a\\left( {n + 2} \\right) + b} \\right)\\)</span></span><span style=\"font-size:14.0pt\">.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span class=\"math-tex\">\\( \\widehat y\\left( {n + 1} \\right) = {2^{n + 1}}\\left( {a\\left( {n + 1} \\right) + b} \\right)\\)</span></span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Thay v&agrave;o phương tr&igrave;nh kh&ocirc;ng thuần nhất ta c&oacute;</span></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span class=\"math-tex\">\\(\\begin{array}{l} {2^{n + 2}}\\left( {an + 2{\\rm{a + b}}} \\right) - {4.2^{n + 1}}\\left( {an + a + b} \\right) + {3.2^n}\\left( {an + b} \\right) = {2^n}\\left( {n + 3} \\right)\\\\  \\Leftrightarrow {2^n}\\left( { - an - b} \\right) = {2^n}(n + 3)\\\\  \\Leftrightarrow \\left\\{ \\begin{array}{l} a =  - 1\\\\ b =  - 3 \\end{array} \\right. \\end{array}\\)</span></span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"> </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Vậy phương tr&igrave;nh sai ph&acirc;n đ&atilde; cho c&oacute; nghiệm l&agrave;&nbsp; &nbsp; &nbsp;</span></span></span><span style=\"font-size:18px\"><span class=\"math-tex\">\\(y = {C_1}{e^x} + {C_2}{e^{3{\\rm{x}}}} - {2^n}\\left( {n + 3} \\right)\\)</span></span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"> </span></span></span></p>', '1551697631', null, '0', '51', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('275', 'Câu 2 (nhóm 2)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 1 (Nh&oacute;m 2) Vẽ quy tr&igrave;nh tiến động marketing.Ph&acirc;n t&iacute;ch c&oacute; v&iacute; dụ minh họa tư tưởng &ldquo;B&aacute;n c&aacute;i thị trường cần chứ kh&ocirc;ng b&aacute;n c&aacute;i m&igrave;nh c&oacute;&rdquo; của marketing hi&ecirc;n đại.</span></span></span></span></p>', '1551698541', null, '270', '58', '1', '270', '270', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('277', 'Chương 2: Môi trường Marketing', '<p style=\"margin-left:120px\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">CHƯƠNG 2: M&Ocirc;I TRƯỜNG MARKETING</span></span></span></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&aacute;i niệm v&agrave; sự cần thiết của nghi&ecirc;n cứu m&ocirc;i trường marketing</span></span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&aacute;i niệm m&ocirc;i trường marketing:</span></span></u></em><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> M&ocirc;i trường marketing bao&nbsp; h&agrave;m c&aacute;c nh&acirc;n tố v&agrave; lực lượng b&ecirc;n ngo&agrave;i marketing đang ảnh hưởng đến khả năng quản trị marketing trong c&ocirc;ng cuộc triển khai v&agrave; duy tr&igrave; mối quan hệ th&agrave;nh c&ocirc;ng với kh&aacute;ch h&agrave;ng mục ti&ecirc;u của doanh nghiệp</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp;Sự cần thiết của nghi&ecirc;n cứu m&ocirc;i trường marketing:</span></span></u></strong></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">M&ocirc;i trường tạo ra sự xung đột, k&igrave;m h&atilde;m sự ph&aacute;t triển của doanh nghiệp nhưng cũng tạo ra động lực th&uacute;c đẩy.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c nh&acirc;n tố của m&ocirc;i trường lu&ocirc;n biến động đ&ograve;i hỏi doanh nghiệp phải nghi&ecirc;n cứu, theo d&otilde;i v&agrave; dự đo&aacute;n.</span></span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c nh&acirc;n tố của m&ocirc;i trường marketing</span></span></strong></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img src=\"/public/images/course/58/lesson/1552134558_image-20190309193310-1.png\" style=\"height:241px; width:500px\" /></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">2.1 M&ocirc;i trường marketing vĩ m&ocirc;</span></span></em></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">-&nbsp; Kh&aacute;i niệm: Bao h&agrave;m c&aacute;c lực lượng x&atilde; hội rộng lớn đang ảnh hưởng đến to&agrave;n cục m&ocirc;i trường vi m&ocirc;, nội bộ DN v&agrave; tạo ra thời cơ cũng như mối đe dọa đối với DN&nbsp; </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">- C&aacute;c nh&acirc;n tố:</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Nh&acirc;n khẩu học</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ch&iacute;nh trị-Ph&aacute;p luật</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tự nhi&ecirc;n</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Văn h&oacute;a- x&atilde; hội</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Khoa học kĩ thuật- C&ocirc;ng nghệ</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kinh tế vĩ m&ocirc;</span></span></span></span>\r\n	<ol start=\"2\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">M&ocirc;i trường marketing vi m&ocirc;</span></span></em></strong></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&aacute;i niệm</span></span></u></em><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">: L&agrave; những lực lượng c&oacute; quan hệ trực tiếp với doanh nghiệp m&agrave; doanh nghiệp c&oacute; thể t&aacute;c động ngược trở lại mang t&iacute;nh c&aacute; biệt v&agrave; cục bộ, c&oacute; thể chia th&agrave;nh 2 nh&oacute;m:</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Nh&oacute;m m&ocirc;i trường nội bộ: c&aacute;c yếu tố trong doanh nghiệp c&oacute; ảnh hưởng khả năng vận dụng marketing hữu hiệu.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Nh&oacute;m m&ocirc;i trường ng&agrave;nh (nhiệm vụ): nh&agrave; cung cấp, trung gian marketing, kh&aacute;ch h&agrave;ng, đối thủ cạnh tranh v&agrave; c&ocirc;ng ch&uacute;ng.</span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><img src=\"/public/images/course/58/lesson/1552134558_image-20190309193310-2.png\" style=\"height:188px; width:500px\" /></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c nh&oacute;m nh&acirc;n tố:</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Nh&agrave; cung cấp</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Trung gian marketing</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đối thủ cạnh tranh</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&aacute;ch h&agrave;ng </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&ocirc;ng ch&uacute;ng</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Yếu tố nội bộ tổ chức</span></span></span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', '1551699336', null, '0', '58', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('278', 'Câu 2( Nhóm 1)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 2 (nh&oacute;m 1) N&ecirc;u kh&aacute;i niệm m&ocirc;i trường marketing?N&ecirc;u c&aacute;c yếu tố của m&ocirc;i trường marketing? Tr&igrave;nh b&agrave;y sự cần thiết của việc nghi&ecirc;n cứu m&ocirc;i trường marketing tại doanh nghiệp? N&ecirc;u kh&aacute;i niệm m&ocirc;i trường marketing vi m&ocirc;? C&aacute;c yếu tố thuộc m&ocirc;i trường marketing vi m&ocirc; ? Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ một trong c&aacute;c yếu tố thuộc m&ocirc;i trường marketing vi m&ocirc;( Trung gian marketing, nh&agrave; cung cấp,đối thủ cạnh tranh) ảnh hưởng đến hoạt động marketing của một doanh nghiệp cụ thể m&agrave; anh/chị biết.</span></span></span></span></p>', '1551699397', null, '277', '58', '1', '277', '277', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('279', 'Câu 2( Nhóm 2)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 2(Nh&oacute;m 2) N&ecirc;u kh&aacute;i niệm m&ocirc;i trường marketing vĩ m&ocirc;? N&ecirc;u c&aacute;c yếu tố thuộc m&ocirc;i trường marketing vĩ m&ocirc;? Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh họa một trong c&aacute;c yếu thuộc m&ocirc;i trường marketing vĩ m&ocirc; (nh&acirc;n khẩu học, m&ocirc;i trường kinh tế vĩ m&ocirc;, m&ocirc;i trường khoa học kĩ thuật)</span></span></span></span></p>', '1551708146', null, '277', '58', '1', '277', '277', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('280', 'Chương 3: Hệ thống thông tin và nghiên cứu marketing', '<p style=\"margin-left:120px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">CHƯƠNG 3: HỆ TH&Ocirc;NG TH&Ocirc;N TIN V&Agrave; NGHI&Ecirc;N CỨU MARKETING</span></span></span></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hệ thống th&ocirc;ng tin marketing (MIS)</span></span></span></span>\r\n\r\n	<ol>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">. Kh&aacute;i niệm v&agrave; vai tr&ograve; của MIS</span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&aacute;i niệm: MIS l&agrave; cấu tr&uacute;c bao gồn con người,thiết bị, v&agrave; c&aacute;c quy tr&igrave;nh thu thập,ph&acirc;n loại, ph&acirc;n t&iacute;ch, lượng gi&aacute; v&agrave; ph&acirc;n phối c&aacute;c th&ocirc;ng tin ch&iacute;nh x&aacute;c, kịp thời, th&iacute;ch hợp cho người quản trị quyết định marketing nhằm tăng cường hiệu quả hoạch h&oacute;a,thực hiện v&agrave; kiểm tra marketing</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Vai tr&ograve;</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Gi&uacute;p nh&agrave; quản trị marketing quản l&yacute; th&ocirc;ng tin ch&iacute;nh x&aacute;c, hiệu quả v&agrave; th&ocirc;ng suốt</span></span></span></span>\r\n\r\n	<ol start=\"2\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u tr&uacute;c MIS</span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:120px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:50px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:72px\"><img src=\"/public/images/course/58/lesson/1552134658_image-20190309193449-1.png\" style=\"height:425px; width:800px\" /></p>\r\n\r\n<p style=\"margin-left:72px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:72px\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hệ hạch to&aacute;n nội bộ: Cung cấp th&ocirc;ng tin cơ bản b&ecirc;n trong.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hệ điều tra marketing (t&igrave;nh b&aacute;o): Tập hợp dữ liệu đang diễn biến h&agrave;ng ng&agrave;y tr&ecirc;n thị trường</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hệ nghi&ecirc;n cứu marketing: Thu thập, xử l&yacute; th&ocirc;ng tin về vấn đề c&ocirc;ng ty đang đối diện</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hệ hỗ trợ c&aacute;c quyết định marketing: C&aacute;c kỹ thuật th&ocirc;ng k&ecirc; v&agrave; m&ocirc; h&igrave;nh ph&acirc;n t&iacute;ch.</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">2.2 Nghi&ecirc;n cứu marketing</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&aacute;i niệm: : Nghi&ecirc;n cứu marketing l&agrave; qu&aacute; tr&igrave;nh thu thập c&oacute; hệ thống,ghi ch&eacute;p, ph&acirc;n loại, ph&acirc;n t&iacute;ch v&agrave; th&ocirc;ng đạt c&oacute; mục ti&ecirc;u c&aacute;c t&agrave;i liệu c&oacute; li&ecirc;n quan đến th&aacute;i độ ứng xử, nhu cầu, quan điểm.. của c&aacute;c c&aacute; nh&acirc;, tổ chức ti&ecirc;u d&ugrave;ng trong mối quan hệ với c&aacute;c hoạt động h&agrave;ng ng&agrave;y.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Vai tr&ograve;:</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Cung cấp th&ocirc;ng tin cho người ra quyết định</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Giảm rủ ro trong kinh doanh</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">T&igrave;m kiếm cơ hội mới. thị trường mới</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Cung cấp th&ocirc;ng tin cho hoạch định chiến lược</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">L&agrave; tiền đề hỗ trợ quảng c&aacute;o</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c loại h&igrave;nh nghi&ecirc;n cứu marketing:</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đặc trưng v&agrave; đo lường kh&aacute;i qu&aacute;t thị trường</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&aacute;ch h&agrave;ng v&agrave; người ti&ecirc;u thụ</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&acirc;n đoạn thị trường mục ti&ecirc;u</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Marketing sản phẩm</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Marketing x&uacute;c tiến</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Marketing ph&acirc;n phối v&agrave; ph&acirc;n t&iacute;ch sức b&aacute;n</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Marketing gi&aacute; kinh doanh</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Cạnh tranh</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Nghi&ecirc;n cứu dự b&aacute;o</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Quy tr&igrave;nh phương ph&aacute;p luận nghi&ecirc;n cứu marketing</span></span></span></span></li>\r\n	<li>&nbsp;</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px; text-align:center\"><img src=\"/public/images/course/58/lesson/1552134659_image-20190309193449-2.png\" style=\"height:424px; width:800px\" /></p>', '1551708926', null, '0', '58', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('281', 'Câu 3 (Nhóm 1)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 3 (Nh&oacute;m 1):</span></span></em></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> N&ecirc;u kh&aacute;i niệm v&agrave; vai tr&ograve; của nghi&ecirc;n cứu marketing? Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ c&aacute;c nguồn dữ liệu&nbsp; trong nghi&ecirc;n cứu marketing? N&ecirc;u kh&aacute;i niệm v&agrave; cấu tr&uacute;c hệ thống th&ocirc;ng tin marketing của doanh nghiệp? Tr&igrave;nh b&agrave;y c&aacute;c loại h&igrave;nh nghi&ecirc;n cứu marketing? (Nghi&ecirc;n cứu cạnh tranh,nghi&ecirc;n cứu kh&aacute;ch h&agrave;ng v&agrave; người ti&ecirc;u thụ)</span></span></span></span></p>', '1551708957', null, '280', '58', '1', '280', '280', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('282', 'Câu 3 (Nhóm 2)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 3( nh&oacute;m 2):</span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> Vẽ sơ đồ quy tr&igrave;nh phương ph&aacute;p luận nghi&ecirc;n cứu marketing của doanh nghiệp? Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh họa một trong c&aacute;c nội dung của kế hoạch nghi&ecirc;n cứu của doanh nghiệp (Lựa chọn nguồn dữ liệu;X&aacute;c định c&ocirc;ng cụ nghi&ecirc;n cứu, Lựa chọn phương ph&aacute;p nghi&ecirc;n cứu)</span></span></span></span></p>', '1551710315', null, '280', '58', '1', '280', '280', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('283', 'Chương 1:', '<ol>\r\n	<li><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&aacute;i niệm v&agrave; sự cần thiết nghi&ecirc;n cứu h&agrave;nh vi mua kh&aacute;ch h&agrave;ng</span></span></span></span></strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&aacute;i niệm: l&agrave; c&aacute;c ứng xử/phản ứng trong qu&aacute; tr&igrave;nh h&igrave;nh th&agrave;nh nhu cầu, mua sắm sản phẩm v&agrave; dịch vụ; nhận biết, đ&aacute;nh gi&aacute;, lựa chọn mua, sử dụng trong số những nh&atilde;n hiệu thế vị đang được ch&agrave;o h&agrave;ng tr&ecirc;n thị trường nhằm thỏa m&atilde;n nhu cầu của NTD v&agrave; tổ chức.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Sự cần thiết của nghi&ecirc;n cứu: gi&uacute;p hiểu r&otilde; nguy&ecirc;n nh&acirc;n thực sự của những h&agrave;nh động, l&agrave; cơ sở gi&uacute;p bảo vệ quyền lợi v&agrave; c&oacute; những điều chỉnh hợp l&yacute;.</span></span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c hoạt động nghi&ecirc;n cứu h&agrave;nh vi mua kh&aacute;ch h&agrave;ng</span></span></span></span></strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c hoạt động nghi&ecirc;n cứu ng&agrave;y c&agrave;ng ph&aacute;t triển </span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ng&agrave;y c&agrave;ng nhiều, đa dạng hơn</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Sự hỗ trợ của c&aacute;c ngh&agrave;nh khoa học kh&aacute;c</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ng&agrave;y c&agrave;ng ph&aacute;t triển, chuy&ecirc;n s&acirc;u hơn</span></span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"3\">\r\n	<li><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c nh&acirc;n tố ảnh hưởng đến h&agrave;nh vi mua của NTD </span></span></span></span></strong></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:50px\"><img src=\"/public/images/course/59/lesson/1552120088_image-20190309153159-1.png\" style=\"height:311px; width:733px\" /></p>\r\n\r\n<ol start=\"4\">\r\n	<li><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Sự tương t&aacute;c giữa h&agrave;nh vi kh&aacute;ch h&agrave;ng với hoạt đ&ocirc;ng MKT của doanh nghiệp</span></span></span></span></strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&aacute;ch h&agrave;ng&nbsp; với hoạt động MKT: </span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">MKT thỏa m&atilde;n nhu cầu </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Sử dụng trong c&aacute;c bước kế hoạch h&oacute;a MKT</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">MKT đến kh&aacute;ch h&agrave;ng</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Định hướng ti&ecirc;u d&ugrave;ng</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">X&uacute;c tiến thương mại t&aacute;c động đến suy nghĩ v&agrave; nhận thức, đ&aacute;nh gi&aacute; của NTD</span></span></span></span></li>\r\n</ul>', '1551712289', null, '0', '59', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('284', 'Câu 1 (nhóm 1):', '<p>N&ecirc;u kh&aacute;i niệm h&agrave;nh vi kh&aacute;ch h&agrave;ng? Tại sao phải nghi&ecirc;n cứu h&agrave;nh vi kh&aacute;ch h&agrave;ng?</p>', '1551712360', null, '283', '59', '1', '283', '283', '1', 'Nêu khái niệm hành vi khách hàng? Tại sao phải nghiên cứu hành vi khách hàng?', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('286', 'Tính gần đúng giá trị', '<p style=\"margin-bottom:11px\">&nbsp;</p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><b><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"color:red\">T&iacute;nh gần đ&uacute;ng 1 gi&aacute; trị </span></span></span></span></b></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><b><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Hướng dẫn giải</span></span></span></b></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background:lime\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Bước 1</span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">: Từ biểu thức đ&atilde; cho x&aacute;c định h&agrave;m z=z (x, y)</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background:lime\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Bước2</span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">: Chọn gi&aacute; trị x<sub>0</sub>, y<sub>0</sub>, ∆x, ∆y</span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">ch&uacute; &yacute; chọn sao cho biểu thức z=z (x<sub>0</sub>, y<sub>0</sub>) t&iacute;nh được dễ d&agrave;ng, v&agrave; ∆x&rarr;0, ∆y&rarr;0</span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background:lime\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Bước 3</span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"> T&iacute;nh đạo h&agrave;m ri&ecirc;ng cấp 1 tại (x<sub>0</sub>, y<sub>0</sub>) v&agrave; </span></span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"color:red\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(z({x_0};{y_0})\\)</span></span></span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background:lime\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Bước 4</span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"> Thay v&agrave;o c&ocirc;ng thức v&agrave; t&iacute;nh ra kết quả</span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"color:red\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(z\\left( {{x_0} + \\Delta x;{y_0} + \\Delta y} \\right) \\cong z\\left( {{x_0};{y_0}} \\right) + z{\'_x}\\left( {{x_0};{y_0}} \\right).\\Delta x + z{\'_y}\\left( {{x_0};{y_0}} \\right).\\Delta y\\)</span></span></span></span></span></span><i> </i></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><i><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">VD7.3 </span></span></span></i><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">T&iacute;nh gi&aacute; trị gần đ&uacute;ng của biểu thức sau</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px; margin-left:36px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"color:red\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(A = \\sqrt {{{\\left( {1,02} \\right)}^3} + {{\\left( {1,97} \\right)}^3}} \\)</span></span></span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px; margin-left:36px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background:lime\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Bước 1</span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">: Từ biểu thức đ&atilde; cho x&aacute;c định h&agrave;m </span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px; margin-left:36px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">z=f (x, y)=</span></span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"color:red\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(\\sqrt {{x^3} + {y^3}} \\)</span></span></span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"> </span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"background:lime\">&nbsp;Bước2</span>: Chọn gi&aacute; trị x<sub>0</sub>, y<sub>0</sub>, ∆x, ∆y</span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">ở đ&acirc;y ta chọn </span></span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"color:red\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\({x_0} = 1;{y_0} = 2;\\Delta x = 0,02;\\Delta y =  - 0,03\\)</span></span></span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"background:lime\">Bước 3</span> T&iacute;nh đạo h&agrave;m ri&ecirc;ng cấp 1 tại (x<sub>0</sub>, y<sub>0</sub>)</span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(f(1;2) = 3\\)</span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(f{&#39;_x}\\left( {x;y} \\right) = \\frac{{3.{x^2}}}{{2\\sqrt {{x^3} + {y^3}} }} \\Rightarrow {f_x}&#39;\\left( {1;2} \\right) = \\frac{1}{2}\\)</span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(\\)</span></span></span></span> <span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(f{&#39;_y}\\left( {x;y} \\right) = \\frac{{3.{y^2}}}{{2\\sqrt {{x^3} + {y^3}} }} \\Rightarrow {f_y}&#39;\\left( {1;2} \\right) = \\frac{1}{2}\\)</span></span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background:lime\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Bước 4</span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"> Thay v&agrave;o c&ocirc;ng thức v&agrave; t&iacute;nh ra kết quả</span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"color:red\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(A \\approx f\\left( {1;2} \\right) + f{\'_x}\\left( {1;2} \\right).0,02 + f{\'_y}\\left( {1;2} \\right).( - 0,03) = 2,95\\)</span></span></span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\">&nbsp;</p>\r\n\r\n<p style=\"margin-bottom:11px\">&nbsp;</p>', '1551712736', null, '166', '51', '1', '166', '166', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('288', 'CHƯƠNG 1: DOANH NGHIỆP VÀ HOẠT ĐỘNG CỦA DOANH NGHIỆP TRONG NỀN KINH TẾ THỊ TRƯỜNG', '<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665832\"><span style=\"color:#000000\"><img alt=\"\" src=\"/public/images/course/45/lesson/1553089912_chuong-11-min.jpg\" style=\"height:886px; width:1000px\" />CHƯƠNG 1: DOANH NGHIỆP V&Agrave; HOẠT ĐỘNG CỦA DOANH NGHIỆP TRONG NỀN KINH TẾ THỊ TRƯỜNG</span></a><span style=\"color:#000000\"> </span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<p>&nbsp;</p>\r\n	</li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665833\"><span style=\"color:#000000\"><strong>1.1 Doanh nghiệp: chức năng, nhiệm vụ v&agrave; mục ti&ecirc;u</strong></span></a></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665834\"><span style=\"color:#000000\"><strong><em>1.1.1 Kh&aacute;i niệm doanh nghiệp</em></strong></span></a></span></span></p>\r\n\r\n<p style=\"margin-left:19px\"><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">C&oacute; nhiều c&aacute;ch tiếp cận: tiếp cận b&ecirc;n ngo&agrave;i, tiếp cận theo luật DN, tiếp cận li&ecirc;n minh kinh tế... tuy nhi&ecirc;n c&oacute; thể định nghĩa kh&aacute;i qu&aacute;t như sau:</span></span></span></p>\r\n\r\n<p style=\"margin-left:19px\"><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>Doanh nghiệp l&agrave; <strong>một cộng đồng người</strong> li&ecirc;n kết với nhau để <strong>sản xuất ra </strong>của cải hoặc dịch vụ v&agrave; <strong>c&ugrave;ng nhau thừa hưởng th&agrave;nh quả</strong> do việc sản xuất đem lại.</em></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em><span style=\"color:#000000\">&nbsp;1.1.2</span><a name=\"_Toc1665835\"><span style=\"color:#000000\">Chức năng, nhiệm vụ của DN</span></a></em></strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>1.1.2.1 Chức năng</em></span></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>Chức năng sản xuất</em>: sản xuất, cung cấp dịch vụ, h&agrave;ng h&oacute;a, sản phẩm cho thị trường</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>Chức năng thương mại :</em> DN thực hiện gi&aacute; trị h&agrave;ng h&oacute;a, dịch vụ bằng c&aacute;ch mua b&aacute;n</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>Chức năng t&agrave;i ch&iacute;nh</em>: đảm bảo cho DN c&oacute; c&aacute;c nguồn t&agrave;i ch&iacute;nh cũng như ph&acirc;n bố c&aacute;c nguồn t&agrave;i ch&iacute;nh một c&aacute;ch hiệu quả.</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>Chức năng quản trị</em> : Chức năng n&agrave;y đảm bảo cho DN được phối hợp, ăn khớp v&agrave; kh&ocirc;ng đi lệch mục ti&ecirc;u dự định.</span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>1.1.2.2 Nhiệm vụ: nhằm thực hiện chức năng</em></span></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Nhiệm vụ xuất ph&aacute;t từ chức năng v&agrave; nhằm thực hiện chức năng</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Nhiệm vụ cụ thể h&oacute;a c&ocirc;ng việc phải ho&agrave;n th&agrave;nh, nhiệm vụ c&oacute; thể bao h&agrave;m nội dung chức năng</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Nhiệm vụ kh&aacute;c nhau trong từng thời kỳ, từng chiến lược kinh doanh cụ thể.</span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Nhiệm vụ thay đổi t&ugrave;y thuộc v&agrave;o thay đổi những điều kiện thực hiện chức năng</span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:#000000\"><em>​​​​1.1.3&nbsp;</em></span><a name=\"_Toc1665836\"><span style=\"color:#000000\">Mục ti&ecirc;u của doanh nghiệp</span></a></span></span></strong></p>\r\n\r\n<p style=\"margin-left:19px\"><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Mục ti&ecirc;u của doanh nghiệp phụ thuộc v&agrave;o mục đ&iacute;ch, chức năng v&agrave; nhiệm vụ của n&oacute;. Doanh nghiệp thường theo đuổi một số mục đ&iacute;ch chủ yếu như: Đảm bảo hoạt động sản xuất ổn định, bền vững, tối đa h&oacute;a lợi nhuận, đa dạng h&oacute;a c&aacute;c lĩnh vực.... ngo&agrave;i ra c&ograve;n c&aacute;c mục đ&iacute;ch của từng loại h&igrave;nh doanh nghiệp kh&aacute;c nhau...</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<p>&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665837\"><span style=\"color:#000000\"><strong>1.2 C&aacute;c h&igrave;nh thức tổ chức doanh nghiệp</strong></span></a></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665838\"><span style=\"color:#000000\"><strong>1.2.1 Doanh nghiệp tư nh&acirc;n</strong></span></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Kh&aacute;i niệm: l&agrave; DN do một c&aacute; nh&acirc;n l&agrave;m chủ v&agrave; tự chịu tr&aacute;ch nhiệm bằng to&agrave;n bộ t&agrave;i sản của m&igrave;nh về mọi hoạt động của DN.</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">To&agrave;n bộ t&agrave;i sản thuộc nh&agrave; đầu tư, người quản l&yacute; DN c&oacute; thể đồng thời l&agrave; người sở hữu vốn, chủ DN c&oacute; to&agrave;n quyền quyết định.</span></span></span></p>\r\n\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<p>&nbsp;</p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665839\"><span style=\"color:#000000\"><strong>1.2.2 C&ocirc;ng ty tr&aacute;ch nhiệm hữu hạn</strong></span></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Kh&aacute;i niệm: L&agrave; DN, trong đ&oacute; c&oacute; c&aacute;c th&agrave;nh vi&ecirc;n c&ugrave;ng g&oacute;p vốn, c&ugrave;ng chia lợi nhuận, c&ugrave;ng chịu lỗ tương ứng với phần vốn g&oacute;p v&agrave; chỉ chịu tr&aacute;ch nhiệm về c&aacute;c khoản nợ của c&ocirc;ng ty trong phạm vi phần vốn của m&igrave;nh g&oacute;p v&agrave;o c&ocirc;ng ty.</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Đặc điểm: C&ocirc;ng ty TNHH kh&ocirc;ng được ph&aacute;t h&agrave;nh cổ phiếu để bổ sung vốn. Ch&iacute;nh v&igrave; vậy, h&igrave;nh thức tổ chức quản l&iacute; n&agrave;y chỉ th&iacute;ch hợp với những dự &aacute;n đầu tư quy m&ocirc; nhỏ hoặc vừa.</span></span></span></p>\r\n\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<p>&nbsp;</p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665840\"><span style=\"color:#000000\"><strong>1.2.3 C&ocirc;ng ty cổ phần</strong></span></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Kh&aacute;i niệm: l&agrave; DN trong đ&oacute; c&aacute;c th&agrave;nh vi&ecirc;n c&ugrave;ng g&oacute;p vốn, c&ugrave;ng chia nhau lợi nhuận, c&ugrave;ng chịu lỗ tương ứng với phần vốn g&oacute;p v&agrave; chỉ chịu tr&aacute;ch nhiệm về c&aacute;c khoản nợ của c&ocirc;ng ty trong phạm vi phần vốn g&oacute;p của m&igrave;nh v&agrave;o c&ocirc;ng ty.</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Trong qu&aacute; tr&igrave;nh hoạt động, c&ocirc;ng ty c&oacute; thể ph&aacute;t h&agrave;nh chứng kho&aacute;n theo quy định. H&igrave;nh thức n&agrave;y đem lại nhiều ưu thế cho vốn đầu tư n&ecirc;n hợp với c&aacute;c doanh nghiệp c&oacute; quy m&ocirc; lớn v&agrave; thường c&oacute; nhu cầu huy động bổ sung vốn nhờ mở rộng quy m&ocirc;.</span></span></span></p>\r\n\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<p>&nbsp;</p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665841\"><span style=\"color:#000000\"><strong>1.2.4 Doanh nghiệp nh&agrave; nước</strong></span></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Kh&aacute;i niệm: l&agrave; tổ chức kinh tế do Nh&agrave; nước đầu tư vốn, th&agrave;nh lập v&agrave; tổ chức quản l&yacute;, hoạt động kinh doanh hoặc hoạt động c&ocirc;ng &iacute;ch nhằm thực hiện c&aacute;c mục ti&ecirc;u kinh tế- x&atilde; hội do Nh&agrave; nước giao.</span></span></span></p>\r\n\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<p>&nbsp;</p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665842\"><span style=\"color:#000000\"><strong>1.2.5 Doanh nghiệp hợp t&aacute;c x&atilde;</strong></span></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">HTX l&agrave; loại h&igrave;nh kinh tế tập thể, do những người lao động v&agrave; c&aacute;c tổ chức c&oacute; nhu cầu, lợi &iacute;ch chung tự nguyện đ&oacute;ng g&oacute;p vốn, g&oacute;p sức lập ra theo quy định của ph&aacute;p luật để ph&aacute;t huy sức mạnh của tập thể v&agrave; của từng x&atilde; vi&ecirc;n nhằm gi&uacute;p nhau thực hiện hiệu quả hơn c&aacute;c hoạt động sản xuất, kinh doanh, dịch vụ v&agrave; cải thiện đời sống, g&oacute;p phần ph&aacute;t triển kinh tế x&atilde; hội.</span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665843\"><span style=\"color:#000000\"><strong>1.3 Doanh nghiệp l&agrave; một đơn vị sản xuất, một đơn vị ph&acirc;n phối</strong></span></a></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665844\"><span style=\"color:#000000\"><strong>1.3.1 Doanh nghiệp l&agrave; một đơn vị sản xuất tạo ra sản phẩm, tạo ra gi&aacute; trị gia tăng</strong></span></a><span style=\"color:#000000\"><strong> </strong></span></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Doanh nghiệp trong qu&aacute; tr&igrave;nh sản xuất kinh doanh đều định ra gi&aacute; b&aacute;n hợp l&yacute; nhằm thu được gi&aacute; trị gia tăng đảm bảo trang trải được chi ph&iacute; v&agrave; thu được lợi nhuận.</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">&nbsp;</span></span></span><span style=\"font-size:14px\">Gi&aacute; trị gia tăng được DN tạo ra l&agrave; sản phẩm của c&aacute;c yếu tố đầu v&agrave;o v&agrave; c&aacute;c yếu tố ti&ecirc;u d&ugrave;ng trung gian</span><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"> </span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665845\"><span style=\"color:#000000\"><strong>1.3.2 Doanh nghiệp l&agrave; một đơn vị ph&acirc;n phối kết quả lao động</strong></span></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Doanh nghiệp sử dụng doanh thu b&aacute;n h&agrave;ng để :</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Chi trả cho người cung ứng c&aacute;c yếu tố ti&ecirc;u d&ugrave;ng trung gian</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Chi trả lương cho người lao động l&atilde;i vốn vay</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Trả cho người chủ do c&ugrave;ng g&oacute;p vốn</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Phần nộp thuế v&agrave; đ&oacute;ng g&oacute;p cho x&atilde; hội; lập quỹ dự trữ v&agrave; quỹ ph&aacute;t triển sản xuất- kinh doanh.</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Lợi nhuận: tr&iacute;ch lập quỹ dự trữ, chia cho c&aacute;c th&agrave;nh phần cổ đ&ocirc;ng</span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><span style=\"color:#000000\">&nbsp; 1</span></strong></span></span><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><span style=\"color:#000000\">.4&nbsp;</span><a name=\"_Toc1665846\"><span style=\"color:#000000\">Cơ chế hoạt động của doanh nghiệp trong nền kinh tế thị trường</span></a></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665847\"><span style=\"color:#000000\"><strong>1.4.1 Kh&aacute;i niệm v&agrave; c&aacute;c yếu tố cấu th&agrave;nh của cơ chế hoạt động của doanh nghiệp</strong></span></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Cơ chế thể hiện phương thức vận h&agrave;nh của hệ thống dưới sự t&aacute;c động của c&aacute;c quy luật kinh tế v&agrave; sự quản l&iacute; của Nh&agrave; nước.</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Kh&aacute;i niệm cơ chế hoạt động của Doanh nghiệp:</span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:67px\"><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">L&agrave; phương thức vận động của DN dưới sự t&aacute;c động của c&aacute;c quy luật kinh tế thị trường v&agrave; sự quản l&yacute; của Nh&agrave; nước.</span></span></span></p>\r\n\r\n<p>1.4.2&nbsp;<span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><a name=\"_Toc1665848\"><span style=\"color:#000000\">Doanh nghiệp l&agrave; một hệ thống mở c&oacute; mục ti&ecirc;u, c&oacute; quản l&yacute;</span></a></strong></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">DN l&agrave; một hệ thống c&oacute;c c&aacute;c phần tử c&oacute; thể theo đuổi những mục ti&ecirc;u kh&aacute;c nhau: Chủ DN muốn tối đa h&oacute;a lợi nhuận, người lao động muốn tiền lương cao,....</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">&nbsp;C&aacute;c phần tử hợp lực với nhau để thực hiện mục ti&ecirc;u chung của DN v&agrave; th&ocirc;ng qua đ&oacute; để đạt dc mục ti&ecirc;u ri&ecirc;ng của m&igrave;nh. Để thực hiện, đ&ograve;i hỏi phải c&oacute; sự phối hợp, kết hợp c&aacute;c hoạt động, do đ&oacute; phải c&oacute; quản l&yacute;.</span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665849\"><span style=\"color:#000000\"><strong>1.4.3 C&aacute;c quy luật kinh tế chủ yếu chi phối hoạt động của doanh nghiệp trong nền kinh tế thị trường</strong></span></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>Quy luật gi&aacute; trị: </em>c&oacute; t&aacute;c dụng điều tiết sản xuất lưu th&ocirc;ng h&agrave;ng h&oacute;a, k&iacute;ch th&iacute;ch lực lượng sản xuất ph&aacute;t triển, thực hiện sự b&igrave;nh tuyển tự nhi&ecirc;n. Quy luật gi&aacute; trị t&aacute;c động đến hoạt động của DN theo kiểu &ldquo; b&agrave;n tay v&ocirc; h&igrave;nh&rdquo;</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>Quy luật cung cầu</em>: Cung cầu kh&ocirc;ng tồn tại độc lập m&agrave; c&oacute; quan hệ biện chứng với nhau: cầu l&agrave;m xuất hiện cung, cung th&uacute;c đẩy cầu. C&oacute; thể nhận biết cung qua cầu v&agrave; ngược lại. Quan hệ cung cầu l&agrave; quan hệ c&oacute; bản chất lặp đi lặp lại của kinh tế thị trường, n&oacute; trở th&agrave;nh quy luật kinh tế cơ bản của nền kinh tế thị trường.</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>Quy luật cạnh tranh</em>: tự do kinh doanh l&agrave; nguồn gốc dẫn tới cạnh tranh<strong><em>.</em></strong> Cạnh tranh l&agrave; động lực th&uacute;c đẩy sản xuất, lưu th&ocirc;ng h&agrave;ng h&oacute;a ph&aacute;t triển. Cạnh tranh tr&ecirc;n thị trường rất phức tạp v&agrave; đa dạng, giữa những chủ thể c&oacute; lợi &iacute;ch v&agrave; chủ thể c&oacute; lợi &iacute;ch đối lập nhau.</span></span></span></p>\r\n\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<p>&nbsp;</p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665850\"><span style=\"color:#000000\"><strong>1.4.4 Sự điều tiết của Nh&agrave; nước đối với hoạt động của doanh nghiệp trong nền kinh tế thị trường</strong></span></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Hệ thống ph&aacute;p luật</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">Hệ thống c&aacute;c ch&iacute;nh s&aacute;ch vĩ m&ocirc;</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:14px\"><span style=\"font-family:Times New Roman,Times,serif\">C&ocirc;ng cụ đ&ograve;n bẩy: thuế, t&iacute;n dụng, tỷ gi&aacute;...</span></span></span></p>\r\n	</li>\r\n</ul>', '1551713248', null, '0', '45', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('289', 'CHƯƠNG 2 : HOẠT ĐỘNG TIÊU THỤ CỦA DOANH NGHIỆP', '<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><img alt=\"\" src=\"/public/images/course/45/lesson/1553090066_chuong-2-min.jpg\" style=\"height:778px; width:960px\" />CHƯƠNG 2 : HOẠT ĐỘNG TI&Ecirc;U THỤ CỦA DOANH NGHIỆP</span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<p>&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><a name=\"_Toc1665852\"><strong>2.1 Kh&aacute;i niệm, mục ti&ecirc;u v&agrave; vai tr&ograve; của ti&ecirc;u thụ</strong></a></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><a name=\"_Toc1665853\"><strong>2.1.1 Kh&aacute;i niệm</strong></a></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">- Ti&ecirc;u thụ h&agrave;ng h&oacute;a l&agrave; qu&aacute; tr&igrave;nh gồm c&aacute;c hoạt động</span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">- Ti&ecirc;u thụ h&agrave;ng h&oacute;a được hiểu l&agrave; hoạt động b&aacute;n h&agrave;ng</span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">- Hoạt động b&aacute;n h&agrave;ng l&agrave; một qu&aacute; tr&igrave;nh thực hiện chuyển quyền sở hữu h&agrave;ng h&oacute;a cho kh&aacute;ch h&agrave;ng v&agrave; thu tiền về hay được quyền thu tiền về do b&aacute;n h&agrave;ng.</span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">- Kết quả ti&ecirc;u thụ h&agrave;ng h&oacute;a:</span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Sản lượng</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Doanh thu b&aacute;n h&agrave;ng</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Lợi nhuận từ hoạt động b&aacute;n h&agrave;ng</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Thị phần</span></span></p>\r\n\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<p>&nbsp;</p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><a name=\"_Toc1665854\"><strong>2.1.2 Mục ti&ecirc;u</strong></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Mục ti&ecirc;u doanh thu</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Mục ti&ecirc;u lợi nhuận</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Mục ti&ecirc;u chiếm lĩnh thị trường v&agrave; tạo vị thế, uy t&iacute;n của doanh nghiệp tr&ecirc;n thị trường.</span></span></p>\r\n\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<p>&nbsp;</p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><a name=\"_Toc1665855\"><strong>2.1.3 Vai tr&ograve; của ti&ecirc;u thụ</strong></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Ti&ecirc;u thụ h&agrave;ng h&oacute;a thể hiện tr&igrave;nh độ, khả năng của DN</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Qua hoạt động b&aacute;n h&agrave;ng, DN chiếm lĩnh thị phần, thu lợi nhuận</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Mở rộng ti&ecirc;u thụ h&agrave;ng h&oacute;a l&agrave; con đường cơ bản n&acirc;ng cao hiệu quả kinh doanh.</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:43px\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<p>&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><a name=\"_Toc1665856\"><strong>2.2 C&aacute;c h&igrave;nh thức ti&ecirc;u thụ</strong></a></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><a name=\"_Toc1665857\"><strong>2.2.1 B&aacute;n lẻ</strong></a></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">- Kh&aacute;i niệm: l&agrave; b&aacute;n h&agrave;ng trực tiếp cho người ti&ecirc;u d&ugrave;ng để thỏa m&atilde;n nhu cầu c&aacute; nh&acirc;n v&agrave; tập thể. </span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">- Đặc điểm:</span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Ưu điểm: kh&ocirc;ng sợ khủng hoảng thừa, DN c&oacute; điều kiện tiếp x&uacute;c trực tiếp với người ti&ecirc;u d&ugrave;ng, nắm bắt thị trường cung cầu</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Nhược điểm: thời gian thu hồi vốn chậm.</span></span></p>\r\n\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<p>&nbsp;</p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><strong>&nbsp;2.2.2&nbsp;<a name=\"_Toc1665858\">B&aacute;n bu&ocirc;n</a></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><u>-Kh&aacute;i niệm</u>: B&aacute;n bu&ocirc;n l&agrave; qu&aacute; tr&igrave;nh b&aacute;n h&agrave;ng cho c&aacute;c đơn vị sản xuất c&aacute;c đơn vị kết quả ti&ecirc;u thụ h&agrave;ng ho&aacute; thương mại để tiếp tục đưa v&agrave;o qu&aacute; tr&igrave;nh sản xuất, gia c&ocirc;ng chế biến tạo ra sản phẩm mới hoặc tiếp tục được chuyển b&aacute;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">-&nbsp;<u>Đặc điểm: </u></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><strong><em>Ưu điểm</em></strong>: khối lượng b&aacute;n lớn, thu hồi vốn nhanh, c&oacute; điều kiện nhanh ch&oacute;ng đổi mới lĩnh vực hoạt động n&acirc;ng cao hiệu quả. </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><strong><em>Nhược điểm</em></strong>: chậm nắm bắt những diễn biến nhu cầu thị trường dẫn đến tồn đọng hoặc ti&ecirc;u thụ chậm. </span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><strong>- C&aacute;c h&igrave;nh thức b&aacute;n h&agrave;ng phổ biến:</strong></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">B&aacute;n h&agrave;ng tại chợ truyền thống, cửa h&agrave;ng b&aacute;ch h&oacute;a, cửa h&agrave;ng chuy&ecirc;n doanh.</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Đại l&yacute; thương mại</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Kinh doanh online</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">B&aacute;n h&agrave;ng đa cấp</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">B&aacute;n h&agrave;ng theo chuỗi</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Nhượng quyền thương mại</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Một số h&igrave;nh thức b&aacute;n h&agrave;ng kh&aacute;c ( hội chợi, triển l&atilde;m, b&aacute;n h&agrave;ng tại nh&agrave;,....)</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:67px\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<p>&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><a name=\"_Toc1665859\"><strong>2.3 Qu&aacute; tr&igrave;nh ti&ecirc;u thụ</strong></a></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><strong>&nbsp;2.3.1&nbsp;<a name=\"_Toc1665860\">Giai đoạn chuẩn bị</a></strong></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">L&agrave; giai đoạn mở đầu nhưng rất quan trọng. </span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Giai đoạn n&agrave;y, người b&aacute;n cần phải hiểu biết mặt h&agrave;ng, hiểu biết thị trường, phải lập luận chứng thể hiện những yếu tố tạo thuận lợi v&agrave; kh&oacute; khăn cho hoạt động b&aacute;n h&agrave;ng.</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Chuẩn bị những c&acirc;u hỏi v&agrave; trả lời cho kh&aacute;ch h&agrave;ng.</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<p>&nbsp;</p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><a name=\"_Toc1665861\"><strong>2.3.2 Giai đoạn triển khai</strong></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">B&aacute;n h&agrave;ng gồm 5 pha: tiếp x&uacute;c, luận chứng, chứng minh, trả lời b&aacute;c bỏ của kh&aacute;ch h&agrave;ng v&agrave; kết th&uacute;c.</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Tiếp x&uacute;c, tạo ra những ấn tượng tốt đẹp, tự m&igrave;nh t&igrave;m hiểu nhu cầu của người mua v&agrave; t&igrave;m c&aacute;ch thuyết phục để kh&aacute;ch mua h&agrave;ng.</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Th&aacute;i độ của người b&aacute;n đối với việc kh&aacute;ch h&agrave;ng từ chối mua phải kh&eacute;o l&eacute;o, căn cứ v&agrave;o kinh nghiệm v&agrave; kiến thức để ph&aacute; tan ho&agrave;i nghi của kh&aacute;ch h&agrave;ng. C&aacute;ch kết th&uacute;c b&aacute;n h&agrave;ng tốt nhất l&agrave; kiểu &ldquo; thắng &ndash; thắng&rdquo; tr&ecirc;n nguy&ecirc;n tắc phải l&agrave;m vui l&ograve;ng kh&aacute;ch đến, vừa l&ograve;ng kh&aacute;ch đi, tạo ấn tượng tốt cho lần sau.</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">B&aacute;n h&agrave;ng kết th&uacute;c bằng h&agrave;nh vi &lsquo; tiền trao, ch&aacute;o m&uacute;c&rdquo;&nbsp; với nhiều h&igrave;nh thức b&aacute;n kh&aacute;c nhau. Tuy nhi&ecirc;n, việc b&aacute;n h&agrave;ng đến đ&acirc;y chưa kết th&uacute;c. Vấn đề quan trọng l&agrave; phải đảm bảo c&aacute;c dịch vụ sau b&aacute;n v&igrave; ảnh hưởng đến uy t&iacute;n v&agrave; th&uacute;c đẩy qu&aacute; tr&igrave;nh b&aacute;n trong tương lai.</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Quản l&yacute; việc b&aacute;n h&agrave;ng: Việc b&aacute;n h&agrave;ng đi đến xử l&yacute; một đơn h&agrave;ng của c&aacute;c bộ phận trong doanh nghiệp. Giai đoạn cuối l&agrave; giao h&agrave;ng cho kh&aacute;ch người b&aacute;n phải gi&aacute;m s&aacute;t v&agrave; giao h&agrave;ng ho&agrave;n tất. </span></span></p>\r\n\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<p>&nbsp;</p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><a name=\"_Toc1665862\"><strong>2.3.3 C&aacute;c dịnh vụ sau b&aacute;n</strong></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Người b&aacute;n cần đảm bảo cho người mua đầy đủ quyền lợi của họ. Dịch vụ sau b&aacute;n cực kỳ quan trọng tạo chữ t&iacute;n bền vững cho DN.</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Đối với từng loại mặt h&agrave;ng c&oacute; nhiều loại DV sau b&aacute;n kh&aacute;c nhau: lắp đặt, chạy thử, bảo h&agrave;nh....</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:43px\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<p>&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><a name=\"_Toc1665863\"><strong>2.4 Lựa chọn mặt h&agrave;ng kinh doanh</strong></a><strong> </strong></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">- Kh&aacute;i niệm: <em>Mặt h&agrave;ng kinh doanh </em>l&agrave; lời giải đ&aacute;p cho DN về một nhu cầu&nbsp; đ&atilde; được lượng h&oacute;a th&ocirc;ng qua nghiện cứu nhu cầu của người ti&ecirc;u d&ugrave;ng v&agrave; thị trường.-</span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">-&nbsp;Đặc trưng:</span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Đặc trưng vật chất ( chế tạo bằng vật liệu g&igrave;, c&ocirc;ng nghệ g&igrave;....)</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Đặc trưng chức năng ( t&iacute;nh năng, t&aacute;c dụng...)</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Đặc trưng t&acirc;m l&yacute; ( tốt, xấu, đẹp, rẻ....)</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">-&nbsp;Ph&acirc;n loại mặt h&agrave;ng kinh doanh: </span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">&nbsp;Mặt h&agrave;ng ti&ecirc;u d&ugrave;ng h&agrave;ng ng&agrave;y: Gạo, mắm, dầu ăn,...</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Mặt h&agrave;ng đắt tiền: &ocirc; t&ocirc; , nh&agrave;, xe m&aacute;y,...</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Mặt h&agrave;ng đặc biệt: mặt h&agrave;ng thương hiệu như : Nước hoa, đồng hồ, t&uacute;i, sneaker...</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">-&nbsp; Cơ cấu MHKD: </span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">&nbsp;Mặt h&agrave;ng trục</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Mặt h&agrave;ng vệ tinh</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:67px\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<p>&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><a name=\"_Toc1665864\"><strong>2.5 Định gi&aacute; b&aacute;n</strong></a></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">- Mục ti&ecirc;u định gi&aacute;: LN, thị phần, cạnh tranh</span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">C&aacute;c yếu tố ảnh hưởng đến định gi&aacute;: ( Mục ti&ecirc;u của DN, t&igrave;nh h&igrave;nh cung- cầu thị trường đối với mặt h&agrave;ng, chi ph&iacute; sản xuất v&agrave; lưu th&ocirc;ng, M&ocirc;i trường cạnh tranh v&agrave; sự điều tiết của nh&agrave; nước, Đặc điểm của sản phẩm v&agrave; c&aacute;c yếu tố t&acirc;m l&yacute;...)</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">- C&aacute;c phương ph&aacute;p định gi&aacute;: </span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Định gi&aacute; tr&ecirc;n cơ sở chi ph&iacute;</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Định gi&aacute; tr&ecirc;n cơ sở gi&aacute; thị trường</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Định gi&aacute; ph&acirc;n biệt</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<p>&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><a name=\"_Toc1665865\"><strong>2.6 Dự b&aacute;o b&aacute;n h&agrave;ng</strong></a></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">-&nbsp; Kh&aacute;i niệm: Dự b&aacute;o b&aacute;n h&agrave;ng l&agrave; <strong><em>qu&aacute; tr&igrave;nh x&aacute;c định</em></strong> c&aacute;c <strong><em>chỉ ti&ecirc;u b&aacute;n h&agrave;ng</em></strong> trong tương lai v&agrave; <strong><em>triển vọng đạt được</em></strong> c&aacute;c chỉ ti&ecirc;u đ&oacute;</span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">- C&aacute;c phương ph&aacute;p dự b&aacute;o b&aacute;n h&agrave;ng: </span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Phương ph&aacute;p tốc độ b&igrave;nh qu&acirc;n</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Phương ph&aacute;p san bằng mũ ( tự th&iacute;ch nghi)</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">M&ocirc; h&igrave;nh nhiều nh&acirc;n tố</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Phương ph&aacute;p chuy&ecirc;n gia</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Phương ph&aacute;p điều tra khảo s&aacute;t</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:67px\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<p>&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><a name=\"_Toc1665866\"><strong>2.7 C&aacute;c yếu tố ảnh hưởng đến ti&ecirc;u thụ</strong></a></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">- C&aacute;c yếu tố b&ecirc;n trong: ( chủ quan)</span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Gi&aacute; b&aacute;n h&agrave;ng h&oacute;a</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Chất lượng h&agrave;ng h&oacute;a v&agrave; bao g&oacute;i</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Mặt h&agrave;ng v&agrave; ch&iacute;nh s&aacute;ch mặt h&agrave;ng kinh doanh</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Dịch vụ rong v&agrave; sau b&aacute;n</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Mạng lưới ph&acirc;n phối</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Vị tr&iacute; điểm b&aacute;n</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Quảng c&aacute;o</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Hoạt động của những người b&aacute;n h&agrave;ng v&agrave; đại l&yacute;</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">-&nbsp;C&aacute;c yếu tố b&ecirc;n ngo&agrave;i ( kh&aacute;ch quan) </span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Thị trường</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Cung cầu </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Kh&aacute;ch h&agrave;ng</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Nh&agrave; cung ứng</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Đối thủ cạnh tranh</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">C&aacute;c yếu tố vĩ m&ocirc; kh&aacute;c</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<p>&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><a name=\"_Toc1665867\"><strong>2.8 Một số quyết định kinh tế trong ti&ecirc;u thụ</strong></a></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><a name=\"_Toc1665868\"><strong>2.8.1 Mối tương quan giữa Tổng chi ph&iacute;, Doanh thu, Lợi nhuận</strong></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Chi ph&iacute; ( TF): TF= Fcđ + Fbđ</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">&nbsp;Kh&aacute;i niệm: l&agrave; biểu hiện bằng tiền của việc sử dụng c&aacute;c yếu tố của qu&aacute; tr&igrave;nh kinh doanh m&agrave; qua đ&oacute; DN tạo ra được SP v&agrave; thực hiện SP tr&ecirc;n thị trường </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Ph&acirc;n loại chi ph&iacute;:</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><em>Chi ph&iacute; cố định</em>: ( chi ph&iacute; bất biến, định ph&iacute;- Fcđ) l&agrave; những chi ph&iacute; kh&ocirc;ng phụ thuộc trực tiếp v&agrave;o số lượng v&agrave; cơ cấu h&agrave;ng h&oacute;a ti&ecirc;u thụ. Trong thực tế đ&oacute; l&agrave; những chi ph&iacute; khấu hao, tiền thu&ecirc; nh&agrave; v&agrave; t&agrave;i sản, lương h&agrave;nh ch&iacute;nh, bảo hiểm...</span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><em>&nbsp;Chi ph&iacute; biến đổ</em>i ( chi ph&iacute; khả biến- Fbđ): l&agrave; những chi ph&iacute; phụ thuộc trực tiếp v&agrave;o số lượng v&agrave; cơ cấu h&agrave;ng h&oacute;a ti&ecirc;u thụ. Trong thực tế, chi ph&iacute; n&agrave;y gồm: chi ph&iacute; mua h&agrave;ng, chi ph&iacute; nguy&ecirc;n vật liệu phụ, tiền lương kho&aacute;n, chi ph&iacute; vận chuyển...</span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">+ Chi ph&iacute; cố định ( F<sub>cđ</sub>) &nbsp;<em><span class=\"math-tex\"><iframe allowtransparency=\"true\" frameborder=\"0\" scrolling=\"no\" src=\"javascript:void(0)\" style=\"border: 0px; width: 93px; height: 29px; display: inline; vertical-align: middle;\"></iframe></span><img src=\"data:image/gif;base64,R0lGODlhAQABAPABAP///wAAACH5BAEKAAAALAAAAAABAAEAAAICRAEAOw==\" /><span style=\"background-color:rgba(220,220,220,0.5)\"><img src=\"data:image/gif;base64,R0lGODlhAQABAPABAP///wAAACH5BAEKAAAALAAAAAABAAEAAAICRAEAOw==\" style=\"height:15px; width:15px\" title=\"Nhấp chuột và kéo để di chuyển\" /></span></em></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">+ Chi ph&iacute; biến đổi b&igrave;nh qu&acirc;n&nbsp;&nbsp;<span class=\"math-tex\"><iframe allowtransparency=\"true\" frameborder=\"0\" scrolling=\"no\" src=\"javascript:void(0)\" style=\"border: 0px; width: 135px; height: 32px; display: inline; vertical-align: middle;\"></iframe></span><img src=\"data:image/gif;base64,R0lGODlhAQABAPABAP///wAAACH5BAEKAAAALAAAAAABAAEAAAICRAEAOw==\" /><span style=\"background-color:rgba(220,220,220,0.5)\"><img src=\"data:image/gif;base64,R0lGODlhAQABAPABAP///wAAACH5BAEKAAAALAAAAAABAAEAAAICRAEAOw==\" style=\"height:15px; width:15px\" title=\"Nhấp chuột và kéo để di chuyển\" /></span>&nbsp;</span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">( chi ph&iacute; biến đổi b&igrave;nh qu&acirc;n thay đổi khi P v&agrave; f<sub>v</sub> c&ugrave;ng thay đổi)</span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">+ Chi ph&iacute; biến đổi F<sub>bđ</sub>:&nbsp;<em><span class=\"math-tex\"><iframe allowtransparency=\"true\" frameborder=\"0\" scrolling=\"no\" src=\"javascript:void(0)\" style=\"border: 0px; width: 94px; height: 25px; display: inline; vertical-align: middle;\"></iframe></span><img src=\"data:image/gif;base64,R0lGODlhAQABAPABAP///wAAACH5BAEKAAAALAAAAAABAAEAAAICRAEAOw==\" /><span style=\"background-color:rgba(220,220,220,0.5)\"><img src=\"data:image/gif;base64,R0lGODlhAQABAPABAP///wAAACH5BAEKAAAALAAAAAABAAEAAAICRAEAOw==\" style=\"height:15px; width:15px\" title=\"Nhấp chuột và kéo để di chuyển\" /></span></em></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">+ Tỷ suất chi ph&iacute; biến đổi: <em>\\[{f_v} = \\frac{{{F_{bd}}x100}}{{DT}} = \\frac{{\\overline V x100}}{P}\\]</em> &nbsp;(%)</span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">+ Tổng chi ph&iacute; ( TF): TF= Fcđ+Fbđ</span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">+ Chi ph&iacute; b&igrave;nh qu&acirc;n Z ( Gi&aacute; th&agrave;nh sản phẩm) </span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><em>\\[Z = \\frac{{TF}}{Q} = \\frac{{{F_{cd}}}}{Q} + \\overline V \\]</em></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">+ Lợi nhuận</span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">&nbsp;&nbsp;LN= DT- TF = (P - &nbsp;<em><span class=\"math-tex\"><iframe allowtransparency=\"true\" frameborder=\"0\" scrolling=\"no\" src=\"javascript:void(0)\" style=\"border: 0px; width: 18px; height: 25px; display: inline; vertical-align: middle;\"></iframe></span><img src=\"data:image/gif;base64,R0lGODlhAQABAPABAP///wAAACH5BAEKAAAALAAAAAABAAEAAAICRAEAOw==\" /><span style=\"background-color:rgba(220,220,220,0.5)\"><img src=\"data:image/gif;base64,R0lGODlhAQABAPABAP///wAAACH5BAEKAAAALAAAAAABAAEAAAICRAEAOw==\" style=\"height:15px; width:15px\" title=\"Nhấp chuột và kéo để di chuyển\" /></span></em> ) * Q &ndash; Fcđ = P*Q &ndash; (Fcđ + Fbđ) </span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">&nbsp;LN = Q1(P1-<em>V</em>1) + Q2( P2- <em>V</em><em>2</em>) &ndash; Fcđ</span></span></p>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><em><span class=\"math-tex\"><iframe allowtransparency=\"true\" frameborder=\"0\" scrolling=\"no\" src=\"javascript:void(0)\" style=\"border: 0px; width: 103px; height: 28px; display: inline; vertical-align: middle;\"></iframe></span><img src=\"data:image/gif;base64,R0lGODlhAQABAPABAP///wAAACH5BAEKAAAALAAAAAABAAEAAAICRAEAOw==\" /><span style=\"background-color:rgba(220,220,220,0.5)\"><img src=\"data:image/gif;base64,R0lGODlhAQABAPABAP///wAAACH5BAEKAAAALAAAAAABAAEAAAICRAEAOw==\" style=\"height:15px; width:15px\" title=\"Nhấp chuột và kéo để di chuyển\" /></span></em>&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Với R l&agrave; thuế suất thuế TNDN</span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">+ DT = P x Q</span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">+ P l&agrave; gi&aacute; h&agrave;ng h&oacute;a</span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<p>&nbsp;</p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><a name=\"_Toc1665869\"><strong>2.8.2 C&aacute;c chỉ ti&ecirc;u của điểm h&ograve;a vốn:</strong></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Sản lượng h&ograve;a vốn&nbsp;&nbsp;<em>\\[{Q_{hv}} = \\frac{{{F_{cd}}}}{{P - \\overline V }}\\]</em></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">DT h&ograve;a vốn &nbsp;DT<sub>hv </sub>= P x Q<sub>hv</sub></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Thời điểm h&ograve;a vốn <em><span class=\"math-tex\"><iframe allowtransparency=\"true\" frameborder=\"0\" scrolling=\"no\" src=\"javascript:void(0)\" style=\"border: 0px; width: 89px; height: 28px; display: inline; vertical-align: middle;\"></iframe></span><img src=\"data:image/gif;base64,R0lGODlhAQABAPABAP///wAAACH5BAEKAAAALAAAAAABAAEAAAICRAEAOw==\" /><span style=\"background-color:rgba(220,220,220,0.5)\"><img src=\"data:image/gif;base64,R0lGODlhAQABAPABAP///wAAACH5BAEKAAAALAAAAAABAAEAAAICRAEAOw==\" style=\"height:15px; width:15px\" title=\"Nhấp chuột và kéo để di chuyển\" /></span></em><img src=\"file:///C:\\Users\\ASUS\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image002.gif\" style=\"height:19px; width:12px\" />&nbsp;&nbsp;<img id=\"_x0000_i1031\" src=\"file:///C:\\Users\\ASUS\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image012.wmz\" style=\"height:14.25pt; width:9pt\" /> &nbsp;</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">&nbsp;m = mức ti&ecirc;u thụ b&igrave;nh qu&acirc;n th&aacute;ng = <em>\\[\\frac{{D{T_{nam}}}}{{12}}\\]&nbsp;&nbsp;&nbsp;</em>( 1 th&aacute;ng = 30 ng&agrave;y)</span></span></p>\r\n\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<p>&nbsp;</p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><a name=\"_Toc1665870\"><strong>2.8.3 X&aacute;c định sản lượng ti&ecirc;u thụ, DT ti&ecirc;u thụ năm kế hoạch để đạt được lợi nhuận theo dự kiến (LN = L)</strong></a></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><em>\\[{Q_L} = {F_{cd}} + \\frac{L}{P} - \\overline V \\]</em></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">DT<sub>L </sub>= Q<sub>L </sub>x P</span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<ol start=\"5\">\r\n			<li>\r\n			<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Hệ số đ&ograve;n bẩy kinh doanh H<sub>KQ</sub></span></span></p>\r\n			</li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Hệ số đ&ograve;n bẩy kinh doanh l&agrave; hệ số đo lường sự thay đổi của lợi nhuận ph&aacute;t sinh do thay đổi sản phẩm ti&ecirc;u thụ</span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><em>\\[{H_{KD}} = \\frac{{Q(P - \\overline V )}}{{Q{\\rm{[}}(P - \\overline V )*Q - {F_{cd}}{\\rm{]}}}}\\]</em></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">H<sub>KD</sub> &gt; 0 : Khi đ&oacute; DN đ&atilde; thu được l&atilde;i v&agrave; cứ tăng 1% khối lượng ti&ecirc;u thụ sẽ tăng H<sub>KQ</sub> % lợi nhuận</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">H<sub>KD </sub>&lt; 0 </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">H<sub>KD </sub>= 0 : Khi đ&oacute; DT vẫn chưa b&ugrave; đặp đủ chi ph&iacute; cố định do đ&oacute; chưa thể hạ gi&aacute; b&aacute;n</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>', '1551713260', null, '0', '45', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('290', 'CHƯƠNG 3: HOẠT ĐỘNG CUNG ỨNG CỦA DOANH NGHIỆP', '<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665871\"><strong>CHƯƠNG 3: HOẠT ĐỘNG CUNG ỨNG CỦA DOANH NGHIỆP</strong></a></span></span></samp></big></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665872\"><strong>3.1 Kh&aacute;i niệm, chức năng v&agrave; mục ti&ecirc;u của cung ứng</strong></a></span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665873\"><strong>3.1.1 Kh&aacute;i niệm v&agrave; chức năng của cung ứng</strong></a></span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Kh&aacute;i niệm: </span></span></samp></big></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Theo nghĩa rộng: cung ứng l&agrave; việc tổ chức c&aacute;c yếu tố đầu v&agrave;o nhằm đảm bảo cho hoạt động kinh doanh của doanh nghiệp</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Theo nghĩa hẹp: Cung ứng l&agrave; việc tổ chức nguồn nguy&ecirc;n nhi&ecirc;n vật liệu, b&aacute;n th&agrave;nh phẩm, h&agrave;ng h&oacute;a để đảm bảo cho hoạt động kinh doanh của DN.</span></span></samp></big></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Chức năng: Mua v&agrave; dự trữ</span></span></samp></big></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Chức năng mua h&agrave;ng: mua đ&uacute;ng, đủ, ph&ugrave; hợp với loại h&agrave;ng m&agrave; DN cần tr&aacute;nh ứng đọng, tồn h&agrave;ng</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Chức năng dự trữ: cung cấp đầy đủ v&agrave; b&aacute;n h&agrave;ng li&ecirc;n tục tuy nhi&ecirc;n c&oacute; thể g&acirc;y ứ đọng v&agrave; tốc đ&ocirc; chu chuyển h&agrave;ng h&oacute;a chậm. V&igrave; vậy, dự trữ hợp l&yacute; đ&aacute;p ứng tốt hơn nhu cầu thị trường v&agrave; hiệu quả hoạt động kinh doanh của DN</span></span></samp></big></p>\r\n\r\n	<p>&nbsp;</p>\r\n\r\n	<ol>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665874\"><strong>3.1.2 Mục ti&ecirc;u của cung ứng</strong></a></span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">3.1.2.1 Mục ti&ecirc;u của nh&agrave; quản trị cấp cao</span></span></samp></big></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Đ&uacute;ng chất lượng</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Đ&uacute;ng nh&agrave; cung cấp</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Đ&uacute;ng số lượng</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Đ&uacute;ng thời điểm</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Đ&uacute;ng gi&aacute;</span></span></samp></big></p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">3.1.2.2&nbsp; Mục ti&ecirc;u của bộ phận quản trị chiến lược cung ứng</span></span></samp></big></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Đảm bảo cho hoạt động của c&ocirc;ng ty được li&ecirc;n tục, ổn định</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Mua h&agrave;ng với gi&aacute; cạnh tranh</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Dự trữ ở mức tối ưu</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ph&aacute;t triển những nguồn cung cấp hữu hiệu v&agrave; đ&aacute;ng tin cậy</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Giữ vững mối quan hệ tốt đẹp với c&aacute;c nh&agrave; cung cấp hiện c&oacute;</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Tăng cường hợp t&aacute;c với c&aacute;c bộ phận kh&aacute;c trong c&ocirc;ng ty</span></span></samp></big></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">3.1.2.3&nbsp; Mục ti&ecirc;u của bộ phận nghiệp vụ cung ứng</span></span></samp></big></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Thực hiện c&aacute;c nghiệp vụ mang t&iacute;nh chất chiến thuật nhằm ho&agrave;n th&agrave;nh tốt c&aacute;c kế hoạch mua h&agrave;ng/ cung ứng đ&atilde; được lập ra</span></span></samp></big></p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n</ol>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665875\"><strong>3.2 Quản l&iacute; cung ứng c&oacute; lựa chọn</strong></a></span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665876\"><strong>3.2.1 Nguy&ecirc;n l&yacute; Pareto</strong></a></span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Quy luật ph&acirc;n phối kh&ocirc;ng đồng đều v&agrave; nguy&ecirc;n l&iacute; Pareto </span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">(đọc tham khảo gi&aacute;o tr&igrave;nh)</span></span></samp></big></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<p>&nbsp;</p>\r\n\r\n	<ol>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665877\"><strong>3.2.2 Ứng dụng nguy&ecirc;n l&iacute; pareto trong cung ứng</strong></a><strong> </strong></span></span></samp></big></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Phương ph&aacute;p 20/80 trong đa số trường hợp DN kinh doanh nhiều mặt h&agrave;ng th&igrave; 20% mặt h&agrave;ng đem lại 80% doanh số, 80% lợi nhuận v&agrave; cũng đ&ograve;i hỏi lượng đầu tư cho dự trữ chiếm 80% tổng số. 80% mặt h&agrave;ng c&ograve;n lại chiếm 20% dự trữ. Từ tr&ecirc;n, DN phải lựa chọn mặt h&agrave;ng th&iacute;ch hợp để ưu ti&ecirc;n. </span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Phương ph&aacute;p A-B-C:</span></span></samp></big></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">A 10-20% mặt h&agrave;ng thực hiện 70-80% gi&aacute; trị dự trữ</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">B 20-30% mặt h&agrave;ng thực hiện 10-20% GTDT</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C 50-60% mặt h&agrave;ng thực hiện 5-10% GTDT</span></span></samp></big></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665878\"><strong>3.3 Mua h&agrave;ng</strong></a><strong> </strong></span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665879\"><strong>3.3.1 Kh&aacute;i niệm v&agrave; mục ti&ecirc;u của mua h&agrave;ng</strong></a></span></span></samp></big></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kh&aacute;i niệm: </span></span></samp></big></p>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Mua h&agrave;ng l&agrave; qu&aacute; tr&igrave;nh trao đổi, thỏa thuận tiền- h&agrave;ng giữa người mua v&agrave; người b&aacute;n</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Với DN sản xuất , dịnh vụ mua h&agrave;ng gồm: Nguy&ecirc;n nhi&ecirc;n vật liệu, b&aacute;n th&agrave;nh phẩm...</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Với DNTM: H&agrave;ng h&oacute;a, nguy&ecirc;n vật liệu, bao g&oacute;i... </span></span></samp></big></p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<p>&nbsp;</p>\r\n\r\n	<ol>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665880\"><strong>3.3.2 C&aacute;c h&igrave;nh thức mua h&agrave;ng</strong></a></span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">3.3.2.1&nbsp; Mục ti&ecirc;u mua h&agrave;ng: </span></span></samp></big></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Mục ti&ecirc;u chung: Đ&aacute;p ứng nhu cầu của dự trữ, đảm bảo hoạt động kinh doanh b&igrave;nh thường, li&ecirc;n tục với chi ph&iacute; thấp nhất</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Mục ti&ecirc;u cụ thể: </span></span></samp></big></p>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Mục ti&ecirc;u chi ph&iacute;</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Mục ti&ecirc;u chất lượng</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Mục ti&ecirc;u an to&agrave;n</span></span></samp></big></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">3.3.2.2 C&aacute;c h&igrave;nh thức mua h&agrave;ng</span></span></samp></big></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Tập trung thu mua: phương ph&aacute;p n&agrave;y tiết kiệm chi ph&iacute; nhưng n&oacute; c&oacute; nhược điểm l&agrave; mua b&aacute;n t&aacute;ch rời nhau, nhiều khi mua h&agrave;ng về kh&ocirc;ng b&aacute;n được.</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ph&acirc;n t&aacute;n thu mua: Điều kiện rằng DN đặt mức kho&aacute;n cho từng quầy họ phải tự lo vốn v&agrave; nguồn h&agrave;ng kinh doanh. Ưu điểm của phương ph&aacute;p n&agrave;y l&agrave; nắm chắc được nhu cầu thị trường mua v&agrave; b&aacute;n gắn liền nhau. Nhược điểm : số lượng mua &iacute;t, gi&aacute; cả cao, chi ph&iacute; k&yacute; kết tăng.</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>Li&ecirc;n kết</em>: ưu điểm: Mua nhiều n&ecirc;n được gi&aacute; thấp, tiết kiệm chi ph&iacute; vận chuyển tr&ecirc;n một đơn vị h&agrave;ng h&oacute;a, chi ph&iacute; đi lại của c&aacute;n bộ thu mua v&agrave; một số chi ph&iacute; kh&aacute;c li&ecirc;n quan. Nhưng nhược điểm mua nhiều l&agrave; chi ph&iacute; bảo quản, hao hụt, tăng tốc, tốc độ chu chuyển vốn chậm. </span></span></samp></big></p>\r\n\r\n	<ol>\r\n		<li style=\"list-style-type:none\">&nbsp;</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665881\"><strong>3.3.3 Qu&aacute; tr&igrave;nh mua h&agrave;ng</strong></a><strong> </strong></span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Qu&aacute; tr&igrave;nh mua h&agrave;ng gồm 5 bước:</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Mua v&agrave;o = b&aacute;n ra + dự trữ đầu k&igrave; &ndash; dự trữ cuối k&igrave; </span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">B1: X&aacute;c định nhu cầu mua h&agrave;ng =&gt; T&igrave;m v&agrave; lựa chọn người cung ứng</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">B2: Thương lượng v&agrave; đặt h&agrave;ng </span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">B3: Theo d&otilde;i việc kiểm tra v&agrave; giao nhận h&agrave;ng</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">B4: Đ&aacute;nh gi&aacute; kết quả, </span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">+ nếu kh&ocirc;ng thỏa m&atilde;n =&gt; quay về &ldquo; T&igrave;m v&agrave; lựa chọn người cung ứng</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">+ nếu thỏa m&atilde;n = &gt; tiếp tục qu&aacute; tr&igrave;nh từ B1</span></span></samp></big></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">&nbsp;</li>\r\n</ul>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665882\"><strong>3.4 Quản l&iacute; kinh tế dự trữ</strong></a><strong> </strong></span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665883\"><strong>3.4.1 Kh&aacute;i niệm v&agrave; mục ti&ecirc;u</strong></a></span></span></samp></big></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kh&aacute;i niệm: Dự trữ h&agrave;ng h&oacute;a l&agrave; to&agrave;n bộ h&agrave;ng h&oacute;a được t&iacute;ch lũy lại để chờ sử dụng nhằm cung cấp dần dần c&aacute;c nguyện vật liệu, b&aacute;n th&agrave;nh phẩm cho Dn để sản xuất hoặc h&agrave;ng h&oacute;a, nguy&ecirc;n vật liệu, bao g&oacute;i chờ b&aacute;n ra được b&igrave;nh thường li&ecirc;n tục </span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Mục ti&ecirc;u: </span></span></samp></big></p>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Dự ph&ograve;ng </span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Dự b&aacute;o tốt</span></span></samp></big></p>\r\n\r\n	<ol>\r\n		<li style=\"list-style-type:none\">&nbsp;</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665884\"><strong>3.4.2 C&aacute;c chi ph&iacute; li&ecirc;n quan đến dự trữ</strong></a></span></span></samp></big></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">A: Chi ph&iacute; do c&oacute; dự trữ h&agrave;ng h&oacute;a</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">B: Chi ph&iacute; đặt h&agrave;ng</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C: Chi ph&iacute; do gi&aacute;n đoạn dự trữ khi xảy ra thiếu hụt h&agrave;ng h&oacute;a, nguy&ecirc;n nhi&ecirc;n vật liệu.</span></span></samp></big></p>\r\n\r\n	<ol>\r\n		<li style=\"list-style-type:none\">&nbsp;</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665885\"><strong>3.4.3 Ứng dụng m&ocirc; h&igrave;nh Ford- Hariss trong x&acirc;y dựng kế hoạch dự trữ v&agrave; cung ứng:</strong></a></span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">X&acirc;y dựng kế hoạch dự trữ h&agrave;ng h&oacute;a cho từng mặt h&agrave;ng: </span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">X&aacute;c định 3 chỉ ti&ecirc;u: </span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Khối lượng mua mỗi lần</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C&aacute;c thời điểm nhập, đặt h&agrave;ng</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Tổng chi ph&iacute; dự trữ</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>3.4.3.1</strong>&nbsp; <strong>Ứng dụng m&ocirc; h&igrave;nh Wilson&nbsp; x&aacute;c định lượng h&agrave;ng tối ưu cho một đơn h&agrave;ng:</strong></span></span></samp></big></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Giả định </span></span></samp></big></p>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">&nbsp;H&agrave;ng h&oacute;a được ti&ecirc;u thụ li&ecirc;n tục đều đặn</span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Khối lượng h&agrave;ng v&agrave;o mỗi lần l&agrave; như nhau trog năm KH</span></span></samp></big></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Năm kế hoạch:</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">D: Số lượng nhu cầu h&agrave;ng h&oacute;a mua v&agrave;o trong năm KH</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Q: Số lượng h&agrave;ng h&oacute;a cho 1 đơn h&agrave;ng</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">N: Số lần nhập h&agrave;ng trong năm KH </span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">P<sub>mua </sub>: Gi&aacute; mua một đơn vị h&agrave;ng h&oacute;a</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">I: Tỷ suất chi ph&iacute; bảo quản</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Fbq: Chi ph&iacute; bảo quản t&iacute;nh cho một đơn vị h&agrave;ng h&oacute;a trong năm KH</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">( Fbq= I x P<sub>mua</sub>) </span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Fđh: Chi ph&iacute; cho một lần đặt h&agrave;ng</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Q<sup>*</sup>: Số lượng mua h&agrave;ng tối ưu một lần</span></span></samp></big></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Số lượng h&agrave;ng đặt tối ưu mỗi lần:</span></span></samp></big></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span class=\"math-tex\">\\(Q* = \\sqrt {\\frac{{2*{F_{dh}}*D}}{{{F_{bq}}}}}  = \\sqrt {\\frac{{2*{F_{dh}}*D}}{{I*P}}} \\)</span> </span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">N= D/Q</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Lưu &yacute;: N nguy&ecirc;n</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">N lẻ chia 2 trường hợp: </span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">VD: Nếu N= 3.9 =&gt;&nbsp; - TH1: N= 3&nbsp; &oacute; Q1 =&gt; TF1</span></span></samp></big></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">&nbsp;TH2: N=4 &oacute; Q2 =&gt; TF2 </span></span></samp></big></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Chọn TF c&oacute; gi&aacute; trị nhỏ hơn </span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C&oacute; Q* &oacute; TF min </span></span></samp></big></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span class=\"math-tex\">\\(TF = {F_{dh}}*N + {F_{bq}}*\\frac{Q}{2}\\)</span></span></span></samp></big></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span class=\"math-tex\">\\( = ({F_{Dh}}*\\frac{D}{Q}) + (I*{P_{mua}}*\\frac{Q}{2})\\)</span></span></span></samp></big></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Khoảng c&aacute;ch giữa 2 lần đặt h&agrave;ng li&ecirc;n tiếp: T= 360/ N (ng&agrave;y) </span></span></samp></big></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Thời gian đặt h&agrave;ng ( thường đề b&agrave;i cho) </span></span></samp></big></p>\r\n	</li>\r\n	<li>\r\n	<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Thời gian nhập h&agrave;ng ( t&iacute;nh dựa v&agrave;o đề b&agrave;i để lập bảng thống k&ecirc;)</span></span></samp></big></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>3.4.3.2&nbsp; Ứng dụng m&ocirc; h&igrave;nh Wilson cho trường hợp khi mua nhiều h&agrave;ng một lần được giảm gi&aacute; ( chiết khấu): </strong></span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">= &gt; X&aacute;c định khối lượng h&agrave;ng mua một lần tối ưu theo c&aacute;c bước:</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>B1: </strong>X&aacute;c định Q ở từng mức khấu trừ theo c&ocirc;ng thức:</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span class=\"math-tex\">\\(Qi = \\sqrt {\\frac{{2*{F_{dh}}*D}}{{I*{P_i}}}} \\)</span><br />\r\n(i= 1,2,3,......n) <img src=\"file:///C:\\Users\\ASUS\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image002.gif\" style=\"height:19px; width:12px\" />&nbsp;</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">I: Tỷ suất chi ph&iacute; bảo quản </span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">P<sub>Mua</sub>i: Gi&aacute; mua trong từng mức chiết khấu i</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>B2: </strong>&nbsp;Điều chỉnh Qi </span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Qi= lượng mua tối thiểu để nhận được mức chiết khấu i</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>B3</strong>: X&aacute;c định tổng chi ph&iacute; h&agrave;ng h&oacute;a dự trữ theo c&aacute;c Qi đ&atilde; điều chỉnh ở b2</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">(tổng chi ph&iacute; h&agrave;ng tồn kho hay TF<sub>TK</sub>)</span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">TC= TF<sub>Tki</sub> = (P <sub>mua i</sub>*D) + (Fdh * D/Qi) + (I* P<sub>muai</sub>* Qi/2) </span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>B4:</strong> Lựa chọn Q c&oacute; TF<sub>TK </sub>nhỏ nhất </span></span></samp></big></p>\r\n\r\n<p><big><samp><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">= &gt; X&aacute;c định kế hoạch cung ứng: Q* , TF<sub>TK</sub>, N, .....</span></span></samp></big></p>', '1551713286', null, '0', '45', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('293', 'Chương 4: Thị trường và hành vi khách hàng', '<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">CHƯƠNG 4: THỊ TRƯỜNG V&Agrave; H&Agrave;NH VI KH&Aacute;CH H&Agrave;NG</span></span></span></span></p>\r\n\r\n<ol>\r\n	<li><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&aacute;i qu&aacute;t về thị trường:</span></span></span></span></strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>C&aacute;c g&oacute;c độ tiếp cận</em></u> :Vi m&ocirc; v&agrave; vĩ m&ocirc;</span></span></span></span></li>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Những yếu tố x&aacute;c định thị trường:</span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Mua c&aacute;i g&igrave;?</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Tại sao mua?</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Ai mua?</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Mua như thế n&agrave;o</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Mua bao nhi&ecirc;u</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Mua ở đ&acirc;u</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&acirc;n loại thị trường:</span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Căn cứ quan hệ mua b&aacute;n giữa c&aacute;c quốc gia</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Căn cứ mức độ x&atilde; hội h&oacute;a của thị trường</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Căn cứ h&agrave;ng h&oacute;a lưu th&ocirc;ng tr&ecirc;n thị trường</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Căn cứ v&agrave;o thế vị v&agrave; vai tr&ograve; của người mua v&agrave; người b&aacute;n</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Căn cứ v&agrave;o tương quan số lượng người mua v&agrave; người b&aacute;n tr&ecirc;n thị trường</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Căn cứ v&agrave;o c&aacute;c yếu tố đầu v&agrave;o</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp;Cấu tr&uacute;c bậc thị trường:</span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:48px; text-align:center\"><img src=\"/public/images/course/58/lesson/1552134731_image-20190309193557-1.png\" style=\"height:406px; width:800px\" /></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Dung lượng thị trường của một sản phẩm</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Thị trường tiềm năng của doanh nghiệp</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Thị trường khả hiệu lực của doanh nghiệp</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Thị trường hữu hiệu của doanh nghiệp</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Thị trường được cung ứng (mục ti&ecirc;u) của doanh nghiệp</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Thị trường hiện hữu của doanh nghiệp</span></span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">H&agrave;nh vi kh&aacute;ch h&agrave;ng người ti&ecirc;u d&ugrave;ng:</span></span></span></span></strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Kh&aacute;i niệm</em></u>: l&agrave; tập niềm tin của tư duy v&agrave; thi&ecirc;n hướng phản xạ t&acirc;m l&yacute; nội tại, biểu hiện ra ngo&agrave;i bằng sự ủng hộ, phản đối, thờ ơ,...với một kh&aacute;ch thể hoặc diễn biến. </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Cấu tr&uacute;c</em></u>: tri thức, t&igrave;nh cảm, thi&ecirc;n hướng</span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:center\"><img src=\"/public/images/course/58/lesson/1552134736_image-20190309193557-2.png\" style=\"height:562px; width:800px\" /></p>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Quyết định mua h&agrave;ng của người ti&ecirc;u d&ugrave;ng:</span></span></span></span></em></u></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp;<em>Vai tr&ograve; trong mua sắm: </em></span></span></span></span>\r\n	<ul style=\"list-style-type:circle\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Người khởi xướng</span></span></span></span></li>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Người ảnh hưởng</span></span></span></span></li>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Người quyết định</span></span></span></span></li>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Người mua</span></span></span></span></li>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Người sử dụng</span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c loại h&igrave;nh h&agrave;nh vi mua</span></span></span></span></em></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:96px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:12px; text-align:center\"><img src=\"/public/images/course/58/lesson/1552134734_image-20190309193557-3.png\" style=\"height:221px; width:800px\" /></p>\r\n\r\n<p style=\"margin-left:12px\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tiến tr&igrave;nh quyết định mua</span></span></span></span></em></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:6px\"><img src=\"/public/images/course/58/lesson/1552134731_image-20190309193557-4.png\" style=\"height:89px; width:700px\" /></p>\r\n\r\n<p style=\"margin-left:6px\">&nbsp;</p>\r\n\r\n<ol start=\"3\">\r\n	<li><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">H&agrave;nh vi mua kh&aacute;ch h&agrave;ng tổ chức</span></span></span></span></strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đặc điểm h&agrave;nh vi mua kh&aacute;ch h&agrave;ng tổ&nbsp; chức</span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Sử dụng sản phẩm trực tiếp/gi&aacute;n tiếp cho việc vận h&agrave;nh tổ chức</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Mức độ tập trung theo v&ugrave;ng địa l&yacute; rất cao</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Số lượng người mua &iacute;t nhưng số lượng trong một lần mua lớn</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Nhu cầu đầu v&agrave;o ph&aacute;t sinh từ cầu đầu ra  k&eacute;m co gi&atilde;n, dao động theo cầu đầu ra, v&agrave; c&oacute; t&iacute;nh li&ecirc;n kết v&agrave; bổ sung trong sử dụng</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Mối quan hệ người mua v&agrave; người b&aacute;n rất chặt chẽ</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Qu&aacute; tr&igrave;nh ra quyết định mua h&agrave;ng l&agrave; tập thể</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Việc mua h&agrave;ng thường mang t&iacute;nh trực tiếp. </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Người mua v&agrave; người b&aacute;n c&oacute; thể l&agrave; kh&aacute;ch h&agrave;ng của nhau.</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">M&ocirc; h&igrave;nh h&agrave;nh vi mua kh&aacute;ch h&agrave;ng tổ chức</span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:6px\"><img src=\"/public/images/course/58/lesson/1552134734_image-20190309193557-5.png\" style=\"height:373px; width:800px\" /></p>\r\n\r\n<p style=\"margin-left:6px\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Qu&aacute; tr&igrave;nh quyết định mua của kh&aacute;ch h&agrave;ng tổ chức</span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:-6px; text-align:center\"><img src=\"/public/images/course/58/lesson/1552134736_image-20190309193557-6.png\" style=\"height:345px; width:800px\" /></p>', '1551714062', null, '0', '58', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('294', 'Câu 4 (nhóm 1)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Vẽ sơ đồ qu&aacute; tr&igrave;nh quyết định mua của kh&aacute;ch h&agrave;ng-người ti&ecirc;u dung. Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh họa một trong c&aacute;c giai đoạn của qu&aacute; tr&igrave;nh quyết định mua của kh&aacute;ch h&agrave;ng.(nhu cầu được nhận thức, t&igrave;m kiếm th&ocirc;ng tin)</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u kh&aacute;i niệm thị trường theo quan điểm của marketing? Tr&igrave;nh b&agrave;y c&aacute;c yếu tố x&aacute;c định thị trường của doanh nghiệp? Cho biết &yacute; nghĩa của c&aacute;c yếu tố n&agrave;y với hoạt động marketing của doanh nghiệp.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u c&aacute;c loại thị trường căn cứ v&agrave;o c&aacute;c yếu tố đầu v&agrave;o, đầu ra của doanh nghiệp?Tr&igrave;nh b&agrave;y ti&ecirc;u chuẩn lựa chọn nh&agrave; cung ứng của doanh nghiệp? Vẽ sơ đồ cấu tr&uacute;c bậc thị trường của doanh nghiệp? Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ bậc thị trường hiện hữu của doanh nghiệp?</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u vai tr&ograve; trong mua sắm v&agrave; c&aacute;c loại h&agrave;nh vi mua của kh&aacute;ch h&agrave;ng người ti&ecirc;u dung? Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh họa một trong c&aacute;c h&agrave;nh vi mua của kh&aacute;ch h&agrave;ng người ti&ecirc;u d&ugrave;ng( Mua theo th&oacute;i quen, mua sắm đa dạng, mua sắm phức tạp), Vẽ m&ocirc; h&igrave;nh h&agrave;nh vi mua của kh&aacute;ch h&agrave;ng người ti&ecirc;u d&ugrave;ng? Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh họa một trong c&aacute;c giai đoạn.</span></span></span></span></p>', '1551714114', null, '293', '58', '1', '293', '293', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('296', 'Câu 2 (nhóm 1) và câu 1 (nhóm 2)', '<p><span style=\"font-size:13.0pt\"><span style=\"line-height:130%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Vẽ m&ocirc; h&igrave;nh c&aacute;c yếu tố ảnh hưởng đến h&agrave;nh vi mua của kh&aacute;ch h&agrave;ng người ti&ecirc;u d&ugrave;ng? Ph&acirc;n t&iacute;ch c&oacute; v&iacute; dụ minh họa c&aacute;c yếu tố?&nbsp; C&aacute;c hoạt động nghi&ecirc;n cứu h&agrave;nh vi kh&aacute;ch h&agrave;ng?</span></span></span></p>', '1551749467', null, '283', '59', '1', '283', '283', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('297', 'Câu 2 (nhóm 2)', '<p><span style=\"font-size:13pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Tr&igrave;nh b&agrave;y mối quan hệ giữa H&agrave;nh Vi mua kh&aacute;ch h&agrave;ng với hoạt động Marketing?</span></span></p>\r\n\r\n<p>&nbsp;</p>', '1551749942', null, '283', '59', '1', '283', '283', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('298', 'Chương 2: Nhận thức và động cơ của NTD', '<ol>\r\n	<li><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Nhận thức</span></span></span></strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><u><em>Kh&aacute;i niệm</em></u>: </span><span style=\"font-size:13.0pt\">Nhận thức c&aacute; nh&acirc;n l&agrave; tiến tr&igrave;nh qua đ&oacute; con người lựa chọn,tổ chức v&agrave; th&ocirc;ng đạt những th&ocirc;ng tin hội nhập để tạo th&agrave;nh một h&igrave;nh ảnh c&oacute; &yacute; nghĩa về thế giới. Cũng c&oacute; thể hiểu nhận thức l&agrave; qu&aacute; tr&igrave;nh k&iacute;ch th&iacute;ch được lựa chọn, sắp xếp v&agrave; giải th&iacute;ch</span></span></span></li>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Đặc điểm: </span></span></span></em></u></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; NTD chỉ nhận được một số k&iacute;ch th&iacute;ch nhất định từ m&ocirc;i trường</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Mức độ ch&uacute; &yacute; c&aacute;c k&iacute;ch th&iacute;ch kh&ocirc;ng giống nhau, số lượng k&iacute;ch th&iacute;ch đưuọc ch&uacute; &yacute; &iacute;t hơn</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Sự nhận thức, đ&aacute;nh gi&aacute; k&iacute;ch th&iacute;ch mang t&iacute;nh chủ quan.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Nhận thức l&agrave; sự thừa nhận của NTD.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Qu&aacute; tr&igrave;nh nhận thức:</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Qu&aacute; tr&igrave;nh nhận thức tổng qu&aacute;t</span></span></span></em></u></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:74px\"><img src=\"/public/images/course/59/lesson/1552120162_image-20190309153313-1.png\" style=\"height:583px; width:788px\" /></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Ph&acirc;n biệt cảm gi&aacute;c v&agrave; nhận thức</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Hệ thống cảm gi&aacute;c </span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Ngưỡng cảm gi&aacute;c </span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Thuyết phục tiềm thức</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Nhận thức c&oacute; lựa chọn </span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Động cơ</span></span></span></strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><u><em>Kh&aacute;i niệm:</em></u> </span><span style=\"font-size:13.0pt\">L&agrave; những động lực b&ecirc;n trong mỗi c&aacute; nh&acirc;n, n&oacute; th&uacute;c đẩy họ h&agrave;nh động&hellip;vv</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><u><em>Nguy&ecirc;n nh&acirc;n xuất hiệ</em></u>n: khi căng thằng do nhu cầu chưa thỏa m&atilde;n, nhu cầu được khơi gợi -&gt; NTD cố gắng giảm căng thẳng.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><u><em>Qu&aacute; tr&igrave;nh động cơ th&uacute;c đấy</em></u>: </span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:50px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Nhận thức nhu cầu -&gt; Căng thẳng -&gt; Sức mạnh động cơ -&gt; Hướng động cơ -&gt; H&agrave;nh vi -&gt; Mong muốn -&gt; Mục ti&ecirc;u.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Xung đột động cơ:</span></span></span></em></u></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Xung đột tiến tới &ndash; tiến tới</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Xung đột tiến tới &ndash; n&eacute; tr&aacute;nh</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Xung đột n&eacute; tr&aacute;nh &ndash; n&eacute; tr&aacute;nh</span></span></span></li>\r\n</ul>', '1551750155', null, '0', '59', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('299', 'Câu 3 (nhóm 1&2)', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u kh&aacute;i niệm nhận thức? Vẽ m&ocirc; h&igrave;nh &ldquo; Qu&aacute; tr&igrave;nh nhận thức tổng qu&aacute;&rdquo;. Tr&igrave;nh b&agrave;y C&aacute;c bước của qu&aacute; tr&igrave;nh nhận thức?</span></span></p>', '1551750250', null, '298', '59', '1', '298', '298', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('300', 'Câu 4 (nhóm 1)', '<p><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u kh&aacute;i niệm giải th&iacute;ch v&agrave; c&aacute;c đặc trưng của giải th&iacute;ch?</span></span></p>', '1551750821', null, '298', '59', '1', '298', '298', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('301', 'Câu 4 (nhóm 2) và câu 5 (nhóm 1)', '<p><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c yếu tố cấu th&agrave;nh &ldquo;hệ thống cảm gi&aacute;c&rdquo;. Ph&acirc;n t&iacute;ch c&aacute;c yếu tố cấu th&agrave;nh hệ thống cảm gi&aacute;c?</span></span></p>', '1551751039', null, '298', '59', '1', '298', '298', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('302', 'Câu 6 (nhóm 1) và Câu 5 (nhóm 2)', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u kh&aacute;i niệm động cơ v&agrave; nguy&ecirc;n nh&acirc;n xuất hiện động cơ. Tr&igrave;nh b&agrave;y qu&aacute; tr&igrave;nh động cơ th&uacute;c đẩy? Ph&acirc;n t&iacute;ch &ldquo; sức mạnh động cơ&rdquo;?</span></span></p>', '1551751081', null, '298', '59', '1', '298', '298', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('303', 'Câu 7 ( nhóm 1)', '<p><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u kh&aacute;i niệm v&agrave; đặc điểm của &ldquo;sức mạnh động cơ&rdquo; </span></span></p>', '1551751122', null, '298', '59', '1', '298', '298', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('304', 'Câu 8 (nhóm 1)', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u hướng động cơ v&agrave; c&aacute;c loại xung đột động cơ?</span></span></p>', '1551751146', null, '298', '59', '1', '298', '298', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('305', 'Chương 3: Học tập và thái độ của người tiêu dùng', '<p style=\"text-align:center\"><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chương 3:&nbsp; Học tập v&agrave; th&aacute;i độ của NTD</span></span></span></span></strong></p>\r\n\r\n<ol>\r\n	<li><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Học tập </span></span></span></span></strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><u><em><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&aacute;i niệm: </span></span></em></u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Sự thay đổi kh&ocirc;ng ngừng trong h&agrave;nh vi của cong người xảy ra như kết quả của kinh nghiệm</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Nguy&ecirc;n tắc học tập</em></u>: Kinh nghiệm &ndash; học tập </span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Trực tiếp hoặc gi&aacute;n tiếp qua quan s&aacute;t</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Học tập ngẫu nhi&ecirc;n </span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c thuyết học tập</span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Học thuyết học tập điều kiện</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Học thuyết học tập hoạt động</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Học thuyết học tập nhận thức</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Học thuyết học tập h&agrave;nh vi</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Học thuyết học tập x&atilde; hội</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong>Qu&aacute; tr&igrave;nh ghi nhớ:</strong> Đầu v&agrave;o -&gt; M&atilde; h&oacute;a -&gt; Lưu giữ -&gt; Phục hồi</span></span></span></span></li>\r\n	<li><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hệ thống tr&iacute; nhớ: </span></span></span></span></strong></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Ghi nhớ cảm gi&aacute;c</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Ch&uacute; &yacute;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Tr&iacute; nhớ tạm thời</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Nhắc lại chi tiết</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Tr&iacute; nhớ l&acirc;u d&agrave;i</span></span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Th&aacute;i độ</span></span></span></span></strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><em>Kh&aacute;i niệm</em>: Th&aacute;i độ l&agrave; những đ&aacute;nh gi&aacute; c&oacute; t&iacute;nh l&acirc;u d&agrave;i v&agrave; kh&aacute;i qu&aacute;t về con người (bao gồm của bản th&acirc;n), c&aacute;c vật thể hoặc c&aacute;c vấn đề.</span></span></span></span></li>\r\n	<li><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đặc điểm: </span></span></span></span></em></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Hướng tới một đ&oacute;i tượng cụ thể</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Cho ph&eacute;p c&aacute; nh&acirc;n ứng xử tương đối ổn định với những g&igrave; quen thuộc</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Th&aacute;i độ c&oacute; thể ổn định hoặc thay đổi</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Bị ảnh hưởng bởi m&ocirc;i trường</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Cấu tr&uacute;c th&aacute;i độ</span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Nhận thức: thể hiện sự hiểu biết</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Cảm nhận </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &Yacute; định h&agrave;nh động </span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chiến thuật giao tiếp thay đổi th&aacute;i độ kh&aacute;ch h&agrave;ng</span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Nguồn ph&aacute;t th&ocirc;ng điệp</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Cấu tr&uacute;c th&ocirc;ng điệp</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Phương tiện truyền tải </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Đặc điểm của người nhận</span></span></span></span></li>\r\n</ul>', '1551752038', null, '0', '59', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('306', 'Câu 9 (Nhóm 1) và câu 6( nhóm 2):', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&aacute;i niệm học tập; nguy&ecirc;n tắc học tập; Kể t&ecirc;n c&aacute;c học thuyết học tập. Tr&igrave;nh b&agrave;y c&aacute;c thuyết học tập( Thuyết học tập điều kiện cổ điển, học tập hoạt động v&agrave; học tập quan s&aacute;t)</span></span></p>', '1551752063', null, '305', '59', '1', '305', '305', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('307', 'Câu 11(nhóm 1) và câu 7(nhóm 2)', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u kh&aacute;i niệm v&agrave; đặc điểm của th&aacute;i độ. Kể t&ecirc;n c&aacute;c m&ocirc; h&igrave;nh nghi&ecirc;n cứu th&aacute;i độ. N&ecirc;u c&aacute;c th&agrave;nh tố &ldquo;cấu tr&uacute;c th&aacute;i độ&rdquo;. tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh họa m&ocirc; h&igrave;nh nghi&ecirc;n cứu th&aacute;i độ( M&ocirc; h&igrave;nh th&aacute;i độ đa thuộc t&iacute;nh, m&ocirc; h&igrave;nh điểm l&iacute; tưởng)</span></span></p>', '1551752126', null, '305', '59', '1', '305', '305', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('308', 'Câu 12(Nhóm 1) và Câu 8 (nhóm 2)', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u c&aacute;c th&agrave;nh tố cấu th&agrave;nh&rdquo; Cấu tr&uacute;c th&aacute;i độ&rdquo;. Tr&igrave;nh b&agrave;y c&aacute;c chiến thuật giao tiếp thay đổi th&aacute;i độ?</span></span></p>', '1551752159', null, '305', '59', '1', '305', '305', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('309', 'Câu 10 (Nhóm 1)', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u kh&aacute;i niệm qu&aacute; tr&igrave;nh ghi nhớ. Vẽ m&ocirc; h&igrave;nh qu&aacute; tr&igrave;nh ghi nhớ.?</span></span></p>', '1551752638', null, '305', '59', '1', '305', '305', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('310', 'Chương 4: Quan niệm về bản thân và lối sống của NTD', '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chương 4: Quan niệm về bản th&acirc;n v&agrave; lối sống của NTD</span></span></span></span></p>\r\n\r\n<ol>\r\n	<li><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Quan niệm bản th&acirc;n</span></span></span></span></strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Kh&aacute;i niệm</em></u>: L&agrave; th&aacute;i độ của c&aacute; nh&acirc;n đối với bản th&acirc;n họ.</span></span></span></span></li>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Th&agrave;nh tố:</span></span></span></span></em></u></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Nội dụng: Hấp dẫn vẻ mặt v&agrave; năng lực tr&iacute; &oacute;c</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;T&iacute;nh chất: T&iacute;ch cực hay ti&ecirc;u cực</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Cường độ: Mạnh-yếu</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Mức độ ổn định theo thời gian: Ngắn ngủi- l&acirc;u d&agrave;i</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Độ ph&ugrave; hợp- tương th&iacute;ch với thực tế bản th&acirc;n đến mức độ n&agrave;o</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; <strong>&nbsp; &nbsp; 2. C&aacute; t&iacute;nh</strong></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Kh&aacute;i niệm</em></u>: L&agrave; c&aacute;c đặc điểm t&acirc;m l&yacute; ri&ecirc;ng c&oacute; của c&aacute; nh&acirc;n c&aacute;o ảnh hưởng nhất qu&aacute;n tới c&aacute;ch con người phản ứng với m&ocirc;i trường của người đ&oacute;.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Đặc điểm</em></u>: </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Thể hiện sự kh&aacute;c biệt giữa c&aacute;c c&aacute; nh&acirc;n, kh&ocirc;ng c&oacute; hai người giống nhau ho&agrave;n to&agrave;n về c&aacute; t&iacute;nh</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;C&aacute; t&iacute;nh nhất qu&aacute;n v&agrave; bền vững</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; C&aacute; t&iacute;nh c&oacute; thể thay đổi</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; C&aacute; t&iacute;nh c&oacute; vai tr&ograve; quan trọng x&aacute;c định h&agrave;nh vi</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c học thuyết c&aacute; t&iacute;nh</span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Học thuyết Freud</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Học thuyết Freud mới</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Học thuyết dấu hiệu</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp;<strong> &nbsp; &nbsp; &nbsp;3. Giới t&iacute;nh</strong> </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Vai tr&ograve; nữ giới </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Vai tr&ograve; nam giới</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp;<strong>4.Lối sống</strong></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Kh&aacute;i niệm</em></u>: L&agrave; những gi&aacute; trị hay sở th&iacute;ch được chia sẻ, đặc biệt l&agrave; những c&aacute;i được phản &aacute;nh trong c&aacute;c m&ocirc; h&igrave;nh ti&ecirc;u thụ.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Ph&acirc;n loại</em></u>: theo m&ocirc; h&igrave;nh AIOs v&agrave; VALS2</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>AIOs:</em></u> ph&acirc;n loại theo 3 nh&oacute;m biến số: Hoạt động; Mối quan t&acirc;m; Quan điểm.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>VALS2</em></u>: M&ocirc; h&igrave;nh ti&ecirc;u thụ v&agrave; th&aacute;i độ với SP của người SD</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Hoạt động MKT theo lối sống</em></u>:</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;X&aacute;c định thị trường mục ti&ecirc;u </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;H&igrave;nh th&agrave;nh c&aacute;ch nh&igrave;n mới v&egrave; thị trường </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Định vị sản phẩm </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Thể hiện tốt hơn c&aacute;c đặc điểm của SP</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;MKT c&aacute;c vấn đề x&atilde; hội, ch&iacute;nh trị</span></span></span></span></li>\r\n</ul>', '1551753376', null, '0', '59', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('311', 'Câu 13 (Nhóm 1) và Câu 9 (Nhóm 2)', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u kh&aacute;i niệm v&agrave; c&aacute;c th&agrave;nh tố cấu th&agrave;nh &ldquo;quan niệm về bản th&acirc;n&rdquo;. Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh họa c&aacute;c th&agrave;nh tố &ldquo; quan niệm bản th&acirc;n&rdquo;</span></span></p>', '1551753477', null, '310', '59', '1', '310', '310', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('312', 'Câu 14 (nhóm 1) và câu 10( nhóm 2)', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u kh&aacute;i niệm v&agrave; c&aacute;c đặc điểm của c&aacute; t&iacute;nh. Tr&igrave;nh b&agrave;y c&aacute;c học thuyết về c&aacute; t&iacute;nh</span></span></p>', '1551753502', null, '310', '59', '1', '310', '310', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('313', 'Câu 15 (nhóm 1) và câu 11 (nhóm 2).', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&acirc;n t&iacute;ch vai tr&ograve; giới t&iacute;nh. &nbsp;</span></span></p>', '1551753529', null, '310', '59', '1', '310', '310', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('314', 'Câu 16 (nhóm 1) và câu 12 (nhóm 2)', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u kh&aacute;i niệm lối sống. N&ecirc;u c&aacute;c m&ocirc; h&igrave;nh nghi&ecirc;n cứu lỗi sống. Tr&igrave;nh b&agrave;y c&oacute; li&ecirc;n hệ thực tế c&aacute;c m&ocirc; h&igrave;nh nghi&ecirc;n cứu về lối sống?</span></span></p>', '1551753570', null, '310', '59', '1', '310', '310', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('315', 'Câu 13 (nhóm 2)', '<p><span style=\"font-size:13pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Tr&igrave;nh b&agrave;y c&oacute; li&ecirc;n hệ thực tế c&aacute;c xu hương về lối sống?</span></span></p>', '1551754813', null, '310', '59', '1', '310', '310', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('316', 'Chương 5:  Văn hóa - xã hội đến hành vi NTD', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chương 5: Ảnh hưởng của văn h&oacute;a - x&atilde; hội đến h&agrave;nh vi NTD</span></span></span></span></p>\r\n\r\n<ol>\r\n	<li><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Văn h&oacute;a</span></span></span></span></strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Kh&aacute;i niệm văn h&oacute;a</em></u>: L&agrave; c&aacute;c phương thức kh&aacute;c nhau của lối sống v&agrave; những m&ocirc; h&igrave;nh tổng thể của một nh&oacute;m người về lối sống của hộ. C&aacute;c c&aacute; nh&acirc;n học tập những m&ocirc; h&igrave;nh v&agrave; phương thức tr&ecirc;n qua cộng đồng v&agrave; được chia sẻ giữa c&aacute;c th&agrave;nh vi&ecirc;n trong c&ugrave;ng x&atilde; hội.</span></span></span></span></li>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ảnh hưởng của văn h&oacute;a: </span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Ng&ocirc;n ngữ bằng lời v&agrave; ng&ocirc;n ngữ cử chỉ</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &Yacute; nghĩa của c&aacute;c biểu tượng</span></span> </span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Đồ ăn thức uống</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Điều cấm kị v&agrave; cấm đo&aacute;n</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; C&aacute;c c&aacute;ch ứng xử trong c&aacute;c ho&agrave;n cảnh kh&aacute;c nhau</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Gi&aacute; trị văn h&oacute;a</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Chuẩn mực văn h&oacute;a</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; 2.Nh&aacute;nh văn h&oacute;a</span></span></span></span></strong></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Kh&aacute;i niệm</em></u>: L&agrave; nh&oacute;m văn h&oacute;a ri&ecirc;ng biệt tồn tại như một ph&acirc;n đoạn thị trường r&otilde; r&agrave;ng trong một x&atilde; hội rộng lớn v&agrave; phức tạp hơn.</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; 3.Tầng lớp x&atilde; hội: </span></span></span></span></strong></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Kh&aacute;i niệm</em></u>: L&agrave; c&aacute;c lớp người kh&aacute;c nha do kết quả ph&acirc;n chia tương đối đồng nhất v&agrave; ổn định trong x&atilde; hội, được sắp xếp&nbsp; theo một thứ bậc, những th&agrave;nh vi&ecirc;n trong từng thứ bậc c&ugrave;ng chia sẻ những gi&aacute; trị lợi &iacute;ch v&agrave; c&aacute;ch ứng xử giống nhau.</span></span></span></span></li>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đặc điểm: </span></span></span></span></em></u></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Biểu thị địa vị kh&aacute;c nhau, sắp xếp theo chiều dọc từ cao xuống thấp</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; L&agrave; biểu tượng của địa vị</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; C&oacute; trạng th&aacute;i kh&ocirc;ng r&otilde; r&agrave;ng của chủ nghĩa biểu tượng</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Đa k&iacute;ch thước: thu nhập, tr&igrave;nh độ văn h&oacute;a, nghề,&hellip;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Năng động &ndash; dễ thay đổi giai tầng.</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; <strong>&nbsp; &nbsp; 4. Nh&oacute;m tham khảo</strong></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Kh&aacute;i niệm</em></u>: L&agrave; c&aacute;c c&aacute; nh&acirc;n (nh&oacute;m) c&oacute; thực( tưởng tượng) c&oacute; ảnh hưởng lớn đến những đ&aacute;nh gi&aacute;, nguyện vọng v&agrave; h&agrave;nh vi của từng c&aacute; nh&acirc;n.</span></span></span></span></li>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">H&igrave;nh thức t&aacute;c động </span></span></span></span></em></u></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Th&ocirc;ng tin: về sản phẩm, nh&atilde;n hiệu từ c&aacute;c hiệp hội, bạn b&egrave;, người th&acirc;n&hellip;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Thực tế: Chọn mua ảnh hưởng, sở th&iacute;ch của c&aacute;c đồng nghiệp, gia đ&igrave;nh&hellip;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Biểu lộ gi&aacute; trị: N&acirc;ng cao h&igrave;nh ảnh bản th&acirc;n &ndash; giống người sử dụng sản phẩm trong quảng c&aacute;o&hellip;</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp;<strong> &nbsp;5.Gia đ&igrave;nh</strong></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Kh&aacute;i niệm:</em></u> Bao gồm một nh&oacute;m c&aacute;c th&agrave;nh vi&ecirc;n c&oacute; mối li&ecirc;n hệ nhất định được x&atilde; hội v&agrave; ph&aacute;p luật thừa nhạn, c&ugrave;ng chung sống một c&aacute;ch l&acirc;u d&agrave;i v&agrave; ổn định; c&oacute; hoạt động kinh tế chung bằng c&aacute;ch khai th&aacute;c t&agrave;i sản sở hữu chung.</span></span></span></span></li>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chức năng hộ gia đ&igrave;nh</span></span></span></span></em></u></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; T&aacute;i sản xuất sức lao động</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Nu&ocirc;i dưỡng v&agrave; gi&aacute;o dục</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Kinh tế</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Chăm s&oacute;c người cao tuổi</span></span></span></span></li>\r\n</ul>', '1551754850', null, '0', '59', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('317', 'Câu 17(nhóm 1)', '<p><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&aacute;i niệm &ldquo;Văn h&oacute;a&rdquo;; Nh&aacute;nh văn h&oacute;a; Nh&oacute;m tham khảo; tầng lớp x&atilde; hội; Gia đ&igrave;nh</span></span></p>', '1551754868', null, '316', '59', '1', '316', '316', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('318', 'Câu 18 (nhóm 1) và câu 14 (Nhóm 2)', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&acirc;n t&iacute;ch đặc trưng của văn h&oacute;a ảnh hưởng đến h&agrave;nh vi mua của người ti&ecirc;u d&ugrave;ng</span></span></p>', '1551754890', null, '316', '59', '1', '316', '316', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('319', 'Câu 19 (Nhóm 1)', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u cơ sở ph&acirc;n loại văn h&oacute;a?</span></span></p>', '1551754917', null, '316', '59', '1', '316', '316', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('320', 'Câu 15 (Nhóm 2)', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&acirc;n t&iacute;ch nội dung c&aacute;c loại nh&aacute;nh văn h&oacute;a?</span></span></p>', '1551754938', null, '316', '59', '1', '316', '316', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('321', 'Câu 22 (nhóm 1) và câu 16 (nhóm 2)', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u v&agrave; Ph&acirc;n t&iacute;ch đặc điểm của giai tầng x&atilde; hội?</span></span></p>', '1551754968', null, '316', '59', '1', '316', '316', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('322', 'Câu 20( nhóm 1) và câu 17(nhóm 2)', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&acirc;n loại sự ảnh hưởng của nh&oacute;m tham khảo.Ph&acirc;n t&iacute;ch h&igrave;nh thức t&aacute;c động của nh&oacute;m tham khảo (th&ocirc;ng tin, thực tế, biểu lộ gi&aacute; trị) đến h&agrave;nh vi mua của người ti&ecirc;u d&ugrave;ng.</span></span></p>', '1551754990', null, '316', '59', '1', '316', '316', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('323', 'Chương 6: NTD với vai trò người ra quyết định', '<p style=\"text-align:center\"><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chương 6: NTD với vai tr&ograve; người ra quyết định</span></span></span></span></strong></p>\r\n\r\n<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1. Ra quyết định của người ti&ecirc;u d&ugrave;ng</span></span></span></span></strong></p>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c quan điểm: </span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Theo kiểu l&iacute; t&iacute;nh: Nhận dạng được sản phẩm tốt nhất </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Theo kiểu thụ động: Ho&agrave;n to&agrave;n tu&acirc;n thủ c&aacute;c lợi &iacute;ch của bản th&acirc;n</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Theo kiểu nhận thức: Chủ động t&igrave;m kiếm th&ocirc;ng tin về sản phẩm, cửa h&agrave;ng</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Theo kiểu cảm t&iacute;nh: Dựa v&agrave;o t&acirc;m trạng v&agrave; cảm gi&aacute;c</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c giai đoạn của m&ocirc; h&igrave;nh đơn giản </span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Đầu v&agrave;o - ảnh hưởng của b&ecirc;n ngo&agrave;i</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul style=\"list-style-type:circle\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;+ Nỗ lực marketing của c&aacute;c c&ocirc;ng ty</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; +M&ocirc;i trường văn h&oacute;a x&atilde; hội</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Qu&aacute; tr&igrave;nh &ndash; ra quyết định của NTD</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul style=\"list-style-type:circle\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;+Nhận dạng nhu cầu</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; +T&igrave;m kiếm th&ocirc;ng tin trước mua</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;+&nbsp;Đ&aacute;nh gi&aacute; c&aacute;c phương &aacute;n</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đầu ra &ndash; h&agrave;nh vi sau quyết định mua</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul style=\"list-style-type:circle\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; +Mua </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;+ Đ&aacute;nh gi&aacute; sau khi mua</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c bước ra quyết định mua của NTD</span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;B1: Nhận thức nhu cầu</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;B2: T&igrave;m kiếm th&ocirc;ng tin về</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;B3: Đ&aacute;nh gi&aacute; c&aacute;c phương &aacute;n</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;B4: Lựa chọn giữa c&aacute;c phương &aacute;n thế vị</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;B5: Kh&aacute;m ph&aacute;: đường tắt của tr&iacute; &oacute;c trong đ&aacute;nh gi&aacute;</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">2.Ra quyết định mua của hộ gia đ&igrave;nh</span></span></span></span></strong></p>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đặc điểm của hộ gia đ&igrave;nh:</span></span></span></span></em></u></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Ra quyết định của hộ gia đ&igrave;nh phức tạp</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Nhu cầu v&agrave; chi ti&ecirc;u của hộ gia đ&igrave;nh thay đổi</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Chu k&igrave; sống của gia đ&igrave;nh thay đổi</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c giai đoạn trong chu k&igrave; sống của hộ gia đ&igrave;nh</span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Giai đoạn độc th&acirc;n</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Vợ chồng mới cưới, chưa c&oacute; con</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Tổ ấm đầy đủ 1( con nhỏ nhất dưới 6 tuổi)</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Tổ ấm đầy đủ 2(con nhỏ nhất 6 tuổi trở l&ecirc;n)</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Tổ ấm đầy đủ 3: ( đ&ocirc;i vợ chồng gi&agrave; v&agrave; c&aacute;c con đ&atilde; tự lập)</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Tổ ấm trống vắng 1( đ&ocirc;i vợ chồng gi&agrave; kh&ocirc;ng c&ograve;n sống chung với con c&aacute;i, l&agrave; lực lương lao động ch&iacute;nh)</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Tổ ấm trống vắng&nbsp; 2( v&agrave; chồng gi&agrave;, lao động ch&iacute;nh đ&atilde; nghie hưu)</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; Người gi&agrave; độc th&acirc;n</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">3. C&aacute;c t&igrave;nh thế mua h&agrave;ng</span></span></span></span></strong></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Mua tại của h&agrave;ng</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Phi mua sắm( ko mua tại cửa h&agrave;ng)</span></span></span></span></li>\r\n</ul>', '1551756005', null, '0', '59', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('324', 'Câu 23 (nhóm 1) và câu 18 (nhóm 2)', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u c&aacute;c &ldquo; Quan điểm ra quyết định mua của người ti&ecirc;u d&ugrave;ng&rdquo;. C&aacute;c giai đoạn của m&ocirc; h&igrave;nh đơn giản ra quyết định h&agrave;nh vi mua kh&aacute;ch h&agrave;ng.Ph&acirc;n t&iacute;ch &ldquo;c&aacute;c loại h&igrave;nh ra quyết định mua&rdquo; của kh&aacute;ch h&agrave;ng người ti&ecirc;u d&ugrave;ng ( đ&aacute;p ứng theo th&oacute;i quen, giải quyết vấn đề c&oacute; giới hạn v&agrave; giải quyết vấn đề mở rộng).</span></span></p>', '1551756042', null, '323', '59', '1', '323', '323', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('325', 'Câu 21 (nhóm 1) và câu 20 (nhóm 2)', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u chức năng của gia đ&igrave;nh. Ph&acirc;n t&iacute;ch &ldquo;c&aacute;c vai tr&ograve; trong trung t&acirc;m mua gia đ&igrave;nh&rdquo;.</span></span></p>', '1551756067', null, '323', '59', '1', '323', '323', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('326', 'Câu 24(nhóm 1)', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u đặc điểm của hộ gia đ&igrave;nh. C&aacute;c giai đoạn sống trong chu kid sống của hộ gia đ&igrave;nh.</span></span></p>', '1551756090', null, '323', '59', '1', '323', '323', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('327', 'Câu 21(nhóm 2)', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&acirc;n t&iacute;ch xung đột trong quyết định mua của hộ gia đ&igrave;nh.</span></span></p>', '1551756137', null, '323', '59', '1', '323', '323', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('328', 'Câu 19 (nhóm 2)', '<p><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&acirc;n t&iacute;ch c&aacute;c bước ra quyết định mua của người ti&ecirc;u d&ugrave;ng? </span></span></p>', '1551756436', null, '323', '59', '1', '323', '323', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('329', 'Chương 7, 8: Về hành vi mua của khách hàng tổ chức', '<p style=\"text-align:center\"><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chương 7: Kh&aacute;i qu&aacute;t h&agrave;nh vi mua của kh&aacute;ch h&agrave;ng tổ chức</span></span></span></span></strong></p>\r\n\r\n<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1.Đặc điểm của thị trường tổ chức</span></span></span></span></strong></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Loại h&igrave;nh đa dạng</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Mua h&agrave;ng h&oacute;a với nhiều mục đ&iacute;ch</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Nhu cầu ph&aacute;i sinh</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Doanh số rất cao</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&oacute; sự kh&aacute;c biệt với thị trường NTD</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">2.Quy tr&igrave;nh quyết định mua của tổ chức</span></span></span></span></strong></p>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c bước trong tiến tr&igrave;nh mua </span></span></span></span></em></u></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Bước 1: &yacute; thức nhu cầu: </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Bước 2: M&ocirc; tả kh&aacute;i qu&aacute;t nhu cầu </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Bước 3: x&aacute;c định quy c&aacute;ch sản phẩm </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Bước 4: T&igrave;m kiếm nh&agrave; cung cấp </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Bước 5: y&ecirc;u cầu ch&agrave;o h&agrave;ng </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp;&nbsp;Bước 6: lựa chọn nh&agrave; cung cấp </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Bước 7: l&agrave;m thủ tục đặt h&agrave;ng </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Bước 8: đ&aacute;nh gi&aacute; kết quả thực hiện </span></span></span></span></li>\r\n</ul>\r\n\r\n<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">3.Kh&aacute;i niệm v&agrave; loại h&igrave;nh h&agrave;nh vi mua của tổ chức</span></span></span></span></strong></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Kh&aacute;i niệm</em></u>: H&agrave;nh vi mua của kh&aacute;ch h&agrave;ng tổ chức l&agrave; c&aacute;c c&aacute;ch ứng xử/ phản ứng của kh&aacute;ch h&agrave;ng tổ chức trong qu&aacute; tr&igrave;nh c&aacute;c tổ chức h&igrave;nh th&agrave;nh nhu cầu mua sắm sản phẩm v&agrave; dịch vụ, nhận biết, đ&aacute;nh gi&aacute; v&agrave; lựa chọn mua trong số những nh&atilde;n hiệu v&agrave; nh&agrave; cung cấp thế vị đang ch&agrave;o h&agrave;ng tr&ecirc;n thị trường.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Loại h&igrave;nh</em></u>: T&aacute;i chọn mua trực tiếp, t&aacute;i chọn mua c&oacute; điều chỉnh, mua phục vụ nhiệm vụ mới.</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">4. Đặc điểm của kh&aacute;ch h&agrave;ng tổ chức</span></span></span></span></strong></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&oacute; số lượng kh&aacute;ch h&agrave;ng &iacute;t hơn nhưng qui m&ocirc; kh&aacute;ch h&agrave;ng lớn hơn.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tập trung hơn về địa l&yacute;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&oacute; quan hệ chặt chẽ giữa người cung ứng v&agrave; kh&aacute;ch h&agrave;ng.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Nhu cầu ph&aacute;i sinh, kh&ocirc;ng co d&atilde;n, biến động mạnh.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Người đi mua h&agrave;ng c&oacute; t&iacute;nh chuyen nghiệp hơn so với người ti&ecirc;u d&ugrave;ng.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&oacute; nhiều người ảnh hưởng đến việc mua h&agrave;ng.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Quyết đinh mua h&agrave;ng thường phức tạp, qu&aacute; tr&igrave;nh quyết định l&acirc;u hơn.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">....</span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:center\"><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chương 8: Cấu tr&uacute;c h&agrave;nh vi của kh&aacute;ch h&agrave;ng tổ chức</span></span></span></span></strong></p>\r\n\r\n<ol>\r\n	<li><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">M&ocirc; h&igrave;nh h&agrave;nh vi mua của kh&aacute;ch h&agrave;ng tổ chức</span></span></span></span></strong></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px\"><img src=\"/public/images/course/59/lesson/1552121879_image-20190309160151-1.jpeg\" style=\"height:142px; width:356px\" /></p>\r\n\r\n<ol start=\"2\">\r\n	<li><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c nh&acirc;n tố ảnh hưởng đến h&agrave;nh vi mua của tổ chức</span></span></span></span></strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c nh&acirc;n tố m&ocirc;i trường</span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Địa l&yacute;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Kinh tế </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; C&ocirc;ng nghệ</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Ch&iacute;nh trị-Ph&aacute;p luật</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Văn h&oacute;a</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Cạnh tranh</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp;C&aacute;c nh&acirc;n tố tổ chức</span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;mục ti&ecirc;u, nhiệm vụ v&agrave; định hướng ph&aacute;t triển</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Ch&iacute;nh s&aacute;ch v&agrave; thủ tục</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Cấu tr&uacute;c tổ chức v&agrave; văn h&oacute;a </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Vai tr&ograve; x&atilde; hội </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;C&ocirc;ng nghệ</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Khen thưởng</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">- C&aacute;c nh&acirc;n tố trung t&acirc;m mua </span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Qui m&ocirc;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Chuẩn mực</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp;Th&agrave;nh phần - quyền lực</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">- C&aacute;c nh&acirc;n tố người tham gia</span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Tuổi v&agrave; thu nhập</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Kiến thức cơ sở </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Địa vị</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Nh&acirc;n c&aacute;ch</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Ch&iacute;nh trị</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Đạo đức</span></span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"3\">\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c bậc h&agrave;nh vi mua kh&aacute;ch h&agrave;ng của tổ chức</span></span></span></span></em></u></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Bậc c&aacute; nh&acirc;n</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Bậc nh&oacute;m</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Bậc tổ chức</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n</ul>', '1551756978', null, '0', '59', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('330', 'Câu 22 (nhóm 2)', '<p><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&acirc;n t&iacute;ch đặc điểm của thị trường tổ chức?</span></span></p>', '1551757125', null, '329', '59', '1', '329', '329', '1', '', '0', null, null, null, null, null, '0');
