INSERT INTO `lesson` VALUES ('332', 'bài tập thử nghiệm ngày  05-03', '', '1551764104', null, '331', '33', '1', '331', '331', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('333', 'Tìm cực trị tự do', '<p><span style=\"font-size:20px;\"><strong>T&igrave;m cực trị tự do của h&agrave;m hai biến </strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Hướng dẫn giải</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Bước 1 X&aacute;c định điểm dừng M(x<sub>0</sub>, y<sub>0</sub>) thỏa m&atilde;n <span class=\"math-tex\">\\(\\left\\{ \\begin{array}{l} z{\'_x}\\left( {{x_0};{y_0}} \\right) = 0\\\\ z{\'_y}\\left( {{x_0};{y_0}} \\right) = 0 \\end{array} \\right.\\)</span></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Bước 2 T&iacute;nh <span class=\"math-tex\">\\(\\begin{array}{l} A = z\'{\'_{x{\\rm{x}}}}\\left( {{x_0};{y_0}} \\right)\\\\ B = z\'{\'_{xy}}\\left( {{x_0};{y_0}} \\right)\\\\ C = z\'{\'_{yy}}\\left( {{x_0};{y_0}} \\right) \\end{array}\\)</span></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">D=B<sup>2</sup>-AC</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Bước 3 Kết luận</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">&nbsp; D&gt;0 tại M (x<sub>0</sub>, y<sub>0</sub>) h&agrave;m số kh&ocirc;ng c&oacute; cực trị</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">&nbsp; D&lt; 0 th&igrave; tại M (x<sub>0</sub>, y<sub>0</sub>) h&agrave;m số c&oacute; cực trị</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Cụ thể</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">+với A&gt;0 h&agrave;m số đạt cực tiểu tại M (x<sub>0</sub>, y<sub>0</sub>), z<sub>ct</sub>=f (x<sub>0</sub>, y<sub>0</sub>)</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">+ Với A&lt;0 h&agrave;m số đạt cực đại tại M (x<sub>0</sub>, y<sub>0</sub>), z<sub>cđ</sub>=f (x<sub>0</sub>, y<sub>0</sub>)</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Ch&uacute; &yacute; D=0 th&igrave; ch&uacute;ng ta phải l&agrave;m theo phương ph&aacute;p kh&aacute;c, thường l&agrave; sử dụng tổng b&igrave;nh phương b&eacute; nhất, tức l&agrave; gh&eacute;p th&agrave;nh tổng c&aacute;c b&igrave;nh phương</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">VD7.4 T&igrave;m cực trị của h&agrave;m hai biến sau<span class=\"math-tex\">\\(f\\left( {x;y} \\right) = {x^2} + 2{\\rm{x}}y + 2{y^2} + x - y + 1\\)</span></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Bước 1 X&aacute;c định điểm dừng M(x<sub>0</sub>, y<sub>0</sub>) thỏa m&atilde;n<span class=\"math-tex\">\\(\\left\\{ \\begin{array}{l} f{\'_x}\\left( {{x_0};{y_0}} \\right) = 0\\\\ f{\'_y}\\left( {{x_0};{y_0}} \\right) = 0 \\end{array} \\right. \\Leftrightarrow \\left\\{ \\begin{array}{l} 2{{\\rm{x}}_o} + 2{y_0} + 1 = 0\\\\ 2{{\\rm{x}}_0} + 4{y_0} - 1 = 0 \\end{array} \\right. \\Leftrightarrow \\left\\{ \\begin{array}{l} {x_0} =  - \\frac{3}{2}\\\\ {y_0} = 1 \\end{array} \\right.\\)</span></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Bước 2 T&iacute;nh</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px;\"><span class=\"math-tex\">\\(\\begin{array}{l} A = f\'{\'_{x{\\rm{x}}}}\\left( {{x_0};{y_0}} \\right) = 2 &gt; 0\\\\ B = f\'{\'_{xy}}\\left( {{x_0};{y_0}} \\right) = 2\\\\ C = f\'{\'_{yy}}\\left( {{x_0};{y_0}} \\right) = 4 \\end{array}\\)</span></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">D=B<sup>2</sup>-AC= -4&lt;0&nbsp; suy ra <span class=\"math-tex\">\\(M\\left( { - \\frac{3}{2};1} \\right)\\)</span> l&agrave; điểm cực trị m&agrave;<span class=\"math-tex\">\\( A = f\'{\'_{x{\\rm{x}}}}\\left( {{x_0};{y_0}} \\right) = 2 &gt; 0\\)</span>n&ecirc;n&nbsp; n&oacute; l&agrave; điểm cực tiểu</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Vậy h&agrave;m số đ&atilde; cho đạt cực tiểu l&agrave; &nbsp;<span class=\"math-tex\">\\({f_{ct}} = f\\left( { - \\frac{3}{2};1} \\right) = \\frac{{ - 1}}{4}\\)</span> tại <span class=\"math-tex\">\\(M\\left( { - \\frac{3}{2};1} \\right)\\)</span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\">&nbsp;</p>', '1551765469', null, '166', '51', '1', '166', '166', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('334', 'Tìm cực trị có điều kiện', '<p><span style=\"font-size:20px;\"><strong>T&igrave;m cực trị c&oacute; điều kiện của h&agrave;m hai biến </strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Hướng dẫn giải</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#2980b9;\"><span style=\"font-size:20px;\">Sử dụng phương ph&aacute;p thế</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px;\">Nếu h&agrave;m g(x)=0 đơn giản, việc r&uacute;t một biến theo biến c&ograve;n lại đơn giản v&agrave; h&agrave;m một biến nhận được sau c&ugrave;ng kh&ocirc;ng phức tạp th&igrave; ta sử dụng phương ph&aacute;p thế</span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#3498db;\">Bước 1</span>: từ điều kiện cho trước ta r&uacute;t 1 ẩn theo ẩn c&ograve;n lại</span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#3498db;\">Bước 2</span>: Thế v&agrave;o h&agrave;m hai biến đ&atilde; cho để n&oacute; quay về h&agrave;m một biến,</span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#3498db;\">Bước 3</span>: t&igrave;m cực trị như đ&atilde; học ở THPT</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#2980b9;\"><span style=\"font-size:20px;\">Sử dụng phương ph&aacute;p nh&acirc;n tử Largrange</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#e74c3c;\">Bước 1</span> Lập h&agrave;m Lagrange c&oacute; dạng <span class=\"math-tex\">\\(L\\left( {x;y;\\lambda } \\right) = z\\left( {x;y} \\right) + \\lambda g\\left( {x;y} \\right)\\)</span></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">trong đ&oacute; &nbsp;? được gọi l&agrave; nh&acirc;n tử Lagrange</span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#e74c3c;\">Bước 2</span>: T&igrave;m điểm dừng: x&aacute;c định bộ số (x<sub>0</sub>, y<sub>0</sub>, ?<sub>0</sub>) thỏa m&atilde;n đồng thời</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">L<sub>x</sub>&rsquo; (x<sub>0</sub>, y<sub>0</sub>, ?<sub>0</sub>) =0 ;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; L<sub>y</sub>&rsquo;(x<sub>0</sub>,y<sub>0</sub>, ?<sub>0</sub>)=0;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;L<sub>?</sub>&rsquo;(x<sub>0</sub>,y<sub>0</sub>, ?<sub>0</sub>)=0</span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#e74c3c;\">Bước 3</span>: T&iacute;nh định thức Hessian tại điểm dừng<span class=\"math-tex\">\\(\\left| H \\right| = \\left| {\\begin{array}{*{20}{c}} 0&amp;{g{\'_x}}&amp;{g{\'_y}}\\\\ {g{\'_x}}&amp;{L\'{\'_{x{\\rm{x}}}}}&amp;{L\'{\'_{xy}}}\\\\ {g{\'_y}}&amp;{{L_{xy}}}&amp;{L\'{\'_{yy}}} \\end{array}} \\right|\\)</span></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">|H|&gt;0 th&igrave; h&agrave;m số đạt cực đại c&oacute; điều kiện tại M (x<sub>0</sub>, y<sub>0</sub>), z<sub>cđ</sub>=f (x<sub>0</sub>, y<sub>0</sub>)</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">|H|&lt;0 th&igrave; h&agrave;m số đạt cực tiểu c&oacute; điều kiện tại M (x<sub>0</sub>, y<sub>0</sub>), z<sub>ct</sub>=f (x<sub>0</sub>, y<sub>0</sub>)</span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><em>VD7.5 T&igrave;m cực trị c&oacute; điều kiện của h&agrave;m hai biến sau</em><span class=\"math-tex\">\\(z = f\\left( {x;y} \\right) = 6 - 4{\\rm{x}} - 3y\\)</span><em> với </em><span class=\"math-tex\">\\({x^2} + {y^2} = 1\\)</span><em> </em></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Ta c&oacute; <span class=\"math-tex\">\\(g\\left( {x;y} \\right) = {x^2} + {y^2} - 1 = 0\\)</span></span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#ffffff;\"><span style=\"background-color:#3498db;\">Bước 1</span></span> Lập h&agrave;m Lagrange <span class=\"math-tex\">\\(L = 6 - 4{\\rm{x}} - 3y + \\lambda \\left( {{x^2} + {y^2} - 1} \\right)\\)</span></span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#ffffff;\"><span style=\"background-color:#3498db;\">Bước 2</span></span>: T&igrave;m điểm dừng</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Điểm dừng của h&agrave;m Lagrange <span class=\"math-tex\">\\(\\left\\{ \\begin{array}{l} L{\'_x} = 2\\lambda x - 4 = 0\\\\ L{\'_y} = 2\\lambda x - 3 = 0\\\\ L{\'_\\lambda } = {x^2} + {y^2} - 1 = 0 \\end{array} \\right. \\Leftrightarrow \\left\\{ \\begin{array}{l} x = \\frac{2}{\\lambda }\\\\ y =  = \\frac{3}{{2\\lambda }}\\\\ {x^2} + {y^2} - 1 = 0 \\end{array} \\right. \\Leftrightarrow \\left\\{ \\begin{array}{l} \\lambda  = \\frac{5}{2}\\\\ x = \\frac{4}{5}\\\\ y = \\frac{3}{5} \\end{array} \\right.;\\left\\{ \\begin{array}{l} \\lambda  =  - \\frac{5}{2}\\\\ x =  - \\frac{4}{5}\\\\ y =  - \\frac{3}{5} \\end{array} \\right.\\)</span> </span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Vậy c&oacute; 2 điểm dừng l&agrave; <span class=\"math-tex\">\\(\\begin{array}{l} M\\left( {\\frac{4}{5};\\frac{3}{5}} \\right);{\\lambda _1} = \\frac{5}{2}\\\\ N\\left( { - \\frac{4}{5}; - \\frac{3}{5}} \\right);{\\lambda _2} =  - \\frac{5}{2} \\end{array}\\)</span></span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#ffffff;\"><span style=\"background-color:#3498db;\">Bước 3</span></span>: T&iacute;nh định thức Hessian tại điểm dừng<span class=\"math-tex\">\\(\\begin{array}{l} L\'{\'_{x{\\rm{x}}}} = 2\\lambda ;L\'{\'_{yy}} = 2\\lambda ;L\'{\'_{xy}} = 0\\\\ g{\'_x} = 2{\\rm{x}};g{\'_y} = 2y \\end{array}\\)</span></span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span class=\"math-tex\">\\(|H| = \\left| {\\begin{array}{*{20}{c}} 0&amp;{2{\\rm{x}}}&amp;{2y}\\\\ {2{\\rm{x}}}&amp;{2\\lambda }&amp;0\\\\ {2y}&amp;0&amp;{2\\lambda } \\end{array}} \\right| =  - 8\\lambda \\left( {{x^2} + {y^2}} \\right) =  - 8\\lambda \\)</span></span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span class=\"math-tex\">\\(M\\left( {\\frac{4}{5};\\frac{3}{5}} \\right);{\\lambda _1} = \\frac{5}{2} \\Rightarrow |H| &lt; 0\\)</span>n&ecirc;n<span class=\"math-tex\">\\( M\\left( {\\frac{4}{5};\\frac{3}{5}} \\right)\\)</span>l&agrave; điểm cực tiểu v&agrave; <span class=\"math-tex\">\\({f_{ct}} = f\\left( {\\frac{4}{5};\\frac{3}{5}} \\right) = 1\\)</span></span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span class=\"math-tex\">\\(N\\left( { - \\frac{4}{5}; - \\frac{3}{5}} \\right);{\\lambda _2} =  - \\frac{5}{2} \\Rightarrow |H| &gt; 0\\)</span>n&ecirc;n<span class=\"math-tex\">\\( N\\left( { - \\frac{4}{5}; - \\frac{3}{5}} \\right)\\)</span>l&agrave; điểm cực đại v&agrave; <span class=\"math-tex\">\\({f_{cd}} = f\\left( { - \\frac{4}{5}; - \\frac{3}{5}} \\right) = 11\\)</span></span></p>', '1551765548', null, '166', '51', '1', '166', '166', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('335', 'Phương trình vi phân cấp 1 biến số phân li', '<p><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:red\">Dạng 1:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:red\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:red\">&prime; = </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:red\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:red\"> (</span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:red\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:red\">). </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:red\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:red\">( </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:red\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:red\"> )(2.1)</span></span></span><br />\r\n<br />\r\n<span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\"><strong>phương ph&aacute;p &nbsp;giải</strong><br />\r\nNếu </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\">( </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\"> )&ne; 0 th&igrave; ta c&oacute; </span></span></span><br />\r\n<br />\r\n<span style=\"font-size:11.0pt\"><span calibri=\"\" style=\"font-family:\"><img src=\"data:image/png;base64,\" style=\"height:55px; width:137px\" /></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\">Suy ra t&iacute;ch ph&acirc;n tổng qu&aacute;t :<br />\r\n&nbsp;</span></span></span><br />\r\n<br />\r\n<span style=\"font-size:11.0pt\"><span calibri=\"\" style=\"font-family:\"><img src=\"data:image/png;base64,\" style=\"height:55px; width:218px\" /></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\">Nếu </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\">(</span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\">) = 0 c&oacute; nghiệm </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\"> = </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\"> th&igrave; </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\"> = </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\"> l&agrave; nghiệm của phương tr&igrave;nh</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><b><span style=\"font-size:16.0pt\"><span style=\"color:black\">V&iacute; dụ</span></span></b><span style=\"font-size:16.0pt\"><span style=\"color:black\"> : Giải phương tr&igrave;nh</span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(y\' = x\\left( {1 + {y^2}} \\right)\\)</span></span></span></span><br />\r\n<span style=\"font-size:16.0pt\"><span style=\"color:black\">Giải:<br />\r\nChia 2 vế cho </span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(1 + {y^2}\\)</span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> &nbsp;ta c&oacute;</span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(\\frac{{dy}}{{1 + {y^2}}} = x{\\rm{dx}}\\)</span></span></span></span> </span></span></p>\r\n\r\n<p><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span calibri=\"\" style=\"font-family:\">Suy ra </span></span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(\\int {\\frac{{dy}}{{1 + {y^2}}}}  = \\int {x{\\rm{dx}}}  + C \\Rightarrow \\arctan y = \\frac{{{x^2}}}{2} + C{\\rm{x}} + D\\)</span></span></span></span></span></p>\r\n\r\n<p><br />\r\n<br />\r\n<br />\r\n<br />\r\n<br />\r\n<span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:red\">. Dạng 2 :&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:red\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:red\">(</span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:red\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:red\">) </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:red\">??</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:red\"> + </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:red\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:red\">(</span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:red\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:red\">) </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:red\">??</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:red\"> = 0 (2.2)</span></span></span><br />\r\n<span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\"><strong>phương ph&aacute;p &nbsp;giải</strong><br />\r\nTa c&oacute; t&iacute;ch ph&acirc;n tổng qu&aacute;t :</span></span></span><br />\r\n<span style=\"font-size:11.0pt\"><span calibri=\"\" style=\"font-family:\"><img src=\"data:image/png;base64,\" style=\"height:51px; width:271px\" /></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><b><span style=\"font-size:16.0pt\"><span style=\"color:black\">V&iacute; dụ</span></span></b><span style=\"font-size:16.0pt\"><span style=\"color:black\"> : Giải phương tr&igrave;nh </span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\">\\[\\cos {\\rm{y}}{\\rm{.y&#39; = x}}\\]</span></span></span><br />\r\n<span style=\"font-size:16.0pt\"><span style=\"color:black\">Giải. </span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\">\\[\\cos {\\rm{yd}}y = x{\\rm{dx}}\\]</span></span></span><br />\r\n<span style=\"font-size:16.0pt\"><span style=\"color:black\">Ta c&oacute; t&iacute;ch ph&acirc;n tổng qu&aacute;t :</span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(\\)</span></span></span></span> <span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\">\\[\\int {\\cos {\\rm{yd}}y}&nbsp; = \\int {x{\\rm{dx}}}&nbsp; + C \\Rightarrow \\sin y = \\frac{{{x^2}}}{2} + C{\\rm{x}} + D\\]</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"color:red\">Dạng 3 :<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></span><span style=\"font-size:11.0pt\"><span calibri=\"\" style=\"font-family:\"><img src=\"data:image/png;base64,\" style=\"height:27px; width:302px\" /></span></span><span style=\"font-size:16.0pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"color:red\">(2.3)</span></span></span><br />\r\n<strong><span style=\"font-size:16.0pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"color:black\">phương ph&aacute;p &nbsp;giải</span></span></span></strong><br />\r\n<span style=\"font-size:16.0pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"color:black\">Nếu </span></span></span><span style=\"font-size:11.0pt\"><span calibri=\"\" style=\"font-family:\"><img src=\"data:image/png;base64,\" style=\"height:27px; width:89px\" /></span></span><span style=\"font-size:16.0pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"color:black\">&ne; 0 chia 2 vế cho </span></span></span><span style=\"font-size:11.0pt\"><span calibri=\"\" style=\"font-family:\"><img src=\"data:image/png;base64,\" style=\"height:27px; width:89px\" /></span></span><span style=\"font-size:16.0pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"color:black\">&ne; 0 th&igrave; ta c&oacute; :</span></span></span><br />\r\n<span style=\"font-size:11.0pt\"><span calibri=\"\" style=\"font-family:\"><img src=\"data:image/png;base64,\" style=\"height:55px; width:239px\" /></span></span></span></span><br />\r\n<br />\r\n<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"color:black\">Suy ra t&iacute;ch ph&acirc;n tổng qu&aacute;t :</span></span></span></span></span><br />\r\n<span style=\"font-size:11.0pt\"><span calibri=\"\" style=\"font-family:\"><img src=\"data:image/png;base64,\" style=\"height:56px; width:264px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\">Nếu từ</span></span></span><span style=\"font-size:11.0pt\"><span calibri=\"\" style=\"font-family:\"><img src=\"data:image/png;base64,\" style=\"height:27px; width:86px\" /></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\">&nbsp;ta c&oacute; nghiệm </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\"> = </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\"> th&igrave; đ&acirc;y l&agrave; nghiệm ri&ecirc;ng của phương tr&igrave;nh<br />\r\nNếu từ </span></span></span><span style=\"font-size:11.0pt\"><span calibri=\"\" style=\"font-family:\"><img src=\"data:image/png;base64,\" style=\"height:27px; width:83px\" /></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\">&nbsp;ta c&oacute; nghiệm </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\"> = </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\"> th&igrave; đ&acirc;y l&agrave; nghiệm ri&ecirc;ng của phương tr&igrave;nh</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><b><span style=\"font-size:16.0pt\"><span style=\"color:black\">V&iacute; dụ</span></span></b><span style=\"font-size:16.0pt\"><span style=\"color:black\"> : Giải phương tr&igrave;nh vi ph&acirc;n </span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(x(1 + {y^2})d{\\rm{x}} + y\\left( {1 + {x^2}} \\right)dy = 0\\)</span></span></span></span><br />\r\n<span style=\"font-size:16.0pt\"><span style=\"color:black\">Giải<br />\r\nChia 2 vế cho</span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(\\left( {1 + {x^2}} \\right)\\left( {1 + {y^2}} \\right)\\)</span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> phương tr&igrave;nh đ&atilde; cho tương đương với :</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(\\frac{x}{{1 + {x^2}}}d{\\rm{x}} + \\frac{y}{{1 + {y^2}}}dy = 0\\)</span></span></span></span> </span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-size:16.0pt\"><span style=\"color:black\">T&iacute;ch ph&acirc;n tổng qu&aacute;t </span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(\\int {\\frac{x}{{1 + {x^2}}}d{\\rm{x}}}  + \\int {\\frac{y}{{1 + {y^2}}}dy}  = C \\Rightarrow \\frac{1}{2}\\ln (1 + {x^2}) + \\frac{1}{2}\\ln (1 + {y^2}) = C\\)</span></span></span></span></span></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:red\">Dạng 4 :<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:red\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:red\">&prime; = </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:red\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:red\">(</span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:red\">??</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:red\"> + </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:red\">??</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:red\"> + </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:red\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:red\">) (2.4)</span></span></span><br />\r\n<br />\r\n<span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\"><strong>Phương ph&aacute;p &nbsp;giải</strong><br />\r\nNếu </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\"> = 0 hoặc </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\"> = 0 ta c&oacute; dạng (2.1)<br />\r\nNếu </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">??</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\"> &ne; 0, đặt </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\"> = </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">??</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\"> + </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">??</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\"> + </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\"> ta c&oacute; :<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\">&prime; = </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">??</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\">(</span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\">) + </span></span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><br />\r\n<br />\r\n<span style=\"font-size:16.0pt\"><span calibri=\"\" style=\"font-family:\"><span style=\"color:black\">Đ&acirc;y l&agrave; dạng (2.1) v&agrave; giải được</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><b><span style=\"font-size:16.0pt\"><span style=\"color:black\">V&iacute; dụ</span></span></b><span style=\"font-size:16.0pt\"><span style=\"color:black\"> : Giải phương tr&igrave;nh&nbsp;&nbsp;&nbsp;&nbsp; y&rsquo;=x-y+5<br />\r\nGiải<br />\r\nĐặt </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> = x-y+5</span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">&rArr;</span></span></span></span></span><span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(z&#39; = 1 - y&#39; = 1 - z \\Rightarrow \\frac{{dz}}{{1 - z}} = d{\\rm{x}} \\Rightarrow \\ln \\left| {1 - z} \\right| = x + C\\)</span></span></span></span></span><br />\r\n<span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\( \\Rightarrow 1 - z = D.{e^x} \\Rightarrow z = 1 - D.{e^x} \\Rightarrow y = D.{e^x} + x + 4\\)</span></span></span></span></span> &nbsp;</p>\r\n\r\n<p>&nbsp;</p>', '1551768231', null, '169', '51', '1', '169', '169', '1', '', '0', null, null, null, null, null, '0');
