INSERT INTO `lesson` VALUES ('342', 'Bài tập số 02', '', '1551775360', null, '88', '37', '1', '88', '88', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('345', 'Câu 4( Nhóm 2)', '<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"line-height:107%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tr&igrave;nh b&agrave;y nội dung giai đoạn cảm nhận sau mua v&agrave; cho biết c&aacute;c ứng xử marketing&nbsp; của giai đoạn n&agrave;y? N&ecirc;u kh&aacute;i niệm h&agrave;nh vi ứng xử kh&aacute;ch h&agrave;ng.- người ti&ecirc;u d&ugrave;ng. Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh họa sự ảnh hưởng của một trong c&aacute;c nh&oacute;m nh&acirc;n tố ảnh hưởng đến h&agrave;nh vi ứng xử của kh&aacute;ch h&agrave;ng-người ti&ecirc;u d&ugrave;ng?</span></span></span></span></span></span></p>', '1551792140', null, '293', '58', '1', '293', '293', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('346', 'Chương 5: Chiến lược chung marketing', '<p style=\"margin-left:-6px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:-6px; text-align:center\"><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">CHƯƠNG 5: CHIẾN LƯỢC CHUNG MARKETING</span></span></span></span></strong></p>\r\n\r\n<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1. Một số kh&aacute;i niệm</span></span></span></span></strong></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em><strong>Chiến lược</strong></em></u> được hiểu l&agrave; tổ hợp c&aacute;c mục ti&ecirc;u d&agrave;i hạn v&agrave; c&aacute;c biện ph&aacute;p, c&aacute;c c&aacute;ch thức, con đường đạt đến c&aacute;c mục ti&ecirc;u</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em><strong>Chiến lược marketing </strong></em></u>được hiểu bao gồm c&aacute;c nguy&ecirc;n tắc, nguy&ecirc;n l&yacute; rộng lớn m&agrave; c&aacute;c nh&agrave; quản trị marketing mong muốn nhờ c&oacute; n&oacute; để đạt được mục ti&ecirc;u marketing của m&igrave;nh tr&ecirc;n thị trường mục ti&ecirc;u.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em><strong>Marketing mục ti&ecirc;u</strong></em></u> l&agrave; qu&aacute; tr&igrave;nh ph&acirc;n chia thị trường th&agrave;nh nhiều ph&acirc;n đoạn kh&aacute;c nhau (c&oacute; những đặc điểm v&agrave; đường n&eacute;t th&aacute;i độ mua kh&aacute;c nhau), lựa chọn một hoặc nhiều ph&acirc;n đoạn thị trường v&agrave; x&acirc;y dựng chương tr&igrave;nh marketing tương ứng cho từng ph&acirc;n đoạn đ&oacute;.</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">2. Tư duy marketing v&agrave; loại h&igrave;nh chiến lược marketing</span></span></span></span></strong></p>\r\n\r\n<ul>\r\n	<li><u><em><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp;Tư duy marketing mục ti&ecirc;u </span></span></span></span></strong></em></u></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Ph&acirc;n đoạn thị trường</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Lựa chọn thị trường mục ti&ecirc;u</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Định vị tr&ecirc;n thị trường mục ti&ecirc;u</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><u><strong><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c loại h&igrave;nh chiến lược marketing</span></span></span></span></em></strong></u></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Marketing đại tr&agrave;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Marketing ph&acirc;n biệt</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Marketing tập trung</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><u><em><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c bước của marketing mục ti&ecirc;u</span></span></span></span></strong></em></u></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><img src=\"/public/images/course/58/lesson/1552138987_image-20190309204658-1.png\" style=\"height:379px; width:800px\" /></p>\r\n\r\n<p style=\"margin-left:48px\"><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">B1: Ph&acirc;n đoạn thị trường:</span></span></span></span></em></u></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <em>&nbsp; Kh&aacute;i niệm</em>: L&agrave; &ldquo;qu&aacute; tr&igrave;nh chi tiết&rdquo; của marketing mục ti&ecirc;u nhằm ph&acirc;n định thị trường tổng thể th&agrave;nh những cấu tr&uacute;c nhỏ hơn (đoạn, kh&uacute;c mảng, l&aacute;t cắt) c&oacute; th&ocirc;ng số đặc t&iacute;nh v&agrave; đường n&eacute;t th&aacute;i độ kh&aacute;c biệt nhau, nhưng trong nội bộ một đoạn lại đồng nhất với nhau m&agrave; doanh nghiệp c&oacute; thể vận dụng marketing-mix hữu hiệu.</span></span></span></span></li>\r\n	<li><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Y&ecirc;u cầu:</span></span></span></span></em></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<p><img src=\"/public/images/course/58/lesson/1552138987_image-20190309204659-2.png\" style=\"height:320px; width:700px\" /></p>\r\n\r\n<p><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">B2: Lựa chọn thị trường mục ti&ecirc;u</span></span></span></span></em></u></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Quy m&ocirc; v&agrave; sức tăng trưởng</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Sức hấp dẫn về cấu tr&uacute;c</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Mục ti&ecirc;u v&agrave; nguồn lực của doanh nghiệp</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">B3: Đinh vị tr&ecirc;n thị trường mục ti&ecirc;u</span></span></span></span></em></u></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Dị biệt h&oacute;a h&igrave;nh ảnh</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Dị biết h&oacute;a nh&acirc;n sự</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Dị biệt h&oacute;a dịch vụ</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Dị biệt h&oacute;a sản phẩm</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">3.Nội dung chiến lược marketing</span></span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><u><em><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">X&aacute;c định thị trường mục ti&ecirc;u</span></span></span></span></strong></em></u></li>\r\n	<li><u><em><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Mục ti&ecirc;u chiến lược chung marketing</span></span></span></span></strong></em></u></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> Lợi nhuận </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> Thế lực (địa vị) </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> An to&agrave;n</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> X&atilde; hội (nh&acirc;n bản)</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><u><em><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&aacute;t triển marketing &ndash; mix</span></span></span></span></strong></em></u></li>\r\n	<li><u><em><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Nguồn lực marketing</span></span></span></span></strong></em></u></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><strong>Marketing hỗn hợp</strong></u>:</span></span> <span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Marketing&ndash;mix l&agrave; phối thức định hướng c&aacute;c biến số marketing c&oacute; thể kiểm so&aacute;t được sử dụng một c&aacute;ch li&ecirc;n ho&agrave;n, đồng bộ nhằm theo đuổi một sức b&aacute;n v&agrave; lợi nhuận dự kiến trong một thị trường trọng điểm.</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">4. Quy tr&igrave;nh soạn thảo chiến lược chung marketing</span></span></span></span></strong></p>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:48px; text-align:center\"><img src=\"/public/images/course/58/lesson/1552138987_image-20190309204659-3.png\" style=\"height:267px; width:600px\" /></p>\r\n\r\n<p style=\"margin-left:72px\">&nbsp;</p>', '1551792496', null, '0', '58', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('347', 'Câu 5 (Nhóm 1)', '<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">C&acirc;u 5 (Nh&oacute;m 1):</span></span></strong><span style=\"font-size:13.0pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"> N&ecirc;u kh&aacute;i niệm ph&acirc;n đoạn thị trường? C&aacute;c ti&ecirc;u thức ph&acirc;n đoạn thị trường v&agrave; c&aacute;c y&ecirc;u cầu ph&acirc;n đoạn thị trường? Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh họa một trong c&aacute;c ti&ecirc;u thức ph&acirc;n đoạn thị trường?N&ecirc;u kh&aacute;i niệm v&agrave; cấu tr&uacute;c marketing-mix? Tr&igrave;nh b&agrave;y cơ sở x&aacute;c định marketing-mix của doanh nghiệp?</span></span></span></span></p>', '1551792530', null, '346', '58', '1', '346', '346', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('348', 'Câu 5( Nhóm 2)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 5 (Nh&oacute;m 2</span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> N&ecirc;u kh&aacute;i niệm makt mục ti&ecirc;u? Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh họa nội dung &ldquo; Lựa chọn thị trường mục ti&ecirc;u của doanh nghiệp? N&ecirc;u c&aacute;c bước của marketing mục ti&ecirc;u? Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh họa nội dung &ldquo;Định vị thị trường&rdquo;của doanh nghiệp</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u kh&aacute;i niệm chiến lược chung marketing? Tr&igrave;nh b&agrave;y nội dung cơ bản của một chiến lược chung marketing?Thiết lập quy tr&igrave;nh soạn thảo chiến lược chung marketing?Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh họa một trong c&aacute;c nội dung của quy tr&igrave;nh soạn thảo chiến lược chung marketing?(Ph&acirc;n t&iacute;ch t&igrave;nh thế chiến lược marketing;x&aacute;c định mục ti&ecirc;u marketing)</span></span></span></span></p>', '1551792986', null, '346', '58', '1', '346', '346', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('349', 'Chương 6: Chính sách sản phẩm', '<p style=\"margin-left:72px; text-align:center\"><span style=\"font-size:20px\"><strong><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">CHƯƠNG 6: CH&Iacute;NH S&Aacute;CH SẢN PHẨM</span></span></strong></span></p>\r\n\r\n<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1.Một số vấn đề cơ bản về sản phẩm</span></span></span></span></strong></p>\r\n\r\n<p style=\"margin-left:24px\"><u><em><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1.1 Một số kh&aacute;i niệm cơ bản</span></span></span></span></strong></em></u></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Kh&aacute;i niệm sản phẩm</em></u>: Sản phẩm (theo quan điểm marketing) l&agrave; bất k&igrave; thứ g&igrave; được cung ứng ch&agrave;o h&agrave;ng cho một thị trường để tạo ra sự ch&uacute; &yacute;, đạt tới việc mua v&agrave; ti&ecirc;u d&ugrave;ng nhằm thỏa m&atilde;n một nhu cầu hoặc một mong muốn n&agrave;o đ&oacute;</span></span></span></span></li>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Cấu tr&uacute;c sản phẩm</span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img src=\"/public/images/course/58/lesson/1552141568_image-20190309213000-1.png\" style=\"height:393px; width:800px\" /></p>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Đơn vị sản phẩm (Product Item)</em></u>: L&agrave; một chỉnh thể ri&ecirc;ng biệt được đặc trưng bằng đơn vị độ lớn, gi&aacute; cả, vẻ bề ngo&agrave;i v&agrave; c&aacute;c thuộc t&iacute;nh kh&aacute;c.  l&agrave; 1ĐV hạch to&aacute;n độc lập l&agrave;m cơ sở tạo n&ecirc;n chủng loại SP.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Tuyến (chủng loại) sản phẩm (product line)</em></u>: l&agrave; nh&oacute;m c&aacute;c sản phẩm c&oacute; quan hệ chặt chẽ với nhau, do thực hiện một chức năng tương tự hoặc được b&aacute;n cho c&ugrave;ng một nh&oacute;m người ti&ecirc;u d&ugrave;ng hoặc qua c&ugrave;ng một k&ecirc;nh hoặc c&ugrave;ng khung gi&aacute; nhất định</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Sản phẩm hỗn hợp (product mix): l&agrave; tập hợp tất cả c&aacute;c tuyến sản phẩm v&agrave; c&aacute;c đơn vị sản phẩm m&agrave; một tổ chức hay một người b&aacute;n c&oacute; thể tung ra thị trường để phục vụ kh&aacute;ch h&agrave;ng.</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><em>1.2. Ph&acirc;n loại sản phẩm</em>:</span></span></span></span></strong></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">H&agrave;ng ti&ecirc;u d&ugrave;ng:</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">H&agrave;ng tư liệu sản xuất</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><em><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1.3. Chu k&igrave; sống của sản phẩm:</span></span></span></span></strong></em></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em><strong>Kh&aacute;i niệm</strong>:</em></u> M&ocirc; tả động th&aacute;i của việc ti&ecirc;u thụ một h&agrave;ng ho&aacute; từ thời điểm xuất hiện n&oacute; tr&ecirc;n thị trường tới khi kh&ocirc;ng b&aacute;n được ch&uacute;ng nữa (r&uacute;t khỏi thị trường).</span></span></span></span></li>\r\n	<li><strong><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c giai đoạn của chu k&igrave; sống </span></span></span></span></em></u></strong></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px; text-align:center\"><img src=\"/public/images/course/58/lesson/1552141568_image-20190309213000-2.png\" style=\"height:230px; width:800px\" /></p>\r\n\r\n<p style=\"margin-left:48px; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+ Triển khai</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+Tăng trưởng</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+B&atilde;o h&ograve;a</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+Suy tho&aacute;i</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">2. Nội dung cơ bản của ch&iacute;nh s&aacute;ch sản phẩm</span></span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><u><strong><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Mục ti&ecirc;u của ch&iacute;nh s&aacute;ch sản phẩm</span></span></span></span></em></strong></u></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+ Đảm bảo n&acirc;ng cao khả năng b&aacute;n</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+ Đảm bảo tạo lập được điều kiện sinh lời</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+Đảm bảo uy t&iacute;n, n&acirc;ng cao sức cạnh tranh v&agrave; k&eacute;o d&agrave;i chu kỳ sống sản phẩm</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><u><em><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Vai tr&ograve; của ch&iacute;nh s&aacute;ch sản phẩm</span></span></span></span></strong></em></u></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+Nền tảng, xương sống của chiến lược marketing </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+Gi&uacute;p thực hiện mục ti&ecirc;u của chiến lược marketing</span></span></span></span></p>\r\n\r\n<p><u><em><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">2.1. C&aacute;c ch&iacute;nh s&aacute;ch sản phẩm</span></span></span></span></strong></em></u></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ch&iacute;nh s&aacute;ch chủng loại, cơ cấu, chất lượng sản phẩm</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ch&iacute;nh s&aacute;ch nh&atilde;n hiệu bao g&oacute;i</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ch&iacute;nh s&aacute;ch sản phẩm mới</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ch&iacute;nh s&aacute;ch dịch vụ hỗ trợ sản phẩm</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c giai đoạn ph&aacute;t triển sản phẩm mới:</span></span></span></span></em></u></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">B1: H&igrave;nh th&agrave;nh &yacute; tưởng</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">B2: Lựa chọn &yacute; tưởng</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">B3: Soạn thảo dự &aacute;n v&agrave; thẩm định</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">B4: Soạn thảo chiến lược marketing</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">B5: Ph&acirc;n t&iacute;ch khả năng sản xuất v&agrave; ti&ecirc;u thụ</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">B6: Thiết kế sản phẩm</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">B7: Thử nghiệm trong điều kiện thị trường</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">B8: Triển khai sản xuất đại tr&agrave;</span></span></span></span></p>', '1551794078', null, '0', '58', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('350', 'câu 1', '', '1551794462', null, '155', '54', '1', '155', '155', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('351', 'Câu 6 (nhóm 1)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 6( Nh&oacute;m 1</span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">): N&ecirc;u kh&aacute;i niệm sản phẩm v&agrave; ch&iacute;nh s&aacute;ch sản phẩm? Vẽ sơ đồ cấu tr&uacute;c 3 lớp của sản phẩm? Cho v&iacute; dụ minh họa?N&ecirc;u c&aacute;c ch&iacute;nh s&aacute;ch sản phẩm cơ bản của doanh nghiệp </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u kh&aacute;i niệm chu k&igrave; sống của sản phẩm v&agrave; vẽ sơ đồ chu k&igrave; sống của sản phẩm? Tr&igrave;nh b&agrave;y đặc điểm cơ bản của một trong c&aacute;c giai đoạn của chu k&igrave; sống của sản phẩm?( Triển khai, tăng trưởng, b&atilde;o h&ograve;a)</span></span></span></span></p>', '1551795510', null, '349', '58', '1', '349', '349', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('352', 'Câu 6 (nhóm 2)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u&nbsp; 6(Nh&oacute;m 2</span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">) N&ecirc;u kh&aacute;i niệm sản phẩm mới theo quan điểm marketing? Thiết kế quy tr&igrave;nh ph&aacute;i triển sản phẩm mới? Tr&igrave;nh b&agrave;y nội dung của một trong c&aacute;c bước của quy tr&igrave;nh ph&aacute;t triển sản phẩm mới (H&igrave;nh th&agrave;nh &yacute; tưởng sản phẩm mới; Chuẩn bị thương mại h&oacute;a sản phẩm). N&ecirc;u kh&aacute;i niệm sản phẩm, tuyến sản phẩm: Kh&aacute;i niệm ch&iacute;nh s&aacute;ch sản phẩm? N&ecirc;u nội dung của ch&iacute;nh s&aacute;ch sản phẩm? Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ nội dung &ldquo; Cơ cấu v&agrave; chủng loại sản phẩm;Nh&atilde;n hiệu v&agrave; bao g&oacute;i sản phẩm; Ch&iacute;nh s&aacute;ch dịch vụ hỗ trợ&rdquo;</span></span></span></span></p>', '1551796380', null, '349', '58', '1', '349', '349', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('353', 'NHÓM CÂU HỎI 1', null, '1551796939', null, '0', '60', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('354', 'Chương 7: Chính sách giá', '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">CHƯƠNG 7: CH&Iacute;NH S&Aacute;CH GI&Aacute;</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1.Một số vấn đề cơ bản về gi&aacute; kinh doanh</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1.1 Một s&ocirc; kh&aacute;i niệm</span></span></u></em></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Gi&aacute;:</span></span></u></em><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> Số tiền trả cho h&agrave;ng h&oacute;a hoặc dịch vụ, hay tổng gi&aacute; trị lợi &iacute;ch của việc sử dụng h&agrave;ng h&oacute;a hoặc dịch vụ m&agrave; kh&aacute;ch h&agrave;ng quy đổi được.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ch&iacute;nh s&aacute;ch gi&aacute;</span></span></u></em><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">: L&agrave; sự tập hợp những c&aacute;ch thức v&agrave; quy tắc x&aacute;c định mức gi&aacute; cơ sở của sản phẩm v&agrave; quy định bi&ecirc;n độ giao động cho ph&eacute;p thay đổi mức gi&aacute; cơ sở trong những điều kiện nhất định của hoạt động sản xuất kinh doanh tr&ecirc;n thị trường.</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1.2 Quy tr&igrave;nh định gi&aacute;</span></span></u></em></strong></span></span></p>\r\n\r\n<p style=\"margin-left:74px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:74px; text-align:center\"><img src=\"/public/images/course/58/lesson/1552142730_image-20190309214923-1.png\" style=\"height:299px; width:500px\" /></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">&nbsp;</li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1.3Điều chỉnh gi&aacute;</span></span></u></em></strong></span></span></p>\r\n\r\n<p style=\"margin-left:74px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chủ động cắt giảm hoặc tăng gi&aacute;</span></span></u></em></span></span></p>\r\n\r\n<p style=\"margin-left:74px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp;&nbsp;&nbsp; - Theo kh&aacute;ch h&agrave;ng</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:74px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp;&nbsp;&nbsp; - Theo đối thủ cạnh tranh</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:74px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đối ph&oacute; với thay đổi gi&aacute;</span></span></u></em></span></span></p>\r\n\r\n<p style=\"margin-left:74px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp;&nbsp;&nbsp; - Theo phản ứng của kh&aacute;ch h&agrave;ng</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:74px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp;&nbsp;&nbsp; - Theo phản ứng của đối thủ cạnh tranh</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">2. Ch&iacute;nh s&aacute;ch gi&aacute;</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">2. 1. Ch&iacute;nh s&aacute;ch gi&aacute; cho sản phẩm mới</span></span></u></em></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><u><em><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Định gi&aacute; &ldquo;hớt v&aacute;ng sữa&rdquo;</span></span></span></span></strong></em></u></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Dựa v&agrave;o lượng cầu lớn</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chi ph&iacute; sản xuất kh&ocirc;ng phụ thuộc quy m&ocirc;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hạn chế cạnh tranh</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tạo lập h&igrave;nh ảnh, định vị</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Định gi&aacute; th&acirc;m nhập</span></span></u></em></strong></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Thị trường nhạy cảm với gi&aacute;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chi ph&iacute; sản xuất v&agrave; ph&acirc;n phối giảm theo quy m&ocirc;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&ocirc;ng l&agrave;m tăng cạnh tranh</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">2.2&nbsp; Ch&iacute;nh s&aacute;ch gi&aacute;&nbsp; cho sản phẩm hỗn hợp</span></span></u></em></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">- Định gi&aacute; cho tuyến sản phẩm/nh&atilde;n hiệu:</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">- H&igrave;nh th&agrave;nh khung gi&aacute; cho c&aacute;c sp trong tuyến</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">- Định gi&aacute; sản phẩm bổ sung v&agrave; thay thế</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">- Định gi&aacute; cho g&oacute;i sản phẩm</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">2.3 Ch&iacute;nh s&aacute;ch gi&aacute; ph&acirc;n h&oacute;a:</span></span></u></em></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Gi&aacute; theo gi&aacute; trị</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Gi&aacute; chiết khấu v&agrave; phụ gi&aacute;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Gi&aacute; t&acirc;m l&yacute;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Gi&aacute; khuyến mại</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Gi&aacute; theo v&ugrave;ng địa l&yacute;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Gi&aacute; quốc tế</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Gi&aacute; ph&acirc;n biệt (theo KH, thời gian, địa điểm, h&igrave;nh ảnh, nh&atilde;n hiệu)</span></span></span></span></li>\r\n</ul>', '1551796943', null, '0', '58', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('355', 'Câu 7 (nhóm 1)', '<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><b><span style=\"font-size:13.0pt\"><span style=\"line-height:107%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 7(Nh&oacute;m 1)</span></span></span></b><span style=\"font-size:13.0pt\"><span style=\"line-height:107%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> Kh&aacute;i niệm gi&aacute; kinh doanh theo quan điểm marketing? N&ecirc;u c&aacute;c bước trong quy tr&igrave;nh x&aacute;c lập gi&aacute; kinh doanh? Tr&igrave;nh b&agrave;y nội dung một trong c&aacute;c bước x&aacute;c lập gi&aacute; kinh doanh ( T&iacute;nh to&aacute;n v&agrave; ph&acirc;n t&iacute;ch chi ph&iacute;) Tr&igrave;nh b&agrave;y c&aacute;c phương ph&aacute;p định gi&aacute; của doanh nghiệp? N&ecirc;u kh&aacute;i niệm điểm h&ograve;a điểm h&ograve;a vốn? Vẽ đồ thị h&ograve;a vốn v&agrave; giải th&iacute;ch &yacute; nghĩa của kinh doanh?</span></span></span></span></span></span></p>', '1551796963', null, '354', '58', '1', '354', '354', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('356', 'NHÓM CÂU HỎI 2', null, '1551796969', null, '0', '60', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('359', 'Phương trình đẳng cấp cấp 1', '<p style=\"margin-left:96px; margin-bottom:11px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:96px; margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><b><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Phương tr&igrave;nh vi ph&acirc;n cấp 1 đẳng cấp</span></span></span></b></span></span></span></p>\r\n\r\n<p style=\"margin-left:36px\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-size:16.0pt\"><span style=\"color:red\">Dạng :&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(y\' = f\\left( {x;y} \\right) = \\varphi \\left( {\\frac{y}{x}} \\right)\\)</span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:red\"> (3.1)</span></span><br />\r\n<b><span style=\"font-size:16.0pt\"><span style=\"color:black\">Phương ph&aacute;p&nbsp; giải</span></span></b><br />\r\n<span style=\"font-size:16.0pt\"><span style=\"color:black\">Đặt </span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(u = \\frac{y}{x}\\)</span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> suy ra </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> = </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">??</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> n&ecirc;n </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">&prime; = </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> + </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">&prime;</span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">, thay v&agrave;o ta c&oacute;:<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">&prime;</span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> = </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">(</span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">) &minus; </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><br />\r\n<br />\r\n<span style=\"font-size:16.0pt\"><span style=\"color:black\">Nếu </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">(</span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">) &minus; </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> &ne; 0, ta c&oacute; :</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:27px\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(\\frac{{du}}{{\\varphi (u) - u}} = \\frac{{d{\\rm{x}}}}{x}\\)</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:27px\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-size:16.0pt\"><span style=\"color:black\">Suy ra t&iacute;ch ph&acirc;n tổng qu&aacute;t : </span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(\\int {\\frac{{du}}{{\\varphi (u) - u}}}  = \\int {\\frac{{d{\\rm{x}}}}{x}} \\)</span></span></span></span><br />\r\n<span style=\"font-size:16.0pt\"><span style=\"color:black\">&nbsp;Nếu </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">(</span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">) &minus; </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> = 0 c&oacute; nghiệm </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> = </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> th&igrave; </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> = </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">??</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> l&agrave; nghiệm<br />\r\nNếu Nếu </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">(</span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">) &minus; </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> &equiv; 0 th&igrave; phương tr&igrave;nh trở th&agrave;nh</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:27px\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(y\' = \\frac{y}{x}\\)</span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> &nbsp;v&agrave; n&oacute; c&oacute; nghiệm </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> = </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">??</span></span></span><br />\r\n<span style=\"font-size:16.0pt\"><span style=\"color:black\"><b>Ch&uacute; &yacute; :</b></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:51px\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-size:16.0pt\"><span style=\"color:black\">Muốn kiểm tra phương tr&igrave;nh </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">&prime; = </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">(</span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">, </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">)<br />\r\nc&oacute; phải đẳng cấp cấp 1 kh&ocirc;ng</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:27px\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-size:16.0pt\"><span style=\"color:black\">ta c&oacute; thể kiểm tra </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">(</span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">??</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">,</span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">??</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">)= </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">(</span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">, </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">),</span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">&forall;?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> th&igrave; cho </span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(t = \\frac{1}{x}\\)</span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> ta c&oacute;</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:27px\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(f\\left( {x;y} \\right) = f\\left( {t{\\rm{x}};ty} \\right) = f\\left( {1;\\frac{y}{x}} \\right) = \\varphi \\left( {\\frac{y}{x}} \\right)\\)</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:27px\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-size:16.0pt\"><span style=\"color:black\">V&iacute; dụ : Giải phương tr&igrave;nh</span></span></span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(y\' = \\frac{{x + y}}{{x - y}}\\)</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-size:16.0pt\"><span style=\"color:black\">Giải<br />\r\nĐặt</span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(u = \\frac{y}{x}\\)</span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> &nbsp;&nbsp;ta c&oacute; </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> = </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">??</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> v&agrave; </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">&prime; = </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">&prime;</span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> + </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\">&nbsp; suy ra :</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:27px\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(u\'x + u = \\frac{{1 + u}}{{1 - u}} \\Rightarrow \\frac{{1 - u}}{{1 + {u^2}}}du = \\frac{{d{\\rm{x}}}}{x}\\)</span></span></span></span><br />\r\n<br />\r\n<span style=\"font-size:16.0pt\"><span style=\"color:black\">T&iacute;ch ph&acirc;n 2 vế ta c&oacute; :</span></span></span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\">\\[\\arctan u - \\frac{1}{2}\\ln (1 + {u^2}) = \\ln \\left| x \\right| + C\\]\\[ \\Rightarrow D\\left( {{x^2} + {y^2}} \\right) = {e^{\\arctan \\frac{y}{x}}}\\]</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:27px\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-size:16.0pt\"><span style=\"background:yellow\"><span style=\"color:black\">&nbsp;</span></span></span><span style=\"font-size:16.0pt\"><span style=\"background:yellow\"><span style=\"color:red\">Phương tr&igrave;nh </span></span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(y\' = f\\left( {\\frac{{{a_1}x + {b_1}y + {c_1}}}{{{a_2}x + {b_2}y + {c_2}}}} \\right)\\)</span></span></span></span><br />\r\n<span style=\"font-size:16.0pt\"><span style=\"color:black\">C&oacute; thể đưa về dạng biến số ph&acirc;n ly</span></span></span></span><br />\r\n<span style=\"font-size:20px;\">Trường hợp 1 :Nếu <span class=\"math-tex\">\\(\\left| {\\begin{array}{*{20}{c}} {{a_1}}&amp;{{b_1}}\\\\ {{a_2}}&amp;{{b_2}} \\end{array}} \\right| \\ne 0\\)</span> th&igrave; đặt? = ? + ?;? = ? + ?&nbsp;Với ?, ? l&agrave; nghiệm của hệ <span class=\"math-tex\">\\(\\left\\{ \\begin{array}{l} {a_1}x + {b_1}y + {c_1} = 0\\\\ {a_2}x + {b_2}y + {c_2} = 0 \\end{array} \\right.\\)</span><br />\r\nKhi đ&oacute;&nbsp;&nbsp; <strong><span class=\"math-tex\">\\(\\frac{{dz}}{{dt}} = \\frac{{dz}}{{dy}}\\frac{{dy}}{{d{\\rm{x}}}}\\frac{{d{\\rm{x}}}}{{dt}} = \\frac{{dy}}{{d{\\rm{x}}}}\\)</span></strong>Thay v&agrave;o ta c&oacute; : <strong><span class=\"math-tex\">\\(z\' = f\\left( {\\frac{{{a_1}t + {b_1}z}}{{{a_2}t + {b_2}z}}} \\right)\\)</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Đ&acirc;y l&agrave; phương tr&igrave;nh đẳng cấp cấp 1.<br />\r\nTrường hợp 2 :Nếu <span class=\"math-tex\">\\(\\left| {\\begin{array}{*{20}{c}} {{a_1}}&amp;{{b_1}}\\\\ {{a_2}}&amp;{{b_2}} \\end{array}} \\right| = 0\\)</span>th&igrave; <strong><span class=\"math-tex\">\\(\\frac{{{a_1}}}{{{a_2}}} = \\frac{{{b_1}}}{{{b_2}}} = k\\)</span></strong>; ta đặt :<span class=\"math-tex\">\\( z = {a_2}x + {b_2}y\\)</span>;Ta c&oacute; : <strong><span class=\"math-tex\">\\(z\' = {a_2} + {b_2}f\\left( {\\frac{{k{\\rm{z + }}{{\\rm{c}}_1}}}{{z + {c_2}}}} \\right)\\)</span></strong>Đ&acirc;y l&agrave; phương tr&igrave;nh biến số ph&acirc;n ly.</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-size:16.0pt\"><span style=\"color:black\">V&iacute; dụ : Giải phương tr&igrave;nh vi ph&acirc;n<br />\r\n&nbsp;(</span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> + </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> &ndash; 2) </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">??</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> &ndash;( </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> &minus; </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> + 4) </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">??</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> = 0<br />\r\nGiải<br />\r\nNếu </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> &minus; </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> + 4 &ne; 0 phương tr&igrave;nh đ&atilde; cho tương đương với :</span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(y\' = \\frac{{x + y - 2}}{{x - y + 4}}\\)</span></span></span></span><br />\r\n<span style=\"font-size:16.0pt\"><span style=\"color:black\">Do n&ecirc;n giải hệ :<br />\r\n&nbsp;</span></span><br />\r\n<span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> + </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> &minus; 2 = 0</span></span><br />\r\n<span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> &minus; </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> + 4 = 0</span></span><br />\r\n<span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">&rArr;</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> &alpha;=1;&beta;=3<br />\r\n<br />\r\nĐặt: x=t-1 v&agrave; y=z+3</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-size:16.0pt\"><span style=\"color:black\">Đặt </span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(u = \\frac{z}{t}\\)</span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> ta c&oacute; </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">?</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> = </span></span><span style=\"font-size:16.0pt\"><span cambria=\"\" math=\"\" style=\"font-family:\"><span style=\"color:black\">??</span></span></span><span style=\"font-size:16.0pt\"><span style=\"color:black\"> v&agrave; Lấy t&iacute;ch ph&acirc;n 2 vế</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\">\\[\\int {\\frac{{1 - u}}{{1 + {u^2}}}du = \\int {\\frac{{dt}}{t} + \\ln C} } \\]</span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\">\\[ \\Leftrightarrow \\arctan u - \\ln \\sqrt {1 + {u^2}} \\]</span></span></span></span> </span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\">\\[ \\Leftrightarrow {e^{\\arctan \\frac{{y - 3}}{{x + 1}}}} = Ct\\sqrt {{{\\left( {y - 3} \\right)}^2} + {{\\left( {x + 1} \\right)}^2}} \\]</span></span></span></span> </span></span></span></p>\r\n\r\n<p style=\"margin-left:27px\">&nbsp;</p>', '1551840893', null, '169', '51', '1', '169', '169', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('360', 'Phương trình vi phân tuyến tính cấp 1', '<p style=\"margin-left:120px; margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><b><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Phương tr&igrave;nh tuyến t&iacute;nh cấp 1</span></span></span></b></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"color:red\">Dạng&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(y\' + p\\left( x \\right)y = q\\left( x \\right)\\)</span></span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"color:red\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (phương tr&igrave;nh tuyến t&iacute;nh cấp 1)</span></span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:27px; margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(y\' + p\\left( x \\right) = 0\\)</span></span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"color:red\">&nbsp; (phương tr&igrave;nh tuyến t&iacute;nh cấp 1 thuần nhất)</span></span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><b><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Hướng dẫn giải</span></span></span></b></span></span></span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><strong>*Phương ph&aacute;p biến thi&ecirc;n hằng số</strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#3498db;\">Bước 1</span> T&igrave;m nghiệm tổng qu&aacute;t của phương tr&igrave;nh thuần nhất</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">nghiệm tổng qu&aacute;t của phương tr&igrave;nh thuần nhất<span style=\"color:#e74c3c;\"><strong><span class=\"math-tex\">\\(y\' + p\\left( x \\right) = 0\\)</span></strong></span>l&agrave; &nbsp;&nbsp;<span style=\"font-size:20px;\"><span style=\"color:#e74c3c;\"><strong><span class=\"math-tex\">\\(y = C.{e^{ - \\int {p\\left( x \\right)d{\\rm{x}}} }}\\)</span></strong></span></span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#3498db;\">Bước 2</span> (biến thi&ecirc;n hằng số)</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">ở nghiệm tổng qu&aacute;t<span style=\"color:#e74c3c;\"><strong><span class=\"math-tex\">\\(y = C.{e^{ - \\int {p\\left( x \\right)d{\\rm{x}}} }}\\)</span></strong></span> ta coi C l&agrave; một h&agrave;m của x :&nbsp;C=C(x), nghĩa l&agrave; cho rằng <em>C&#39;&ne;0</em>). Khi đ&oacute; đạo h&agrave;m hai vế</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong><span class=\"math-tex\">\\(y\' = C\'.{e^{ - \\int {p\\left( x \\right)d{\\rm{x}}} }} - C.p\\left( x \\right).{e^{ - \\int {p\\left( x \\right)d{\\rm{x}}} }}\\)</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Thay v&agrave;o phương tr&igrave;nh <strong><span class=\"math-tex\">\\(y\' + p\\left( x \\right) = 0\\)</span></strong>&nbsp;, r&uacute;t gọn , ta được</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color:#e74c3c;\"><span class=\"math-tex\">\\(     \\begin{array}{l} C\' = q\\left( x \\right).{e^{\\int {p\\left( x \\right)d{\\rm{x}}} }} \\Rightarrow C = \\int {q\\left( x \\right).{e^{\\int {p\\left( x \\right)d{\\rm{x}}} }}}  + D\\\\  \\Rightarrow y = \\left( {\\int {q\\left( x \\right).{e^{\\int {p\\left( x \\right)d{\\rm{x}}} }}}  + D} \\right).{e^{ - \\int {p\\left( x \\right)d{\\rm{x}}} }} \\end{array}\\)</span></span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(D l&agrave; hằng số t&ugrave;y &yacute;)</span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><strong>*Giải phương tr&igrave;nh thuần nhất cấp 1 hệ số hằng bằng phương ph&aacute;p chọn (phương ph&aacute;p hệ số bất định)</strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Dạng phương tr&igrave;nh<span class=\"math-tex\">\\(       y\' + py = f\\left( x \\right)\\)</span></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Phương tr&igrave;nh đặc trưng <strong><span class=\"math-tex\">\\(\\lambda  + p = 0 \\Leftrightarrow \\lambda  =  - p\\)</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Khi đ&oacute; nghiệm tổng qu&aacute;t của phương tr&igrave;nh thuần nhất l&agrave;<strong><span class=\"math-tex\">\\(\\overline y  = C.{e^{ - p{\\rm{x}}}}\\)</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">T&igrave;m nghiệm ri&ecirc;ng của phương tr&igrave;nh kh&ocirc;ng thuần nhất</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">TH1 <span style=\"color:#e74c3c;\"><span class=\"math-tex\">\\(f\\left( x \\right) = {P_n}\\left( x \\right).{e^{\\alpha x}}\\)</span></span>&nbsp; &nbsp;th&igrave;&nbsp;<span style=\"color:#3498db;\"><span class=\"math-tex\">\\(\\widehat y = \\left[ \\begin{array}{l} {Q_n}\\left( x \\right).{e^{\\alpha x}}\\left( {\\alpha  \\ne  - p} \\right)\\\\ x{Q_n}\\left( x \\right).{e^{\\alpha x}}(\\alpha  =  - p) \\end{array} \\right.\\)</span></span></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">TH2&nbsp;&nbsp;<span style=\"color:#e74c3c;\"><span class=\"math-tex\">\\(f\\left( x \\right) = {e^{\\alpha x}}\\left[ {{P_m}\\left( x \\right){\\rm{cosx + }}{{\\rm{Q}}_n}\\left( x \\right){\\mathop{\\rm s}\\nolimits} {\\rm{inx}}} \\right]\\)</span></span>th&igrave; <span style=\"color:#3498db;\"><span class=\"math-tex\">\\(\\widehat y = {e^{\\alpha x}}\\left[ {{R_l}\\left( x \\right){\\rm{cosx + }}{{\\rm{S}}_l}\\left( x \\right){\\mathop{\\rm s}\\nolimits} {\\rm{inx}}} \\right]\\)</span></span> l=max{m,n}</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Vậy nghiệm của phương tr&igrave;nh vi ph&acirc;n cấp 1 tuyến t&iacute;nh l&agrave;<span style=\"color:#e74c3c;\"><span class=\"math-tex\">\\( y = \\overline y  + \\widehat y\\)</span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\">&nbsp;</p>\r\n\r\n<p style=\"margin-bottom:11px\">&nbsp;</p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">VD </span></span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(y\' - 5y = {e^{4{\\rm{x}}}}\\left( {3{\\rm{x}} + 7} \\right)\\)</span></span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Ta c&oacute; </span></span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(\\alpha  = 4;{P_n}\\left( x \\right) = 3{\\rm{x}} + 7\\)</span></span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Phương tr&igrave;nh thuần nhất: y&rsquo;-5y=0</span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Phương tr&igrave;nh đặc trưng </span></span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(\\lambda  - 5 = 0 \\Leftrightarrow \\lambda  = 5 \\ne 4 = \\alpha \\)</span></span></span></span></span> </span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Suy ra nghiệm tổng qu&aacute;t của phương tr&igrave;nh thuần nhất l&agrave; </span></span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(\\overline y  = C.{e^{5{\\rm{x}}}}\\)</span></span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Nghiệm ri&ecirc;ng </span></span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(\\widehat y = {e^{4{\\rm{x}}}}\\left( {{\\rm{Ax + B}}} \\right)\\)</span></span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Lấy đạo h&agrave;m Thay v&agrave;o phương tr&igrave;nh kh&ocirc;ng thuần nhất ta c&oacute;&nbsp;</span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\({e^{4{\\rm{x}}}}\\left( {{\\rm{ - Ax + A - B}}} \\right) = {e^{4{\\rm{x}}}}(3{\\rm{x}} + 7)\\)</span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:20px;\">A=-3;B=-10</span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\( \\Rightarrow \\widehat y = {e^{4{\\rm{x}}}}( - 3{\\rm{x}} - 10)\\)</span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"> <span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span class=\"math-tex\">\\( \\Rightarrow y = C.{e^{{\\rm{5x}}}} - {e^{4{\\rm{x}}}}(3{\\rm{x + }}10)\\)</span></span>. </span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\">&nbsp;</p>', '1551840921', null, '169', '51', '1', '169', '169', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('361', 'Phương trình Bernoulli', '<p><span style=\"font-size:20px;\"><strong>Phương tr&igrave;nh Bernouli</strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Dạng phương tr&igrave;nh <span class=\"math-tex\">\\(y\' + p\\left( x \\right)y = q\\left( x \\right).{y^\\alpha }\\)</span><br />\r\n<strong>Hướng dẫn giải </strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#3498db;\">Nếu &alpha;=0</span> th&igrave; phương tr&igrave;nh Bernoulli l&agrave; phương tr&igrave;nh tuyến t&iacute;nh cấp 1 (đ&atilde; biết c&aacute;ch giải)</span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#3498db;\">Nếu &alpha;=1</span> th&igrave; ta c&oacute; l&agrave; phương tr&igrave;nh tuyến t&iacute;nh thuần nhất cấp 1 (đ&atilde; biết c&aacute;ch giải)</span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#3498db;\">Nếu &alpha;&lt;0</span> th&igrave; chia hai vế của phương tr&igrave;nh cho y<sup>&alpha;</sup> ta được&nbsp; &nbsp;<strong><span class=\"math-tex\">\\({y^{ - \\alpha }}.y\' + p\\left( x \\right){y^{1 - \\alpha }} = q\\left( x \\right)\\)</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Đặt <strong><span class=\"math-tex\">\\(z = {y^{1 - \\alpha }}\\)</span></strong> , đưa về dạng phương tr&igrave;nh tuyến t&iacute;nh:&nbsp;<strong><span class=\"math-tex\">\\(z\' + \\left( {1 - \\alpha } \\right)p\\left( x \\right)z = \\left( {1 - \\alpha } \\right)q\\left( x \\right)\\)</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Nếu th&igrave; ngo&agrave;i nghiệm như ở 3) c&ograve;n th&ecirc;m nghiệm y=0</span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">VD&nbsp; </span></span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(y\' + \\left( {3{\\rm{x}} - 7} \\right)y = 5{\\rm{x}}\\sqrt y \\)</span></span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px; margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">&nbsp; Phương tr&igrave;nh c&oacute; 1 nghiệm y=0</span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:13px; margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"line-height:115%\"><span style=\"tab-stops:4.5pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Khi </span></span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span class=\"math-tex\">\\(y \\ne 0\\)</span></span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"> ta c&oacute; </span></span></span></span></span></span></span></p>\r\n\r\n<p class=\"MTDisplayEquation\" style=\"margin-left:24px; text-indent:0in; margin-bottom:13px\"><span style=\"font-size:16pt\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\">\\[\\frac{{y&#39;}}{{\\sqrt y }} + \\left( {3{\\rm{x}} - 7} \\right)\\sqrt y&nbsp; = 5{\\rm{x}}\\]</span></span> </span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px; margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Đặt </span></span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(z = \\sqrt y \\)</span></span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"> th&igrave; được&nbsp; </span></span></span></span></span></span></span></p>\r\n\r\n<p class=\"MTDisplayEquationCxSpFirst\"><span style=\"font-size:16pt\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></span></span><span style=\"font-size:16pt\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\">\\[z&#39; + \\frac{{\\left( {3{\\rm{x}} - 7} \\right)z}}{2} = \\frac{{5{\\rm{x}}}}{2}\\]</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\">\\[ \\Rightarrow \\overline z&nbsp; = C.{e^{ - \\int {\\frac{{\\left( {3{\\rm{x}} - 7} \\right)}}{2}dz} }} = C.{e^{\\frac{{ - 3{{\\rm{x}}^2}}}{4} + \\frac{7}{2}x}}\\]</span></span></span></span> </span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\">\\[ \\Rightarrow z&#39; = C&#39;.{e^{\\frac{{ - 3{{\\rm{x}}^2}}}{4} + \\frac{7}{2}x}} - C.\\frac{{\\left( {3{\\rm{x}} - 7} \\right)}}{2}.{e^{\\frac{{ - 3{{\\rm{x}}^2}}}{4} + \\frac{7}{2}x}}\\]</span></span></span></span> </span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\">\\[ \\Rightarrow C&#39; = \\frac{{5{\\rm{x}}}}{{2.{e^{\\frac{{ - 3{{\\rm{x}}^2}}}{4} + \\frac{7}{2}x}}}}\\]</span></span></span></span> <span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\">\\[ \\Rightarrow C = \\int {\\frac{{5{\\rm{x}}}}{{2.{e^{\\frac{{ - 3{{\\rm{x}}^2}}}{4} + \\frac{7}{2}x}}}}d{\\rm{x}}} \\]</span></span></span></span> </span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\">\\[ \\Rightarrow y = {z^2} = {\\left( {{e^{\\frac{{ - 3{{\\rm{x}}^2}}}{4} + \\frac{7}{2}x}}.\\int {\\frac{{5{\\rm{x}}}}{{2.{e^{\\frac{{ - 3{{\\rm{x}}^2}}}{4} + \\frac{7}{2}x}}}}d{\\rm{x}}} } \\right)^2}\\]</span></span></span></span> </span></span></span></p>\r\n\r\n<p class=\"MTDisplayEquationCxSpFirst\"><span style=\"font-size:16pt\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">&nbsp;&nbsp;&nbsp;</span></span></span></p>', '1551840932', null, '169', '51', '1', '169', '169', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('362', 'phương trình vi phân cấp 2 giảm cấp được(dạng khuyết y\';y)', '<p><span style=\"font-size:20px;\"><strong>Khuyết y,y&rsquo;&nbsp;</strong>Phương tr&igrave;nh c&oacute; dạng&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>y&rsquo;&rsquo;=f(x)</em></span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><strong>Hướng dẫn giải</strong>T&iacute;ch ph&acirc;n 2 lần &nbsp;<strong><span class=\"math-tex\">\\(y\' = \\int {f\\left( x \\right)d{\\rm{x}} + {C_1}}  \\Rightarrow y = \\int {\\left( {\\int {f\\left( x \\right)d{\\rm{x}} + {C_1}} } \\right)} d{\\rm{x}} + {C_2}\\)</span></strong></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><b><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">VD</span></span></span></b><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> Cho </span></span></span><span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(y&#39;&#39; = 5{{\\rm{x}}^2}\\)</span></span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> &nbsp;t&igrave;m nghiệm ri&ecirc;ng thỏa m&atilde;n</span></span></span><span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\({y_{x = 0}} = 0;y{&#39;_{x = 0}} = 1\\)</span></span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ta c&oacute; </span></span></span><span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(y&#39; = \\int {5{{\\rm{x}}^2}d{\\rm{x}}}&nbsp; = \\frac{5}{3}{x^3} + C \\Rightarrow C = 1\\)</span></span></span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\( \\Rightarrow y = \\int {\\frac{5}{3}{x^3} + 1{\\rm{dx}}}&nbsp; = \\frac{5}{{12}}{x^4} + x + D \\Rightarrow D = 0\\)</span></span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:96px; margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\( \\Rightarrow y = \\frac{5}{{12}}{x^4} + x\\)</span></span></span></span></span> </span></span></span></span></p>', '1551840962', null, '169', '51', '1', '169', '169', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('363', 'phương trình vi phân cấp 2 giảm cấp được(dạng khuyết y)', '<p><span style=\"font-size:20px;\"><strong>+Khuyết y&nbsp;</strong>Dạng phương tr&igrave;nh <em>y&rsquo;&rsquo;=f(x;y&rsquo;)</em></span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><strong>Hướng dẫn giải&nbsp;</strong>Đặt <em>y&rsquo;=p(x)</em> ta c&oacute;<em>y&rsquo;&rsquo;=p&rsquo;(x) </em>Khi đ&oacute; phương tr&igrave;nh về dạng&nbsp;<strong><span class=\"math-tex\">\\(p\'\\left( x \\right) = f\\left( {x;p} \\right)\\)</span></strong> &nbsp;Đ&acirc;y l&agrave; 1 phương tr&igrave;nh vi ph&acirc;n cấp 1 đối với h&agrave;m p biến x.</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Giả sử nghiệm tổng qu&aacute;t của phương tr&igrave;nh n&agrave;y l&agrave; <strong><span class=\"math-tex\">\\(y\' = \\varphi \\left( {x;{C_1}} \\right)\\)</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Giải tiếp ta được nghiệm <strong><span class=\"math-tex\">\\(y = \\int {\\varphi (x;{C_1})d{\\rm{x}}}  + {C_2}\\)</span></strong></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><b><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">VD&nbsp;&nbsp; &nbsp;</span></span></span></b><span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(y&#39;&#39; + 5{\\rm{x}}y&#39; = 0\\)</span></span></span></span></span><b> &nbsp;&nbsp;</b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đặt </span></span></span><a name=\"MTBlankEqn\"><span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(y&#39; = p\\)</span></span></span></span></span></a><span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\( \\Rightarrow y&#39;&#39; = p&#39; \\Rightarrow p&#39; + 5{\\rm{x}}p = 0\\)</span></span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\( \\Rightarrow p = {C_1}.{e^{ - \\frac{5}{2}{x^2}}} \\Rightarrow y = \\int {{C_1}.{e^{ - \\frac{5}{2}{x^2}}}d{\\rm{x}}}&nbsp; + {C_2}\\)</span></span></span></span></span> </span></span></span></span></p>', '1551840985', null, '169', '51', '1', '169', '169', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('368', 'bài tập định dạng bài kiểm tra', '', '1551863696', null, '84', '37', '1', '84', '84', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('369', 'bài tập thử nghiệm ngày 06-03', '', '1551869613', null, '331', '33', '1', '331', '331', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('370', 'bài tập kiểm tra', '', '1551869948', null, '88', '37', '1', '88', '88', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('371', 'Câu 7 (nhóm 2)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 7 (N2</span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">): N&ecirc;u c&aacute;c bước trong quy tr&igrave;nh x&aacute;c lập gi&aacute; kinh doanh? Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh họa một trong c&aacute;c kĩ thuật định của doanh nghiệp ( Kĩ thuật định gi&aacute; th&ecirc;m v&agrave;o chi ph&iacute;; Định gia theo cảm nhận của kh&aacute;ch h&agrave;ng?) Tr&igrave;nh b&agrave;y c&aacute;c mục ti&ecirc;u định gi&aacute; của doanh nghiệp? N&ecirc;u c&aacute;c nội dung của&nbsp; ch&iacute;nh s&aacute;ch định gi&aacute;? Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh họa một trong c&aacute;c ch&iacute;nh s&aacute;ch gi&aacute; của doanh nghiệp? (Ch&iacute;nh s&aacute;ch ph&acirc;n s&aacute;ch gi&aacute; ph&acirc;n h&oacute;a; Ch&iacute;nh s&aacute;ch gi&aacute; cho sản phẩm mới)</span></span></span></span></p>', '1551877226', null, '354', '58', '1', '354', '354', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('372', 'Chương 8: Chính sách phân phối', '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">CHƯƠNG 8: CH&Iacute;NH S&Aacute;CH PH&Acirc;N PHỐI</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong>1. Một số vấn đề cơ bản về ph&acirc;n phối</strong></span></span></span></span></p>\r\n\r\n<p><u><em><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">1.1 Một số kh&aacute;i niệm cơ bản</span></span></span></span></span></span></span></span></strong></em></u></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Kh&aacute;i niệm ph&acirc;n phối</em></u>: Ph&acirc;n phối trong marketing l&agrave; những quyết định đưa h&agrave;ng h&oacute;a v&agrave;o k&ecirc;nh ph&acirc;n phối theo một hệ thống tổ chức v&agrave; c&ocirc;ng nghệ điều h&ograve;a, c&acirc;n đối, h&agrave;ng h&oacute;a để tiếp cận v&agrave; khai th&aacute;c hợp l&yacute; nhất nhu cầu của thị trường, để đưa h&agrave;ng h&oacute;a từ sản xuất đến KH cuối c&ugrave;ng nhanh nhất v&agrave; nhằm đạt lợi nhuận tối đa.</span></span></span></span></li>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c yếu tố cấu th&agrave;nh hệ ph&acirc;n phối: </span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Người cung ứng v&agrave; NTD;</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Trung gian; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hệ thống kho t&agrave;ng; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hệ thống th&ocirc;ng tin thị trường</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c nh&acirc;n tố ảnh hưởng ph&acirc;n phối: </span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Sản phẩm; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&aacute;ch h&agrave;ng; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Doanh nghiệp; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Trung gian; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đối thủ cạnh tranh;</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp;M&ocirc;i trường</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Kh&aacute;i niệm k&ecirc;nh ph&acirc;n phối </em></u>:L&agrave; sự kết hợp h&agrave;i h&ograve;a giữa người SX với người trung gian để tổ chức vận chuyển h&agrave;ng h&oacute;a hợp l&yacute; nhất nhằm thỏa m&atilde;n tối đa nhu cầu của kh&aacute;ch h&agrave;ng cuối c&ugrave;ng.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><u><em>Kh&aacute;i niệm ch&iacute;nh s&aacute;ch ph&acirc;n phối:</em></u> Bao gồm một tổ hợp c&aacute;c định hướng, nguy&ecirc;n tắc, biện ph&aacute;p v&agrave; quy tắc hoạt động được c&aacute;c tổ chức marketing chấp nhận v&agrave; t&ocirc;n trọng thực hiện đối với việc lựa chọn k&ecirc;nh ph&acirc;n phối, ph&acirc;n c&ocirc;ng nhiệm vụ marketing giữa c&aacute;c chủ thể kh&aacute;c nhau v&agrave; c&aacute;c ứng xử cơ bản được chấp nhận theo định hướng thỏa m&atilde;n nhu cầu thị trường</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">​​​​​​​1.2.&nbsp;</span></span></span></span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c d&ograve;ng vận động của k&ecirc;nh ph&acirc;n phối</span></span></span></span></strong></p>\r\n\r\n<ul>\r\n	<li><u><em><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kết cấu tổ chức</span></span></span></span></strong></em></u></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><img src=\"/public/images/course/58/lesson/1552146108_image-20190309224539-1.png\" style=\"height:51px; width:700px\" /></p>\r\n\r\n<ul>\r\n	<li><u><em><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kết cấu d&ograve;ng:</span></span></span></span></strong></em></u></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px; text-align:center\"><img src=\"/public/images/course/58/lesson/1552146108_image-20190309224539-2.png\" style=\"height:210px; width:800px\" /></p>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<p style=\"list-style-type:none\"><strong>​​​​​​​​​​​​​​1.3.&nbsp;<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c loại h&igrave;nh k&ecirc;nh ph&acirc;n phối</span></span></span></span></strong></p>\r\n\r\n<ul>\r\n	<li><u><em><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">K&ecirc;nh ph&acirc;n phối h&agrave;ng ti&ecirc;u d&ugrave;ng</span></span></span></span></strong></em></u></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:24px\"><img src=\"/public/images/course/58/lesson/1552146108_image-20190309224539-3.png\" style=\"height:282px; width:600px\" /></p>\r\n\r\n<p style=\"margin-left:24px\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li><u><em><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">K&ecirc;nh ph&acirc;n phối h&agrave;ng c&ocirc;ng nghiệp</span></span></span></span></strong></em></u></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px; text-align:center\"><img src=\"/public/images/course/58/lesson/1552146108_image-20190309224539-4.png\" style=\"height:239px; width:600px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">2. Nội dung cơ bản của ch&iacute;nh s&aacute;ch ph&acirc;n phối</span></span></span></span></strong></p>\r\n\r\n<p><u><em><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">2.1 Mục ti&ecirc;u của ch&iacute;nh&nbsp; s&aacute;ch ph&acirc;n phối</span></span></span></span></strong></em></u></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Mức độ bao phủ thị trường.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Lợi nhuận, thị phần v&agrave; doanh số tr&ecirc;n c&aacute;c đoạn thị trường.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Mức độ kiểm so&aacute;t thị trường v&agrave; chất lượng hoạt động MKT.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tiết kiệm chi ph&iacute; ph&acirc;n phối, đặc biệt l&agrave; vận chuyển.</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><u><em><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">2.2 Một số ch&iacute;nh s&aacute;ch ph&acirc;n phối chủ yếu</span></span></span></span></strong></em></u></p>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ch&iacute;nh s&aacute;ch k&ecirc;nh ph&acirc;n phối</span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+</span></span> <span style=\"font-size:13.0pt\">Thiết kế k&ecirc;nh ph&acirc;n phối</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+ Quản l&yacute; k&ecirc;nh ph&acirc;n phối</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ch&iacute;nh s&aacute;ch trung gian</span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+ Ph&acirc;n phối kh&ocirc;ng hạn chế</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+</span></span> <span style=\"font-size:13.0pt\">Ph&acirc;n phối chọn lọc </span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+ Ph&acirc;n phối qua đại l&yacute; đặc quyền</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><u><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ch&iacute;nh s&aacute;ch li&ecirc;n kết</span></span></span></span></em></u></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+</span></span><span style=\"font-size:13.0pt\">Hệ thống k&ecirc;nh li&ecirc;n kết dọc/ngang</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+ Hệ thống đơn k&ecirc;nh/đa k&ecirc;nh</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>', '1551879719', null, '0', '58', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('373', 'Câu 8 (nhóm 1)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 8(nh&oacute;m 1)</span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> N&ecirc;u kh&aacute;i niệm k&ecirc;nh ph&acirc;n phối? Vẽ m&ocirc; h&igrave;nh k&ecirc;nh ph&acirc;n phối h&agrave;ng ti&ecirc;u d&ugrave;ng của c&ocirc;ng ty doanh nghiệp? Tr&igrave;nh b&agrave;y k&ecirc;nh ph&acirc;n phối&nbsp; kh&ocirc;ng cấp/hai cấp ở c&ocirc;ng ty kinh doanh? Vẽ m&ocirc; h&igrave;nh v&agrave; lấy v&iacute; dụ minh họa k&ecirc;nh ph&acirc;n phối h&agrave;ng c&ocirc;ng nghiệp?</span></span></span></span></p>', '1551879933', null, '372', '58', '1', '372', '372', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('374', 'Câu 8 (Nhóm 2)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 8 (Nh&oacute;m 2):</span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> N&ecirc;u kh&aacute;i niệm ph&acirc;n phối trong marketing? Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh họa c&aacute;c nh&acirc;n tố ảnh hưởng đến ch&iacute;nh s&aacute;ch ph&acirc;n phối của doanh nghiệp?Tr&igrave;nh b&agrave;y nội dung cơ bản của ch&iacute;nh s&aacute;ch ph&acirc;n phối. &nbsp;N&ecirc;u kh&aacute;i niệm ch&iacute;nh s&aacute;ch ph&acirc;n phối? Tr&igrave;nh b&agrave;y nội dung của một trong c&aacute;c ch&iacute;nh s&aacute;ch ph&acirc;n phối?(Ph&acirc;n phối đại tr&agrave;; ph&acirc;n phối chọn lọc)</span></span></span></span></p>', '1551880189', null, '372', '58', '1', '372', '372', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('375', 'Chương 9: Chính sách xúc tiến', null, '1551881972', null, '0', '58', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('376', 'Câu 9 (nhóm 1)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 9 (nh&oacute;m 1)</span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> N&ecirc;u kh&aacute;i niệm x&uacute;c tiến b&aacute;n? Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh họa một trong c&aacute;c c&ocirc;ng cụ x&uacute;c tiến với người ti&ecirc;u d&ugrave;ng? N&ecirc;u kh&aacute;i niệm quảng c&aacute;o? Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh họa c&aacute;c y&ecirc;u cầu cơ bản với hoạt động qu&agrave;ng c&aacute;o của doanh nghiệp? N&ecirc;u c&aacute;c c&ocirc;ng cụ cơ bản của x&uacute;c tiến. Tr&igrave;nh b&agrave;y sự kh&aacute;c biệt giữa c&ocirc;ng cụ quảng c&aacute;o v&agrave; c&ocirc;ng ch&uacute;ng trong x&uacute;c tiến x&uacute;c tiến.</span></span></span></span></p>', '1551882037', null, '375', '58', '1', '375', '375', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('377', 'Câu 9 (nhóm 2)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 9(Nh&oacute;m 2): </span></span></strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u c&aacute;c phương ph&aacute;p x&aacute;c lập ng&acirc;n s&aacute;ch x&uacute;c tiến? Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh họa một trong c&aacute;c phương ph&aacute;p lập ng&acirc;n s&aacute;ch x&uacute;c tiến (Tỉ lệ phần tram tr&ecirc;n doanh số/Căn cứ v&agrave;o mục ti&ecirc;u v&agrave; nhiệm vụ? N&ecirc;u kh&aacute;i niệm quảng c&aacute;o? Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh hoạt hoạt động quảng c&aacute;o theo chu k&igrave; sống của sản phẩm? Kh&aacute;i niệm x&uacute;c tiến thương mại? Vẽ m&ocirc; h&igrave;nh x&uacute;c tiến thương mại? Tr&igrave;nh b&agrave;y nội dung &ldquo;K&ecirc;nh x&uacute;c tiến thương mại&rdquo; trong x&uacute;c tiến? Tr&igrave;nh b&agrave;y c&oacute; v&iacute; dụ minh họa nội dung x&aacute;c lập th&ocirc;ng điệp x&uacute;c tiến? Tr&igrave;nh b&agrave;y c&aacute;c cơ sở để x&aacute;c lập phối thức x&uacute;c tiến của doanh nghiệp?</span></span></span></span></p>', '1551884001', null, '375', '58', '1', '375', '375', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('378', 'Câu 26 (nhóm 1)', '<p><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u kh&aacute;i niệm &ldquo; kh&aacute;ch h&agrave;ng tổ chức&rdquo; v&agrave; &ldquo; h&agrave;nh vi mua của kh&aacute;ch h&agrave;ng tổ chức&rdquo;?</span></span></p>', '1551943145', null, '329', '59', '1', '329', '329', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('379', 'Câu 27 (nhóm 1) và Câu 24 (nhóm 2)', '<p><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u đặc điểm &ldquo; kh&aacute;ch h&agrave;ng tổ chức&rdquo; v&agrave; liệt k&ecirc; c&aacute;c loại h&igrave;nh kh&aacute;ch h&agrave;ng tổ chức. Quy tr&igrave;nh quyết định mua của kh&aacute;ch h&agrave;ng tổ chức?</span></span></p>', '1551943707', null, '329', '59', '1', '329', '329', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('380', 'Câu 28 (nhóm 1) và Câu 25 ( nhóm 2)', '<p><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u c&aacute;c loại h&agrave;nh vi mua v&agrave; đặc trưng h&agrave;nh vi mua của kh&aacute;ch h&agrave;ng &ndash;tổ chức</span></span></p>', '1551944615', null, '329', '59', '1', '329', '329', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('381', 'Câu 29 (nhóm 1)', '<p><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u đặc điểm h&agrave;nh vi của c&aacute;c loại kh&aacute;ch h&agrave;ng tổ chức( kh&aacute;ch h&agrave;ng tổ chức thương mại, kh&aacute;ch h&agrave;ng tổ chức sản xuất, kh&aacute;ch h&agrave;ng tổ chức Nh&agrave; nước). Vẽ m&ocirc; h&igrave;nh tổng qu&aacute;t về &ldquo; h&agrave;nh vi mua của kh&aacute;ch h&agrave;ng tổ chức ). </span></span></p>', '1551945163', null, '329', '59', '1', '329', '329', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('382', 'Câu 30 ( nhóm 1)  và câu 29 + 28 ( nhóm 2)', '<p>C&aacute;c bậc h&agrave;nh vi mua của kh&aacute;ch h&agrave;ng tổ chức? N&ecirc;u/ph&acirc;n t&iacute;ch&nbsp;c&aacute;c nh&acirc;n tố ảnh hưởng đến h&agrave;nh vi mua của kh&aacute;ch h&agrave;ng tổ chức( m&ocirc;i trường, tổ chức, mối quan hệ giữa c&aacute;c c&aacute; nh&acirc;n trong tổ chức- nh&acirc;n tố trung t&acirc;m mua v&agrave; c&aacute; nh&acirc;n) ?</p>\r\n\r\n<p>&nbsp;</p>', '1551945612', null, '329', '59', '1', '329', '329', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('383', 'Câu 23 ( nhóm 2)', '<p><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&acirc;n định sự kh&aacute;c biệt giữa thị trường tổ chức v&agrave; thị trường người ti&ecirc;u d&ugrave;ng?</span></span></p>', '1551946555', null, '329', '59', '1', '329', '329', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('384', 'Câu 26 ( nhóm 2)', '<p><span style=\"font-size:18px;\"><span style=\"font-family:Arial,Helvetica,sans-serif;\"><span new=\"\" roman=\"\" times=\"\">Ph&acirc;n t&iacute;ch quan điểm về h&agrave;nh vi mua của kh&aacute;ch h&agrave;ng tổ chức&nbsp;</span><span style=\"line-height:107%\">( m&ocirc; h&igrave;nh kinh tế, m&ocirc; h&igrave;nh cảm nhận, m&ocirc; h&igrave;nh phối hợp)</span><span new=\"\" roman=\"\" times=\"\">?</span></span></span></p>', '1551946692', null, '329', '59', '1', '329', '329', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('385', 'Câu 27 (nhóm 2)', '<p>Ph&acirc;n t&iacute;ch &quot; M&ocirc; h&igrave;nh tổng qu&aacute;t về h&agrave;nh vi mua của kh&aacute;ch h&agrave;ng tổ chức&quot; ( c&aacute;c k&iacute;ch th&iacute;ch b&ecirc;n ngo&agrave;i, trung t&acirc;m mua v&agrave; những đ&aacute;p ứng của kh&aacute;ch h&agrave;ng tổ chức).</p>', '1551946939', null, '329', '59', '1', '329', '329', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('386', 'Câu 30 (nhóm 2)', '<p>Ph&acirc;n t&iacute;ch vai tr&ograve; trong trung t&acirc;m mua của kh&aacute;ch h&agrave;ng tổ chức?</p>', '1551947182', null, '329', '59', '1', '329', '329', '1', '', '0', null, null, null, null, null, '0');
