INSERT INTO `lesson` VALUES ('390', 'Câu 18: Quan niệm HCM về thực chất và nhiệm vụ lịch sử của thời kỳ quá độ lên CNXH? HCM đã giải thích tính chất khó khăn, phức tạp của thời kì này như thế nào?', '', '1551970493', null, '203', '55', '1', '203', '203', '1', '', '0', null, null, null, null, null, '0');
