INSERT INTO `lesson` VALUES ('400', 'Chương 01', '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><u><span style=\"font-size:15.0pt\">Chương 01: BẢN CHẤT CỦA KẾ TO&Aacute;N</span></u></strong></span></span></p>\r\n\r\n<ol style=\"list-style-type:upper-roman\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><u><span style=\"font-size:13.0pt\">Kh&aacute;i niệm v&agrave; vai tr&ograve; của kế to&aacute;n trong quản l&iacute; kinh tế</span></u></strong></span></span></li>\r\n</ol>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\">Kh&aacute;i niệm v&agrave; ph&acirc;n loại:</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Kh&aacute;i niệm hạch to&aacute;n:</span></span></span></em></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">C&aacute;c hoạt động quan s&aacute;t, đo lường (3 thước đo: hiện vật, lao động, gi&aacute; trị), t&iacute;nh to&aacute;n, ghi ch&eacute;p c&aacute;c qu&aacute; tr&igrave;nh kinh tế của con người, nhằm thực hiện chức năng phản &aacute;nh v&agrave; gi&aacute;m đốc c&aacute;c hoạt động trong qu&aacute; tr&igrave;nh t&aacute;i sản xuất được gọi l&agrave; hạch to&aacute;n.</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\" style=\"list-style-type:lower-alpha\">\r\n	<li><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Hệ thống hạch to&aacute;n:</span></span></span></em></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Hạch to&aacute;n nghiệp vụ</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Hạch to&aacute;n thống k&ecirc;</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Hạch to&aacute;n kế to&aacute;n</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Hạch to&aacute;n kế to&aacute;n l&agrave; m&ocirc;n khoa học thu nhận xử l&iacute; v&agrave; cung cấp to&agrave;n bộ th&ocirc;ng tin về t&igrave;nh h&igrave;nh t&agrave;i sản v&agrave; sự vận động của t&agrave;i sản trong c&aacute;c đơn vị sản xuất kinh doanh, tổ chức sự nghiệp v&agrave; c&aacute;c cơ quan nhằm kiểm tra, gi&aacute;m s&aacute;t to&agrave;n bộ t&agrave;i sản v&agrave; c&aacute;c hoạt động kinh tế t&agrave;i ch&iacute;nh của c&aacute;c đơn vị đ&oacute;.</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"3\" style=\"list-style-type:lower-alpha\">\r\n	<li><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Ph&acirc;n loại:</span></span></span></em></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Ph&acirc;n loại theo c&aacute;ch ghi ch&eacute;p, thu nhận th&ocirc;ng tin</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">- Kế to&aacute;n ghi đơn</span></span></span></p>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">- Kế to&aacute;n ghi k&eacute;p</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Ph&acirc;n loại theo mức độ, t&iacute;nh chất của th&ocirc;ng tin</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">- Kế to&aacute;n tổng hợp</span></span></span></p>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">- Kế to&aacute;n chi tiết</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Ph&acirc;n loại theo phạm vi th&ocirc;ng tin kế to&aacute;n cung cấp</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">- Kế to&aacute;n t&agrave;i ch&iacute;nh: đối tượng ngo&agrave;i doanh nghiệp</span></span></span></p>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">- Kế to&aacute;n quản trị: c&aacute;c nh&agrave; quản trị c&aacute;c cấp trong đơn vị</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Ph&acirc;n loại theo đặc điểm v&agrave; mục đ&iacute;ch của đơn vị</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">- Kế to&aacute;n c&ocirc;ng </span></span></span></p>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">- Kế to&aacute;n doanh nghiệp</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\">&nbsp;</p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\">Vai tr&ograve;</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;Vai tr&ograve; của kế to&aacute;n l&agrave; một bộ phận cấu th&agrave;nh n&ecirc;n hệ thống c&ocirc;ng cụ quản l&yacute; nền kinh tế quốc d&acirc;n. Ng&agrave;y nay người ta x&aacute;c định kế to&aacute;n l&agrave; một mặt của hoạt động kinh tế n&oacute;i chung v&agrave; qu&aacute; tr&igrave;nh sản xuất kinh doanh n&oacute;i ri&ecirc;ng tr&ecirc;n &yacute; nghĩa kế to&aacute;n l&agrave; c&ocirc;ng cụ thu thập, đo lường xử l&yacute; v&agrave; cung cấp th&ocirc;ng tin hữu &iacute;ch cho việc để ra c&aacute;c quyết định quản l&yacute;.</span></span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:13.0pt\">Kế to&aacute;n l&agrave; c&ocirc;ng cụ quản l&yacute;, gi&aacute;m s&aacute;t chặt chẽ, c&oacute; hiệu quả mọi hoạt động kinh tế t&agrave;i ch&iacute;nh cung cấp th&ocirc;ng tin đầy đủ, kịp thời, trung thực, c&ocirc;ng khai, minh bạch, đ&aacute;p ứng y&ecirc;u cầu tổ chức, quản l&yacute; điều h&agrave;nh của cơ quan nh&agrave; nước, doanh nghiệp, tổ chức v&agrave; c&aacute; nh&acirc;n.</span></span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:13.0pt\">Với vai tr&ograve; đ&oacute; hoạt động của kế to&aacute;n cần thiết ở tất cả c&aacute;c cơ quan h&agrave;nh ch&iacute;nh từ Trung ương đến cấp tỉnh, huyện x&atilde;, ở tất cả c&aacute;c đơn vị sự nghiệp, c&aacute;c doanh nghiệp thuộc mọi loại h&igrave;nh, quy m&ocirc;.</span></span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:13.0pt\">Hoạt động của kế to&aacute;n được điều chỉnh bởi c&aacute;c quy định về ph&aacute;p l&yacute; v&agrave; c&aacute;c quy định về nghiệp vụ chuy&ecirc;n ng&agrave;nh.</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<ol start=\"2\" style=\"list-style-type:upper-roman\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><u><span style=\"font-size:13.0pt\">Nguy&ecirc;n tắc v&agrave; c&aacute;c y&ecirc;u cầu cơ bản của kế to&aacute;n </span></u></strong></span></span></li>\r\n</ol>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\">Một số kh&aacute;i niệm cơ bản của kế to&aacute;n</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Kh&aacute;i niệm về đơn vị kế to&aacute;n:</span></span></span></em>\r\n\r\n	<ul>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Đơn vị kế to&aacute;n l&agrave; đơn vị m&agrave; ở đ&oacute; n&oacute; kiểm so&aacute;t c&aacute;c nguồn lực t&agrave;i sản v&agrave; thực h&agrave;nh c&aacute;c hoạt động kinh doanh c&ugrave;ng qu&aacute; tr&igrave;nh ghi ch&eacute;p, tổng hợp v&agrave; b&aacute;o c&aacute;o.</span></span></span></li>\r\n	</ul>\r\n	</li>\r\n	<li><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Kỳ kế to&aacute;n:</span></span></span></em>\r\n	<ul>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">L&agrave; khoảng thời gian x&aacute;c định thời điểm đơn vị kế to&aacute;n bắt đầu ghi sổ kế to&aacute;n đến thời điểm kết th&uacute;c việc ghi sổ kế to&aacute;n, kh&oacute;a sổ kế to&aacute;n để lập b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh.</span></span></span></li>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">C&oacute; 3 loại kỳ kế to&aacute;n: Kỳ kế to&aacute;n năm, kỳ kế to&aacute;n qu&yacute;, kỳ kế to&aacute;n th&aacute;ng.</span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\">&nbsp;</p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\">C&aacute;c nguy&ecirc;n tắc cơ bản của kế to&aacute;n (VAS01)</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Nguy&ecirc;n tắc cơ sở dồn t&iacute;ch:</span></span></span></em></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:80px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">- Mọi nghiệp vụ kinh tế t&agrave;i ch&iacute;nh li&ecirc;n quan đến t&agrave;i sản, nguồn vốn, doanh thu, chi ph&iacute; phải được ghi v&agrave;o thời điểm ph&aacute;t sinh, kh&ocirc;ng căn cứ v&agrave;o thười điểm thực tế thu chi tiền hoặc c&aacute;c khoản tương đương tiền. B&aacute;o c&aacute;o t&agrave;i ch&iacute;nh của đơn vị trong qu&aacute; khứ, hiện v&agrave; tương lai.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">VD: 2 DN A v&agrave; B c&ugrave;ng b&aacute;n 1 l&ocirc; h&agrave;ng trị gi&aacute; 100 trđ trong c&ugrave;ng 1 ng&agrave;y X/Y</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp; - DN A thu tiền ngay 100trđ</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp; - DN B cho kh&aacute;ch h&agrave;ng nơ 30 ng&agrave;y</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp; Ghi nhận doanh thu trong 2 trường hợp n&agrave;y như thế n&agrave;o?</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Giải:&nbsp;&nbsp; + Doanh thu ph&aacute;t sinh được ghi nhận của cả 2 DN v&agrave;o ng&agrave;y X/Y</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + DN A ph&aacute;t sinh doanh thu tăng 100 triệu X/Y</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + DN B ph&aacute;t sinh khoản nợ từ kh&aacute;ch h&agrave;ng</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + 30 ng&agrave;y sau, DN B ghi nhận ph&aacute;t sinh thu được tiền từ kh&aacute;ch h&agrave;ng v&agrave; x&oacute;a nợ. </span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">VD2: Thu&ecirc; nh&agrave; 6 th&aacute;ng, mỗi th&aacute;ng 5tr từ th&aacute;ng 1 đến th&aacute;ng 6. Th&aacute;ng 1 trả to&agrave;n bộ 30tr tiền thu&ecirc; nh&agrave;. Ghi nhận khaonr chi ph&iacute; từ việc thu&ecirc; nh&agrave; v&agrave;o th&aacute;ng n&agrave;o?</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Giải: + Ghi nhận chi ph&iacute; thu&ecirc; nh&agrave; th&aacute;ng 1(2,3,4,5,6) l&agrave; 5tr</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + Giảm tiền mặt 30tr ở th&aacute;ng 1 </span></span></span></p>\r\n\r\n<ul>\r\n	<li><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Nguy&ecirc;n tắc hoạt động li&ecirc;n tục:</span></span></span></em></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:80px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">- B&aacute;o c&aacute;o t&agrave;i ch&iacute;nh phải được lập tr&ecirc;n cơ sở giả định l&agrave; doanh nghiệp đang hoạt động li&ecirc;n tục v&agrave; sẽ tiếp tục hoạt động b&igrave;nh thường trong tương lai gần, nghĩa l&agrave; doanh nghiệp kh&ocirc;ng c&oacute; &yacute; định cũng như kh&ocirc;ng bắt buộc phỉa ngừng hoạt động hoặc thu hẹp đ&aacute;ng kể quy m&ocirc; hoạt động của m&igrave;nh. Khi doanh nghiệp chuẩn bị b&aacute;n, s&aacute;t nhập, giải thể ... th&igrave; nguy&ecirc;n tắc hoạt động li&ecirc;n t&uacute;c sẽ kh&ocirc;ng được vận dụng v&agrave;o việc lập c&aacute;c b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh. Trong trường hợp n&agrave;y, c&aacute;c t&agrave;i sản của doanh nghiệp sẽ phải phản &aacute;nh theo gi&aacute; trị thị trường.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Nguy&ecirc;n tắc gi&aacute; gốc:</span></span></span></em></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">- T&agrave;i sản phải được ghi nhận theo gi&aacute; gốc. Gi&aacute; gốc của t&agrave;i sản được t&iacute;nh theo số tiền hoặc khoản tương đương tiền đ&atilde; trả, phải trả haowcj t&iacute;nh theo gi&aacute; trị hợp l&iacute; của t&agrave;i sản đ&oacute; v&agrave;o thời điểm t&agrave;i sản được ghi nhận. Gi&aacute; gốc của t&agrave;i sản kh&ocirc;ng được thay đổi trừ khi c&oacute; quy định kh&aacute;c trong chuẩn mực kế to&aacute;n cụ thể.</span></span></span></p>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">- VD: DN mua &ocirc; t&ocirc; 4 chỗ</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Gi&aacute; mua chưa thế 800.000.000 đồng</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thuế gi&aacute; trị gia tăng VAT 10%</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Chi ph&iacute; vận chuyển 2.000.000</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lệ ph&iacute; trước bạ 15%</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Gi&aacute; trị &ocirc; t&ocirc; bằng bao nhi&ecirc;u?</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Giải:</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:13.0pt\">(+ VAT: Phương ph&aacute;p khấu trừ thuế: Kh&ocirc;ng cộng thuế</span></em></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Phương ph&aacute;p trực tiếp: Cộng thuế</span></em></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:13.0pt\">+ Lệ ph&iacute; trước bạ: Thuế đăng k&iacute; quyền sở hữu)</span></em><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp; </span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Lệ ph&iacute; trước bạ = (800+800*10%)*15% = 132 triệu đồng</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Gi&aacute; trị &ocirc; t&ocirc; = 800+2+132 = 934 triệu đồng</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; = 800+2+132+80 = 1014 triệu đồng </span></span></span></p>\r\n\r\n<ul>\r\n	<li><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Nguy&ecirc;n tắc ph&ugrave; hợp:</span></span></span></em></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">- Việc ghi nhận doanh thu v&agrave; chi ph&iacute; phải ph&ugrave; hợp với nhau. Khi ghi nhận 1 khoản doanh thu th&igrave; phải ghi nhận 1 khoản chi ph&iacute; tương ứng c&oacute; li&ecirc;n quan đến việc tạo ra doanh thu đ&oacute;. Chi ph&iacute; tương ứng với doanh thu gồm chi ph&iacute; của kỳ tạo ra doanh thu v&agrave; chi ph&iacute; của c&aacute;c kỳ trước hoặc chi ph&iacute; phải trả nhưng li&ecirc;n quan đến doanh thu của kỳ đ&oacute;.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Nguy&ecirc;n tắc nhất qu&aacute;n:</span></span></span></em></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">- C&aacute;c ch&iacute;nh s&aacute;ch v&agrave; phương ph&aacute;p kế to&aacute;n doanh nghiệp đ&atilde; chọn phải được &aacute;p dụng thống nhất &iacute;t nhất trong 1 kỳ kế to&aacute;n năm. Trường hợp c&oacute; thay đổi ch&iacute;nh s&aacute;ch v&agrave; phương ph&aacute;p kế to&aacute;n đ&atilde; chọn th&igrave; phải giải tr&igrave;nh l&iacute; do v&agrave; ảnh hưởng của sự thay đổi đ&oacute; trong phần thuyết minh b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Nguy&ecirc;n tắc th&acirc;n trọng:</span></span></span></em></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">- Thận trọng l&agrave; việc xem x&eacute;t, c&acirc;n nhắc, ph&aacute;n đo&aacute;n cần thiết để lập c&aacute;c ước t&iacute;nh kế to&aacute;n trong c&aacute;c điều kiện kh&ocirc;ng chắc chắn.</span></span></span></p>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">- Đ&ograve;i hỏi:</span></span></span></p>\r\n\r\n<p style=\"margin-left:76px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Phải lập c&aacute;c khoản dự ph&ograve;ng nhưng kh&ocirc;ng lập qu&aacute; lớn</span></span></span></p>\r\n\r\n<p style=\"margin-left:76px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Kh&ocirc;ng đ&aacute;nh gi&aacute; cao hơn gi&aacute; trị của c&aacute;c t&agrave;i sản v&agrave; c&aacute;c khoản thu nhập</span></span></span></p>\r\n\r\n<p style=\"margin-left:76px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Kh&ocirc;ng đ&aacute;nh gi&aacute; thấp hơn gi&aacute; trị của c&aacute;c khoản nợ phải trả v&agrave; chi ph&iacute;</span></span></span></p>\r\n\r\n<p style=\"margin-left:76px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Doanh thu v&agrave; thu nhập chỉ được ghi nhận khi c&oacute; bằng chứng chắc chắn về khả năng thu được lợi &iacute;ch kinh tế, c&ograve;n chi ph&iacute; phải được ghi nhận khi c&oacute; bằng chứng về khả năng ph&aacute;t sinh chi ph&iacute;.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Nguy&ecirc;n tắc trọng yếu:</span></span></span></em></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">- Th&ocirc;ng tin được coi l&agrave; trọng yếu trong trường hợp nếu thiếu th&ocirc;ng tin hoặc thiếu ch&iacute;nh x&aacute;c của th&ocirc;ng tin đ&oacute; c&oacute; thể l&agrave;m sai lệch đ&aacute;ng kể b&agrave;o c&aacute;o t&agrave;i ch&iacute;nh, l&agrave;m ảnh hưởng đến quyết định kinh tế của người sử dụng b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh. T&iacute;nh trọng yếu phụ thuộc v&agrave;o độ lớn v&agrave; t&iacute;nh chất của th&ocirc;ng tin hoặc c&aacute;c sai s&oacute;t được đ&aacute;nh gi&aacute; trong ho&agrave;n cảnh cụ thể. T&iacute;nh trọng yếu của th&ocirc;ng tin phải được xem x&eacute;t tr&ecirc;n cả phương diện định lượng v&agrave; định t&iacute;nh.</span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\">&nbsp;</p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\">C&aacute;c y&ecirc;u cầu của kế to&aacute;n</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Trung thực</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Li&ecirc;n tục</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Đầy đủ</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Kịp thời</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Dễ hiểu</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">C&oacute; thể so s&aacute;nh</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"3\" style=\"list-style-type:upper-roman\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><u><span style=\"font-size:13.0pt\">Chức năng v&agrave; nhiệm vụ của kế to&aacute;n</span></u></strong></span></span></li>\r\n</ol>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\">Chức năng</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"background-color:#fcfcff\">Tổ chức hạch to&aacute;n kế to&aacute;n, quản l&yacute; điều h&agrave;nh bộ m&aacute;y kế to&aacute;n trong doanh nghiệp</span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\">&nbsp;</p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\">Nhiệm vụ </span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"background-color:#fcfcff\">Ghi ch&eacute;p phản &aacute;nh trung thực ch&iacute;nh x&aacute;c, kịp thời, đầy đủ c&aacute;c nghiệm vụ kinh tế ph&aacute;t sinh</span></span></span></span></li>\r\n</ul>', '1552016918', null, '0', '61', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('403', 'Câu 21: Quan điểm của HCM về bản chất giai cấp của ĐCSVN?', '', '1552037658', null, '202', '55', '1', '202', '202', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('404', 'Câu 22: Khái niệm “Đảng cầm quyền” theo quan niệm của HCM. Làm rõ luận điểm: “Đảng cầm quyền vừa là người lãnh đạo, vừa là người đầy tớ trung thành của nhân dân”', '', '1552038248', null, '202', '55', '1', '202', '202', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('405', 'Câu 23: Quan niệm của HCM về mục đích, lý tưởng của Đảng cầm quyền. Làm rõ luận điểm: “Đảng cầm quyền vừa là người lãnh đạo, vừa là người đầy tớ trung thành của nhân dân”', '', '1552039755', null, '202', '55', '1', '202', '202', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('406', 'Câu 19: Quan điểm HCM về nguyên tắc, bước đi và biện pháp cụ thể trong quá trình XD XHCN?', '', '1552058926', null, '203', '55', '1', '203', '203', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('407', 'Câu 20: Quan điểm HCM về nội lực XD CNXH ở VN. Liên hệ với quá trình phát huy nội lực trong công cuộc CNH-HĐH đất nước?', '', '1552059518', null, '203', '55', '1', '203', '203', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('408', 'Chương 1: Tổng quan quản trị marketing', '<p style=\"text-align:center\"><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">CHƯƠNG 1:&nbsp; TỔNG QUAN QUẢN TRỊ MARKETING</span></span></strong></p>\r\n\r\n<ol>\r\n	<li><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">C&aacute;c kh&aacute;i niệm cốt l&otilde;i của quản trị marketing</span></span></strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Thị trường mục ti&ecirc;u v&agrave; ph&acirc;n đoạn thị trường</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Nhu cầu, mong muốn, y&ecirc;u cầu</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Ch&agrave;o h&agrave;ng thị trường v&agrave; thương hiệu</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Gi&aacute; trị v&agrave; sự thỏa m&atilde;n</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Mối quan hệ v&agrave; mạng lưới</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">K&ecirc;nh marketing</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Chuỗi cung ứng</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Cạnh tranh</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">M&ocirc;i trường marketing</span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Những thay đổi của marketing trong thế kỉ 21</span></span></strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Những thay đổi v&agrave; xu thế của marketing trong thế kỉ 21</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+ Xu hướng của nền kinh tế mới</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Những thay đổi trong thực tế kinh doanh</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Phạm vi v&agrave; tầm quan trong của marketing trong thế kỉ 21</span></span></li>\r\n</ul>\r\n\r\n<ol start=\"3\">\r\n	<li><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Hoạch định chiến lược định hướng thị trường</span></span></strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Kh&aacute;i niệm v&agrave; m&ocirc; h&igrave;nh</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">C&aacute;c bước</span></span></li>\r\n</ul>\r\n\r\n<ol start=\"4\">\r\n	<li><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Đối tượng, nội dung, nhiệm vụ, phương ph&aacute;p&nbsp; của quản trị marketing</span></span></strong></li>\r\n</ol>', '1552060394', null, '0', '62', '1', '0', null, '0', null, null, null, null, null, null, null, null);
