INSERT INTO `lesson` VALUES ('465', 'Chương 1: Tổng quan về MKT B2B', '<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Kh&aacute;i niệm v&agrave; bản chất của MKT B2B</span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; &nbsp; &nbsp; -&nbsp;</span></span></span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Kh&aacute;i niệm: </span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Marketing b2b (nghĩa hẹp) l&agrave; hoạt động mua b&aacute;n h&agrave;ng h&oacute;a v&agrave; dịch vụ giữa c&aacute;c tổ chức nhằm sử dụng trực tiếp phục vụ cho qu&aacute; tr&igrave;nh hoạt động, sản xuất c&aacute;c sản phẩm kh&aacute;c </span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Marketing b2b (nghĩa rộng) l&agrave; hoạt động mua b&aacute;n h&agrave;ng h&oacute;a v&agrave; dịch vụ giữa c&aacute;c tổ chức nhằm sử dụng trực tiếp hay gi&aacute;n tiếp tục vụ cho qu&aacute; tr&igrave;nh hoạt động, sản xuất sản phẩm kh&aacute;c hoặc b&aacute;n lại của tổ chức </span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Bản chất: B2B v&agrave; B2C vẫn l&agrave; một, kh&aacute;c biệt ch&iacute;nh l&agrave; c&aacute;ch tiếp cận v&agrave; sự vận dụng c&aacute;c c&ocirc;ng cụ/kỹ thuật của MKT trong ho&agrave;n cảnh cụ thể. </span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Hệ thống MKT B2B.</span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px\"><img src=\"/public/images/course/63/lesson/1552555832_image-20190314163433-1.png\" style=\"height:384px; width:683px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Đặc trưng: </span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Mối quan hệ giữa người mua v&agrave; người b&aacute;n rất chặt chẽ</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">C&aacute;c k&ecirc;nh ph&acirc;n phối ngắn v&agrave; trực tiếp hơn</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Nhấn mạnh đến b&aacute;n h&agrave;ng c&aacute; nh&acirc;n v&agrave; thương lượng gi&aacute;</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Nhu cầu x&acirc;y dựng v&agrave; duy tr&igrave;nh quan hệ với đối t&aacute;c.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Mối quan hệ b&ecirc;n trong doanh nghiệp</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">C&aacute;c chiến lược x&uacute;c tiến v&agrave; truyền th&ocirc;ng hợp l&yacute;</span></span></span></li>\r\n</ul>', '1552210548', null, '0', '63', '1', '0', null, '0', null, null, null, null, null, null, null, null);
