INSERT INTO `lesson` VALUES ('472', 'Câu 1(Nhóm 1)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 1(Nh&oacute;m 1)&nbsp;: </span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tr&igrave;nh b&agrave;y ph&acirc;n t&iacute;ch c&aacute;c kh&aacute;i niệm cốt l&otilde;i của Quản trị marketing : Thị trường : Ch&agrave;o h&agrave;ng thị trường . Nhu cầu , mong muốn v&agrave; y&ecirc;u cầu ; Marketing quan hệ , Mạng lưới Marketing K&ecirc;nh Marketing ? </span></span></span></span></p>', '1552297364', null, '408', '62', '1', '408', '408', '1', '', '0', null, null, null, null, null, '0');
