INSERT INTO `lesson` VALUES ('491', 'Chương 1: Tổng quan thương mại điện tử', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Chương 1: Tổng quan trong thương mại điện tử</span></span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Sự h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển của thương mại điện tử</span></span></u></em></span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">1960: + Trao đổi dữ liệu điện tử, e-mail</span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + Xử l&yacute; s&eacute;c, thẻ t&iacute;n dụng</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + Trạm giao dịch tự động</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">1970: mạng ARPANET ra đời</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">1980: mạng APRANET t&aacute;ch th&agrave;nh 2 mạng MILNET cho qu&acirc;n sự v&agrave; một mạng APRANET nhỏ hơn cho c&aacute;c địa điểm phi qu&acirc;n sự.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">1985: b&ugrave;ng nổ INTERNET</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">1995: Internet trở th&agrave;nh mạng to&agrave;n cầu</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">1997: mạng m&aacute;y t&iacute;nh Việt Nam kết nối với mạng INterner, TMĐT ra đời ở VN</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">2000: b&ugrave;ng nổ dotcom</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">2001: h&agrave;ng loạt c&ocirc;ng ty dotcom bị ph&aacute; sản</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">2002: TMĐT phục hồi, quảng c&aacute;o trực tuyến ra đời</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">2006: thời kỳ ph&aacute;t triển của web 2.0 v&agrave; mạng x&atilde; hội</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">2007: thanh to&aacute;n trực tuyến</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">2009: đ&agrave;o tạo trực tuyến</span></span></span></span>\r\n	<ol start=\"2\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Kh&aacute;i niệm thương mại điện tử</span></span></u></em></span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Kh&aacute;i niệm: TMĐT l&agrave; việc tiến h&agrave;nh c&aacute;c giao dịch thương mại qua mạng Internet, mạng truyền th&ocirc;ng v&agrave; c&aacute;c phương tiện điện tử kh&aacute;c</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Đặc điểm của thương mại điện tử</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ L&agrave; một phương thức thương mại sử dụng c&aacute;c phương tiện điện tử để tiến h&agrave;nh c&aacute;c giao dịch thương mại</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ TMĐT c&oacute; li&ecirc;n quan mật thiết đến thương mại truyền thống, v&agrave; phụ thuộc sự ph&aacute;t triển mạng m&aacute;y t&iacute;nh v&agrave; internet</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ C&aacute;c hoạt động TMĐT rất đa dạng v&agrave; phong ph&uacute;</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ &ldquo;Thương mại điện tử&rdquo; l&agrave; một thuật ngữ mang t&iacute;nh lịch sử-</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"3\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">C&aacute;c y&ecirc;u tố cấu th&agrave;nh v&agrave; c&aacute;c giai đoạn ph&aacute;t triển của thương mại điện tử</span></span></u></em></span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">C&aacute;c yếu tố cấu th&agrave;nh</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ D&ograve;ng chảy của th&ocirc;ng tin</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ D&ograve;ng chảy của h&agrave;ng h&oacute;a</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ D&ograve;ng chảy của tiền tệ</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">C&aacute;c giai đoạn ph&aacute;t triển</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Thương mại th&ocirc;ng tin: </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Th&ocirc;ng tin l&ecirc;n mạng web</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Trao đổi, đ&agrave;m ph&aacute;n, đặt h&agrave;ng qua mạng (e-mail, chat, forum&hellip;)</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Thanh to&aacute;n, giao h&agrave;ng truyền thống</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Thương mại gi&aacute;o dịch</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Hợp đồng điện tử (k&yacute; kết qua mạng)</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Thanh to&aacute;n điện tử ( thực hiện qua mạng)</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Thương mại cộng t&aacute;c</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Nội bộ doanh nghiệp c&aacute;c bộ phận li&ecirc;n kết v&agrave; kết nối với c&aacute;c đối t&aacute;c kinh doanh </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"4\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Ph&acirc;n loại thương mại điện tử</span></span></u></em></span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Ph&acirc;n loại theo mức độ số h&oacute;a, ảo h&oacute;a</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Thương mại truyền thống: tổ chức (c&ocirc;ng ty) thuần t&uacute;y vật thể</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ TMĐT từng phần: tổ chức (c&ocirc;ng ty) hỗn hợp</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ TMĐT thuần t&uacute;y: tổ chức (c&ocirc;ng ty) ảo</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Ph&acirc;n loại theo bản chất của c&aacute;c giao dịch hoặc c&aacute;c mối tương t&aacute;c</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ TMĐT giữa c&aacute;c doanh nghiệp (B2B)</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ TMĐT giữa doanh nghiệp v&agrave; người ti&ecirc;u d&ugrave;ng (B2C)</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ TMĐT doanh nghiệp &ndash; doanh nghiệp &ndash; người ti&ecirc;u d&ugrave;ng (B2B2C)</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Người ti&ecirc;u d&ugrave;ng &ndash; doanh nghiệp (C2B)</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Người ti&ecirc;u d&ugrave;ng &ndash; người ti&ecirc;u d&ugrave;ng (C2C)</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ C&aacute;c ứng dụng ngang h&agrave;ng (P2P)</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Thương mại di động ( Mobile Commerce)</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ TMĐT nội bộ doanh nghiệp</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Doanh nghiệp &ndash; nh&acirc;n vi&ecirc;n (B2E)</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Thương mại hợp t&aacute;c</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ TMĐT phi kinh doanh</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"5\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Lợi &iacute;ch v&agrave; hạn chế của TMĐT</span></span></u></em></span></span>\r\n\r\n		<ol>\r\n			<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Lợi &iacute;ch</span></span></span></span></li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;Đối với tổ chức</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Tiếp cận to&agrave;n cầu</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Giảm chi ph&iacute;</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Ho&agrave;n thiện chuỗi cung ứng</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Chuy&ecirc;n m&ocirc;n h&oacute;a người b&aacute;n</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Đ&aacute;p ứng nhu cầu c&aacute; biệt của kh&aacute;ch h&agrave;ng</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ R&uacute;t ngắn thời gian triển khai &yacute; tưởng</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Tăng hiệu quả mua h&agrave;ng </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Cải thiện quan hệ kh&aacute;ch h&agrave;ng </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ X&acirc;y dựng c&aacute;c m&ocirc; h&igrave;nh kinh doanh mới</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Cập nhật h&oacute;a tư liệu c&ocirc;ng ty</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;Đối với người ti&ecirc;u d&ugrave;ng </span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ T&iacute;nh rộng khắp</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Nhiều sự lựa chọn</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ C&aacute; biệt h&oacute;a nhu cầu của kh&aacute;ch h&agrave;ng</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Th&ocirc;ng tin sẵn t&igrave;m</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Ph&acirc;n phối nhanh ch&oacute;ng</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Sản phẩm v&agrave; dịch vụ tệ hơn</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Tham gia đấu gia</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Cộng đồng điện tử</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ B&aacute;n h&agrave;ng kh&ocirc;ng phải nộp thuế</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Đối với x&atilde; hội</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Th&ocirc;ng tin li&ecirc;n lạc được cải thiện</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ G&oacute;p phần tạo mức sống cao hơn</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ N&acirc;ng cao an ninh trong nước</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Tiếp cận c&aacute;c dịch vụ c&ocirc;ng</span></span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<ol start=\"2\">\r\n			<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Hạn chế</span></span></span></span></li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Ch&iacute; ph&iacute; ban đầu kh&aacute; lớn</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Rủi ro gặp phải trong c&aacute;c giao dịch tr&ecirc;n mạng</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Kiến thức v&agrave; khả năng ứng dụng của c&aacute;c chủ thể tham gia</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Tắc nghẽn mạng do hạ tầng viễn th&ocirc;ng c&ograve;n yếu k&eacute;m</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">+ Khả năng tiếp cận kh&aacute;ch h&agrave;ng thấp</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"6\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><u><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">C&aacute;c xu hướng mới trong TMĐT</span></span></u></em></span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Thương mại di động (Mobile commerce)</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Mua sắm cộng đồng ( Social shopping)</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">C&acirc;u lạc bộ mua sắm c&aacute; nh&acirc;n (Private shopping club)</span></span></span></span></li>\r\n</ul>', '1552319344', null, '0', '64', '1', '0', null, '0', null, null, null, null, '/images/course/64/lesson/audio/1552319378_thuong-mai-dien-tu-can-ban.docx', null, null, null);
INSERT INTO `lesson` VALUES ('493', 'Chương 2: Mô hình kinh doanh trong thương mại điện tử', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\">Chương 2: M&ocirc; h&igrave;nh kinh doanh trong thương mại điện tử</span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><u><span style=\"font-size:13.0pt\">2.1. Kh&aacute;i niệm v&agrave; c&aacute;c nh&acirc;n tố cơ bản của m&ocirc; h&igrave;nh kinh doanh trong TMĐT</span></u></em></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">2.1.1. Kh&aacute;i niệm: M&ocirc; h&igrave;nh kinh doanh thương mại điện tử l&agrave; m&ocirc; h&igrave;nh kinh doanh m&agrave; c&aacute;c doanh nghiệp đ&atilde; khai th&aacute;c v&agrave; tận dụng những đặc trưng ri&ecirc;n c&oacute; của Internet v&agrave; Web.</span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">2.1.2. C&aacute;c nh&acirc;n tố cơ bản</span></span></span></span></li>\r\n	<li style=\"list-style-type:none\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">2.1.2.1. Mục ti&ecirc;u gi&aacute; trị</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Mục ti&ecirc;u gi&aacute; trị của một doanh nghiệp l&agrave; điểm cốt yếu của m&ocirc; h&igrave;nh kinh doanh</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Trả lời c&acirc;u hỏi &ldquo;Tại sao kh&aacute;ch h&agrave;ng n&ecirc;n mua h&agrave;ng của doanh nghiệp&rdquo;.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Mục ti&ecirc;u gi&aacute; trị được hiểu l&agrave; c&aacute;ch thức để sản phẩm hay dịch vụ của một doanh nghiệp đ&aacute;p ứng được nhu cầu của kh&aacute;ch h&agrave;ng.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Th&agrave;nh c&ocirc;ng của mục ti&ecirc;u gi&aacute; trị gồm:</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">+ C&aacute; nh&acirc;n h&oacute;a, c&aacute; biệt h&oacute;a sản phẩm</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">+ Giảm bớt chi ph&iacute; t&igrave;m kiếm sản phẩm</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">+ Thuận tiện trong giao dịch</span></span></span></span></p>\r\n\r\n<p style=\"list-style-type:none\">2.<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">1.2.2. M&ocirc; h&igrave;nh doanh thu</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Kh&aacute;i niệm: + l&agrave; c&aacute;ch thức doanh nghiệp c&oacute; được doanh thu</span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + tạo lợi nhuận</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + c&oacute; mức lợi nhuận tr&ecirc;n vốn đầu tư lớn hơn c&aacute;c h&igrave;nh thức đầu tư kh&aacute;c</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Ph&acirc;n loại m&ocirc; hinh doanh thu</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">+ M&ocirc; h&igrave;nh doanh thu quảng c&aacute;o</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Đ&acirc;y l&agrave; một trong c&aacute;c m&ocirc; h&igrave;nh doanh thu cơ bản v&agrave; l&agrave; nguồn thu chủ yếu của doanh thu tr&ecirc;n Internet</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">C&aacute;c h&igrave;nh thức tồn tại: c&aacute;c trang b&aacute;o điện tử, cổng th&ocirc;ng tin,&hellip; VD: Yahoo.com, 24h.com.vn,&nbsp;&nbsp; </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">C&aacute;c h&igrave;nh tức quảng c&aacute;o: banner, h&igrave;nh ảnh, video, văn bản, dưới dạng tĩnh,&hellip;</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">+ M&ocirc; h&igrave;nh doanh thu li&ecirc;n kết</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">M&ocirc; h&igrave;nh doanh thu li&ecirc;n kết l&agrave; m&ocirc; h&igrave;nh m&agrave; doanh nghiệp sẽ nhận được khoản ph&iacute; dẫn kh&aacute;ch hoặc l&agrave; % tr&ecirc;n doanh thu của c&aacute;c hoạt động b&aacute;n h&agrave;ng dựa tr&ecirc;n cơ sở li&ecirc;n kết, giới thiệu.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Doanh thu thu được: khoản ph&iacute; dẫn kh&aacute;ch, khoản % tr&ecirc;n doanh thu của c&aacute;c hoạt động b&aacute;n h&agrave;ng,&hellip;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">VD: google.com, vnexpress.net,&hellip;</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">+ M&ocirc; h&igrave;nh doanh thu đăng k&iacute;</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Doanh nghiệp sẽ cung cấp những th&ocirc;ng tin, dịch vụ thong qua website của m&igrave;nh. Người sử dụng phải trả một khoản ph&iacute; đăng k&iacute; cho việ csuwr dụng một phần hay to&agrave;n bộ nội dung n&oacute;i tr&ecirc;n.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Y&ecirc;u cầu: nội dung c&oacute; gi&aacute; trị gia tăng cao, hạn chế người đăng k&yacute; sao ch&eacute;p những nội dung truy cập được (phần mềm DRM &ndash; Digital rights management)</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">VD: Alibaba.com,&hellip;</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">+ M&ocirc; h&igrave;nh doanh thu ph&iacute; giao dịch</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Đ&acirc;y l&agrave; m&ocirc; h&igrave;nh m&agrave; doanh nghiệp nhận được một khoản chi ph&iacute; khi c&aacute;c đối t&aacute;c thực hiện giao dịch th&ocirc;ng qua website của doanh nghiệp</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Doanh nghiệp đ&oacute;ng vai tr&ograve; l&agrave; nh&agrave; tạo thị trường, hoặc l&agrave; nh&agrave; trung gian giao dịch,</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">VD: ebay.com,&hellip;</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">+ M&ocirc; h&igrave;nh doanh thu b&aacute;n h&agrave;ng</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">L&agrave; m&ocirc; h&igrave;nh m&agrave; doanh nghiệp sẽ tạo ra doanh thu th&ocirc;ng qua việc b&aacute;n h&agrave;ng h&oacute;a, dịch vụ v&agrave; th&ocirc;ng tin cho kh&aacute;ch h&agrave;ng.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">VD: vinabook.com &ndash; nh&agrave; s&aacute;ch tr&ecirc;n mạng, thegioididong.com, dell.com &ndash; vua b&aacute;n lẻ m&aacute;y t&iacute;nh trực tuyến,&hellip;</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">So s&aacute;nh c&aacute;c m&ocirc; h&igrave;nh doanh thu</span></span></span></span></li>\r\n</ul>\r\n\r\n<table border=\"1\" cellspacing=\"0\" class=\"MsoTableGrid\" style=\"border-collapse:collapse; border:solid windowtext 1.0pt; margin-left:24px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:top; width:.95in\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:67.5pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Quảng c&aacute;o</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:93.65pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Li&ecirc;n kết</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:90.85pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Đăng k&iacute;</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:1.0in\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Ph&iacute; giao dịch</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:.95in\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">B&aacute;n h&agrave;ng</span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:top; width:.95in\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Mục đ&iacute;ch kh&aacute;ch h&agrave;ng</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:67.5pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Quảng c&aacute;o</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:93.65pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Quảng c&aacute;o</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:90.85pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Xem th&ocirc;ng tin, sử dụng dịch vụ c&oacute; ph&iacute;</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:1.0in\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Mua &ndash; b&aacute;n h&agrave;ng h&oacute;a, dịch vụ</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:.95in\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Mua h&agrave;ng h&oacute;a, dịch vụ</span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:top; width:.95in\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">H&igrave;nh thức tồn tại</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:67.5pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">B&aacute;o điện tử, cổng th&ocirc;ng tin</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:93.65pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">B&aacute;o điện tử, cổng th&ocirc;ng tin,&hellip;</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:90.85pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">S&agrave;n giao dịch, trang cung cấp nội dung, trang cho ph&eacute;p dowload b&aacute;o c&aacute;o theo ng&agrave;nh</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:1.0in\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Chợ điện tử, trung t&acirc;m thương mại,&hellip;</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:.95in\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Website mua b&aacute;n h&agrave;ng h&oacute;a, dịch vụ,&hellip;</span></span></span></span></p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:top; width:.95in\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">C&aacute;ch thức thu tiền </span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:67.5pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Thu ph&iacute; quảng c&aacute;o</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:93.65pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Ph&iacute; li&ecirc;n kết, % doanh thu b&aacute;n h&agrave;ng,&hellip;</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:90.85pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Ph&iacute; đăng k&iacute;</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:1.0in\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Chi ph&iacute; thu&ecirc; giao h&agrave;ng hoặc % hoa hồng</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:.95in\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Ph&iacute; b&aacute;n h&agrave;ng</span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:top; width:.95in\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Đối tượng thu tiền</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:67.5pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Doanh nghiệp quảng c&aacute;o</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:93.65pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Doanh nghiệp quảng c&aacute;o</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:90.85pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Kh&aacute;ch h&agrave;ng muốn xem nội dung, th&ocirc;ng tin</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:1.0in\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Doanh nghiệp b&aacute;n h&agrave;ng, dịch vụ,..</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:.95in\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Kh&aacute;ch h&agrave;ng mua h&agrave;ng</span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:top; width:.95in\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">C&aacute;ch thức hiển thị</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:67.5pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Th&ocirc;ng tin, h&igrave;nh ảnh, văn bản tĩnh</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:93.65pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Th&ocirc;ng tin, h&igrave;nh ảnh, &acirc;m thanh động, v&agrave; c&oacute; sự tương t&aacute;c với người xem</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:90.85pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Th&ocirc;ng tin,&hellip;</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:1.0in\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Sản phẩm dịch vụ,&hellip;</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:.95in\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Sản phẩm dịch vụ,&hellip;</span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"list-style-type:none\">​​​​​​​2.1.2.3.<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Cơ hội thị trường</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Cơ hội thị trường l&agrave; sự xuất hiện của c&aacute;c yếu tố hay điều kiện thuận lợi một c&aacute;ch đồng thời tại một thời điểm nhất định, sao cho việc tận dụng yếu tố đ&oacute; gi&uacute;p cho doanh nghiệp c&oacute; được sự trưởng th&agrave;nh nhanh ch&oacute;ng v&agrave; mạnh mẽ, chiếm lĩnh phần lớn thị phần tạo ra thu nhập cao v&agrave; ph&aacute;t triển bền vững.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">C&aacute;c yếu tố của cơ hội thị trường:</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">+ Nhu cầu</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">+ C&aacute;c phương tiện để thỏa m&atilde;n nhu cầu</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">+ C&aacute;c phương ph&aacute;p phối hợp phương tiện n&agrave;y nhằm thỏa m&atilde;n nhu cầu</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">+ Phương ph&aacute;p thu được lợi nhuận từ việc thỏa m&atilde;n nhu cầu</span></span></span></span></p>\r\n\r\n<p style=\"list-style-type:none\">​​​​​​​2.1.2.4.&nbsp;<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Chiến lược thị trường</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Doanh nghiệp dựng kế hoạch marketing trong d&agrave;i hạn dựa tr&ecirc;n t&igrave;nh h&igrave;nh kinh doanh hiện tại, những dự b&aacute;o về sự ph&aacute;t triển của doanh nghiệp ( nh&acirc;n lực, vật lực, t&agrave;i lực) v&agrave; những dự b&aacute;o về sự thay đổi của thị trường trong tời gian d&agrave;i</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">2.1.2.5.&nbsp;</span></span></span></span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">M&ocirc;i trường cạnh tranh</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">M&ocirc;i trường cạnh tranh của doanh nghiệp l&agrave; khả năng tương t&aacute;c giữa c&aacute;c đối thủ cạnh tranh trong c&ugrave;ng một đoạn thị trường</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">M&ocirc;i trường cạnh tranh của một doanh nghiệp chịu t&aacute;c động của yếu tố:</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">+ Số lượng đối thủ cạnh tranh</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">+ Phạm vi hoạt động của đối thủ</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">+ Thị phần của đối thủ</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">+ Mức gi&aacute; b&aacute;n của đối thủ cạnh tranh</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">C&oacute; 2 loại đối thủ cạnh tranh</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">+ Đối thủ cạnh tranh trực tiếp</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">+ Đối thủ cạnh tranh gi&aacute;n tiếp</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">M&ocirc;i trường cạnh tranh l&agrave; một trong c&aacute;c căn cứ quan trọng để đ&aacute;nh gi&aacute; tiềm năng của thị trường</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">&rarr; Như vậy, việc ph&acirc;n t&iacute;ch yếu tố m&ocirc;i trường cạnh tranh gi&uacute;p doanh nghiệp quyết định n&ecirc;n đầu tư v&agrave;o đoạn thị trường n&agrave;o c&oacute; lợi nhất.</span></span></span></span></p>\r\n\r\n<p style=\"list-style-type:none\">​​​​​​​2.1.2.6.&nbsp;<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Lợi thế cạnh tranh </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Lợi thế của một doanh nghiệp l&agrave; những g&igrave; m&agrave; doanh nghiệp c&oacute; v&agrave; l&agrave; điểm mạnh của doanh nghiệp</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Lợi thế cạnh tranh của một doanh nghiệp l&agrave; gi&aacute; trị m&agrave; bản th&acirc;n doanh nghiệp c&oacute; ưu thế v&agrave; lợi thế hơn những doanh nghiệp kh&aacute;c, c&oacute; khả năng cạnh tranh với đối thủ của m&igrave;nh </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Nhiều lợi thế, ưu thế =&gt; bất đối xứng =&gt; cung cấp sản phẩm tốt hơn, nhanh hơn, rẻ hơn.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Trong một số trường hợp, lợi thế cạnh tranh h&igrave;nh th&agrave;nh tr&ecirc;n sự bất b&igrave;nh đẳng giữa c&aacute;c doanh nghiệp</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Lợi thế cạnh tranh của doanh nghiệp c&ograve;n c&oacute; t&iacute;nh chất đ&ograve;n bẩy, gi&uacute;p doanh nghiệp ng&agrave;y c&agrave;ng mở rộng phạm vi hoạt động cũng như mở rộng lĩnh vực kinh doanh của m&igrave;nh.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">2.1.2.7 Sự ph&aacute;t triển của tổ chức</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Mọi doanh nghiệp, nhất l&agrave; c&aacute;c doanh nghiệp mới, cần c&oacute; một hệ thống tổ chức đảm bảo thực thi c&oacute; hiệu quả c&aacute;c kế hoạch v&agrave; chiến lược kinh doanh</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Nhiều doanh nghiệp kinh doanh ( TMTT &amp; TMĐT) đ&atilde; thất bại do cơ cấu tổ chức thiếu hợp l&yacute;.</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">V&igrave; thế phải lựa chọn cơ cấu tổ chức tổ chức hợp l&yacute;, ph&ugrave; hợp với m&ocirc; h&igrave;nh kinh doanh của doanh nghiệp</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Cấu tr&uacute;c chức năng TMĐT thuần t&uacute;y </span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/public/images/course/64/lesson/1552486598_xcxv.PNG\" style=\"height:149px; width:482px\" /></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"color:black\">Cấu tr&uacute;c dự &aacute;n dự &aacute;n TMĐT ngắn hạn</span></span></span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:48px\"><img alt=\"\" src=\"/public/images/course/64/lesson/1552486632_efg.PNG\" style=\"height:375px; width:518px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><u><span style=\"font-size:13.0pt\">Cấu tr&uacute;c ma trận ph&ugrave; hợp với TMĐT hỗn hợp</span></u></em></span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/public/images/course/64/lesson/1552486722_dfg.PNG\" style=\"height:346px; width:673px\" /></p>\r\n\r\n<p style=\"list-style-type:none\">​​​​​​​2.1.2.8.&nbsp;<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Đội ngũ quản trị</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Nh&acirc;n tố quan trọng nhất của một m&ocirc; h&igrave;nh kinh doanh chịu tr&aacute;ch nhiệm x&acirc;y dựng c&aacute;c mẫu c&ocirc;ng việc trong doanh nghiệp</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Doanh nghiệp c&oacute; đội ngũ quản trị giỏi l&agrave; lợi thế cạnh tranh chủ yếu của doanh nghiệp đ&oacute;</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Đội ngũ quản trị gồm:</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Nh&agrave; quản trị cấp cao: tổng gi&aacute;m đốc, chủ tịch hội đồng quản trị</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Nh&agrave; quản trị cấp chức năng: gi&aacute;m đốc marketing, gi&aacute;m đốc t&agrave;i ch&iacute;nh</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Y&ecirc;u cầu về kinh nghiệm, kiến thức, kỹ năng</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Kinh nghiệm: Trong lĩnh vực kinh doanh,&hellip;</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Kiến thức: Chuy&ecirc;n m&ocirc;n,&hellip;</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Kỹ năng: </span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Giao tiếp với nh&acirc;n vi&ecirc;n, kh&aacute;ch h&agrave;ng, đối t&aacute;c</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Phối hợp với ph&ograve;ng ban kh&aacute;c</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Nhanh nhạy về thị trường</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><u><span style=\"font-size:13.0pt\">2.2. C&aacute;c m&ocirc; h&igrave;nh kinh doanh chủ yếu trong thương mại điện tử giữa doanh nghiệp v&agrave; người ti&ecirc;u d&ugrave;ng (B2C e-commerce)</span></u></em></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">2.2.1. Cổng th&ocirc;ng tin </span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp; - Kh&aacute;i niệm: Cổng th&ocirc;ng tin (cổng th&ocirc;ng tin điện tử ) l&agrave; một website cung cấp c&aacute;c nội dung th&ocirc;ng tin c&ugrave;ng rất nhiều c&aacute;c dịch vụ trực tuyến tiện &iacute;ch tại c&ugrave;ng một vị tr&iacute; tr&ecirc;n website</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; &nbsp;&nbsp;&nbsp;- Nội dung th&ocirc;ng tin v&agrave; dịch vụ tiện &iacute;ch được cung cấp</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Cung cấp cung cụ t&igrave;m kiếm</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Cung cấp c&aacute;c dịch ụ tin tức</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ Dịch vụ thư t&iacute;n điện tử ( email), tr&ograve; chuyện trực tuyến, t&igrave;m kiếm th&ocirc;ng tin tr&ecirc;n web</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ C&aacute;c dịch vụ trực tuyến: nghe nhạc, xem phim, chơi game,&hellip;</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">+ C&aacute;c dịch vụ mua sắm trực tuyến</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Ph&acirc;n loại: + Cổng th&ocirc;ng tin theo chiều rộng</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + Cổng th&ocirc;ng tin theo chiều s&acirc;u</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">M&ocirc; h&igrave;nh doanh thu: quảng c&aacute;o, li&ecirc;n kết, đăng k&yacute;</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Mục ti&ecirc;u gi&aacute; trị: Do t&iacute;nh đa dịch vụ m&agrave; m&ocirc; h&igrave;nh cổng th&ocirc;ng tin cung cấp n&ecirc;n m&ocirc; h&igrave;nh n&agrave;y mang lại cho người sử dụng một sự đơn giản v&agrave; tiện lợi</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Cơ hội thị trường: Hầu hết người sử dụng đều th&ocirc;ng qua c&aacute;c cổng th&ocirc;ng tin để truy cập v&agrave;o những website thương mại tr&ecirc;n khắp thế giới</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">2.2.2. Nh&agrave; b&aacute;n lẻ thương mại điện tử</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Kh&aacute;i niệm: B&aacute;n lẻ điện tử l&agrave; việc b&aacute;n h&agrave;ng h&oacute;a v&agrave; dịch vụ qua Internet v&agrave; c&aacute;c k&ecirc;nh điện tử kh&aacute;c đến người ti&ecirc;u d&ugrave;ng c&aacute; nh&acirc;n v&agrave; hộ gia đ&igrave;nh bao gồm mọi h&igrave;nh thức v&agrave; quy m&ocirc;, từ những cửa h&agrave;ng rất lớn cho tới c&aacute;c cửa h&agrave;ng nhỏ b&eacute; mang t&iacute;nh chất địa phương.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">M&ocirc; h&igrave;nh doanh thu:doanh thu b&aacute;n h&agrave;ng, ph&iacute; dịch vụ</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Mục ti&ecirc;u gi&aacute; trị: Sự thuận tiện trong giao dịch mua b&aacute;n, kinh doanh, thanh to&aacute;n,. Tạo cho kh&aacute;ch h&agrave;ng sự thuận lợi trong qu&aacute; tr&igrave;nh mua b&aacute;n, tiết kiệm được thời gian, chi ph&iacute;,&hellip;</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Cơ hội thị trường: Mọi người sử dụng Internet đều c&oacute; thể l&agrave; kh&aacute;ch h&agrave;ng tiềm năng </span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">2.2.3. Nh&agrave; trung gian giao dịch </span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">C&aacute;c nh&agrave; trung gian giao dịch cung cấp những bộ xử l&yacute; giao dịch b&aacute;n h&agrave;ng trực tuyến tương tự như c&aacute;c nh&agrave; m&ocirc;i giới chứng kho&aacute;n hay c&aacute;c đại l&yacute; du lịch.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">M&ocirc; h&igrave;nh doanh thu: ph&iacute; giao dịch</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Mục ti&ecirc;u gi&aacute; trị: Tiết kiệm thời gian v&agrave; chi ph&iacute; của kh&aacute;ch h&agrave;ng, tăng hiệu suất mua b&aacute;n h&agrave;ng h&oacute;a, dịch vụ cho kh&aacute;ch h&agrave;ng v&agrave; doanh nghiệp.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">Cơ hội thị trường: C&ugrave;ng với sự ph&aacute;t triển của Internet, sự gia tăng của thị trường t&agrave;i ch&iacute;nh v&agrave; thị trường chứng kho&aacute;n, cơ hội thị trường đối với c&aacute;c nh&agrave; trung gian giao dịch trực tuyến ng&agrave;y c&aacute;ng lớn.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">2.2.4. Nh&agrave; kiến tạo thị trường</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\">L&agrave; những người x&acirc;y dựng n&ecirc;n m&ocirc;i trường số h&oacute;a&nbsp;</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">để người mua v&agrave; người b&aacute;n gặp nhau, l&agrave; nơi trưng b&agrave;y sản phẩm, thực hiện c&aacute;c hoạt động nghi&ecirc;n cứu sản phẩm v&agrave; nơi gi&aacute; của mỗi sản phẩm được thiết lập</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">M&ocirc; h&igrave;nh doanh thu: ph&iacute; đăng k&yacute;, ph&iacute; giao dịch</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Cơ hội thị trường: do sự ph&aacute;t triển của kinh doanh tr&ecirc;n mạng, l&agrave;m gia tăng số lượng c&aacute;c c&aacute; nh&acirc;n tham gia giao dịch, v&igrave; vậy m&agrave; m&ocirc; h&igrave;nh n&agrave;y c&oacute; tiềm năng rất lớn.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">2.2.5. Nh&agrave; cung cấp dịch vụ</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Nh&agrave; cung cấp dịch vụ l&agrave; những nh&agrave; cung cấp c&aacute;c dịch vụ trực tuyến cho kh&aacute;ch h&agrave;nh</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quo