INSERT INTO `lesson` VALUES ('497', 'Câu 31: Quan điểm HCM về Nhà nước của dân? Nhà nước cần làm gì để đảm bảo quyền làm chủ thật sự của NDLĐ hiện nay?', '', '1552321344', null, '203', '55', '1', '203', '203', '1', '', '0', null, null, null, null, null, '0');
