INSERT INTO `lesson` VALUES ('499', 'Câu 33: Quan điểm HCM về bản chất giai cấp công nhân của NN? Vận dụng để tăng cường hơn nữa sự lãnh đạo của Đảng đối với NN?', '', '1552352618', null, '203', '55', '1', '203', '203', '1', '', '0', null, null, null, null, null, '0');
