INSERT INTO `lesson` VALUES ('507', 'Câu 1 (Nhóm 1)', '<p><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tr&igrave;nh b&agrave;y kh&aacute;i niệm v&agrave; ph&acirc;n t&iacute;ch bản chất của marketing B2B. Ph&acirc;n t&iacute;ch c&aacute;c kh&aacute;i niệm marketing c&ocirc;ng nghiệp (Industrial marketing) v&agrave; marketing B2B (Business to business marketing). </span></span></p>', '1552355874', null, '465', '63', '1', '465', '465', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('508', 'Câu 2 (nhóm 2)', '<p><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tr&igrave;nh b&agrave;y/ph&acirc;n t&iacute;ch c&aacute;c đặc điểm, đặc trưng của marketing&nbsp;B2B </span></span></p>', '1552355924', null, '465', '63', '1', '465', '465', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('509', 'Chương 2: Chức năng và hành vi mua khách hàng tổ chức', '<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">M&ocirc; h&igrave;nh mua của kh&aacute;ch h&agrave;ng tổ chức</span></span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:48px\"><input alt=\"\" src=\"/public/images/course/63/lesson/1552556033_image-20190314163754-1.png\" style=\"height: 384px; width: 683px;\" type=\"image\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c yếu tố ảnh hưởng đến quyết định mua của kh&aacute;ch h&agrave;ng tổ chức.</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c yếu tố m&ocirc;i trường kinh doanh</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c yếu tố tổ chức</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c yếu tố quan hệ c&aacute; nh&acirc;n/trung t&acirc;m mua</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c yếu tố c&aacute; nh&acirc;n tham gia </span></span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tiến tr&igrave;nh quyết định mua của kh&aacute;ch h&agrave;ng</span></span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">3.1 T&igrave;nh huống mua </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Mua hệ thống</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Mua phục vụ nhiệm vụ mới</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">T&aacute;i đặt mua c&oacute; điều chỉnh</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">T&aacute;i đặt mua trực tiếp</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">-&gt; Sự phức tạp của quyết định giảm dần.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">3.2 C&aacute;c vai trong mua sắm </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Người sử dụng </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Người khởi xướng</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Người ảnh hưởng </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Người g&aacute;c cổng </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Người quyết định</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Người ph&ecirc; duyệt</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Người mua</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">3.3&nbsp;C&aacute;c bước trong tiến tr&igrave;nh mua </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Bước 1: &yacute; thức nhu cầu: </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Bước 2: M&ocirc; tả kh&aacute;i qu&aacute;t nhu cầu </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Bước 3: x&aacute;c định quy c&aacute;ch sản phẩm </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Bước 4: T&igrave;m kiếm nh&agrave; cung cấp </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Bước 5: y&ecirc;u cầu ch&agrave;o h&agrave;ng </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Bước 6: lựa chọn nh&agrave; cung cấp </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Bước 7: l&agrave;m thủ tục đặt h&agrave;ng </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Bước 8: đ&aacute;nh gi&aacute; kết quả thực hiện </span></span></span></span>\r\n	<ol start=\"4\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chức năng mua </span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">- Triết l&yacute; mua: chi phối h&agrave;nh vi của kh&aacute;ch h&agrave;ng l&agrave; một trong kh&iacute;a cạnh phản &aacute;nh c&aacute;c dạng quan hệ c&oacute; thể c&oacute; giữa người mua v&agrave; người b&aacute;n th&ocirc;ng qua việc người mua lựa chọn nh&agrave; cung cấp. Gồm: Triết l&yacute; mua h&agrave;ng truyền thống v&agrave; mua h&agrave;ng cộng t&aacute;c/hợp t&aacute;c.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">- Mục ti&ecirc;u mua: </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Mua&nbsp;đ&uacute;ng&nbsp;danh&nbsp;mục&nbsp;sản&nbsp;phẩm</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đ&uacute;ng số lượng </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đ&uacute;ng mức gi&aacute; </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Giao h&agrave;ng đ&uacute;ng thời điểm v&agrave; địa điểm</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">6 y&ecirc;u cầu: </span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+Chất lượng v&agrave; t&iacute;nh cảm ứng của mặt h&agrave;ng &nbsp;</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+Thời hạn v&agrave; t&iacute;nh kịp thời</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+ Gi&aacute; cả v&agrave; điều kiện thanh to&aacute;n</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+ Dịch vụ thương mại của người b&aacute;n(, trước, trong, sau b&aacute;n)</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+ Khả năng th&iacute;ch nghi của nh&agrave; cung cấp với nhu cầu đặc biệt</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+ Khối lượng v&agrave; t&iacute;nh li&ecirc;n tục của cung ứng</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">- Chiến lược mua: </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đảm bảo nguồn cung ứng</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Giảm chi ph&iacute;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hỗ trợ lẫn nhau trong cung cấp</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Thay đổi m&ocirc;i trường</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chiến lược cạnh tranh</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kỹ năng truyền thống</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kỹ năng hiện đại</span></span></span></span></li>\r\n</ul>', '1552356583', null, '0', '63', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('510', 'Câu 2 (nhóm 1)', '<p><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tr&igrave;nh b&agrave;y/ph&acirc;n t&iacute;ch sự ảnh hưởng của c&aacute;c nh&oacute;m yếu tố ảnh hưởng đến h&agrave;nh vi của kh&aacute;ch h&agrave;ng. Vận dụng để ph&acirc;n t&iacute;ch hoạt động marketing của doanh nghiệp.</span></span></p>', '1552356609', null, '509', '63', '1', '509', '509', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('511', 'Câu 3 (nhóm 2)', '<p><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tr&igrave;nh b&agrave;y/ph&acirc;n t&iacute;ch c&aacute;c t&igrave;nh huống mua h&agrave;ng trong marketing b2b. Li&ecirc;n hệ với một doanh nghiệp.</span></span></p>', '1552356638', null, '509', '63', '1', '509', '509', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('512', 'Câu 3 (nhóm 1)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tr&igrave;nh&nbsp;b&agrave;y/Ph&acirc;n&nbsp;t&iacute;ch&nbsp;vai&nbsp;tr&ograve;&nbsp;của&nbsp;những&nbsp;người&nbsp;tham gia&nbsp;v&agrave;o&nbsp;tiến&nbsp;tr&igrave;nh&nbsp;quyết&nbsp;định&nbsp;mua tr&ecirc;n&nbsp;thị&nbsp;trường&nbsp;B2B?</span></span></span></span></p>', '1552356680', null, '509', '63', '1', '509', '509', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('513', 'Câu 35:Tính sáng tạo của HCM trong giải quyết mối quan hệ giữa tính giai cấp với tính ND và tính DT của NN?', '', '1552356695', null, '203', '55', '1', '203', '203', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('514', 'Câu 4 (nhóm 1)', '<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tr&igrave;nh&nbsp;b&agrave;y&nbsp;ph&acirc;n&nbsp;t&iacute;ch&nbsp;triết&nbsp;l&yacute;&nbsp;mua,&nbsp;mục&nbsp;ti&ecirc;u mua&nbsp;v&agrave;&nbsp;chiến&nbsp;lược&nbsp;mua trong&nbsp;thị&nbsp;trường&nbsp;B2B</span></span>&nbsp;</span></span></p>', '1552356738', null, '509', '63', '1', '509', '509', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('515', 'Câu 4 (nhóm 2)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&acirc;n&nbsp;t&iacute;ch&nbsp;nội&nbsp;dung&nbsp;c&aacute;c&nbsp;bước&nbsp;trong&nbsp;tiến&nbsp;tr&igrave;nh&nbsp;quyết&nbsp;định&nbsp;mua&nbsp;của&nbsp;kh&aacute;ch&nbsp;h&agrave;ng&nbsp;trong&nbsp;marketing&nbsp;B2B?</span></span></span></span></p>', '1552356806', null, '509', '63', '1', '509', '509', '1', '', '0', null, null, null, null, null, '0');
