INSERT INTO `lesson` VALUES ('519', 'Câu 37: Các nguyên tắc xây dựng đạo đức mới theo TTHCM. Liên hệ bản thân?', '', '1552358186', null, '203', '55', '1', '203', '203', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('522', 'Chương 3: Cơ hội thị trường và chiến lược MKT B2B.', '<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Thị trường B2B </span></span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">- Đặc điểm: </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Nhu cầu</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Mục đ&iacute;ch mua </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&aacute;ch h&agrave;ng</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Sản phẩm dịch vụ</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Qu&aacute; tr&igrave;nh mua </span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">- C&aacute;c dạng thị trường </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Doanh nghiệp sản xuất</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c nh&agrave; b&aacute;n lại</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&ocirc;ng quyền v&agrave; x&atilde; hội</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Cung ứng dịch vụ</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">- T&igrave;m kiếm cơ hội thị trường</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&aacute;t triển thị trường trong số KH hiện hữu</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">T&igrave;m kiếm cơ hội từ KH hiện hữu</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">T&igrave;m kiếm KH mới</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&aacute;t triển cơ hội mới th&ocirc;ng qua nghi&ecirc;n cứu MKT.</span></span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chiến lược MKT B2B </span></span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">- C&aacute;c yếu tố cấu th&agrave;nh chiến lược:</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">X&aacute;c định thị trường &ndash; sản phẩm </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Nguồn lực</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">X&aacute;c lập mục ti&ecirc;u v&agrave; kế hoạch</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Với doanh nghiệp đa ng&agrave;nh: Ph&acirc;n bố nguồn lực t&agrave;i nguy&ecirc;n cho c&aacute;c SBUs; sự cổng hưởng.</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">- C&aacute;c bước</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&acirc;n t&iacute;ch bối cảnh (SWOT)</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Lựa chọn thị trường mục ti&ecirc;u</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">X&aacute;c định mục ti&ecirc;u marketing </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Lựa chọn marketing mix </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Thực thi v&agrave; điều khiển</span></span></span></span></li>\r\n</ul>', '1552359233', null, '0', '63', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('524', 'Câu 5 (nhóm 1)', '<p><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tr&igrave;nh&nbsp;b&agrave;y&nbsp;kh&aacute;i&nbsp;niệm&nbsp;đặc&nbsp;điểm&nbsp;v&agrave;&nbsp;c&aacute;c&nbsp;dạng&nbsp;thị&nbsp;trường?</span></span></p>', '1552359290', null, '522', '63', '1', '522', '522', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('525', 'Câu 6 (nhóm 1)', '<p><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&acirc;n&nbsp;t&iacute;ch&nbsp;c&aacute;c&nbsp;ti&ecirc;u&nbsp;chuẩn&nbsp;sử&nbsp;dụng&nbsp;ph&acirc;n&nbsp;đoạn&nbsp;thị&nbsp;trường?</span></span></p>', '1552359356', null, '522', '63', '1', '522', '522', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('526', 'Câu 5 (nhóm 2)', '<p><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tr&igrave;nh&nbsp;b&agrave;y/Ph&acirc;n&nbsp;t&iacute;ch&nbsp;nội&nbsp;dung&nbsp;t&igrave;m&nbsp;kiếm&nbsp;cơ&nbsp;hội&nbsp;thị&nbsp;trường&nbsp;B2B?</span></span></p>', '1552359401', null, '522', '63', '1', '522', '522', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('527', 'Câu 6 (nhóm 2)', '<p><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&acirc;n&nbsp;t&iacute;ch&nbsp;c&aacute;c&nbsp;yếu&nbsp;tố&nbsp;cấu&nbsp;th&agrave;nh&nbsp;chiến&nbsp;lược&nbsp;mkt&nbsp;v&agrave;&nbsp;ph&aacute;t&nbsp;triển&nbsp;chiến&nbsp;lược&nbsp;b2b.</span></span></p>', '1552359462', null, '522', '63', '1', '522', '522', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('530', 'Chương 4: Quyết định sản phẩm trong MKT B2B', '<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c vẫn đề cơ bản của sản phẩm </span></span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">- Kh&aacute;i niệm sản phẩm: sản phẩm b2b l&agrave; tổng hợp c&aacute;c lợi &iacute;ch v&agrave; giải ph&aacute;p thỏa m&atilde;n nhu cầu v&agrave; mong muốn của kh&aacute;ch h&agrave;ng tổ chức về sản xuất, kinh doanh v&agrave; hoạt động của tổ chứ.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">- Đặc trưng sản phẩm: </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Nguồn gốc nhu cầu</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Quy c&aacute;ch, thiết kế sản phẩm</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chu k&igrave; sống của sản phẩm</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c dịch vụ hỗ trợ</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Bao b&igrave; sản phẩm v&agrave; y&ecirc;u tố thẩm mỹ</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">- Cấu tr&uacute;c sản phẩm: </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><input alt=\"\" src=\"/public/images/course/63/lesson/1552556330_image-20190314164251-1.png\" style=\"height: 384px; width: 683px;\" type=\"image\" /></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">- C&aacute;c dạng sản phẩm: </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Sản phẩm hỗ trợ</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c sản phẩm nền tảng điều kiện cho sản xuất, hoạt động</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Sản phẩm h&agrave;ng h&oacute;a đầu v&agrave;o</span></span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Quản trị sản phẩm B2B </span></span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">2.1 Ph&acirc;n t&iacute;ch hồ sơ sản phẩm </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">2.2 Quyết định MKT theo c&aacute;c giai đoạn trong chu k&igrave; sống của sản phẩm </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:50px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:50px\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"3\">\r\n		<li><img src=\"/public/images/course/63/lesson/1552556330_image-20190314164251-2.png\" style=\"height:351px; width:630px\" /></li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p>2.3&nbsp;<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Quyết định ph&aacute;t triển sản phẩm mới</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">-&nbsp;</span></span></span></span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c giai đoạn ph&aacute;t triển sản phẩm mới</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">H&igrave;nh th&agrave;nh &yacute; tưởng</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">S&agrave;ng lọc v&agrave; điều tra sơ bộ</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chi tiết h&oacute;a c&aacute;c đặc điểm&nbsp;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&aacute;t triển sản phẩm </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Thử nghiệm</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tung sản phẩm </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đ&aacute;nh gi&aacute; </span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">3. Quyết định loại bỏ sản phẩm </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">4. Dịch vụ hỗ trợ </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Dịch vụ hỗ trợ sản phẩm: tập hỗn hợp c&oacute; đương lượng quan trọng của c&aacute;c h&igrave;nh thức dịch vụ thương mại trong mối quan hệ với to&agrave;n bộ qu&aacute; tr&igrave;nh tiếp thị - b&aacute;n v&agrave; ti&ecirc;u d&ugrave;ng sản phẩm x&aacute;c định m&agrave; doanh nghiệp hoạch định cung ứng cho KH trọng điểm.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tăng độ thỏa m&atilde;n ccủa kh&aacute;ch h&agrave;ng</span></span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">MKT c&ocirc;ng nghệ cao</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Bao gồm một loạt c&aacute;c lĩnh vực c&ocirc;ng nghệ</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&ocirc;ng chắc chắn </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Thị trường lu&ocirc;n thay đổi</span></span></span></span></li>\r\n</ul>', '1552360996', null, '0', '63', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('531', 'Câu 7 ( nhóm 1)', '<p><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tr&igrave;nh&nbsp;b&agrave;y/ph&acirc;n&nbsp;t&iacute;ch&nbsp;kh&aacute;i&nbsp;niệm,&nbsp;đặc&nbsp;trưng,&nbsp;cấu&nbsp;tr&uacute;c&nbsp;v&agrave;&nbsp;c&aacute;c&nbsp;dạng&nbsp;sản&nbsp;phẩm&nbsp;B2B</span></span>&nbsp;</p>', '1552361019', null, '530', '63', '1', '530', '530', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('532', 'Câu 7 ( nhóm 2)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&acirc;n t&iacute;ch đặc điểm c&aacute;c giai đoạn trong chu kỳ sống sản phẩm b2b </span></span></span></span></p>', '1552361059', null, '530', '63', '1', '530', '530', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('533', 'Câu 8 (nhóm 2)', '<p><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tr&igrave;nh&nbsp;b&agrave;y&nbsp;c&aacute;c&nbsp;giai&nbsp;đoạn&nbsp;ph&aacute;t&nbsp;triển&nbsp;sản&nbsp;phẩm&nbsp;B2B&nbsp;mới.</span></span>&nbsp;</p>', '1552361141', null, '530', '63', '1', '530', '530', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('534', 'Câu 9 ( nhóm 2)', '<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14.0pt\">Ph&acirc;n t&iacute;ch nội dung li&ecirc;n quan đến dịch vụ hỗ trợ sản phẩm.</span></span></p>', '1552361218', null, '530', '63', '1', '530', '530', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('535', 'Câu 10 (nhóm 2)', '<p><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Tr&igrave;nh b&agrave;y đặc điểm v&agrave; nội dung marketing sản phẩm c&ocirc;ng nghệ cao.</span></span></p>', '1552361263', null, '530', '63', '1', '530', '530', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('539', 'Chương 5: Quyết định kênh MKT B2B', '<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&aacute;i niệm v&agrave; đặc trưng của k&ecirc;nh MKT B2B</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&aacute;i niệm: một tập hợp c&aacute;c tổ chức v&agrave; c&aacute; nh&acirc;n độc lập v&agrave; phụ thuộc lẫn nhau tham gia v&agrave;o qu&aacute; tr&igrave;nh đưa h&agrave;ng h&oacute;a hoặc dịch vụ từ nơi sản xuất đến người ti&ecirc;u d&ugrave;ng cuối c&ugrave;ng hoặc người sử dụng c&ocirc;ng nghiệp.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chuỗi cung ứng: ứng l&agrave; một mạng lưới gồm c&aacute;c tổ chức c&oacute; li&ecirc;n quan th&ocirc;ng qua mối li&ecirc;n hệ ph&iacute;a trước v&agrave; ph&iacute;a sau, trong qu&aacute; tr&igrave;nh v&agrave; hoạt động kh&aacute;c nhau giảm sinh ra gi&aacute; trị dưới h&igrave;nh thức sản phẩm dịch vụ.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đặc trưng: </span></span></span></span></li>\r\n</ul>\r\n\r\n<ul style=\"margin-left:40px\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">K&ecirc;nh ph&acirc;n phối ngắn so với k&ecirc;nh ph&acirc;n phối trong thị trường ti&ecirc;u d&ugrave;ng </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&ocirc;ng c&oacute; nhiều loại h&igrave;nh trung gian để lựa chọn </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Việc lựa cho trung gian kh&oacute; gan </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tồn kho lu&ocirc;n l&agrave; vấn đề lớn đối với c&aacute;c doanh nghiệp trong thị trường b2b </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Quan hệ giữa sản xuất v&agrave; kh&aacute;ch h&agrave;ng, sản xuất -trung gian-kh&aacute;ch h&agrave;ng khăng kh&iacute;t </span></span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hệ thống k&ecirc;nh MKT B2B</span></span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:48px\"><img src=\"/public/images/course/63/lesson/1552556562_image-20190314164643-1.png\" style=\"height:384px; width:683px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">K&ecirc;nh trực tiếp: </span></span></span></span></li>\r\n</ul>\r\n\r\n<ul style=\"margin-left:40px\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Người b&aacute;n h&agrave;ng b&ecirc;n ngo&agrave;i</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Người b&aacute;n h&agrave;ng nội bộ</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Telemarketing </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Cataloge </span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">K&ecirc;nh gi&aacute;n tiếp</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul style=\"margin-left:40px\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Nh&agrave; ph&acirc;n phối c&ocirc;ng nghiệp</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đại diện nh&agrave; sản xuất </span></span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Thiết kế k&ecirc;nh MKT B2B </span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">X&aacute;c định mục ti&ecirc;u k&ecirc;nh</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đ&aacute;nh gi&aacute; sự lựa chọn k&ecirc;nh </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Lựa chọn c&aacute;c k&ecirc;nh ph&ugrave; hợp</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Thiết lập c&aacute;c quan hệ k&ecirc;nh</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kiểm tra v&agrave; đ&aacute;nh gi&aacute; k&ecirc;nh đ&atilde; chọn </span></span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Quản trị k&ecirc;nh</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Lựa chọn th&agrave;nh vi&ecirc;n trong k&ecirc;nh </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Động vi&ecirc;n/kh&iacute;ch lệ c&aacute;c th&agrave;nh vi&ecirc;n của k&ecirc;nh</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kiểm so&aacute;t sự xung đột k&ecirc;nh </span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">5. C&aacute;c dạng quan hệ trong k&ecirc;nh </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">K&ecirc;nh giao dịch</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">K&ecirc;nh quản trị</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">K&ecirc;nh hợp đồng </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">K&ecirc;nh tổ chức/doanh nghiệp</span></span></span></span></li>\r\n</ul>', '1552399129', null, '0', '63', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('540', 'Câu 8 (nhóm 1)', '<p><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&acirc;n&nbsp;t&iacute;ch&nbsp;một&nbsp;số&nbsp;kh&aacute;i&nbsp;niệm&nbsp;cơ&nbsp;bản,&nbsp;đặc&nbsp;trưng,&nbsp;cấu&nbsp;tr&uacute;c&nbsp;của&nbsp;k&ecirc;nh&nbsp;marketing&nbsp;b2b.</span></span></p>', '1552399199', null, '539', '63', '1', '539', '539', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('541', 'Câu 9 (nhóm 1)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&acirc;n&nbsp;t&iacute;ch&nbsp;c&aacute;c&nbsp;dạng&nbsp;k&ecirc;nhtrong&nbsp;hệ&nbsp;thống&nbsp;k&ecirc;nh&nbsp;marketing&nbsp;b2b</span></span>&nbsp;</span></span></p>', '1552399260', null, '539', '63', '1', '539', '539', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('542', 'Câu 10 (nhóm 1)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&acirc;n&nbsp;t&iacute;ch&nbsp;nội&nbsp;dung&nbsp;quản&nbsp;trị&nbsp;k&ecirc;nh&nbsp;v&agrave;&nbsp;c&aacute;c&nbsp;dạng&nbsp;quan&nbsp;hệ&nbsp;trong k&ecirc;nh.</span></span></span></span></p>', '1552399316', null, '539', '63', '1', '539', '539', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('543', 'phương trình vi phân cấp 2 giảm cấp được(dạng khuyết x)', '<p><span style=\"font-size:20px;\"><strong>+Khuyết x&nbsp;</strong>Dạng phương tr&igrave;nh <em>y&rsquo;&rsquo;=f(y&rsquo;;y)</em></span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><strong>Hướng dẫn giải&nbsp;</strong>Kiểm tra trường hợp y=0</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Trường hợp <strong><span class=\"math-tex\">\\(y \\ne 0\\)</span></strong>&nbsp;th&igrave; đặt <strong><span class=\"math-tex\">\\(y\' = p(y)\\)</span></strong> &nbsp;khi đ&oacute; <strong><span class=\"math-tex\">\\(y\'\' = \\frac{{dp\\left( y \\right)}}{{d{\\rm{x}}}} = \\frac{{dp}}{{dy}}.\\frac{{dy}}{{d{\\rm{x}}}} = p\\frac{{dp}}{{dy}}\\)</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Thay v&agrave;o phương tr&igrave;nh ta c&oacute; <strong><span class=\"math-tex\">\\(p\\frac{{dp}}{{dy}} = f(y;p)\\)</span></strong> Đ&acirc;y l&agrave; 1 phương&nbsp;tr&igrave;nh vi ph&acirc;n cấp 1 đối với h&agrave;m p biến y.</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Giả sử phương tr&igrave;nh n&agrave;y c&oacute; nghiệm l&agrave; <strong><span class=\"math-tex\">\\(p = \\varphi \\left( {y;{C_1}} \\right)\\)</span></strong> Giải tiếp phương tr&igrave;nh <strong><span class=\"math-tex\">\\(\\frac{{dy}}{{d{\\rm{x}}}} = \\varphi \\left( {y;{C_1}} \\right)\\)</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Ta được <strong><span class=\"math-tex\">\\(\\frac{{dy}}{{\\varphi \\left( {y;{C_1}} \\right)}} = d{\\rm{x}}\\)</span></strong> &nbsp;. Từ đ&acirc;y ta c&oacute; <strong><span class=\"math-tex\">\\(\\int {\\frac{{dy}}{{\\varphi \\left( {y;{C_1}} \\right)}}}  = x + {C_2}\\)</span></strong></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><b><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">VD</span></span></span></b> <span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(2y.y&#39;&#39; = {(y&#39;)^2} + {y^2}\\)</span></span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:13px; margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"line-height:115%\"><span style=\"tab-stops:4.5pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Phương tr&igrave;nh c&oacute; y=0 l&agrave; một nghiệm </span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:13px; margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"line-height:115%\"><span style=\"tab-stops:4.5pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Khi </span></span></span><span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span class=\"math-tex\">\\(y \\ne 0\\)</span></span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> đặt </span></span></span><span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span class=\"math-tex\">\\(y&#39; = p(y)\\)</span></span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> th&igrave; suy ra</span></span></span><span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span class=\"math-tex\">\\(y&#39;&#39; = p\\frac{{dp}}{{dy}}\\)</span></span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Khi đ&oacute; </span></span></span><span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(2y.p\\frac{{dp}}{{dy}} = {p^2} + {y^2}\\)</span></span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px; margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"tab-stops:4.5pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\( \\Leftrightarrow \\frac{{d{p^2}}}{{dy}} = \\frac{1}{y}{p^2} + y \\Rightarrow {p^2} = {y^2} + Cy\\)</span></span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\( \\Rightarrow d{\\rm{x}} =&nbsp; \\pm \\frac{{dy}}{{\\sqrt {{y^2} + Cy} }} \\Rightarrow x =&nbsp; \\pm \\int {\\frac{{dy}}{{\\sqrt {{y^2} + Cy} }} + D} \\)</span></span></span></span></span> </span></span></span></p>', '1552399344', null, '169', '51', '1', '169', '169', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('544', 'Câu 11 (nhóm 2)', '<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14.0pt\">Ph&acirc;n t&iacute;ch nội dung c&aacute;c bước trong quyết định thiết kế k&ecirc;nh marketing b2b</span>&nbsp;</span></p>', '1552399443', null, '539', '63', '1', '539', '539', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('545', 'phương trình vi phân tuyến tính cấp 2 hệ số hằng', '<p><span style=\"font-size:20px;\"><strong>Phương tr&igrave;nh tuyến t&iacute;nh cấp 2 hệ số hằng&nbsp;</strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><strong>Dạng phương tr&igrave;nh&nbsp;&nbsp; </strong><strong><span class=\"math-tex\">\\(y\'\' + py\' + qy = f\\left( x \\right)\\)</span></strong><strong> </strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><strong>Hướng dẫn giải</strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#3498db;\"><strong>&nbsp;</strong>Bước 1 </span>Giải phương tr&igrave;nh đặc trưng&nbsp;&nbsp;&nbsp; <strong><span class=\"math-tex\">\\({\\lambda ^2} + p\\lambda  + q = 0\\)</span></strong> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#3498db;\">Bước 2 </span>T&igrave;m nghiệm tổng qu&aacute;t <strong><span class=\"math-tex\">\\(\\overline y \\)</span></strong> của phương tr&igrave;nh thuần nhất</span></p>\r\n\r\n<p>-&nbsp;<span style=\"font-size:20px;\"><span style=\"color:#e74c3c;\"><strong><span class=\"math-tex\">\\(\\overline y  = {C_1}.{e^{{\\lambda _1}x}} + {C_2}.{e^{{\\lambda _2}x}}\\)</span></strong></span> nếu&nbsp; <span style=\"color:#e74c3c;\"><strong><span class=\"math-tex\">\\({\\lambda _1} \\ne {\\lambda _2}\\)</span></strong></span> l&agrave; hai nghiệm ph&acirc;n biệt của phương tr&igrave;nh đặc trưng</span></p>\r\n\r\n<p>-&nbsp;<span style=\"font-size:20px;\"><span style=\"color:#e74c3c;\"><strong><span class=\"math-tex\">\\(\\overline y  = {C_1}.{e^{kx}} + {C_2}x.{e^{kx}}\\)</span></strong></span>nếu <span style=\"color:#e74c3c;\"><strong><span class=\"math-tex\">\\({\\lambda _1} = {\\lambda _2} = k\\)</span></strong></span>l&agrave; nghiệm k&eacute;p của phương tr&igrave;nh đặc trưng</span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#e74c3c;\"><strong><span class=\"math-tex\">\\(\\overline y  = {e^{\\alpha x}}\\left[ {{C_1}{\\rm{cos}}\\beta {\\rm{x + }}{C_2}{\\rm{sin}}\\beta {\\rm{x}}} \\right]\\)</span></strong></span>-&nbsp; nếu <span style=\"color:#e74c3c;\"><strong><span class=\"math-tex\">\\({\\lambda _{1;2}} = \\alpha  \\pm i\\beta \\)</span></strong></span> l&agrave; nghiệm phức li&ecirc;n hợp của phương tr&igrave;nh đặc trưng</span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#3498db;\">Bước 3</span> T&igrave;m nghiệm ri&ecirc;ng <strong><span class=\"math-tex\">\\(\\widehat y\\)</span></strong> của phương tr&igrave;nh kh&ocirc;ng thuần nhất</span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#e74c3c;\">*Trường hợp 1</span> <strong><span class=\"math-tex\">\\(f\\left( x \\right) = {e^{\\alpha x}}.{P_n}\\left( x \\right)\\)</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Dạng phương tr&igrave;nh <strong><span class=\"math-tex\">\\(y\'\' + py\' + qy = {e^{\\alpha x}}.{P_n}\\left( x \\right)\\)</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Khi đ&oacute; th&igrave; ta x&aacute;c định nghiệm ri&ecirc;ng như sau<br />\r\n<span style=\"color:#e74c3c;\"><strong><span class=\"math-tex\">\\(\\widehat y = {Q_n}\\left( x \\right)\\)</span></strong></span>&nbsp;nếu &alpha; kh&ocirc;ng l&agrave; nghiệm của phương tr&igrave;nh đặc trưng</span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#e74c3c;\"><strong><span class=\"math-tex\">\\(\\widehat y = x{Q_n}\\left( x \\right)\\)</span></strong></span> nếu &alpha; l&agrave; nghiệm đơn của phương tr&igrave;nh đặc trưng</span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#e74c3c;\"><strong><span class=\"math-tex\">\\(\\widehat y = {x^2}{Q_n}\\left( x \\right)\\)</span></strong></span> nếu &alpha; l&agrave; nghiệm k&eacute;p của phương tr&igrave;nh đặc trưng</span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#e74c3c;\">*Trường hợp 2</span><strong><span class=\"math-tex\">\\(f(x) = {e^{\\alpha x}}\\left[ {{P_n}\\left( x \\right){\\rm{cos}}\\beta {\\rm{x + }}{{\\rm{P}}_m}\\left( x \\right){\\mathop{\\rm s}\\nolimits} {\\rm{in}}\\beta {\\rm{x}}} \\right]\\)</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Dạng phương tr&igrave;nh <strong><span class=\"math-tex\">\\(y\'\' + py\' + qy = {e^{\\alpha x}}\\left[ {{P_n}\\left( x \\right){\\rm{cos}}\\beta {\\rm{x + }}{{\\rm{P}}_m}\\left( x \\right){\\mathop{\\rm s}\\nolimits} {\\rm{in}}\\beta {\\rm{x}}} \\right]\\)</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Khi đ&oacute; ta x&aacute;c định nghiệm ri&ecirc;ng như sau</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Nếu <span style=\"color:#e74c3c;\"><strong><span class=\"math-tex\">\\(\\alpha  \\pm i\\beta \\)</span></strong></span> kh&ocirc;ng l&agrave; nghiệm của phương tr&igrave;nh đặc trưng th&igrave; nghiệm ri&ecirc;ng c&oacute; thể t&igrave;m ở dạng</span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#e74c3c;\"><strong><span class=\"math-tex\">\\(\\widehat y = {e^{\\alpha x}}\\left[ {{Q_l}\\left( x \\right){\\rm{cos}}\\beta {\\rm{x + }}{{\\rm{R}}_l}\\left( x \\right){\\mathop{\\rm s}\\nolimits} {\\rm{in}}\\beta {\\rm{x}}} \\right]\\)</span></strong></span> với l=max{m;n}</span></p>\r\n\r\n<p><span style=\"font-size:20px;\">Nếu<span style=\"color:#e74c3c;\"><strong><span class=\"math-tex\">\\(\\alpha  \\pm i\\beta \\)</span></strong></span> l&agrave; nghiệm của phương tr&igrave;nh đặc trưng th&igrave; nghiệm ri&ecirc;ng c&oacute; thể t&igrave;m ở dạng</span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#e74c3c;\"><strong><span class=\"math-tex\">\\(\\widehat y = x{e^{\\alpha x}}\\left[ {{Q_l}\\left( x \\right){\\rm{cos}}\\beta {\\rm{x + }}{{\\rm{R}}_l}\\left( x \\right){\\mathop{\\rm s}\\nolimits} {\\rm{in}}\\beta {\\rm{x}}} \\right]\\)</span></strong></span> với l=max{m;n}</span></p>\r\n\r\n<p><span style=\"font-size:20px;\"><span style=\"color:#3498db;\">Bước 4 </span>nghiệm của phương tr&igrave;nh vi ph&acirc;n tuyến t&iacute;nh cấp 2 đ&atilde; cho c&oacute; dạng <span style=\"color:#e74c3c;\"><strong><span class=\"math-tex\">\\(y = \\overline y  + \\widehat y\\)</span></strong></span> &nbsp;</span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><b><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">VD</span></span></span></b><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"> <span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span class=\"math-tex\">\\(y\'\' - 2y\' + y = x + 1\\)</span></span></span> </span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"line-height:115%\"><span style=\"tab-stops:4.5pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background:lime\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Bước 1</span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"> Giải phương tr&igrave;nh đặc trưng</span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"line-height:115%\"><span style=\"tab-stops:4.5pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Phương tr&igrave;nh đặc trưng <span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span class=\"math-tex\">\\({\\lambda ^2} - 2\\lambda  + 1 = 0 \\Leftrightarrow {\\lambda _1} = {\\lambda _2} = 1\\)</span></span></span> </span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"line-height:115%\"><span style=\"tab-stops:4.5pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background:lime\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Bước 2</span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"> T&igrave;m nghiệm tổng qu&aacute;t<span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span class=\"math-tex\">\\(\\overline y \\)</span></span></span> &nbsp;của phương tr&igrave;nh thuần nhất </span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"line-height:115%\"><span style=\"tab-stops:4.5pt\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span class=\"math-tex\">\\(\\overline y  = {C_1}.{e^x} + {C_2}x.{e^x}\\)</span></span></span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"line-height:115%\"><span style=\"tab-stops:4.5pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background:lime\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Bước 3</span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"> T&igrave;m nghiệm ri&ecirc;ng <span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span class=\"math-tex\">\\(\\widehat y\\)</span></span></span>của phương tr&igrave;nh kh&ocirc;ng thuần nhất</span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"line-height:115%\"><span style=\"tab-stops:4.5pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Ta c&oacute;</span></span></span><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span class=\"math-tex\">\\(y\'\' - 2y\' + y = x + 1 = {e^{0{\\rm{x}}}}\\left( {x + 1} \\right)\\)</span></span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"> &nbsp;suy ra &alpha;=0&nbsp; v&agrave; n&oacute; </span></span></span><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">kh&ocirc;ng l&agrave; nghiệm của phương tr&igrave;nh đặc trưng&nbsp; v&agrave; <span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span class=\"math-tex\">\\({P_n}\\left( x \\right) = x + 1\\)</span></span></span> n&ecirc;n </span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"line-height:115%\"><span style=\"tab-stops:4.5pt\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span class=\"math-tex\">\\(\\widehat y = a + b{\\rm{x}} \\Rightarrow y\' = a;y\'\' = 0\\)</span></span></span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"line-height:115%\"><span style=\"tab-stops:4.5pt\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span class=\"math-tex\">\\( \\Rightarrow a - 2b + b{\\rm{x}} = x + 1\\)</span></span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"line-height:115%\"><span style=\"tab-stops:4.5pt\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span class=\"math-tex\">\\( \\Rightarrow a - 2b = 1;b = 1 \\Leftrightarrow a = 3;b = 1 \\Rightarrow \\widehat y = x + 3\\)</span></span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"line-height:115%\"><span style=\"tab-stops:4.5pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background:lime\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Bước 4</span></span></span></span> </span></span></span></span></p>\r\n\r\n<p style=\"margin-top:16px; margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"line-height:115%\"><span style=\"tab-stops:4.5pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Vậy nghiệm của phương tr&igrave;nh vi ph&acirc;n tuyến t&iacute;nh cấp 2 đ&atilde; cho c&oacute; dạng </span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span class=\"MTConvertedEquation\" new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"font-weight:bold\"><span style=\"font-size:16.0pt\"><span style=\"line-height:107%\"><span class=\"math-tex\">\\(y = \\overline y  + \\widehat y = {C_1}.{e^x} + {C_2}x.{e^x} + x + 3\\)</span></span></span></span></span> </span></span></span></p>', '1552399572', null, '169', '51', '1', '169', '169', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('549', 'Chương 6: Quyết định truyền thông trong MKT B2B', '<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Quyết định truyền th&ocirc;ng c&aacute; nh&acirc;n </span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Quyết định b&aacute;n h&agrave;ng c&aacute; nh&acirc;n</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul style=\"margin-left:40px\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">B&aacute;n h&agrave;ng c&aacute; nh&acirc;n: h&igrave;nh thức tr&igrave;nh b&agrave;y trực tiếp giữa nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng với một hay nhiều kh&aacute;ch h&agrave;ng với mục đ&iacute;ch l&agrave; b&aacute;n h&agrave;ng. </span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Quyết định MKT trực tiếp: h&igrave;nh thức truyền th&ocirc;ng nhằm đến việc t&aacute;c động cụ thể v&agrave;o từng c&aacute;c nh&acirc;n kh&aacute;ch h&agrave;ng nhưng kh&ocirc;ng c&oacute; sự giao tiếp đối mặt.</span></span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c quyết định truyền th&ocirc;ng đại ch&uacute;ng </span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Quảng c&aacute;o: chỉ hỗ trợ cho hoạt động b&aacute;n h&agrave;ng c&aacute; nh&acirc;n</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">X&uacute;c tiến b&aacute;n: loại h&igrave;nh cataloge; t&agrave;i liệu cho nh&agrave; bu&ocirc;n v&agrave; ph&acirc;n phối; quảng c&aacute;o hợp t&aacute;c; hội trợ triển l&atilde;m&hellip;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Quan hệ c&ocirc;ng ch&uacute;ng: l&agrave; bất k&igrave; h&igrave;nh thức th&ocirc;ng tin n&agrave;o về doanh nghiệp được đăng tải tr&ecirc;n c&aacute;c phương tiện truyền th&ocirc;ng đại ch&uacute;ng.</span></span></span></span></li>\r\n</ul>', '1552445444', null, '0', '63', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('550', 'Câu 12 (nhóm 2)', '<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14.0pt\">Tr&igrave;nh b&agrave;y nội dung c&aacute;c quyết định truyền th&ocirc;ng c&aacute; nh&acirc;n v&agrave; đại ch&uacute;ng trong marketing b2b.</span>&nbsp;</span></p>', '1552445664', null, '549', '63', '1', '549', '549', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('551', 'Chương 7: Quyết định giá trong MKT B2B', '<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&aacute;i niệm v&agrave; đặc trưng của gi&aacute; </span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&aacute;i niệm: gi&aacute; cả l&agrave; một khoản tiền gắn với sản phẩm hoặc dịch vụ==&gt;bản chất l&agrave; sự quy đổi ra tiền tệ của sản phẩm.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đặc trưng: </span></span></span></span></li>\r\n</ul>\r\n\r\n<ul style=\"margin-left:40px\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&Iacute;t quan trọng hơn </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Cầu thị trường &iacute;t nhạy cảm </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">H&igrave;nh thức định gi&aacute; theo kiểu đấu thầu </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Thương lượng về gi&aacute;</span></span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c yếu tố ảnh hưởng đến quyết định gi&aacute;</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Nhu cầu kh&aacute;ch h&agrave;ng</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chi ph&iacute;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Cạnh tranh</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chiến lược </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Nh&acirc;n tố thương mại</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Luật ph&aacute;p</span></span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Biện ph&aacute;p điều chỉnh gi&aacute; </span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Gi&aacute; ni&ecirc;m yết v&agrave; gi&aacute; thanh to&aacute;n </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Định gi&aacute; tuyến sản phẩm </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Định gi&aacute; ph&acirc;n biệt</span></span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kỹ thuật định gi&aacute; trong MKT B2B </span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đấu thầu: h&igrave;nh thức phổ biến tr&ecirc;n thị trường B2B</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Định gi&aacute; bằng đ&agrave;m ph&aacute;n</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Thu&ecirc; mướn c&oacute; k&igrave; hạn</span></span></span></span></li>\r\n</ul>', '1552446282', null, '0', '63', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('552', 'Câu 11 ( nhóm 1)', '<p><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Ph&acirc;n&nbsp;t&iacute;ch&nbsp;kh&aacute;i&nbsp;niệm,&nbsp;đặc&nbsp;trưng&nbsp;của&nbsp;gi&aacute;&nbsp;v&agrave;&nbsp;sự&nbsp;ảnh&nbsp;hưởng&nbsp;của&nbsp;nh&oacute;m&nbsp;yếu&nbsp;tố&nbsp;đến&nbsp;quyết&nbsp;định&nbsp;gi&aacute;&nbsp;trong&nbsp;marketing&nbsp;b2b.</span></span>&nbsp;</p>', '1552446327', null, '551', '63', '1', '551', '551', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('553', 'Câu 12 (nhóm 1)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tr&igrave;nh b&agrave;y c&aacute;c biện ph&aacute;p điều chỉnh gi&aacute; trong b2b.</span></span></span></span></p>', '1552446357', null, '551', '63', '1', '551', '551', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('554', 'Câu 13 (nhóm 2)', '<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14.0pt\">Tr&igrave;nh</span>&nbsp;<span style=\"font-size:14.0pt\">b&agrave;y</span>&nbsp;<span style=\"font-size:14.0pt\">nội&nbsp;dung&nbsp;c&aacute;c</span>&nbsp;<span style=\"font-size:14.0pt\">h&igrave;nh</span>&nbsp;<span style=\"font-size:14.0pt\">thức</span>&nbsp;<span style=\"font-size:14.0pt\">kỹ</span>&nbsp;<span style=\"font-size:14.0pt\">thuật</span>&nbsp;<span style=\"font-size:14.0pt\">định</span>&nbsp;<span style=\"font-size:14.0pt\">gi&aacute;&nbsp;trong&nbsp;marketing&nbsp;b2b</span>&nbsp;</span></p>', '1552446436', null, '551', '63', '1', '551', '551', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('555', 'Chương 8: Thiết lập, phát triển và quản trị mối quan hệ trong MKT B2B.', '<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Bản chất của một kh&aacute;ch h&agrave;ng trong MKT B2B </span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c cấp độ của quan hệ kh&aacute;ch h&agrave;ng </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c vấn đề chủ yếu về mối quan hệ người b&aacute;n v&agrave; người mua trong thị trường B2B</span></span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Nội dung v&agrave; quan điểm quản trị mối quan hệ kh&aacute;ch h&agrave;ng</span></span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Quan điểm về CRM: </span></span></span></span></li>\r\n</ul>\r\n\r\n<ul style=\"margin-left:40px\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Crm như một giải ph&aacute;p c&ocirc;ng nghệ </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Crm như l&agrave; một quy tr&igrave;nh kinh doanh hay b&aacute;n h&agrave;ng </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">CRV l&agrave; một chiến lược kinh doanh của doanh nghiệp </span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Vai tr&ograve; của CRM: </span></span></span></span></li>\r\n</ul>\r\n\r\n<ul style=\"margin-left:40px\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Sử dụng đ&ograve;n bẩy c&ocirc;ng nghệ l&agrave; phương tiện để: </span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:120px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+ Quản trị c&aacute;c mối quan hệ v&agrave; t&iacute;ch hợp c&aacute;c b&ecirc;n Hữu quan </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:120px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+ Quản trị khối lượng dữ liệu </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:120px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+ Mang gi&aacute; trị vượt trội cho kh&aacute;ch h&agrave;ng</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&aacute;c nội dung quản trị mối quan hệ kh&aacute;ch h&agrave;ng</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul style=\"margin-left:40px\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hợp t&aacute;c mua </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hợp t&aacute;c b&aacute;n </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hợp t&aacute;c nội bộ </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hợp t&aacute;c chiều ngang </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&oacute; c&oacute; hai loại h&igrave;nh quan hệ hợp t&aacute;c marketing </span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:120px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+ Hợp t&aacute;c thương hiệu </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:120px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">+ Hợp t&aacute;c marketing cộng sinh </span></span></span></span></p>', '1552447507', null, '0', '63', '1', '0', null, '0', null, null, null, null, null, null, null, null);
