INSERT INTO `lesson` VALUES ('557', 'Câu 14 (nhóm 2)', '<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14.0pt\">Ph&acirc;n&nbsp;t&iacute;ch&nbsp;quan&nbsp;điểm</span>&nbsp;<span style=\"font-size:14.0pt\">v&agrave;</span>&nbsp;<span style=\"font-size:14.0pt\">nội&nbsp;dung&nbsp;quản</span>&nbsp;<span style=\"font-size:14.0pt\">trị</span>&nbsp;<span style=\"font-size:14.0pt\">mối&nbsp;quan&nbsp;hệ</span>&nbsp;<span style=\"font-size:14.0pt\">kh&aacute;ch</span>&nbsp;<span style=\"font-size:14.0pt\">h&agrave;ng.</span></span></p>', '1552447624', null, '555', '63', '1', '555', '555', '1', '', '0', null, null, null, null, null, '0');
