INSERT INTO `lesson` VALUES ('574', 'Câu 2 (Nhóm 1)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 2 : N&ecirc;u v&agrave; tr&igrave;nh b&agrave;y c&aacute;c xu hướng của nền kinh tế mới , v&agrave; c&aacute;c thay đổi trong thực tiễn marketing ? </span></span></strong></span></span></p>', '1552484724', null, '408', '62', '1', '408', '408', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('575', 'Câu 3 (Nhóm 1)', '<p><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 3 : </span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">N&ecirc;u v&agrave; tr&igrave;nh b&agrave;y c&aacute;c nhiệm vụ của quản trị marketing ? </span></span></p>', '1552485672', null, '408', '62', '1', '408', '408', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('576', 'Chương 2: SÁNG TẠO GIÁ TRỊ, SỰ THỎA MÃN, GÌN GIỮ KHÁCH HÀNG', '<p style=\"margin-left:24px; text-align:center\"><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">CHƯƠNG 2: S&Aacute;NG TẠO GI&Aacute; TRỊ, SỰ THỎA M&Atilde;N, G&Igrave;N GIỮ KH&Aacute;CH H&Agrave;NG </span></span></strong></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Kh&aacute;i niệm gi&aacute; trị v&agrave; sự thỏa m&atilde;n kh&aacute;ch h&agrave;ng</span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Kh&aacute;i niệm gi&aacute; trị cảm nh&acirc;n của kh&aacute;ch h&agrave;ng: Sự đ&aacute;nh gi&aacute; của kh&aacute;ch h&agrave;ng về sự kh&aacute;ch biệt giữa cả lợi &iacute;ch v&agrave; chi ph&iacute; marketing đưa ra trong mối li&ecirc;n quan đến ch&agrave;o h&agrave;ng đối thủ. Bao gồm c&aacute;c gi&aacute; trị lới &iacute;ch của kh&aacute;ch h&agrave;ng theo thiết kế của doanh nghiệp v&agrave; gi&aacute; trị nhận thức được.</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">M&ocirc; h&igrave;nh gi&aacute; trị cảm nhận của kh&aacute;ch h&agrave;ng</span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:center\"><img src=\"/public/images/course/62/lesson/1552960534_image-20190319085947-1.png\" style=\"height:450px; width:800px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Kh&aacute;i niệm sự thỏa m&atilde;n kh&aacute;ch h&agrave;ng: trạng th&aacute;i được cảm thụ bởi một người th&ocirc;ng qua một hiệu suất đ&aacute;p ứng được kỳ vọng của người đ&oacute;</span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Bản chất của c&aacute;c doanh nghiệp c&oacute; hiệu năng cao</span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">4 Yếu tố th&agrave;nh c&ocirc;ng</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+C&aacute;c b&ecirc;n hữu quan</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+C&aacute;c tiến tr&igrave;nh</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+C&aacute;c nguồn lực</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Tổ chức v&agrave; văn h&oacute;a tổ chức</span></span></p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Cung ứng gi&aacute; trị v&agrave; sự thỏa m&atilde;n cho kh&aacute;ch h&agrave;ng</span></span>\r\n\r\n	<ol>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Chuỗi gi&aacute; trị</span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">C&aacute;c thực tiễn kinh doanh cốt l&otilde;i</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+CẢm nhận thị trường</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Thu h&uacute;t kh&aacute;ch h&agrave;ng</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Quản trị quan hệ kh&aacute;ch h&agrave;ng</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+THực hiện c&ocirc;ng t&aacute;c quản trị</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Thực hiện những cung ứng mới</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">M&ocirc; h&igrave;nh qu&aacute; trị quản trị marketing</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><img src=\"/public/images/course/62/lesson/1552960907_image.png\" style=\"height:391px; width:700px\" /></p>\r\n\r\n<p style=\"margin-left:72px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:72px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:72px\">&nbsp;</p>\r\n\r\n<ol start=\"3\">\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"2\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Mạng lưới ph&acirc;n phối gi&aacute; trị: được x&acirc;y dựng từ chuỗi gi&aacute; trị của doanh nghiệp v&agrave; c&aacute;c nh&agrave; cung cấp , ph&acirc;n phối v&agrave; tr&ecirc;n hết l&agrave; kh&aacute;ch h&agrave;ng , tất cả hợp t&aacute;c với nhau mang lại gi&aacute; trị cho kh&aacute;ch h&agrave;ng .</span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Quản trị quan hệ kh&aacute;ch h&agrave;ng v&agrave; marketing cơ sở dữ liệu kh&aacute;ch h&agrave;ng</span></span>\r\n	<ol>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Quản trị quan hệ kh&aacute;ch h&agrave;ng</span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Quản trị quan hệ kh&aacute;ch h&agrave;ng ( CRM ) : một tiến tr&igrave;nh tổng qu&aacute;t của việc x&acirc;y dựng v&agrave; duy tr&igrave; c&aacute;c mối quan hệ kh&aacute;ch h&agrave;ng c&oacute; l&atilde;i bằng việc cung cấp c&aacute;c gi&aacute; trị v&agrave; sự thỏa m&atilde;n cao hơn cho kh&aacute;ch h&agrave;ng</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Thu h&uacute;t v&agrave; giữ kh&aacute;ch h&agrave;ng</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+giảm số lượng kh&aacute;ch h&agrave;ng từ bỏ</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+ giữ v&agrave; nu&ocirc;i dưỡng kh&aacute;ch h&agrave;ng hiện tại</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">M&ocirc; h&igrave;nh c&aacute;c cấp độ marketing quan hệ</span></span></li>\r\n</ul>\r\n\r\n<table border=\"1\" cellspacing=\"0\" class=\"MsoTableGrid\" style=\"border-collapse:collapse; border:solid windowtext 1.0pt\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:top; width:116.85pt\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:116.85pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">lợi nhuận bi&ecirc;n thấp</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:116.9pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lợi nhuận bi&ecirc;n cao&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:116.9pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">lợi nhuận bi&ecirc;n trung b&igrave;nh&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:top; width:116.85pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Số kh&aacute;ch h&agrave;ng v&agrave; nh&agrave; ph&acirc;n phối nhiều</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:116.85pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Marketing tương t&aacute;c</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:116.9pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Marketing phản ứng lại</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:116.9pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Marketing căn bản/ phản ứng lại</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:top; width:116.85pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Số kh&aacute;ch h&agrave;ng v&agrave; nh&agrave; ph&acirc;n phối trung b&igrave;nh</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:116.85pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Marketing phản ứng trước</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:116.9pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Marketing tương t&aacute;c</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:116.9pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Marketing phản ứng lại</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:top; width:116.85pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Số kh&aacute;ch h&agrave;ng v&agrave; nh&agrave; ph&acirc;n phối &iacute;t</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:116.85pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Marketing cộng t&aacute;c</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:116.9pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Marketing phản ứng trước </span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:116.9pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Marketing tương t&aacute;c</span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"margin-left:72px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:72px\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">X&acirc;y dựng l&ograve;ng trung th&agrave;nh</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Th&ecirc;m c&aacute;c lợi &iacute;ch t&agrave;i ch&iacute;nh</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Th&ecirc;m c&aacute;c lợi &iacute;ch x&atilde; hội</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Th&ecirc;m c&aacute;c rang buộc về mặt cấu tr&uacute;c</span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"2\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Marketing cơ sở dữ liệu kh&aacute;ch h&agrave;ng</span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Kh&aacute;i niệm: Tập hợp c&oacute; tổ chức những dữ liệu to&agrave;n diện về kh&aacute;ch h&agrave;ngg hiện tại hay tương lai m&agrave; c&oacute; thể hoạt động để tạo ra, ph&acirc;n loại, b&aacute;n sản phẩm v&agrave; dịch vụ, đồng thời duy tr&igrave; mối quan hệ kh&aacute;ch h&agrave;ng</span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', '1552486995', null, '0', '62', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('577', 'Câu 4 (Nhóm 1):', '<p>&nbsp;Tr&igrave;nh b&agrave;y/ph&acirc;n t&iacute;ch kh&aacute;i niệm :Gi&aacute; trị cảm nhận kh&aacute;ch h&agrave;ng; sự thỏa m&atilde;n kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<div id=\"eJOY__extension_root\" style=\"all:unset\">&nbsp;</div>', '1552487042', null, '576', '62', '1', '576', '576', '1', '', '0', null, null, null, null, null, '0');
