INSERT INTO `lesson` VALUES ('580', 'Câu 5 (Nhóm 1)', '<p><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 5 : Tr&igrave;nh b&agrave;y ph&acirc;n t&iacute;ch nội dung c&aacute;c thực tiễn kinh doanh cốt l&otilde;i của doanh nghiệp ? </span></span></strong></p>', '1552488140', null, '576', '62', '1', '576', '576', '1', '', '0', null, null, null, null, null, '0');
