INSERT INTO `lesson` VALUES ('587', 'CHƯƠNG 4: HOẠT ĐỘNG SẢN XUẤT CỦA DOANH NGHIỆP', '<p><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><a name=\"_Toc1665886\"><span style=\"color:#000000\"><strong><span style=\"font-size:18.0pt\">CHƯƠNG 4: HOẠT ĐỘNG SẢN XUẤT CỦA DOANH NGHIỆP</span></strong></span></a></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">&nbsp;</li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><a name=\"_Toc1665887\"><span style=\"color:#000000\"><strong><span style=\"font-size:16.0pt\">4.1 Kh&aacute;i niệm v&agrave; mục ti&ecirc;u của sản xuất</span></strong></span></a></span></span></p>\r\n\r\n<p><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><a name=\"_Toc1665888\"><span style=\"color:#000000\"><strong><span style=\"font-size:16.0pt\">4.1.1&nbsp;Kh&aacute;i niệm sản xuất</span></strong></span></a></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">- Sản xuất l&agrave; qu&aacute; tr&igrave;nh biến đổi c&aacute;c nguồn lực th&agrave;nh của cải, dịch vụ nhằm đ&aacute;p ứng nhu cầu của thị trường.</span></span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><strong><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">4.1.2 Mục ti&ecirc;u của sản xuất:</span></span></span></strong></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Tối đa h&oacute;a kết quả của hoạt động sản xuất với sự sử dụng tiết kiệm c&aacute;c nguồn lực.</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">&nbsp;</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><a name=\"_Toc1665889\"><span style=\"color:#000000\"><strong><span style=\"font-size:16.0pt\">4.2 C&aacute;c loại h&igrave;nh sản xuất</span></strong></span></a></span></span></p>\r\n\r\n<p><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><a name=\"_Toc1665890\"><span style=\"color:#000000\"><strong><span style=\"font-size:16.0pt\">4.2.1 Sản xuất đơn chiếc</span></strong></span></a><span style=\"color:#000000\"><strong> </strong></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Sản xuất đơn chiếc l&agrave; sản xuất ra sản phẩm để đ&aacute;p ứng nhu cầu của một kh&aacute;ch h&agrave;ng </span></span></span></span></li>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Sản xuất đơn chiếc</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Thực hiện khi kh&aacute;ch h&agrave;ng đặt h&agrave;ng</span></span></span></span></li>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Kh&ocirc;ng chịu chi ph&iacute; tồn kho</span></span></span></span></li>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">&Aacute;p dụng cho sản phẩm c&oacute; gi&aacute; trị lớn, c&oacute; đặc t&iacute;nh ri&ecirc;ng</span></span></span></span></li>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Năng suất thấp</span></span></span></span>\r\n	<ol>\r\n		<li style=\"list-style-type:none\">&nbsp;</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:16.0pt\"><span style=\"color:#000000\">&nbsp;4.2.2</span><a name=\"_Toc1665891\"><span style=\"color:#000000\">Sản xuất h&agrave;ng loạt</span></a><span style=\"color:#000000\"> </span></span></strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">L&agrave; sản xuất sử dụng thiết bị, c&ocirc;ng nghệ đắt tiền, c&oacute; c&ocirc;ng suất chế tạo lớn, sản xuất theo d&acirc;y chuyền</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Sản xuất h&agrave;ng loạt </span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Lao động chuy&ecirc;n m&ocirc;n h&oacute;a cao</span></span></span></span></li>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">C&oacute; t&iacute;nh ti&ecirc;u chuẩn h&oacute;a cao</span></span></span></span></li>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Kh&ocirc;ng đ&aacute;p ứng được c&aacute;c nhu cầu ri&ecirc;ng biệt của kh&aacute;ch h&agrave;ng</span></span></span></span></li>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Năng suất lao động cao hơn</span></span></span></span>\r\n	<ol>\r\n		<li style=\"list-style-type:none\">&nbsp;</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><a name=\"_Toc1665892\"><span style=\"color:#000000\"><strong><span style=\"font-size:16.0pt\">4.2.3 Sản xuất theo qu&aacute; tr&igrave;nh</span></strong></span></a></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">L&agrave; sản xu&aacute;t li&ecirc;n tục ra c&aacute;c sản phẩm m&agrave; ch&uacute;ng kh&ocirc;ng c&oacute; sự kh&aacute;c biệt h&oacute;a trước c&ocirc;ng đoạn cuối c&ugrave;ng của sản xuất</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Sản xuất theo qu&aacute; tr&igrave;nh </span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">&nbsp;Sản xuất khối lượng lớn </span></span></span></span></li>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Chỉ sản xuất 1 loại chi tiết SP hoặc bước c&ocirc;ng việc </span></span></span></span></li>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Thường sử dụng m&aacute;y m&oacute;c, thiết bị chuy&ecirc;n d&ugrave;ng</span></span></span></span></li>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Đường đi ngắn, SP dở dang &iacute;t</span></span></span></span>\r\n	<ol>\r\n		<li style=\"list-style-type:none\">&nbsp;</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><a name=\"_Toc1665893\"><span style=\"color:#000000\"><strong><span style=\"font-size:16.0pt\">4.2.4 Tự động h&oacute;a</span></strong></span></a></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Tự động h&oacute;a l&agrave; loại h&igrave;nh sản xuất sử dựng m&aacute;y m&oacute;c thay thế cho con người trong vận h&agrave;nh, kiểm so&aacute;t hoạt động của m&aacute;y m&oacute;c </span></span></span></span></li>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">VD: Sử dụng robot </span></span></span></span>\r\n	<ol>\r\n		<li style=\"list-style-type:none\">&nbsp;</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><a name=\"_Toc1665894\"><span style=\"color:#000000\"><strong><span style=\"font-size:16.0pt\">4.2.5 Chương tr&igrave;nh h&oacute;a sản xuất</span></strong></span></a></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">L&agrave; loại h&igrave;nh sản xuất theo đ&oacute; người ta thiết kế một hệ thống sản xuất gồm cả kh&acirc;u sản xuất v&agrave; vận chuyển, kiểm tra sản phẩm được sắp xếp liền kề nhau do con người chỉ huy trực tiếp dưới sự trợ gi&uacute;p của m&aacute;y t&iacute;nh với những phần mềm quản l&yacute;.</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><a name=\"_Toc1665895\"><span style=\"color:#000000\"><strong><span style=\"font-size:16.0pt\">4.2.6 Tổ chức sản xuất</span></strong></span></a></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Tổ chức sản xuất l&agrave; qu&aacute; tr&igrave;nh tổ chức phối hợp, hợp l&yacute; c&aacute;c yếu tố của qu&aacute; tr&igrave;nh sản xuất ( lao động, vật chất, t&agrave;i ch&iacute;nh, th&ocirc;ng tin) để tạo ra của cải, dịch vụ. </span></span></span></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14pt\"><span style=\"font-family:Cambria,serif\"><a name=\"_Toc1665896\"><span style=\"color:#000000\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">4.3.1 Chuẩn bị sản xuất</span></span></span></a></span></span></strong></h3>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">-&nbsp; L&agrave; qu&aacute; tr&igrave;nh gồm c&aacute;c giai đoạn kh&aacute;c nhau nhằm biến &yacute; tưởng về sản phẩm th&agrave;nh sản phẩm thực tế đ&aacute;p ứng nhu cầu kh&aacute;ch h&agrave;ng, thị trường </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Sản xuất c&aacute;i g&igrave;? </span></span></span></span></li>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Sản phẩm n&agrave;o c&oacute; thể sản xuất? Sản xuất bằng c&aacute;ch n&agrave;o</span></span></span></span></li>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Khi n&agrave;o đưa sản phẩm ra thị trường? Ai thực hiện qu&aacute; tr&igrave;nh sản xuất? Sản xuất ở đ&acirc;u? Cần bao nhi&ecirc;u thời gian để sản xuất ra sản phẩm? </span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Để giải quyết c&aacute;c vấn đề tr&ecirc;n, Dn cần tổ chức c&aacute;c bộ phận chức năng của sản xuất, thực hiện sự ph&acirc;n c&ocirc;ng, phối hợp của bộ phận n&agrave;y</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\"><strong>- C&aacute;c bộ phận sản xuất:</strong></span></span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">- Bộ phận nghi&ecirc;n cứu: nghi&ecirc;n cứu kh&aacute;ch h&agrave;ng, thị trường, từ đ&oacute; đề xuất &yacute; tưởng về sản phẩm dự định sản xuất.</span></span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">- Bộ phận phương ph&aacute;p: Ch&iacute;nh x&aacute;c h&oacute;a việc sản xuất sản phẩm bằng phương ph&aacute;p cụ thể, lựa chọn m&aacute;y m&oacute;c, thiết bị sản xuất hoặc c&ocirc;ng cụ sản xuất v&agrave; thời gian chế tạo sản phẩm.</span></span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">- Bộ phận điều h&agrave;nh tổ chức điều h&agrave;nh qu&aacute; tr&igrave;nh sản xuất bằng việc tổ chức, ph&acirc;n c&ocirc;ng, phối hợp c&aacute;c kh&acirc;u, c&ocirc;ng đoạn của qu&aacute; tr&igrave;nh sản xuất. </span></span></span></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14pt\"><span style=\"font-family:Cambria,serif\"><a name=\"_Toc1665898\"><span style=\"color:#000000\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">4.3.2 C&aacute;c kiểu tổ chức sản xuất chủ yếu</span></span></span></a></span></span></strong></h3>\r\n\r\n<p><em><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">4.3.2.1 &nbsp;Tổ chức sản xuất cổ điển:</span></span></span></span></em></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">C&aacute;c kiểu tổ chức sản xuất cổ điển gồm: </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Sản xuất được bố tr&iacute; ở nơi cố định trong đ&oacute; c&aacute;c hoạt động sản xuất diễn ra ở một số nơi cố định trong đ&oacute; c&aacute;c hoạt động sản xuất diễn ra ở một số nơi cố định như sản xuất t&agrave;u thủy, m&aacute;y bay....</span></span></span></span></li>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Sản xuất được bố tr&iacute; theo qu&aacute; tr&igrave;nh (li&ecirc;n tục) như đ&uacute;c, sản xuất thủy tinh, dệt sợi, bột giấy</span></span></span></span></li>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Sản xuất theo bộ phận theo đ&oacute; nguy&ecirc;n liệu đầu v&agrave;o đi qua c&aacute;c bộ phận gia c&ocirc;ng v&agrave; kết th&uacute;c cho ra sản phẩm (th&agrave;nh phẩm) </span></span></span></span></li>\r\n</ul>\r\n\r\n<p><em><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">4.3.2.2 Tổ chức sản xuất theo nh&oacute;m </span></span></span></span></em></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">L&agrave; kiểu tổ chức kh&ocirc;ng thiết kế quy tr&igrave;nh c&ocirc;ng nghệ, bố tr&iacute; m&aacute;y m&oacute;c để sản xuất từng loại sản phẩm m&agrave; l&agrave;m chung cả nh&oacute;m sau đ&oacute; đưa v&agrave;o chi tiết tổng hợp</span></span></span></span></li>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">C&aacute;c chi tiết của một nh&oacute;m được gia c&ocirc;ng trong một lần điều chỉnh m&aacute;y</span></span></span></span></li>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Để sản xuất một sản phẩm c&oacute; nhiều chi tiết tổng hợp mỗi nh&oacute;m sẽ sản xuất 1 chi tiết tổng hợp sau đ&oacute; lắp r&aacute;p ra th&agrave;nh phẩm.</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><em><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">4.3.2.3 Tổ chức theo Just-in-time (J.I.T) </span></span></span></span></em></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Hệ thống sản xuất JIT l&agrave; hệ thống đồng bộ h&oacute;a c&aacute;c thao t&aacute;c của tất cả c&aacute;c bộ phận trong doanh nghiệp, theo nguy&ecirc;n tắc l&agrave; chỉ sản xuất khi kh&aacute;ch h&agrave;ng c&oacute; nhu cầu, cung cấp sản phẩm đ&uacute;ng l&uacute;c họ cần, kh&ocirc;ng c&oacute; đơn h&agrave;ng th&igrave; kh&ocirc;ng sản xuất. </span></span></span></span></li>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">JIT l&agrave; sản xuất kh&ocirc;ng c&oacute; dự trữ sản phẩm, do đ&oacute; tiết kiệm chi ph&iacute;, thường sản xuất l&ocirc; nhỏ.</span></span></span></span></li>\r\n</ul>\r\n\r\n<h3><strong><span style=\"font-size:14pt\"><span style=\"font-family:Cambria,serif\"><a name=\"_Toc1665899\"><span style=\"color:#000000\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">4.3.3 Đảm bảo chất lượng sản xuất</span></span></span></a></span></span></strong></h3>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">- Chất lượng sản phẩm l&agrave; năng lực của sản phẩm / dịch vụ thỏa m&atilde;n nhu cầu người sử dụng</span></span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">- Lợi &iacute;ch của chất lượng sản phẩm: </span></span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">- Biện ph&aacute;p đảm bảo chất lượng sản phẩm:</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Kiểm tra trước khi chế tạo sản phẩm</span></span></span></span></li>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Kiểm tra trong qu&aacute; tr&igrave;nh sản xuất</span></span></span></span></li>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Kiểm tra th&agrave;nh phẩm, loại bỏ phế phẩm</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><a name=\"_Toc1665900\"><span style=\"color:#000000\"><strong><span style=\"font-size:16.0pt\">4.4 Chi ph&iacute; v&agrave; gi&aacute; th&agrave;nh sản xuất</span></strong></span></a></span></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14pt\"><span style=\"font-family:Cambria,serif\"><a name=\"_Toc1665901\"><span style=\"color:#000000\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">4.4.1 Chi ph&iacute; sản xuất:</span></span></span></a></span></span></strong></h3>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">- L&agrave; biểu hiện bằng tiền của c&aacute;c yếu tố của qu&aacute; tr&igrave;nh sản xuất m&agrave; DN sử dụng để chế tạo sản phẩm </span></span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">- Ph&acirc;n loại chi ph&iacute; sản xuất: Cho biết tỷ trọng v&agrave; kết cấu từng loại chi ph&iacute; sản xuất ở trong 1 thời kỳ</span></span></span></span></p>\r\n\r\n<h3><strong><span style=\"font-size:14pt\"><span style=\"font-family:Cambria,serif\"><a name=\"_Toc1665902\"><span style=\"color:#000000\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">4.4.2 Gi&aacute; th&agrave;nh sản xuất:</span></span></span></a></span></span></strong></h3>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">L&agrave; biểu hiện bằng tiền của to&agrave;n bộ c&aacute;c yếu tố của qu&aacute; tr&igrave;nh sản xuất t&iacute;nh tr&ecirc;n một sản phẩm sản xuất</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Gi&aacute; th&agrave;nh Sx t&iacute;nh tr&ecirc;n 1 SP: <span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span class=\"math-tex\">\\(Z = \\frac{{TF}}{Q}\\)</span></span></span></span></span></span></li>\r\n</ul>\r\n\r\n<h3><strong><span style=\"font-size:14pt\"><span style=\"font-family:Cambria,serif\"><a name=\"_Toc1665903\"><span style=\"color:#000000\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">4.4.3 Kết quả hoạt động sản xuất:</span></span></span></a></span></span></strong></h3>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">KQHĐSX l&agrave; to&agrave;n bộ sản lượng sản xuất/ gi&aacute; trị sản xuất trong một thời kỳ</span></span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">C&aacute;ch t&iacute;nh gi&aacute; trị sản xuất (GO)</span></span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(GO = \\sum\\limits_{i = 1}^n {Qi*Pi} \\)</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">Trong đ&oacute;: Qi= sản lượng sản phẩm i</span></span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:14pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\">&nbsp;Pi = Đơn gi&aacute; sản xuất</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>', '1552538986', null, '0', '45', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('588', 'CHƯƠNG 5: HOẠT ĐỘNG DỊCH VỤ CỦA DOANH NGHIỆP', '<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><a name=\"_Toc1665904\"><span style=\"color:#000000\"><span style=\"font-size:16.0pt\">CHƯƠNG 5: HOẠT ĐỘNG DỊCH VỤ CỦA DOANH NGHIỆP</span></span></a><span style=\"color:#000000\"> </span></span></span></p>\r\n\r\n<p style=\"list-style-type:none\">&nbsp;</p>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><a name=\"_Toc1665905\"><span style=\"color:#000000\"><strong><span style=\"font-size:16.0pt\">5.1 Kh&aacute;i niệm, đặc điểm, c&aacute;c yếu tố đ&aacute;nh gi&aacute; chất lượng của dịch vụ</span></strong></span></a></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><a name=\"_Toc1665906\"><span style=\"color:#000000\"><strong><span style=\"font-size:16.0pt\">5.1.1 Kh&aacute;i niệm dịch vụ</span></strong></span></a></span></span></p>\r\n\r\n<p><em><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">Dịch vụ l&agrave; <strong>hoạt động</strong> sinh ra do tiếp x&uacute;c giữa b&ecirc;n <strong>cung ứng</strong> v&agrave; <strong>kh&aacute;ch h&agrave;ng</strong> v&agrave; c&aacute;c hoạt động <strong>nội bộ</strong> b&ecirc;n cung ứng để <strong>đ&aacute;p ứng nhu cầu</strong> của kh&aacute;ch h&agrave;ng. </span></span></span></span></em></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><a name=\"_Toc1665907\"><span style=\"color:#000000\"><strong><span style=\"font-size:16.0pt\">5.1.2 Đặc điểm của dịch vụ</span></strong></span></a></span></span></p>\r\n\r\n<p><em><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">- Sản phẩm dịch vụ c&oacute; t&iacute;nh v&ocirc; h&igrave;nh </span></span></span></span></em></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">&nbsp;T&iacute;nh chất kh&ocirc;ng x&aacute;c định của chất lượng dịch vụ hay c&ograve;n gọi l&agrave; t&iacute;nh chất kh&ocirc;ng đồng nhất về chất lượng</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">T&iacute;nh chất kh&ocirc;ng t&aacute;ch rời giữa tạo ra sản phẩm dịch vụ với ti&ecirc;u d&ugrave;ng sản phẩm dịch vụ</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">Sự tồn kho của dịch vụ l&agrave; kh&oacute; khăn hơn so với h&agrave;ng h&oacute;a.</span></span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:24px\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<p style=\"list-style-type:none\">&nbsp;</p>\r\n\r\n	<ol>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><a name=\"_Toc1665908\"><span style=\"color:#000000\"><strong><span style=\"font-size:16.0pt\">5.1.3 C&aacute;c yếu tố đ&aacute;nh gi&aacute; chất lượng dịch vụ</span></strong></span></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">Sản phẩm dịch vụ ổn định, kh&ocirc;ng sai s&oacute;t</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">Cơ sở vật chất, trang bị kỹ thuật đầy đủ, địa điểm thuận lợi, khang trang, đẹp đẽ, trang thiết bị hiện đại.</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">Khả năng th&agrave;nh thạo v&agrave; hiểu biết tốt của nh&acirc;n vi&ecirc;n về chuy&ecirc;n m&ocirc;n, nghiệp vụ, tr&igrave;nh độ quản l&yacute;, khả năng giao tiếp, phong c&aacute;ch l&agrave;m việc.</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">Sẵn s&agrave;ng đ&aacute;p ứng đầy đủ, kịp thời nhu cầu của KH</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">T&iacute;nh trung thực, uy t&iacute;n của DN, Sp đối với kh&aacute;ch h&agrave;ng.</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">An to&agrave;n trong qu&aacute; tr&igrave;nh sử dụng dịch vụ.</span></span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><a name=\"_Toc1665909\"><span style=\"color:#000000\"><strong><span style=\"font-size:16.0pt\">5.2 Qu&aacute; tr&igrave;nh cung ứng dịch vụ:</span></strong></span></a></span></span></p>\r\n\r\n<p><em><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">Gồm 3 giai đoạn tuần ho&agrave;n </span></span></span></span></em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<p style=\"list-style-type:none\">&nbsp;</p>\r\n\r\n	<ol>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><a name=\"_Toc1665910\"><span style=\"color:#000000\"><strong><span style=\"font-size:16.0pt\">5.2.1 KH tiếp cận v&agrave; đăng k&yacute; sử dụng dịch vụ</span></strong></span></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">Đ&acirc;y l&agrave; giai đoạn đầu ti&ecirc;n của qu&aacute; tr&igrave;nh cung ứng DV, người cung ứng phải c&oacute; cơ sở vật chất, địa điểm thuận lợi v&agrave; tạo điều kiện để kh&aacute;ch h&agrave;ng c&oacute; thể tiếp cận v&agrave; t&igrave;m hiểu DV</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">T&igrave;m hiểu nhu cầu kh&aacute;ch h&agrave;ng, giới thiệu sản phẩm</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">Thương lượng v&agrave; k&yacute; hợp đồng cung ứng cho kh&aacute;ch h&agrave;ng.</span></span></span></span></p>\r\n\r\n	<p style=\"list-style-type:none\">&nbsp;</p>\r\n\r\n	<ol>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><a name=\"_Toc1665911\"><span style=\"color:#000000\"><strong><span style=\"font-size:16.0pt\">5.2.2 Tiến h&agrave;nh cung ứng dịch vụ</span></strong></span></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">L&agrave; giai đoạn quyết định sự th&agrave;nh c&ocirc;ng trong cung ứng dịch vụ. Nh&acirc;n vi&ecirc;n phải căn cứ theo y&ecirc;u cầu của kh&aacute;ch h&agrave;ng để tiến h&agrave;nh nhằm thỏa m&atilde;n nhu cầu kh&aacute;ch h&agrave;ng</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">Phải t&aacute;c động tới kh&aacute;ch h&agrave;ng hoặc t&agrave;i sản của kh&aacute;ch h&agrave;ng để h&igrave;nh th&agrave;nh dịch vụ, phải hiểu ch&iacute;nh x&aacute;c, kịp thời th&ocirc;ng tin của kh&aacute;ch để thao t&aacute;c đ&uacute;ng v&agrave; c&ugrave;ng kh&aacute;ch ho&agrave;n thiện dịch vụ.</span></span></span></span></p>\r\n\r\n	<p style=\"list-style-type:none\">&nbsp;</p>\r\n\r\n	<ol>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><a name=\"_Toc1665912\"><span style=\"color:#000000\"><strong><span style=\"font-size:16.0pt\">5.2.3 Kết th&uacute;c dịch vụ, đ&aacute;nh gi&aacute; v&agrave; thanh to&aacute;n tiền:</span></strong></span></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">Đ&acirc;y l&agrave; giai đoạn người cung ứng đ&atilde; ho&agrave;n th&agrave;nh c&aacute;c nhu cầu kh&aacute;c h&agrave;ng.</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">Kh&aacute;ch h&agrave;ng đ&aacute;nh gi&aacute; dịch vụ</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">Thanh to&aacute;n</span></span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">Qu&aacute; tr&igrave;nh cung ứng dịch vụ phải ch&uacute; &yacute; :</span></span></span></span></strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">Giao tiếp,mối quan hệ kh&iacute;ch lệ giữa nh&acirc;n vi&ecirc;n với kh&aacute;ch h&agrave;ng </span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">Ch&uacute; trọng qu&aacute; tr&igrave;nh điều khiển dịch vụ</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">Xử l&yacute; thắc mắc, sự cố xảy ra trong qu&aacute; tr&igrave;nh cung ứng</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">Chất lượng đ&atilde; l&agrave;m kh&aacute;ch h&agrave;ng h&agrave;i l&ograve;ng hay chưa.</span></span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><a name=\"_Toc1665913\"><span style=\"color:#000000\"><strong><span style=\"font-size:16.0pt\">5.3 Định gi&aacute; dịch vụ</span></strong></span></a><span style=\"color:#000000\"><strong> </strong></span></span></span></p>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><a name=\"_Toc1665914\"><span style=\"color:#000000\"><strong><span style=\"font-size:16.0pt\">5.3.1 Kh&aacute;i niệm v&agrave; c&aacute;c mục ti&ecirc;u định gi&aacute;</span></strong></span></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\"><strong><em>Gi&aacute; dịch vụ</em></strong> được gọi dưới nhiều t&ecirc;n gọi kh&aacute;c nhau t&ugrave;y thuộc v&agrave;o từng loại dịch vụ: Cước, Lệ ph&iacute;, học ph&iacute;....</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">Chi ph&iacute; dịch vụ được x&aacute;c định&nbsp; nhằm b&ugrave; đắp chi ph&iacute; v&agrave; c&oacute; l&atilde;i hợp l&iacute;.</span></span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:24px\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<p style=\"list-style-type:none\">&nbsp;</p>\r\n\r\n	<ol>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><a name=\"_Toc1665915\"><span style=\"color:#000000\"><strong><span style=\"font-size:16.0pt\">5.3.2 Phương ph&aacute;p định gi&aacute;:</span></strong></span></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">Dựa tr&ecirc;n cơ sở t&iacute;nh ph&iacute; ( t&iacute;nh tr&ecirc;n một đơn vị thời gian) </span></span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><strong><span style=\"font-size:16.0pt\">Gi&aacute; b&aacute;n dịch vụ = Chi ph&iacute; trực tiếp + lợi nhuận</span></strong></span></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">C&aacute;ch kh&aacute;c: </span></span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><strong><span style=\"font-size:16.0pt\">Gi&aacute; b&aacute;n DV = Chi ph&iacute; dịch vụ + % Chi ph&iacute; dịch vụ</span></strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">( <strong><em>Chi ph&iacute; dịch vụ = Chi ph&iacute; trực tiếp + chi ph&iacute; gi&aacute;n tiếp</em></strong>) </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"color:#000000\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:14pt\"><span style=\"font-size:16.0pt\">( Tỷ lệ % tr&ecirc;n chi ph&iacute; Dv được x&aacute;c định theo thống k&ecirc; kinh nghiệm v&agrave; t&igrave;nh h&igrave;nh thực tế tr&ecirc;n thị trường) </span></span></span></span></p>', '1552539001', null, '0', '45', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('589', 'CHƯƠNG 6: HIỆU QUẢ SỬ DỤNG CÁC NGUỒN LỰC CỦA DOANH NGHIỆP', '<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665916\"><span style=\"color:#000000\">CHƯƠNG 6: HIỆU QUẢ SỬ DỤNG C&Aacute;C NGUỒN LỰC CỦA DOANH NGHIỆP</span></a></span></span></p>\r\n\r\n<p style=\"list-style-type:none\">&nbsp;</p>\r\n\r\n<ol>\r\n</ol>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665917\"><span style=\"color:#000000\"><strong>6.1 Nguồn lực chủ yếu của doanh nghiệp</strong></span></a></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665918\"><span style=\"color:#000000\"><strong>6.1.1&nbsp;Nguồn lực lao động</strong></span></a></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>6.1.1.1 Kh&aacute;i niệm, ph&acirc;n loại lao động</em></span></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kh&aacute;i niệm: <strong><em>lao động</em></strong><em> </em>l&agrave; hoạt động <strong><em>c&oacute; mục đ&iacute;ch</em></strong> của con người, th&ocirc;ng qua con người <strong><em>t&aacute;c động v&agrave;o thế giới tự nhi&ecirc;n</em></strong> nhằm <strong><em>tạo ra của cải</em></strong> để phục vụ con người</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ph&acirc;n loại lao động: </span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Theo lĩnh vực hoạt động: lao động sản xuất, lao động thương mại, lao động dịch vụ.</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Theo ti&ecirc;u chuẩn t&aacute;c động ( v&agrave;o đối tượng lao động): Lao động trực tiếp, lao động gi&aacute;n tiếp</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Theo tr&igrave;nh độ chuy&ecirc;n m&ocirc;n: chuy&ecirc;n vi&ecirc;n, chuy&ecirc;n vi&ecirc;n ch&iacute;nh, chuy&ecirc;n vi&ecirc;n cao cấp....</span></span></span></p>\r\n\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<p style=\"list-style-type:none\">&nbsp;</p>\r\n\r\n		<ol>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>6.1.1.2 Năng suất lao động: </em></span></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kh&aacute;i niệm: Năng suất lao động thể hiện sức sản xuất của lao động, được đo lường bởi số lượng sản phẩm sản xuất ra trong 1 đơn vị thời gian hoặc thời gian cần thiết để sản xuất 1 SP.</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Chỉ ti&ecirc;u đo lường:</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img src=\"file:///C:\\Users\\ASUS\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image002.gif\" style=\"height:19px; width:12px\" />&nbsp;<span class=\"math-tex\">\\(NSLD(DNSX) = \\frac{{sanluongsx}}{{sonhanvien}}\\)</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span class=\"math-tex\">\\(NSLD(DNTM) = \\frac{{DTthuan}}{{Sonhanvien}}\\)</span></span></span></span></p>\r\n\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<p style=\"list-style-type:none\">&nbsp;</p>\r\n\r\n		<ol>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>6.1.1.3 Tiền lương trong DN </em></span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Kh&aacute;i niệm: Tiền lương trong DN l&agrave; gi&aacute; cả của lao động, được h&igrave;nh th&agrave;nh th&ocirc;ng qua sự thỏa thuận giữa người lao động với người sử dụng sử dụng lao động, do cung cầu lao động tr&ecirc;n thị trường quyết định v&agrave; dựa tr&ecirc;n năng suất, chất lượng, hiệu quả lao động, đồng thời tuần thủ ph&aacute;p luật của nh&agrave; nước</span></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C&aacute;c h&igrave;nh thức tiền lương:</span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Lương thời gian</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Lương sản phẩm</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Lương hỗn hợp</span></span></span></p>\r\n\r\n	<p style=\"list-style-type:none\">&nbsp;</p>\r\n\r\n	<ol>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665919\"><span style=\"color:#000000\"><strong>6.1.2 Nguồn lực t&agrave;i ch&iacute;nh</strong></span></a></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Vốn v&agrave; cấu th&agrave;nh vốn trong doanh nghiệp</span></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kh&aacute;i niệm: Vốn l&agrave; biểu hiện bằng tiền của t&agrave;i sản của DN </span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">T&agrave;i sản của DN: đất đai, nh&agrave; xưởng, m&aacute;y m&oacute;c,....</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Cấu th&agrave;nh vốn:</span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Vốn cố định: L&agrave; sự biểu hiện bằng tiền c&aacute;c t&agrave;i sản cố định của DN </span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Vốn lưu động: L&agrave; sự biểu hiện bằng tiền c&aacute;c t&agrave;i sản lưu động của DN</span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665920\"><span style=\"color:#000000\"><strong>6.2 Chi ph&iacute; v&agrave; kết quả hoạt động kinh tế của doanh nghiệp</strong></span></a></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><a name=\"_Toc1665921\"><span style=\"color:#000000\">6.2.1 Chi ph&iacute; của DN:</span></a><span style=\"color:#000000\"> </span></strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Chi ph&iacute;:</span></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kh&aacute;i niệm: L&agrave; biểu hiện bằng tiền của việc sử dụng c&aacute;c yếu tố của qu&aacute; tr&igrave;nh sản xuất kinh doanh ( vốn, lao động, trang thiết bị, th&ocirc;ng tin,...) m&agrave; qua đ&oacute; DN tạo ra v&agrave; thực hiện sản phẩm tr&ecirc;n thị trường.</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ph&acirc;n loại chi ph&iacute;: </span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">&nbsp;Ph&acirc;n theo đặc t&iacute;nh h&igrave;nh th&agrave;nh của chi ph&iacute;: chi ph&iacute; x&aacute;c định, chi ph&iacute; cơ hội</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ph&acirc;n theo mục đ&iacute;ch cua kế hoạch h&oacute;a: chi ph&iacute; chuẩn, chi ph&iacute; thực hiện </span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ph&acirc;n theo t&aacute;c động của chi ph&iacute; đến hoạt động kinh doanh: </span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em>Chi ph&iacute; trực tiếp</em></strong>: L&agrave; những chi ph&iacute; ph&aacute;t sinh gắn liền với một sản phẩm hoặc một nghiệp vụ kinh doanh nhất định. C&aacute;c khoản chi ph&iacute; n&agrave;y được hạch to&aacute;n trực tiếp v&agrave;o gi&aacute; th&agrave;nh sản phẩm hoặc nghiệp vụ kinh doanh đ&oacute;. VD: Lương nh&acirc;n vi&ecirc;n, nguy&ecirc;n liệu....</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em>Chi ph&iacute; gi&aacute;n tiếp</em></strong> l&agrave; những khoản chi ph&iacute; ph&aacute;t sinh c&oacute; li&ecirc;n quan đến nhiều sản phẩm, hoặc nhiều nghiệp vụ kinh doanh kh&aacute;c nhau. </span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">VD: Lương quản l&iacute;, điện, nước, thu&ecirc; nh&agrave;,...</span></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ph&acirc;n theo mục đ&iacute;ch hạch to&aacute;n v&agrave; l&ecirc;n phương &aacute;n ph&acirc;n phối thu nhập của doanh nghiệp ( chi ph&iacute; KHTSCD, nguy&ecirc;n vật liệu, tiền lương, mua ngo&agrave;i,...) </span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ph&acirc;n theo t&iacute;nh chất biến động của chi ph&iacute; so với số lượng h&agrave;ng h&oacute;a ti&ecirc;u thụ:</span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em>Chi ph&iacute; cố định: </em></strong>( chi ph&iacute; bất biến, định ph&iacute;)&nbsp; l&agrave; những khoản chi ph&iacute; kh&ocirc;ng biến đổi trong một thời kỳ nhất định cho d&ugrave; khối lượng h&agrave;ng h&oacute;a c&oacute; tăng hay giảm.</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><em>Chi ph&iacute; biến đổi</em></strong> ( chi ph&iacute; khả biến, biến ph&iacute;) l&agrave; những khoản chi ph&iacute; biến động c&ugrave;ng chiều với khối lượng h&agrave;ng h&oacute;a ti&ecirc;u thụ, bao gồm chi ph&iacute; mua h&agrave;ng, chi ph&iacute; vận chuyển, tiền lương trả cho nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng theo doanh số.....</span></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C&aacute;c chỉ ti&ecirc;u chi ph&iacute; cơ bản: </span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Tổng chi ph&iacute; (TF)</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Chi ph&iacute; b&igrave;nh qu&acirc;n </span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Chi ph&iacute; bi&ecirc;n</span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C&aacute;c nh&acirc;n tố ảnh hưởng đến chi ph&iacute;:</span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Khối lượng h&agrave;ng h&oacute;a ti&ecirc;u thụ</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Gi&aacute; c&aacute;c yếu tố đầu v&agrave;o, Gi&aacute; b&aacute;n h&agrave;ng h&oacute;a</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Cơ cấu h&agrave;ng h&oacute;a kinh doanh</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Tr&igrave;nh độ quản l&iacute; kinh tế, tr&igrave;nh độ hạch to&aacute;n của DN</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Cơ sở vật chất, kỹ thuật</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Tr&igrave;nh độ chuy&ecirc;n m&ocirc;n, nghiệp vụ của nh&acirc;n vi&ecirc;n</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ph&acirc;n c&ocirc;ng v&agrave; hợp t&aacute;c của Thương mại với ng&agrave;nh kh&aacute;c</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">M&ocirc;i trường ph&aacute;p l&yacute; của DN</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Điều kiện kinh tế mở, hội nhập</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C&aacute;c nh&acirc;n tố kh&aacute;c</span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><a name=\"_Toc1665922\"><span style=\"color:#000000\">6.2.2 Kết quả hoạt động kinh tế</span></a></strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">6.2.2.1 Doanh thu:</span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Dt b&aacute;n h&agrave;ng</span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Dt dịch vụ</span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">6.2.2.2 Lợi nhuận</span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- L&agrave; phần ch&ecirc;nh lệch giữa tổng thu nhập của DN v&agrave; tổng chi ph&iacute; trong một thời kỳ nhất định</span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- B&aacute;o c&aacute;o kết quả kinh doanh của DN</span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- C&aacute;c nh&acirc;n tố ảnh hưởng đến lợi nhuận:</span></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Khối lượng ti&ecirc;u thụ</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Gi&aacute; bạn, gi&aacute; ph&iacute;</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Chu kỳ sống của SP</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Lợi thế thương mại, uy t&iacute;n, danh tiếng của DN</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Chiến lược kinh doanh của DN</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Tr&igrave;nh độ quản l&yacute; của DN</span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665923\"><span style=\"color:#000000\"><strong>6.3 Hiệu quả kinh tế của DN:</strong></span></a></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665924\"><span style=\"color:#000000\"><strong>6.3.1 Kh&aacute;i niệm, ti&ecirc;u chuẩn hiệu quả kinh tế</strong></span></a></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kn: Hiệu quả l&agrave; mối tương quan so s&aacute;nh giữa kết quả/ mục ti&ecirc;u đạt được với chi ph&iacute; bỏ ra để đạt được kết quả / mục ti&ecirc;u đ&oacute;.</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Hiệu quả kinh doanh gồm: HQ tuyệt đối v&agrave; HQ tương đối</span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">HQ tuyệt đối: HQ = K-C </span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">HQ tương đối: HQ = K/C </span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Với K: L&agrave; kết quả hoạt động kinh doanh ( DT thuần, LN) </span></span></span></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">&nbsp;&nbsp;&nbsp;&nbsp; C: L&agrave; tổng chi ph&iacute; kinh doanh</span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<p style=\"list-style-type:none\">&nbsp;</p>\r\n\r\n	<ol>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><a name=\"_Toc1665925\"><span style=\"color:#000000\"><strong>6.3.2 Hệ thống chỉ ti&ecirc;u đ&aacute;nh gi&aacute; hệ quả kinh tế</strong></span></a></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">6.3.2.1 C&aacute;c chỉ ti&ecirc;u đ&aacute;nh gi&aacute; hiệu quả tổng hợp</span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- DT thuần/ tổng chi ph&iacute; KD</span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- DT thuần/ Vốn KD b&igrave;nh qu&acirc;n</span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- LN/ tổng CP</span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- LN/ vốn KD</span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">6.3.2.2 C&aacute;c chỉ ti&ecirc;u đ&aacute;nh gi&aacute; hiệu quả bộ phận</span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- HQ sử dụng lao động</span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">+ <span class=\"math-tex\">\\(NSLD(DNTM) = \\frac{{DTthuan}}{{Sonhanvien}}\\)</span> &nbsp;</span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">+ HQ sử dụng tiền lương = <span class=\"math-tex\">\\( = \\frac{{DTthuan}}{{QL}}\\)</span></span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">QL: Quỹ tiền lương trong một thời kỳ</span></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">HQ sử dụng vốn: </span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">+ Chỉ ti&ecirc;u HQ sd vốn cố định:</span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Hiệu suất sd VCĐ = <span class=\"math-tex\">\\( = \\frac{{DTthuan}}{{VC{{\\rm{D}}_{bq}}}}\\)</span></span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">+&nbsp; Chỉ ti&ecirc;u HQ sd Vốn lưu động </span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">V&ograve;ng chu chuyển VLĐ = <span class=\"math-tex\">\\(\\frac{{DTgiavon}}{{VL{D_{bq}}}}\\)</span></span></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Khả năng sinh lời VLĐ &nbsp;<span class=\"math-tex\">\\( = \\frac{{LN}}{{VL{D_{bq}}}}\\)</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>', '1552539010', null, '0', '45', '1', '0', null, '0', null, null, null, null, null, null, null, null);
