INSERT INTO `lesson` VALUES ('617', 'Câu 6 (Nhóm 1)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 6: tr&igrave;nh b&agrave;y / ph&acirc;n t&iacute;ch&nbsp; c&oacute; so s&aacute;nh qu&aacute; tr&igrave;nh marketing theo c&aacute;ch tiếp cận truyền thống v&agrave; hiện đại?&nbsp;</span></span></strong></span></span></p>', '1552568200', null, '576', '62', '1', '576', '576', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('618', 'Câu 1 (Nhóm 2)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 1: Tr&igrave;nh b&agrave;y m&ocirc; h&igrave;nh Gi&aacute; trị cảm nhận của kh&aacute;ch h&agrave;ng; Gi&aacute; trị chức năng;gi&aacute; trị t&acirc;m l&yacute;; mối quan hệ giữa gi&aacute; trị cẩm nhận của kh&aacute;ch h&agrave;ng v&agrave; sự thỏa m&atilde;n của kh&aacute;ch h&agrave;ng</span></span></strong></span></span></p>', '1552568502', null, '576', '62', '1', '576', '576', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('619', 'Câu 2 (Nhóm 2)', '<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 2: Tr&igrave;nh b&agrave;y nội dung c&aacute;c yếu tố tạo n&ecirc;n th&agrave;nh c&ocirc;ng của c&aacute;c doanh nghiệp c&oacute; hiệu năng cao</span></span></span></span></strong></p>', '1552568857', null, '576', '62', '1', '576', '576', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('620', 'Câu 3 (Nhóm 2)', '<p><span style=\"font-size:13.0pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><strong>C&acirc;u 3</strong><span style=\"color:black\"> Tr&igrave;nh b&agrave;y/ ph&acirc;n t&iacute;ch nội dung c&aacute;c giai đoạn. Lựa chọn, cung ứng v&agrave; truyền th&ocirc;ng gi&aacute; trị trong m&ocirc; h&igrave;nh qu&aacute; tr&igrave;nh s&aacute;ng tạo v&agrave; ph&acirc;n phối gi&aacute; trị của doanh nghiệp? </span></span></span></p>', '1552569104', null, '576', '62', '1', '576', '576', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('621', 'Câu 4 (Nhóm 2)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">C&acirc;u 4.</span></span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\"> Tr&igrave;nh b&agrave;y/ ph&acirc;n t&iacute;ch nội dung c&aacute;c phương c&aacute;ch x&acirc;y dựng l&ograve;ng trung th&agrave;nh của kh&aacute;ch h&agrave;ng ?</span></span></span></span></span></p>', '1552569914', null, '576', '62', '1', '576', '576', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('622', 'Chương 3:QUẢN TRỊ THÔNG TIN, PHÂN TÍCH VÀ DỰ BÁO MARKETING', '<p style=\"margin-left:72px\"><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">CHƯƠNG 3: QUẢN TRỊ TH&Ocirc;NG TIN, PH&Acirc;N T&Iacute;CH V&Agrave; DỰ B&Aacute;O MARKETING</span></span></strong></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Kh&aacute;i niệm v&agrave; m&ocirc; h&igrave;nh ph&acirc;n t&iacute;ch marketing</span></span>\r\n\r\n	<ol>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Kh&aacute;i niệm: Ph&acirc;n t&iacute;ch marketing tập hợp c&aacute;c kỹ thuật thống k&ecirc; v&agrave; c&aacute;c m&ocirc; h&igrave;nh để ph&acirc;n t&iacute;ch c&aacute;c dữ liệu đ&atilde; được thu thập về c&aacute;c vấn đề marketing nhằm tạo cơ sở cho c&aacute;c quyết định v&agrave; điều h&agrave;nh kinh doanh hữu&nbsp;&nbsp; hiệu hơn .</span></span></li>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">M&ocirc; h&igrave;nh</span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:72px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:6px\"><img src=\"/public/images/course/62/lesson/1552961396_image-20190319091408-1.png\" style=\"height:669px; width:600px\" /></p>\r\n\r\n<p style=\"margin-left:72px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">1.3 C&aacute;c c&ocirc;ng cụ ph&acirc;n t&iacute;ch marketing</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C&aacute;c c&ocirc;ng cụ thống k&ecirc;</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+ X&aacute;c định mối quan hệ b&ecirc;n trong một tập hợp dữ liệu, độ tin cậy về mặt th&ocirc;ng k&ecirc; của ch&uacute;ng</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Biến số ch&iacute;nh n&agrave;o đang ảnh hưởng đến doanh số v&agrave; mức độ quan trọng của mối biến số?</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C&aacute;c c&ocirc;ng cụ m&ocirc; h&igrave;nh:</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+M&ocirc;i m&ocirc; h&igrave;nh đại diện cho hệ thống tiến tr&igrave;nh hay kết quả thực hiện n&agrave;o đ&oacute;: L&agrave; g&igrave; nếu?C&aacute;i g&igrave; l&agrave; n&ecirc;n?</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ph&acirc;n t&iacute;ch v&agrave; sử dụng dữ liệu kh&aacute;ch h&agrave;ng</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+ Bao &nbsp;gồm h&agrave;nh động mua h&agrave;ng của kh&aacute;ch h&agrave;ng, sự tiếp x&uacute;c của c&aacute;c lực lượng b&aacute;n, gh&eacute; thăm website&hellip;</span></span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Thu thập th&ocirc;ng tin v&agrave; quan trắc m&ocirc;i trường marketing</span></span>\r\n\r\n	<ol>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Thu thập th&ocirc;ng tin marketing</span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Kh&aacute;i niệm: C&aacute;c yếu tố v&agrave; c&aacute;c nguồn lực marketing b&ecirc;n ngo&agrave;i c&oacute; thể t&aacute;c động đến khả năng quản trị marketing&nbsp; trong việc x&acirc;y dựng v&agrave; duy tr&igrave; mối quan hệ th&agrave;nh c&ocirc;ng với kh&aacute;ch h&agrave;ng mục ti&ecirc;u</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">C&aacute;c m&ocirc;i trường:</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+ M&ocirc;i trường nh&acirc;n khẩu học</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+M&ocirc;i trường ch&iacute;nh trị-ph&aacute;p luật</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+M&ocirc;i trường tự nhi&ecirc;n</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+M&ocirc;i trường c&ocirc;ng nghệ</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+ M&ocirc;i trường văn h&oacute;a- x&atilde; hội</span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"2\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Quan trắc m&ocirc;i trường marketing</span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Kh&aacute;i niệm: Quan trắc m&ocirc;i trường marketing lệ thống b&aacute;o động NỞm về những lực lượng m&ocirc;i trường c&oacute; thể t&aacute;c động đến sản phẩm v&agrave; thị trường của doanh nghiệp trong tương&nbsp;&nbsp; lai .</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:42px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">-&nbsp; Mục đ&iacute;ch :</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">- Cải thiện khả năng đ&aacute;p ứng với những thay đổi nhanh ch&oacute;ng của m&ocirc;i trường </span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">- Đ&aacute;p ứng lại m&ocirc;i trường marketing </span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+ Những doanh nghiệp l&agrave;m cho sự việc diễn ra</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;+ Những doanh nghiệp đứng nh&igrave;n sự việc diễn ra </span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+ Những doanh nghiệp lu&ocirc;n tự hỏi c&aacute;i g&igrave; đ&atilde; diễn ra </span></span></p>\r\n\r\n<p style=\"margin-left:54px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">- Nhiệm vụ : </span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">-Kiểm so&aacute;t c&aacute;c xu hướng</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;- Chuẩn bị c&aacute;c dự b&aacute;o .</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;- Tư vấn nội bộ . </span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">- Truyền th&ocirc;ng</span></span></p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Ph&acirc;n t&iacute;ch thị trường người ti&ecirc;u d&ugrave;ng</span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">3.1 C&aacute;c nh&acirc;n tố ảnh hưởng</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">- Văn h&oacute;a:</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; +Nền văn h&oacute;a</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; +Ph&acirc;n tầng văn h&oacute;a</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; +Tầng lớp x&atilde; hội</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">-X&atilde; hội</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; +Nh&oacute;m tham khảo</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; +Gia đ&igrave;nh</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; +Vai tr&ograve; v&agrave; vị thế</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">-C&aacute; nh&acirc;n</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; +Tuổi t&aacute;c</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; +Nghề nghiệp</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; +C&aacute;ch sống</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &hellip;.</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">-T&acirc;m l&yacute;:</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; +Động cơ</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; +Nhận thức</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; +Sự cảm nhận</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; +Niềm tin</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">3.2 C&aacute;c vai trong mua sắm</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">- NGười khởi xướng</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">-Người ảnh hưởng</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">-Người quyết định</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">-Người mua</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">-Người sử dụng</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">3.3 C&aacute;c loại h&agrave;nh vi quyết định</span></span></p>\r\n\r\n<table border=\"1\" cellspacing=\"0\" class=\"MsoTableGrid\" style=\"border-collapse:collapse; border:solid windowtext 1.0pt\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:top; width:155.8pt\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:155.85pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">C&oacute; quan hệ mật thiết v&agrave; thu h&uacute;t cao</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:155.85pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">C&oacute; quan hệ mật thiết v&agrave; thu h&uacute;t thấp</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:top; width:155.8pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">C&oacute; sự kh&aacute;c biệt thương hiệu nhiều</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:155.85pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">H&agrave;nh vi mua phức tạp</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:155.85pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">H&agrave;nh vi mua định hướng đa dạng h&oacute;a</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:27.85pt; vertical-align:top; width:155.8pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">C&oacute; sự kh&aacute;c biệt thương hiệu &iacute;t</span></span></p>\r\n			</td>\r\n			<td style=\"height:27.85pt; vertical-align:top; width:155.85pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">H&agrave;nh vi l&agrave;m giảm tr&aacute;i nghịch</span></span></p>\r\n			</td>\r\n			<td style=\"height:27.85pt; vertical-align:top; width:155.85pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">H&agrave;nh vi mua theo th&oacute;i quen</span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">3.4 Tiến tr&igrave;nh ra quyết định mua</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Bước 1: Nhu cầu được cảm nhận</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Bước 2: T&igrave;m kiếm th&ocirc;ng tin</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Bước 3: Đ&aacute;nh gi&aacute; thế vị</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Bước 4: Quyết định mua</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Bước 5: Cảm nhận sau mua:</span></span></p>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Ph&acirc;n t&iacute;ch thị trường doanh nghiệp</span></span>\r\n\r\n	<ol>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Đặc điểm thị trường doanh nghiệp</span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Kh&aacute;i niệm: Thị trường doanh nghiệp b&agrave;o h&agrave;m mọi tổ chức mua h&agrave;ng h&oacute;a dịch vụ sử dụng cho việc sản xuất ra h&agrave;ng h&oacute;a v&agrave; dịch vụ kh&aacute;c, để b&aacute;n ra, cho thu&ecirc; hoặc cung ứng cho người ti&ecirc;u d&ugrave;ng</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Đặc điểm thị trường:</span></span>\r\n	<ol start=\"2\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">T&igrave;nh huống mua doanh nghiệp</span></span></li>\r\n	</ol>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Mua lặp lại</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Mua thay đổi</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Mua mới</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Mua hệ thống</span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"3\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">C&Aacute;c yếu tố ảnh hưởng mua h&agrave;ng doanh nghiệp</span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">M&ocirc;i trường: Cung-cầu;Lạm ph&aacute;t</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Tổ chức: Mục ti&ecirc;u;ch&iacute;nh s&aacute;ch;Thủ tục;Cơ cấu tổ chức&hellip;</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Quan hệ c&aacute; nh&acirc;n: Quyền thế; Sự đồng cảm;Sức thuyết phục&hellip;</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">C&aacute; nh&acirc;n: Tuổi t&aacute;c; thu nhập; Học vấn&hellip;</span></span>\r\n	<ol start=\"4\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Tiến tr&igrave;nh quyết định mua của kh&aacute;ch h&agrave;ng tổ chức</span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">C&aacute;c vai trong mua sắm</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+ Người khởi xướng </span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Người ảnh hưởng</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Người ph&ecirc; chuẩn</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Người quyết định</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Người mua</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Người gi&aacute;m s&aacute;t</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Người sử dụng</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Tiến tr&igrave;nh quyết định mua</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">B1 :Nhận thức nhu cầu</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">B2: Ph&aacute;c họa nhu cầu</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">B3: X&aacute;c định quy c&aacute;ch sản phẩm</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">B4: T&igrave;m kiếm nh&agrave; cung cấo</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">B5: Y&ecirc;u cầu ch&agrave;o h&agrave;ng</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">B6: Lựa chọn nh&agrave; cung cấp</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">B7: L&agrave;m thủ&nbsp; tục đặt h&agrave;ng</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">B8: Đ&aacute;nh gi&aacute; kết quả thực hiện</span></span></p>\r\n\r\n<ol start=\"5\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Hệ thống th&ocirc;ng tin v&agrave; dự b&aacute;o marketing</span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Kh&aacute;i niệm: Bao gồm con người, thiết bị v&agrave; c&aacute;c thủ tục để thu thập, ph&acirc;n loại, ph&acirc;n t&iacute;ch, lượng gi&aacute; v&agrave; ph&acirc;n phối ch&iacute;nh x&aacute;c, đ&uacute;ng thời gian v&agrave; đ&uacute;ng nhu cầu của người ra quyết định marketing</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">M&ocirc; h&igrave;nh hệ thống:</span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"/public/images/course/62/lesson/1552961396_image-20190319091408-2.png\" style=\"height:447px; width:700px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Hệ giữa liệu nội bộ</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Hệ điều tra marketing</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Hệ nghi&ecirc;n cứu marketing</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Hệ hỗ trợ marketing</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Dự b&aacute;o marketing</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Thị trường</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Đo lường nhu cầu</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Dự đo&aacute;n doanh thu v&agrave; nhu cầu hiện tại</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Nhu cầu tương lai</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', '1552570202', null, '0', '62', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('623', 'Câu 7 (Nhóm 1)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 7 : Tr&igrave;nh b&agrave;y kh&aacute;i niệm v&agrave; bản chất Mạng lưới ph&acirc;n phối gi&aacute; trị ( chuỗi cung ứng gi&aacute; trị )?</span></span></strong></span></span></p>', '1552570365', null, '576', '62', '1', '576', '576', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('624', 'Câu 8 (Nhóm 1)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 8 : Tr&igrave;nh b&agrave;y ph&acirc;n t&iacute;ch kh&aacute;i niệm quản trị quan hệ kh&aacute;ch h&agrave;ng ; c&aacute;c cấp độ marketing quan hệ của doanh nghiệp ? </span></span></strong></span></span></p>', '1552570757', null, '576', '62', '1', '576', '576', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('625', 'Câu 9(Nhóm 1)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 9: . Tr&igrave;nh b&agrave;y / Ph&acirc;n t&iacute;ch kh&aacute;i niệm , vai tr&ograve; của marketing cơ sở dữ liệu kh&aacute;ch h&agrave;ng ? </span></span></strong></span></span></p>', '1552571276', null, '576', '62', '1', '576', '576', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('626', 'Câu 10(Nhóm 1)', '<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><b><span style=\"font-size:13.0pt\"><span style=\"line-height:107%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 10</span></span></span></b><span style=\"font-size:13.0pt\"><span style=\"line-height:107%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> : Tr&igrave;nh b&agrave;y ph&acirc;n t&iacute;ch kh&aacute;i niệm , m&ocirc; h&igrave;nh v&agrave; c&aacute;c c&ocirc;ng cụ ph&acirc;n t&iacute;ch marketing của doanh nghiệp ?</span></span></span></span></span></span></p>', '1552571490', null, '622', '62', '1', '622', '622', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('627', 'Câu 11 (Nhóm 1)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 11</span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> : N&ecirc;u c&aacute;c nh&oacute;m th&ocirc;ng tin thuộc m&ocirc;i trường marketing ? Vận dụng trong ph&acirc;n t&iacute;ch v&agrave; quản trị marketing của doanh nghiệp ?</span></span></span></span></p>', '1552572686', null, '622', '62', '1', '622', '622', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('628', 'Câu 12 (Nhóm 1)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 12(nh&oacute;m 1)</span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> : Tr&igrave;nh b&agrave;y / ph&acirc;n t&iacute;ch kh&aacute;i niệm , mục đ&iacute;ch , nhiệm vụ quan trắc m&ocirc;i trường mảketing?&nbsp; Vận ung c&aacute;c kết quả trong ph&acirc;n t&iacute;ch v&agrave; quản trị m&agrave; keting của doanh nghiệp . </span></span></span></span></p>', '1552572781', null, '622', '62', '1', '622', '622', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('629', 'Câu 13 (Nhóm 1)', '<p><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 13</span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> : N&ecirc;u v&agrave; tr&igrave;nh b&agrave;y / ph&acirc;n t&iacute;ch nội dung c&aacute;c loại h&igrave;nh h&agrave;nh vi quyết định mua của kh&aacute;ch h&agrave;ng</span></span></p>', '1552573113', null, '622', '62', '1', '622', '622', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('631', 'câu 6 (Nhóm 2)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">C&acirc;u 6</span></span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">: Tr&igrave;nh b&agrave;y/ ph&acirc;n t&iacute;ch nội dung &ldquo;c&aacute;c vai tr&ograve; trong mua sắm&rdquo; trong h&agrave;nh vi mua của kh&aacute;c h&agrave;ng người ti&ecirc;u d&ugrave;ng.</span></span></span></span></span></p>', '1552574529', null, '622', '62', '1', '622', '622', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('632', 'Câu 7 (Nhóm 2)', '<p>C&acirc;u 7: Tr&igrave;nh b&agrave;y/ph&acirc;n tich nội dung c&aacute;c bước trong tiến tr&igrave;nh ra quyết định mua của kh&aacute;ch h&agrave;ng v&agrave; li&ecirc;n hệ thực tế của một doanh nghiệp?</p>', '1552574665', null, '622', '62', '1', '622', '622', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('634', 'bài tập trắc nghiệm thử', '', '1552625710', null, '45', '33', '1', '45', '45', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('635', 'Đề thi số 1', null, '1552635147', null, '0', '55', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('636', 'Câu 1: Tại sao Hồ Chí Minh cho rằng: Đại đoàn kết dân tộc phải trở thành lực lượng vật chất có tổ chức? Hình thức tồn tại của Mặt trận dân tộc thống nhất ở Viết Nam?', '', '1552635662', null, '635', '55', '1', '635', '635', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('637', 'Câu 2: Phân tích luận điểm sau đây của Hồ Chí Minh: \"Cách mạng giải phóng dân tộc cần được tiến hành chủ động sáng tạo và có khả năng giành thắng lợi trước cách mạng vô sản ở chính quốc\". Liên hệ với cách mạng Việt Nam.', '', '1552636103', null, '635', '55', '1', '635', '635', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('638', 'Đề thi số 2', null, '1552636822', null, '0', '55', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('639', 'Câu 1: Trình bày quan điểm của HCM về loại hình quá độ lên Chủ nghĩa xã hội ở VN. Đặc điểm lớn nhất và mâu thuẫn cơ bản nhất trong thời kì quá độ lên Chủ nghĩa xã hội ở nước ta theo TTHCM là gì?', '', '1552637149', null, '638', '55', '1', '638', '638', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('640', 'Câu 2: Quan điểm HCM về nguyên tắc đoàn kết quốc tế? Nhà nước ta vận dụng các nguyên tắc này trong quá trình hội nhập hiện nay như thế nào?', '', '1552637310', null, '638', '55', '1', '638', '638', '1', '', '0', null, null, null, null, null, '0');
