INSERT INTO `lesson` VALUES ('645', 'bài tập 15-03', '', '1552644569', null, '643', '33', '1', '643', '643', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('646', 'Chương 4:PHÂN TÍCH VÀ LỰA CHỌN CHIẾN LƯỢC CẠNH TRANH', '<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">CHƯƠNG 4: PH&Acirc;N T&Iacute;CH V&Agrave; LỰA CHỌN CHIẾN LƯỢC CẠNH TRANH</span></span></strong></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">C&aacute;c lực lượng cạnh tranh v&agrave; nhận diện đối thủ cạnh tranh</span></span>\r\n\r\n	<ol>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">C&aacute;c lực lượng cạnh tranh</span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">5 lực lượng cạnh tranh</span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"/public/images/course/62/lesson/1552961637_image-20190319091810-1.png\" style=\"height:509px; width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"2\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Nhận diện đối thủ cạnh tranh</span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">C&aacute;ch tiếp cận ng&agrave;nh về cạnh tranh: L&agrave; một nh&oacute;m c&aacute;c doanh nghiệp&nbsp; ch&agrave;o b&aacute;n một sản phẩm hay một lớp sản phẩm c&oacute; thể thay thế ho&agrave;n to&agrave;n cho nhau</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+ Sản phẩm c&ugrave;ng loại</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Thay đổi theo thời gian</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Định dạng bởi nhiều yếu tố kh&aacute;ch nhau</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Cấu tr&uacute;c ng&agrave;nh</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Độc quyền</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; B&aacute;n độc quyền đơn thuần</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; B&aacute;n độc quyền kh&aacute;c biệt</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; B&aacute;n Cạnh tranh</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Cạnh tranh ho&agrave;n hảo</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">C&aacute;ch tiếp cận thị trường về đối thủ cạnh tranh</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+C&aacute;c đối thủ cạnh tranh l&agrave; những doanh nghiệp c&ugrave;ng thoải m&atilde;n nhu cầu của kh&aacute;ch h&agrave;ng</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+&nbsp;&nbsp;&nbsp;&nbsp; C&aacute;ch tiếp cận n&agrave;y gợi mở một tập hợp c&aacute;c đối thủ cạnh tranh tiềm năng v&agrave; hiện tại mở rộng hơn ==&gt;Lập kế hoạch marketing l&acirc;u d&agrave;i.</span></span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Ph&acirc;n t&iacute;ch đối thủ cạnh tranh</span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Chiến lược</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Mục ti&ecirc;u</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Điểm mạnh-điểm yếu</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Ước lượng phản ứng của đối thủ</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Đối thủ cạnh tranh điềm tĩnh</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Đối thủ cạnh tranh k&eacute;n chọn</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Đối thủ cạnh tranh hung dữ</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Đối thủ cạnh tranh kh&ocirc;n ngoan</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Lựa chọn đối thủ tấn c&ocirc;ng/n&eacute; tranh</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+ Đối thủ cạnh tranh mạnh v&agrave; yếu</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Đối thủ cạnh tranh gần v&agrave; xa</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Đối thủ cạnh tranh &ldquo;Tốt&rdquo; v&agrave; &ldquo;Xấu&rdquo;</span></span></p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Thiết kế hệ th&ocirc;ng điều tra cạnh tranh</span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Thiết lập hệ thống</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Thu thập dữ liệu</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Đ&aacute;nh gi&aacute; v&agrave; ph&acirc;n t&iacute;ch</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Phổ biến th&ocirc;ng tin v&agrave; giải đ&aacute;p thắc mắc</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Thiết kế c&aacute;c chiến lược cạnh tranh</span></span>\r\n\r\n	<ol>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Chiến lược dẫn đạo thị trường</span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Mở rộng tổng cầu thị trường</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+ T&igrave;m kiếm kh&aacute;ch h&agrave;ng mới</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Khai th&aacute;c kh&aacute;ch h&agrave;ng hiện hữu</span></span></p>', '1552653708', null, '0', '62', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('647', 'Câu 15 (Nhóm 1)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 15: Ph&acirc;n t&iacute;ch nội dung lựa chọn chiến lược cạnh tranh dựa tr&ecirc;n năng lực marketing v&agrave; c&acirc;n bằng định hướng kh&aacute;ch h&agrave;ng với đối thủ cạnh tranh.</span></span></span></span></p>', '1552653769', null, '646', '62', '1', '646', '646', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('648', 'Câu 16 (Nhóm 1)', '<p>C&acirc;u 16: Tr&igrave;nh b&agrave;y/ph&acirc;n t&iacute;ch nội dung thiết kế hệ thống cạnh tranh</p>', '1552654108', null, '646', '62', '1', '646', '646', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('649', 'Câu 8 (Nhóm 2)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">C&Acirc;U 8: &nbsp;</span></span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">Tr&igrave;nh b&agrave;y/ph&acirc;n t&iacute;ch nội dung c&aacute;c lực lượng cạnh tranh theo m&ocirc; h&igrave;nh 5 lực lượng cạnh tranh của M.Porter.</span></span></span></span></span></p>', '1552654429', null, '646', '62', '1', '646', '646', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('650', 'Câu 9 (Nhóm 2)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">C&Acirc;U 9: Tr&igrave;nh b&agrave;y/ph&acirc;n t&iacute;ch nội dung nhận diện đối thủ cạnh tranh theo c&aacute;c tiếp cận ng&agrave;nh v&agrave; thị trường về cạnh tranh</span></span></span></span></span></p>', '1552654737', null, '646', '62', '1', '646', '646', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('651', 'Câu 10 (Nhóm 2)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">C&acirc;u 10:</span></span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\"> Tr&igrave;nh b&agrave;y/ph&acirc;n t&iacute;ch nội dung trong ph&acirc;n t&iacute;ch đối thủ cạnh tranh của doanh nghiệp.</span></span></span></span></span></p>', '1552655153', null, '646', '62', '1', '646', '646', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('652', 'Chương 5: PHÁT TRIỂN CHIẾN LƯỢC MARKEING', '<p style=\"margin-left:72px\"><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">CHƯƠNG 5: PH&Aacute;T TRIỂN CHIẾN LƯỢC MARKEING</span></span></strong></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">C&aacute;c kh&aacute;i niệm cốt l&otilde;i của quản trị chiến lược marketing</span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Chiến lược marketing</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Quản trị chiến lược marketing</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Quản trị thị trường chiến lược</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Marketing chiến lược</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Ph&aacute;t triển chiến lược marketing</span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Nhận dạng v&agrave; ph&acirc;n đoạn thị trườngngười ti&ecirc;u d&ugrave;ng v&agrave; thị trường doanh nghiệp mục ti&ecirc;u</span></span>\r\n\r\n	<ol>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Nhận dạng c&aacute;c cấp độ ph&acirc;n đoạn thị trường</span></span></li>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Ph&acirc;n đoạn thị trường người ti&ecirc;u d&ugrave;ng v&agrave; thị trường doanh nghiệp mục ti&ecirc;u</span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Kh&aacute;i niệm ph&acirc;n đoạn thị trường: l&agrave; qu&aacute; tr&igrave;nh ph&acirc;n chia thị trường tổng thể&nbsp; th&agrave;nh c&aacute;c nh&oacute;m nhỏ hơn (đoạn, kh&uacute;c, mảng, l&aacute;t cắt..) tr&ecirc;n cơ sở những đặc điểm kh&aacute;c biệt về nhu cầu,ước muốn v&agrave; c&aacute;c đặc điểm h&agrave;nh vi</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Cơ sở ph&acirc;n đoạn thị trường B2B</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+ Địa dư</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Nh&acirc;n khẩu học</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Ph&aacute;c đồ t&acirc;m l&yacute;</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+H&agrave;nh vi ứng xử</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Ph&acirc;n đoạn thị trường B2B</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+C&aacute;c biến số hoạt động</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+C&Aacute;c c&aacute;ch mua h&agrave;ng</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+C&aacute;c nh&acirc;n tố t&igrave;nh huống</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+C&aacute;c đặc điểm c&aacute; nh&acirc;n</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+C&aacute;c biến số nh&acirc;n khẩu học</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Ph&acirc;n đoạn hiệu quả</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+ C&oacute; thể đo lường được: Quy m&ocirc;, sức mua, đặcc điểm&nbsp; của c&aacute;c ph&acirc;n đoạn c&oacute; thể đo lường được</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Quy m&ocirc; đ&aacute;ng kể: C&aacute;c ph&acirc;n đoạn đủ lớn v&agrave; sinh lời để phục vụ</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Tiếp cận được: C&Aacute;c ph&acirc;n đoạn c&oacute; thể tiếp cận v&agrave; phục vụ hiệu quả</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Ph&acirc;n biệt được:C&aacute;c ph&acirc;n đoạn c&oacute; thể ph&acirc;n biệt được v&agrave; kh&aacute;i niệm v&agrave; đ&aacute;p ứng với marketing mix kh&aacute;ch nhau</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Tồn tại được: C&oacute; thể h&igrave;nh th&agrave;nh c&aacute;c chương tr&igrave;nh hiệu quả để thu h&uacute;t v&agrave; phục vụ c&aacute;c ph&acirc;n đoạn</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Đ&aacute;nh gi&aacute; v&agrave; lựa chọn thị trường mục ti&ecirc;u</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Quy m&ocirc;, sức tăng trưởng của ph&acirc;n đoạn</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Sức hấp dẫn về cấu tr&uacute;c đoạn thị trường</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Mục ti&ecirc;u v&agrave; nguồn lực của doanh nghiệp</span></span></p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Ph&aacute;t triển chiến lược định vị kh&aacute;c biệt h&oacute;a c&aacute;c ch&agrave;o h&agrave;ng thị trường</span></span>\r\n\r\n	<ol>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">C&aacute;c quan điểm về định vị</span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Kh&aacute;i niệm:l&agrave; qu&aacute; tr&igrave;nh x&acirc;y dựng, th&ocirc;ng đạt c&aacute;c gi&aacute; trị đặc trưng&nbsp; của thương hiệu/sản phẩm v&agrave;o t&acirc;m tri kh&aacute;ch h&agrave;ng mục ti&ecirc;u</span></span>\r\n\r\n	<ol start=\"2\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">C&aacute;c c&ocirc;ng cụ</span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;Dị biệt h&oacute;a h&igrave;nh ảnh</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Dị biệt h&oacute;a k&ecirc;nh</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Dị biệt&nbsp; h&oacute;a nh&acirc;n sự</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Dị biệt h&oacute;a dịch vụ</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Dị biệt h&oacute;a sản phẩm</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">3.3 Ph&aacute;t triển v&agrave; truyển th&ocirc;ng&nbsp; chiến lược định vị</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;Cần nhấn mạnh những dị biệt n&agrave;o?</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; +Những đặc t&iacute;nh sản phẩm/thương hiệu kh&aacute;c biệt gi&uacute;p ph&acirc;n biệt với đối thủ cạnh tranh</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; +Ti&ecirc;u chuẩn cho một kh&aacute;c biệt/dị biệt</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Ph&aacute;t triển tuy&ecirc;n ng&ocirc;n về định vị: Hướng đến (nh&oacute;m kh&aacute;ch h&agrave;ng mục ti&ecirc;u/nhu cầu) (nh&atilde;n hiệu) của ch&uacute;ng t&ocirc;i l&agrave; (kh&aacute;i niệm) đem lại (điểm kh&aacute;c biệt)</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">THực hiện v&agrave; truyền th&ocirc;ng định vị đ&atilde; lựa chọn</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Tiến h&agrave;nh c&aacute;c bước</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Duy tr&igrave; định vị </span></span></p>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Ph&aacute;t triển chiến lược marketing theo chiến lược </span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">M&ocirc; h&igrave;nh chu k&igrave; sống của sản phẩm</span></span></p>\r\n\r\n<p style=\"margin-left:48px; text-align:center\"><img src=\"/public/images/course/62/lesson/1553003097_image-20190319204910-1.png\" style=\"height:383px; width:600px\" /></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Chiến lược marketing trong gia đoạn triển khai</span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Đặc trưng của giai đoạn</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Chiến lược marketing</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Hớt v&aacute;ng sữa chớp nho&aacute;ng</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Hớt v&aacute;ng sữa từ từ</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Th&acirc;m nhập từ từ</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Th&acirc;m nhập chớp nho&aacute;ng</span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"2\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Chiến lược marketing trong giai đoạn ph&aacute;t triển</span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Đặc trưng:</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Chiến lược marketing</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+ Sử dụng một số chiến lược duy tr&igrave; tốc độ mở rộng thị phần nhanh ch&oacute;ng</span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"3\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Chiến lược marketing trong giai đoạn b&atilde;o h&ograve;a</span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Đặc trưng</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Chiến lược marketing</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Cải biến thị trường</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Cải biến sản phẩm</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Cải biến marketing mix</span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"4\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Chiến lược marketing trong giai đoạn suy tho&aacute;i</span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Đặc trưng:</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Chiến lược marketing</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+5 loại chiến lược</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+loại bỏ sản phẩm</span></span></p>\r\n\r\n<ol start=\"5\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Ph&aacute;t triển c&aacute;c chiến lược ch&agrave;o h&agrave;ng quốc tế</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Quản trị thị trường chiến lược v&agrave; c&aacute;c m&ocirc; h&igrave;nh marketing mix</span></span>\r\n	<ol>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Đặc điểm quản trị thị trường chiến lược</span></span></li>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">C&aacute;c m&ocirc; h&igrave;nh chiến lược marketing</span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Lập kế hoạch chiến lược v&agrave; kế hoạch marketing hằng năm</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">M&ocirc; h&igrave;nh&nbsp; kế hoạch h&oacute;a chiến lược v&agrave; kế hoạch h&oacute;a marketing</span></span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"/public/images/course/62/lesson/1553003097_image-20190319204911-2.png\" style=\"height:581px; width:600px\" /></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Qu&aacute; tr&igrave;nh lập kế hoạch chiến lược</span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Sứ mạng kinh doanh</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+ Doanh nghiệp của ta l&agrave; g&igrave;?</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Kh&aacute;ch h&agrave;ng l&agrave; ai?</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+C&aacute;i g&igrave; c&oacute; &yacute; nghĩa, quan trọng với kh&aacute;ch h&agrave;ng đ&oacute;</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+ Doanh nghiệp của ch&uacute;ng ta sẽ ra sao?</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Ch&uacute;ng ta cần phải l&agrave;m như thế n&agrave;o?</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">X&aacute;c định mục ti&ecirc;u của doanh nghiệp</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+ Sứ mạng chuyển h&oacute;a th&agrave;nh mục ti&ecirc;u cụ thể v&agrave; c&oacute; khả năng thực hiện được</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;X&aacute;c định chiến lược</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+sản phẩm/thị trường</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Lợi thế cạnh tranh</span></span></p>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Gi&aacute; trị</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Ph&acirc;n t&iacute;ch hồ sơ kinh doanh</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:72px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">+Sử dụng BCG, GE..</span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"2\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Qu&aacute; tr&igrave;nh lập kế hoạch marketing hằng năm</span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Ph&acirc;n t&iacute;ch bối cảnh (ph&acirc;n t&iacute;ch SWOT)</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Thiết lập c&aacute;c mục ti&ecirc;u</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Lựa chọn thị trường mục ti&ecirc;u</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Ph&aacute;t triển marketing mix</span></span></li>\r\n</ul>', '1552656139', null, '0', '62', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('653', 'Câu 17 (Nhóm 1)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">C&acirc;u 17:Tr&igrave;nh b&agrave;y/ph&acirc;n t&iacute;ch kh&aacute;i niệm cốt l&otilde;i của quản trị chiến lược marketing</span></span></span></span></p>', '1552656179', null, '652', '62', '1', '652', '652', '1', '', '0', null, null, null, null, null, '0');
