INSERT INTO `lesson` VALUES ('656', 'Câ 12 (Nhóm 2)', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">C&Acirc;U 12</span></span></span></strong><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">: Tr&igrave;nh b&agrave;y/ph&acirc;n t&iacute;ch nội dung ph&aacute;t triển v&agrave; truyền th&ocirc;ng chiến lược định vị</span></span></span></span></span></p>', '1552656960', null, '652', '62', '1', '652', '652', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('657', 'Câu 13 (Nhóm 2)', '<p><span style=\"font-size:11pt\"><span style=\"line-height:normal\"><span style=\"font-family:Calibri,sans-serif\"><b><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">C&Acirc;U 13:</span></span></span></b><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\"> Tr&igrave;nh b&agrave;y nội dung c&aacute;c chiến lược marketing trong c&aacute;c giai đoạn chu k&igrave; sống của sản phẩm/</span></span></span></span></span></span></p>', '1552659140', null, '652', '62', '1', '652', '652', '1', '', '0', null, null, null, null, null, '0');
