INSERT INTO `lesson` VALUES ('675', 'bài tập 17-03', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:16.0pt\"><span style=\"color:red\">T&iacute;nh gần đ&uacute;ng 1 gi&aacute; trị </span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:16.0pt\">Hướng dẫn giải</span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 1</span></span><span style=\"font-size:16.0pt\">: Từ biểu thức đ&atilde; cho x&aacute;c định h&agrave;m z=z (x, y)</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước2</span></span><span style=\"font-size:16.0pt\">: Chọn gi&aacute; trị x<sub>0</sub>, y<sub>0</sub>, ∆x, ∆y</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">ch&uacute; &yacute; chọn sao cho biểu thức z=z (x<sub>0</sub>, y<sub>0</sub>) t&iacute;nh được dễ d&agrave;ng, v&agrave; ∆x&rarr;0, ∆y&rarr;0</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 3</span></span><span style=\"font-size:16.0pt\"> T&iacute;nh đạo h&agrave;m ri&ecirc;ng cấp 1 tại (x<sub>0</sub>, y<sub>0</sub>) v&agrave; </span><span style=\"color:red\"><strong><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(z({x_0};{y_0})\\)</span></span></strong></span> </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 4</span></span><span style=\"font-size:16.0pt\"> Thay v&agrave;o c&ocirc;ng thức v&agrave; t&iacute;nh ra kết quả</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:red\"><strong><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(z\\left( {{x_0} + \\Delta x;{y_0} + \\Delta y} \\right) \\cong z\\left( {{x_0};{y_0}} \\right) + z{\'_x}\\left( {{x_0};{y_0}} \\right).\\Delta x + z{\'_y}\\left( {{x_0};{y_0}} \\right).\\Delta y\\)</span></span></strong></span><em> </em></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:16.0pt\">VD7.3 </span></em><span style=\"font-size:16.0pt\">T&iacute;nh gi&aacute; trị gần đ&uacute;ng của biểu thức sau</span></span></span></p>\r\n\r\n<p style=\"margin-left:36px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:red\"><strong><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(A = \\sqrt {{{\\left( {1,02} \\right)}^3} + {{\\left( {1,97} \\right)}^3}} \\)</span></span></strong></span> </span></span></p>\r\n\r\n<p style=\"margin-left:36px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 1</span></span><span style=\"font-size:16.0pt\">: Từ biểu thức đ&atilde; cho x&aacute;c định h&agrave;m </span></span></span></p>\r\n\r\n<p style=\"margin-left:36px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">z=f (x, y)=</span><span style=\"color:red\"><strong><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(\\sqrt {{x^3} + {y^3}} \\)</span></span></strong></span><span style=\"font-size:16.0pt\"> </span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"background-color:lime\">&nbsp;Bước2</span>: Chọn gi&aacute; trị x<sub>0</sub>, y<sub>0</sub>, ∆x, ∆y</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">ở đ&acirc;y ta chọn </span><span style=\"color:red\"><strong><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\({x_0} = 1;{y_0} = 2;\\Delta x = 0,02;\\Delta y =  - 0,03\\)</span></span></strong></span> </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"background-color:lime\">Bước 3</span> T&iacute;nh đạo h&agrave;m ri&ecirc;ng cấp 1 tại (x<sub>0</sub>, y<sub>0</sub>)</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(f\\left( {1;2} \\right) = 3\\)</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(f{&#39;_x}\\left( {x;y} \\right) = \\frac{{3{{\\rm{x}}^2}}}{{2\\sqrt {{x^3} + {y^3}} }} \\Rightarrow f{&#39;_x}\\left( {1;2} \\right) = \\frac{1}{2}\\)</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(f{&#39;_y}\\left( {x;y} \\right) = \\frac{{3{y^2}}}{{2\\sqrt {{x^3} + {y^3}} }} \\Rightarrow f{&#39;_y}\\left( {1;2} \\right) = 2\\)</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"background-color:lime\">Bước 4</span></span><span style=\"font-size:16.0pt\"> Thay v&agrave;o c&ocirc;ng thức v&agrave; t&iacute;nh ra kết quả</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:red\"><strong><span style=\"font-size:16.0pt\"><span class=\"math-tex\">\\(A \\approx f\\left( {1;2} \\right) + f{\'_x}\\left( {1;2} \\right).0,02 + f{\'_y}\\left( {1;2} \\right).( - 0,03) = 2,95\\)</span></span></strong></span> </span></span></p>', '1552708578', null, '0', '33', '1', '0', null, '0', null, null, null, null, null, null, null, null);
