INSERT INTO `lesson` VALUES ('721', 'chương 1', '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\">CHƯƠNG I: TỔNG QUAN VỀ KINH TẾ HỌC VI M&Ocirc;</span></strong></span></span></p>\r\n\r\n<ol style=\"list-style-type:upper-roman\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\">Đối tượng v&agrave; phương ph&aacute;p nghi&ecirc;n cứu</span></em></strong></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\">1,Kh&aacute;i niệm:</span></em></strong><span style=\"font-size:12.0pt\"> kinh tế học vi m&ocirc; l&agrave; một ph&acirc;n ng&agrave;nh của kinh tế học-nghi&ecirc;n cứu sự vận động v&agrave; những mối quan hệ kinh tế chủ yếu của một đất nước tr&ecirc;n b&igrave;nh diện to&agrave;n bộ nền kinh tế quốc d&acirc;n.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\">2, Đối tượng nghi&ecirc;n cứu</span></em></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Tăng trưởng sản lượng</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Lạm ph&aacute;t- thất nghiệp</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">C&aacute;n c&acirc;n thương mại</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">[</span><span style=\"font-size:12.0pt\">L&atilde;i suất, tỷ gi&aacute;,&hellip;.</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\">3,phương ph&aacute;p nghi&ecirc;n cứu</span></em></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">C&acirc;n bằng tổng hợp</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Ph&acirc;n t&iacute;ch thống k&ecirc; số lớn</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">M&ocirc; h&igrave;nh h&oacute;a,&hellip;</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\" style=\"list-style-type:upper-roman\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\">Mục ti&ecirc;u v&agrave; c&ocirc;ng cụ của kinh tế vĩ m&ocirc;</span></em></strong></span></span></li>\r\n</ol>\r\n\r\n<ol start=\"5\" style=\"list-style-type:lower-roman\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\">Mục ti&ecirc;u chung</span></em></strong><em><span style=\"font-size:12.0pt\">: ổn định kinh tế, tăng trưởng v&agrave; c&ocirc;ng bằng x&atilde; hội.</span></em></span></span></li>\r\n	<li value=\"5\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\">Mục ti&ecirc;u cụ thể: 5 mục ti&ecirc;u</span></em></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Mục ti&ecirc;u sản lượng:</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Quốc gia muốn đạt mức sản lượng cao bằng mức sản lượng tiềm năng (Y=Y*)</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Sả lượng tiềm năng(Y*): l&agrave; mức sản lượng tối đa m&agrave; một quốc gia đạt được trong điều kiện to&agrave;n dụng nh&acirc;n c&ocirc;ng v&agrave; kh&ocirc;ng g&acirc;y lạm ph&aacute;t.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Tốc độ tăng trưởng sản lượng cao v&agrave; bền vững:</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>Tốc độ tăng trưởng sản lượng năm t:</strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; g(%)=</span><span style=\"font-size:12.0pt\">[</span><span style=\"font-size:12.0pt\">(<img id=\"_x0000_i1025\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image001.wmz\" style=\"height:23.25pt; width:81pt\" /> </span><span style=\"font-size:12.0pt\">]</span><span style=\"font-size:12.0pt\">*100</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Mục ti&ecirc;u việc l&agrave;m:</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Mọi người lao động đều c&oacute; việc l&agrave;m (tỷ lệ thất nghiệp thấp u</span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Cambria Math&quot;,&quot;serif&quot;\">&asymp;u*)</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Tạo được nhiều việc l&agrave;m tốt, mang lại mức thu nhập cao cho người lao động</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Cơ cấu việc l&agrave;m ph&ugrave; hợp</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Mục ti&ecirc;u gi&aacute; cả:</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">-ổn đinh gi&aacute; cả trong nền kinh tế:</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">+ gi&aacute; cả kh&ocirc;ng biến động qu&aacute; l</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">+ tỷ lệ tăng gi&aacute; thấp: 2%-5% một năm</span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Cambria Math&quot;,&quot;serif&quot;\">=&gt;</span></span><span style=\"font-size:12.0pt\"> ổn định</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">+mức gi&aacute; chung tăng: lạm ph&aacute;t</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">+mức gi&aacute; chung giảm: giảm ph&aacute;t</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Mục ti&ecirc;u kinh tế đối ngoại:</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">ổn định tỷ gi&aacute;</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">c&acirc;n bằng c&aacute;n c&acirc;n thương mại (NX=X-IM)</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">c&acirc;n bằng c&aacute;n c&acirc;n thanh to&aacute;n quốc tế( BOP)</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">mở rộng ch&iacute;nh s&aacute;ch đối ngoại,&hellip;</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Mục ti&ecirc;u ph&acirc;n phối thu nhập c&ocirc;ng bằng:</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&nbsp;Giảm khoảng c&aacute;ch gi&agrave;u ngh&egrave;o giữa c&aacute;c nh&oacute;m d&acirc;n cư</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Cơ hội tiếp cận c&ocirc;ng bằng với c&aacute;c nguồn lực</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"5\" style=\"list-style-type:lower-roman\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\">C&ocirc;ng cụ của ch&iacute;nh s&aacute;ch kinh tế vĩ m&ocirc;: </span></em></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">L&agrave;&nbsp; c&aacute;c ch&iacute;nh s&aacute;ch m&agrave; ch&iacute;nh phủ c&oacute; thể sử dụng để t&aacute;c động v&agrave;o nền kinh tế nhằm hướng nền kinh tế đạt được c&aacute;c mục ti&ecirc;u mong muốn.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">C&aacute;c ch&iacute;nh s&aacute;ch cơ bản:</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Ch&iacute;nh s&aacute;ch t&agrave;i kh&oacute;a</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Ch&iacute;nh s&aacute;ch tiền tệ</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Ch&iacute;nh s&aacute;ch thu nhập</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Ch&iacute;nh s&aacute;ch kinh tế đối ngoại</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"3\" style=\"list-style-type:upper-roman\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\">Hệ thống kinh tế vĩ m&ocirc;</span></em></strong></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">1, sơ đồ</span></span></span></p>\r\n\r\n<p>Đầu v&agrave;o: tổng cầu: tiền tệ, chi ti&ecirc;u, thuế v&agrave; c&aacute;c nguồn lực kh&aacute;c</p>\r\n\r\n<p>Hộp đen kinh tế vĩ m&ocirc;: t&aacute;c động qua lại giữa tổng cung v&agrave; tổng cầu</p>\r\n\r\n<p>Đầu ra: sản lượng, việc l&agrave;m, gi&aacute; cả</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol start=\"4\" style=\"list-style-type:upper-roman\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Tổng cầu v&agrave; tổng cung</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Tổng cầu</span></strong></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\">Kh&aacute;i niệm: </span></em></strong><span style=\"font-size:12.0pt\">l&agrave; tổng khối lượng h&agrave;ng h&oacute;a v&agrave; dịch vụ trong nước m&agrave; c&aacute;c t&aacute;c nh&acirc;n trong nền kinh tế sẵn s&agrave;ng v&agrave; c&oacute; khả năng mua tại mỗi mức gi&aacute; chung cho trước (giả định c&aacute;c nh&acirc;n tố kh&aacute;c l&agrave; kh&ocirc;ng đổi)</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\">C&aacute;c yếu tố t&aacute;c động đến tổng cầu:</span></em></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Mức gi&aacute; chung</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Thu nhập quốc d&acirc;n</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Kỳ vọng</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">C&aacute;c ch&iacute;nh s&aacute;ch kinh tế vĩ m&ocirc;</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Thị hiếu, tập qu&aacute;n ti&ecirc;u d&ugrave;ng,&hellip;</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\">Đường tổng cầu: </span></em></strong><span style=\"font-size:12.0pt\">l&agrave; đường biểu thị mối quan hệ giữa tổng cầu v&agrave; mức gi&aacute; chung khi c&aacute;c yếu tố kh&aacute;c kh&ocirc;ng đổi.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Đồ thị: <img id=\"Picture_x0020_17\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image002.jpg\" style=\"height:167.25pt; rotation:-90; width:124.5pt\" /> </span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Di chuyển của đường AD l&agrave; sự trượt dọc tr&ecirc;n đường tổng cầu do sự thay đổi của mức gi&aacute; chung</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Dịch chuyển của đường AD l&agrave; sự dịch chuyển song song của đường AD do sự thay đổi của c&aacute;c yếu tố kh&aacute;c ngo&agrave;i mức gi&aacute; chung ( c&aacute;c yếu tố t&aacute;c động đến tổng cầu)</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\" style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Tổng cung</span></strong></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\">Kh&aacute;i niệm:</span></em></strong><span style=\"font-size:12.0pt\"> l&agrave; tổng khối lượng h&agrave;ng h&oacute;a v&agrave; dịch vụ m&agrave; c&aacute;c doanh nghiệp sẵn s&agrave;ng v&agrave; c&oacute; khả năng sản xuất, cung ứng tại mỗi mức gi&aacute; chung cho trước ( giả định c&aacute;c yếu tố kh&aacute;c kh&ocirc;ng đổi)</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\">C&aacute;c yếu tố t&aacute;c động:</span></em></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Mức gi&aacute; chung </span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Gi&aacute; cả của c&aacute;c yếu tố đầu v&agrave;o</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Tr&igrave;nh độ c&ocirc;ng nghệ sản xuất</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Sự thay đổi nguồn lực ( chất lượng, số lượng,&hellip;)</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Ch&iacute;nh s&aacute;ch, thời tiết,&hellip;</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\">Ngắn hạn v&agrave; d&agrave;i hạn:</span></em></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\">Ngắn hạn: </span></em></strong><span style=\"font-size:12.0pt\">gi&aacute; cả cứng nhắc. G&iacute;a cả của yếu tố đầu v&agrave;o chưa kịp thay đổi c&ugrave;ng với sự thay đổi của gi&aacute; cả đầu ra.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\">D&agrave;i hạn:</span></em></strong><span style=\"font-size:12.0pt\"> gi&aacute; cả linh hoạt. G&iacute;a yếu tố đầu v&agrave;o thay đổi c&ugrave;ng tỷ lệ với mức thay đổi của gi&aacute; cả đầu ra.</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\">Đường tổng cung d&agrave;i hạn (AS<img id=\"_x0000_i1026\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image003.wmz\" style=\"height:18pt; width:12.75pt\" /> ) &amp; đường tổng cung ngắn hạn(AS<img id=\"_x0000_i1027\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image004.wmz\" style=\"height:18pt; width:12pt\" /> )</span></em></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Đường tổng cung d&agrave;i hạn: l&agrave; đường thẳng đứng tại mức sản lượng tiềm năng.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Đường tổng cung ngắn hạn: l&agrave; đường dốc l&ecirc;n, thể hiện mối quan hệ thuận giữa mức gi&aacute; chung v&agrave; sản lượng cung ứng.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Đồ thị:</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><img id=\"Picture_x0020_16\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image005.jpg\" style=\"height:162pt; rotation:-90; width:120.75pt\" /> </span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Di chuyển tr&ecirc;n đường AS l&agrave; sự di chuyển dọc tr&ecirc;n một đường tổng cung đ&atilde; cho do sự thay đổi của mức gi&aacute; chung.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Dịch chuyển tr&ecirc;n đường AS l&agrave; sự thay đổi vị tr&iacute; của đường AS song song sang tr&aacute;i hoặc sang phải do sự thay đổi của c&aacute;c yếu tố t&aacute;c động đến tổng cung ngo&agrave;i mức gi&aacute; chung.</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"3\" style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">C&acirc;n bằng tổng cầu v&agrave; tổng cung</span></strong></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">C&acirc;n bằng ngắn hạn: </span></strong><span style=\"font-size:12.0pt\">mức sản lượng c&acirc;n bằng ngắn hạn c&oacute; thể nhỏ hơn hoặc lớn hơn mức sản lượng tiềm năng.</span></span></span></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><img id=\"Picture_x0020_18\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image006.jpg\" style=\"height:199.5pt; rotation:-90; width:148.5pt\" /> </span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">C&acirc;n bằng d&agrave;i hạn:</span></strong><span style=\"font-size:12.0pt\"> l&agrave; trạng th&aacute;i c&acirc;n bằng đạt được khi sản lượng c&acirc;n bằng đạt được ở mức sản lượng tiềm năng.</span></span></span></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><img id=\"Picture_x0020_19\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image007.jpg\" style=\"height:174.75pt; rotation:-90; width:129.75pt\" /> </span></span></span></p>\r\n\r\n<ol start=\"4\" style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Ph&acirc;n t&iacute;ch biến động của sản lượng, gi&aacute; cả tr&ecirc;n m&ocirc; h&igrave;nh AD-AS</span></strong></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><img id=\"Picture_x0020_20\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image008.jpg\" style=\"height:143.5pt; position:absolute; width:255.1pt; z-index:251674624\" /> <span style=\"font-size:12.0pt\"><img id=\"Picture_x0020_22\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image009.png\" style=\"height:144.75pt; width:257.25pt\" /> </span><br />\r\n<span style=\"font-size:12.0pt\"><strong>e,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; chu kỳ kinh tế: </strong>l&agrave; sự dao động của sản lượng thực tế xung quanh sản lượng tiềm năng</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Thiếu hụt sản lượng l&agrave; độ ch&ecirc;nh lệch giữa sản lượng thực tế v&agrave; sản lượng tiềm năng.</span></span></span></p>\r\n\r\n<ol start=\"5\" style=\"list-style-type:upper-roman\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Quan hệ giữa c&aacute;c biến số kinh tế vĩ m&ocirc; cơ bản</span></strong></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Tăng trưởng v&agrave; thất nghiệp:</span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Tăng trưởng kinh tế l&agrave; sự gia tăng tổng sản lượng theo thời gian</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Thất nghiệp phản &aacute;nh những người trong lực lượng lao động k c&oacute; việc l&agrave;m</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Định luật Okun: nếu sản lượng thực tế tăng 2,5% th&igrave; tỷ lệ thất nghiệp giảm 1%</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Cambria Math&quot;,&quot;serif&quot;\">=&gt;</span></span><span style=\"font-size:12.0pt\">tăng trưởng v&agrave; thghfhgất nghiệp MQH nghịch</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Tăng trưởng v&agrave; lạm ph&aacute;t:</span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Lạm ph&aacute;t phản &aacute;nh sự tăng l&ecirc;n của mức gi&aacute; chung</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Mối quan hệ:</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">tăng trưởng do AD tăng </span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Cambria Math&quot;,&quot;serif&quot;\">=&gt;</span></span><span style=\"font-size:12.0pt\">lạm ph&aacute;t</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">tăng trưởng do AS tăng </span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Cambria Math&quot;,&quot;serif&quot;\">=&gt;</span></span><span style=\"font-size:12.0pt\"> kh&ocirc;ng g&acirc;y lạm ph&aacute;t</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">suy tho&aacute;i do AS giảm </span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Cambria Math&quot;,&quot;serif&quot;\">=&gt;</span></span><span style=\"font-size:12.0pt\">lạm ph&aacute;t</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Cambria Math&quot;,&quot;serif&quot;\">=≫</span></span><span style=\"font-size:12.0pt\"> kh&ocirc;ng c&oacute; mối quan hệ r&otilde; r&agrave;ng</span></span></span></li>\r\n</ul>', '1552879198', null, '0', '68', '1', '0', null, '0', null, null, null, null, null, null, null, null);
