INSERT INTO `lesson` VALUES ('728', 'bài 5', '', '1552879836', null, '723', '67', '1', '723', '723', '1', '', '0', null, null, null, null, null, '0');
INSERT INTO `lesson` VALUES ('729', 'chương 2', '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\">CHƯƠNG II: ĐO LƯỜNG C&Aacute;C CHỈ TI&Ecirc;U KINH TẾ VĨ M&Ocirc; CƠ BẢN</span></strong></span></span></p>\r\n\r\n<ol style=\"list-style-type:upper-roman\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Đo lường sản lượng quốc gia</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Tổng sản phẩm quốc nội (GDP) v&agrave; tổng sản phẩm quốc d&acirc;n (GNP)</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Kh&aacute;i niệm</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GDP: </span></strong><span style=\"font-size:12.0pt\">Đo lường tổng gi&aacute; trị thị trường của tất cả c&aacute;c h&agrave;ng h&oacute;a v&agrave; dịch vụ cuối c&ugrave;ng được tạo ra trong phạm vi l&atilde;nh thổ của một quốc gia trong một thời kỳ nhất định ( thường l&agrave; một năm).</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GNP: </span></strong><span style=\"font-size:12.0pt\">Đo lường tổng gi&aacute; trị thị trường của tất cả c&aacute;c h&agrave;ng h&oacute;a v&agrave; dịch vụ cuối c&ugrave;ng do c&ocirc;ng d&acirc;n của một nước sản xuất ra trong một thời kỳ nhất định ( thường l&agrave; một năm).</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\" style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">C&ocirc;ng thức x&aacute;c định</span></strong></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GDP(GNP)=<img id=\"_x0000_i1025\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image001.wmz\" style=\"height:23.25pt; width:42pt\" /> </span></strong></span></span></p>\r\n\r\n<ol start=\"3\" style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Mối quan hệ</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ol style=\"list-style-type:upper-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Thu nhập của người d&acirc;n sở tại ở nước ngo&agrave;i</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Thu nhập của người d&acirc;n nước ngo&agrave;i ở nước sở tại</span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">NIA- thu nhập r&ograve;ng từ t&agrave;i sản nước ngo&agrave;i</span></span></span></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">NIA= A-B</span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GNP= GDP+NIA</span></strong></span></span></p>\r\n\r\n<ol start=\"4\" style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GDP danh nghĩa v&agrave; GDP thực</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GDP danh nghĩa</span></strong><span style=\"font-size:12.0pt\"> l&agrave; chỉ ti&ecirc;u đo lường tổng gi&aacute; trị h&agrave;ng h&oacute;a, dịch vụ theo gi&aacute; hiện h&agrave;nh (gi&aacute; thực tế).</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">K&yacute; hiệu: GDP<img id=\"_x0000_i1026\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image002.wmz\" style=\"height:18pt; width:9.75pt\" /> &nbsp;(GNP<img id=\"_x0000_i1027\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image003.wmz\" style=\"height:18pt; width:9.75pt\" /> )</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">GNP<img id=\"_x0000_i1028\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image004.wmz\" style=\"height:18.75pt; width:9.75pt\" /> &nbsp;(GDP<img id=\"_x0000_i1029\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image005.wmz\" style=\"height:18.75pt; width:9.75pt\" /> ) = <img id=\"_x0000_i1030\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image006.wmz\" style=\"height:26.25pt; width:42pt\" /> </span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GDP thực </span></strong><span style=\"font-size:12.0pt\">l&agrave; chỉ ti&ecirc;u đo lườ g tổng gi&aacute; trị h&agrave;ng h&oacute;a dịch vụ theo gi&aacute; cố định (gi&aacute; so s&aacute;nh)</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">KH: </span></strong><span style=\"font-size:12.0pt\">: GDP<img id=\"_x0000_i1031\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image007.wmz\" style=\"height:18pt; width:9pt\" /> &nbsp;(GNP<img id=\"_x0000_i1032\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image008.wmz\" style=\"height:18pt; width:9pt\" /> )</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">GNP</span><img id=\"_x0000_i1033\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image009.wmz\" style=\"height:18.75pt; width:9pt\" /> <span style=\"font-size:12.0pt\">( GDP</span><img id=\"_x0000_i1034\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image010.wmz\" style=\"height:18.75pt; width:9pt\" /> <span style=\"font-size:12.0pt\">)=<img id=\"_x0000_i1035\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image011.wmz\" style=\"height:26.25pt; width:42.75pt\" /> </span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Sự thay đổi của GDP danh nghĩa v&agrave; GNP thực</span></strong></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GDP </span></strong><span style=\"font-size:12.0pt\">danh nghĩa thay đổi do: gi&aacute; cả thay đổi; sản lượng thay đổi</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GNP thực </span></strong><span style=\"font-size:12.0pt\">chỉ thay đổi khi c&oacute; sự thay đổi của sản lượng v&igrave; gi&aacute; cả cố định tại năm gốc</span></span></span></p>\r\n\r\n<ol start=\"5\" style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">&Yacute; Nghĩa của GDP(GNP) trong ph&acirc;n t&iacute;ch kinh tế vĩ m&ocirc;</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">L&agrave; thước đo đ&aacute;nh gi&aacute; th&agrave;nh quả hoạt động của nền kinh tế</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">GDP Được sử dụng để t&iacute;nh tốc độ tăng trưởng kinh tế của một quốc gia</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">GDP b&igrave;nh qu&acirc;n đầu người đ&aacute;nh gi&aacute; mức sống của d&acirc;n cư</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">X&aacute;c định sự thay đổi của mức gi&aacute; chung</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"6\" style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Một số hạn chế của GDP v&agrave; GNP</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Phương ph&aacute;p t&iacute;nh bỏ s&oacute;t v&agrave; t&iacute;nh tr&ugrave;ng nhiều sản phẩm</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Thời gian, sự nghỉ ngơi,&hellip;. Cũng l&agrave; những loại h&agrave;ng h&oacute;a v&agrave; dịch vụ đem lại sự thỏa m&atilde;n cho con người nhưng k được t&iacute;nh v&agrave;o GDP</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">C&aacute;c chỉ ti&ecirc;u kh&aacute;c về thu nhập</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Sản phẩm quốc d&acirc;n r&ograve;ng(NNP)</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Phần GNP c&ograve;n lại sau khi trừ đi khấu hao</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">NNP = GNP-De</span></span></span></p>\r\n\r\n<ol start=\"2\" style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Thu nhập quốc d&acirc;n (Y)</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Tổng thu nhập từ c&aacute;c yếu tố sản xuất</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Y= w+i+R+</span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Cambria Math&quot;,&quot;serif&quot;\">&pi;</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Hoặc phần c&ograve;n lại của NNP sau khi trừ đi thuế gi&aacute;n thu(Te):</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px; text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Y=GNP-De-Te</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px; text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Y=NNP-Te</span></span></span></p>\r\n\r\n<ol start=\"3\" style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Thu nhập quốc d&acirc;n c&oacute; thể sử dụng (Y<img id=\"_x0000_i1036\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image012.wmz\" style=\"height:18pt; width:8.25pt\" /> )</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Phần thu nhập quốc d&acirc;n c&ograve;n lại sau khi trừ đi c&aacute;c loại thuế trực thu v&agrave; nhận được trợ cấp của ch&iacute;nh phủ hoặc doanh nghiệp.</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Y<img id=\"_x0000_i1037\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image013.wmz\" style=\"height:18pt; width:8.25pt\" /> &nbsp;=Y-Td+Tr</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Y<img id=\"_x0000_i1038\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image014.wmz\" style=\"height:18pt; width:8.25pt\" /> =Y-T</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Trong đ&oacute;: Tr: trợ cấp</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Td: thuế trực thu</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; T :thuế r&ograve;ng T=Td-Tr</span></span></span></p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Phương ph&aacute;p t&iacute;nh GDP</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">X&aacute;c định theo phương ph&aacute;p chi ti&ecirc;u</span></strong></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">GDP= C+I+G+NX</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">C: chi ti&ecirc;u cho ti&ecirc;u d&ugrave;ng của c&aacute;c hộ gia đ&igrave;nh</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">I: chi ti&ecirc;u cho đầu tư</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">G: chi ti&ecirc;u về h&agrave;ng h&oacute;a dịch vụ của ch&iacute;nh phủ</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">NX: xuất khẩu r&ograve;ng</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Ti&ecirc;u d&ugrave;ng của hộ gia đ&igrave;nh(C):</span></strong><span style=\"font-size:12.0pt\"> l&agrave; gi&aacute; trị của tất cả c&aacute;c h&agrave;ng h&oacute;a v&agrave; dịch vụ m&agrave; hộ gia đ&igrave;nh mua</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Bao gồm: h&agrave;ng l&acirc;u bền: oto, đồ nội thất,&hellip;</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; H&agrave;ng mau hỏng:đồ ăn, quần &aacute;o,&hellip;</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Dịch vụ: du lịch, cắt t&oacute;c,&hellip;.</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Ch&uacute; &yacute;: chỉ t&iacute;nh c&aacute;c khoản chi m&agrave; hộ gia đ&igrave;nh mua mới c&aacute;c sản phẩm, dịch vụ ti&ecirc;u d&ugrave;ng. Kh&ocirc;ng t&iacute;nh c&aacute;c khoản tự cung, tự cấp, cho, tặng,..</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Đầu tư (I)</span></strong><span style=\"font-size:12.0pt\">: Chi ti&ecirc;u mua h&agrave;ng h&oacute;a, dịch vụ sử dụng trong tương lai</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">I (tổng đầu tư)= Đầu tư r&ograve;ng+ khấu hao</span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Bao gồm</span></strong><span style=\"font-size:12.0pt\">:&nbsp;&nbsp;&nbsp; -Đầu tư mua t&agrave;i sản cố định</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -</span></strong><span style=\"font-size:12.0pt\">Đầu tư v&agrave;o nh&agrave; ở</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Đầu tư v&agrave;o h&agrave;ng tồn kho&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Ch&uacute; &yacute;: chỉ t&iacute;nh những khoản đầu tư mua sắm c&aacute;c tư liệu lao động mới,tạo ra tư bản dưới dạng hiện vật.&nbsp; kh&ocirc;ng t&iacute;nh c&aacute;c khoản đầu tư t&agrave;i ch&iacute;nh, mua sắm c&aacute;c tư liệu lao động đ&atilde; qua sử dụng</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Chi ti&ecirc;u của ch&iacute;nh phủ (G</span></strong><span style=\"font-size:12.0pt\">): bao gồm tất cả c&aacute;c khoản chi ti&ecirc;u của ch&iacute;nh phủ.</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Kh&ocirc;ng bao gồm c&aacute;c khoản chi chuyển giao thu nhập như trợ cấp.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Xuất khẩu r&ograve;ng</span></strong><span style=\"font-size:12.0pt\"> (NX): NX=X-IM</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\" style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">X&aacute;c định GDP theo thu nhập( chi ph&iacute;)</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">GDP được t&iacute;nh theo chi ph&iacute; c&aacute;c yếu tố đầu v&agrave;o của sx cộng với chi ph&iacute; khấu hao v&agrave; thuế gi&aacute;n thu</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GDP =w +I + R + </span></strong><strong><span style=\"font-size:12.0pt\">&pi; </span></strong><strong><span style=\"font-size:12.0pt\">+ De + Te</span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Trong đ&oacute;:</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">W: tiền lương trả cho cho lao động</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">i: l&atilde;i r&ograve;ng chả cho c&aacute;c khaorn vốn vay</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">R: thu nhập từ c&aacute;c t&agrave;i sản cho thu&ecirc;( đất đai,..)</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&Pi;</span><span style=\"font-size:12.0pt\">: lợi nhuận c&ocirc;ng ty</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">De</span><span style=\"font-size:12.0pt\">: khấu hao</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Te : thuế gi&aacute;n thu</span></span></span></p>\r\n\r\n<ol start=\"3\" style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">X&aacute;c định theo phương ph&aacute;p gi&aacute; trị gia tăng </span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Gi&aacute; trị gia tăng (VA) của một doanh nghiệp l&agrave; phần gi&aacute; trị tăng th&ecirc;m của h&agrave;ng h&oacute;a v&agrave; dịch vụ do doanh nghiệp sử dụng c&aacute;c yếu tố sản xuất tạo ra</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">VA= gi&aacute; trị sản lượng của doanh nghiệp &ndash; gi&aacute; trị của h&agrave;ng h&oacute;a trung gian mua v&agrave;o của doanh nghiệp để sx ra mức sản lượng đ&atilde; cho</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">GDP = tổng gi&aacute; trị gia tăng của c&aacute;c doanh nghiệp trong nề kinh tế</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GDP =<img id=\"_x0000_i1039\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image015.wmz\" style=\"height:12pt; width:11.25pt\" /> VA<img id=\"_x0000_i1040\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image016.wmz\" style=\"height:18pt; width:6pt\" /> </span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<ol start=\"2\" style=\"list-style-type:upper-roman\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">&nbsp;Đo lường gi&aacute; cả</span></strong></span></span>\r\n\r\n	<ol>\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Chỉ số điều chỉnh GDP (D<img id=\"_x0000_i1041\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image017.wmz\" style=\"height:18pt; width:18pt\" /> )</span></strong></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Đo lường mức gi&aacute; chung của tất cả c&aacute;c h&agrave;ng h&oacute;a v&agrave; dịch vụ được t&iacute;nh v&agrave;o GDP của năm hiện h&agrave;nh so với mức gi&aacute; đ&oacute; ở năm gốc</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">D<img id=\"_x0000_i1042\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image018.wmz\" style=\"height:18pt; width:18pt\" /> =<img id=\"_x0000_i1043\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image019.wmz\" style=\"height:35.25pt; width:156.75pt\" /> </span></span></span></p>\r\n\r\n<p style=\"margin-left:96px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Cambria Math&quot;,&quot;serif&quot;\">=≫</span></span></em></strong><strong><em><span style=\"font-size:12.0pt\">L&agrave; một chỉ số phản &aacute;nh mức gi&aacute; chung</span></em></strong></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"2\">\r\n		<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Cambria Math&quot;,&quot;serif&quot;\">Chỉ số gi&aacute; ti&ecirc;u d&ugrave;ng (CPI)</span></span></strong></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">CPI phản &aacute;nh gi&aacute; của một &ldquo;rổ&rdquo; h&agrave;ng h&oacute;a v&agrave; dịch vụ ti&ecirc;u d&ugrave;ng ở thời kỳ hiện h&agrave;nh so với gi&aacute; của &ldquo;rổ&rdquo; h&agrave;ng h&oacute;a dịch vụ như thế trong một năm n&agrave;o đ&oacute; được chọn l&agrave;m năm cơ sở.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Để t&iacute;nh CPI:</span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">B1: x&aacute;c định rổ h&agrave;ng h&oacute;a, dịch vụ ti&ecirc;u d&ugrave;ng v&agrave; quyền số đối với mỗi loại h&agrave;ng h&oacute;a</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">B2:t&iacute;nh gi&aacute; của rổ h&agrave;ng h&oacute;a, dịch vụ ti&ecirc;u d&ugrave;ng tại kỳ gốc (=<img id=\"_x0000_i1044\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image020.wmz\" style=\"height:12pt; width:11.25pt\" /> P<img id=\"_x0000_i1045\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image021.wmz\" style=\"height:18pt; width:9.75pt\" /> Q<img id=\"_x0000_i1046\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image022.wmz\" style=\"height:18pt; width:9.75pt\" /> )</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">B3: t&iacute;nh gi&aacute; của rổ h&agrave;ng h&oacute;a, dịch vụ ti&ecirc;u d&ugrave;ng tại kỳ hiện h&agrave;nh(=<img id=\"_x0000_i1047\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image023.wmz\" style=\"height:12pt; width:11.25pt\" /> P<img id=\"_x0000_i1048\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image024.wmz\" style=\"height:18pt; width:8.25pt\" /> Q<img id=\"_x0000_i1049\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image025.wmz\" style=\"height:18pt; width:9.75pt\" /> )</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">B4: x&aacute;c định chỉ số gi&aacute; ti&ecirc;u d&ugrave;ng &nbsp;<img id=\"_x0000_i1050\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image026.wmz\" style=\"height:35.25pt; width:114pt\" /> </span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\">&Yacute; nghĩa:</span></em></strong><span style=\"font-size:12.0pt\">&nbsp; -l&agrave; một chỉ ti&ecirc;u tương đối phản &aacute;nh xu thế v&agrave; mức độ biến động của gi&aacute; b&aacute;n lẻ h&agrave;ng h&oacute;a v&agrave; dịch vụ d&ugrave;ng trong sinh hoạt của d&acirc;n cư v&agrave; c&aacute;c hộ gia đ&igrave;nh</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -trong thực tế CPI được d&ugrave;ng để: -theo d&otilde;i sự thay đổi của chi ph&iacute; sinh hoạt theo thời gian; t&iacute;nh tỷ lệ lạm ph&aacute;t; l&agrave;m cơ sở điều chỉnh c&aacute;c biến số kinh tế theo lạm ph&aacute;t(tiền lương, l&atilde;i suất,&hellip;)</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><em><span style=\"font-size:12.0pt\">So s&aacute;nh D<img id=\"_x0000_i1051\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image027.wmz\" style=\"height:18pt; width:18pt\" /> &nbsp;V&agrave; CPI:</span></em></strong></span></span></p>\r\n\r\n<table border=\"1\" cellspacing=\"0\" class=\"Table\" style=\"border-collapse:collapse; border:solid windowtext 1.0pt; margin-left:26px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:18.95pt; vertical-align:top; width:7.0cm\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">D<img id=\"_x0000_i1052\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image028.wmz\" style=\"height:18pt; width:18pt\" /> </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:18.95pt; vertical-align:top; width:184.25pt\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">CPI</span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:151.9pt; vertical-align:top; width:7.0cm\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Phản &aacute;nh gi&aacute; của tất cả c&aacute;c loại h&agrave;ng h&oacute;a, dịch vụ được sản xuất ra</span></span></span></p>\r\n\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Chỉ phản &aacute;nh gi&aacute; của h&agrave;ng h&oacute;a sản xuất trong nước</span></span></span></p>\r\n\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Quyền số thay đổi</span></span></span></p>\r\n\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">D<img id=\"_x0000_i1053\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image018.wmz\" style=\"height:18pt; width:18pt\" /> =<img id=\"_x0000_i1054\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image019.wmz\" style=\"height:35.25pt; width:156.75pt\" /> </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:151.9pt; vertical-align:top; width:184.25pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Phản &aacute;nh gi&aacute; cả h&agrave;ng h&oacute;a, dịch vụ người ti&ecirc;u d&ugrave;ng mua</span></span></span></p>\r\n\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Bao h&agrave;m cả gi&aacute; biến động của h&agrave;m nhập khẩu</span></span></span></p>\r\n\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Quyền số cố định</span></span></span></p>\r\n\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><img id=\"_x0000_i1055\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image026.wmz\" style=\"height:35.25pt; width:114pt\" /> </span></span></span></p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<ol start=\"2\" style=\"list-style-type:upper-roman\">\r\n	<li style=\"list-style-type:none\">\r\n	<ol start=\"3\">\r\n		<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Chỉ số gi&aacute; sản xuất ( kh&ocirc;ng quan trọng)</span></span></span></li>\r\n	</ol>\r\n	</li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Đo lường thất nghiệp</span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:96px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Tỷ lệ thất nghiệp </span></strong><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Cambria Math&quot;,&quot;serif&quot;\">=</span></span></strong><strong><em><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Cambria Math&quot;,&quot;serif&quot;\">số người thất nghiệp</span></span></em></strong><strong><em><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Cambria Math&quot;,&quot;serif&quot;\">lực lượng lao động</span></span></em></strong><strong><em><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Cambria Math&quot;,&quot;serif&quot;\">&times;100</span></span></em></strong><span style=\"font-size:11.0pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><img id=\"_x0000_i1025\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image029.png\" style=\"height:29.25pt; width:141.75pt\" /> </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:96px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Người thất nghiệp: những người kh&ocirc;ng l&agrave;m việc v&agrave; đang t&igrave;m kiếm việc l&agrave;m</span></span></span></p>\r\n\r\n<p style=\"margin-left:96px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Lực lượng lao động: tổng số người sẵn s&agrave;ng cho việc sản xuất ra h&agrave;ng h&oacute;a v&agrave; dịch vụ, gồm những người c&oacute; việc l&agrave;m v&agrave; những người thất nghiệp</span></span></span></p>\r\n\r\n<p style=\"margin-left:96px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Kh&ocirc;ng thuộc lực lượng lao đọng: những người kh&ocirc;ng l&agrave;m việc v&agrave; k c&oacute; nhu cầu t&igrave;m việc</span></span></span></p>\r\n\r\n<p style=\"margin-left:96px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Tỷ lệ tham gia lực lượng lao động: </span></strong><span style=\"font-size:12.0pt\">l&agrave; tỷ lệ những người trong độ tuổi lao động tham gia v&agrave;o lực lượng lao động</span></span></span></p>\r\n\r\n<p style=\"margin-left:96px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Tỷ lệ tham gia lực lượng lao động=</span></strong><strong><em><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Cambria Math&quot;,&quot;serif&quot;\">lực lượng lao động</span></span></em></strong><strong><em><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Cambria Math&quot;,&quot;serif&quot;\">số người trong độ tuổi lao động</span></span></em></strong><strong><em><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Cambria Math&quot;,&quot;serif&quot;\">&times;100</span></span></em></strong><span style=\"font-size:11.0pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><img id=\"_x0000_i1025\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image030.png\" style=\"height:29.25pt; width:192.75pt\" /> </span></span></span></span></p>\r\n\r\n<ol start=\"4\" style=\"list-style-type:upper-roman\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Đồng nhất thức kinh tế vĩ m&ocirc;</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Đống nhất thức giữa tiết kiệm v&agrave; đầu tư</span></strong></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:156px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Y= C + S</span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:156px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Y = C + I</span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:156px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Cambria Math&quot;,&quot;serif&quot;\">=&gt;</span></span></strong><strong><span style=\"font-size:12.0pt\">S=I</span></strong></span></span></p>\r\n\r\n<ol start=\"2\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Đồng nhất thức m&ocirc; tả mối quan hệ giữa c&aacute;c khu vực tronh nền kinh tế đ&oacute;ng</span></span></strong></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">a, Trong nền kinh tế đ&oacute;ng</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;C + S + T = C + I + G</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">(T &ndash; G )= ( I &ndash; S)</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong>&nbsp;</strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Trong đ&oacute; T &ndash; G : khu vực ch&iacute;nh phủ</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; I &ndash; S: khu vực tư nh&acirc;n</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">b, trong nền kinh tế mở</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;C + S + T + IM = C + I + G + X&nbsp; </span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">(T &ndash; G)= ( I &ndash; S) + ( X &ndash; IM)</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Trong đ&oacute; T &ndash; G : khu vực ch&iacute;nh phủ</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; I &ndash; S: khu vực tư nh&acirc;n</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; I &ndash; IM:&nbsp; khu vực nước ngo&agrave;i</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\">CHƯƠNG II: ĐO LƯỜNG C&Aacute;C CHỈ TI&Ecirc;U KINH TẾ VĨ M&Ocirc; CƠ BẢN</span></strong></span></span></p>\r\n\r\n<ol style=\"list-style-type:upper-roman\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Đo lường sản lượng quốc gia</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Tổng sản phẩm quốc nội (GDP) v&agrave; tổng sản phẩm quốc d&acirc;n (GNP)</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Kh&aacute;i niệm</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GDP: </span></strong><span style=\"font-size:12.0pt\">Đo lường tổng gi&aacute; trị thị trường của tất cả c&aacute;c h&agrave;ng h&oacute;a v&agrave; dịch vụ cuối c&ugrave;ng được tạo ra trong phạm vi l&atilde;nh thổ của một quốc gia trong một thời kỳ nhất định ( thường l&agrave; một năm).</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GNP: </span></strong><span style=\"font-size:12.0pt\">Đo lường tổng gi&aacute; trị thị trường của tất cả c&aacute;c h&agrave;ng h&oacute;a v&agrave; dịch vụ cuối c&ugrave;ng do c&ocirc;ng d&acirc;n của một nước sản xuất ra trong một thời kỳ nhất định ( thường l&agrave; một năm).</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\" style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">C&ocirc;ng thức x&aacute;c định</span></strong></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GDP(GNP)=<img id=\"_x0000_i1025\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image001.wmz\" style=\"height:23.25pt; width:42pt\" /> </span></strong></span></span></p>\r\n\r\n<ol start=\"3\" style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Mối quan hệ</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ol style=\"list-style-type:upper-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Thu nhập của người d&acirc;n sở tại ở nước ngo&agrave;i</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Thu nhập của người d&acirc;n nước ngo&agrave;i ở nước sở tại</span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">NIA- thu nhập r&ograve;ng từ t&agrave;i sản nước ngo&agrave;i</span></span></span></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">NIA= A-B</span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GNP= GDP+NIA</span></strong></span></span></p>\r\n\r\n<ol start=\"4\" style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GDP danh nghĩa v&agrave; GDP thực</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GDP danh nghĩa</span></strong><span style=\"font-size:12.0pt\"> l&agrave; chỉ ti&ecirc;u đo lường tổng gi&aacute; trị h&agrave;ng h&oacute;a, dịch vụ theo gi&aacute; hiện h&agrave;nh (gi&aacute; thực tế).</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">K&yacute; hiệu: GDP<img id=\"_x0000_i1026\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image002.wmz\" style=\"height:18pt; width:9.75pt\" /> &nbsp;(GNP<img id=\"_x0000_i1027\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image003.wmz\" style=\"height:18pt; width:9.75pt\" /> )</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">GNP<img id=\"_x0000_i1028\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image004.wmz\" style=\"height:18.75pt; width:9.75pt\" /> &nbsp;(GDP<img id=\"_x0000_i1029\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image005.wmz\" style=\"height:18.75pt; width:9.75pt\" /> ) = <img id=\"_x0000_i1030\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image006.wmz\" style=\"height:26.25pt; width:42pt\" /> </span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GDP thực </span></strong><span style=\"font-size:12.0pt\">l&agrave; chỉ ti&ecirc;u đo lườ g tổng gi&aacute; trị h&agrave;ng h&oacute;a dịch vụ theo gi&aacute; cố định (gi&aacute; so s&aacute;nh)</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">KH: </span></strong><span style=\"font-size:12.0pt\">: GDP<img id=\"_x0000_i1031\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image007.wmz\" style=\"height:18pt; width:9pt\" /> &nbsp;(GNP<img id=\"_x0000_i1032\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image008.wmz\" style=\"height:18pt; width:9pt\" /> )</span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">GNP</span><img id=\"_x0000_i1033\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image009.wmz\" style=\"height:18.75pt; width:9pt\" /> <span style=\"font-size:12.0pt\">( GDP</span><img id=\"_x0000_i1034\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image010.wmz\" style=\"height:18.75pt; width:9pt\" /> <span style=\"font-size:12.0pt\">)=<img id=\"_x0000_i1035\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image011.wmz\" style=\"height:26.25pt; width:42.75pt\" /> </span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Sự thay đổi của GDP danh nghĩa v&agrave; GNP thực</span></strong></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GDP </span></strong><span style=\"font-size:12.0pt\">danh nghĩa thay đổi do: gi&aacute; cả thay đổi; sản lượng thay đổi</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GNP thực </span></strong><span style=\"font-size:12.0pt\">chỉ thay đổi khi c&oacute; sự thay đổi của sản lượng v&igrave; gi&aacute; cả cố định tại năm gốc</span></span></span></p>\r\n\r\n<ol start=\"5\" style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">&Yacute; Nghĩa của GDP(GNP) trong ph&acirc;n t&iacute;ch kinh tế vĩ m&ocirc;</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">L&agrave; thước đo đ&aacute;nh gi&aacute; th&agrave;nh quả hoạt động của nền kinh tế</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">GDP Được sử dụng để t&iacute;nh tốc độ tăng trưởng kinh tế của một quốc gia</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">GDP b&igrave;nh qu&acirc;n đầu người đ&aacute;nh gi&aacute; mức sống của d&acirc;n cư</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">X&aacute;c định sự thay đổi của mức gi&aacute; chung</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"6\" style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Một số hạn chế của GDP v&agrave; GNP</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Phương ph&aacute;p t&iacute;nh bỏ s&oacute;t v&agrave; t&iacute;nh tr&ugrave;ng nhiều sản phẩm</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Thời gian, sự nghỉ ngơi,&hellip;. Cũng l&agrave; những loại h&agrave;ng h&oacute;a v&agrave; dịch vụ đem lại sự thỏa m&atilde;n cho con người nhưng k được t&iacute;nh v&agrave;o GDP</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">C&aacute;c chỉ ti&ecirc;u kh&aacute;c về thu nhập</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Sản phẩm quốc d&acirc;n r&ograve;ng(NNP)</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Phần GNP c&ograve;n lại sau khi trừ đi khấu hao</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">NNP = GNP-De</span></span></span></p>\r\n\r\n<ol start=\"2\" style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Thu nhập quốc d&acirc;n (Y)</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Tổng thu nhập từ c&aacute;c yếu tố sản xuất</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Y= w+i+R+</span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Cambria Math&quot;,&quot;serif&quot;\">&pi;</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Hoặc phần c&ograve;n lại của NNP sau khi trừ đi thuế gi&aacute;n thu(Te):</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px; text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Y=GNP-De-Te</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px; text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Y=NNP-Te</span></span></span></p>\r\n\r\n<ol start=\"3\" style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Thu nhập quốc d&acirc;n c&oacute; thể sử dụng (Y<img id=\"_x0000_i1036\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image012.wmz\" style=\"height:18pt; width:8.25pt\" /> )</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Phần thu nhập quốc d&acirc;n c&ograve;n lại sau khi trừ đi c&aacute;c loại thuế trực thu v&agrave; nhận được trợ cấp của ch&iacute;nh phủ hoặc doanh nghiệp.</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Y<img id=\"_x0000_i1037\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image013.wmz\" style=\"height:18pt; width:8.25pt\" /> &nbsp;=Y-Td+Tr</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Y<img id=\"_x0000_i1038\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image014.wmz\" style=\"height:18pt; width:8.25pt\" /> =Y-T</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Trong đ&oacute;: Tr: trợ cấp</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Td: thuế trực thu</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; T :thuế r&ograve;ng T=Td-Tr</span></span></span></p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Phương ph&aacute;p t&iacute;nh GDP</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">X&aacute;c định theo phương ph&aacute;p chi ti&ecirc;u</span></strong></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">GDP= C+I+G+NX</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">C: chi ti&ecirc;u cho ti&ecirc;u d&ugrave;ng của c&aacute;c hộ gia đ&igrave;nh</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">I: chi ti&ecirc;u cho đầu tư</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">G: chi ti&ecirc;u về h&agrave;ng h&oacute;a dịch vụ của ch&iacute;nh phủ</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">NX: xuất khẩu r&ograve;ng</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Ti&ecirc;u d&ugrave;ng của hộ gia đ&igrave;nh(C):</span></strong><span style=\"font-size:12.0pt\"> l&agrave; gi&aacute; trị của tất cả c&aacute;c h&agrave;ng h&oacute;a v&agrave; dịch vụ m&agrave; hộ gia đ&igrave;nh mua</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Bao gồm: h&agrave;ng l&acirc;u bền: oto, đồ nội thất,&hellip;</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; H&agrave;ng mau hỏng:đồ ăn, quần &aacute;o,&hellip;</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Dịch vụ: du lịch, cắt t&oacute;c,&hellip;.</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Ch&uacute; &yacute;: chỉ t&iacute;nh c&aacute;c khoản chi m&agrave; hộ gia đ&igrave;nh mua mới c&aacute;c sản phẩm, dịch vụ ti&ecirc;u d&ugrave;ng. Kh&ocirc;ng t&iacute;nh c&aacute;c khoản tự cung, tự cấp, cho, tặng,..</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Đầu tư (I)</span></strong><span style=\"font-size:12.0pt\">: Chi ti&ecirc;u mua h&agrave;ng h&oacute;a, dịch vụ sử dụng trong tương lai</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">I (tổng đầu tư)= Đầu tư r&ograve;ng+ khấu hao</span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Bao gồm</span></strong><span style=\"font-size:12.0pt\">:&nbsp;&nbsp;&nbsp; -Đầu tư mua t&agrave;i sản cố định</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -</span></strong><span style=\"font-size:12.0pt\">Đầu tư v&agrave;o nh&agrave; ở</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Đầu tư v&agrave;o h&agrave;ng tồn kho&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Ch&uacute; &yacute;: chỉ t&iacute;nh những khoản đầu tư mua sắm c&aacute;c tư liệu lao động mới,tạo ra tư bản dưới dạng hiện vật.&nbsp; kh&ocirc;ng t&iacute;nh c&aacute;c khoản đầu tư t&agrave;i ch&iacute;nh, mua sắm c&aacute;c tư liệu lao động đ&atilde; qua sử dụng</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Chi ti&ecirc;u của ch&iacute;nh phủ (G</span></strong><span style=\"font-size:12.0pt\">): bao gồm tất cả c&aacute;c khoản chi ti&ecirc;u của ch&iacute;nh phủ.</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Kh&ocirc;ng bao gồm c&aacute;c khoản chi chuyển giao thu nhập như trợ cấp.</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Xuất khẩu r&ograve;ng</span></strong><span style=\"font-size:12.0pt\"> (NX): NX=X-IM</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"2\" style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">X&aacute;c định GDP theo thu nhập( chi ph&iacute;)</span></strong></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">GDP được t&iacute;nh theo chi ph&iacute; c&aacute;c yếu tố đầu v&agrave;o của sx cộng với chi ph&iacute; khấu hao v&agrave; thuế gi&aacute;n thu</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">GDP =w +I + R + </span></strong><strong><span style=\"font-size:12.0pt\">&pi; </span></strong><strong><span style=\"font-size:12.0pt\">+ De + Te</span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Trong đ&oacute;:</span></span></span></p>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">W: tiền lương trả cho cho lao động</span></span></span></p>\r\n\r\n<p style=\"margin-l', '1552880835', null, '0', '68', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('730', 'chương 3', '<p>v</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">CHƯƠNG III: TỔNG CẦU V&Agrave; CH&Iacute;NH S&Aacute;CH T&Agrave;I KH&Oacute;A</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Gỉa thiết: </span></span></strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">-G&iacute;a cả cố định</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">-tổng cung sẵn c&oacute; v&agrave; c&oacute; thể đ&aacute;p ứng mọi nhu cầu cầu của nền kt. N&ecirc;n sản lượng c&acirc;n bằng do tổng cầu quyết định</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">- nghi&ecirc;n cứu thị trường h&agrave;ng h&oacute;a độc&nbsp; lập với thị trường tiền tệ</span></span></span></span></p>\r\n\r\n<ol style=\"list-style-type:upper-roman\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Tổng cầu v&agrave; sản lượng c&acirc;n bằng</span></span></strong></span></span></li>\r\n</ol>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Tổng cầu v&agrave; sản lượng c&acirc;n bằng trong nền kinh tế giản đơn</span></span></strong></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Tổng cầu l&agrave; tổng c&aacute;c nhu cầu chi ti&ecirc;u dự kiến của hộ gia đ&igrave;nh, doanh nghiệp để mua h&agrave;ng h&oacute;a v&agrave; dịch vụ trong nền kinh tế tương ứng</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">AD = C + I</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">AD: tổng cầu</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">C: cầu ti&ecirc;u d&ugrave;ng của hộ gia đ&igrave;nh</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:24px\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">I: cầu đầu tư của doanh nghiệp</span></span></span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Cầu ti&ecirc;u d&ugrave;ng (C)</span></span></strong></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">C: l&agrave; dự kiến chi ti&ecirc;u của c&aacute;c hộ gia đ&igrave;nh về c&aacute;c h&agrave;ng h&oacute;a dịch vụ cuối c&ugrave;ng.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">C phụ thuộc v&agrave;o: - thu nhập quốc d&acirc;n</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -Của cải/ t&agrave;i sản</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-tập qu&aacute;n t&acirc;m l&yacute;, thị hiếu ti&ecirc;u d&ugrave;ng,&hellip;</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -c&aacute;c ch&iacute;nh s&aacute;ch kinh tế vĩ m&ocirc; ( T, I,...)</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">H&agrave;m ti&ecirc;u d&ugrave;ng:&nbsp; C = <img id=\"_x0000_i1025\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image001.wmz\" style=\"height:20.25pt; width:69.75pt\" /> </span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">C:</span></span></strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"> cầu ti&ecirc;u d&ugrave;ng của hộ gia đ&igrave;nh</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><img id=\"_x0000_i1026\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image002.wmz\" style=\"height:17.25pt; width:12pt\" /> </span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">: ti&ecirc;u d&ugrave;ng tự định (ti&ecirc;u d&ugrave;ng kh&ocirc;ng phụ thuộc v&agrave;o thu nhập, mức ti&ecirc;u d&ugrave;ng tối thiểu)</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">MPC xu hướng ti&ecirc;u d&ugrave;ng cận bi&ecirc;n : MPC=<img id=\"_x0000_i1027\" src=\"file:///C:\\Users\\user\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image003.wmz\" style=\"height:33.75pt; width:26.25pt\" /> </span></span></span>