INSERT INTO `lesson` VALUES ('816', 'CHƯƠNG I: TỔNG QUAN VỀ KINH TẾ VI MÔ', '<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Kinh tế học l&agrave; m&ocirc;n khoa học gi&uacute;p cho con người hiểu về c&aacute;ch vận h&agrave;nh của nền kinh tế chung v&agrave; c&aacute;ch thức ứng xử của từng t&aacute;c nh&acirc;n trong nền kinh tế n&oacute;i ri&ecirc;ng.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Ph&acirc;n biệt kinh tế học vi m&ocirc; v&agrave; kinh tế học vĩ m&ocirc;:</span></span></span></li>\r\n</ol>\r\n\r\n<table border=\"1\" cellspacing=\"0\" class=\"MsoTableGrid\" style=\"border-collapse:collapse; border:solid windowtext 1.0pt; margin-left:-2px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:top; width:256.2pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Kinh tế học vi m&ocirc;</span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:224.3pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Kinh tế học vĩ m&ocirc;</span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:top; width:256.2pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Nghi&ecirc;n cứu c&aacute;c tế b&agrave;o của nền kinh tế l&agrave; c&aacute;c hộ gia đ&igrave;nh, doanh nghiệp hay thị trường ri&ecirc;ng lẻ</span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:224.3pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Nghi&ecirc;n cứu l&yacute; giải hoạt động của nền kinh tế với tư c&aacute;ch l&agrave; một tổng thể như tăng trưởng, lạm ph&aacute;t, thất nghiệp, c&aacute;c ch&iacute;nh s&aacute;ch kinh tế vĩ m&ocirc;&hellip;</span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:top; width:256.2pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Vd: x&aacute;c định gi&aacute; của &ocirc; t&ocirc;, doanh thu của một doanh nghiệp&hellip;</span></span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:224.3pt\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Vd: x&aacute;c định mức gi&aacute; chung của nền kinh tế, sản lượng của cả một nền kinh tế&hellip;</span></span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Kinh tế học thực chứng m&ocirc; tả, ph&acirc;n t&iacute;ch, giải th&iacute;ch c&aacute;c sự kiện, hiện tượng kinh tế một c&aacute;ch kh&aacute;ch quan, khoa học. Để nhận biết đ&acirc;u l&agrave; nghi&ecirc;n cứu kinh tế học thực chứng, ch&uacute;ng ta xem x&eacute;t nghi&ecirc;n cứu đ&oacute; c&oacute; trả lời cho c&aacute;c c&acirc;u hỏi sau hay kh&ocirc;ng: vấn đề đ&oacute; l&agrave; g&igrave;? L&agrave; như thế n&agrave;o? Tại sao lại như vậy? điều g&igrave; sẽ xảy ra? Khi nghi&ecirc;n cứu kinh tế học thực chứng ta xem x&eacute;t những luận điểm dưới dạng: nếu điều n&agrave;y thay đổi th&igrave; điều kia sẽ xảy ra.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Kinh tế học chuẩn tắc li&ecirc;n quan đến việc đ&aacute;nh gi&aacute; chủ quan của c&aacute;c c&aacute; nh&acirc;n, ph&aacute;n x&eacute;t về mặt gi&aacute; trị. C&aacute;c nghi&ecirc;n cứu kinh tế học chuẩn tắc thường để trả lời cho c&acirc;u hỏi: n&ecirc;n l&agrave;m g&igrave;? N&ecirc;n l&agrave;m như thế n&agrave;o?</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Khan hiếm l&agrave; t&igrave;nh trạng h&agrave;ng h&oacute;a, dịch vụ hoặc nguồn lực kh&ocirc;ng đủ so với mong muốn hay nhu cầu.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Nguồn lực l&agrave; khan hiếm v&igrave; số lượng nguồn lực được sử dụng để sản xuất ra h&agrave;ng h&oacute;a, dịch vụ l&agrave; c&oacute; han v&agrave; ng&agrave;y một cạn kiệt.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Chi ph&iacute; cơ hội: l&agrave; gi&aacute; trị của phương &aacute;n tốt nhất bị bỏ qua khi thực hện sự lựa chọn. Chi ph&iacute; cơ hội xuất hiện khi t&igrave;nh trạng khan hiếm nguồn lực xảy ra, v&igrave; khi đ&oacute; người ta sẽ buộc phải đ&aacute;nh đổi, nếu tiến h&agrave;nh hoạt động n&agrave;y th&igrave; phải bo qua h&agrave;nh động kh&aacute;c. Ch&iacute;nh v&igrave; v&acirc;y, khi đưa ra bất cứ sự lựa chọn kinh tế n&agrave;o, ch&uacute;ng ta cũng phải c&acirc;n nhắc, so s&aacute;nh c&aacute;c phương &aacute;n với nhau dựa theo chi ph&iacute; cơ hội của c&aacute;c phương &aacute;n đ&oacute; với nguy&ecirc;n tắc chọn phương &aacute;n c&oacute; chi ph&iacute; cơ hội l&agrave; nhỏ nhất.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Đường PPF l&agrave; t&acirc;p hợp c&aacute;c phối hợp tối đa số lượng c&aacute;c sản phảm m&agrave; nền kinh tế c&oacute; thể sản xuất được, l&agrave; đường gồm t&acirc;p hợp tất cả c&aacute;c điểm biểu thị sự kết hợp c&aacute;c nguồn lực th&iacute;ch hợp để sản xuấ ra một khối lượng sản phẩm nhất định của nền kinh tế. đường PPF cho biết c&aacute;c mức phối hợp tối đa của sản lượng m&agrave; nền kinh tế c&oacute; thể sản xuất được khi sử dụng to&agrave;n bộ nguồn lực sẵn c&oacute;.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Quy luật chi ph&iacute; cơ hội ng&agrave;y c&agrave;ng tăng l&ecirc;n cho biết: để sản xuất th&ecirc;m một đơn vị h&agrave;ng h&oacute;a n&agrave;y, x&atilde; hội sẽ phải bỏ ra ng&agrave;y c&agrave;ng nhiều c&aacute;c đơn bị của loại h&agrave;ng h&oacute;a kh&aacute;c.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Nền kinh tế l&agrave; một cơ chế ph&acirc;n bố c&aacute;c nguồn lực khan hiếm cho c&aacute;c mục ti&ecirc;u cạnh tranh. Cơ chế n&agrave;y nhằm gỉai quyết ba vấn đề kinh tế cơ bản: sản xuất c&aacute;i g&igrave;? Sản xuất như thế n&agrave;o? V&agrave; sản xuất cho ai?</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Nền kinh tế chỉ huy (khinh tế h&oacute;a tập trung): đối với nền kinh tế n&agrave;y, nh&agrave; nước đứng ra giải quyết ba vấn đề kinh tế cơ bản. Nh&agrave; nước giao chỉ ti&ecirc;u ph&aacute;p lệnh cho c&aacute;c ng&agrave;nh, c&aacute;c địa phương v&agrave; cơ sở sản xuất kinh doanh. Nh&agrave; nước tiến h&agrave;nh quốc doanh h&oacute;a v&agrave; tập thể h&oacute;a, x&oacute;a bỏ tư nh&acirc;n. Nh&agrave; nước cấp ph&aacute;t vốn v&agrave; vật tư cho c&aacute;c ng&agrave;nh, c&aacute;c địa phương v&agrave; cơ sở thực hiện nhiệm vụ. Sau khi ho&agrave;n th&agrave;nh nhiệm vụ phải giao nộp sản phẩm v&agrave; t&iacute;ch lũy cho nh&agrave; nước theo chỉ ti&ecirc;u ph&aacute;p lệnh. Nh&agrave; nước sử dụng chế độ ph&acirc;n phối bằng hiện vật cho c&aacute;c cơ quan nh&agrave; nước, d&ugrave;ng chế độ tem phiếu để ph&acirc;n phối cho người ti&ecirc;u d&ugrave;ng.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Nền kinh tế tự do: để giải quyế ba vấn đề kinh tế cơ bản, nền kinh tế thị trường tự do phải th&ocirc;ng qua hoạt động của quan hệ cung cầu tr&ecirc;n thị trường quan hệ cạnh tranh, gi&aacute; cả thị trường. c&aacute;ch thức giải quyết n&agrave;y xuất ph&aacute;t từ tư tưởng &ldquo;b&agrave;n tay v&ocirc; h&igrave;nh&rdquo; của Adam Smith. Gi&aacute; cả thị trường c&oacute; vai tr&ograve; quyết định trong qu&aacute; tr&igrave;nh lựa chọn va ra quyết định, gi&aacute; cả thị trường do quan hệ cung cầu quyết định v&agrave; phản &aacute;nh quan hệ cung cầu, cạnh tranh tr&ecirc;n thị trường.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">M&ocirc; h&igrave;nh kinh tế hỗn hợp: nền kinh tế hỗn hợp, đ&ograve;i hỏi trước hết phải ph&aacute;t triển theo cơ chế thị trường (b&agrave;n tay v&ocirc; h&igrave;nh), c&oacute; nghĩa l&agrave; cần ph&aacute;t triển c&aacute;c quan hệ cung cầu, cạnh tranh, t&ocirc;n trọng vai tr&ograve; của gi&aacute; cả thị trường, lấy lợi nhu&acirc;n l&agrave;m mục ti&ecirc;u v&agrave; động cơ phấn đấu. tuy nhi&ecirc;n, b&agrave;n tay hữu h&igrave;nh cũng rất c&acirc;n thiết đ&oacute; l&agrave; sự can thiệp của Nh&agrave; nước. ( nền kinh tế Việt Nam hiện nay l&agrave; nền kinh tế hỗn hợp).</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Một số vấn đề cần note:</span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Chi ph&iacute; cơ hội =</span></span></span><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">\\[\\left| {\\frac{{\\Delta Y}}{{\\Delta X}}} \\right|\\]</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Nền kinh tế hỗn hợp = mệnh lệnh + thị trường.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Sự kh&aacute;c biệt căn bản giữa m&ocirc; h&igrave;nh kinh tế hỗn hợp v&agrave; m&ocirc; h&igrave;nh kinh tế thị trường l&agrave;&nbsp; <em><u>nh&agrave; nước tham gia quản l&iacute; kinh tế</u></em>.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Vấn đề kh&ocirc;ng được m&ocirc; tả tr&ecirc;n đường giới han khả năng sản xuất l&agrave; <em><u>cung cầu.</u></em></span></span></span></li>\r\n</ul>', '1552981468', null, '0', '70', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('817', 'CHƯƠNG II: CUNG CẦU VÀ CƠ CHẾ HOẠT ĐỘNG CỦA THỊ TRƯỜNG', '<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Thị trường l&agrave; một tập hợp c&aacute;c d&agrave;n xếp m&agrave; th&ocirc;ng qua đ&oacute; những người b&aacute;n v&agrave; người mua tiếp x&uacute;c với nhau để trao đổi h&agrave;ng h&oacute;a v&agrave; dịch vụ.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Thị trường cạnh tranh ho&agrave;n hảo (thuần t&uacute;y): đ&acirc;y l&agrave; một thị trường c&oacute; rất nhiều người mua v&agrave; người b&aacute;n, trao đổi một loại sản phẩm đồng nhất, mọi th&ocirc;ng tin tr&ecirc;n thị trường n&agrave;y đều được người b&aacute;n, người mua nắm r&otilde; v&agrave; họ kh&ocirc;ng c&oacute; quyền quyết định đến mức gi&aacute; cũng như sản lượng h&agrave;ng h&oacute;a trao đổi tr&ecirc;n thị trường.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Thị trường độc quyền thu&acirc;n t&uacute;y (độc quyền mua hoặc độc quyền b&aacute;n): chỉ c&oacute; một người mua v&agrave; nhiều người b&aacute;n hoặc chỉ c&oacute; một người b&aacute;n nhiều người mua.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Thị trường cạnh tranh kh&ocirc;ng ho&agrave;n hảo: bao gồm cạnh tranh độc quyền v&agrave; độc quyền t&acirc;p đo&agrave;n.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Cầu (D) l&agrave; số lượng h&agrave;ng h&oacute;a hoặc dịch vụ m&agrave; người mua muốn mua v&agrave; c&oacute; khả năng mua tại c&aacute;c mức gi&aacute; kh&aacute;c nhau trong một khỏang thời gian nhất định, c&aacute;c yếu tố kh&aacute;c kh&ocirc;ng đổi. nếu thiếu một trong hai yếu tố muốn mua v&agrave; c&oacute; khả năng mua th&igrave; sẽ kh&ocirc;ng tồn tại cầu. muốn mua biểu thị nhu cầu của người ti&ecirc;u d&ugrave;ng về một h&agrave;ng h&oacute;a hoặc dịch vụ n&agrave;o đ&oacute;. C&oacute; khả năng mua biểu thị khả năng thanh to&aacute;n.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Lượng cầu (</span></span></span><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:13.0pt\"><span class=\"math-tex\">\\({Q_D}\\)</span></span></span> <span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">) l&agrave; số lượng h&agrave;ng h&oacute;a hoặc dịch vụ cụ thể m&agrave; người mua muốn mua v&agrave; sẵn s&agrave;ng mua tại mức gi&aacute; đ&atilde; cho trong một khoảng thời gian nhất định.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Nhu cầu l&agrave; những mong muốn, sở th&iacute;ch của người ti&ecirc;u d&ugrave;ng về h&agrave;ng h&oacute;a v&agrave; dịch vụ, nhưng c&oacute; thể kh&ocirc;ng c&oacute; khả năng thanh to&aacute;n.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">C&aacute;c yếu tố t&aacute;c động đến cầu:</span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Thu nhập của người ti&ecirc;u d&ugrave;ng, xem x&eacute;t đối với&nbsp; c&aacute;c hoạt động h&agrave;ng h&oacute;a (xa xỉ, cao cấp, thiết yếu v&agrave; thứ cấp).</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">H&agrave;ng h&oacute;a li&ecirc;n quan trong ti&ecirc;u d&ugrave;ng gồm: h&agrave;ng h&oacute;a thay thế hoặc h&agrave;ng h&oacute;a bổ sung</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Số lượng người ti&ecirc;u d&ugrave;ng hay quy m&ocirc; thị trường l&agrave; một trong những yếu tố quan trọng x&aacute;c định lượng ti&ecirc;u d&ugrave;ng tiềm năng.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">C&aacute;c ch&iacute;nh s&aacute;ch kinh tế của ch&iacute;nh phủ: đ&aacute;nh thuế v&agrave;o người ti&ecirc;u d&ugrave;ng th&igrave; cầu sẽ giảm, ch&iacute;nh phủ trợ cấp người ti&ecirc;u d&ugrave;ng th&igrave; cầu sẽ tăng&hellip;k&igrave; vọng thu nhập v&agrave; kỳ vọng về gi&aacute; cả.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Thị hiếu, phong tục, t&acirc;p qu&aacute;n, mốt, quảng c&aacute;o&hellip;</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">C&aacute;c nh&acirc;n tố kh&aacute;c bao gồm: m&ocirc;i trường tự nhi&ecirc;n, sự kiện mang t&iacute;nh thời sự&hellip;</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"9\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Luật cầu: số lượng h&agrave;ng h&oacute;a được cầu trong khoảng thời gian đ&atilde; tăng l&ecirc;n khi gi&aacute; của h&agrave;ng h&oacute;a đ&oacute; giảm xuống v&agrave; ngược lại, giả định c&aacute;c yếu tố kh&aacute;c kh&ocirc;ng đổi.</span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Gi&aacute; cả tăng th&igrave; lượng c&acirc;u giảm: P</span><em><span style=\"font-size:13.0pt\">&uarr;</span></em><span style=\"font-size:11.0pt\"><img src=\"file:///C:\\Users\\ADMIN\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image004.png\" style=\"height:27px; width:10px\" /></span><span style=\"font-size:13.0pt\">&nbsp;th&igrave; Q</span><em><span style=\"font-size:13.0pt\">&darr;</span></em><span style=\"font-size:11.0pt\"><img src=\"file:///C:\\Users\\ADMIN\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image006.png\" style=\"height:27px; width:10px\" /></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Gi&aacute; cả giảm th&igrave; lượng cầu tăng: P</span><em><span style=\"font-size:13.0pt\">&darr;</span></em><span style=\"font-size:11.0pt\"><img src=\"file:///C:\\Users\\ADMIN\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image006.png\" style=\"height:27px; width:10px\" /></span><span style=\"font-size:13.0pt\">&nbsp;th&igrave; Q</span><em><span style=\"font-size:13.0pt\">&uarr;</span></em><span style=\"font-size:11.0pt\"><img src=\"file:///C:\\Users\\ADMIN\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image004.png\" style=\"height:27px; width:10px\" /></span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"10\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Phương tr&igrave;nh đường cầu: </span></span></span><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:13.0pt\"><span class=\"math-tex\">\\({Q_D}\\)</span></span></span> <span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">= a - bP</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Cung (S) l&agrave; số lượng h&agrave;ng h&oacute;a hoặc dịch vụ m&agrave; người b&aacute;n muốn b&aacute;n v&agrave; c&oacute; khả năng b&aacute;n tại c&aacute;c mức gi&aacute; kh&aacute;c nhau trong một khoảng thời gian nhất định, c&aacute;c yếu tố kh&aacute;c kh&ocirc;ng đổi.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Lượng cung </span></span></span><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:13.0pt\"><span class=\"math-tex\">\\({Q_S}\\)</span>&nbsp;</span></span><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">l&agrave; lượng h&agrave;ng h&oacute;a hoặc dịch vụ cụ thể m&agrave; người b&aacute;n muốn b&aacute;n v&agrave; sẵn s&agrave;ng b&aacute;n tại mức gi&aacute; đ&atilde; cho trong một khoảng thời gian nhất định.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Luật cung: số lượng h&agrave;ng h&oacute;a được cung trong khoảng thời gian đ&atilde; cho tăng l&ecirc;n khi gi&aacute; của n&oacute; tăng l&ecirc;n v&agrave; ngược lại, giả định c&aacute;c yếu tố kh&aacute;c kh&ocirc;ng đổi.</span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Gi&aacute; cả tăng th&igrave; lượng cung tăng: P</span><em><span style=\"font-size:13.0pt\">&uarr;</span></em><span style=\"font-size:11.0pt\"><img src=\"file:///C:\\Users\\ADMIN\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image004.png\" style=\"height:27px; width:10px\" /></span><span style=\"font-size:13.0pt\">&nbsp;th&igrave; Q</span><em><span style=\"font-size:13.0pt\">&uarr;</span></em><span style=\"font-size:11.0pt\"><img src=\"file:///C:\\Users\\ADMIN\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image004.png\" style=\"height:27px; width:10px\" /></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Gi&aacute; cả giảm th&igrave; lượng cung giảm: P</span><em><span style=\"font-size:13.0pt\">&darr;</span></em><span style=\"font-size:11.0pt\"><img src=\"file:///C:\\Users\\ADMIN\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image006.png\" style=\"height:27px; width:10px\" /></span><span style=\"font-size:13.0pt\">&nbsp;th&igrave; Q</span><em><span style=\"font-size:13.0pt\">&darr;</span></em><span style=\"font-size:11.0pt\"><img src=\"file:///C:\\Users\\ADMIN\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image006.png\" style=\"height:27px; width:10px\" /></span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"14\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Phương tr&igrave;nh đường cung: </span></span></span><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:13.0pt\"><span class=\"math-tex\">\\({Q_S}\\)</span></span></span><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;= c + dP</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">C&aacute;c yếu tố t&aacute;c động đến cung:</span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Tiến bộ c&ocirc;ng nghệ (ứng dụng c&ocirc;ng nghệ mới l&agrave;m tăng năng suất.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Gi&aacute; của c&aacute;c yếu tố đầu v&agrave;o của qu&aacute; tr&igrave;nh sản xuất (chi ph&iacute; sản xuất).</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Số lượng nh&agrave; sản xuất trong ng&agrave;nh.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Gi&aacute; của c&aacute;c h&agrave;ng h&oacute;a li&ecirc;n quan trong sản xuất.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">C&aacute;c ch&iacute;nh s&aacute;ch kinh tế của ch&iacute;nh phủ</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">L&atilde;i suất</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Kỳ vọng gi&aacute; cả v&agrave; thu nh&acirc;p</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Điều kiện thời tiết kh&iacute; h&acirc;u</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">M&ocirc;i trường kinh doanh</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"16\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">C&acirc;n bằng thị trường: l&agrave; trạng th&aacute;i tại đ&oacute; kh&ocirc;ng c&oacute; sức &eacute;p l&agrave;m thay đổi gi&aacute; v&agrave; sản lượng. c&acirc;n bằng thị trường l&agrave; trạng th&aacute;i m&agrave; khả năng cung ứng vừa đủ cho nhu cầu thị trường. t&aacute;c động qua lại giữa cung c&agrave; cầu x&aacute;c định gi&aacute; va sản lượng h&agrave;ng h&oacute;a, dịch vụ được mua v&agrave; b&aacute;n tr&ecirc;n thị trường. khi tất cả mọi người tham gia v&agrave;o thị trường c&oacute; thể mua v&agrave; b&aacute;n một lượng bất k&igrave; m&agrave; họ mong muốn, ch&uacute;ng ta n&oacute;i rằng thị trường trong trạng th&aacute;i c&acirc;n bằng. mức gi&aacute; m&agrave; người mua sẵn l&ograve;ng mua va người b&aacute;n muốn b&aacute;n theo &yacute; của họ được gọi lại mức gi&aacute; c&acirc;n bằng.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Thặng dư ti&ecirc;u d&ugrave;ng (CS) l&agrave; kh&aacute;i niệm phản &aacute;nh sự ch&ecirc;nh lệch giữa lợi &iacute;ch của người ti&ecirc;u d&ugrave;ng một đơn vị h&agrave;ng h&oacute;a n&agrave;o đ&oacute; với chi ph&iacute; thực tế để c&oacute; được lợi &iacute;ch đ&oacute;. Thặng dư ti&ecirc;u d&ugrave;ng l&agrave; phần ch&ecirc;nh lệch giữa gi&aacute; m&agrave; một người ti&ecirc;u d&ugrave;ng đ&oacute; thực sự phải trả khi mua h&agrave;ng h&oacute;a đ&oacute;.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Thặng dư sản xuất (PS) l&agrave; phần diện t&iacute;ch nằm dưới đường gi&aacute; v&agrave; tr&ecirc;n đường cung.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Độ co gi&atilde;n của cầu theo gi&aacute; l&agrave; hệ số (tỷ lệ) giữa % thay đổi trong lượng cầu so với % thay đổi trong gi&aacute; cả của h&agrave;ng h&oacute;a đ&oacute;. Khi gi&aacute; cả tăng 1% th&igrave; lượng cầu của h&agrave;ng h&oacute;a giảm bao nhi&ecirc;u % va ngược lại. hệ số co d&atilde;n của cầu theo gi&aacute; đo lường mức độ phản ứng của gi&aacute; cả so với lượng cầu (c&aacute;c yếu tố kh&aacute;c kh&ocirc;ng đổi). </span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;CT: </span></span></span><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:13.0pt\">\\[E_P^D = \\frac{{\\% \\Delta Q}}{{\\% \\Delta P}} = \\frac{{\\Delta Q}}{Q}:\\frac{{\\Delta P}}{P} = \\frac{{\\Delta Q}}{{\\Delta P}}.\\frac{P}{Q}\\]</span></span><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></span></p>\r\n\r\n<ol start=\"20\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Độ co d&atilde;n của cầu theo thu nhập l&agrave; hệ số phản &aacute;nh % thay đổi trong lượng cầu so với % thay đổi trong thu nhập. n&oacute;i c&aacute;c kh&aacute;c, khi thu nhập thay đổi 1% th&igrave; lượng cầu thay đổi bao nhi&ecirc;u %. Hệ số co d&atilde;n của cầu theo thu nhập đo lường mức độ phản ứng của thu nhập của người ti&ecirc;u d&ugrave;ng so với lượng cầu (c&aacute;c nh&acirc;n tố kh&aacute;c kh&ocirc;ng đổi).</span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; CT:&nbsp;</span><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:13.0pt\">\\[E_I^D = \\frac{{\\% \\Delta Q}}{{\\% \\Delta I}} = \\frac{{\\Delta Q}}{{\\Delta I}}.\\frac{I}{Q} = {Q&#39;_{\\left( I \\right)}}.\\frac{I}{Q}\\]</span></span></span></span></p>\r\n\r\n<ol start=\"21\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Độ co d&atilde;n của cầu theo gi&aacute; ch&eacute;o l&agrave; hệ số phản &aacute;nh % thay đổi trong lượng cầu của h&agrave;n h&oacute;a n&agrave;y so với % thay đổi trong gi&aacute; cả của h&agrave;ng h&oacute;a kia. N&oacute;i c&aacute;ch kh&aacute;c, khi gi&aacute; cả của h&agrave;ng h&oacute;a kia thay đổi 1% th&igrave; lượng cầu của h&agrave;ng h&oacute;a n&agrave;y thay đổi bao nhi&ecirc;u %. Hệ số co d&atilde;n của cầu theo gi&aacute; ch&eacute;o đo lường mức độ phản ứng của gi&aacute; cả của h&agrave;ng h&oacute;a kia so với lượng cầu của h&agrave;ng h&oacute;a n&agrave;y (c&aacute;c yếu tố kh&aacute;c kh&ocirc;ng đổi).</span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">CT:&nbsp;</span><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:13.0pt\">\\[E_{{P_Y}}^{{D_X}} = \\frac{{\\% \\Delta {Q_X}}}{{\\% \\Delta {P_Y}}} = \\frac{{\\Delta {Q_X}}}{{\\Delta {P_Y}}}.\\frac{{{P_Y}}}{{{Q_X}}} = {Q&#39;_{\\left( {{P_Y}} \\right)}}.\\frac{{{P_Y}}}{{{Q_X}}}\\]</span></span></span></span></p>\r\n\r\n<ol start=\"22\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Gi&aacute; trần l&agrave; mức gi&aacute; cao nhất đối với một mặt h&agrave;ng n&agrave;o đ&oacute; do ch&iacute;nh phủ ấn định. T&aacute;c dụng của gi&aacute; trần l&agrave; nhằm bảo vệ lợi &iacute;ch của người ti&ecirc;u d&ugrave;ng. C&aacute;c doanh nghiệp sản xuất kh&ocirc;ng được đặt gi&aacute; cao hơn mức gi&aacute; trần. mức gi&aacute; n&agrave;y &aacute;p dụng cho những h&agrave;ng h&oacute;a (dịch vụ) thiết yếu ảnh hưởng trực tiếp đến đời sống nh&acirc;n d&acirc;n như: xăng dầu, gi&aacute; thu&ecirc; nh&agrave; cho người ngh&egrave;o v&agrave; sinh vi&ecirc;n&hellip;</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Gi&aacute; s&agrave;n l&agrave; mức gi&aacute; thấp nhất đối với một mặt h&agrave;ng h&oacute;a hay dịch vụ n&agrave;o đ&oacute; do ch&iacute;nh phủ quy định. T&aacute;c dụng của gi&aacute; s&agrave;n l&agrave; nhằm bảo vệ lợi &iacute;ch của nh&agrave; sản xuất. c&oacute; 2 loại gi&aacute; s&agrave;n: gi&aacute; s&agrave;n cao hơn mức gi&aacute; c&acirc;n bằn tr&ecirc;n thị trường v&agrave; gi&aacute; s&agrave;n thấp hơn gi&aacute; c&acirc;n bằng tr&ecirc;n thị trường. đối với mức gi&aacute; s&agrave;n thấp hơn gi&aacute; c&acirc;n bằng th&igrave; đ&acirc;y l&agrave; mức gi&aacute; s&agrave;n kh&ocirc;ng c&oacute; r&agrave;ng buộc, &iacute;t khi xảy ra. C&ograve;n mức gi&aacute; s&agrave;n cao hơn gi&aacute; c&acirc;n bằng tr&ecirc;n thị trường l&agrave; mức gi&aacute; c&oacute; r&agrave;ng buộc.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Trạng th&aacute;i dư thừa: khi gi&aacute; b&aacute;n cao hơn gi&aacute; thị trường&nbsp; sẽ xuất hiện trạng th&aacute;i dư thừa (dư cung) h&agrave;ng h&oacute;a một lượng:&nbsp;</span></span></span><span style=\"font-size:13.0pt\"><span class=\"math-tex\">\\(Q = \\;{Q_S} - {Q_D}\\)</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Trạng th&aacute;i thiếu hụt: gi&aacute; c&acirc;n bằng tr&ecirc;n thị trường giảm l&agrave;m cho lượng cung tr&ecirc;n thị trường giảm đi v&agrave; ngược lại, người ti&ecirc;u d&ugrave;ng mua nhiều hơn dẫn đến hiện tượng cầu lớn hơn cung hay thiếu h&agrave;ng h&oacute;a một lượng:&nbsp;</span></span></span><span style=\"font-size:13.0pt\">\\[Q = \\;{Q_D} - {Q_s}\\]</span></li>\r\n</ol>', '1552982322', null, '0', '70', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('818', 'CHƯƠNG III: LÝ THUYẾT VỀ HÀNH VI CỦA NGƯỜI TIÊU DÙNG', '<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Thị hiếu người ti&ecirc;u d&ugrave;ng được xem x&eacute;t dựa v&agrave;o 3 giải định cơ bản gồm: sở th&iacute;ch người ti&ecirc;u d&ugrave;ng c&oacute; t&iacute;nh chất ho&agrave;n chỉnh, sở th&iacute;ch người ti&ecirc;u d&ugrave;ng c&oacute; t&iacute;nh chất bắc cầu v&agrave; người ti&ecirc;u d&ugrave;ng kh&ocirc;ng bao giờ thảo m&atilde;n, th&iacute;ch nhiều hơn th&iacute;ch &iacute;t.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Lợi &iacute;ch của người ti&ecirc;u d&ugrave;ng được đo bằng đường b&agrave;ng quan, l&agrave; một tập hợp c&aacute;c điểm m&ocirc; tả những l&ocirc; h&agrave;ng h&oacute;a kh&aacute;c nhau nhưng ch&uacute;ng đem lại cho người d&ugrave;ng một độ thảo dụng lợi &iacute;ch như nhau, được k&yacute; hiệu l&agrave; U.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Đường b&agrave;ng quan (U) c&oacute; t&iacute;nh chất cơ bản: c&oacute; h&igrave;nh dạng cong lồi về ph&iacute;a gốc tọa độ v&agrave; c&oacute; độ dốc &acirc;m, c&agrave;ng xa gốc tọa độ biểu thị lợi &iacute;ch c&agrave;ng cao, hai đường b&agrave;ng quan kh&ocirc;ng bao giờ cắt nhau.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;Khi nghi&ecirc;n cứu lợi &iacute;ch của người ti&ecirc;u d&ugrave;ng cho thấy, lợi &iacute;ch của người ti&ecirc;u d&ugrave;ng tu&acirc;n theo một quy luật mang t&iacute;nh x&atilde; hội &ldquo;quy luật lợi &iacute;ch cận bi&ecirc;n giảm dần: lợi &iacute;ch cận bi&ecirc;n của một h&agrave;ng h&oacute;a c&oacute; xu hướng giảm đi khi lượng h&agrave;ng h&oacute;a đ&oacute; được người ti&ecirc;u d&ugrave;ng nhiều hơn trong một giai đoạn nhất định&rdquo;. Bản chất l&agrave; sự h&agrave;i l&ograve;ng hay sự th&iacute;ch th&uacute; của ti&ecirc;u d&ugrave;ng với một mặt h&agrave;ng c&oacute; xu hướng giảm đi khi ti&ecirc;u d&ugrave;ng th&ecirc;m một đơn vị mặt h&agrave;n đ&oacute;.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Tỷ lệ thay thế c&acirc;n bi&ecirc;n trong ti&ecirc;u d&ugrave;ng cho biết sự sẵng s&agrave;ng đ&aacute;nh đổi h&agrave;ng h&oacute;a n&agrave;y để lấy h&agrave;ng h&oacute;a kh&aacute;c của người ti&ecirc;u d&ugrave;ng sao cho tổng thỏa m&atilde;n của họ được giữ kh&ocirc;ng đổi.</span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CT</span><span style=\"font-size:13.0pt\">:&nbsp;</span></span></span><span style=\"font-size:13.0pt\">\\[MR{S_{X/Y}} = \\frac{{M{U_X}}}{{M{U_Y}}}\\]</span></p>\r\n\r\n<ol start=\"6\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Người ti&ecirc;u d&ugrave;ng lu&ocirc;n kh&ocirc;ng thảo m&atilde;n với lợi &iacute;ch hiện c&oacute;, tuy nhi&ecirc;n h&agrave;nh vi ti&ecirc;u d&ugrave;ng lu&ocirc;n bị r&agrave;ng buộc bởi một giới hạn ng&acirc;n s&aacute;ch nhất định, tổ hợp số lượng tối đa h&agrave;ng h&oacute;a c&oacute; thể mua trong mức ng&acirc;n s&aacute;ch nhất định được gọi l&agrave; đường ng&acirc;n s&aacute;ch.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Đường ng&acirc;n s&aacute;ch k&iacute; hiệu l&agrave; I hoặc M c&oacute; thể thay đổi khi tổng thu nhập cho ti&ecirc;u d&ugrave;ng của người ti&ecirc;u d&ugrave;ng thay đổi hoặc khi gi&aacute; cả h&agrave;ng h&oacute;a m&agrave; người ti&ecirc;u d&ugrave;ng dự kiến mua thay đổi.</span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Phương tr&igrave;nh đường ng&acirc;n s&aacute;ch:</span><span style=\"font-size:13.0pt\">&nbsp;</span></span></span>&nbsp; &nbsp;<span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">\\[X.{P_X} + Y.{P_Y} = I\\]</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Phương tr&igrave;nh giới hạn ng&acirc;n s&aacute;ch:</span><span style=\"font-size:13.0pt\">&nbsp;</span></span></span><span style=\"font-size:13.0pt\">\\[X.{P_X} + Y.{P_Y} \\le I\\]</span></li>\r\n</ul>\r\n\r\n<ol start=\"8\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Khi thu nhập ti&ecirc;u d&ugrave;ng thay đổi, đường ng&acirc;n s&aacute;ch kh&ocirc;ng thay đổi độ dốc m&agrave; dịch chuyển song song; c&ograve;n khi gi&aacute; cả h&agrave;ng h&oacute;a người ti&ecirc;u d&ugrave;ng dự kiến mua thay đổi sẽ l&agrave;m cho đường ng&acirc;n s&aacute;ch tay đổi độ dốc nếu gi&aacute; c&aacute;c h&agrave;ng h&oacute;a thay đổi kh&ocirc;ng c&ugrave;ng một tỷ lệ v&agrave; đường ng&acirc;n s&aacute;ch dịch chuển song song nếu gi&aacute; cả h&agrave;ng h&oacute;a thay đổi c&ugrave;ng tỉ lệ.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Nguy&ecirc;n tắc lựa chọn để tối đa h&oacute;a lợi &iacute;ch của người ti&ecirc;u d&ugrave;ng l&agrave; lợi &iacute;ch cận bi&ecirc;n t&iacute;nh tr&ecirc;n một đồng của h&agrave;ng h&oacute;a n&agrave;y phải bằng với lợi &iacute;ch c&acirc;n bi&ecirc;n t&iacute;nh tr&ecirc;n một đồng của bất kỳ h&agrave;ng h&oacute;a n&agrave;o kh&aacute;c.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Điểm ti&ecirc;u d&ugrave;ng tối ưu sẽ thay đổi khi c&aacute;c yếu tố t&aacute;c động đến đường ng&acirc;n s&aacute;ch thay đổi, như thu nhập d&agrave;nh cho người ti&ecirc;u d&ugrave;ng của người ti&ecirc;u d&ugrave;ng thay đổi; gi&aacute; cả của h&agrave;ng h&oacute;a m&agrave; người ti&ecirc;u d&ugrave;ng dự kiến mua thay đổi.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Điều kiện cần v&agrave; đủ để người ti&ecirc;u d&ugrave;ng tối đa h&oacute;a lợi &iacute;ch tại một mức ng&acirc;n s&aacute;ch nhất định:&nbsp;</span></span></span>\r\n	<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:Cambria,serif\"><span style=\"font-size:13.0pt\">\\[\\left\\{ {\\begin{array}{*{20}{c}}{\\frac{{M{U_X}}}{{{P_X}}} = \\frac{{M{U_Y}}}{{{P_Y}}}}\\\\{X.{P_X} + Y.{P_Y} = I}\\end{array}} \\right.\\]</span></span></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol start=\"12\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Điều kiện cần v&agrave; đủ để người ti&ecirc;u d&ugrave;ng lựa chọn được giỏ h&agrave;ng h&oacute;a tối ưu để tối thiểu h&oacute;a chi ti&ecirc;u tại mức lợi &iacute;ch nhất định:</span>&nbsp;</span></span>\r\n\r\n	<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:Cambria,serif\"><span style=\"font-size:13.0pt\">\\[\\left\\{ {\\begin{array}{*{20}{c}}{\\frac{{M{U_X}}}{{{P_X}}} = \\frac{{M{U_Y}}}{{{P_Y}}}}\\\\{T{U_{X,Y}} = f(X,Y) = T{U_I}}\\end{array}} \\right.\\]</span></span></span></span></p>\r\n	</li>\r\n</ol>', '1552983037', null, '0', '70', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('819', 'CHƯƠNG IV: LÝ THUYẾT VỀ HÀNH VI CỦA CÁC DOANH NGHIỆP', '<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">H&agrave;m sản xuất l&agrave; một m&ocirc; h&igrave;nh to&aacute;n học cho biết lượng đầu ra tối đa của một doanh nghiệp c&oacute; thể tạo ra được từ c&aacute;c t&acirc;p hợp kh&aacute;c nhau của c&aacute;c yếu tố đầu v&agrave;o tương ứng với một tr&igrave;nh độ c&ocirc;ng nghệ nhất định.</span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Dạng h&agrave;m sản xuất tuyến t&iacute;nh: </span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-size:13.0pt\">\\[Q = aK + bL(a,b &gt; 0)\\]</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">H&agrave;m sản xuất Leontief: </span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-size:13.0pt\">\\[Q = \\min (aK,bL)(a,b &gt; 0)\\]</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">H&agrave;m sản xuất Cobb-Douglas:</span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span></span><span style=\"font-size:13.0pt\">\\[Q = A.{K^\\alpha }{L^\\beta }(A,\\alpha ,\\beta&nbsp; &gt; 0)\\]</span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Sản phẩm trung b&igrave;nh của một yếu tố đầu v&agrave;o (AP) l&agrave; số sản phẩm b&igrave;nh qu&acirc;n do một đơn vị đ&acirc;u v&agrave;o tạo ra trong một thời gian nhất định.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Sản phẩm trung b&igrave;nh của lao động (</span></span></span><span style=\"font-size:13.0pt\"><span class=\"math-tex\">\\(A{P_L}\\)</span></span> <span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">) l&agrave; số sản phẩm t&iacute;nh b&igrave;nh qu&acirc;n do một đơn vị đầu v&agrave;o lao động tạo ra.</span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">CT: </span></span></span><span style=\"font-size:13.0pt\"><span class=\"math-tex\">\\(A{P_L}\\)</span></span> &nbsp;<span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">= </span><em><span style=\"font-size:13.0pt\">Q</span></em><em><span style=\"font-size:13.0pt\">L</span></em><span style=\"font-size:11.0pt\"><img src=\"file:///C:\\Users\\ADMIN\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image010.png\" style=\"height:38px; width:11px\" /></span></span></span></p>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Sản phẩm trung b&igrave;nh của vốn( </span></span></span><span style=\"font-size:13.0pt\"><span class=\"math-tex\">\\(A{P_K}\\)</span>&nbsp;</span><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">) l&agrave; số sản phẩm t&iacute;nh b&igrave;nh qu&acirc;n do một đơn vị đầu v&agrave;o vốn tạo ra.</span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">CT: </span></span></span><span style=\"font-size:13.0pt\"><span class=\"math-tex\">\\(A{P_K}\\)</span>&nbsp;</span><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">= </span><em><span style=\"font-size:13.0pt\">Q</span></em><em><span style=\"font-size:13.0pt\">K</span></em><span style=\"font-size:11.0pt\"><img src=\"file:///C:\\Users\\ADMIN\\AppData\\Local\\Temp\\msohtmlclip1\\01\\clip_image014.png\" style=\"height:38px; width:11px\" /></span></span></span></p>\r\n\r\n<ol start=\"5\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Sản phẩm cận bi&ecirc;n của lao động </span></span></span><span style=\"font-size:13.0pt\"><span class=\"math-tex\">\\(M{P_L}\\)</span></span> <span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;phản &aacute;nh sự thay đổi trong tổng số sản phẩm sản xuất ra khi yếu tố đầu v&agrave;o lao động thay đổi một đơn vị.</span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">CT: </span></span></span><span style=\"font-size:13.0pt\"><span class=\"math-tex\">\\(M{P_L}\\)</span></span> &nbsp;<span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">=&nbsp;</span></span></span><span style=\"font-size:13.0pt\"><span class=\"math-tex\">\\(\\frac{Q}{L}\\)</span></span></p>\r\n\r\n<ol start=\"6\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Sản phẩm cận bi&ecirc;n của vốn:</span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; MRT{S_{L/K}} = &nbsp;- \\frac{\\Delta{K}}{\\Delta{L}}</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">7. Quy luật sản phẩm cận bi&ecirc;n giảm dần: khi gia tăng li&ecirc;n tiếp những đơn vị của một yếu tố đầu v&agrave;o biến đổi trong khi cố định c&aacute;c yếu tố đầu v&agrave;o kh&aacute;c th&igrave; đến một l&uacute;c n&agrave;o đ&oacute;, sản phẩm cận bi&ecirc;n của yếu tố đầu v&agrave;o biến đổi đ&oacute; sẽ giảm dần.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">8.Tỷ lệ thay thế kỹ thuật cận bi&ecirc;n đo lường mức độ thay thế được cho nhau giữa c&aacute;c yếu tố đầu v&agrave;o.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">CT:&nbsp;</span></span></span><span style=\"font-family:Cambria,serif\"><span style=\"font-size:13.0pt\"><span class=\"math-tex\">\\(M{P_K} = \\frac{\\Delta{K}}{\\Delta{Q}}\\)</span></span></span></p>\r\n\r\n<ol start=\"9\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Hiệu suất kinh tế theo quy m&ocirc;:</span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Hiệu suất kinh tế tăng theo quy m&ocirc;</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Hiệu suất kinh tế giảm theo quy m&ocirc;</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Hiệu suất kinh tế kh&ocirc;ng đổi theo quy m&ocirc;</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"5\" style=\"list-style-type:lower-roman\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Đối với h&agrave;m sản xuất Cobb-Douglas</span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Khi </span></span></span><span style=\"font-size:13.0pt\"><span class=\"math-tex\">\\(\\alpha \\; + \\;\\beta \\)</span></span> <span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&gt; 1 h&agrave;m thể hiện hiệu suất kinh tế tăng theo quy m&ocirc;</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Khi </span></span></span><span style=\"font-size:13.0pt\"><span class=\"math-tex\">\\(\\alpha \\; + \\;\\beta \\)</span></span> <span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&lt; 1 h&agrave;m thể hiện hiệu suất kinh tế giảm theo quy m&ocirc;</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Khi </span></span></span><span style=\"font-size:13.0pt\"><span class=\"math-tex\">\\(\\alpha \\; + \\;\\beta \\)</span></span> <span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;= 1 h&agrave;m thể hiện hiệu suất kinh tế kh&ocirc;ng đổi theo quy m&ocirc;</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"10\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Tổng chi ph&iacute; (TC) l&agrave; to&agrave;n bộ những ph&iacute; tổn d&ugrave;ng để tiến h&agrave;nh sản xuất kinh doanh trong ngắn hạn.</span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">CT: TC = TFC + TVC</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Trong đ&oacute;:</span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">TFC l&agrave; chi ph&iacute; cố định được h&igrave;nh th&agrave;nh từ c&aacute;c yếu tố đầu v&agrave;o cố định.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">TVC l&agrave; chi ph&iacute; biến đổi được h&igrave;nh th&agrave;nh từ c&aacute;c yếu tố đầu v&agrave;o biến đổi.</span></span></span></li>\r\n</ul>\r\n\r\n<ol start=\"11\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Chi ph&iacute; cố định b&igrave;nh qu&acirc;n ( AFC) l&agrave; mức chi ph&iacute; cố định t&iacute;nh b&igrave;nh qu&acirc;n cho một đơn vị sản phẩm.</span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">CT: AFC =&nbsp;</span></span></span><span style=\"font-size:13.0pt\"><span class=\"math-tex\">\\(\\frac{{TFC}}{Q}\\)</span></span></p>\r\n\r\n<ol start=\"12\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Chi ph&iacute; biến đổi b&igrave;nh qu&acirc;n (AVC) l&agrave; mức chi ph&iacute; biến đổi t&iacute;nh b&igrave;nh qu&acirc;n cho một đơn vị sản phẩm.</span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">CT: AVC =&nbsp;</span></span></span><span style=\"font-size:13.0pt\"><span class=\"math-tex\">\\(\\frac{{TVC}}{Q}\\)</span></span></p>\r\n\r\n<ol start=\"13\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Chi ph&iacute; cận bi&ecirc;n trong ngắn hạn MC hoặc SMC l&agrave; sự thay đổi trong tổng chi ph&iacute; khi doanh nghiệp sản xuất th&ecirc;m một đơn vị sản phẩm.</span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">CT:&nbsp; </span></span></span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:Cambria,serif\"><span style=\"font-size:13.0pt\"><span class=\"math-tex\">\\(MC = TC\' = \\frac{{\\Delta{TC}}}{\\Delta{Q}}\\)</span> </span></span></span></span></p>\r\n\r\n<ol start=\"14\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Phương tr&igrave;nh đường đồng ph&iacute;:</span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C = wL + Rk</span></span></span></p>\r\n\r\n<ol start=\"15\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Điều kiện cần v&agrave; đủ để tối thiểu ho&aacute; chi ph&iacute;:</span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:Cambria,serif\"><span style=\"font-size:13.0pt\">\\[\\left\\{ {\\begin{array}{*{20}{c}}{\\frac{{M{P_L}}}{w} = \\frac{{M{P_K}}}{r}}\\\\{{Q_0} = f(K,L)}\\end{array}} \\right.\\]</span></span></span></span></p>\r\n\r\n<ol start=\"16\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Điều kiện cần v&agrave; đủ để tối thiểu h&oacute;a sản lượng:</span></span><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:Cambria,serif\"><span style=\"font-size:13.0pt\">\\[\\left\\{ {\\begin{array}{*{20}{c}}{\\frac{{M{P_L}}}{w} = \\frac{{M{P_K}}}{r}}\\\\{C = w.L + r.K}\\end{array}} \\right.\\]</span></span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<ol start=\"17\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Lợi nhuận được k&yacute; hiệu l&agrave; </span></span></span><span style=\"font-size:13.0pt\">\\[\\pi \\]</span><span style=\"font-size:13.0pt\">.</span><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">N&oacute; l&agrave; phần ch&ecirc;nh lệch giữa doanh thu v&agrave; chi ph&iacute;. Ta c&oacute; c&ocirc;ng thức t&iacute;nh lợi nhuận như sau:</span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-size:13.0pt\">\\[\\pi&nbsp; = TR - TC\\]</span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Hay :&nbsp;</span></span></span><span style=\"font-size:13.0pt\">\\[\\pi&nbsp; = TR - TC = P.Q - ATC.Q = (P - ATC).Q\\]</span></p>\r\n\r\n<ol start=\"18\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Lợi nhuận kế to&aacute;n = Doanh thu &ndash; Chi ph&iacute; kế to&aacute;n</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Lợi nhuận kinh tế = Doanh thu &ndash; Chi ph&iacute; kinh tế</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Doanh thu cận bi&ecirc;n l&agrave; sự thay đổi trong tổng doanh thu khi doanh nghiệp b&aacute;n th&ecirc;m một đơn vị h&agrave;ng h&oacute;a. Doanh thu cận bi&ecirc;n cũng ch&iacute;nh l&agrave; đạo h&agrave;m bậc nhất của h&agrave;m tổng doanh thu theo biến sản lượng (Q).</span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-size:13.0pt\">\\[MR = \\frac{{\\Delta TR}}{{\\Delta Q}} = T{R&#39;_{(Q)}}\\]</span></p>\r\n\r\n<ol start=\"21\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Doanh thu b&igrave;nh qu&acirc;n (AR) ch&iacute;nh l&agrave; mức doanh thu t&iacute;nh b&igrave;nh qu&acirc;n cho một đơn vị sản phẩm b&aacute;n ra.</span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-size:13.0pt\">\\[AR = \\frac{{TR}}{Q} = \\frac{{P.Q}}{Q} = P\\]</span></p>\r\n\r\n<ol start=\"22\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">x&eacute;t trường hợp tổng qu&aacute;t, nếu một doanh nghiệp c&oacute; đường cầu với phương tr&igrave;nh:</span></span></span><span style=\"font-size:13.0pt\">\\[P = a - bQ\\]</span><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">, khi đ&oacute; tổng doanh thu của doanh nghiệp:</span> </span></span><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\">&nbsp;<span style=\"font-size:13.0pt\">\\[TR = P.Q = a.Q - b{Q^2} \\to MR = a - 2bQ\\]</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Để </span></span></span><span style=\"font-size:13.0pt\">\\[{\\pi _{\\max }}\\]</span><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">th&igrave;</span></span></span><span style=\"font-size:13.0pt\">\\[{\\pi &#39;_{(Q)}} = 0\\],</span><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">hay</span></span></span><span style=\"font-size:13.0pt\">\\[{(TR - TC)&#39;_{(Q)}} = 0\\]</span><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">,suy ra:</span></span></span><span style=\"font-size:13.0pt\">\\[T{R&#39;_{(Q)}} - T{C&#39;_{(Q)}} = 0\\]</span><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">hayMR=MC.</span></span></span></li>\r\n</ol>', '1552983695', null, '0', '70', '1', '0', null, '0', null, null, null, null, null, null, null, null);
