INSERT INTO `lesson` VALUES ('834', 'CHƯƠNG V: CẤU TRÚC THỊ TRƯỜNG', '<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Doanh nghiệp cạnh tranh ho&agrave;n hảo l&agrave; doanh nghiệp chấp nhận gi&aacute; thị trường. h&agrave;nh vi chấp nhận gi&aacute;, đặc th&ugrave; của những thị trường cạnh tranh ho&agrave;n hảo, ph&aacute;t sinh bởi v&igrave; mỗi một doanh nghiệp c&aacute;c biệt tr&ecirc;n thị trường l&agrave; nhỏ so với to&agrave;n bộ thị trường đến mức doanh nghiệp kh&ocirc;ng thể ảnh hưởng đến gi&aacute; thị trường của h&agrave;ng h&oacute;a hay dịch vụ do doanh nghiệp tạo ra với việc thay đổi sản lượng của doanh nghiệp.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Trong ngắn hạn, đối với doanh nghiệp CTHH:</span></span></span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Khi P&gt; ATCmin, doanh nghiệp sẽ lựa chọn mức sản lượng tối ưu thỏa m&atilde;n điều kiện P = MC.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Khi P = ATCmin, doanh nghiệp sẽ lựa chọn mức sản lượng tối ưu Q* thỏa m&atilde;n điều kiện P = MC, doanh nghiệp sẽ h&ograve;a vốn, doanh nghiệp thu được lợi nhu&acirc;n dương.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Điểm h&ograve;a vốn xảy ra tại điểm cực tiểu của ATC, n&ecirc;n muốn x&aacute;c định điểm h&ograve;a vốn, giải phương tr&igrave;nh MC = ATC hoặc ATC&rsquo; = 0.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Khi AVCmin &lt; P &lt; ATCmin, doanh nghiệp sẽ lựa chọn mức sản lượng Q* thỏa m&atilde;n điều kiện MC = P, doanh nghiệp sẽ bị lỗ vốn.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Khi P = AVCmin, doanh nghiệp bị thua lỗ nhưng vẫn tiếp tục sản xuất.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Khi P &lt; AVCmin, doanh nghiệp phải đ&oacute;ng cửa.</span></span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Ngo&agrave;i ra, ch&uacute;ng ta c&oacute; lợi nhuận của doanh nghiệp CTHH:</span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-size:13.0pt\">\\[\\pi&nbsp; = TR - TC = P.Q - TC\\]</span></span></span></p>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Điều kiện cần để tối đa h&oacute;a lợi nhuận</span></span></span><span style=\"font-size:13.0pt\">\\[{\\pi _{\\max }}\\]</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-size:13.0pt\">\\[\\frac{{d\\pi }}{{dQ}} = P - \\frac{{dTC}}{{dQ}} = P - MC = 0 \\Rightarrow P = MC\\]</span></span></span></p>\r\n\r\n<ol start=\"5\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Điều kiện đủ để tối đa h&oacute;a lợi nhuận l&agrave;:</span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-size:13.0pt\">\\[\\frac{{{d^2}\\pi }}{{d{Q^2}}} = \\frac{{ - dMC}}{{dQ}} &lt; 0 \\Rightarrow \\frac{{dMC}}{{dQ}} &gt; 0\\]</span></span></span></p>\r\n\r\n<ol start=\"6\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Trong d&agrave;i hạn, c&aacute;c doanh nghiệp cạnh tranh thu được lợi nhuận bằng 0, bởi sự gia nhập hoặc từ bỏ thị trường cạnh tranh l&agrave; kh&ocirc;ng hạn chế.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Thị trường độc quyền b&aacute;n thuần t&uacute;y l&agrave; thị trường chỉ c&oacute; một người b&aacute;n duy nhất. do một số nguy&ecirc;n nh&acirc;n như qu&aacute; tr&igrave;nh sản xuất của doanh nghiệp đạt được t&iacute;nh kinh tế theo quy m&ocirc;, hoặc do những quy định của ch&iacute;nh phủ, hoặc do doanh nghiệp nắm giữ được yếu tố đ&acirc;u v&agrave;o cơ quan hay do những quy định về bằng ph&aacute;t minh s&aacute;ng chế.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Thị trường độc quyền mua thuần t&uacute;y: l&agrave; thị trường m&agrave; trong đ&oacute; chỉ c&oacute; một người mua duy nhất. do người mua duy nhất n&ecirc;n người mua n&agrave;y cũng c&oacute; khả năng t&aacute;c động đến gi&aacute; của thị trường.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Thị trường cạnh tranh độc quyền l&agrave; một thị trường c&oacute; đặc điểm giống với thị trường cạnh tranh ho&agrave;n hảo, chỉ kh&aacute;c biệt một điểm duy nhất l&agrave; sản xuất của c&aacute;c doanh nghiệp tr&ecirc;n thị trường c&oacute; sự kh&aacute;c biệt đ&ocirc;i ch&uacute;t, c&oacute; nghĩa đ&acirc;y l&agrave; những h&agrave;ng h&oacute;a c&oacute; thể thay thế cho nhau nhưng kh&ocirc;ng thể thay thế ho&agrave;n to&agrave;n cho nhau giống như thị trường cạnh tranh ho&agrave;n hảo.</span></span></span></li>\r\n</ol>', '1552987429', null, '0', '70', '1', '0', null, '0', null, null, null, null, null, null, null, null);
INSERT INTO `lesson` VALUES ('835', 'CHƯƠNG VI: THỊ TRƯỜNG CÁC YẾU TỐ SẢN XUẤT', '<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Cầu về lao động l&agrave; số lượng lao động m&agrave; doanh nghiệp muốn thu&ecirc; v&agrave; c&oacute; khả năng thu&ecirc; tại c&aacute;c mức tiền c&ocirc;ng kh&aacute;c nhau trong một khoảng thời gian nhất định.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Cung lao động l&agrave; lượng lao động m&agrave; người lao động sẵn s&agrave;ng v&agrave; c&oacute; khả năng cung ứng tai c&aacute;c mức tiền c&ocirc;ng kh&aacute;c nhau trong một giai đoạn nhất định (giả định c&aacute;c yếu tố đầu v&agrave;o kh&aacute;c kh&ocirc;ng đổi).</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">\\[M{P_L}\\] Sản phẩm cận bi&ecirc;n của lao động l&agrave; sự thay đổi trong tổng số sản phẩm đ&acirc;u ra do sử dụng th&ecirc;m một yếu tố đầu v&agrave;o l&agrave; lao động. N&oacute; được x&aacute;c định bằng c&ocirc;ng thức:</span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-size:13.0pt\">\\[M{P_L} = \\frac{{\\Delta Q}}{{\\Delta L}} = {Q_L}\\]</span></span></span></p>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;Sản phẩm doanh thu cận bi&ecirc;n của lao động\\[MR{P_L}\\]l&agrave; phần doanh thu tăng th&ecirc;m do sử dụng th&ecirc;m một đơn vị lao động đầu v&agrave;o.</span></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-size:13.0pt\">\\[MR{P_L} = \\frac{{\\Delta TR}}{{\\Delta L}} = \\frac{{\\Delta TR}}{{\\Delta Q}}.\\frac{{\\Delta Q}}{{\\Delta L}} = MR.M{P_L}\\]</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">\\[MR{P_L} = T{R&#39;_{(L)}}\\]</span></span></span></p>\r\n\r\n<ol start=\"5\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">&nbsp;Sản phẩm gi&aacute; trị bi&ecirc;n của lao động\\[MV{P_L}\\]l&agrave; gi&aacute; trị bằng tiền được t&iacute;nh ra từ c&aacute;c đơn vị sản phẩm tăng th&ecirc;m do sử dụng th&ecirc;m một đơn vị đầu v&agrave;o l&agrave; lao động.</span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">\\[MV{P_L} = P.M{P_L}\\]</span></span></span></p>\r\n\r\n<ol start=\"6\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Tiền c&ocirc;ng/ tiền lương tối thiểu l&agrave; mức lương thấp nhất m&agrave; ch&iacute;nh phủ quy định người sử dụng lao động phải trả cho người lao động.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Vốn l&agrave; lượng trang thiết bị v&agrave; cơ sở hạ tầng sử dụng trong qu&aacute; tr&igrave;nh sản xuất, n&oacute; được hiểu l&agrave; vốn hiện vật của doanh nghiệp.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">Tiền l&atilde;i l&agrave; số tiền phải trả để sử dụng một khoảng tiền trong một khoảng thời gian nhất định. Đ&acirc;y l&agrave; số tiền phải trả khi vay vốn t&agrave;i ch&iacute;nh.</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Arial,sans-serif\"><span style=\"font-size:13.0pt\">L&atilde;i suất l&agrave; tỷ lệ số tiền l&atilde;i v&agrave; lượng tiền vay t&iacute;nh theo phần trăm. N&oacute; ch&iacute;nh l&agrave; gi&aacute; của vốn.</span></span></span></li>\r\n</ol>', '1552987998', null, '0', '70', '1', '0', null, '0', null, null, null, null, null, null, null, null);
