INSERT INTO `lesson` VALUES ('838', '1', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">\\[\\left\\{ {\\begin{array}{*{20}{c}}{P = 10\\% }\\\\{Q = 20\\% }\\end{array}} \\right.\\]</span></span></p>\r\n\r\n<p><span style=\"font-size:11.0pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\">\\[\\begin{array}{l}\\;\\left\\{ {\\begin{array}{*{20}{c}}{P =&nbsp; - 100 + 0,4Q}\\\\{P = 200 - 0,1Q}\\end{array}} \\right.\\\\\\Rightarrow \\left\\{ {\\begin{array}{*{20}{c}}{P - 0,4Q =&nbsp; - 100}\\\\{P + 0,1Q=200}\\end{array}} \\right.\\\\\\Rightarrow \\left\\{ {\\begin{array}{*{20}{c}}{P = 140}\\\\{Q = 600}\\end{array}} \\right.\\end{array}\\]</span></span></p>', '1552990175', null, '0', '70', '1', '0', null, '0', null, null, null, null, null, null, null, null);
