INSERT INTO `question` VALUES ('10', 'VFF khẳng định đây là trang giả mạo, không có liên hệ với ban tổ chức, đồng thời nhấn mạnh chỉ hợp tác với Công ty Cổ phần GMO-Z.com RUNSYSTEM để bán vé online các trận đấu tại AFF Cup 2018 và sẽ không chịu trách nhiệm với các giao dịch được thực hiện trên các trang web giả mạo.\r\n\r\nLường trước nguy cơ có thể xuất hiện các trang web bán vé giả mạo khác, VFF đề nghị người hâm mộ nếu phát hiện những trường hợp này, có thể thông báo cho ban tổ chức qua địa chỉ email info@vff.org.vn. VFF cũng khuyến cáo người hâm mộ nên truy cập trang chủ của VFF ở địa chỉ http://www.vff.org.vn để liên kết tới trang bán vé nhằm tránh nhầm lẫn.\r\n\r\nVé của trận chung kết lượt về giữa Việt Nam và Malaysia sẽ được bán qua đường online theo bốn đợt: 10h, 16h, 22h ngày 10/12 và 10h ngày 11/12. Sau khi mua thành công, khách hàng có thể nhận theo hai cách dưới đây:\r\n\r\n- Thứ nhất, chuyển trực tiếp qua dịch vụ bưu chính cho khách hàng có địa chỉ thuộc 12 quận nội thành Hà Nội, với điều kiện phải có thẻ Chứng minh Nhân dân hoặc thẻ Căn cước gốc để đối chiếu.\r\n\r\n- Thứ hai, trả tại trụ sở VFF từ 9h đến 16h30 ngày 13/12 và 14/12 cho những CĐV ngoại tỉnh hoặc không nhận qua chuyển phát.', null, null, '4', '0', '17', '5', '1544416298', null, null, null, null, null, '25', null, null, null, null, null);
