INSERT INTO `question` VALUES ('15', 'Người ta thường nói \"biết mình biết người, trăm trận trăm thắng\". Mấu chốt vấn đề là ông Park rất hiểu cầu thủ. Ông ấy thuyết phục được từng VĐV rằng ai nên đá trước, ai nên đá sau, và chứng minh bằng kết quả thực tiễn trên sân. Khi có trong tay những kết quả thuận lợi, HLV vừa có thêm tự tin để toan tính những trận kế tiếp, vừa có sự nể phục từ cầu thủ. Ví dụ trận gặp Malaysia vừa rồi, ai cũng nghĩ là Đức Chinh dứt điểm không tốt, nhưng đặt một người khác thay cậu ấy, chắc gì người đó đã nhìn thấy cơ hội hoặc đủ tốc độ băng lên từ những đường chuyền tuyến dưới. Nhiệm vụ của tiền đạo là ghi bàn, nhưng ở một trận đấu cụ thể, có thể họ sẽ phải sắm những vai khác', null, null, '2', '0', '21', '1', '1544621885', null, null, null, null, null, '27', null, null, null, null, null);
INSERT INTO `question` VALUES ('16', 'ĐNgày 12/12, ông Nguyễn Văn Huy - Trưởng phòng Giáo dục và Đào tạo huyện Hà Trung (Thanh Hóa) cho hay, cơ quan chức năng đang làm rõ việc một bé trai 10 tuổi bị sư trụ trì nuôi dưỡng bạo hành.\r\n\r\nTrước đó, cô giáo Phạm Thị Nhuần, giáo viên trường Tiểu học Hà Hải, huyện Hà Trung tố cáo, nhiều lần phát hiện học trò bị đánh đập thậm tệ.', null, null, '3', '0', '21', '1', '1544621987', null, null, null, null, null, '27', null, null, null, null, null);
