INSERT INTO `question` VALUES ('27', 'Bộ Giao thông Vận tải đã kiểm định, đánh giá độc lập. Bộ đồng thời phối hợp với Hội đồng Nghiệm thu nhà nước các công trình xây dựng (Bộ Xây dựng) và các chuyên gia đầu ngành đánh giá nguyên nhân, lựa chọn giải pháp khắc phục.\r\n\r\nTheo đó, các đơn vị chuyên môn đánh giá có 3 nhóm nguyên nhân gây nứt dầm thép CB6 trên đỉnh trụ P29 cầu Vàm Cống là tập trung ứng suất, ứng suất dư và chất lượng đường hàn ráp nối các cấu kiện.', null, null, '3', '0', '23', '1', '1544622237', null, null, null, null, null, '27', null, null, null, null, null);
