INSERT INTO `question` VALUES ('30', 'Bộ Giao thông Vận tải đã kiểm định, đánh giá độc lập. Bộ đồng thời phối hợp với Hội đồng Nghiệm thu nhà nước các công trình xây dựng (Bộ Xây dựng) và các chuyên gia đầu ngành đánh giá nguyên nhân, lựa chọn giải pháp khắc phục.\r\n\r\nTheo đó, các đơn vị chuyên môn đánh giá có 3 nhóm nguyên nhân gây nứt dầm thép CB6 trên đỉnh trụ P29 cầu Vàm Cống là tập trung ứng suất, ứng suất dư và chất lượng đường hàn ráp nối các cấu kiện.', '/images/exercise/Rectangleflash_demo1544622272.png', '/images/exercise/upload_0c844e6fb48a476a931189cd8f690e2e_large1544622276.jpg', '1', '0', '24', '1', '1544622289', null, 'gợi sy 1', 'gợi ý 2', 'đây là ảnh đẹp', null, '27', null, null, null, null, null);
INSERT INTO `question` VALUES ('31', 'Trong văn bản tham mưu gửi UBND TP HCM, Sở Quy hoạch - Kiến trúc đề nghị loại bỏ sân golf Tân Sơn Nhất khỏi quy hoạch của thành phố.\r\n\r\nViệc này dựa trên quyết định hồi tháng 8 của Bộ GTVT về điều chỉnh quy hoạch chi tiết Cảng hàng không quốc tế Tân Sơn Nhất. Phần diện tích sân golf sẽ được làm nhà ga, khu hangar và một phần cây xanh hồ điều tiết.\r\n\r\nSân golf trong sân bay Tân Sơn Nhất có quy mô gần 160 ha, 36 lỗ, kết hợp các dịch vụ vui chơi giải trí cao cấp, trung tâm hội nghị, khách sạn 5 sao, khu nhà ở cho thuê và công trình công cộng.', null, null, '4', '0', '24', '1', '1544622347', null, null, null, null, null, '27', null, null, null, null, null);
