INSERT INTO `question` VALUES ('41', 'Theo ghi nhận, các diện tích lúa non bán cho thương lái tại xã Đại Hòa Lộc (Bình Đại). Sau khi cắt bán hết bông lúa non trên ruộng, hiện cây lúa tiếp tục cho bông mới (dân địa phương gọi là bông chét), sắp thu hoạch đợt hai.\r\n\r\nÔng Nguyễn Văn Tuấn, người vừa bán 2.300 m2 lúa non cho biết, đây là diện tích lúa mùa mỗi năm chỉ thu hoạch một vụ. Những năm trước ông đều đợi lúa chín rồi mới thu hoạch như các đám ruộng khác. Vụ năm nay, khoảng một tháng trước, khi lúa vừa trổ bông, một người đàn ông đang tạm trú trên địa bàn đến ruộng ông đặt vấn đề mua bông lúa non.', null, null, '3', '0', '29', '1', '1544622811', null, null, null, null, null, '27', null, null, null, null, null);
INSERT INTO `question` VALUES ('42', null, null, null, '3', '41', '29', '1', '1544622811', null, 'gợi ý 1', null, 'Sau khi thống nhất giá 10.000 đồng mỗi kg, người ___ đàn ông nói trên cho hai nhân công đến cắt bông lúa non rồi chở về TP HCM ?', null, '27', null, null, null, null, null);
INSERT INTO `question` VALUES ('43', null, null, null, '3', '41', '29', '1', '1544622811', null, 'gợi ý 2', null, 'Ông Tuấn cho biết đã bán tổng cộng 500 kg bông lúa non __ ?', null, '27', null, null, null, null, null);
