INSERT INTO `question` VALUES ('212', 'Câu 37.1	Nguyên tắc xây dựng đạo đức mới theo TTHCM?', null, null, '2', '0', '59', '6', '1545453477', null, null, null, null, null, '34', null, null, null, null, null);
INSERT INTO `question` VALUES ('213', 'Câu 37.2	Liên hệ với bản thân?', null, null, '2', '0', '59', '6', '1545453515', null, null, null, null, null, '34', null, null, null, null, null);
INSERT INTO `question` VALUES ('214', 'Câu 38	TTHCM về những chuẩn mực đạo đức CM?', null, null, '2', '0', '59', '6', '1545453717', null, null, null, null, null, '34', null, null, null, null, null);
INSERT INTO `question` VALUES ('215', 'Câu 39	Sinh viên học tập và làm theo tấm gương đạo đức HCM?', null, null, '2', '0', '59', '6', '1545453941', null, null, null, null, null, '34', null, null, null, null, null);
INSERT INTO `question` VALUES ('216', 'Câu 40.1	TTHCM về vai trò của con người ?', null, null, '2', '0', '59', '6', '1545454083', null, null, null, null, null, '34', null, null, null, null, null);
INSERT INTO `question` VALUES ('217', 'Câu 40.2	TTHCM về chiến lược “ trồng người”?', null, null, '2', '0', '59', '6', '1545454206', null, null, null, null, null, '34', null, null, null, null, null);
INSERT INTO `question` VALUES ('230', null, null, null, '5', '229', '44', '6', '1545475186', null, null, null, '<p>Các nhà khoa học đã đưa ra định nghĩa: \" Tư tưởng HCM là một hệ thống những quan điểm <a class=\"cloze\" href=\"#mce_temp_url#\">toàn diện</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">sâu sắc</a> về những <a class=\"cloze\" href=\"#mce_temp_url#\">vấn đề</a> cơ bản của <a class=\"cloze\" href=\"#mce_temp_url#\">cách mạng</a> VN, từ cm dân tộc dân chủ nhân dân đến cm xhcn; là kết quả sự vận dụng sáng tạo và phát triển cn Mác Lê-nin vào điều kiện cụ thể nước ta, đồng thời là sự kết hợp tinh hoa văn hóa dân tộc và trí tuệ thời đại nhằm giải phóng dân tộc, giải phóng giai cấp và giải phóng con người\".</p>', null, '31', null, null, null, null, null);
INSERT INTO `question` VALUES ('249', null, null, null, '5', '0', '63', '6', '1545475906', '1552921661', null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('250', null, null, null, '5', '249', '63', '6', '1545475906', '1552921661', 'Gợi ý cho cả câu hỏi, cái này hiện ra ở đâu?', null, '<p>Con đường khởi nghĩa của Hoàng Hoa Thám thì vẫn mang nặng \"<a class=\"cloze\" href=\"#mce_temp_url#\">cốt cách phong kiến</a>\", chưa phải là lối thoát rõ ràng, hướng đi đúng đắn.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('251', null, null, null, '5', '0', '63', '6', '1545475939', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('252', null, null, null, '5', '251', '63', '6', '1545475939', null, null, null, '<p>Cuộc cách mạng ở Nga thành công \"mở ra trước mắt họ thời đại của cách mạng <a class=\"cloze\" href=\"#mce_temp_url#\">chống đế quốc</a>, thời đại giải phóng dân tộc\", \"<a class=\"cloze\" href=\"#mce_temp_url#\">thức tỉnh</a> các dân tộc châu Á\"</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('253', null, null, null, '5', '0', '63', '6', '1545475966', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('254', null, null, null, '5', '253', '63', '6', '1545475966', null, null, null, '<p>“Ai cũng có ít hay nhiều <a class=\"cloze\" href=\"#mce_temp_url#\">lòng ái quốc</a>.”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('255', null, null, null, '5', '0', '63', '6', '1545475989', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('256', null, null, null, '5', '255', '63', '6', '1545475989', null, null, null, '<p>HCM đã đúc kết chân lý: \"Dân ta có một lòng <a class=\"cloze\" href=\"#mce_temp_url#\">nồng nàn yêu nước</a>. Đó là một truyền thống quý báu của ta.\"</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('257', null, null, null, '5', '0', '63', '6', '1545476006', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('258', null, null, null, '5', '257', '63', '6', '1545476006', null, null, null, '<p>Đối với văn hóa phương Đông, người tiếp thu những mặt tích cực của <a class=\"cloze\" href=\"#mce_temp_url#\">Nho giáo</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('259', null, null, null, '5', '0', '63', '6', '1545476028', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('260', null, null, null, '5', '259', '63', '6', '1545476028', null, null, null, '<p><a class=\"cloze\" href=\"#mce_temp_url#\">Nho giáo</a> là các triết lý hành động, tư tưởng nhập thế, hành đạo giúp đời, đó là ước vọng về một xã hôi bình trị, hòa mục, hòa đồng, là triết lý nhân sinh, tu thân dưỡng tính, đề cao văn hóa lễ giáo, tạo ra truyền thống hiếu học.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('261', null, null, null, '5', '0', '63', '6', '1545476054', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('262', null, null, null, '5', '261', '63', '6', '1545476054', null, null, null, '<p>Người dẫn lời của <a class=\"cloze\" href=\"#mce_temp_url#\">Lênin</a>: \" Chỉ có những người cách mạng <a class=\"cloze\" href=\"#mce_temp_url#\">chân chính</a> mới thu hái được những hiểu biết quý báu của đời trước để lại\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('263', null, null, null, '5', '0', '63', '6', '1545476122', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('264', null, null, null, '5', '263', '63', '6', '1545476122', null, null, null, '<p>Về Phật giáo, HCM tìm hiểu chủ nghĩa <a class=\"clozetip\" title=\"viết hoa tên riêng\" href=\"#\">Tam Dân</a> của <a class=\"clozetip\" title=\"Viết hoa Họ và Tên\" href=\"#\">Tôn Trung Sơn</a> vì thấy trong đó có những điều kiện thích hợp với điều kiện nước ta.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('265', null, null, null, '5', '0', '63', '6', '1545476149', '1552049110', null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('266', null, null, null, '5', '265', '63', '6', '1545476149', '1552049110', null, null, '<p>HCM còn tiếp thu <a class=\"cloze\" href=\"#mce_temp_url#\">văn hóa dân chủ</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">cách mạng</a> phương Tây. </p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('267', null, null, null, '5', '0', '63', '6', '1545476486', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('268', null, null, null, '5', '267', '63', '6', '1545476486', null, null, null, '<p>Trong bài \"<a class=\"cloze\" href=\"#mce_temp_url#\">Con đường dẫn tôi</a> đến chủ nghĩa Lê nin\":\" Lúc bấy giờ, tôi ủng hộ cm tháng Mười Nga chỉ là theo <a class=\"cloze\" href=\"#mce_temp_url#\">cảm tính</a> tự nhiên... Tôi kính yêu Lenin vì Lenin là một người yêu nước vĩ đại đã giải phóng đồng bào mình... Tôi tham gia Đảng Xã hôi Pháp chẳng qua là vì các \"ông bà\" ấy đã tỏ <a class=\"cloze\" href=\"#mce_temp_url#\">đồng tình</a> với tôi, với cuộc đấu tranh của các dân tộc bị áp bức. Còn như Đảng là gì, công đoàn là gì, cnxh và cn cộng sản là gì thì tôi <a class=\"cloze\" href=\"#mce_temp_url#\">chưa hiểu</a>.\"</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('269', null, null, null, '5', '0', '63', '6', '1545476982', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('270', null, null, null, '5', '269', '63', '6', '1545476982', null, null, null, '<p>Trong bài \"c<a class=\"cloze\" href=\"#mce_temp_url#\">on đường dẫn tôi</a> đến cn Lê nin\": \" Lúc đầu, chính là <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa yêu nước</a>, chứ chưa phải chủ nghĩa cộng sản đã đưa tôi tin theo Lenin, tin theo Quốc tế thứ ba\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('271', null, null, null, '5', '0', '63', '6', '1545477041', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('272', null, null, null, '5', '271', '63', '6', '1545477041', null, null, null, '<p>Trong tác phẩm <a class=\"cloze\" href=\"#mce_temp_url#\">Đường cách mệnh</a>:\" trong cuộc đấu tranh, vừa nghiên cứu lý luận Mác Lenin, vừa làm công tác thực tế, dần dần tôi hiểu ra rằng chỉ có cnxh, chủ nghĩa cộng sản mới <a class=\"cloze\" href=\"#mce_temp_url#\">giải phóng</a> được các dân tộc bị áp bức và những người lao động trên thế giới\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('273', null, null, null, '5', '0', '63', '6', '1545477088', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('274', null, null, null, '5', '273', '63', '6', '1545477088', null, null, null, '<p>Trong tác phẩm <a class=\"clozetip\" title=\"Viết hoa chữ cái đầu từ đầu tiên\" href=\"#\">Đường cách mệnh</a>:\" Bây giờ học thuyết nhiều, chủ nghĩa nhiều, nhưng chủ nghĩa chân chính nhất, chắc chắn nhất, cách mệnh nhất là chủ nghĩa Mác Lenin\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('275', null, null, null, '5', '0', '63', '6', '1545477196', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('276', null, null, null, '5', '275', '63', '6', '1545477196', null, null, null, '<p>Nhân tố chủ quan hình thành tư tưởng HCM là: khả năng <a class=\"cloze\" href=\"#mce_temp_url#\">tư duy</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">trí tuệ</a> HCM, phẩm chất đạo đức và năng lực hoạt động thực tiễn.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('277', null, null, null, '5', '0', '63', '6', '1545477234', '1552049212', null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('278', null, null, null, '5', '277', '63', '6', '1545477234', '1552049212', null, null, '<p>HCM hoạt động ở Thái Lan vào thời gian nào? (câu này có vấn đề)</p>\r\n<p>Cuối thu <a class=\"cloze\" href=\"#mce_temp_url#\">1928-1929</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('279', null, null, null, '5', '0', '63', '6', '1545477292', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('280', null, null, null, '5', '279', '63', '6', '1545477292', null, null, null, '<p>Khi bị bắt ở Hồng Kong, tên trên giấy của Bác là gì -&gt;<a class=\"cloze\" href=\"#mce_temp_url#\">Tống Văn Sơ</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('281', null, null, null, '5', '0', '63', '6', '1545477368', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('282', null, null, null, '5', '281', '63', '6', '1545477368', null, null, null, '<p>Bác bị bắt ở Hong Kong vào thời gian tháng <a class=\"cloze\" href=\"#mce_temp_url#\">6/1931</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('283', null, null, null, '5', '0', '63', '6', '1545477425', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('284', null, null, null, '5', '283', '63', '6', '1545477425', null, null, null, '<p>Mặt trận Liên Việt thành lập năm nào?</p>\r\n<p>Trả lời: Năm <a class=\"cloze\" href=\"#mce_temp_url#\">1951</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('285', null, null, null, '5', '0', '63', '6', '1545477478', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('286', null, null, null, '5', '285', '63', '6', '1545477478', null, null, null, '<p>Đại biểu của tổ chức nào không đến trong hội nghị hợp nhất Đảng 2/1930.</p>\r\n<p>Trả lời:<a class=\"clozetip\" title=\"6 từ viết hoa chữ cái đầu\" href=\"#\">Đông Dương Cộng sản Liên Đoàn</a> </p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('287', null, null, null, '5', '0', '63', '6', '1545479188', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('288', null, null, null, '5', '287', '63', '6', '1545479188', null, null, null, '<p>Tên bác khi hoạt động ở Thái Lan (Xiêm)</p>\r\n<p>Trả lời: <a class=\"clozetip\" title=\"viết hoa\" href=\"#\">Thầu Chín</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('289', null, null, null, '5', '0', '63', '6', '1545479550', '1552048486', null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('290', null, null, null, '5', '289', '63', '6', '1545479550', '1552048486', null, null, '<p>Bác ra đi tìm đường cứu nước trên tàu buôn của nước <a class=\"cloze\" href=\"#mce_temp_url#\">Pháp</a>. (chắc là đúng rồi)</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('291', null, null, null, '5', '0', '63', '6', '1545479590', '1545479834', null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('292', null, null, null, '5', '291', '63', '6', '1545479590', '1545479834', null, null, '<p>Bác ở Liên Xô vào thời gian nào? -</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">1923-1924</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('295', null, null, null, '5', '0', '63', '6', '1545480058', '1545480101', null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('296', null, null, null, '5', '295', '63', '6', '1545480058', '1545480101', null, null, '<p>Bác gia nhập Đảng xã hội Pháp thời gian <a class=\"cloze\" href=\"#mce_temp_url#\">2/1919</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('297', null, null, null, '5', '0', '63', '6', '1545480164', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('298', null, null, null, '5', '297', '63', '6', '1545480164', null, null, null, '<p>Bác gia nhập Đảng Cộng sản Pháp năm nào?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">1920</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('299', null, null, null, '5', '0', '63', '6', '1545481224', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('300', null, null, null, '5', '299', '63', '6', '1545481224', null, null, null, '<p>Bác sống ở thành phố nào khi ở Mỹ</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">Boston</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('301', null, null, null, '5', '0', '63', '6', '1545481263', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('302', null, null, null, '5', '301', '63', '6', '1545481263', null, null, null, '<p>Bác đến Pháp lần 2 vào năm nào</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">cuối 1917</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('303', null, null, null, '5', '0', '63', '6', '1545481336', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('304', null, null, null, '5', '303', '63', '6', '1545481336', null, null, null, '<p>Tháng 5/1954 Bác ở đâu</p>\r\n<p>Trả lời: <a class=\"clozetip\" title=\"2 tỉnh \" href=\"#\">Định Hóa, Thái Nguyên</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('305', null, null, null, '5', '0', '63', '6', '1545481386', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('306', null, null, null, '5', '305', '63', '6', '1545481386', null, null, null, '<p>Măt trận giải phóng miền Nam thành lập năm nào</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">1960</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('307', null, null, null, '5', '0', '63', '6', '1545481418', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('308', null, null, null, '5', '307', '63', '6', '1545481418', null, null, null, '<p>Nước VN hợp hiến ra đời năm nào</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">1946</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('309', null, null, null, '5', '0', '63', '6', '1545481475', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('310', null, null, null, '5', '309', '63', '6', '1545481475', null, null, null, '<p>Bác hồ lần đầu tiên đến Pháp sống ở thành phố nào</p>\r\n<p>Trả lời: <a class=\"clozetip\" title=\"viết hoa \" href=\"#\">Mác xây</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('311', null, null, null, '5', '0', '63', '6', '1545481563', '1545481658', null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('312', null, null, null, '5', '311', '63', '6', '1545481563', '1545481658', null, null, '<p>Đảng Lao động VN ra đời năm nao?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">1951 </a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('313', null, null, null, '5', '0', '63', '6', '1545481615', '1545490984', null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('314', null, null, null, '5', '313', '63', '6', '1545481615', '1545490984', null, null, '<p>UNESCO công nhận Hồ Chí Minh với tư cách là “<a class=\"cloze\" href=\"#mce_temp_url#\">anh hùng giải phóng dân tộc</a> và Nhà văn hóa kiệt xuất của Việt Nam”  vào năm <a class=\"cloze\" href=\"#mce_temp_url#\">1987</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('315', null, null, null, '5', '0', '63', '6', '1545481808', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('316', null, null, null, '5', '315', '63', '6', '1545481808', null, null, null, '<p>Khi về nước Bác sống ở đâu?</p>\r\n<p>Trả lời: <a class=\"clozetip\" title=\"viết hoa, 2 tỉnh \" href=\"#\">Hà Quảng, Cao Bằng</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('317', null, null, null, '5', '0', '63', '6', '1545481855', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('318', null, null, null, '5', '317', '63', '6', '1545481855', null, null, null, '<p>Ở TQ, Bác lấy tên là gì?</p>\r\n<p>Trả lời: <a class=\"clozetip\" title=\"viết hoa\" href=\"#\">Lý Thụy</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('319', null, null, null, '5', '0', '63', '6', '1545481889', '1545481981', null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('320', null, null, null, '5', '319', '63', '6', '1545481889', '1545481981', null, null, '<p>Bác ra đi tìm đường cứu nước <a class=\"cloze\" href=\"#mce_temp_url#\">5/6/1911</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('321', null, null, null, '5', '0', '63', '6', '1545481915', '1545482037', null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('322', null, null, null, '5', '321', '63', '6', '1545481915', '1545482037', null, null, '<p>Các bài báo của Bác khi hoạt động ở Pháp được đăng trên những báo nào?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">Người cùng khổ, Nhân đạo, Đời sống công nhân</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('323', null, null, null, '5', '0', '63', '6', '1545482085', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('324', null, null, null, '5', '323', '63', '6', '1545482085', null, null, null, '<p>Bác sáng lập ra tờ \"Thanh niên\" vào thời gian nào?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">21/6/1925 ra số đầu tiên</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('325', null, null, null, '5', '0', '63', '6', '1545482136', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('326', null, null, null, '5', '325', '63', '6', '1545482136', null, null, null, '<p>Bác đến Thái Lan thời gia nào?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">mùa thu 1928</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('327', null, null, null, '5', '0', '63', '6', '1545482207', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('328', null, null, null, '5', '327', '63', '6', '1545482207', null, null, null, '<p>Tờ báo nào do Bác sáng lập là tờ báo vô sản đầu tiên?</p>\r\n<p>Trả lời: <a class=\"clozetip\" title=\"thêm thời gian\" href=\"#\">Báo Người cùng khổ (1922)</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('329', null, null, null, '5', '0', '63', '6', '1545482254', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('330', null, null, null, '5', '329', '63', '6', '1545482254', null, null, null, '<p>Qúa trình hình thành tư tưởng HCM trải qua <a class=\"cloze\" href=\"#mce_temp_url#\">5</a> giai đoạn.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('331', null, null, null, '5', '0', '63', '6', '1545482401', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('332', null, null, null, '5', '331', '63', '6', '1545482401', null, null, null, '<p>Thời kì trước năm 1911: Hình thành <a class=\"cloze\" href=\"#mce_temp_url#\">tư tưởng</a> yêu nước và <a class=\"cloze\" href=\"#mce_temp_url#\">chí hướng</a> cứu nước.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('333', null, null, null, '5', '0', '63', '6', '1545482483', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('334', null, null, null, '5', '333', '63', '6', '1545482483', null, null, null, '<p>Cụ Phó bảng <a class=\"cloze\" href=\"#mce_temp_url#\">Nguyến Sinh Sắc</a>, thân sinh của Người, là một <a class=\"cloze\" href=\"#mce_temp_url#\">nhà nho</a> cấp tiến, có lòng yêu nước thương dân sâu sắc.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('335', null, null, null, '5', '0', '63', '6', '1545483033', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('336', null, null, null, '5', '335', '63', '6', '1545483033', null, null, null, '<p>HCM nhận thức được : nguồn gốc những đau khổ và áp bức dân tộc là ở ngay tại \"<a class=\"cloze\" href=\"#mce_temp_url#\">chính quốc</a>\", ở nước <a class=\"cloze\" href=\"#mce_temp_url#\">đế quốc</a> đang thống trị dân tộc mình.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('337', null, null, null, '5', '0', '63', '6', '1545483084', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('338', null, null, null, '5', '337', '63', '6', '1545483084', null, null, null, '<p>Thời kì <a class=\"cloze\" href=\"#mce_temp_url#\">1911-1920</a>: Tìm thấy con đường cứu nước, giải phóng dân tộc mình.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('339', null, null, null, '5', '0', '63', '6', '1545483131', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('340', null, null, null, '5', '339', '63', '6', '1545483131', null, null, null, '<p>Phản đế Đồng minh ra đời thời gian nào?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">18/11/1930</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('341', null, null, null, '5', '0', '63', '6', '1545483236', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('342', null, null, null, '5', '341', '63', '6', '1545483236', null, null, null, '<p>Từ năm 1921-1923 bác giữ chức vụ gì ở pháp</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">làm trong ban nghiên cứu thuộc địa ĐCS Pháp và trưởng tiểu ban Đông Dương</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('343', null, null, null, '5', '0', '63', '6', '1545483290', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('344', null, null, null, '5', '343', '63', '6', '1545483290', null, null, null, '<p>Nguyễn Tất Thành lần đầu tiên đặt chân đến Pháp ngày tháng năm nào ? Ở thành phố nào ?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">Mác xây ngày 6/7/1911</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('345', null, null, null, '5', '0', '63', '6', '1545483344', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('346', null, null, null, '5', '345', '63', '6', '1545483344', null, null, null, '<p>Bác lấy tên gì ở đại học Lênin </p>\r\n<p>Trả lời: <a class=\"clozetip\" title=\"viết hoa\" href=\"#\">Lin</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('347', null, null, null, '5', '0', '63', '6', '1545483433', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('348', null, null, null, '5', '347', '63', '6', '1545483433', null, null, null, '<p>Tên Đảng Cộng sản lần thứ 2</p>\r\n<p>Trả lời: <a class=\"clozetip\" title=\"thêm thời gian\" href=\"#\">Tháng 10/1930 đổi tên thành Đảng Cộng sản Đông Dương</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('349', null, null, null, '5', '0', '63', '6', '1545483473', '1545490960', null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('350', null, null, null, '5', '349', '63', '6', '1545483473', '1545490960', null, null, '<p>Tả khuynh là gì?</p>\r\n<p>Trả lời: Tả khuynh là theo Quốc tế Cộng sản,phải giải phóng giai cấp <a class=\"cloze\" href=\"#mce_temp_url#\">trước </a>rồi mới giải phóng <a class=\"cloze\" href=\"#mce_temp_url#\">dân tộc</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('351', null, null, null, '5', '0', '63', '6', '1545483512', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('352', null, null, null, '5', '351', '63', '6', '1545483512', null, null, null, '<p>Hữu khuynh là gì?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">Hữu khuynh là giải phóng dân tộc trước rồi mới giải phóng giai cấp</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('353', null, null, null, '5', '0', '63', '6', '1545483599', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('354', null, null, null, '5', '353', '63', '6', '1545483599', null, null, null, '<p>Bác học ở Quốc học Huế vào thời gian nào? </p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">9/1907 - 5/1908 </a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('355', null, null, null, '5', '0', '63', '6', '1545483667', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('356', null, null, null, '5', '355', '63', '6', '1545483667', null, null, null, '<p>Quan điểm \"Đuổi hổ cửa trước rước beo cửa sau\" là của ai</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">Phan Bội Châu  </a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('357', null, null, null, '5', '0', '63', '6', '1545483756', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('358', null, null, null, '5', '357', '63', '6', '1545483756', null, null, null, '<p>Tại Đại hội lần thứ <a class=\"cloze\" href=\"#mce_temp_url#\">5</a> Quốc tế Cộng sản Bác được cử làm ủy viên <a class=\"cloze\" href=\"#mce_temp_url#\">Ban Phương Đông</a>, phụ trách <a class=\"cloze\" href=\"#mce_temp_url#\">Cục Phương Nam</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('359', null, null, null, '5', '0', '63', '6', '1545483793', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('360', null, null, null, '5', '359', '63', '6', '1545483793', null, null, null, '<p>Những bài giảng của Bác cho lớp đào tạo cán bộ tại Trung quốc được tập hợp thành tác phẩm <a class=\"cloze\" href=\"#mce_temp_url#\">Đường cách mệnh</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('361', null, null, null, '5', '0', '63', '6', '1545483842', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('362', null, null, null, '5', '361', '63', '6', '1545483842', null, null, null, '<p>Pháp xâm lược nước ta vào thời gian nao?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">8/1858</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('363', null, null, null, '5', '0', '63', '6', '1545483885', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('364', null, null, null, '5', '363', '63', '6', '1545483885', null, null, null, '<p>HCM đã tiếp thu tinh thần nào của phật giáo</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">bình đẳng,bác ái</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('365', null, null, null, '5', '0', '63', '6', '1545484335', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('366', null, null, null, '5', '365', '63', '6', '1545484335', null, null, null, '<p>Đại hội quốc dân tổ chức ở <a class=\"cloze\" href=\"#mce_temp_url#\">Tân Trào, Tuyên Quang</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('367', null, null, null, '5', '0', '63', '6', '1545484392', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('368', null, null, null, '5', '367', '63', '6', '1545484392', null, null, null, '<p>Lời kêu gọi toàn quốc kháng chiến: <a class=\"clozetip\" title=\"thời gian\" href=\"#\">19/12/1946</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('369', null, null, null, '5', '0', '63', '6', '1545484514', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('370', null, null, null, '5', '369', '63', '6', '1545484514', null, null, null, '<p>Trong bóng tối dày đặc của chủ nghĩa thực dân, Chủ tịch Hồ Chí Minh đã gặp được ánh sáng của chủ nghĩa Mác - Lênin. Tháng 7 nǎm <a class=\"cloze\" href=\"#mce_temp_url#\">1920</a> qua báo Nhân đạo (L\'Humanité) Pháp, Người được đọc bản “<a class=\"cloze\" href=\"#mce_temp_url#\">Sơ thảo lần thứ nhất những Luận cương về vấn đề dân tộc và thuộc địa</a>” của V.I. Lênin. </p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('371', null, null, null, '5', '0', '63', '6', '1545484586', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('372', null, null, null, '5', '371', '63', '6', '1545484586', null, null, null, '<p>Bác bị quân Tưởng bắt vào thời gian nào?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">29/8/ 1942</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('373', null, null, null, '5', '0', '63', '6', '1545484613', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('374', null, null, null, '5', '373', '63', '6', '1545484613', null, null, null, '<p>Bác đến Pháp lần 2 thì sống ở <a class=\"cloze\" href=\"#mce_temp_url#\">Paris</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('375', null, null, null, '5', '0', '63', '6', '1545484615', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('376', null, null, null, '5', '375', '63', '6', '1545484615', null, null, null, '<p>Bác đến Pháp lần 2 thì sống ở <a class=\"cloze\" href=\"#mce_temp_url#\">Paris</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('377', null, null, null, '5', '0', '63', '6', '1545484667', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('378', null, null, null, '5', '377', '63', '6', '1545484667', null, null, null, '<p>Bác viết thư cho 1 đồng chí trong Quốc tế Cộng sản đề nghị được giao việc sau nhiều ngày không được hoạt động vào <a class=\"clozetip\" title=\"ngày/tháng/năm\" href=\"#\">6/6/1938</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('379', null, null, null, '5', '0', '63', '6', '1545484723', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('380', null, null, null, '5', '379', '63', '6', '1545484723', null, null, null, '<p>Bài \"Thư gửi các giới công thương Việt Nam\", Hồ Chí Minh viết năm bao nhiêu? </p>\r\n<p>Trả lời: <a class=\"clozetip\" title=\"ngày/tháng/năm\" href=\"#\">13/10/ 1945</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('381', null, null, null, '5', '0', '63', '6', '1545484806', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('382', null, null, null, '5', '381', '63', '6', '1545484806', null, null, null, '<p>Đảng lấy tư tưởng Hồ Chí Minh làm kim chỉ nam hành động trong Đại hội Đảng lần <a class=\"cloze\" href=\"#mce_temp_url#\">7</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('383', null, null, null, '5', '0', '63', '6', '1545484855', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('384', null, null, null, '5', '383', '63', '6', '1545484855', null, null, null, '<p>Bác gửi thư tới Quốc tế Cộng sản cần quan tâm tới vấn đề dân tộc thuộc địa thời gian nào?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">7/1923.</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('385', null, null, null, '5', '0', '63', '6', '1545484977', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('386', null, null, null, '5', '385', '63', '6', '1545484977', null, null, null, '<p>\"Hỡi đồng bào bị đọa đày đau khổ! Đây là <a class=\"cloze\" href=\"#mce_temp_url#\">cái cần thiết</a> cho chúng ta, đây là <a class=\"cloze\" href=\"#mce_temp_url#\">con đường giải phóng</a> chúng ta\"</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('387', null, null, null, '5', '0', '63', '6', '1545485008', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('388', null, null, null, '5', '387', '63', '6', '1545485008', null, null, null, '<p>Bác rời đại học Lenin- Liên Xô đến TQ vào thời gian nào ?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">10/1938</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('389', null, null, null, '5', '0', '63', '6', '1545485058', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('390', null, null, null, '5', '389', '63', '6', '1545485058', null, null, null, '<p><a class=\"cloze\" href=\"#mce_temp_url#\">Năm 1911</a> thay mặt những người VN yêu nước <a class=\"cloze\" href=\"#mce_temp_url#\">tại Pháp</a>, Nguyến Aí Quốc gửi bản <a class=\"cloze\" href=\"#mce_temp_url#\">Yêu sách</a> của nhân dân An Nam tới Hội nghị Vecsxay.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('391', null, null, null, '5', '0', '63', '6', '1545485206', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('392', null, null, null, '5', '391', '63', '6', '1545485206', null, null, null, '<p>Việc biểu quyết tán thành Đệ tam Quốc tế( Quốc tế III), tham gia thành lập <a class=\"cloze\" href=\"#mce_temp_url#\">Đảng Cộng sản Pháp</a> (12/1920), trở thành người cộng sản VN đầu tiên, đã đánh dấu bước chuyển biến về chất trong tư tưởng <a class=\"cloze\" href=\"#mce_temp_url#\">Nguyễn Aí Quốc</a>, từ cn yêu nước đến với cn Lenin, từ giác ngộ dân tộc đến giác ngộ giai cấp, từ người yêu nước trở thành người cộng sản.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('393', null, null, null, '5', '0', '63', '6', '1545485261', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('394', null, null, null, '5', '393', '63', '6', '1545485261', null, null, null, '<p>Bác Hồ tham gia thành lập vào Hội liên hiệp các nước thuộc địa vào năm nào?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">1921 </a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('395', null, null, null, '5', '0', '63', '6', '1545485323', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('396', null, null, null, '5', '395', '63', '6', '1545485323', null, null, null, '<p>Thời kì <a class=\"cloze\" href=\"#mce_temp_url#\">1921-1930</a>: Hình thành cơ bản tư tưởng về cm VN.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('397', null, null, null, '5', '0', '63', '6', '1545485352', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('398', null, null, null, '5', '397', '63', '6', '1545485352', null, null, null, '<p>Thời kì <a class=\"cloze\" href=\"#mce_temp_url#\">1930-1945</a>: Vượt qua thử thách, kiên trì giữ vững lập trường cm.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('399', null, null, null, '5', '0', '63', '6', '1545485436', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('400', null, null, null, '5', '0', '63', '6', '1545485436', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('401', null, null, null, '5', '0', '63', '6', '1545485436', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('402', null, null, null, '5', '399', '63', '6', '1545485436', null, null, null, '<p>Tuyên ngôn độc lập ngày <a class=\"cloze\" href=\"#mce_temp_url#\">2/9/1945</a> nêu rõ: \"Tất cả các dân tộc trên thế giới sinh ra đều bình đẳng, dân tộc nào cũng có <a class=\"cloze\" href=\"#mce_temp_url#\">quyền sống, quyền sung sướng, quyền tự do</a>\", \" Nước VN có quyền hưởng <a class=\"cloze\" href=\"#mce_temp_url#\">tự do và độc lập</a>, và sự thật đã thành một nước tự do độc lập. Toàn thể nhân dân VN quyết đem tất cả tinh thần và lực lượng, tính mạng và của cải để giữ vững quyền độc lập và tự do ấy.\"</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('403', null, null, null, '5', '400', '63', '6', '1545485436', null, null, null, '<p>Tuyên ngôn độc lập ngày <a class=\"cloze\" href=\"#mce_temp_url#\">2/9/1945</a> nêu rõ: \"Tất cả các dân tộc trên thế giới sinh ra đều bình đẳng, dân tộc nào cũng có <a class=\"cloze\" href=\"#mce_temp_url#\">quyền sống, quyền sung sướng, quyền tự do</a>\", \" Nước VN có quyền hưởng <a class=\"cloze\" href=\"#mce_temp_url#\">tự do và độc lập</a>, và sự thật đã thành một nước tự do độc lập. Toàn thể nhân dân VN quyết đem tất cả tinh thần và lực lượng, tính mạng và của cải để giữ vững quyền độc lập và tự do ấy.\"</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('404', null, null, null, '5', '401', '63', '6', '1545485436', null, null, null, '<p>Tuyên ngôn độc lập ngày <a class=\"cloze\" href=\"#mce_temp_url#\">2/9/1945</a> nêu rõ: \"Tất cả các dân tộc trên thế giới sinh ra đều bình đẳng, dân tộc nào cũng có <a class=\"cloze\" href=\"#mce_temp_url#\">quyền sống, quyền sung sướng, quyền tự do</a>\", \" Nước VN có quyền hưởng <a class=\"cloze\" href=\"#mce_temp_url#\">tự do và độc lập</a>, và sự thật đã thành một nước tự do độc lập. Toàn thể nhân dân VN quyết đem tất cả tinh thần và lực lượng, tính mạng và của cải để giữ vững quyền độc lập và tự do ấy.\"</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('405', null, null, null, '5', '0', '63', '6', '1545485458', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('406', null, null, null, '5', '405', '63', '6', '1545485458', null, null, null, '<p>Thời kì <a class=\"cloze\" href=\"#mce_temp_url#\">1945-1969</a>: Tư tưởng HCM tiếp tục phát triển, hoàn thiện.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('407', null, null, null, '5', '0', '63', '6', '1545485554', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('408', null, null, null, '5', '407', '63', '6', '1545485554', null, null, null, '<p>Giá trị tư tưởng HCM : tư tưởng HCM soi sáng con đường <a class=\"cloze\" href=\"#mce_temp_url#\">giải phóng và phát triển</a> dân tộc ,tư tưởng HCM với sự phát triển của thế giới.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('409', null, null, null, '5', '0', '63', '6', '1545485589', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('410', null, null, null, '5', '409', '63', '6', '1545485589', null, null, null, '<p>Tư tưởng HCM là tài sản tinh thần vô giá của dân tộc Việt Nam, là <a class=\"cloze\" href=\"#mce_temp_url#\">nền tảng</a> tư tưởng, <a class=\"cloze\" href=\"#mce_temp_url#\">kim chỉ nam</a> cho hành động của cm VN.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('411', null, null, null, '5', '0', '63', '6', '1545485657', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('412', null, null, null, '5', '0', '63', '6', '1545485657', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('413', null, null, null, '5', '411', '63', '6', '1545485657', null, null, null, '<p>Chủ tịch HCM đã căn dặn: <a class=\"cloze\" href=\"#mce_temp_url#\">Lý luận</a> không phải là một cái gì đó cứng nhắc, nó đầy tính <a class=\"cloze\" href=\"#mce_temp_url#\">sáng tạo</a>; lý luận luôn luôn cần được bổ sung bằng những kết luận mới rút ra từ trong <a class=\"cloze\" href=\"#mce_temp_url#\">thực tiễn sinh động</a>\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('414', null, null, null, '5', '412', '63', '6', '1545485657', null, null, null, '<p>Chủ tịch HCM đã căn dặn: <a class=\"cloze\" href=\"#mce_temp_url#\">Lý luận</a> không phải là một cái gì đó cứng nhắc, nó đầy tính <a class=\"cloze\" href=\"#mce_temp_url#\">sáng tạo</a>; lý luận luôn luôn cần được bổ sung bằng những kết luận mới rút ra từ trong <a class=\"cloze\" href=\"#mce_temp_url#\">thực tiễn sinh động</a>\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('415', null, null, null, '5', '0', '63', '6', '1545485662', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('416', null, null, null, '5', '415', '63', '6', '1545485662', null, null, null, '<p>Chủ tịch HCM đã căn dặn: <a class=\"cloze\" href=\"#mce_temp_url#\">Lý luận</a> không phải là một cái gì đó cứng nhắc, nó đầy tính <a class=\"cloze\" href=\"#mce_temp_url#\">sáng tạo</a>; lý luận luôn luôn cần được bổ sung bằng những kết luận mới rút ra từ trong <a class=\"cloze\" href=\"#mce_temp_url#\">thực tiễn sinh động</a>\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('417', null, null, null, '5', '0', '63', '6', '1545485746', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('418', null, null, null, '5', '417', '63', '6', '1545485746', null, null, null, '<p>Tư tưởng HCM với sự phát triển thế giới: Phản ánh <a class=\"cloze\" href=\"#mce_temp_url#\">khát vọng thời đại</a>, tìm ra các <a class=\"cloze\" href=\"#mce_temp_url#\">giải pháp đấu tranh</a> giải phóng loài người, cổ vũ các dân tộc đấu tranh trong sự nghiệp giải phóng.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('419', null, null, null, '5', '0', '63', '6', '1545485782', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('420', null, null, null, '5', '419', '63', '6', '1545485782', null, null, null, '<p>Thực chất của vấn đề dân tộc thuộc địa là đấu tranh chống <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa thực dân</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">giải phóng dân tộc</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('421', null, null, null, '5', '0', '63', '6', '1545485811', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('422', null, null, null, '5', '421', '63', '6', '1545485811', null, null, null, '<p>Nguồn gốc lí luận trực tiếp quyết định bản chất tư tưởng HCM là <a class=\"cloze\" href=\"#mce_temp_url#\">CN Mác-Lênin</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('423', null, null, null, '5', '0', '63', '6', '1545485844', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('424', null, null, null, '5', '423', '63', '6', '1545485844', null, null, null, '<p>Tư tưởng khai dân trí, trấn dân khí, hậu dân sinh của <a class=\"cloze\" href=\"#mce_temp_url#\">Phan Châu Trinh</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('425', null, null, null, '5', '0', '63', '6', '1545485882', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('426', null, null, null, '5', '425', '63', '6', '1545485882', null, null, null, '<p>Tư tưởng chủ đạo trong \"chính cương vắn tắt\" \"sách lươc vắn tắt\" là <a class=\"cloze\" href=\"#mce_temp_url#\">độc lập</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">tự do</a> dân tộc</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('427', null, null, null, '5', '0', '63', '6', '1545485953', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('428', null, null, null, '5', '427', '63', '6', '1545485953', null, null, null, '<p>HCM ra nhiệm vụ: “ Xem xét lại chủ nghĩa Mác về cơ sở lịch sử của nó, củng cố nó bằng dân tộc học phương Đông” trong tác phẩm: Báo cáo về <a class=\"cloze\" href=\"#mce_temp_url#\">Bắc kì</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">Trung kì</a> , <a class=\"cloze\" href=\"#mce_temp_url#\">Nam kì</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('429', null, null, null, '5', '0', '63', '6', '1545486006', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('430', null, null, null, '5', '429', '63', '6', '1545486006', null, null, null, '<p>Sự kiện đánh dấu bước ngoặt về chất trong tư tưởng và cuộc đời HCM, đưa HCM từ một người yêu nước chân chính trở thành người cộng sản đầu tiên của VN :Tham gia ĐH Tua(1920) biểu quyết tán thành viêc Đảng XH Pháp gia nhập <a class=\"cloze\" href=\"#mce_temp_url#\">Quốc tế cộng sản</a> và tham gia sáng lập <a class=\"cloze\" href=\"#mce_temp_url#\">ĐCS Pháp</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('431', null, null, null, '5', '0', '63', '6', '1545486087', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('432', null, null, null, '5', '431', '63', '6', '1545486087', null, null, null, '<p>\"Chính là do cố gắng vận dụng những lời dạy của Lênin, nhưng vận dụng một cách <a class=\"cloze\" href=\"#mce_temp_url#\">sáng tạo</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">phù hợp</a> với thực tiễn Việt Nam ngày nay, mà chúng tôi đã chiến đấu và giành được những thắng lợi đó, trước hết là nhờ cái vũ khí không gì thay thế được là <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa Mác-Lênin</a>\". </p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('433', null, null, null, '5', '0', '63', '6', '1545486122', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('434', null, null, null, '5', '433', '63', '6', '1545486122', null, null, null, '<p>Chủ nghĩa Tam dân được HCM đánh giá là <a class=\"cloze\" href=\"#mce_temp_url#\">phù hợp với hoàn cảnh nước ta</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('435', null, null, null, '5', '0', '63', '6', '1545486143', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('436', null, null, null, '5', '435', '63', '6', '1545486143', null, null, null, '<p>Liên bang Xô Viết thành lập <a class=\"cloze\" href=\"#mce_temp_url#\">30/12/1922</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('437', null, null, null, '5', '0', '63', '6', '1545486195', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('438', null, null, null, '5', '437', '63', '6', '1545486195', null, null, null, '<p>Tầm vóc và quy mô của CM Tháng 10 đã thực sự hấp dẫn mãnh liệt đối vs Nguyễn Ái Quốc cho rằng nguyên nhân nổ ra CM là sự mâu thuẫn tới tột đỉnh giữa <a class=\"cloze\" href=\"#mce_temp_url#\">công nông Nga</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">giai cấp tư sản Nga</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('439', null, null, null, '5', '0', '63', '6', '1545486235', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('440', null, null, null, '5', '439', '63', '6', '1545486235', null, null, null, '<p>Điều gì quan trọng nhất hình thành tư tưởng HCM</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa yêu nước</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('441', null, null, null, '5', '0', '63', '6', '1545486279', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('442', null, null, null, '5', '441', '63', '6', '1545486279', null, null, null, '<p>Đặc điểm nổi bật của xã hội cuối thế kỉ XIX đầu XX là <a class=\"cloze\" href=\"#mce_temp_url#\">Quốc tế cộng sản III ra đời năm 1919</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('443', null, null, null, '5', '0', '63', '6', '1545486360', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('444', null, null, null, '5', '443', '63', '6', '1545486360', null, null, null, '<p>Bác nói câu \"Làm bạn với tất cả mọi nước dân chủ và không gây thù oán với một ai\" năm bao nhiêu?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">1945</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('445', null, null, null, '5', '0', '63', '6', '1545486402', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('446', null, null, null, '5', '445', '63', '6', '1545486402', null, null, null, '<p>Trích Tuyên ngôn Độc lập của Mỹ năm 1776 đề cập tới <a class=\"cloze\" href=\"#mce_temp_url#\">quyền con người</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('447', null, null, null, '5', '0', '63', '6', '1545486473', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('448', null, null, null, '5', '447', '63', '6', '1545486473', null, null, null, '<p>Khi thực dân Pháp xâm lược Việt Nam, mâu thuẫn cơ bản của Việt Nam lúc đó là mâu thuẫn giữa <a class=\"cloze\" href=\"#mce_temp_url#\">toàn thể dân tộc</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">thực dân Pháp</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('449', null, null, null, '5', '0', '63', '6', '1545486529', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('450', null, null, null, '5', '449', '63', '6', '1545486529', null, null, null, '<p>Bác sáng lập hội liên hiệp thuộc địa :<a class=\"clozetip\" title=\"thời gian và địa điểm \" href=\"#\">7/1921 tại Pháp</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('451', null, null, null, '5', '0', '63', '6', '1545486588', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('452', null, null, null, '5', '451', '63', '6', '1545486588', null, null, null, '<p><a class=\"clozetip\" title=\"thời gian\" href=\"#\">5/1941</a>, Người cùng Trung ương Đảng khẳng định: “Trong lúc này… không giải quyết được vấn đề <a class=\"cloze\" href=\"#mce_temp_url#\">dân tộc giải phóng</a>, không đòi được độc lập, tự do…”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('453', null, null, null, '5', '0', '63', '6', '1545486630', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('454', null, null, null, '5', '453', '63', '6', '1545486630', null, null, null, '<p>Trong Cuộc kháng chiến, HCM viết: “Chỉ có giải phóng <a class=\"cloze\" href=\"#mce_temp_url#\">giai cấp vô sản</a> thì mới giải phóng được <a class=\"cloze\" href=\"#mce_temp_url#\">dân tộc</a>…”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('455', null, null, null, '5', '0', '63', '6', '1545486784', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('456', null, null, null, '5', '455', '63', '6', '1545486784', null, null, null, '<p>Nói về <a class=\"cloze\" href=\"#mce_temp_url#\">vai trò đoàn kết</a>: “Muốn cứu nước và giải phóng dân tộc không còn con đường nào khác con đường cách mạng vô sản”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('457', null, null, null, '5', '0', '63', '6', '1545486784', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('458', null, null, null, '5', '457', '63', '6', '1545486784', null, null, null, '<p>Nói về <a class=\"cloze\" href=\"#mce_temp_url#\">vai trò đoàn kết</a>: “Muốn cứu nước và giải phóng dân tộc không còn con đường nào khác con đường cách mạng vô sản”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('459', null, null, null, '5', '0', '63', '6', '1545486823', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('460', null, null, null, '5', '459', '63', '6', '1545486823', null, null, null, '<p>Ai là gốc cách mệnh ?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">Công nông</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('461', null, null, null, '5', '0', '63', '6', '1545486867', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('462', null, null, null, '5', '461', '63', '6', '1545486867', null, null, null, '<p>Ai là bầu bạn cách mệnh ?</p>\r\n<p>Trả lời:<a class=\"cloze\" href=\"#mce_temp_url#\"> Học trò, nhà buôn nhỏ, điền chủ nhỏ</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('463', null, null, null, '5', '0', '63', '6', '1545486910', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('464', null, null, null, '5', '463', '63', '6', '1545486910', null, null, null, '<p>“… nọc độc và sức sống của con rắn độc <a class=\"cloze\" href=\"#mce_temp_url#\">tư bản chủ nghĩa</a> đang tập trung ở các <a class=\"cloze\" href=\"#mce_temp_url#\">thuộc địa</a>”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('465', null, null, null, '5', '0', '63', '6', '1545486993', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('466', null, null, null, '5', '465', '63', '6', '1545486993', null, null, null, '<p>“Kháng chiến trường kỳ gian khổ đồng thời lại phải tự lực cánh sinh. Trông vào sức mình… Cố nhiên sự giúp đỡ của các nước bạn là <a class=\"cloze\" href=\"#mce_temp_url#\">quan trọng</a>, nhưng không được <a class=\"cloze\" href=\"#mce_temp_url#\">ỷ lại</a>… Một dân tộc không tự lực cánh sinh mà cứ <a class=\"cloze\" href=\"#mce_temp_url#\">ngồi chờ</a> dân tộc khác giúp đỡ thì không xứng đáng được độc lập”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('467', null, null, null, '5', '0', '63', '6', '1545487029', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('468', null, null, null, '5', '467', '63', '6', '1545487029', null, null, null, '<p>Ai là đồng minh cách mệnh ?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">Tư sản dân tộc</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('469', null, null, null, '5', '0', '63', '6', '1545487060', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('470', null, null, null, '5', '469', '63', '6', '1545487060', null, null, null, '<p><a class=\"cloze\" href=\"#mce_temp_url#\">C.Mác</a>: “bạo lực là bà đỡ”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('471', null, null, null, '5', '0', '63', '6', '1545487096', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('472', null, null, null, '5', '471', '63', '6', '1545487096', null, null, null, '<p>Hình thức của bạo lực cách mạng bao gồm cả đấu tranh <a class=\"cloze\" href=\"#mce_temp_url#\">chính trị </a>và đấu tranh <a class=\"cloze\" href=\"#mce_temp_url#\">vũ trang</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('473', null, null, null, '5', '0', '75', '6', '1545487212', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('474', null, null, null, '5', '473', '75', '6', '1545487212', null, null, null, '<p>Người viết:\" Để che đấy sự xấu xa của chế độ bóc lột giết người, <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa thực dân</a> luôn luôn điểm trang cho cái huy chương mục nát của nó bằng những châm ngôn lý tưởng: <a class=\"cloze\" href=\"#mce_temp_url#\">Bác ái, Bình đẳng</a>...\"</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('475', null, null, null, '5', '0', '75', '6', '1545487267', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('476', null, null, null, '5', '475', '75', '6', '1545487267', null, null, null, '<p>Trong <a class=\"cloze\" href=\"#mce_temp_url#\">Cương lĩnh chính trị đầu tiên </a>của ĐCS VN ,HCM viết:\" làm tư sản dân quyền cm và thổ địa cm để đi tới <a class=\"cloze\" href=\"#mce_temp_url#\">xã hội cộng sản</a>\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('477', null, null, null, '5', '0', '75', '6', '1545487325', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('478', null, null, null, '5', '477', '75', '6', '1545487325', null, null, null, '<p>HCM nói:\" <a class=\"cloze\" href=\"#mce_temp_url#\">Tự do</a> cho đồng bào tôi, <a class=\"cloze\" href=\"#mce_temp_url#\">độc lập</a> cho Tố quốc tôi, đấy là tất cả những điều tôi muốn, tất cả những điều tôi hiểu\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('479', null, null, null, '5', '0', '75', '6', '1545487379', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('480', null, null, null, '5', '479', '75', '6', '1545487379', null, null, null, '<p>Tháng 5/1941, HCM chủ trì hội nghị lần thứ <a class=\"cloze\" href=\"#mce_temp_url#\">8</a> ban chấp hành TW Đảng, viết thư Kính cáo đồng bảo chỉ rõ: \"trong lúc này, quyền lợi dân tộc, giải phóng dân tộc cai hơn tất thảy\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('481', null, null, null, '5', '0', '75', '6', '1545487423', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('482', null, null, null, '5', '481', '75', '6', '1545487423', null, null, null, '<p>Trước cm tháng <a class=\"cloze\" href=\"#mce_temp_url#\">8</a>, HCM nói với đồng chí <a class=\"cloze\" href=\"#mce_temp_url#\">Võ Nguyên Giap</a>: \"Dù hi sinh tới đâu, dù đốt cháy cả dãy Trường Sơn cũng phải kiên trì giành cho được độc lập\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('483', null, null, null, '5', '0', '75', '6', '1545487479', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('484', null, null, null, '5', '483', '75', '6', '1545487479', null, null, null, '<p>Trong các <a class=\"cloze\" href=\"#mce_temp_url#\">thư và điện văn</a> gửi đến Chính phủ các nước sau cách mạng tháng 8, HCM trịnh trọng tuyên bố: \" Nhân dân chúng tôi thành thật mong muốn hòa bình. Nhưng nhân dân chúng tôi cũng kiên quyết đến cùng để bảo vệ những quyền thiêng liêng nhất: <a class=\"cloze\" href=\"#mce_temp_url#\">toàn vẹn lành thổ</a> cho tổ quốc, và <a class=\"cloze\" href=\"#mce_temp_url#\">độc lập đất nước</a>\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('485', null, null, null, '5', '0', '75', '6', '1545487532', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('486', null, null, null, '5', '485', '75', '6', '1545487532', null, null, null, '<p>Trong lời kêu gọi toàn quốc kháng chiến chống Pháp ngày <a class=\"cloze\" href=\"#mce_temp_url#\">9/12/1946</a>, HCM nói: \"Không. Chúng ta thà hy sinh tất cả chứ nhất định không chịu <a class=\"cloze\" href=\"#mce_temp_url#\">mất nước</a>, nhất định không chịu<a class=\"cloze\" href=\"#mce_temp_url#\"> làm nô lệ</a>\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('487', null, null, null, '5', '0', '75', '6', '1545487562', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('488', null, null, null, '5', '487', '75', '6', '1545487562', null, null, null, '<p>\"Không có gì quý hơn độc lập, tự do\" trích trong tác phẩm <a class=\"cloze\" href=\"#mce_temp_url#\">Không có gì quý hơn độc lập tự do</a> năm 1966</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('489', null, null, null, '5', '0', '75', '6', '1545487612', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('490', null, null, null, '5', '489', '75', '6', '1545487612', null, null, null, '<p>Theo HCM: \"Chính do <a class=\"cloze\" href=\"#mce_temp_url#\">tinh thần yêu nước</a> mà quân đội và nhân dân ta đã mấy năm trường chiu đựng trăm đắng nghìn cay, kiên quyết đánh cho tan bọn <a class=\"cloze\" href=\"#mce_temp_url#\">thực dân cướp nước</a> và bọn <a class=\"cloze\" href=\"#mce_temp_url#\">Việt gian phản quốc</a>, kiên quyết xây dựng một nước VN độc lập thống nhất,dân chủ, tự do, phú cường, một nước VN dân chủ mới\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('491', null, null, null, '5', '0', '75', '6', '1545487652', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('492', null, null, null, '5', '491', '75', '6', '1545487652', null, null, null, '<p>Làm tư sản <a class=\"cloze\" href=\"#mce_temp_url#\">cách mạng dân quyền</a> và thổ địa cách mạng là đánh đổ đế quốc chủ nghĩa và bọn phong kiến.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('493', null, null, null, '5', '0', '75', '6', '1545487690', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('494', null, null, null, '5', '493', '75', '6', '1545487690', null, null, null, '<p>Tùy từng tình hình cụ thể mà quyết định những hình thức đấu tranh cách mạng thích hợp, sử dụng đúng và khéo léo kết hợp hình thức đấu tranh <a class=\"cloze\" href=\"#mce_temp_url#\">vũ trang</a> và đấu tranh <a class=\"cloze\" href=\"#mce_temp_url#\">chính trị</a> để giành thắng lợi cho cm.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('495', null, null, null, '5', '0', '75', '6', '1545487733', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('496', null, null, null, '5', '495', '75', '6', '1545487733', null, null, null, '<p>HCM không chỉ là <a class=\"cloze\" href=\"#mce_temp_url#\">anh hùng giải phóng dân tộc</a> của VN mà còn là: \"Người khởi xướng cuộc đấu tranh của các dân tộc thuộc địa thế kỉ XX\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('497', null, null, null, '5', '0', '75', '6', '1545487778', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('498', null, null, null, '5', '497', '75', '6', '1545487778', null, null, null, '<p>Đại hội Ban chấp hành TW lần 2 đổi tên đảng là gì?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">Đảng Lao động Việt Nam</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('499', null, null, null, '5', '0', '75', '6', '1545487811', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('500', null, null, null, '5', '499', '75', '6', '1545487811', null, null, null, '<p>Khối liên minh giữa cm thuộc địa và cm vô sản đc ví như hình tượng <a class=\"cloze\" href=\"#mce_temp_url#\">hai cánh của một con chim</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('501', null, null, null, '5', '0', '75', '6', '1545487853', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('502', null, null, null, '5', '501', '75', '6', '1545487853', null, null, null, '<p>Theo HCM,nền tảng độc lập của 1 quốc gia là ở vấn đề nào sau đây?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">độc lập về chính trị</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('503', null, null, null, '5', '0', '75', '6', '1545487890', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('504', null, null, null, '5', '503', '75', '6', '1545487890', null, null, null, '<p>Trong những câu nói sau đây về tính tất yếu của bạo lực cách mạng,đâu là câu nói của Hồ Chí Minh:<a class=\"cloze\" href=\"#mce_temp_url#\">Độc lập tự do</a> không thể cầu xin mà có được.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('505', null, null, null, '5', '0', '75', '6', '1545487919', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('506', null, null, null, '5', '505', '75', '6', '1545487919', null, null, null, '<p>Theo Hồ Chí Minh động lực vĩ đại và duy nhất của con người An Nam là <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa dân tộc</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('507', null, null, null, '5', '0', '75', '6', '1545487951', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('508', null, null, null, '5', '507', '75', '6', '1545487951', null, null, null, '<p>Chủ tịch Hồ Chí Minh xác định: “Từ cộng sản nguyên thủy đến chế độ nô lệ, đến chế độ phong kiến, đến chế độ tư bản, đến chủ nghĩa xã hội (cộng sản) - nói chung thì loài người phát triển theo quy luật nhất định như vậy. Nhưng tùy hoàn cảnh, mà các dân tộc phát triển theo con đường khác nhau. Có nước thì đi thẳng đến chủ nghĩa xã hội (cộng sản) như <a class=\"cloze\" href=\"#mce_temp_url#\">Liên Xô</a>. Có nước thì phải kinh qua chế độ dân chủ mới, rồi tiến lên chủ nghĩa xã hội (cộng sản) - như các nước Đông Âu, Trung Quốc, Việt Nam ta”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('509', null, null, null, '5', '0', '75', '6', '1545487993', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('510', null, null, null, '5', '509', '75', '6', '1545487993', null, null, null, '<p>CM giải phóng dân tộc và CM vô sản có mối quan hệ <a class=\"cloze\" href=\"#mce_temp_url#\">khăng khít</a> với nhau nhưng không <a class=\"cloze\" href=\"#mce_temp_url#\">phụ thuộc</a> vào nhau.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('511', null, null, null, '5', '0', '75', '6', '1545488059', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('512', null, null, null, '5', '511', '75', '6', '1545488059', null, null, null, '<p>\"Vì bị áp bức mà sinh ra <a class=\"cloze\" href=\"#mce_temp_url#\">cách mệnh</a>, cho nên ai càng bị áp bức nặng nề thì lòng cách mệnh càng bền, chí cách mệnh càng quyết. Trong điều kiện tư bản áp bức <a class=\"cloze\" href=\"#mce_temp_url#\">công nông</a> thì công nông là người chủ cách mệnh, là gốc cách mệnh; còn học trò, nhà buôn nhỏ, điền chủ nhỏ chỉ là bầu bạn cách mệnh của công nông, vì: công nông bị áp bức nặng hơn; công nông là đông nhất cho nên sức mạnh hơn hết; công nông là tay không chân rồi, nếu thua thì chỉ mất một cái kiếp khổ, nếu được thì được cả thế giới, cho nên họ gan góc .\"</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('513', null, null, null, '5', '0', '75', '6', '1545488120', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('514', null, null, null, '5', '513', '75', '6', '1545488120', null, null, null, '<p>Theo HCM hình thái của bạo lực cách mạng Việt Nam là thực hiện <a class=\"cloze\" href=\"#mce_temp_url#\">kháng chiến toàn dân</a>, toàn diện, trường kì kháng chiến, tự lực cánh sinh.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('515', null, null, null, '5', '0', '75', '6', '1545488196', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('516', null, null, null, '5', '515', '75', '6', '1545488196', null, null, null, '<p>\"Chủ nghĩa tư bản là một con đỉa có một cái vòi bám vào giai cấp ... Nếu muốn giết con vật ấy người ta phải đồng thời <a class=\"cloze\" href=\"#mce_temp_url#\">cắt cả hai vòi</a>.\"</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('517', null, null, null, '5', '0', '75', '6', '1545488264', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('518', null, null, null, '5', '517', '75', '6', '1545488264', null, null, null, '<p>Ai là người bàn nhiều nhất đến vấn đề đấu tranh ở các nước tư bản phương Tây?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">C.Mác</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('519', null, null, null, '5', '0', '75', '6', '1545488302', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('520', null, null, null, '5', '519', '75', '6', '1545488302', null, null, null, '<p>Ai là người nói nhiều nhất về chủ nghĩa đế quốc</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">Lenin</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('521', null, null, null, '5', '0', '75', '6', '1545488338', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('522', null, null, null, '5', '521', '75', '6', '1545488338', null, null, null, '<p>Ai là người nói nhiều nhất về chủ nghĩa thực dân </p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">HCM</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('523', null, null, null, '5', '0', '75', '6', '1545488377', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('524', null, null, null, '5', '523', '75', '6', '1545488377', null, null, null, '<p>Kẻ thù lớn nhất của nông dân là <a class=\"cloze\" href=\"#mce_temp_url#\">đế quốc</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('525', null, null, null, '5', '0', '75', '6', '1545488409', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('526', null, null, null, '5', '0', '75', '6', '1545488409', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('527', null, null, null, '5', '525', '75', '6', '1545488409', null, null, null, '<p>Đối tượng của cách mạng ở thuộc địa: <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa thực dân</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">tay sai phản động</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('528', null, null, null, '5', '526', '75', '6', '1545488409', null, null, null, '<p>Đối tượng của cách mạng ở thuộc địa: <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa thực dân</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">tay sai phản động</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('529', null, null, null, '5', '0', '75', '6', '1545488412', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('530', null, null, null, '5', '529', '75', '6', '1545488413', null, null, null, '<p>Đối tượng của cách mạng ở thuộc địa: <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa thực dân</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">tay sai phản động</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('531', null, null, null, '5', '0', '75', '6', '1545488441', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('532', null, null, null, '5', '531', '75', '6', '1545488441', null, null, null, '<p><a class=\"cloze\" href=\"#mce_temp_url#\">Dân tộc cách mệnh</a> thì chưa phân giai cấp, nghĩa là sĩ, nông, công, thương đều nhất trí chống lại cường quyền.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('533', null, null, null, '5', '0', '75', '6', '1545488485', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('534', null, null, null, '5', '533', '75', '6', '1545488485', null, null, null, '<p>Năm <a class=\"cloze\" href=\"#mce_temp_url#\">1960</a> Người nói: \"Chỉ có cnxh, <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa cộng sản</a> mới giải phóng được các dân tộc bị áp bức và những người lao động trên thế giới khỏi ách nô lệ\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('535', null, null, null, '5', '0', '75', '6', '1545488485', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('536', null, null, null, '5', '535', '75', '6', '1545488485', null, null, null, '<p>Năm <a class=\"cloze\" href=\"#mce_temp_url#\">1960</a> Người nói: \"Chỉ có cnxh, <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa cộng sản</a> mới giải phóng được các dân tộc bị áp bức và những người lao động trên thế giới khỏi ách nô lệ\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('537', null, null, null, '5', '0', '75', '6', '1545488558', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('538', null, null, null, '5', '0', '75', '6', '1545488558', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('539', null, null, null, '5', '0', '75', '6', '1545488558', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('540', null, null, null, '5', '538', '75', '6', '1545488558', null, null, null, '<p>Ai đã nói: \"Nước được độc lập mà dân không được hưởng hạnh phúc tự do, thì độc lập cũng chẳng có nghĩa lý gì\".</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">HCM</a></p>\r\n<p> </p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('541', null, null, null, '5', '539', '75', '6', '1545488558', null, null, null, '<p>Ai đã nói: \"Nước được độc lập mà dân không được hưởng hạnh phúc tự do, thì độc lập cũng chẳng có nghĩa lý gì\".</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">HCM</a></p>\r\n<p> </p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('542', null, null, null, '5', '537', '75', '6', '1545488558', null, null, null, '<p>Ai đã nói: \"Nước được độc lập mà dân không được hưởng hạnh phúc tự do, thì độc lập cũng chẳng có nghĩa lý gì\".</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">HCM</a></p>\r\n<p> </p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('543', null, null, null, '5', '0', '75', '6', '1545488560', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('544', null, null, null, '5', '543', '75', '6', '1545488560', null, null, null, '<p>Ai đã nói: \"Nước được độc lập mà dân không được hưởng hạnh phúc tự do, thì độc lập cũng chẳng có nghĩa lý gì\".</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">HCM</a></p>\r\n<p> </p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('545', null, null, null, '5', '0', '75', '6', '1545488601', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('546', null, null, null, '5', '545', '75', '6', '1545488601', null, null, null, '<p>Người khẳng định: \" Yêu <a class=\"cloze\" href=\"#mce_temp_url#\">tổ quốc</a>, yêu <a class=\"cloze\" href=\"#mce_temp_url#\">nhân dân</a> phải gắn liền với cnxh, vì có tiến lên cnxh thì nhân dân mình mỗi ngày một ấm lo thêm, tổ quốc mỗi ngày một giàu mạnh thêm\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('547', null, null, null, '5', '0', '75', '6', '1545488652', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('548', null, null, null, '5', '547', '75', '6', '1545488652', null, null, null, '<p>Đối tượng của cm ở thuộc địa không phải là giai cấp tư sản bản xứ, càng không phải giai cấp địa chủ nói chung, mà là <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa thực dân</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">tay sai phản động</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('549', null, null, null, '5', '0', '75', '6', '1545488682', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('550', null, null, null, '5', '549', '75', '6', '1545488682', null, null, null, '<p>Yêu cầu bức thiết của nhân dân các dân tộc thuộc địa là <a class=\"cloze\" href=\"#mce_temp_url#\">dân tộc độc lập</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('551', null, null, null, '5', '0', '75', '6', '1545488714', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('552', null, null, null, '5', '551', '75', '6', '1545488714', null, null, null, '<p>Trong nền kinh tế Cộng sản mới ko có thành phần kinh tế nào?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">Địa chủ phong kiến</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('553', null, null, null, '5', '0', '75', '6', '1545488757', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('554', null, null, null, '5', '553', '75', '6', '1545488757', null, null, null, '<p>Mấy điểm lớn trong chính sách cán bộ</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">cất nhắc, hiểu biết, sâu sát, phê bình.</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('555', null, null, null, '5', '0', '75', '6', '1545488786', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('556', null, null, null, '5', '555', '75', '6', '1545488786', null, null, null, '<p>Theo Chủ tịch Hồ Chí Minh, đặc trưng kinh tế nổi bật nhất của chủ nghĩa xã hội là Sở hữu xã hội về <a class=\"cloze\" href=\"#mce_temp_url#\">tư liệu sản xuất</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('557', null, null, null, '5', '0', '75', '6', '1545488811', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('558', null, null, null, '5', '557', '75', '6', '1545488811', null, null, null, '<p>\"Gốc có vững cây mới bền, xây lầu thắng lợi trên nền <a class=\"cloze\" href=\"#mce_temp_url#\">nhân dân</a>\"</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('559', null, null, null, '5', '0', '75', '6', '1545488847', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('560', null, null, null, '5', '559', '75', '6', '1545488847', null, null, null, '<p>Cơ cấu ngành ktế trong thời kì quá độ lên CNXH</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">Nông - công - thương</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('561', null, null, null, '5', '0', '75', '6', '1545488877', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('562', null, null, null, '5', '561', '75', '6', '1545488877', null, null, null, '<p>Cơ sở chủ yếu để Hồ Chí Minh hình thành chính sách con người : Chủ nghĩa Mác lênin về <a class=\"cloze\" href=\"#mce_temp_url#\">quần chúng nhân dân</a> .</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('563', null, null, null, '5', '0', '75', '6', '1545488926', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('564', null, null, null, '5', '563', '75', '6', '1545488926', null, null, null, '<p>“<a class=\"cloze\" href=\"#mce_temp_url#\">Chủ nghĩa cá nhân</a> là một trở ngại lớn cho việc xây dựng chủ nghĩa xã hội. Cho nên thắng lợi của <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa xã hội</a> không thể tách rời thắng lợi của cuộc đấu tranh trừ bỏ chủ nghĩa cá nhân”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('565', null, null, null, '5', '0', '75', '6', '1545488965', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('566', null, null, null, '5', '565', '75', '6', '1545488965', null, null, null, '<p>Mâu thuẫn chủ yếu ở thuộc địa là <a class=\"cloze\" href=\"#mce_temp_url#\">mâu thuẫn dân tộc</a>, quy định tính chất và nhiệm vụ hàng đầu của cm thuộc địa là <a class=\"cloze\" href=\"#mce_temp_url#\">giải phóng dân tộc.</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('567', null, null, null, '5', '0', '75', '6', '1545488997', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('568', null, null, null, '5', '567', '75', '6', '1545488997', null, null, null, '<p>Người nhận thấy: \"Cách mệnh Pháp cũng như cách mệnh Mĩ, nghĩa là <a class=\"cloze\" href=\"#mce_temp_url#\">cách mệnh tư bản</a>, cách mện không đến nơi, tiếng là cộng hòa dân chủ, kì thực trong thì nó tước lục công nông, ngoài thì nó áp bức thuộc địa\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('569', null, null, null, '5', '0', '75', '6', '1545489029', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('570', null, null, null, '5', '569', '75', '6', '1545489029', null, null, null, '<p>Người khẳng định: \"Muốn cứu nước và giải phóng dân tộc không còn con đường nào khác con đường <a class=\"cloze\" href=\"#mce_temp_url#\">cm vô sản</a>\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('571', null, null, null, '5', '0', '75', '6', '1545489079', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('572', null, null, null, '5', '571', '75', '6', '1545489079', null, null, null, '<p>Nguyễn Aí Quốc phân tích: \"Việc gì khó cho mấy quyết tâm làm thì chắc chắn làm được, ít người làm không nổi thì nhiều người <a class=\"cloze\" href=\"#mce_temp_url#\">đồng tâm hiệp lực</a> mà làm thì phải nổi. Đời này làm chưa xong thì đời sau nối theo làm thì phải xong\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('573', null, null, null, '5', '0', '75', '6', '1545489113', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('574', null, null, null, '5', '573', '75', '6', '1545489113', null, null, null, '<p>Trong tác phẩm <a class=\"cloze\" href=\"#mce_temp_url#\">Đường cách mệnh</a> Người khẳng định: \"Trước hết phải có <a class=\"cloze\" href=\"#mce_temp_url#\">đảng cách mệnh</a>, để trong thì vận động và tổ chức dân chúng, ngoài thì liên lạc với các dân tộc bị áp bức và vô sản giai cấp mọi nơi. Đảng có vững cách mệnh mới thành công cũng như người cầm lái có vững thuyền mới chạy\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('575', null, null, null, '5', '0', '75', '6', '1545489150', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('576', null, null, null, '5', '575', '75', '6', '1545489150', null, null, null, '<p>\"Để có cơ hội thắng lợi, một cuộc khởi nghĩa vũ trang ở Đông Dương: Phải có tính chất một cuộc <a class=\"cloze\" href=\"#mce_temp_url#\">khởi nghĩa quần chúng</a> chứ không phải một <a class=\"cloze\" href=\"#mce_temp_url#\">cuộc nổi loạn</a>. Cuộc khởi nghĩa phải được chuẩn bị trong quần chúng...\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('577', null, null, null, '5', '0', '75', '6', '1545489178', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('578', null, null, null, '5', '577', '75', '6', '1545489178', null, null, null, '<p>HCM nói: \"<a class=\"cloze\" href=\"#mce_temp_url#\">Cách mệnh</a> là việc chung của cả dân chúng chứ không phải việc của một hai người\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('579', null, null, null, '5', '0', '75', '6', '1545489227', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('580', null, null, null, '5', '579', '75', '6', '1545489227', null, null, null, '<p>HCM nói: \"Đối với tôi, câu trả lời đã rõ ràng,: trở về nước, đi vào quần chúng, <a class=\"cloze\" href=\"#mce_temp_url#\">thức tỉnh</a> họ, <a class=\"cloze\" href=\"#mce_temp_url#\">tổ chức</a> họ, <a class=\"cloze\" href=\"#mce_temp_url#\">đoàn kết</a> họ, <a class=\"cloze\" href=\"#mce_temp_url#\">huấn luyện</a> họ, đưa họ ra đấu tranh giành độc lập tự do\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('581', null, null, null, '5', '0', '75', '6', '1545489272', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('582', null, null, null, '5', '581', '75', '6', '1545489272', null, null, null, '<p>“Chỉ trong <a class=\"cloze\" href=\"#mce_temp_url#\">đấu tranh</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">công tác</a> hằng ngày, khi quần chúng rộng rãi thừa nhận chính sách…”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('583', null, null, null, '5', '0', '75', '6', '1545489299', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('584', null, null, null, '5', '583', '75', '6', '1545489299', null, null, null, '<p>“Đảng vừa lo tính công việc… đến những việc nhỏ như <a class=\"cloze\" href=\"#mce_temp_url#\">tương cà mắm muối</a> cần thiết cho đời sống hằng ngày của nhân dân”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('585', null, null, null, '5', '0', '75', '6', '1545489331', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('586', null, null, null, '5', '585', '75', '6', '1545489331', null, null, null, '<p>“Sự lãnh đạo trong mọi công tác thiết thực của Đảng, ắt phải từ trong <a class=\"cloze\" href=\"#mce_temp_url#\">quần chúng</a> ra, trở lại nơi quần chúng”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('587', null, null, null, '5', '0', '75', '6', '1545489374', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('588', null, null, null, '5', '587', '75', '6', '1545489374', null, null, null, '<p>Đoàn kết thống nhất trong Đảng: “sống với nhau <a class=\"cloze\" href=\"#mce_temp_url#\">có tình, có nghĩa</a>”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('589', null, null, null, '5', '0', '75', '6', '1545489422', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('590', null, null, null, '5', '589', '75', '6', '1545489422', null, null, null, '<p>“Đối với mọi vấn đề, mọi người <a class=\"cloze\" href=\"#mce_temp_url#\">tự do</a> bày tỏ ý kiến của mình…quyền tự do tư tưởng hóa ra quyền tự do phục tùng chân lý”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('591', null, null, null, '5', '0', '75', '6', '1545492844', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('592', null, null, null, '5', '591', '75', '6', '1545492844', null, null, null, '<p>HCM: “… Nếu không có <a class=\"cloze\" href=\"#mce_temp_url#\">cá nhân phụ trách</a> thì sẽ sinh cái tệ người này ủy cho người kia, người kia ủy cho người nọ, kết quả là không ai thi hành…”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('593', null, null, null, '5', '0', '75', '6', '1545492892', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('594', null, null, null, '5', '593', '75', '6', '1545492892', null, null, null, '<p><a class=\"cloze\" href=\"#mce_temp_url#\">HCM</a> khẳng định: \" <a class=\"cloze\" href=\"#mce_temp_url#\">Dân khí</a> mạnh thì quân lính nào, súng ống nào cũng không chống lại nổi\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('595', null, null, null, '5', '0', '75', '6', '1545492944', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('596', null, null, null, '5', '595', '75', '6', '1545492944', null, null, null, '<p>Trong <a class=\"cloze\" href=\"#mce_temp_url#\">Đường cách mệnh</a>, HCM viết: \"...<a class=\"cloze\" href=\"#mce_temp_url#\">dân tộc cách mệnh</a> thì chưa phân giai cấp, nghĩa là sĩ, nông, công, thương đều nhất trí chống lại cường quyền\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('597', null, null, null, '5', '0', '75', '6', '1545493002', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('598', null, null, null, '5', '597', '75', '6', '1545493002', null, null, null, '<p>HCM nói: \"lòng cách mệnh càng bền, chí cách mệnh càng quyết... Công nông là <a class=\"cloze\" href=\"#mce_temp_url#\">tay không chân rồi</a>, nếu thua thì chỉ mất một cái kiếp khổ, nếu được thì được cả thế giới cho nên họ gan góc\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('599', null, null, null, '5', '0', '75', '6', '1545493033', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('600', null, null, null, '5', '599', '75', '6', '1545493033', null, null, null, '<p>Người chỉ rõ: \"...học trò, nhà buôn nhỏ, điền chủ nhỏ cũng bị tư bản áp bức, song không cực khổ bằng công nông, ba hạng ấy chỉ là <a class=\"cloze\" href=\"#mce_temp_url#\">bầu bạn cách mệnh</a> của công nông thôi\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('601', null, null, null, '5', '0', '75', '6', '1545493079', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('602', null, null, null, '5', '601', '75', '6', '1545493079', null, null, null, '<p>Trong kháng chiến chống Pháp, Người nói: \"Một dân tộc không <a class=\"cloze\" href=\"#mce_temp_url#\">tự lực cánh sinh</a> mà cứ ngồi chờ dân tộc khác giúp đỡ thì không xứng đáng được độc lập\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('603', null, null, null, '5', '0', '75', '6', '1545493126', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('604', null, null, null, '5', '603', '75', '6', '1545493126', null, null, null, '<p><a class=\"cloze\" href=\"#mce_temp_url#\">Vận dụng công thức của Mác</a>: \"Sự giải phóng của giai cấp công nhân phải là sự nghiệp của bản thân công nhân\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('605', null, null, null, '5', '0', '75', '6', '1545493192', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('606', null, null, null, '5', '605', '75', '6', '1545493192', null, null, null, '<p>Tháng <a class=\"cloze\" href=\"#mce_temp_url#\">8/1945</a> khi thời cơ cm xuất hiện, Người kêu gọi:\" <a class=\"cloze\" href=\"#mce_temp_url#\">Toàn quốc đồng bào</a> hãy đứng dậy đem sức ta mà giải phóng cho ta\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('607', null, null, null, '5', '0', '75', '6', '1545493290', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('608', null, null, null, '5', '607', '75', '6', '1545493290', null, null, null, '<p>HCM nói:\"<a class=\"cloze\" href=\"#mce_temp_url#\">Chế độ thực dân</a>, tự bản thân nó, đã là một <a class=\"cloze\" href=\"#mce_temp_url#\">hành động bạo lực</a> của kẻ mạnh đối với kẻ yếu rồi\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('609', null, null, null, '5', '0', '75', '6', '1545493361', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('610', null, null, null, '5', '609', '75', '6', '1545493361', null, null, null, '<p>HCM vạch rõ tính <a class=\"cloze\" href=\"#mce_temp_url#\">tất yếu</a> của bạo lực cm:\"Trong cuộc đấu tranh gian khổ chống kẻ thù của giai cấp và của dân tộc, cần dùng <a class=\"cloze\" href=\"#mce_temp_url#\">bạo lực cách mạng</a> chống lại <a class=\"cloze\" href=\"#mce_temp_url#\">bạo lực phản cách mạng</a>, giành lấy chính quyền và bảo vệ chính quyền\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('611', null, null, null, '5', '0', '77', '6', '1545493631', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('612', null, null, null, '5', '611', '77', '6', '1545493631', null, null, null, '<p>\"Chỉ có <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa cộng sản</a> mới cứu nhân loại, đem lại cho mọi người không phân biệt chủng tộc, nguồn gốc sự tự do, bình đẳng...\"</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('613', null, null, null, '5', '0', '77', '6', '1545493681', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('614', null, null, null, '5', '613', '77', '6', '1545493681', null, null, null, '<p>Khi nói chuyện tại lớp hướng dẫn giáo viên cấp II,III và Hội nghị sư phạm vào 7/1956, HCM bày tỏ quan điểm về <a class=\"cloze\" href=\"#mce_temp_url#\">phân phối theo lao động</a>: \"cnxh là lấy nhà máy, xe lửa, ngân hàng...<a class=\"cloze\" href=\"#mce_temp_url#\">làm của chung</a>. Ai làm nhiều ăn nhiều, làm ít ăn ít, ai không làm thì không ăn tất nhiên là trừ người già cả, đau yếu và trẻ con\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('615', null, null, null, '5', '0', '77', '6', '1545493707', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('616', null, null, null, '5', '615', '77', '6', '1545493707', null, null, null, '<p>Lý tưởng cao nhất của cnxh là \"<a class=\"cloze\" href=\"#mce_temp_url#\">liên hợp tự do</a> của những người lao động\" mà C.Mác. Angghen đã dự báo.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('617', null, null, null, '5', '0', '77', '6', '1545493734', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('618', null, null, null, '5', '617', '77', '6', '1545493734', null, null, null, '<p>Ở HCM, mục tiêu chung của cnxh và mục tiêu phấn đấu của người là một, đó là <a class=\"cloze\" href=\"#mce_temp_url#\">độc lập, tự do</a> cho dân tộc, hạnh phúc cho nhân dân...</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('619', null, null, null, '5', '0', '77', '6', '1545493772', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('620', null, null, null, '5', '619', '77', '6', '1545493772', null, null, null, '<p>Trong bản <a class=\"cloze\" href=\"#mce_temp_url#\">Di chúc</a>, HCM viết :\" Điều mong muốn cuối cùng của tôi là: <a class=\"cloze\" href=\"#mce_temp_url#\">Toàn đảng, toàn dân ta đoàn kết</a> phấn đấu,xây dựng một nước VN hòa bình, thống nhất, độc lập, dân chủ và giàu mạnh góp phần xứng đáng vào sự nghiệp cm thế giới\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('621', null, null, null, '5', '0', '77', '6', '1545493807', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('622', null, null, null, '5', '621', '77', '6', '1545493807', null, null, null, '<p>HCM quan niệm mục tiêu cao nhất của cnxh là <a class=\"cloze\" href=\"#mce_temp_url#\">nâng cao đời sống</a> nhân dân.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('623', null, null, null, '5', '0', '77', '6', '1545493843', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('624', null, null, null, '5', '623', '77', '6', '1545493843', null, null, null, '<p><a class=\"cloze\" href=\"#mce_temp_url#\">Mục tiêu chính trị</a>, theo HCM, trong thời kì quá độ lên cnxh, chế độ chính trị phải do nhân dân lao động làm chủ, Nhà nước là của dân, do dân, vì dân.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('625', null, null, null, '5', '0', '77', '6', '1545493875', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('626', null, null, null, '5', '625', '77', '6', '1545493875', null, null, null, '<p><a class=\"cloze\" href=\"#mce_temp_url#\">Mục tiêu kinh tế</a>, theo HCM, nền kinh tế xhcn với công-nông nghiệp hiện đại, khoa học-kĩ thuật tiên tiến, cách bóc lột theo chủ nghĩa tư bản được bỏ dần, đời sống vật chất nhân dân ngày càng được cải thiện.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('627', null, null, null, '5', '0', '77', '6', '1545493913', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('628', null, null, null, '5', '627', '77', '6', '1545493913', null, null, null, '<p>Nhà thơ <a class=\"cloze\" href=\"#mce_temp_url#\">Thanh Tịnh</a>: “Dễ trăm lần không dân cũng chịu, khó vạn lần dân liệu cũng xong”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('629', null, null, null, '5', '0', '77', '6', '1545493945', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('630', null, null, null, '5', '629', '77', '6', '1545493945', null, null, null, '<p>Cơ sở thực tiễn: <a class=\"cloze\" href=\"#mce_temp_url#\">Công xã Pari</a> 1971 – cách mạng vô sản đầu tiên, “Sự thất bại của các phong trào cách mạng Việt Nam là do sự đơn độc”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('631', null, null, null, '5', '0', '77', '6', '1545494001', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('632', null, null, null, '5', '631', '77', '6', '1545494001', null, null, null, '<p>Chiến lược: <a class=\"cloze\" href=\"#mce_temp_url#\">cơ bản, nhất quán, xuyên suốt</a> phân biệt với thủ đoạn chính trị: giành chính quyền</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('633', null, null, null, '5', '0', '77', '6', '1545494033', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('634', null, null, null, '5', '633', '77', '6', '1545494033', null, null, null, '<p><a class=\"cloze\" href=\"#mce_temp_url#\">C.Mác</a>: “Con người là chủ thể sáng tạo ra lịch sử”, “Vô sản các nước trên thế giới liên hiệp lại”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('635', null, null, null, '5', '0', '77', '6', '1545494078', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('636', null, null, null, '5', '635', '77', '6', '1545494078', null, null, null, '<p>Hưng Đạo Vương <a class=\"cloze\" href=\"#mce_temp_url#\"><span style=\"color: #888888;\">Trần Quốc Tuấn</span></a>: “Khoan thư sức dân”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('637', null, null, null, '5', '0', '77', '6', '1545494105', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('638', null, null, null, '5', '637', '77', '6', '1545494105', null, null, null, '<p><a class=\"cloze\" href=\"#mce_temp_url#\">Lênin</a>: “Vô sản các dân tộc bị áp bức trên thế giới đoàn kết lại”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('639', null, null, null, '5', '0', '77', '6', '1545494151', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('640', null, null, null, '5', '639', '77', '6', '1545494152', null, null, null, '<p>Câu nói của <a class=\"cloze\" href=\"#mce_temp_url#\">Nguyễn Trãi</a>: “Lấy dân làm gốc”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('641', null, null, null, '5', '0', '77', '6', '1545494198', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('642', null, null, null, '5', '641', '77', '6', '1545494198', null, null, null, '<p>HCM khẳng định, động lực quan trọng và quyết định nhất là <a class=\"cloze\" href=\"#mce_temp_url#\">con người</a>, là nhân dân lao động, nòng cốt là công-nông-tri thức.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('643', null, null, null, '5', '0', '77', '6', '1545494235', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('644', null, null, null, '5', '643', '77', '6', '1545494235', null, null, null, '<p>HCM xem <a class=\"cloze\" href=\"#mce_temp_url#\">con người</a> là động lực của cnxh, hơn nữa là động lực quan trọng nhất.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('645', null, null, null, '5', '0', '77', '6', '1545494257', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('646', null, null, null, '5', '645', '77', '6', '1545494257', null, null, null, '<p>Theo HCM, khi bước vào thời kì quá độ lên cnxh, nước ta có đặc điểm lớn nhất là từ nước nông nghiệp lạc hậu tiến lên cnxh không phải kinh qua giai đoạn phát triển <a class=\"cloze\" href=\"#mce_temp_url#\">tư bản chủ nghĩa</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('647', null, null, null, '5', '0', '77', '6', '1545494291', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('648', null, null, null, '5', '647', '77', '6', '1545494291', null, null, null, '<p>HCM đặc biệt lưu ý đến mâu thuẫn cơ bản của thời kì quá độ , đó là mâu thuẫn giữa <a class=\"cloze\" href=\"#mce_temp_url#\">nhu cầu phát triển cao</a> của đất nước theo xu hướng tiến bộ và thực trạng kinh tế xã hội quá thấp kém của nước ta.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('649', null, null, null, '5', '0', '77', '6', '1545494349', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('650', null, null, null, '5', '649', '77', '6', '1545494349', null, null, null, '<p>Nhiệm vụ lịch sử của thời kì quá độ lên cnxh ở nước ta: Một là, xây dựng nền tảng <a class=\"cloze\" href=\"#mce_temp_url#\">vật chất và kỹ thuật</a> cho cnxh, hai là cải tạo xã hội cũ, xây dựng xã hội mới, kết hợp cải tạo và xây dựng, trong đó lấy xây dựng làm trọng tâm, làm nội dung cốt yếu nhất, chủ chốt, lâu dài.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('651', null, null, null, '5', '0', '77', '6', '1545494385', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('652', null, null, null, '5', '651', '77', '6', '1545494385', null, null, null, '<p>Tính chất phức tạp của khó khăn được Người lí giải: thứ nhất, đây thực sự là cuộc cm làm đảo loạn <a class=\"cloze\" href=\"#mce_temp_url#\">mọi mặt của đời sống</a>,cả lực lượng sản xuất và quan hệ sản xuất, cả cơ sở hạ tầng và kiến trúc thượng tầng. Thứ hai, trong sự nghiệp xây dựng cnxh, Đảng, nhà nước và nhân dân ta chưa có kinh nghiệm nhất là trong lĩnh vực kinh tế. Thứ ba, sự nghiệp xây dựng cnxh ở nước ta luôn bị các thế lực phản động trong và ngoài nước tìm cách chống phá.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('653', null, null, null, '5', '0', '77', '6', '1545494447', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('654', null, null, null, '5', '653', '77', '6', '1545494447', null, null, null, '<p>HCM bước đầu đề cập đến vấn đề khoán trong sản xuất, \" <a class=\"cloze\" href=\"#mce_temp_url#\">Chế độ làm khoán</a> là một điều kiện của cnxh, nó khuyến khích người công nhân luôn luôn tiến bộ, cho nhà máy tiến bộ. Làm khoán là ích chung mà lại lợi riêng...;làm khoán tốt thích hợp công bằng dưới chế độ ta hiện nay\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('655', null, null, null, '5', '0', '77', '6', '1545494528', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('656', null, null, null, '5', '655', '77', '6', '1545494528', null, null, null, '<p>HCM xác định phương châm thực hiện bước đi trong xây dựng cnxh: <a class=\"cloze\" href=\"#mce_temp_url#\">dần dần, thận trọng </a>từng bước một, từ thấp đến cao, không chủ quan, nôn nóng và việc xác định bước đi luôn luôn phải căn cứ vào điều kiện khách quan quy định.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('657', null, null, null, '5', '0', '77', '6', '1545494609', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('658', null, null, null, '5', '657', '77', '6', '1545494609', null, null, null, '<p>Để xác định bước đi và tìm cách làm phù hợp với VN, HCM đề ra hai phương pháp có tính chất phương pháp luận: Một là, xây dựng cnxh là một hiện tượng phổ biến mang tính <a class=\"cloze\" href=\"#mce_temp_url#\">quốc tế</a>, cần quán triệt các nguyên lý cơ bản của chủ nghĩa Mac Lenin về xây dựng chế độ mới, có thể tham khảo học hỏi kinh nghiệm của các nước anh em. Thứ hai, xác định bước đi và biện pháp xây dựng cnxh chủ yếu xuất phát từ điều kiện thực tế, đặc điểm dân tộc, nhu cầu và khả năng thực tế của nhân dân.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('659', null, null, null, '5', '0', '77', '6', '1545494638', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('660', null, null, null, '5', '659', '77', '6', '1545494638', null, null, null, '<p>HCM nhận thức về phương châm \" Tiến nhanh, tiến mạnh, tiến vững chắc lên cnxh\" chứ không phải là <a class=\"cloze\" href=\"#mce_temp_url#\">làm bừa, làm ẩu</a> đốt cháy giai đoạn, chủ quan, duy ý chí.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('661', null, null, null, '5', '0', '77', '6', '1545494664', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('662', null, null, null, '5', '661', '77', '6', '1545494664', null, null, null, '<p>HCM khẳng định: \"CNXH là <a class=\"cloze\" href=\"#mce_temp_url#\">công trình tập thể</a> của nhân dân\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('663', null, null, null, '5', '0', '77', '6', '1545494694', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('664', null, null, null, '5', '663', '77', '6', '1545494694', null, null, null, '<p>Nhiệm vụ quan trọng nhất của thời kỳ quá độ lên CNXH-&gt; xây dựng <a class=\"cloze\" href=\"#mce_temp_url#\">cơ sở vật chất</a> nền tảng xã hội chủ nghĩa</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('665', null, null, null, '5', '0', '77', '6', '1545494741', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('666', null, null, null, '5', '665', '77', '6', '1545494741', null, null, null, '<p>Cầu nối giữa các ngành kinh tế theo tt HCM là: <a class=\"cloze\" href=\"#mce_temp_url#\">Thương nghiệp</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('667', null, null, null, '5', '0', '77', '6', '1545494791', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('668', null, null, null, '5', '667', '77', '6', '1545494791', null, null, null, '<p>“nếu dân tộc hóa mà phát triển đến cực điểm thì tức là đến chỗ <a class=\"cloze\" href=\"#mce_temp_url#\">thế giới hóa</a> nó…”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('669', null, null, null, '5', '0', '77', '6', '1545494818', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('670', null, null, null, '5', '669', '77', '6', '1545494818', null, null, null, '<p>Khi nói đến Đảng phải là <a class=\"cloze\" href=\"#mce_temp_url#\">Đảng cách mệnh</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('671', null, null, null, '5', '0', '77', '6', '1545494882', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('672', null, null, null, '5', '671', '77', '6', '1545494882', null, null, null, '<p><a class=\"cloze\" href=\"#mce_temp_url#\">Nêu gương về đạo đức</a> là một nét đẹp truyền thống văn hóa phương Đông</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('673', null, null, null, '5', '0', '77', '6', '1545494906', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('674', null, null, null, '5', '673', '77', '6', '1545494906', null, null, null, '<p>Xây đi đôi với <a class=\"cloze\" href=\"#mce_temp_url#\">chống</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('675', null, null, null, '5', '0', '77', '6', '1545494928', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('676', null, null, null, '5', '675', '77', '6', '1545494928', null, null, null, '<p>Phải tu dưỡng đạo đức <a class=\"cloze\" href=\"#mce_temp_url#\">suốt đời</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('677', null, null, null, '5', '0', '77', '6', '1545494967', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('678', null, null, null, '5', '677', '77', '6', '1545494967', null, null, null, '<p><a class=\"cloze\" href=\"#mce_temp_url#\">C.Mác</a>: “Trong tính hiện thực của nó, bản chất con người là tổng hòa của các mối quan hệ xã hội”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('679', null, null, null, '5', '0', '77', '6', '1545495023', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('680', null, null, null, '5', '679', '77', '6', '1545495023', null, null, null, '<p>“vô luận việc gì, đều do người làm ra, và từ <a class=\"cloze\" href=\"#mce_temp_url#\">nhỏ đến to</a>, từ gần đến xa, đều thế cả”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('681', null, null, null, '5', '0', '77', '6', '1545495047', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('682', null, null, null, '5', '681', '77', '6', '1545495047', null, null, null, '<p>C.Mác: nhà nước là sản phẩm của những mâu thuẫn giai cấp không thể điều hòa, 2 dấu hiệu nhà nước: <a class=\"cloze\" href=\"#mce_temp_url#\">lãnh thổ và quyền lực công</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('683', null, null, null, '5', '0', '77', '6', '1545495083', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('684', null, null, null, '5', '683', '77', '6', '1545495084', null, null, null, '<p>Lênin: <a class=\"cloze\" href=\"#mce_temp_url#\">chuyên chính vô sản</a> là nhà nước nửa nhà nước, nhà nước công nông binh (thống trị giai cấp, quản lý xã hội)</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('685', null, null, null, '5', '0', '77', '6', '1545495120', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('686', null, null, null, '5', '685', '77', '6', '1545495120', null, null, null, '<p>“Muốn xây dựng chủ nghĩa xã hội, trước hết phải có <a class=\"cloze\" href=\"#mce_temp_url#\">con người xã hội chủ nghĩa</a>”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('687', null, null, null, '5', '0', '77', '6', '1545495148', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('688', null, null, null, '5', '687', '77', '6', '1545495148', null, null, null, '<p>HCM: “trong bầu trời không gì quý bằng nhân dân, trong thế giới không gì mạnh bằng <a class=\"cloze\" href=\"#mce_temp_url#\">lực lượng đoàn kết</a> của nhân dân”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('689', null, null, null, '5', '0', '77', '6', '1545495205', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('690', null, null, null, '5', '689', '77', '6', '1545495205', null, null, null, '<p>Trau dồi đạo đức cách mạng là một việc “<a class=\"cloze\" href=\"#mce_temp_url#\">sung sướng </a>vẻ vang nhất trên đời”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('691', null, null, null, '5', '0', '77', '6', '1545495278', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('692', null, null, null, '5', '691', '77', '6', '1545495278', null, null, null, '<p>Nói đi đôi với làm là <a class=\"cloze\" href=\"#mce_temp_url#\">nguyên tắc quan trọng bậc nhất</a> (tác phẩm “Đường cách mệnh”)</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('693', null, null, null, '5', '0', '77', '6', '1545495445', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('694', null, null, null, '5', '693', '77', '6', '1545495445', null, null, null, '<p>“Văn hóa phục vụ ai? Cố nhiên, chúng ta phải nói là phục vụ <a class=\"cloze\" href=\"#mce_temp_url#\">công nông binh</a>…”; “Quần chúng là những người sáng tạo, công nông là những người sáng tạo…”</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('695', null, null, null, '5', '0', '77', '6', '1545495484', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('696', null, null, null, '5', '695', '77', '6', '1545495484', null, null, null, '<p>\"Trình độ văn hóa của nhân dân nâng cao sẽ giúp cho chúng ta đẩy mạnh công cuộc khôi phục kt, phát triển dân chủ. <a class=\"cloze\" href=\"#mce_temp_url#\">Nâng cao trình độ văn hóa</a> của nhân dân cũng là một việc cần thiết để xây dựng nước ta thành một nước hòa bình, thống nhất, độc lập, dân chủ và giàu mạnh\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('697', null, null, null, '5', '0', '77', '6', '1545495526', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('698', null, null, null, '5', '697', '77', '6', '1545495526', null, null, null, '<p>Kinh tế \"vùng tự do\" gồm mấy thành phần?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">6</a></p>\r\n<p> </p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('699', null, null, null, '5', '0', '77', '6', '1545495583', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('700', null, null, null, '5', '699', '77', '6', '1545495583', null, null, null, '<p>Thành phần kinh tế \"nửa XHCN\"</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">hợp tác xã</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('701', null, null, null, '5', '0', '77', '6', '1545495608', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('702', null, null, null, '5', '701', '77', '6', '1545495608', null, null, null, '<p>Phân phối trong chế độ XHCN là <a class=\"cloze\" href=\"#mce_temp_url#\">theo lao động</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('703', null, null, null, '5', '0', '77', '6', '1545495636', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('704', null, null, null, '5', '703', '77', '6', '1545495636', null, null, null, '<p>Lí tưởng XHCN của Hồ Chí Minh được hình thành khi tiếp cận <a class=\"cloze\" href=\"#mce_temp_url#\">lí luận Mác Lenin</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('705', null, null, null, '5', '0', '77', '6', '1545495661', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('706', null, null, null, '5', '705', '77', '6', '1545495661', null, null, null, '<p>HCM quan niệm xây dựng <a class=\"cloze\" href=\"#mce_temp_url#\">nền kinh tế nhiều thành phần</a> ở nước ta.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('707', null, null, null, '5', '0', '77', '6', '1545495720', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('708', null, null, null, '5', '707', '77', '6', '1545495720', null, null, null, '<p>Nhiệm vụ lịch sử của thời kì quá độ lên chủ nghĩa xã hội ở Việt Nam bao gồm hai nội dung lớn:Một là, xây dựng <a class=\"cloze\" href=\"#mce_temp_url#\">nền tảng vật chất và kỹ thuật </a>cho chủ nghĩa xã hội, xây dựng các tiền đề kinh tế, chính trị, văn hóa, tư tưởng cho chủ nghĩa xã hội.Hai là, <a class=\"cloze\" href=\"#mce_temp_url#\">cải tạo xã hội cũ</a>, xây dựng xã hội mới, kết hợp cải tạo và xây dựng, trong đó lấy xây dựng làm trọng tâm làm nội dung cốt yếu nhất, chủ chốt, lâu dài</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('709', null, null, null, '5', '0', '77', '6', '1545495748', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('710', null, null, null, '5', '709', '77', '6', '1545495748', null, null, null, '<p>Động lực quan trọng nhất trong công cuộc xây dựng đất nước là <a class=\"cloze\" href=\"#mce_temp_url#\">con người.</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('711', null, null, null, '5', '0', '79', '6', '1545496649', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('712', null, null, null, '5', '711', '79', '6', '1545496649', null, null, null, '<p>Đảng ta mang bản chất <a class=\"cloze\" href=\"#mce_temp_url#\">giai cấp công nhân</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('713', null, null, null, '5', '0', '79', '6', '1545496679', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('714', null, null, null, '5', '713', '79', '6', '1545496679', null, null, null, '<p>Đảng Cộng sản VN là sự kết hợp của <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa Mác Lênin, phong trào công nhân, phong trào yêu nước</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('715', null, null, null, '5', '0', '79', '6', '1545496723', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('716', null, null, null, '5', '715', '79', '6', '1545496723', null, null, null, '<p>Phát triển sáng tạo về ĐCS trong tư tưởng HCM</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">Đảng của giai cấp vô sản, của dân tộc</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('717', null, null, null, '5', '0', '79', '6', '1545496745', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('718', null, null, null, '5', '717', '79', '6', '1545496745', null, null, null, '<p>Nguyên tắc sinh hoạt đảng: <a class=\"cloze\" href=\"#mce_temp_url#\">phê bình và tự phê bình</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('719', null, null, null, '5', '0', '79', '6', '1545496769', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('720', null, null, null, '5', '719', '79', '6', '1545496769', null, null, null, '<p>Nguyên tắc lãnh đạo của đảng: <a class=\"cloze\" href=\"#mce_temp_url#\">tập thể lãnh đạo cá nhân phụ trách</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('721', null, null, null, '5', '0', '79', '6', '1545496821', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('722', null, null, null, '5', '721', '79', '6', '1545496821', null, null, null, '<p>Theo HCM, khi so sánh với phong trào công nhân, phong trào yêu nước Việt Nam có ưu điểm hơn gì?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">có nhiều tầng lớp tham gia</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('723', null, null, null, '5', '0', '79', '6', '1545497075', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('724', null, null, null, '5', '723', '79', '6', '1545497075', null, null, null, '<p>“Trong giai đoạn này quyển lợi của giai cấp công nhân và nhân dân lao động và của dân tộc là một. Chính vì Đảng Lao động VN là của <a class=\"cloze\" href=\"#mce_temp_url#\">giai cấp công nhân và nhân dân lao động</a> nên nó phải là Đảng của Dân tộc VN\" thuộc bản chất của Đảng Cộng sản VN.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('725', null, null, null, '5', '0', '79', '6', '1545497099', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('726', null, null, null, '5', '725', '79', '6', '1545497099', null, null, null, '<p>Xây dựng chính trị thì xây dựng đường lối chính trị là <a class=\"cloze\" href=\"#mce_temp_url#\">vấn đề cốt tử</a> .</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('727', null, null, null, '5', '0', '79', '6', '1545497137', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('728', null, null, null, '5', '727', '79', '6', '1545497137', null, null, null, '<p>Quốc tế Cộng sản( Quốc tế thứ ba) ra đời thời gian nào?</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">3/1919</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('729', null, null, null, '5', '0', '79', '6', '1545497165', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('730', null, null, null, '5', '729', '79', '6', '1545497165', null, null, null, '<p>HCM khẳng định \"một Đảng mà giấu giếm khuyết điểm là một Đảng hỏng...\"</p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">Tác phẩm Sửa lối làm việc</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('731', null, null, null, '5', '0', '79', '6', '1545497214', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('732', null, null, null, '5', '731', '79', '6', '1545497214', null, null, null, '<p>Câu \"Đoàn kết toàn dân phụng sự tổ quốc\" </p>\r\n<p>Trả lời: <a class=\"cloze\" href=\"#mce_temp_url#\">là của HCM trong Lời kết thúc của buổi ra mắt Đảng Lao động VN ngày 3/3/1951</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('733', null, null, null, '5', '0', '79', '6', '1545497263', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('734', null, null, null, '5', '733', '79', '6', '1545497263', null, null, null, '<p>“Đảng ta là một <a class=\"cloze\" href=\"#mce_temp_url#\">Đảng cầm quyền</a>. Mỗi đảng viên và cán bộ phải thật sự thấm nhuần <a class=\"cloze\" href=\"#mce_temp_url#\">đạo đức cách mạng</a>, thật sự cần kiệm liêm chính, chí công vô tư.” Trích trong tác phẩm <a class=\"cloze\" href=\"#mce_temp_url#\">Di chúc</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('735', null, null, null, '5', '0', '79', '6', '1545497319', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('736', null, null, null, '5', '735', '79', '6', '1545497319', null, null, null, '<p>Đại hội Đảng toàn quốc lần thứ III vào <a class=\"cloze\" href=\"#mce_temp_url#\">9/1960</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('737', null, null, null, '5', '0', '79', '6', '1545497356', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('738', null, null, null, '5', '737', '79', '6', '1545497356', null, null, null, '<p>Trong lễ thành lập ĐCSVN có đại diện của <a class=\"cloze\" href=\"#mce_temp_url#\">Đông Dương Cộng sản Đảng, An Nam Cộng sản Đảng</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('739', null, null, null, '5', '0', '79', '6', '1545497390', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('740', null, null, null, '5', '739', '79', '6', '1545497390', null, null, null, '<p>Cách mạng trước hết phải có: <a class=\"cloze\" href=\"#mce_temp_url#\">Đảng cách mệnh</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('741', null, null, null, '5', '0', '79', '6', '1545497416', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('742', null, null, null, '5', '741', '79', '6', '1545497416', null, null, null, '<p><a class=\"cloze\" href=\"#mce_temp_url#\">Đảng cầm quyền</a> vừa là người lãnh đạo, vừa là đầy tớ trung thành của nhân dân.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('743', null, null, null, '5', '0', '79', '6', '1545497456', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('744', null, null, null, '5', '743', '79', '6', '1545497456', null, null, null, '<p>Với Người, xây dựng Đảng là một nhiệm vụ <a class=\"cloze\" href=\"#mce_temp_url#\">tất yếu</a>, thường xuyên để Đảng hoàn thành vai trò tiên phong trước <a class=\"cloze\" href=\"#mce_temp_url#\">giai cấp, dân tộc và nhân dân.</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('745', null, null, null, '5', '0', '79', '6', '1545497501', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('746', null, null, null, '5', '745', '79', '6', '1545497501', null, null, null, '<p>Các nguyên tắc tổ chức sinh hoạt Đảng: <a class=\"cloze\" href=\"#mce_temp_url#\">tập trung dân chủ</a>, tập thể lãnh đạo, cá nhân phụ trách, <a class=\"cloze\" href=\"#mce_temp_url#\">tự phê bình và phê bình</a>, kỉ luật nghiêm minh tự giác, đoàn kết thống nhất trong Đảng.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('747', null, null, null, '5', '0', '81', '6', '1545497615', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('748', null, null, null, '5', '747', '81', '6', '1545497615', null, null, null, '<p>Trong tư tưởng HCM, đại đoàn kết dân tộc là vấn đề có ý nghĩa <a class=\"cloze\" href=\"#mce_temp_url#\">chiến lược</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">cơ bản</a>, nhất quán, lâu dài, xuyên suốt tiến trình cm.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('749', null, null, null, '5', '0', '81', '6', '1545497666', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('750', null, null, null, '5', '749', '81', '6', '1545497666', null, null, null, '<p>Mặt trận dân tộc thống nhất phải được xây dựng trên nền tảng khối liên minh <a class=\"cloze\" href=\"#mce_temp_url#\">công - nông - trí thức</a>, đặt dưới dự lãnh đạo của Đảng, đây là <a class=\"cloze\" href=\"#mce_temp_url#\">nguyên tắc cốt lõi.</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('751', null, null, null, '5', '0', '81', '6', '1545497724', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('752', null, null, null, '5', '751', '81', '6', '1545497724', null, null, null, '<p>Người chỉ rõ phải lấy liên minh công-nông làm nền tảng \"Vì họ là người trực tiếp sản xuất tất cả mọi tài phú làm cho xã hội sống. Vì họ đông hơn hết mà cũng bị áp bức bóc lột nặng nề hơn hết. Vì chí khí cm của họ chắc chắn bền bỉ hơn mọi tầng lớp khác.\"</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('753', null, null, null, '5', '0', '81', '6', '1545497856', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('754', null, null, null, '5', '753', '81', '6', '1545497856', null, null, null, '<p>Mặt trận dân tộc thống nhất phải hoạt động trên cơ sở đảm bảo <a class=\"cloze\" href=\"#mce_temp_url#\">lợi ích tối cao</a> của dân tộc,<a class=\"cloze\" href=\"#mce_temp_url#\"> quyền lợi cơ bản</a> của các tầng lớp nhân dân.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('755', null, null, null, '5', '0', '81', '6', '1545497881', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('756', null, null, null, '5', '755', '81', '6', '1545497881', null, null, null, '<p>Mặt trận dân tộc thống nhất phải hoạt độn theo <a class=\"cloze\" href=\"#mce_temp_url#\">nguyên tắc hiệp thương dân chủ</a>, đảm bảo đoàn kết ngày càng rộng rãi và bền vững.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('757', null, null, null, '5', '0', '81', '6', '1545497913', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('758', null, null, null, '5', '757', '81', '6', '1545497913', null, null, null, '<p>Mặt trận dân tộc thống nhất là <a class=\"cloze\" href=\"#mce_temp_url#\">khối đại đoàn kết</a> lâu dài, đoàn kết thực sự, chân thành, thân ái giúp đỡ lẫn nhau cùng tiến bộ.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('759', null, null, null, '5', '0', '81', '6', '1545497938', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('760', null, null, null, '5', '759', '81', '6', '1545497938', null, null, null, '<p><a class=\"cloze\" href=\"#mce_temp_url#\">Vai trò của đoàn kết quốc tế</a>: thực hiện đoàn kết quốc tế nhằm kết hợp sức mạnh dân tộc với sức mạnh thời đại, tạo sức mạnh tổng hợp cho cm VN; thực hiện đoàn kết quốc tế nhằm góp phần cùng nhân dân thế giới thực hiện thắng lợi các mụa tiêu cm vủa thời đại.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('761', null, null, null, '5', '0', '81', '6', '1545497984', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('762', null, null, null, '5', '761', '81', '6', '1545497984', null, null, null, '<p>Với phong trào cộng sản và công nhân thế giới- lực lượng nòng cốt của đoàn kết quốc tế, HCM cho rằng, sự đoàn kết giữa <a class=\"cloze\" href=\"#mce_temp_url#\">giai cấp vô sản</a> thế giới là một đảm bảo vững chắc cho <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa cộng sản</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('763', null, null, null, '5', '0', '81', '6', '1545498011', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('764', null, null, null, '5', '763', '81', '6', '1545498011', null, null, null, '<p>Đại đoàn kết dân tộc phải được xây dựng trên nền tảng <a class=\"cloze\" href=\"#mce_temp_url#\">liên minh công nông - lao động trí óc</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('765', null, null, null, '5', '0', '81', '6', '1545498113', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('766', null, null, null, '5', '765', '81', '6', '1545498113', null, null, null, '<p>“<a class=\"cloze\" href=\"#mce_temp_url#\">Đoàn kết thực sự</a> nghĩa là mục đích phải nhất trí và lập trường cũng phải nhất trí. Đoàn kết thực sự nghĩa là <a class=\"cloze\" href=\"#mce_temp_url#\">vừa đoàn kết vừa đấu tranh</a>, học những cái tốt của nhau, phê bình những cái sai của nhau và phê bình trên lập trường thân ái, vì nước, vì dân”.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('767', null, null, null, '5', '0', '81', '6', '1545498140', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('768', null, null, null, '5', '767', '81', '6', '1545498140', null, null, null, '<p>Hồ chí Minh coi cái nền, cái gốc của đại đoàn kết là <a class=\"cloze\" href=\"#mce_temp_url#\">đại đoàn kết toàn dân</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('769', null, null, null, '5', '0', '81', '6', '1545498176', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('770', null, null, null, '5', '769', '81', '6', '1545498176', null, null, null, '<p>Xây dựng đường lối thuộc nội dung công tác <a class=\"cloze\" href=\"#mce_temp_url#\">xây dựng chính trị.</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('771', null, null, null, '5', '0', '81', '6', '1545498262', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('772', null, null, null, '5', '771', '81', '6', '1545498262', null, null, null, '<p>\"Đoàn kết đoàn kết đại đoàn kết. thành công thành công đại thành công “ </p>\r\n<p>Trả lời: <a class=\"clozetip\" title=\"thời gian\" href=\"#\">3/1951.</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('773', null, null, null, '5', '0', '81', '6', '1545498307', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('774', null, null, null, '5', '773', '81', '6', '1545498307', null, null, null, '<p>Trong kháng chiến chống chủ nghĩa đế quốc thực dân, tư tưởng Hồ Chí Minh đã định hướng cho việc hình thành ba tầng mặt trận: <a class=\"cloze\" href=\"#mce_temp_url#\">Mặt trận Việt-Miên-Lào, Mặt trận Á Phi và nhân thế giới</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('775', null, null, null, '5', '0', '81', '6', '1545498335', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('776', null, null, null, '5', '775', '81', '6', '1545498335', null, null, null, '<p>Cái nền cái gốc của đại đoàn kết là <a class=\"cloze\" href=\"#mce_temp_url#\">công nhân, nông dân</a> và các tầng lớp lao động khác.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('777', null, null, null, '5', '0', '81', '6', '1545498368', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('778', null, null, null, '5', '777', '81', '6', '1545498368', null, null, null, '<p>\"Ta phải nhận rằng, đã là con Lạc, cháu Hồng thì ai cũng có ít hay nhiều <a class=\"cloze\" href=\"#mce_temp_url#\">lòng ái quốc</a>\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('779', null, null, null, '5', '777', '81', '6', '1545498368', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('780', null, null, null, '5', '0', '83', '6', '1545498443', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('781', null, null, null, '5', '780', '83', '6', '1545498443', null, null, null, '<p>Trong tác phẩm Thường thức chính trị viết năm <a class=\"cloze\" href=\"#mce_temp_url#\">1953</a>, HCM chỉ rõ: \"Ở nước ta chính quyền là của dân, do nhân dân làm chủ...thế là dân chủ\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('782', null, null, null, '5', '0', '83', '6', '1545498544', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('783', null, null, null, '5', '782', '83', '6', '1545498544', null, null, null, '<p>Lời nói đầu của bản hiến pháp <a class=\"cloze\" href=\"#mce_temp_url#\">1959</a> khẳng định: \"Nhà nước của ta là nhà nước dân chủ nhân dân, dựa trên nền tảng liên minh công nông, do giai cấp công nhân lãnh đạo\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('784', null, null, null, '5', '0', '83', '6', '1545498586', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('785', null, null, null, '5', '784', '83', '6', '1545498586', null, null, null, '<p>Trong bản <a class=\"cloze\" href=\"#mce_temp_url#\">Yêu sách</a>:\" Hai xin phép luật sửa sang... Bảy xin hiếp pháp ban hành, Trăm đều phải có <a class=\"cloze\" href=\"#mce_temp_url#\">thần linh</a> pháp quyền\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('786', null, null, null, '5', '0', '83', '6', '1545498630', '1545502705', null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('787', null, null, null, '5', '786', '83', '6', '1545498630', '1545502705', null, null, '<p>Người coi <a class=\"cloze\" href=\"#mce_temp_url#\">cán bộ</a> nói chung là cái gốc của mọi công việc, \"muôn việc <a class=\"cloze\" href=\"#mce_temp_url#\">thành công hay thất bại</a> đều do cán bộ tốt hay kém\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('788', null, null, null, '5', '0', '83', '6', '1545502746', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('789', null, null, null, '5', '788', '83', '6', '1545502746', null, null, null, '<p>Cán bộ phải: một là, <a class=\"cloze\" href=\"#mce_temp_url#\">tuyệt đối trung thành</a> với cm, hai là <a class=\"cloze\" href=\"#mce_temp_url#\">hăng hái thành thạo</a> công việc, giỏi chuyên môn nghiệp vụ, ba là phải có <a class=\"cloze\" href=\"#mce_temp_url#\">mối quan hệ</a> mật thiết với nhân dân, bốn là cán bộ công chức phải là những người giám phụ trách, dám quyết đoán,dám chịu trách nhiệm, nhất là trong tình huống khó khăn, thắng kho kiêu, bại không nản, năm là phải thường xuyên tự phê bình và phê bình, luôn luôn có ý thức hành động vì sự lớn mạnh, trong sạch của nhà nước.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('790', null, null, null, '5', '0', '83', '6', '1545502776', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('791', null, null, null, '5', '790', '83', '6', '1545502776', null, null, null, '<p>Quan điểm của HCM là:\" Tham ô lãng phí và bện quan liêu, dù cố ý hay không cũng là <a class=\"cloze\" href=\"#mce_temp_url#\">bạn đồng minh</a> của thực dân và phong kiến... Tội lỗi ấy cũng nặng như tội <a class=\"cloze\" href=\"#mce_temp_url#\">Việt gian</a>, mật thám\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('792', null, null, null, '5', '0', '83', '6', '1545502810', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('793', null, null, null, '5', '792', '83', '6', '1545502810', null, null, null, '<p>Nhà nước <a class=\"cloze\" href=\"#mce_temp_url#\">vì dân</a> là một nhà nước lấy <a class=\"cloze\" href=\"#mce_temp_url#\">lợi ích</a> chính đáng của nhân dân làm mục tiêu, tất cả đều vì lợi ích của dân, ngoài ra không có bất cứ một lợi ích nào khác . Đó là một nhà nước trong sạch, không có bất cứ  môt đặc quyền đặc lợi nào.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('794', null, null, null, '5', '0', '83', '6', '1545503018', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('795', null, null, null, '5', '794', '83', '6', '1545503018', null, null, null, '<p>Bạn đồng minh của thực dân và phong kiến : bệnh <a class=\"clozetip\" title=\"6 từ\" href=\"#\">quan liêu tham ô lãng phí</a></p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('796', null, null, null, '5', '0', '83', '6', '1545503071', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('797', null, null, null, '5', '796', '83', '6', '1545503071', null, null, null, '<p>Ai là người đầu tiên đưa ra tư tưởng Nhà nước của dân, do dân, vì dân?</p>\r\n<p>Trả lời: Tổng thống Mĩ Abraham <a class=\"cloze\" href=\"#mce_temp_url#\">Lincoln</a> </p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('798', null, null, null, '5', '0', '83', '6', '1545503122', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('799', null, null, null, '5', '798', '83', '6', '1545503122', null, null, null, '<p>Bệnh công thần là quan chức chỉ <a class=\"cloze\" href=\"#mce_temp_url#\">hưởng lợi</a> chứ không làm việc vì dân, ko vì dân phục vụ mà chỉ dùng <a class=\"cloze\" href=\"#mce_temp_url#\">quyền lực</a> để đục khoét nhân dân.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('800', null, null, null, '5', '0', '83', '6', '1545503153', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('801', null, null, null, '5', '800', '83', '6', '1545503153', null, null, null, '<p>Bệnh hẹp hòi là bệnh tỏ ra ở chỗ đối với người ngoài Đảng nhiều khi quá <a class=\"cloze\" href=\"#mce_temp_url#\">khắt khe</a>, hoặc <a class=\"cloze\" href=\"#mce_temp_url#\">phớt</a> người ta đi, không chịu bàn bạc, hỏi han ý kiến.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('802', null, null, null, '5', '0', '83', '6', '1545503173', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('803', null, null, null, '5', '802', '83', '6', '1545503173', null, null, null, '<p>Bệnh quan liêu tỏ ra ở chỗ thích <a class=\"cloze\" href=\"#mce_temp_url#\">giấy tờ</a>, xa <a class=\"cloze\" href=\"#mce_temp_url#\">quần chúng</a>, không điều tra nghiên cứu, không kiểm tra theo dõi việc thi hành, không học tập kinh nghiệm của quần chúng.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('804', null, null, null, '5', '0', '83', '6', '1545503226', '1545503237', null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('805', null, null, null, '5', '804', '83', '6', '1545503226', '1545503237', null, null, '<p>Bệnh quan niêu làm chúng ta chỉ biết khai <a class=\"cloze\" href=\"#mce_temp_url#\">hội nghị</a>, viết <a class=\"cloze\" href=\"#mce_temp_url#\">chỉ thị</a>, xem báo cáo trên giấy chứ không kiểm tra đến nơi đến chốn.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('806', null, null, null, '5', '0', '83', '6', '1545503263', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('807', null, null, null, '5', '806', '83', '6', '1545503263', null, null, null, '<p>Bệnh quan liêu đã <a class=\"cloze\" href=\"#mce_temp_url#\">ấp ủ,</a> <a class=\"cloze\" href=\"#mce_temp_url#\">dung túng</a>, che chở cho nạn tham ô, lãng phí.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('808', null, null, null, '5', '0', '83', '6', '1545503282', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('809', null, null, null, '5', '808', '83', '6', '1545503282', null, null, null, '<p>Muốn trừ sạch bệnh <a class=\"cloze\" href=\"#mce_temp_url#\">tham ô</a>, lăng phí, thì trước hết phải tẩy sạch bệnh <a class=\"cloze\" href=\"#mce_temp_url#\">quan liêu</a>.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('810', null, null, null, '5', '0', '83', '6', '1545503373', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('811', null, null, null, '5', '810', '83', '6', '1545503373', null, null, null, '<p>Năm điểm lớn định hướng cho việc xây dựng nền văn hóa dân tộc: 1. xây dựng tâm lý: tinh thần <a class=\"cloze\" href=\"#mce_temp_url#\">độc lập</a> tự cường, 2. xây dựng luân lý: biết <a class=\"cloze\" href=\"#mce_temp_url#\">hy sinh</a> mình, làm lợi cho quần chúng, 3. xây dựng xã hội: mọi sự việc có liên quan đến phúc lới xã hội của nhân dân trong đời sống, 4. xây dựng chính trị:dân quyền, 5. xây dựng kinh tế.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('812', null, null, null, '5', '0', '83', '6', '1545503409', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('813', null, null, null, '5', '812', '83', '6', '1545503409', null, null, null, '<p>HCM nói:\" Xã hội thế nào thì <a class=\"cloze\" href=\"#mce_temp_url#\">văn nghệ</a> thế ấy... Dưới chế độ thực dân phong kiến, nhân dân ta bị <a class=\"cloze\" href=\"#mce_temp_url#\">nô lệ</a>, thì văn nghệ cũng bị nô lệ, bị tồi tàn, không thể phát triển được\".</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('814', null, null, null, '5', '0', '83', '6', '1545503533', null, null, null, null, null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('815', null, null, null, '5', '814', '83', '6', '1545503533', null, null, null, '<p>Để văn hóa phát triển tự do thì phải làm <a class=\"cloze\" href=\"#mce_temp_url#\">cách mạng chính trị</a> trước.</p>', null, '35', null, null, null, null, null);
INSERT INTO `question` VALUES ('816', '<p>C&acirc;u 1.1 Kinh tế tự cung tự cấp <math xmlns=\"http://www.w3.org/1998/Math/MathML\"><msqrt><mi>y</mi></msqrt></math>?</p>', null, null, '2', '0', '69', '1', '1545667892', '1547111907', null, null, null, null, '36', null, null, null, null, null);
INSERT INTO `question` VALUES ('817', '<p><math xmlns=\"http://www.w3.org/1998/Math/MathML\"><msub><mi>y</mi><mi>y</mi></msub></math>&nbsp;Kh&aacute;i niệm ?</p>', null, null, '1', '0', '69', '1', '1545667892', '1547112033', 'kiểu tổ chức kinh tế ,sản phẩm , do lao động ,thỏa mãn nhu cầu người sản xuất', 'Là kiểu tổ chức KT mà sản phẩm do lao động tạo ra để thỏa mãn nhu cầu trực tiếp của người sản xuất', null, '', '36', null, null, null, null, null);
INSERT INTO `question` VALUES ('818', 'Trình độ phát triển của lực lượng sản xuất ?', null, null, '1', '0', '69', '1', '1545667892', null, 'thấp , khép kín , lệ thuộc , hạn chế phát triển ', 'Trình độ thấp, khép kín,lệ thuộc vào tự nhiên,hạn chế sản xuất phát triển', '', '', '36', null, null, null, null, null);
INSERT INTO `question` VALUES ('819', 'Quy mô sản xuất ?', null, null, '1', '0', '69', '1', '1545667892', null, 'nhỏ', 'Nhỏ (chỉ đủ cung cấp cho 1 nhóm cá nhân)', '', '', '36', null, null, null, null, null);
INSERT INTO `question` VALUES ('820', 'Ngành sản xuất chính ?', null, null, '1', '0', '69', '1', '1545667892', null, 'Săn bắt, hái lượm, nông nghiệp sản xuất nhỏ', 'Săn bắt, hái lượm, nông nghiệp sản xuất nhỏ', '', '', '36', null, null, null, null, null);
INSERT INTO `question` VALUES ('821', 'Khái niệm hàng hóa ? Hai thuộc tính của hàng hóa ? Đặc trưng, vai trò, mối quan hệ giữa hai thuộc tính của hàng hóa ?', null, null, '2', '0', '69', '1', '1545667892', null, null, null, null, null, '36', null, null, null, null, null);
INSERT INTO `question` VALUES ('822', 'Đặc trưng, vai trò, mối quan hệ giữa hai thuộc tính của hàng hóa ?', null, null, '2', '0', '69', '1', '1545667892', null, null, null, null, null, '36', null, null, null, null, null);
INSERT INTO `question` VALUES ('823', '', null, null, '1', '0', '69', '1', '1545667892', null, 'Lao động cụ thể , lao động trừu tượng ', 'Sở dĩ hàng hóa có hai thuộc tính GT và GTSD là vì: lao động của người sản xuất ra hàng hóa có tính 2 mặt: lao động cụ thể và lao động trừu tượng', 'Vì sao hàng hóa có 2 thuộc tính ?', '', '36', null, null, null, null, null);
INSERT INTO `question` VALUES ('824', '', null, null, '1', '0', '69', '1', '1545667892', null, 'nguồn gốc , giá trị sử dụng của hàng hóa ', 'là nguồn gốc của giá trị sử dụng của hàng hóa', 'Vai trò của lao động cụ thể ?', '', '36', null, null, null, null, null);
