INSERT INTO `question` VALUES ('827', ' Câu hỏi cho flashcard', '', '', '1', '0', '85', '6', '1546678107', null, ' Gợi ý mặt trước (front hint)', ' Gợi ý mặt sau (back hint)', ' Câu hỏi mặt trước (front)', ' Câu hỏi mặt sau (back)', '37', null, null, null, null, null);
