INSERT INTO `question` VALUES ('831', null, null, null, '3', '829', '85', '6', '1546678655', '1546678674', 'Gợi ý cho đáp án 2', null, 'Câu hỏi cho điền từ ngắn ___ 2 ?', null, '37', null, null, null, null, null);
INSERT INTO `question` VALUES ('832', null, null, null, '3', '829', '85', '6', '1546678655', '1546678674', 'Gợi ý cho đáp án 3', null, 'Câu hỏi cho điền từ ngắn ___ 3 ?', null, '37', null, null, null, null, null);
