INSERT INTO `question` VALUES ('1067', 'Quan điểm HCM “Con đường CMGPDT phải được tiến hành bằng con đường CM bạo lực”. Liên hệ VN?', null, null, '2', '0', '241', '45', '1551448327', '1552638627', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1068', 'Quan điểm HCM “Con đường CMGPDT phải được tiến hành bằng con đường CM bạo lực”. Liên hệ VN?', null, null, '5', '0', '241', '45', '1551448430', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1069', null, null, null, '5', '1068', '241', '45', '1551448430', null, null, null, '<p>-    Tính tất yếu của bạo lực CM:</p>\r\n<p>+  <strong><span style=\"text-decoration: underline;\">Kẻ thù sử dụng <a class=\"cloze\" href=\"#mce_temp_url#\">bạo lực</a> để xâm lược</span></strong> đất nước ta nên con đường để giành và giữ được độc lập DT chỉ có thể là CM bạo lực</p>\r\n<p>+  <strong><span style=\"text-decoration: underline;\">Bạo lực CM là bạo lực của <a class=\"cloze\" href=\"#mce_temp_url#\">quần chúng</a></span></strong> bao gồm: lực lượng chính trị quần chúng và lực lượng vũ trang ND, tùy từng điều kiện mà sử dụng hình thức đấu tranh phù hợp</p>\r\n<p>-    Bạo lực CM gắn bó hữu cơ với nhân đạo hòa bình:</p>\r\n<p>+  HCM luôn <strong><span style=\"text-decoration: underline;\">tranh thủ</span></strong> khả năng <strong><span style=\"text-decoration: underline;\">giải quyết xung đột</span></strong> bằng <a class=\"cloze\" href=\"#mce_temp_url#\"><strong><span style=\"text-decoration: underline;\">đàm phán</span></strong></a> hòa bình</p>\r\n<p>+  <a class=\"cloze\" href=\"#mce_temp_url#\"><strong><span style=\"text-decoration: underline;\">Chiến tranh</span></strong></a> chỉ là <strong><span style=\"text-decoration: underline;\">giải pháp</span></strong> bắt buộc <strong><span style=\"text-decoration: underline;\">cuối cùng</span></strong></p>\r\n<p>+  <strong><span style=\"text-decoration: underline;\">Kết hợp</span></strong> giành thắng lợi <strong><span style=\"text-decoration: underline;\">quân sự </span></strong>với giải pháp <a class=\"cloze\" href=\"#mce_temp_url#\"><strong><span style=\"text-decoration: underline;\">ngoại giao</span></strong></a> để kết thúc chiến tranh</p>\r\n<p>-    Hình thái của bao lực CM:</p>\r\n<p>+  <strong><span style=\"text-decoration: underline;\">Khởi nghĩa <a class=\"cloze\" href=\"#mce_temp_url#\">toàn dân</a> và chiến tranh ND</span></strong> là nét đặc sắc trong TTHCM</p>\r\n<p>+  Kết hợp các hình thái của bạo lực CM, quân sự, ngoại giao, KT, VH, tư tưởng</p>\r\n<p>+  <strong><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">Tự lực cánh sinh</a>,</span></strong> độc lập tự chủ, tranh thủ sự giúp đỡ của bạn bè quốc tế</p>\r\n<p>-    Liên hệ đến CMVN: Các cuộc kháng chiến như CMT8 (1945), kháng chiến chống Pháp, kháng chiến chỗng Mỹ, tư tưởng bạo lực CM được thể hiện:</p>\r\n<p>+  Kết hợp nhiều lực lượng chính trị quần chúng vũ trang ND</p>\r\n<p>+  Đấu tranh trên tất cả mặt trận: đường không, đường thủy, đường bộ</p>\r\n<p>+  Tư tưởng nhân đạo hòa bình</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1070', 'Cách tiếp cận của HCM đối với vấn đề DT? ND của độc lập DT?', null, null, '2', '0', '242', '45', '1551449125', '1552638734', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1071', 'Cách tiếp cận của HCM đối với vấn đề DT? ND của độc lập DT?', null, null, '5', '0', '242', '45', '1551449260', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1072', null, null, null, '5', '1071', '242', '45', '1551449260', null, null, null, '<p>-    Cách tiếp cận của HCM: từ quyền con người được nêu trong:</p>\r\n<p>+  <strong><span style=\"text-decoration: underline;\">Tuyên ngôn</span></strong> độc lập <strong><span style=\"text-decoration: underline;\">Mỹ</span></strong> 1976: tất cả mọi người sinh ra đều có <strong><span style=\"text-decoration: underline;\">quyền <a class=\"cloze\" href=\"#mce_temp_url#\">bình đẳng</a></span></strong></p>\r\n<p>+  <strong><span style=\"text-decoration: underline;\">Tuyên ngôn </span></strong>nhân quyền và dân quyền <strong><span style=\"text-decoration: underline;\">Pháp</span></strong> 1791: người ta sinh ra được tự do và bình đẳng về quyền lợi, và phải <strong><span style=\"text-decoration: underline;\">luôn luôn được <a class=\"cloze\" href=\"#mce_temp_url#\">tự do</a> và bình đẳng về quyền lợi</span></strong></p>\r\n<p>=&gt;HCM đã khái quát lên thành vấn đề DT: tất cả các DT sinh ra trên TG đều có quyền bình đẳng, <strong><span style=\"text-decoration: underline;\">DT nào cũng có <a class=\"cloze\" href=\"#mce_temp_url#\">quyền sống</a>, quyền sung sướng, quyền tự do</span></strong></p>\r\n<p>-    ND của độc lập DT:</p>\r\n<p>+  Độc lập DT là <a class=\"cloze\" href=\"#mce_temp_url#\">khát vọng</a> lớn nhất của các DT thuộc địa</p>\r\n<ul>\r\n<li>1919, NAQ đã gửi đến Hội nghị Véc xây bản Yêu sách của Nhân dân An Nam đòi CP pháp thừa nhận các quyền dân chủ tự do, bình đẳng của ND VN.</li>\r\n<li>CMT8 thành công, Người thay mặt chính phủ lâm thời đọc tuyên ngôn độc lập, long trọng khẳng định trước toàn TG.</li>\r\n</ul>\r\n<p>+  Độc lập DT phải là một nền độc lập <a class=\"cloze\" href=\"#mce_temp_url#\">thực sự</a>, hoàn toàn gắn với hòa bình, thống nhất, toàn vẹn lãnh thổ</p>\r\n<p>+  Độc lập DT phải mang lại <a class=\"cloze\" href=\"#mce_temp_url#\">cơm no áo ấm</a> cho ND</p>\r\n<p>+  Độc lập, tự do là <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu</a> chiến đấu, là nguồn sức mạnh làm nên chiến thắng của DTVN trong thế kỉ XX, là khát vọng trong suốt cuộc đời CM của HCM</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1073', 'Thực chất vấn đề DT thuộc địa trong TTHCM? Độc lập trong TTHCM gồm những ND nào?', null, null, '2', '0', '243', '45', '1551450829', '1552638786', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1074', 'Thực chất vấn đề DT thuộc địa trong TTHCM? Độc lập trong TTHCM gồm những ND nào?', null, null, '5', '0', '243', '45', '1551450989', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1075', null, null, null, '5', '1074', '243', '45', '1551450989', null, null, null, '<p>-    Thực chất vấn đề DT thuộc địa trong TTHCM:</p>\r\n<p>+  Đấu tranh chống <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa thực dân</a>, GPDT, <strong><span style=\"text-decoration: underline;\">giành độc lập DT</span></strong>, thực hiện quyền DT tự quyết, thành lập Nhà nước độc lập</p>\r\n<p>+  Lựa chọn <strong><span style=\"text-decoration: underline;\">con đường phát triển DT</span></strong> trong bối cảnh thời đại mới là <strong><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa xã hội</a><br /></span></strong></p>\r\n<p>+  Cuộc đấu tranh GPDT dẫn đến sự thức tỉnh DT</p>\r\n<p>=&gt; Hình thành nên các quốc gia độc lập DT</p>\r\n<p>-    ND độc lập DT trong TTHCM:</p>\r\n<p>+  Độc lập DT là khát vọng <a class=\"cloze\" href=\"#mce_temp_url#\">lớn nhất</a> của toàn DT thuộc địa</p>\r\n<ul>\r\n<li>1919, NAQ đã gửi đến Hội nghị <a class=\"cloze\" href=\"#mce_temp_url#\">Véc xây</a> bản Yêu sách của Nhân dân An Nam đòi CP pháp thừa nhận các quyền dân chủ tự do, bình đẳng của ND VN.</li>\r\n<li>CMT8 thành công, Người thay mặt chính phủ lâm thời đọc tuyên ngôn độc lập, long trọng khẳng định trc toàn TG.</li>\r\n</ul>\r\n<p>+  Độc lập DT phải là một nền độc lập thực sự, hoàn toàn gắn với hòa bình, thống nhất toàn vẹn lãnh thổ</p>\r\n<p>+  Độc lập DT phải mang lại cơm no áo ấm cho người dân</p>\r\n<p>+  Độc lập, tự do là mục tiêu chiến đấu, là nguồn sức mạnh làm nên chiến thắng của DTVN trong thế kí XX, là khát vọng suốt cuộc đời CM của HCM</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1076', 'Mối quan hệ giữa vấn đề DT và GC? Tại sao HCM lại quan niệm GPDT là tiền đề GPGC?', null, null, '2', '0', '244', '45', '1551452580', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1077', 'Mối quan hệ giữa vấn đề DT và GC', null, null, '5', '0', '244', '45', '1551452733', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1078', null, null, null, '5', '1077', '244', '45', '1551452733', null, null, null, '<p>-    Vấn đề DT và vấn đề GC có mối quan hệ chặt chẽ với nhau:</p>\r\n<p>+  HCM <strong><span style=\"text-decoration: underline;\">luôn đứng trên vấn đề <a class=\"cloze\" href=\"#mce_temp_url#\">giai cấp</a></span></strong> để nhận thức và giải quyết vấn đề DT</p>\r\n<p>+  HCM <strong><span style=\"text-decoration: underline;\">khẳng định <a class=\"cloze\" href=\"#mce_temp_url#\">vai trò</a> lịch sử của GCCN</span></strong> và quyền lãnh đạo duy nhất của ĐCS trong quá trình CMVN, sử dụng bạo lực CM quần chúng để chống lại bạo lực phản CM của kẻ thù; thiết lập chính quyền Nhà nước của dân, do dân, vì dân; gắn kết mục tiêu độc lập DT với CNXH</p>\r\n<p>-    GPDT là vấn đề trên hết, độc lập DT gắn liền với CNXH:</p>\r\n<p>+        HCM luôn gắn <strong><span style=\"text-decoration: underline;\">DT với GC, DT với <a class=\"cloze\" href=\"#mce_temp_url#\">quốc tế</a>, độc lập DT với CNXH</span></strong></p>\r\n<p>+        TTHCM phản ánh:</p>\r\n<ul>\r\n<li>Quy luật khách quan của sự nghiệp GPDT trong thời đại CNĐQ</li>\r\n<li>Phản ánh mối quan hệ khăng khít giữa GPDT với GPGC và GP con người</li>\r\n</ul>\r\n<p>-    GPDT là tiền đề GPGC:</p>\r\n<p>+  HCM giải quyết vấn đề DT theo quan điểm <a class=\"cloze\" href=\"#mce_temp_url#\">giai cấp</a>, đồng thời đặt vấn đề GC trong vấn đề DT</p>\r\n<p>+  <strong><span style=\"text-decoration: underline;\">GPDT là <a class=\"cloze\" href=\"#mce_temp_url#\">điều kiện</a> để GPGC</span></strong>, lợi ích của GC phải phục tùng lợi ích DT</p>\r\n<p>-    Đấu tranh cho độc lập DT mình, đồng thời tôn trọng độc lập DT nước khác:</p>\r\n<p>+  HCM không chỉ đấu tranh cho DTVN mà còn đấu tranh cho các DT bị <a class=\"cloze\" href=\"#mce_temp_url#\">áp bức</a></p>\r\n<p>+  HCM giúp đỡ các DT khác trên TG với phương châm <strong><span style=\"text-decoration: underline;\">“giúp bạn là tự giúp mình”</span></strong></p>\r\n<p>+  HCM chủ trương phải bằng CM thắng lợi của mỗi nước mà <a class=\"cloze\" href=\"#mce_temp_url#\">đóng góp</a> vào thắng lợi chung của CMTG</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1079', 'Tại sao HCM lại quan niệm GPDT là tiền đề GPGC?', null, null, '5', '0', '244', '45', '1551452852', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1080', null, null, null, '5', '1079', '244', '45', '1551452852', null, null, null, '<p>HCM quan niệm GPDT là tiền đề GPGC vì: HCM cho rằng VN là nước nửa phong kiến nửa <a class=\"cloze\" href=\"#mce_temp_url#\">thuộc địa</a>, mâu thuẫn giữa DTVN với <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa đế quốc</a> và tay sai nổi trội hơn mâu thuẫn giữa GCND và địa chủ PK, giữa tư sản với vô sản. Do đó, cần phải giải quyết vấn đề <a class=\"cloze\" href=\"#mce_temp_url#\">giai cấp</a> rồi mới giải quyết vấn đề DT như ở phương Tây</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1082', 'ND LĐ “CMGPDT muốn thắng lợi phải đi theo CM vô sản” của HCM?', null, null, '5', '0', '245', '45', '1551453625', '1552639593', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1083', null, null, null, '5', '1082', '245', '45', '1551453625', '1552639593', null, null, '<p>-    <strong><span style=\"text-decoration: underline;\">Bài học từ sự <a class=\"cloze\" href=\"#mce_temp_url#\">thất bại</a></span></strong> trước con đường cứu nước của HCM</p>\r\n<p>+        Con đường cứu nước của <a class=\"cloze\" href=\"#mce_temp_url#\">Phan Bội Châu</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">Phan Châu Trinh</a> giống như “đưa hổ cửa trước, đưa beo cửa sau”, “xin giặc rủ lòng thương”</p>\r\n<p>+        Con đường CM của Hoàng Hoa Thám mang nặng cốt cách của <a class=\"cloze\" href=\"#mce_temp_url#\">phong kiến</a></p>\r\n<p>-    <strong><span style=\"text-decoration: underline;\">CM dân chủ tư sản là <a class=\"cloze\" href=\"#mce_temp_url#\">không triệt để</a></span></strong>: Người đọc Tuyên ngôn độc lập của nước Mỹ, tìm hiểu cuộc CM tư sản Mỹ; đọc tuyên ngôn dân quyền và nhân quyền của CM Pháp, tìm hiểu CM tư sản Pháp. Người nhận thấy: \" Cách mệnh Pháp cũng như cách mệnh Mỹ, nghĩa là <a class=\"cloze\" href=\"#mce_temp_url#\">cách mệnh tư bản</a>, cách mệnh không đến nơi tiếng cộng hoà và dân chủ, kỳ thực trong thì nó tước lục công nông, ngoài thì nó áp bức thuộc địa\". Bởi lẽ đó, Người không đi theo con đường <a class=\"cloze\" href=\"#mce_temp_url#\">cách mạng tư sản</a>.</p>\r\n<p>-    <strong><span style=\"text-decoration: underline;\">Thắng lợi của CMT10 Nga và sự ra đời của QTCS</span></strong></p>\r\n<p>+  HCM thấy được <strong><span style=\"text-decoration: underline;\">CMT10 Nga</span></strong> ko chỉ là cuộc CM vô sản mà còn là một cuộc <strong><span style=\"text-decoration: underline;\">cách mạng <a class=\"cloze\" href=\"#mce_temp_url#\">giải phóng dân tộc</a><br /></span></strong></p>\r\n<p>+  Người thấy trong lý luận của <a class=\"cloze\" href=\"#mce_temp_url#\">Lênin</a> một phương hướng mới để GPDT: <strong><span style=\"text-decoration: underline;\">con đường CM vô sản</span></strong>.Thực chất đây là con đường độc lập DT <a class=\"cloze\" href=\"#mce_temp_url#\">gắn liền</a> với CNXH, GPDT gắn liền với GPGC và giải phóng con người.</p>\r\n<p>=&gt; Vượt ra sự hạn chế về tư tưởng của các sĩ phu yêu nước và của các nhà CM có xu hướng tư sản đương thời, <strong><span style=\"text-decoration: underline;\">HCM đã đến với học thuyết CM của <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa Mác - Lênin</a></span></strong><a class=\"cloze\" href=\"#mce_temp_url#\"> </a>và lựa chọn khuynh hướng <a class=\"cloze\" href=\"#mce_temp_url#\">chính trị vô sản</a>. Người khẳng định: “Muốn cứu nước và GPDT không có con đường nào khác ngoài con đường CM <a class=\"cloze\" href=\"#mce_temp_url#\">vô sản</a>”</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1086', 'Quan niệm của HCM về Chủ nghĩa xã hội ở VN?', null, null, '5', '0', '237', '51', '1551499309', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1087', null, null, null, '5', '1086', '237', '51', '1551499309', null, null, null, '<p>- Chủ nghĩa xã hội là một chế độ <a class=\"cloze\" href=\"#mce_temp_url#\">xã hội hoàn chỉnh</a>, bao gồm nhiều mặt khác nhau của <a class=\"cloze\" href=\"#mce_temp_url#\">đời sống xã hội</a></p>\r\n<p>- Nhấn mạnh một số mặt của đời sống xã hội: <a class=\"cloze\" href=\"#mce_temp_url#\">chính trị</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">kinh tế</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">văn hóa</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">xã hội<br /></a>- Nhấn mạnh mục tiêu vì lợi ích của <a class=\"cloze\" href=\"#mce_temp_url#\">tổ quốc</a>, của <a class=\"cloze\" href=\"#mce_temp_url#\">nhân dân</a></p>\r\n<p>- Xác định <a class=\"cloze\" href=\"#mce_temp_url#\">động lực</a> xây dựng Chủ nghĩa xã hội: <a class=\"cloze\" href=\"#mce_temp_url#\">sức mạnh toàn dân tộc</a> kết hợp với <a class=\"cloze\" href=\"#mce_temp_url#\">sức mạnh thời đại</a>.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1089', 'Yếu tố quan trọng và quyết định nhất trong việc xây dựng Xã hội chủ nghĩa?', null, null, '5', '0', '247', '51', '1551500273', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1090', null, null, null, '5', '1089', '247', '51', '1551500273', null, null, null, '<p>Động lực <a class=\"cloze\" href=\"#mce_temp_url#\">con người</a> là yếu tố quan trọng và quyết định nhất vì Hồ Chí Minh đã nhận thấy ở động lực này có sự kết hợp giữa <a class=\"cloze\" href=\"#mce_temp_url#\">cá nhân</a> với <a class=\"cloze\" href=\"#mce_temp_url#\">xã hội</a>: Người cho rằng, không có chế độ xã hội nào coi trọng <a class=\"cloze\" href=\"#mce_temp_url#\">lợi ích chính đáng</a> của cá nhân con người bằng chế độ xã hội chủ nghĩa. <a class=\"cloze\" href=\"#mce_temp_url#\">Truyền thống yêu nước</a> của dân tộc, sự đoàn kết cộng đồng, <a class=\"cloze\" href=\"#mce_temp_url#\">sức lao động sáng tạo</a> của nhân dân, đó là <a class=\"cloze\" href=\"#mce_temp_url#\">sức mạnh tổng hợp</a> tạo nên <a class=\"cloze\" href=\"#mce_temp_url#\">động lực</a> quan trọng của chủ nghĩa xã hội</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1091', null, null, null, '5', '1089', '247', '51', '1551500273', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1092', 'Quan điểm của HCM về nội lực trong xây dựng Chủ nghĩa xã hội ở VN?', null, null, '5', '0', '247', '51', '1551500438', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1093', null, null, null, '5', '1092', '247', '51', '1551500438', null, null, null, '<p>- Động lực con người: Bao gồm <a class=\"cloze\" href=\"#mce_temp_url#\">cộng đồng</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">cá nhân</a>. Để phát huy động lực con người cần phải:<br />+  Phát huy sức mạnh <a class=\"cloze\" href=\"#mce_temp_url#\">đoàn kết</a> của cả cộng đồng dân tộc.<br />+  Phát huy sức mạnh <a class=\"cloze\" href=\"#mce_temp_url#\">con người</a>, phát huy quyền <a class=\"cloze\" href=\"#mce_temp_url#\">làm chủ</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">ý thức làm chủ</a> của người lao động trong sở hữu, trong sản xuất và phân phối</p>\r\n<p>- Động lực <a class=\"cloze\" href=\"#mce_temp_url#\">kinh tế</a>: phát triển kinh tế, sản xuất, giải phóng mọi <a class=\"cloze\" href=\"#mce_temp_url#\">năng lực sản xuất</a>, làm cho mọi người, mọi nhà trở nên có ích quốc lợi dân, gắn liền kinh tế với <a class=\"cloze\" href=\"#mce_temp_url#\">kĩ thuật</a>, kinh tế với <a class=\"cloze\" href=\"#mce_temp_url#\">xã hội</a>.</p>\r\n<p>- Động lực văn hóa, khoa học, giáo dục: coi đó là động lực <a class=\"cloze\" href=\"#mce_temp_url#\">tinh thần</a> không thể thiếu của Chủ nghĩa xã hội</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1095', 'Quan điểm của HCM về động lực xây dựng Chủ nghĩa xã hội ở VN ?', null, null, '5', '0', '248', '51', '1551501225', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1096', null, null, null, '5', '1095', '248', '51', '1551501225', null, null, null, '<p>- Nội lực của <a class=\"cloze\" href=\"#mce_temp_url#\">Chủ nghĩa xã hội</a>:<br />  +  Động lực con người: Bao gồm <a class=\"cloze\" href=\"#mce_temp_url#\">cộng đồng</a> và cá nhân. Để phát huy động lực con người cần phải:</p>\r\n<ul>\r\n<li>Phát huy sức mạnh đoàn kết của <a class=\"cloze\" href=\"#mce_temp_url#\">cả cộng đồng dân tộc</a>.</li>\r\n<li>Phát huy sức mạnh <a class=\"cloze\" href=\"#mce_temp_url#\">con người</a>, phát huy quyền làm chủ và ý thức làm chủ của người lao động trong sở hữu, trong sản xuất và phân phối</li>\r\n</ul>\r\n<p>  + Động lực kinh tế: phát triển kinh tế, sản xuất, giải phóng mọi <a class=\"cloze\" href=\"#mce_temp_url#\">năng lực sản xuất</a>, làm cho mọi người, mọi nhà trở nên có ích quốc lợi dân, gắn liền kinh tế với <a class=\"cloze\" href=\"#mce_temp_url#\">kĩ thuật</a>, kinh tế với <a class=\"cloze\" href=\"#mce_temp_url#\">xã hội</a>.</p>\r\n<p>  + Động lực văn hóa, khoa học, giáo dục: coi đó là động lực <a class=\"cloze\" href=\"#mce_temp_url#\">tinh thần</a> không thể thiếu của Chủ nghĩa xã hội</p>\r\n<p>- Ngoại lực:</p>\r\n<p>  + Tranh thủ sự giúp đỡ, <a class=\"cloze\" href=\"#mce_temp_url#\">hợp tác quốc tế</a>, kết hợp <a class=\"cloze\" href=\"#mce_temp_url#\">sức mạnh dân tộc</a> với <a class=\"cloze\" href=\"#mce_temp_url#\">sức mạnh quốc tế</a></p>\r\n<p>  + Phải sử dụng tốt những thành tựu <a class=\"cloze\" href=\"#mce_temp_url#\">khoa học – kĩ thuật</a></p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1098', 'Quan điểm của HCM về loại hình quá độ lên Chủ nghĩa xã hội ở VN. Đặc điểm lớn nhất và mâu thuẫn cơ bản nhất trong thời kì quá độ lên Chủ nghĩa xã hội ở nước ta theo TTHCM là gì?', null, null, '5', '0', '249', '51', '1551501918', '1552637217', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1099', null, null, null, '5', '1098', '249', '51', '1551501918', '1552637217', null, null, '<p>- Quan điểm của HCM về loại hình quá độ lên Chủ nghĩa xã hội ở VN:</p>\r\n<p>  + <a class=\"cloze\" href=\"#mce_temp_url#\">Chủ nghĩa Mác Lênin</a> chỉ ra có 2 con đường quá độ lên Chủ nghĩa xã hội: <a class=\"cloze\" href=\"#mce_temp_url#\">quá độ trực tiếp</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">quá độ gián tiếp</a></p>\r\n<p>  + Theo HCM ở VN theo con đường <a class=\"cloze\" href=\"#mce_temp_url#\">quá độ gián tiếp</a>, cụ thể từ một nước thuộc địa <a class=\"cloze\" href=\"#mce_temp_url#\">nửa phong kiến</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">nông nghiệp</a> lạc hậu sau khi giành được <a class=\"cloze\" href=\"#mce_temp_url#\">độc lập dân tộc</a> quá độ đi lên Chủ nghĩa xã hội.</p>\r\n<p>- Đặc điểm lớn nhất trong thời kì quá độ lên Chủ nghĩa xã hội ở nước ta: là từ một nước <a class=\"cloze\" href=\"#mce_temp_url#\">nông nghiệp lạc hậu</a> tiến lên Chủ nghĩa xã hội, không kinh qua giai đoạn phát triển <a class=\"cloze\" href=\"#mce_temp_url#\">tư bản chủ nghĩa</a>.</p>\r\n<p>- Mâu thuẫn cơ bản nhất trong thời kì quá độ lên Chủ nghĩa xã hội ở nước ta: mâu thuẫn giữa nhu cầu <a class=\"cloze\" href=\"#mce_temp_url#\">phát triển cao</a> của đất nước theo xu hướng tiến bộ và <a class=\"cloze\" href=\"#mce_temp_url#\">thực trạng kinh tế - xã hội</a> quá thấp kém của nước ta.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1101', 'Xây dựng Chủ nghĩa xã hội ở VN trên lĩnh vực chính trị', null, null, '5', '0', '250', '51', '1551502668', '1552637178', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1102', null, null, null, '5', '1101', '250', '51', '1551502668', '1552637178', null, null, '<p>Lĩnh vực chính trị</p>\r\n<p>  + <a class=\"cloze\" href=\"#mce_temp_url#\">Giữ vững</a> và phát huy vai trò <a class=\"cloze\" href=\"#mce_temp_url#\">lãnh đạo</a> của Đảng</p>\r\n<p>  + Củng cố và <a class=\"cloze\" href=\"#mce_temp_url#\">tăng cường</a> vai trò <a class=\"cloze\" href=\"#mce_temp_url#\">quản lý</a> của Nhà nước</p>\r\n<p>  + Củng cố và mở rộng <a class=\"cloze\" href=\"#mce_temp_url#\">Mặt trận dân tộc thống nhất</a></p>\r\n<p>  + Củng cố và <a class=\"cloze\" href=\"#mce_temp_url#\">tăng cường sức mạnh</a> của toàn bộ hệ thống chính trị cũng như từng <a class=\"cloze\" href=\"#mce_temp_url#\">thành tố</a></p>\r\n<p> </p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1104', 'Quan điểm của HCM về mục tiêu chung và mục tiêu cao nhất của Chủ nghĩa xã hội ở VN', null, null, '5', '0', '251', '51', '1551508343', '1552638021', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('1105', null, null, null, '5', '1104', '251', '51', '1551508343', '1552638021', null, null, '<ul>\r\n<li>Mục tiêu chung: <a class=\"cloze\" href=\"#mce_temp_url#\">độc lập</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">tự do</a> cho dân tộc, <a class=\"cloze\" href=\"#mce_temp_url#\">hạnh phúc</a> cho nhân dân</li>\r\n<li>Mục tiêu cao nhất: <a class=\"cloze\" href=\"#mce_temp_url#\">nâng cao đời sống</a> nhân dân</li>\r\n</ul>\r\n<p> </p>', null, '55', null, null, null, null, null);
