INSERT INTO `question` VALUES ('1129', null, null, null, '5', '1128', '69', '1', '1551511975', null, '', null, '  Trong Công văn số <a class=\"clozetip\" title=\" gợi ý 1 \" href=\"#\">gửi Cục thuế các tỉnh </a>, mới đây về công tác quản lý thuế đối với hộ kinh doanh nộp thuế khoán năm 2019, <a class=\"clozetip\" title=\" gợi ý 2\" href=\"#\">Tổng cục Thuế</a> cho biết hoạt động này nhằm đưa vào quản lý thuế nề nếp, chống thất thu thuế.', null, '36', null, null, null, null, null);
