INSERT INTO `question` VALUES ('1239', null, null, null, '5', '1238', '271', '463', '1551697069', '1551697260', null, null, '<ul>\r\n<li>Marketing là <strong><a class=\"cloze\" href=\"#mce_temp_url#\">một dạng hoạt động</a> của con người</strong> nhằm thỏa mãn <a class=\"cloze\" href=\"#mce_temp_url#\">nhu cầu</a> và mong muốn <strong>thông qua <a class=\"cloze\" href=\"#mce_temp_url#\">trao đổi</a></strong></li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
