INSERT INTO `question` VALUES ('1243', null, null, null, '5', '1242', '271', '463', '1551697378', null, null, null, '<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"232\">\r\n<p> </p>\r\n</td>\r\n<td valign=\"top\" width=\"263\">\r\n<p><strong>Marketing cổ điển</strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"285\">\r\n<p><strong>Marketing hiện đại</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"232\">\r\n<p>Điểm xuất phát</p>\r\n</td>\r\n<td valign=\"top\" width=\"263\">\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\">Nhà sản xuất</a></p>\r\n</td>\r\n<td valign=\"top\" width=\"285\">\r\n<p>Thị trường</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"232\">\r\n<p>Hoạt động</p>\r\n</td>\r\n<td valign=\"top\" width=\"263\">\r\n<p>Bán cái mình có</p>\r\n</td>\r\n<td valign=\"top\" width=\"285\">\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\">Bán cái thị trường cần</a></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"232\">\r\n<p>Trọng tâm</p>\r\n</td>\r\n<td valign=\"top\" width=\"263\">\r\n<p>Bán được nhiều hàng hóa</p>\r\n</td>\r\n<td valign=\"top\" width=\"285\">\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\">Thỏa mãn nhu cầu khách hàng</a></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"232\">\r\n<p>Phương tiện/công cụ</p>\r\n</td>\r\n<td valign=\"top\" width=\"263\">\r\n<p>Sản phẩm</p>\r\n</td>\r\n<td valign=\"top\" width=\"285\">\r\n<p>Marketing mix</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"232\">\r\n<p>Điểm đích</p>\r\n</td>\r\n<td valign=\"top\" width=\"263\">\r\n<p>Lợi nhuận dựa trên <a class=\"cloze\" href=\"#mce_temp_url#\">việc bán hàng</a></p>\r\n</td>\r\n<td valign=\"top\" width=\"285\">\r\n<p>Lợi nhuận dựa trên thỏa mãn tối ưu<a class=\"cloze\" href=\"#mce_temp_url#\"> nhu cầu khách hàng</a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1246', 'Nêu Các giai đoạn phát triển của marketing ?', null, null, '5', '0', '271', '463', '1551697810', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1247', null, null, null, '5', '1246', '271', '463', '1551697810', null, null, null, '<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td colspan=\"2\" valign=\"top\" width=\"749\"><ol>\r\n<li><strong><span style=\"color: #ff0000;\">Sản xuất sản phẩm quyết định</span></strong></li>\r\n</ol></td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"172\">\r\n<p>Thời gian</p>\r\n</td>\r\n<td valign=\"top\" width=\"577\">\r\n<p>30/XX</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"172\">\r\n<p>Tình thế</p>\r\n</td>\r\n<td valign=\"top\" width=\"577\">\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\">Cung</a> &lt;&lt; <a class=\"cloze\" href=\"#mce_temp_url#\">Cầu </a>thị trường</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"172\">\r\n<p>Mục tiêu</p>\r\n</td>\r\n<td valign=\"top\" width=\"577\">\r\n<p>Sản xuất hàng loạt sản phẩm để hợp lý hóa sản xuất</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"172\">\r\n<p>Hoạt động</p>\r\n</td>\r\n<td valign=\"top\" width=\"577\">\r\n<p>Tập trung hợp lý hóa <a class=\"cloze\" href=\"#mce_temp_url#\">sản xuất</a></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"172\">\r\n<p>Hậu quả</p>\r\n</td>\r\n<td valign=\"top\" width=\"577\">\r\n<p>Xuất hiện marketing <a class=\"cloze\" href=\"#mce_temp_url#\">cổ điển</a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p> </p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td colspan=\"2\" valign=\"top\" width=\"749\">       <span style=\"color: #ff0000;\"> <strong>  2. Phân phối- bán hàng quyết định</strong></span></td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"172\">\r\n<p>Thời gian</p>\r\n</td>\r\n<td valign=\"top\" width=\"577\">\r\n<p>Chiến tranh thế giới thứ 2- 50/XX</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"172\">\r\n<p>Tình thế</p>\r\n</td>\r\n<td valign=\"top\" width=\"577\">\r\n<p><a class=\"clozetip\" title=\"Cung-cầu\" href=\"#\">Cung </a>&gt; <a class=\"clozetip\" title=\"Cung-cầu\" href=\"#\">Cầu</a> thị trường</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"172\">\r\n<p>Mục tiêu</p>\r\n</td>\r\n<td valign=\"top\" width=\"577\">\r\n<p>Quay vòng sản xuất bằng <a class=\"cloze\" href=\"#mce_temp_url#\">phân phối </a>và marketing</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"172\">\r\n<p>Hoạt động</p>\r\n</td>\r\n<td valign=\"top\" width=\"577\">\r\n<p>Phân phối hàng loạt, quảng cáo, xúc tiến bán</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"172\">\r\n<p>Hậu quả</p>\r\n</td>\r\n<td valign=\"top\" width=\"577\">\r\n<p>Xuất hiện marketing cổ điển vận hàng ở <a class=\"clozetip\" title=\"So sánh mức độ\" href=\"#\">mức cao</a> hơn</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p> </p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td colspan=\"2\" valign=\"top\" width=\"749\">        <span style=\"color: #ff0000;\">  <strong>  3. Nhu cầu thị trường quyết định</strong></span></td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"172\">\r\n<p>Thời gian</p>\r\n</td>\r\n<td valign=\"top\" width=\"577\">\r\n<p>50/XX-70/XX</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"172\">\r\n<p>Tình thế</p>\r\n</td>\r\n<td valign=\"top\" width=\"577\">\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\">Cạnh tranh</a> quá mức, KT-CN phát triển</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"172\">\r\n<p>Mục tiêu</p>\r\n</td>\r\n<td valign=\"top\" width=\"577\">\r\n<p>Nghiên cứu để thỏa mãn tối đa</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"172\">\r\n<p>Hoạt động</p>\r\n</td>\r\n<td valign=\"top\" width=\"577\">\r\n<p>Vận dụng<a class=\"cloze\" href=\"#mce_temp_url#\"> marketing mix</a></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"172\">\r\n<p>Hậu quả</p>\r\n</td>\r\n<td valign=\"top\" width=\"577\">\r\n<p>Xuất hiên <a class=\"cloze\" href=\"#mce_temp_url#\">marketing hiện đại</a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p> </p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td colspan=\"2\" valign=\"top\" width=\"749\">    <strong> 4. Khách hàng có sức mua và nhu cầu chưa được thỏa mãn quyết định</strong></td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"172\">\r\n<p>Thời gian</p>\r\n</td>\r\n<td valign=\"top\" width=\"577\">\r\n<p>70/XX-đến nay</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"172\">\r\n<p>Tình thế</p>\r\n</td>\r\n<td valign=\"top\" width=\"577\">\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\">Cạnh tranh </a>quyết liệt; chất lượng nhu cầu và mức sống phát triển</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"172\">\r\n<p>Mục tiêu</p>\r\n</td>\r\n<td valign=\"top\" width=\"577\">\r\n<p>Thảo mãn toàn diện các <a class=\"cloze\" href=\"#mce_temp_url#\">phân đoạn</a> thị trường</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"172\">\r\n<p>Hoạt động</p>\r\n</td>\r\n<td valign=\"top\" width=\"577\">\r\n<p>Phát triển đồng bộ<a class=\"cloze\" href=\"#mce_temp_url#\"> marketing mix</a> với dịch vụ khách hàng và mở rộng lĩnh vực vận dụng</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"172\">\r\n<p>Hậu quả</p>\r\n</td>\r\n<td valign=\"top\" width=\"577\">\r\n<p>Marketing hiện đại vận dụng ở mức cao hơn</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p> </p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1248', null, null, null, '5', '1246', '271', '463', '1551697810', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1249', 'Nêu các chức năng của marketing kinh doanh? Trình bày có ví dụ minh họa một trong các chức năng marketing kinh doanh (Thích ứng, tiêu thụ).', null, null, '5', '0', '271', '463', '1551698163', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1250', null, null, null, '5', '1249', '271', '463', '1551698163', null, null, null, '<p>Các chức năng của marketing (6 chức năng):</p>\r\n<p>          Thích ứng</p>\r\n<p>      <a class=\"cloze\" href=\"#mce_temp_url#\">    Phân phối</a></p>\r\n<p>          Tiêu thụ</p>\r\n<p>        <a class=\"cloze\" href=\"#mce_temp_url#\">  Hỗ trợ</a></p>\r\n<p>          Mạo hiểm</p>\r\n<p>          <a class=\"cloze\" href=\"#mce_temp_url#\">Điều hòa, phối hợp</a></p>\r\n<p> </p>\r\n<ol>\r\n<li><strong>1.     </strong><strong>Thích ứng</strong></li>\r\n</ol>\r\n<p>-        Marketing có chức năng làm cho sản phẩm luôn <strong><a class=\"cloze\" href=\"#mce_temp_url#\">thích ứng với nhu cầu thị trường</a>:</strong> Kiểu cách sản phẩm, mẫu mã sản phẩm,.. luôn được đổi mới phù hợp với nhu cầu thị trường.</p>\r\n<p>+Marketing sản phẩm</p>\r\n<p>+Nghiên cứu <a class=\"cloze\" href=\"#mce_temp_url#\">thị trường</a> ,thái độ của khách hàng với sản phẩm..</p>\r\n<p>+ Tư vấn cho bộ phận sản xuất</p>\r\n<p>-      Ví dụ: Tương ớt Trung Thành giai đoạn đầu đóng chai thủy tinhèDễ vớ, khó rótèkhách hàng phản ánhèBộ phận marketing phân tích, nghiên cứu tác động tới bộ phận sản xuất.Tuy nhiên không thể thay đổi một lúc toàn bộ dây chuyền đóng chai, công ty đã đưa ra chiến lược marketing với thông điệp “Tương ớt Trung Thành đậm đặc hơn tất cả” so với loại tương ớt trên thị tường lúc đó nên mới khó rótèHạn chế được rủi ro.</p>\r\n<p> </p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> <strong>2. </strong></div>\r\n<div style=\"all: unset;\"><strong>Tiêu thụ</strong>\r\n<p>-        Chất lượng quan trọng nhất thể hiện chât lượng vận hành các chức năng còn lại</p>\r\n<p>+<a class=\"cloze\" href=\"#mce_temp_url#\"> Nghiên cứu</a> marketing với khách hàng</p>\r\n<p>+<a class=\"cloze\" href=\"#mce_temp_url#\">Tổ chức</a> hoạt động hậu cần</p>\r\n<p>+ Ấn định, <a class=\"cloze\" href=\"#mce_temp_url#\">kiểm soát</a> giá bán</p>\r\n<p>+Tổ chức quảng cáo và xúc tiến bán</p>\r\n<p>+Lựa chọn phương pháp, <a class=\"cloze\" href=\"#mce_temp_url#\">công nghệ,</a> trình độ kĩ thuật thương lượng và bán hàng</p>\r\n<p>+Quyết định sức bán và tổ chức lực lượng bán</p>\r\n<p>+Theo dõi và <a class=\"cloze\" href=\"#mce_temp_url#\">quản trị bán</a></p>\r\n<p>+Tổ chức <a class=\"cloze\" href=\"#mce_temp_url#\">dịch vụ </a>thương mại trước, trong và sau bán hàng</p>\r\n</div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1251', null, null, null, '5', '1249', '271', '463', '1551698163', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1252', 'Nêu khái niệm giá trị (theo quan điểm marketing)?', null, null, '5', '0', '271', '463', '1551698386', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1253', null, null, null, '5', '1252', '271', '463', '1551698386', null, null, null, '<p>•Giá trị (theo quan điểm của marketing) của một sản phẩm là <a class=\"cloze\" href=\"#mce_temp_url#\">sự đánh giá </a>của người tiêu dùng về khả năng của nó trong việc<a class=\"cloze\" href=\"#mce_temp_url#\"> thỏa mãn nhu cầu</a> với họ.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1254', 'Quá trình tiến động marketing?', null, null, '5', '0', '275', '463', '1551698698', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1255', null, null, null, '5', '1254', '275', '463', '1551698698', null, null, null, '<p>Quy trình tiến động marketing gồm 10 bước:</p>\r\n<ol>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Thị trường</a> (nhu cầu)</li>\r\n<li>Ấn định <a class=\"cloze\" href=\"#mce_temp_url#\">sản phẩm</a></li>\r\n<li>Chọn <a class=\"cloze\" href=\"#mce_temp_url#\">thị trường</a> đích và mục tiêu</li>\r\n<li>Định<a class=\"cloze\" href=\"#mce_temp_url#\"> sản phẩm</a> hỗn hợp</li>\r\n<li>Định giá bán</li>\r\n<li>Định kênh <a class=\"cloze\" href=\"#mce_temp_url#\">phân phối</a></li>\r\n<li>Định sức bán</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Xúc tiến</a> thương mại</li>\r\n<li>Theo dõi và khuếch trương <a class=\"cloze\" href=\"#mce_temp_url#\">bán</a></li>\r\n<li>Thị trường (nhu cầu sản phẩm)</li>\r\n</ol>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1256', null, null, null, '5', '1254', '275', '463', '1551698698', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1257', 'Phân tích có ví dụ minh họa tư tưởng “Bán cái thị trường cần chứ không bán cái mình có” của marketing hiên đại?', null, null, '5', '0', '275', '463', '1551699176', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1258', null, null, null, '5', '1257', '275', '463', '1551699176', null, null, null, '<p>Tư tưởng “bán cái thị trường cần chứ không bán cái mình có” yêu cầu nhà sản xuất phải hiểu rất rõ<a class=\"cloze\" href=\"#mce_temp_url#\"> khách hàng</a> của mình thông qua hoạt động <a class=\"cloze\" href=\"#mce_temp_url#\">nghiên cứu</a> thị trường. Do vậy, doanh nghiệp phải tiến hành hoạt động marketing ở tất cả các giai đoạn trong<a class=\"cloze\" href=\"#mce_temp_url#\"> quá trình sản xuất</a>, từ nghiên cứu thị trường để năm bắt <a class=\"cloze\" href=\"#mce_temp_url#\">nhu cầu</a> cho đến sau khi <a class=\"cloze\" href=\"#mce_temp_url#\">bán hàng.</a></p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1259', null, null, null, '5', '1257', '275', '463', '1551699176', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1263', 'Nêu khái niệm môi trường marketing?', null, null, '5', '0', '278', '463', '1551699465', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1264', null, null, null, '5', '1263', '278', '463', '1551699465', null, null, null, '<p>Khái niệm môi trường marketing bao  hàm các<a class=\"cloze\" href=\"#mce_temp_url#\"> nhân tố </a>và lực lượng bên ngoài marketing đang <a class=\"cloze\" href=\"#mce_temp_url#\">ảnh hưởng </a>đến khả năng quản trị marketing trong công cuộc triển khai và <a class=\"cloze\" href=\"#mce_temp_url#\">duy trì</a> mối quan hệ thành công với khách hàng <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu </a>của doanh nghiệp.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1265', null, null, null, '5', '1263', '278', '463', '1551699465', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1266', 'Sự cần thiết của việc nghiên cứu môi trường marketing?', null, null, '5', '0', '278', '463', '1551699581', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1267', null, null, null, '5', '1266', '278', '463', '1551699581', null, null, null, '<ul>\r\n<li>Môi trường tao ra <a class=\"cloze\" href=\"#mce_temp_url#\">sự xung đột</a>, kìm hãm sự phát triển của doanh nghiệp nhưng đồng thời cũng tạo ra<a class=\"cloze\" href=\"#mce_temp_url#\"> động lực </a>thúc đẩy</li>\r\n</ul>\r\n<p>                Môi trường marketing vừa mang đến cơ hội vừa mang đến thách thức do đó doanh nghiệp cần tận dụng những cơ hội và tránh các thách thức để đạt được sự phát triển như mong muốn.</p>\r\n<ul>\r\n<li>Các nhân tố của môi trường luôn<a class=\"cloze\" href=\"#mce_temp_url#\"> biến động</a> do đó đòi hỏi các doanh nghiệp phải <a class=\"cloze\" href=\"#mce_temp_url#\">nghiên cứu </a>và dự đoán</li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
